INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/32548f7e-8c25-4c49-9f22-78dee3543704','key_15782','','','2143','2143.1.1','environmental mining engineer',replace('environmental mining technology engineering specialist\nenvironmental mining technology engineering expert\nmining environmental engineer\nmine environmental engineer\nspecialised engineer\nenvironmental co-ordinator\nmine environmental specialists\nenvironmental mining engineering consultant\nmine environmental specialist\nenvironmental mining manager\nmine environmental manager\nenvironmental mining engineering specialist\nenvironmental mining engineering adviser\nenvironmental mining technology engineering adviser\nenvironmental mining engineer\nenvironmental co-ordinators\nmine environmental managers\nenvironmental mining technology engineer\nenvironmental mining technology engineering consultant\nmine environmental engineers\nenvironmental mining engineering expert\ngreen engineers\nenvironmental officers','\n',char(10)),'Environmental mining engineers oversee the environmental performance of mining operations. They develop and implement environmental systems and strategies to minimise environmental impacts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3256e283-0892-4330-b9f2-6dc4aa4bd86b','key_15783','','','8160','8160.52','sugar refinery operator',replace('sugar production operative\nsugar refinery operator\nsugar refinery operative\nsugar worker\nsugar plant operative','\n',char(10)),replace('Sugar refinery operators tend and control refinery equipment to produce sugars and related products from raw sugar or other raw materials like corn starch. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/326027fc-5408-47cd-9b32-fbe770dbbe80','key_15784','','','3112','3112.1.4','construction quality manager',replace('construction quality manager\nconstruction QA manager\nconstruction quality assurance manager\nconstruction QA QS manager\nconstruction quality and safety control supervisor\nconstruction quality and safety manager\nconstruction quality managers','\n',char(10)),'Construction quality managers make sure the quality of the work meets standards set in the contract, as well as minimum legislative standards. They establish procedures to check quality, perform inspections, and propose solutions to quality shortcomings.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3288a2fe-4934-47c8-ad0b-11b16c9cd461','key_15785','','','3119','3119.19','utilities inspector',replace('sewerage inspector\nsewer inspector\nutilities inspector\nutilities supervisor\nutility inspector\nturbine inspector\nstreetworks inspector\nequipment inspector\npipeline inspector\nutilities manager','\n',char(10)),replace('Utilities inspectors examine products, systems and machiney such as sewer, water, gas or electric turbines ensuring they are built and functioning according to regulations. They write inspection reports and provide recommendations to improve the systems and repair the broken components.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/32ac76f6-20a0-469e-8cac-e68634087dd3','key_15786','','','3252','3252.3','picture archiving and communication systems administrator',replace('administrator of PACS\nadministrator of PACS system\nPACS manager\nPACS system analyst\nPACS interface analyst\nPACS associate\nPACS administration manager\npicture archiving and communication systems administrator\nPACS administrator\nPACS support engineer','\n',char(10)),'Picture archiving and communication systems administrators manage picture archiving and communication systems (PACS). They store images taken by various types of medical equipment, such as X-rays, in order to enable easy access to these images for medical. PACS administrators ensure the day-to-day management and maintenance of the system."','','Excludes people performing patients treatment or medical treatment assistance.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/32ef07df-1b0a-439a-916e-8289eb9f4895','key_15787','','','3240','3240.2.3','equine dental technician',replace('horse dentist\nequine dental technicians\nhorse teeth technician\nhorse dental care assistant\nhorse dental technician\nequine dental technician\nhorse dental care technician','\n',char(10)),replace(' Equine dental technicians provide routine equine dental care, using appropriate equipment in accordance with national legislation. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/32fafe2c-0099-4852-889e-46614ce9a9ae','key_15788','','','3312','3312.3','credit manager',replace('credit manager\nsenior credit officer\ncredit administration manager\ncredit control supervisor\nconsumer credit manager\nbranch credit administrator\ncredit controller\ncredit policy manager\ncredit department manager\ncredit control manager\ncredit and collection manager\nloan and credit manager','\n',char(10)),'Credit managers oversee the application of credit policy in the bank. They decide the credit limits to be imposed, the reasonable levels of risk accepted and the conditions and terms of payment made to the customers. They control the collection of payments from their customers and manage the credit department of a bank.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/32fcdc6f-1c8a-44e9-99b8-5519e4db4414','key_15789','','','7322','7322.7','printing textile technician',replace('textile printing technician\nprinting textile machine operator\ntextile screen printer technician\nprinting textile technician\nclothing printing press machine operator\nprinting textile supervisor\ntextile printing press operator\ncutting and printing machine set-up operator\nscreen printing technician','\n',char(10)),'Printing textile technicians perform operations related to setting up the printing processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3327a087-cd36-4b02-ac1b-6455f59f9312','key_15790','','','5413','5413.2','prison officer',replace('jailer\ngaoler\nprison guard\nprisoner custody officer\nturnkey\ndetention officer\nprison warden\ncorrections officer\nprison officer','\n',char(10)),'Prison officers supervise inmates in a correctional facility and ensure the security and peace-keeping in the facility. They perform checks and searches to ensure compliance to regulations, monitor visitations and the activities of inmates as well as participate in programs of rehabilitation, and ensure records maintenance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/336cf331-8082-4a0c-bf5f-e7c54706211c','key_15791','','','7234','7234.1','bicycle mechanic',replace('scooter technician\nbicycle fixer\nbike repairer\nbicycle repairer\nbicycle shop attendant\nbike shop assistant\ncycle technician\nbicycle technician\nbicycle mechanic\nbike mechanic\ncycle mechanic\nbike technician\ncycle repairer\nbicycle maintenance technician\nbike shop worker\nscooter mechanic\nbicycle shop assistant\nscooter repairer\nbicycle repairman\nbicycle shop worker\nbike shop attendant','\n',char(10)),'Bicycle mechanics maintain and repair a variety of bicycle models and component parts. They may perform customized alterations, according to their client''s preferences.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/33960bab-4423-4808-af6c-ec2b485dba41','key_15792','','','7411','7411.1.1','building electrician',replace('installation electrician\nindustrial building electrician\ncommercial building electrician\nbuilding electrician\nelectrical maintenance worker\nelectrical installer\nbuilding maintenance electrician\nmaintenance electrician\nelectrical maintenance technician\nelectrical systems installer\nbuilding services electrician\nelectrical services installer','\n',char(10)),'Building electricians install and maintain electricity cables and other electrical infrastructure in buildings. They make sure installed electrical equipment is isolated and presents no fire hazards. They understand existing situations and make improvements if called for.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/33ae2ba9-9835-410f-8ea6-e5ac991f5975','key_15793','','','3413','3413.1','monk/nun',replace('monk/nun\nnun\nmonk','\n',char(10)),'Monks/nuns dedicate themselves to a monastic lifestyle. They vow to take part in sprititual works as part of their religious community. Monks/nuns take part in daily prayer and often live in self-sufficient monasteries or convents alongside other monks/nuns.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/33cd8ff4-62f2-4a3f-b053-6e009ecef6da','key_15794','','','3331','3331.2.1.22','import export specialist in meat and meat products',replace('import export specialist in meat and meat products\ngraduate export specialist in meat and meat products\ncustoms compliance specialist in meat and meat products\ngraduate import specialist in meat and meat products\ninternational trade compliance specialist in meat and meat products\nsenior import specialist in meat and meat products\nsenior import export specialist in meat and meat products\ntrainee export specialist in meat and meat products\ngraduate import export specialist in meat and meat products\ntrainee import export specialist in meat and meat products\nsenior export specialist in meat and meat products\nassistant export specialist in meat and meat products\nassistant import export specialist in meat and meat products\nassistant import specialist in meat and meat products\ntrainee import specialist in meat and meat products','\n',char(10)),'Import export specialists in meat and meat products have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/33da5532-8c4e-4d1b-b52e-c201ee1bc7ab','key_15795','','','2269','2269.4','occupational therapist',replace('rehabilitation therapist\nexpert occupational therapist\nspecialist occupational therapist\noccupational therapist\nexpert practitioner of occupational therapy\nspecialist in occupational therapy\npractitioner of occupational therapy\nrehabilitation counsellor\nergotherapist\nvocational rehabilitation counsellor\nactivity therapist','\n',char(10)),'Occupational therapists assist individuals or groups who have occupational limitations due to diseases, physical disorders, and temporary or permanent mental disabilities, in regaining their ability to perform daily activities. They provide treatment and rehabilitation to enable them to actively participate in society, to live their lives according to their wishes and to perform those activities that are meaningful to them. Occupational therapists work in public health and social care services, but can also be involved in supporting asylum seekers, refugees and/or homeless people.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/33e3a746-f209-4a1a-96df-301505531cb2','key_15796','','','1431','1431.2.1','beauty salon manager',replace('beauty salon director\nbeauty parlor director\nsalon manager\nbeauty salon manager\nbeauty centre manager\nbeauty spa manager\nbeauty parlour director\nbeauty spa director\nhair salon manager','\n',char(10)),'Beauty salon managers oversee the daily operations and staff management in a beauty salon. They ensure customer satisfaction, budget control and inventory management. Beauty salon managers set up and enforce salon rules and cleanliness guidelines. They are also in charge of promoting the salon to attract new clients.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/33e4f026-a823-4ffc-99e4-60d3fe597a1e','key_15797','','','2519','2519.7.1','digital games tester',replace('digital games tester\ndigital games testers\ndigital game tester\ncomputer games tester\nvirtual games tester\nvideo games tester','\n',char(10)),'Digital games testers review and test various genres of digital games by playing them in order to find bugs and glitches within the game''s functionality or graphics. They may evaluate the capacity of attraction and playability of games. They may also debug the games themselves.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/33fe4c90-c4fd-4860-bdc5-24fcab16f45a','key_15798','','','3111','3111.3','food analyst',replace('food researcher\nfood research specialist\nfood analysis expert\nfood analysis specialist\nfood analyst','\n',char(10)),'Food analysts perform standardised tests to determine the chemical, physical, or microbiological features of products for human consumption.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/340b04fd-6312-47e5-80ff-28222a343864','key_15799','','','3151','3151.1','fisheries refrigeration engineer',replace('fisheries refrigerating engineer\nfishery refrigeration engineer\nengineer in fisheries refrigeration\nfishing vessel refrigeration engineer\nfishing boat freezer engineer\nfisheries electrician and refrigerating engineer\nfisheries refrigeration and power engineer\nfisheries refrigeration technician\nfishing boat refrigeration engineer\nfisheries electrician\nfisheries cold storage engineer\nfisheries refrigeration engineering expert\nfisheries refrigeration engineer\nfisheries refrigeration engineering adviser\nfisheries refrigeration engineering consultant\nrefrigeration plant engineer\nfisheries electrician and refrigeration engineer\nfisheries refrigeration engineering specialist\nfishing vessel freezer engineer','\n',char(10)),'Fisheries refrigeration engineers perform maintenance and repair machines and equipment in the fish hold and refrigeration system on board of fishing vessels.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3413f234-6230-47a8-8cc7-2691fd54ce3a','key_15800','','','5311','5311.1','child care worker',replace('early childhood worker\nday care assistant\nchild care worker\npreschool worker\npreschool teacher\nnursery school worker\nnursery school assistant\nchild care assistant\nday care worker\nearly childhood assistant','\n',char(10)),'Child care workers provide care for children when the parents or family members are unavailable. They look after the children''s basic needs and help or supervise them during play. Child care workers can work for preschools, daycare centres, childcare agencies or individual families.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3417aab3-8b87-4c3b-bee6-be03fe05be2b','key_15801','','','8152','8152.3.1','tufting operator',replace('tufter\nyardage-tufting machine operator\ntufting machinist\nchenille machine operator\ntufting operator\ntufting creeler\ntufting supervisor\ntuft operator\ntufting machine operator','\n',char(10)),'Tufting operators supervise the tufting process of a group of machines, monitoring fabric quality and tufting conditions. They inspect tufting machines after set up, start up, and during production to ensure the product being tufted meets specs and quality standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/341b405b-12cd-4ece-9b4b-cbfae3551ea3','key_15802','','','3118','3118.3.3','automotive engineering drafter',replace('automotive engineering drafter\nautomotive engineering draughtsperson\nmotor vehicle drafter\nautomotive engineering draftsperson\nautomotive draftsperson\nautomotive draughter\nautomotive draughtsperson\nautomotive drafting expert\nmotor vehicle engineering drafter\nautomotive drafting adviser\nautomotive drafting specialist\nautomotive drafting consultant\nautomotive engineering draughter\nautomotive drafter','\n',char(10)),'Automotive engineering drafters convert the automotive engineers’ designs into technical drawings usually using software. Their drawings detail dimensions, fastening and assembling methods and other specifications used in the manufacture of automotive components, cars, buses, trucks and other motor vehicles.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3469cbcc-5332-41dc-977a-29cf1b7ef7be','key_15803','','','4226','4226.1.1','front line medical receptionist',replace('front desk assistant\nmedical care front line receptionist\nfront desk receptionist\nmedical receptionist\nreceptionist in GP surgery\nGP surgery receptionist\nmedical receptionist front line\nmedical institution front line receptionist\nfront line medical receptionist','\n',char(10)),'Front line medical receptionists greet clients and patients as they arrive at the medical facility and check them in, collect patient notes and make appointments work under the supervision and direction of healthcare institution manager.','',replace('Excludes people performing patients treatment. \n Excludes people performing assistance in medical treatment.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/34852729-a2f6-4063-aa90-18b3d16754f7','key_15804','','','1345','1345.1.10','university department head',replace('academic head of department\nhead of university department\nhead of department in a university\nhead of academic department\nuniversity department director\nuniversity department head\nuniversity head of department','\n',char(10)),'University department heads lead and manage the department of their discipline in which they are academic leaders and work with the faculty dean and other heads of department to deliver the agreed faculty and university strategic objectives. They develop and support academic leadership in their department, and lead entrepreneurial activity for income-generating purposes as they promote the reputation and interests of their department within the university and to a wider community in their field.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/349ee6f6-c295-4c38-9b98-48765b55280e','key_15805','','','2512','2512.1','cloud engineer',replace('cloud-native engineer\ncloud software engineer\nhybrid cloud engineer\ncloud security engineer\ncloud computing engineer\ncloud solution engineer\ncloud infrastructure engineer\ncloud developer\ncloud devops engineer\ncloud network engineer\ncloud architect','\n',char(10)),'Cloud engineers are responsible for the design, planning, management and maintenance of cloud-based systems. They develop and implement cloud-applications, handle the migration of existing on-premise applications to the cloud, and debug cloud stacks.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/34b63359-faad-4d6a-9d4d-98c2c15b9fac','key_15806','','','8160','8160.21','cocoa press operator',replace('cocoa press operator\ncocoa compression operator\ncocoa pressing operative\ncocoa compression operative\ncocoa pressing operator','\n',char(10)),replace('Cocoa press operators tend one or more hydraulic cocoa presses to remove specified amounts of cocoa butter (natural oil of cocoa bean) from chocolate liquor. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/34c191ec-e3e3-4746-ae10-7ff4306a2794','key_15807','','','8131','8131.19.2','soap drier operator',replace('flaker operator\nsoap dryer machine tender\nviscous soap machine worker\nviscous soap machine operator\nsoap drier tender\nsoap dryer operator\noperator of soap flake dryer machine\noperator of soap flake drier machine\nsoap drier operator\nviscous soap machine operative\nsoap drier machine tender','\n',char(10)),'Soap drier operators control and maintain viscous soap machine to produce soap flakes. They perform sample tests and coordinate the dried flakes discharge into storage bins.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/34c57104-ff9b-4003-809e-56eece3cb4d5','key_15808','','','2163','2163.1.5','jewellery designer',replace('jewellery designer and maker\njewellery artist & designer\njewellery artist\njewelry designer\njewelry designer and sculptural artist\njewelry artist and designer\njewelry artist\njewellery artist and designer\njewelry artist & designer\njewellery designer and sculptural artist','\n',char(10)),'Jewellery designers use a variety of materials, including gold, silver and precious stones to design and plan pieces of jewellery that can have a wearable or decorative purpose. They are involved in the different stages of the making process and may design for individual clients or for mass production clients.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/34e1c889-0fd1-4d1f-aba9-3b6263ae667a','key_15809','','','3432','3432.4','set builder',replace('set builder and designer\nproperty maker & set builder\nset designer & builder\nset constructor\nstage and set builder\nset builder and property maker\nchief carpenter\nprop maker & set builder\nset builder and dresser\nset builder & prop maker\nscenic builder\nset builder & property maker\nset and stage builder\nset dresser and builder\nset designer and builder\ncarpenter\nprop maker and set builder\nset & stage builder\nset builder\nset builder & dresser\nexhibition builder\nstage & set builder\nset builder & designer\nproperty maker and set builder\nset dresser & builder\nset builder and prop maker\nblacksmith welder','\n',char(10)),'Set builders construct, build, prepare, adapt and maintain scenic elements used on stage and for filming movies or television programs. They use a wide variety of materials like wood, steel, aluminium and plastics. Their work is based on artistic vision, scale models, sketches and plans. They work in close cooperation with the designers and may build exibition stands for fairs, carnavals and other events.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/34ec79d0-85fb-445a-a5f2-df51b70c6375','key_15810','','','7511','7511.6','slaughterer',replace('animal slaughterhouse worker\nslaughter worker\nanimal slaughterer\nslaughterer','\n',char(10)),replace(' Slaughterers slaughter animals and process carcasses for further processing and distribution. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/351a0eb0-a1d0-49bd-a2f8-6dc447ddf316','key_15811','','','2133','2133.10','pipeline environmental project manager',replace('pipeline environmental impact supervisor\npipeline environmental impact analyst\nmanager of pipeline environmental projects\npipeline environmental project supervisor\npipeline environmental impact analysis manager\nsupervisor of pipeline environmental projects\npipeline environmental impact manager\npipeline environmental project manager','\n',char(10)),'Pipeline environmental project managers ensure the accomplishment of environmental preservation within pipeline transport projects. They, together with a group of managers and specialists, analyse the sites and routes of pipelines in order to advice on environmental issues to be considered and addressed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35291723-dc32-4a7f-a9f0-39c0e5fd96dd','key_15812','','','8151','8151.2.1','man-made fiber spinner',replace('man-made fiber spinner\nfilament yarn manufacturer\noperator for synthetic and glass fiber\nman made fiber spinner\nfilament yarn or staple fiber producer\nstaple fiber manufacturer\nmanufacturer of fiber or filament out of granule\nextruding and forming machine setter\nsynthetic filament spinner','\n',char(10)),'Man-made fiber spinners perform fibre or filament processing operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3530b443-d2bf-4741-b771-12c77e7c6232','key_15813','','','1324','1324.5','movie distributor',replace('film distribution agent\ntelevision series distributor\nmotion picture distribution agent\ndistribution agent\ntelevision distribution agent\nmovie distribution agent\nfilm distributor\nmovie distributor\nmotion picture distributor','\n',char(10)),'Movie distributors are responsible for the coordination of the distribution of motion pictures and television series. They also oversee the sale of these movies on DVD, Blu ray and on other media.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/353a22a7-1c55-410c-a7a7-c531692b5e50','key_15814','','','7115','7115.6','window installer',replace('window installer\nwindow installation team member\nwindow technician\nwindow installation team worker\nwindow fitter','\n',char(10)),'Window installers place windows into structures and service them. They take out old windows if present, prepare the opening, mount the window, and attach it in place plumb, straight, square and watertight.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/353c19d6-62d6-4dfc-85e9-f59d219d457c','key_15815','','','2612','2612.1.1','supreme court judge',replace('lord advocate\nhigh court judge\njustice\njustice of the supreme court\nsupreme court judge\njustice of the high court','\n',char(10)),'Supreme court judges preside in high courts, dealing with complex criminal and civil cases. They examine the case during trials in order to formulate a sentence or to direct a jury in reaching a conclusion, and decide on any punishments if an offending party is found guilty. They rule proceedings and ensure the trial is held in a fair manner compliant with legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35553663-deab-4d9a-bf22-15c1625d28e8','key_15816','','','2511','2511.11','ICT intelligent systems designer',replace('artificial intelligence designer\ndesigner of intelligent systems\nintelligent systems designers\nintelligent system designer\nICT intelligent systems designer\nsemantic technologies engineer\nmachine learning systems designer\nlinked data engineer\nintelligent systems engineer\nAI system designer\nintelligent systems developer','\n',char(10)),'ICT intelligent systems designers apply methods of artificial intelligence in engineering, robotics and computer science to design programs which simulate intelligence including thinking models, cognitive and knowledge-based systems, problem solving, and decision making. They also integrate structured knowledge into computer systems (ontologies, knowledge bases) in order to solve complex problems normally requiring a high level of human expertise or artificial intelligence methods.','','Excludes people performing managerial activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/356227b9-2263-4dbd-81e9-7dfc6d3f3af7','key_15817','','','2431','2431.10.4','online marketer',replace('graduate online marketer\nonline marketing specialist\nonline marketer\ne-commerce marketing specialist\nsocial media marketing coordinator\ndigital marketer\ninternet marketer\ninternet marketing specialist\nonline sales channel marketer\nassistant online marketer\ne-commerce marketer\ntrainee online marketer\nsocial media marketing specialist\nsenior online marketer','\n',char(10)),replace('Online marketers use e-mail, internet and social media in order to market goods and brands.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3562e70c-016c-455c-b949-521a367418a5','key_15818','','','7318','7318.4','knitter',replace('hand knitter\nknitting and crochet designer\ntraditional knitter\nknitting artist\nknitter\nlacemaker\nknitting pattern designer\nwarp knitter\nknit designer','\n',char(10)),'Knitters create textiles or fabric by knitting pieces of yarn. They use traditional techniques to create interlocking loops of yarn which form a uniform piece of fabric. Knitters use different techniques, needles and yarn materials to create knitted materials of diverse proportions.','','Excludes <a resource="http://data.europa.eu/esco/occupation/ab104661-db96-4752-8c84-97d98f6e8969" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/ab104661-db96-4752-8c84-97d98f6e8969"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">knitting machine supervisor</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3594efa8-cd69-411e-a345-d88ba756b758','key_15819','','','7543','7543.8.3','pulp grader',replace('pulp and paper grader\npulp and paper tester\npulp and paper quality grader\npulp grader\npulp tester\npulp quality grader\npulp quality tester\npulp and paper qaulity tester','\n',char(10)),'Pulp graders grade paper pulp based on a number of possible criteria, such as pulping process, raw materials, bleaching methods, yield, and fibre length.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35bc3847-58ad-46f5-8921-e58acc2762a6','key_15820','','','3116','3116.1','chemical engineering technician',replace('chemical process engineering technician\nproduction chemistry technician\nchemical engineering worker\nchemical engineering technician\nchemical research technician\nchemical engineering developer\nchemical operations engineering technician\nchemical industry engineering technician','\n',char(10)),'Chemical engineering technicians transform raw materials in order to develop and test chemical products. They also work on improving chemical plant operations and processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35bedc78-1c15-4db7-9c6d-c55e9b5c7b54','key_15821','','','3332','3332.3','venue programmer',replace('dance programmer\nvenue programming assistant\nmusic programmer\ntheatre programmer\nvenue programer\nin-house promoter\nvenue booker & promoter\nclub programmer\nvenue booker','\n',char(10)),replace('Venue programmers are in charge of the artistic programme of a venue (theatres, cultural centers, concert halls etc.) or of temporary settings (festivals). They follow artistic trends and up-coming artists, keep in contact with bookers and agents to build a consistent programme and encourage artistic creation. All of this happens within the boundaries of the artistic and financial scope of the organisation they are involved in.  \n ','\n',char(10)),'','Includes venue booker.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35cf772d-bbc9-4eb4-9ca6-5f982649d436','key_15822','','','2149','2149.11.2','wood technology engineer',replace('wood technology engineering adviser\nwooden technology operative\nwooden technology operator\nwood technology operative\nwood technology engineer\ntimber technology technician\nwood technologist\ntimber technology engineer\ntimber products engineer\nwood tech engineer\nwooden technology technician\nwood technology engineering consultant\nwooden technology specialist\nwood technology engineering expert\nwood technology expert\nwood technology engineering specialist\nwooden technology engineer\nwooden technologist\nwood technology specialist\nwood technology technician\nwood products engineer\nwood technology operator','\n',char(10)),'Wood technology engineers develop materials and components made of wood, construct production facilities and control and monitor the production. They also examine products and materials and advise customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35d41f4f-334b-452e-9e31-99f376302493','key_15823','','','2152','2152.1.11','optoelectronic engineer',replace('specialist in optoelectronics\noptoelectronic engineering adviser\noptoelectronic engineering consultant\noptoelectronic engineer\nengineer in optoelectronic systems\noptoelectronic technology engineering expert\nengineer in optoelectronics\noptoelectronics specialist\noptoelectronic technology engineering specialist\noptoelectronic technology engineering adviser\nopto-electronics engineer\noptoelectronic technology engineering consultant\noptoelectronic engineering expert\nopto electronics engineer\nopto-electronical engineer\nsenior optoelectronic engineer\noptoelectronic systems engineer\noptoelectronics engineer\noptoelectronic engineering specialist\noptoelectronic technology engineer\nopto electronic engineer\noptoelectronical engineer\nopto electronical engineer\nopto-electronic engineer\nspecialist in optoelectronic systems','\n',char(10)),'Optoelectronic engineers design and develop optoelectronic systems and devices, such as UV sensors, photodiodes, and LEDs. Optoelectronic engineering combines optical engineering with electronic engineering in the design of these systems and devices. They conduct research, perform analysis, test the devices, and supervise the research.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35d4ab0d-5a2a-4ed8-b8ba-7169314168f5','key_15824','','','3324','3324.4.8','wholesale merchant in computers, computer peripheral equipment and software',replace('business to business sales consultant in computers, computer peripheral equipment and software\nassistant wholesale merchant in computers, computer peripheral equipment and software\nwholesaler in computers, computer peripheral equipment and software\nsenior wholesale merchant in computers, computer peripheral equipment and software\ntrainee wholesale merchant in computers, computer peripheral equipment and software\nwholesale merchandiser in computers, computer peripheral equipment and software\nB2B sales consultant in computers, computer peripheral equipment and software\nbusiness to business seller in computers, computer peripheral equipment and software\nwholesale merchant in computers, computer peripheral equipment and software\ngraduate wholesale merchant in computers, computer peripheral equipment and software\nwholesale trader in computers, computer peripheral equipment and software\nwholesale buyer in computers, computer peripheral equipment and software','\n',char(10)),'Wholesale merchants in computers, computer peripheral equipment and software investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35e42c87-faf7-465f-abe4-f26717996dd1','key_15825','','','2330','2330.1.17','religious education teacher at secondary school',replace('religious education teacher\nhigh school religious studies teacher\nsecondary school religious education teacher\nreligious education teacher high school\nreligious education teacher in secondary school\nRE teacher\nhigh school RS teacher\nsecondary school RE teacher\nhigh school religious education teacher\nteacher of religious education in high school\nhigh school RE teacher\nreligious studies teacher\nRE teacher in secondary school','\n',char(10)),'Religious education teachers at secondary schools provide education to students, commonly children and young adults, in a secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, religion. They prepare lesson plans and materials, monitor the students'' progress, assist individually when necessary, and evaluate the students'' knowledge and performance on the subject of religion through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35e67944-231e-4bc9-8c69-560f203167ff','key_15826','','','0310','0310.4','intelligence communications interceptor',replace('signal intelligence voice interceptor\ncommunications intelligence intercept operator\ncommunications interceptor, intelligence analyst\ncommunications interceptor\nelectronic intelligence intercept operator\nintelligence intercept operator\nmilitary intelligence communications interceptor\nintelligence communications interceptor\ncommunications locator','\n',char(10)),'Intelligence communications interceptors work in the air force in the development of intelligence in places like headquarters and command posts. They search and intercept electromagnetic traffic transmitted in different languages.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35eb0aa6-b40d-449d-9390-719be8912277','key_15827','','','3432','3432.6','visual merchandiser',replace('visual merchandising supervisor\nwindow display assistant\nassistant visual merchandiser\nsenior visual merchandiser\nretail visual strategy manager\nvisual merchandiser\ngraduate visual merchandiser\ndisplay assistant\nvisual merchandising manager\nvisual merchandising controller\nimage controller\ndisplay designer\nvisual merchandising associate\nwindow dresser','\n',char(10)),'Visual merchandisers are specialised in the promotion of the sale of goods, especially their presentation in retail outlets.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35efc04c-429a-407e-a00a-f07fd03c2743','key_15828','','','7422','7422.4','radio technician',replace('radio technicians\nradio technician\nradio communications technician','\n',char(10)),'Radio technicians install, adjust, test, maintain, and repair mobile or stationary radio transmitting and receiving equipment and two-way radio communications systems. They also monitor their performance and determine causes of faults.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35f62568-eb4e-4a09-9d83-ecc73116f94c','key_15829','','','7512','7512.2','chocolatier',replace('chocolate producer\nchocolate goods maker\nchocolate maker\nchocolatier','\n',char(10)),replace(' Chocolatiers make confectionery products with chocolate. They perform activities such as examination, feeling, and tasting of ground chocolate paste. Such analysis leads them to ascertain if colour, texture, and taste of the chocolate paste meets specifications. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/35fbb8cd-c044-49a1-850c-010481c99b4a','key_15830','','','4312','4312.2.1','financial markets back office administrator',replace('operations officer\noperations manager\nsettlements clerk\nback office administrator\nsettlements officer\nback office payments assistant\nbroker assistant\nfinancial markets back office operator\nfinancial markets back office administrator\nbrokerage clerk\nsettlements manager\nbrokerage assistant\nsettlements administrator\nbroker clerk','\n',char(10)),'Financial markets back office administrators perform administrative tasks for all the transactions registered in the trading room. They process transactions involving securities, derivatives, foreign exchange, commodities, and manage the clearing and settling of trades.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/36095ccd-c62d-4f1f-8af7-1fd10618b044','key_15831','','','3521','3521.1.8','sound editor',replace('dialogue editor\nmusic supervisor\ndubbing manager\nsound designer\naudio editor\nADR editor\nfoley editor\nmusic editor\nautomatic dialogue replacement editor\nsound editor\nfoley artist','\n',char(10)),'Sound editors create the soundtrack and sound effects for motion pictures, television series or other multimedia productions. They are responsible for all the music and sound featured in the movie, series or videogames. Sound editors use equipment to edit and mix image and sound recordings and make sure that the music, sound and dialogue is synchronised with and fits in the scene. They work closely together with the video and motion picture editor.','',replace('Includes people producing new music especially designed for a movie. \nIncludes people working with music composers.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/36494988-69d2-4420-9db4-eb56605ac983','key_15832','','','2221','2221.1','advanced nurse practitioner',replace('clinical trials nurse\npublic health development manager\nadvanced nurse, team leader\nhead of community nursing\nclinical matron\ninfection control nurse\nservice development and governance manager\nconsultant health visitor\nclinical academic\nnamed nurse child protection\noperating room nurse manager\nstaff development manager\nadvanced nursing practitioner\nservice manager\nadvanced nurse\nclinical academic nurse specialist\nlead practice nurse\nadvanced nurse manager\nteam manager\nadvanced nurse practitioner\ncommunity team leader','\n',char(10)),'Advanced nurse practitioners are in charge of promoting and restoring patients` health, provide diagnosis and care in advanced settings, coordinating care within areas of chronic disease management, providing integrated care, and supervising assigned team members. Advanced nurse practitioners are general care nurses who have acquired an expert knowledge base, complex decision making skills and clinical competencies for expanded clinical practice on advanced level.','','','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/364cb5ca-3002-425f-b815-aefa513df78a','key_15833','','','3118','3118.3.2','architectural drafter',replace('architectural drafting expert\narchitecture draughtsperson\narchitectural draughtsperson\narchitectural drafter\narchitectural draftsperson\narchitectural drafting consultant\narchitecture draftsperson\narchitectural engineering drafter\narchitectural drafting adviser\narchitecture draughter\narchitectural drafting specialist\narchitecture drafter\narchitectural draughter','\n',char(10)),'Architectural drafters make drawings of the specifications and ideas provided by architects. They draw architectural drawings using computer-aided equipment and software, or using conventional methods like paper and pen.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/364d9e9f-b14c-4905-b9e5-5de623dba268','key_15834','','','2166','2166.14','stop-motion animator',replace('stop motion animation specialist\n3D stop-motion animator\npuppet animator\nstop motion animator\nstop motion animators\n2D stop-motion animator\nstop-motion clay animator\nstop-motion animator\nstop-motion animation artist\nstop motion animation designer\nstop-motion artist\nstop motion artist\nstop motion animation artist\nstop-motion puppet animator\nmodel animator','\n',char(10)),'Stop-motion animators create animations by using puppets or clay models.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/364e61ab-1631-4fe1-a170-e88797aad6f9','key_15835','','','8111','8111.2','driller',replace('earth driller\ndriller\ndrillers assistant\nassistant driller','\n',char(10)),'Drillers set up and operate drilling rigs and related equipment designed to drill holes for mineral exploration, in shotfiring operations, and for construction purposes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/36b11444-8634-4194-b679-ed302acdfb22','key_15836','','','8160','8160.23','coffee roaster',replace('coffee bean roasting specialist\ncoffee roaster\ncoffee bean roasting expert\ncoffee bean roaster','\n',char(10)),replace('Coffee roasters control gas fired roasters to dry coffee beans. They dump coffee beans into roasting ovens and once roasted, they compare colour of roasting beans against specifications. They perform cooling of the beans by operating mechanical blowers. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/371885b5-0f93-406f-b96a-58ecd5bbc3ce','key_15837','','','7543','7543.9.2','leather goods quality controller',replace('leather goods quality analyst\nleather goods quality control officer\nleather goods quality controller\nleather goods quality inspector','\n',char(10)),'Leather goods quality controllers analyse visual aspects of the leather goods related to appearance, robustness, absence of wrinkles, straightness of seams, cleanliness and colour uniformity, compared to the specifications. They also perform expedite tests.','',replace('Excludes people performing laboratory control tests. \nExcludes people performing managerial activities.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3723d7a6-7e65-4735-8235-cb01fb661b2f','key_15838','','','8171','8171.6','paper pulp moulding operator',replace('paper pulp moulding machinist\npaper pulp moulding machine specialist\npaper pulp moulding technician\npaper pulp moulding worker\npaper pulp moulding operative\npaper pulp moulding specialist\npaper pulp moulding machine operator\npaper pulp molding operator\npaper pulp moulding machine technician','\n',char(10)),'Paper pulp moulding operators tend a machine that moulds paper pulp in various shapes, usually for use in lightweight but sturdy packaging material, such as egg boxes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/373db468-bd25-4b67-a6e0-4ac79d9a9e85','key_15839','','','9211','9211.1','fruit and vegetable picker',replace('fruit picker\nfruit and vegetable harvester\nfruit & vegetable picker\nberry picker\nvegetable picker\nfruit and veg harvester\nfruit and vegetable picker\nfarm hands\nfruit and veg picker\ncrop harvester\ncrop picker','\n',char(10)),'Fruit and vegetable pickers select and harvest fruits, vegetables and nuts according to the method appropriate for the type of fruit, vegetable or nut.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/37442765-f7bb-4ccd-9de1-4790296f3d37','key_15840','','','5414','5414.1.9','security guard supervisor',replace('guard manager\nguard supervisor\ncommercial security officers supervisor\nguard chief\nsecurity guard supervisor\nsecurity officers manager\nsecurity chief\nsecurity guard manager\nsecurity supervisor\nsecurity guard chief','\n',char(10)),'Security guard supervisors monitor and oversee the activities of guards who protect properties from vandalism acts and theft. They assign areas to be patrolled by guards on a regular basis, transfer the individual caught trespassing to police custody and develop safety plans and drills for the buildings and employees under their supervision.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/374d925a-107b-4c99-bc8b-75483441f062','key_15841','','','2651','2651.1','artistic painter',replace('painting & drawing artist\ncreative painter\npainting artist\ndrawing & painting artist\nartistic painter\ndrawing and painting artist\nsign painter\nmatte painter\npainting and drawing artist\nstreet painter\nvisual artist','\n',char(10)),'Artistic painters create paintings in oil or water colours or pastel, miniatures, collages, and drawings executed directly by the artist and/or entirely under their control .','','Excludes people performing non-artistic painting.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/375137f7-5fc2-424e-9f63-d7ff4441c11e','key_15842','','','7313','7313.1.3','jewellery engraver',replace('jewelry etcher\njewelry engraver\ngold etcher\ngold engraver\njewellery etcher\njewellery engraving designer\nsilver etcher\njewelry decorator\njewellery decorator\njewellery hand engraver\nsilver engraver\njewelry engraving designer\nmachine engraver\njewellery hand etcher\nmachine etcher\njewelry finisher','\n',char(10)),'Jewellery engravers engrave lettering and ornamental designs onto jewellery articles, using engravers handtools. They sketch and lay out the lettering and designs on the article, cut the design in the article and clean it up.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/375a4d32-0950-45db-9d85-3fc2a86f1d85','key_15843','','','8160','8160.50','starch converting operator',replace('starch converter\nstarch converting operator\nstarch conversion worker\nstarch worker\nstarch operator','\n',char(10)),'Starch converting operators control converters to change starch into glucose or corn syrup. After processing, they test products to verify their purity.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3765a20d-5160-4e8f-82f1-ad7872b9db24','key_15844','','','2622','2622.1','information manager',replace('knowledge manager\ndocumentalist\ninformation manager\ninformation officer\ninformation scientist\nacademic information coordinator','\n',char(10)),'Information managers are responsible for systems that provide information to people. They assure access to the information in different work environments (public or private) based on theoretical principles and hands-on capabilities in storing, retrieving and communicating information.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/377865f0-c327-4599-8c7e-d428c6588edf','key_15845','','','1324','1324.8.2','purchasing manager',replace('purchasing coordinator\npurchasing office administrator\nprocurement and supply manager\npurchasing manager\nsupply manager\npurchasing administrator\npurchasing and supply office administrator\nbuying manager','\n',char(10)),'Purchasing managers are in charge of buying goods, equipment and services for their company, and try to ensure the most competitive prices. They are also responsible for negotiating contracts, reviewing the quality of products and analysing suppliers, and for the use and resale of goods and services.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/379a4f3f-4985-4764-8b2f-037946da855d','key_15846','','','3359','3359.6','gaming inspector',replace('gambling investigator\ngambling supervisor\ngaming investigator\ngambling inspector\ngaming inspector','\n',char(10)),'Gaming inspectors, have the ability to deal and inspect all the respective games and carry out all gaming operations to the highest possible standards of efficiency, security and customer service in accordance with company procedures and current legislation. Their responsibility is to oversee all the irrespective games played, and ensure that rules are adhered to and players don''t cheat. They are responsible for the maintenance of the equipment and handle all customer questions and complaints.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/37b15470-3b2d-4ae0-816c-c7c06ed15c99','key_15847','','','4411','4411.1','library assistant',replace('library assistant\nlibrary technician\nlibrary clerk\nassistant librarian\nlibrary services assistant\nsenior library assistant','\n',char(10)),'Library assistants assist the librarian in the day-to-day activities of the library. They help clients find the materials they need, check out library materials and restock the shelves. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/37c44fdd-eff0-4e16-b459-b3a81fe663ec','key_15848','','','2320','2320.1.13','food service vocational teacher',replace('food service vocational instructor\ncooking teacher\ncooking instructor\nfood service vocational teacher\nvocational teacher in food service\nfood technology instructor\nfood service teacher\nteacher of food and hospitality\nvocational instructor in food service\nfood technology teacher','\n',char(10)),'Food service vocational teachers for food service instruct students in their specialised field of study, food service, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for a food service-related profession. Food service vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of food service through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/37c7880e-cfee-4c41-a41b-061682fefde3','key_15849','','','7511','7511.1','butcher',replace('butcher\nbutchery expert\nbutchery worker\nanimal butcher','\n',char(10)),replace(' Butchers order, inspect and buy meat to prepare it and sell it as consumable meat products. They perform activities such as cutting, trimming, boning, tying, and grinding beef, pork, and poultry meat. They prepare those mentioned sorts of meat for consumption. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/37f7677a-97d1-420b-a8b0-09339472121f','key_15850','','','3122','3122.3.4','machinery assembly supervisor',replace('leadingmachinist\nlead machine operative\nmachinery assembly supervisor\nmachine charge hand\nmachine team leader','\n',char(10)),'Machinery assembly supervisors monitor the machinery assembly process. They train and coach assembly workers to achieve production goals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/37f92bd9-b14c-4ab5-82fb-545ba7ba3b8b','key_15851','','','1324','1324.3.1.6.5','china and glassware distribution manager',replace('china and glassware supply chain planner\nchina and glassware supply chain manager\ngraduate china and glassware distribution manager\nchina and glassware distribution manager\nsenior china and glassware distribution manager\ntrainee china and glassware distribution manager\nchina and glassware distribution planner\nassistant china and glassware distribution manager\nchina and glassware logistics planner\nchina and glassware logistics manager','\n',char(10)),'China and glassware distribution managers plan the distribution of china and glassware to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/381b9a17-521c-4702-b7d9-2059bbd8ce76','key_15852','','','7543','7543.10.2','clothing quality inspector',replace('clothing quality inspector\nclothing quality control inspector\ngarment quality controller\ngarment and textile quality controller\nclothing quality control supervisor','\n',char(10)),'Clothing quality inspectors inspect manufactured components and ready-made garments in order to classify them according to their quality by ensuring compliance with quality standards and identifying defects or deviations from specifications. They inspect and test products, parts and materials for conformity with specifications and standards. They ensure all work produced meets or exceeds the department''s quality standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/38240cc5-d1d9-4de9-9480-29c51da291eb','key_15853','','','3118','3118.3.11','mechanical engineering drafter',replace('mechanical drafting consultant\nmechanical design draughting inspector\nmechanical drafting specialist\nmechanical drafting adviser\nmechanical technology drafting specialist\nmechanics engineering draftsperson\nmechanics engineering draughtsperson\nmechanical design drafting operative\nmechanical design draughting operator\nmechanics engineering draughter\nmechanical design draughter\nmechanical technology drafter\nmechanical design drafting inspector\nmachinery engineering draughtsperson\nmechanical design draughting supervisor\nmechanical engineering drafter\nmechanical engineering drafting apprentice\nmachinery engineering draftsperson\nmechanical drafting expert\nmachinery engineering drafter\nmechanical draughter\nmachinery engineering draughter\nmechanical technology drafting consultant\nmechanical design drafting operator\nmechanical design draughting operative\nmechanical draftsperson\nmechanical engineering draughting apprentice\nmechanical design drafter\nmechanical design drafting supervisor\nmechanical technology drafting adviser\nmechanical draughtsperson\nmechanical technology drafting expert\nmechanical design drafting engineer\nmechanical design draughting engineer','\n',char(10)),'Mechanical engineering drafters convert mechanical engineers'' designs and sketches into technical drawings detailing dimensions, fastening and assembling methods and other specifications used for example in manufacturing processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3826cb92-27f5-40ac-8dad-70af5abd6036','key_15854','','','2422','2422.12.2','competition policy officer',replace('competition policy manager\ncompetition policy officer\nconsumer protection officer\ncivil servant\nentrepreneurship policy officer\npolicy manager for competition\npolicy manager','\n',char(10)),'Competition policy officers manage the development of regional and national competition policies and law, in order to regulate competition and competitive practices, to encourage open and transparent trade practices and to protect consumers and businesses.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/38395ab3-766c-4c6c-8d96-430eff19ec8d','key_15855','','','5223','5223.1','car leasing agent',replace('senior car leasing agent\nvehicle fleet leasing consultant\ncar leasing team leader\nvehicle leasing sales agent\ncar leasing office manager\nassistant car leasing agent\nvehicle fleet leasing agent\ngraduate car leasing agent\ncar leasing team worker\ntrainee car leasing agent\nvehicle leasing consultant\ncar leasing office worker\nautomobile leasing agent\nfleet leasing consultant\ncar leasing agent\nautomobile leasing representative\nfleet leasing agent','\n',char(10)),'Car leasing agents represent businesses involved in financing vehicles, offering appropriate leasing schemes and additional services related to the vehicle. They document transactions, insurances and instalments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/384ed128-e86e-4dd8-96ff-b6e6657cb027','key_15856','','','3115','3115.1.9','marine surveyor',replace('maritime ship inspector\nmarine surveyor\nyacht and small craft surveyor\ninland water ship inspector\ninland waterway vessel inspector\ncargo surveyor\nhull and machinery surveyor\nmarine warranty surveyor\nmarine surveyors','\n',char(10)),'Marine surveyors inspect vessels intended for operations in maritime or open sea waters. They ensure that vessels and equipment follow the regulations laid down by the International Maritime Organisation (IMO). They may also act as third parties for the review of offshore facilities and construction projects.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3880e9e8-bc2b-48d9-8afc-a0f258c1c4b0','key_15857','','','3355','3355.2','police detective',replace('detective inspector\ndetective superintendent\npolice detective\ndetective constable\nchief detective','\n',char(10)),'Police detectives gather and compile evidence which assists them in the solution of crimes. They use investigative techniques to gather evidence, and interview all parties connected to their line of enquiry, and cooperate with other police department divisions to gather the evidence.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/388155cc-7ab8-46c7-8051-aefc7b276c84','key_15858','','','3422','3422.5','sports official',replace('off-court official\nreferee\ntechnical official\nassistant referee\nplate umpire\njudge\nsport official\non-ice official\non-the-water official\ntimekeeper\narbiter\ntouch judge\nlinesman\nbasketball crew chief\ncommissaire\nfield umpire\narbitrator\nmatch referee\numpire\non-court official','\n',char(10)),'Sports officials are responsible for administering the rules and laws of a sport and for ensuring fair play according to the rules and laws. The role includes applying rules during the sport or activity, contributing to health, safety and protection of participants and others during the sport or activity, organising sport events, establishing and maintaining effective working relationships with competitors and others, and communicating effectively.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/38a7f453-a7f0-45d2-a74d-5b92506e4a21','key_15859','','','3114','3114.1.2','computer hardware engineering technician',replace('IT engineering technician\ncomputer hardware engineering technician\ntechnician in IT hardware\ncomputer hardware technician\nPC hardware engineering technician\nPC hardware technician\nIT hardware engineering technician\ncomputer engineering technician\ntechnician in computer hardware engineering\ntechnician in computer hardware','\n',char(10)),'Computer hardware engineering technicians collaborate with computer hardware engineers in the development of computer hardware, such as motherboards, routers, and microprocessors. Computer hardware engineering technicians are responsible for building, testing, monitoring, and maintaining the developed computer technology.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/38d9c76b-1a7b-4d65-879b-733ae7e9364e','key_15860','','','2412','2412.6','investment adviser',replace('financial and investment adviser\ninvestment advisor\ninvestment consultant\ninvestment adviser\ncorporate investment adviser\ninvesting director\ninvestment executive\ninvestment manager\nRIA','\n',char(10)),'Investment advisers are professionals who offer transparent advice by recommending suitable solutions on financial matters to their clients. They advise on investing pension or free funds in securities such as stocks, bonds, mutual funds and exchange-traded funds to customers. Investment advisers serve individuals, households, families and owners of small companies.','',replace('Excludes people working with large companies or institutions. \nIncludes people working with individual clients and small companies.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/38eb308f-0075-451e-9c1a-c557fd173022','key_15861','','','5153','5153.1.1','amusement park cleaner',replace('cleaners\njanitorial worker\namusement park cleaner\ncleaning worker\npremises maintenance worker\namusement park janitor','\n',char(10)),'Amusement park cleaners work to keep the amusement park clean and take on small repairs. Amusement park cleaners usually work at night, when the park is closed, but urgent maintenance and cleaning is done during the day.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3900b684-6b4b-4133-b134-e1d129d60383','key_15862','','','5249','5249.2','rental service representative',replace('rental sales assistant\nrental sales supervisor\nrental advisor\nrental service manager\nrental service worker\nrental specialist\nrental service desk agent\nrental service representative\nrental service assistant\nrental sales desk supervisor\nrental service team leader\nrental sales desk agent\nrental service supervisor\nrental sales manager\nrental service desk clerk\nrental sales team leader\nrental sales worker\nrental service desk supervisor\nrental sales desk clerk','\n',char(10)),'Rental service representatives are in charge of renting out equipment and determining specific periods of usage. They document transactions, insurances and payments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/394a4bdd-7032-4d87-89a2-9d937cebdd09','key_15863','','','2631','2631.1','economic policy officer',replace('economic policy analyst\neconomic policy specialist\neconomic policy officer\nfinance policy officer\neconomic policy advisor\npolicy officer economics\npolicy officer for economy\neconomical policy officer\neconomic policy researcher','\n',char(10)),'Economic policy officers develop economic strategies. They monitor aspects of economics such as competitiveness, innovation and trade. Economic policy officers contribute to the development of economic policies, projects and programs. They research, analyse and assess public policy problems and recommend appropriate actions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/394bdd9b-3ee8-43a9-b943-442ce146bb21','key_15864','','','3423','3423.2.4','sport therapist',replace('sport and exercise therapist\nsport therapist\nhealth exercise specialist\nsport & exercise therapist\nexercise therapist\nexercise specialist\nexercise consultant','\n',char(10)),'Sport therapists program and supervise rehabilitation exercises for individuals and groups. They work with individuals that have chronic health conditions or are at high risk of developing them. They communicate with medical and healthcare professionals about participants'' conditions using correct medical terminology, and with an understanding of the standard treatment options for an individual’s condition. Sport therapists take a holistic approach to the wellness of their clients that includes advising on lifestyle, food or time management. They do not have a medical background and do not require medical qualifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/39803100-c338-4f01-ad2c-085d488920ca','key_15865','','','3331','3331.2','forwarding manager',replace('freight forwarding agent\nforwarding manager\nexport manager\nfreight forwarding, head\nhead of freight forwarding\nfreight forwarding manager\nforwarding supervisor\nlogistics line manager','\n',char(10)),'Forwarding managers plan and organise cargo shipments within national and international areas. They communicate with carriers and negotiate the best way to send the cargo to its destination which can be a single customer or a point of distribution. Forwarding managers act as experts in supply chain management. They know and apply the rules and regulations for each specific type of cargo and communicate the conditions and costs to the clients.','','Includes freight forwarding agents.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/399afb40-d58b-4f4a-aa52-a0378fccaf65','key_15866','','','5223','5223.7.28','pet and pet food specialised seller',replace('animal food specialised seller\npet salesperson\npet superstore salesperson\npet food specialised seller\nanimal salesperson\npet  food salesperson\npet and pet food salesperson\nanimal food salesperson\npet superstore specialised seller\npet supplies salesperson\nanimal specialised seller\npet specialised seller\npet supplies specialised seller\npet and pet food superstore salesperson\nanimal care products salesperson\nanimal care products specialised seller\npet and pet food superstore specialised seller\npet and pet food specialized seller','\n',char(10)),'Pet and pet food specialised sellers sell pets, pet foods, accessories, care products and related services in specialised shops.','',replace('Excludes <a resource="http://data.europa.eu/esco/occupation/5a940ae0-3fa4-40f9-8ca4-17bf792da243" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/5a940ae0-3fa4-40f9-8ca4-17bf792da243"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">animal groomers</span></a>. \n Excludes <a resource="http://data.europa.eu/esco/occupation/fc1da244-880f-407d-bfa7-789a2f468ea0" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/fc1da244-880f-407d-bfa7-789a2f468ea0"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">animal trainers</span></a>.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/39a59c5c-4338-4480-bdc8-96328a491907','key_15867','','','9629','9629.3','attraction operator',replace('attraction supervisor\nattraction & ride operator\nattraction and ride operator\nride supervisor\nride leader\nride & attraction operator\nride operator\nsenior ride attendant\nattraction operator\nfairground attraction operator\nride and attraction operator','\n',char(10)),'Attraction operators control rides and monitor the attraction. They provide first aid assistance and materials as needed, and immediately report to the area supervisor. They conduct opening and closing procedures in assigned areas.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/39b4e367-94b5-49fe-bcd7-4b116865ca59','key_15868','','','3331','3331.2.1.14','import export specialist in fruit and vegetables',replace('assistant import export specialist in fruit and vegetables\ntrainee import export specialist in fruit and vegetables\ninternational trade compliance specialist in fruit and vegetables\ngraduate import specialist in fruit and vegetables\nsenior import specialist in fruit and vegetables\ntrainee export specialist in fruit and vegetables\nsenior import export specialist in fruit and vegetables\ntrainee import specialist in fruit and vegetables\nassistant import specialist in fruit and vegetables\ngraduate import export specialist in fruit and vegetables\nimport export specialist in fruit and vegetables\ngraduate export specialist in fruit and vegetables\ncustoms compliance specialist in fruit and vegetables\nassistant export specialist in fruit and vegetables\nsenior export specialist in fruit and vegetables','\n',char(10)),'Import export specialists in fruit and vegetables have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/39c89133-bd2a-45db-b3eb-529b94daeeaf','key_15869','','','3354','3354.2','licensing officer',replace('fire protection specialist\nlicensing compliance officer\nlicensing officer\nlicensing standards officer\nbusiness permit licensing officer\nrestaurant licensing officer\ndriver licensing officer\nlicense inspector\npatents officer\nbuilding authority officer\nlicensing examiner\nbuilding permit licensing officer\ntechnology licensing officer\nfire protection licensing officer\nalcohol licensing officer','\n',char(10)),'Licensing officers process licence applications and provide advice on licensing legislation. They also perform investigative duties to ensure the applicant is eligible for the requested licence, ensure licence fees are paid in a timely manner, and ensure compliance with legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/39d7dca7-5e0b-4308-889e-9e3d22a3cb56','key_15870','','','2320','2320.1.19','occupational driving instructor',replace('PCV driving instructor\npassenger carrying vehicle instructor\ninstructor in occupational driving\nspecialist driving instructor\nLGV driving instructor\noccupational driving instruction practitioner\nheavy equipment operator instructor\nHGV driving instructor\noccupational driving instructor\noccupational driving teacher','\n',char(10)),'Occupational driving instructors teach occupational drivers how to operate their vehicles according to driving regulations. They teach their students theory and techniques on how to optimally drive and maintain their specific vehicle, observe and evaluate the students'' practice. They also focus on non-driving related subjects such as customer service (in case of person transportation drivers) and safety measure regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/39e06c20-d262-44f9-a6a8-a0f3ba9e887c','key_15871','','','8131','8131.19','soap maker',replace('soapmaker\nsoap boiler\nsoap factory operator\nmaker of soap\nsoap maker\nsoap and cosmetics maker\nartisan soap maker','\n',char(10)),'Soap makers operate equipments and mixers that produce soap, making sure the end product is produced according to specified formula.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/39e9fc8f-02bd-4577-b603-2dc96204ce74','key_15872','','','2422','2422.2','community development officer',replace('social development officer\ncommunity development officer\nlocal community development officer\noutreach support worker','\n',char(10)),'Community development officers develop plans to improve quality of life in local communities. They investigate and assess the community''s issues and needs, manage resources, and develop implementation strategies. They communicate with the community for investigation purposes, and to inform the community on development plans.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3a15ec1b-9250-41a0-9344-feb2956481b7','key_15873','','','8331','8331.1.1','trolley bus driver',replace('trolley bus driver\nbus operator\ntransit bus driver\nguided bus driver\ntrolley bus operator\ntrolleybus driver\ntrolleybus operator\ndriver of trolley buses','\n',char(10)),'Trolley bus drivers operate trolley buses or guided buses, take fares, and look after passengers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3a17bd32-8a00-47cb-82aa-2b5660cf1192','key_15874','','','5223','5223.7.3','audiology equipment specialised seller',replace('audiology shop salesperson\naudiology centre salesperson\nhearing help specialised seller\naudiology salesperson\naudiology specialised seller\nhearing help salesperson\naudiology shop specialised seller\naudiology equipment specialized seller\nhearing aid salesperson\naudiology centre specialised seller\naudiology equipment salesperson\nhearing aid specialised seller','\n',char(10)),'Audiology equipment specialised seller sell goods and equipment in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3a3fac9d-323e-4ba6-989b-0e1d44e113cc','key_15875','','','8350','8350.6','ordinary seaman',replace('seamanship apprentice\nordinary seawoman\nOS\nseaman recruit\nordinary seaman','\n',char(10)),'Ordinary seamen occupy the lowest rank of a maritime ship’s deck crew. They comprise the main labour force on board a boat where they help to operate the ship. They are supervised by the ship captain and the engineer, and can be given instructions by any individuals ranked above them.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3a4c4e33-ee34-4abe-ba5c-26f9037d292f','key_15876','','','1420','1420.6','trade regional manager',replace('assistant trade regional manager\narea sales manager\nregional sales and marketing manager\nsenior trade regional manager\nstore trade regional manager\ntrade regional manager\ntrainee trade regional manager\nregional sales manager\ngraduate trade regional manager\nregional product sales manager','\n',char(10)),replace('Trade regional managers are responsible for activities and staff in an assigned region for a specific chain of stores.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3a55bf97-a0d2-47a0-a8b3-b248d32c7969','key_15877','','','7316','7316.2','glass engraver',replace('diamond point glass engraver\ndecorative glass engraver\nglass decorator\nprecision etcher\nornamental glass engraver\nglass hand etcher\nprecision engraver\nglass etcher\nglass engraver\nheritage glass engraver','\n',char(10)),'Glass engravers engrave lettering and ornamental designs onto glass articles, using engravers hand tools. They sketch and lay out the lettering and designs on the article, cut the design in the article and finish it.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3a55ef85-5abf-48e2-884b-5efaf881bfb1','key_15878','','','2310','2310.2','social work practice educator',replace('practice social work educator\npractice placement coordinator\nsocial work field instructor\npractice placement worker\npractice placement facilitator\nsocial work practice educator\npractice-based educator\npractice educator','\n',char(10)),'Social work practice educators teach, supervise and assess social work students prior, during and after they have obtained their degree. They contribute to their placement and have the authority to recommend students on the basis of appropriate evidence.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3a67544c-919f-4051-b812-c08e69eec3fd','key_15879','','','8131','8131.18','plodder operator',replace('soap production worker\nsoap compression machine operative\nplodding-machine operative\nplodder operative\nplodder operator\nplodding-machine operator\nplodding-machine minder\nsoap production operative\nplodder worker\nsoap compression machine worker\nsoap compression machine operator\nplodding-machine worker','\n',char(10)),'Plodder operators control the milled soap compression machine that produces specific shapes and sizes of soap bars, ensuring the products conform to specifications and quality requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3a75b477-e151-40fb-9909-b3641401bfed','key_15880','','','7543','7543.1','automated optical inspection operator',replace('AOI machine operator\nprinted circuit board inspector\nAOI operator\nautomated optical inspection operator\noperator of automated optical inspection equipment\nprinted circuit board assembly inspector\nvision system operator\nautomated optical inspection machines operator\nPCB inspector\nPCB assembly inspector','\n',char(10)),'Automated optical inspection operators (AOI operators) operate automated optical inspection machines to inspect assembled printed circuit boards. They read blueprints and inspect the finished or in-process PCB assemblies for flaws or defects.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3a8a7f87-d394-42cd-804b-7a32375b90f0','key_15881','','','7223','7223.4.5','metal sawing machine operator',replace('cut-off operative\nmetal cut-off saw operator\nmetal sawing machine technician\nsaw shop worker\nmetal cut-off saw trimmer\nmetal power saw operator\nmetal trimmer\nsawing machine operative\nmachine saw operator\nmetal cutter\nmetal sawing machine operator\nsaw operator\nshear saw operator\nmetal power saw trimmer\nsaw shop operator\nmetal sawing machine operative\nmetal sawing machine worker\nmetal saw operator\ncut-off operator\nmetal sawyer\nmetal power saw technician\nmetalworking power saw operator\nshear operator\nmachine power saw operator\nmetal cut-off saw operative','\n',char(10)),'Metal sawing machine operators set up and operate metal sawing machines designed to cut excess metal from a metal workpiece by the use of a (or several) large toothed-edges blade(s). They also trim clean finished shapes out of metal using tin snips, metal shears or wire cutters.  They also smoothen and trim sharp or rough edges using various tools.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3a8ac6cd-b59a-4b93-b071-0324e895b8e4','key_15882','','','1420','1420.4.9','building materials shop manager',replace('building equipment shop manager\nbuilding materials shop manager\nbuilders merchants trade counter manager\nbuilders merchants store manager\nconstruction materials store manager\nbuilding materials DIY shop manager\nconstruction materials shop manager\nbuilders merchants shop manager\nbuilding materials store manager\nbuilding materials do it yourself shop manager\nbuilding materials do it yourself store manager\nbuilding materials DIY store manager','\n',char(10)),'Building materials shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3a959644-beed-4afc-8e57-d5068ae6a43e','key_15883','','','9331','9331.1','bicycle courier',replace('bike courier\nbicycle dispatch rider\nbicycle letter carrier\nbicycle courier\nbicycle package carrier\ncycle courier\nbike messenger\nbicycle messenger\ncycle messenger','\n',char(10)),'Bicycle couriers collect and deliver packages and mail by bicycle.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3ac7fbb4-91b2-4d4d-abc8-7a6c55ed648f','key_15884','','','3324','3324.4','wholesale merchant',replace('graduate wholesale merchant\nbusiness to business sales consultant\nwholesale merchant\nsenior wholesale merchant\nassistant wholesale merchant\ntrader (wholesale)\nbusiness to business seller\nwholesale buyer\nB2B sales consultant\nwholesale merchandiser\nwholesale trader\nwholesaler\ntrainee wholesale merchant','\n',char(10)),'Wholesale merchants investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3ae4fd99-814a-4d06-a7db-5ee680c22e26','key_15885','','','3339','3339.8','tourism contract negotiator',replace('contract negotiator\nhotel contracts negotiator\ntravel contract negotiator\ncontracts negotiator\nhotel contract negotiator\ncontract manager\ntourism contract negotiator','\n',char(10)),'Tourism contract negotiators negotiate tourism-related contracts between a tour operator and tourism service providers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3ae7c42b-7929-402f-bcbc-882b8679abf4','key_15886','','','3331','3331.2.1.16','import export specialist in hardware, plumbing and heating equipment',replace('graduate import specialist in hardware, plumbing and heating equipment\nsenior export specialist in hardware, plumbing and heating equipment\nsenior import specialist in hardware, plumbing and heating equipment\ngraduate import export specialist in hardware, plumbing and heating equipment\nassistant export specialist in hardware, plumbing and heating equipment\ntrainee import specialist in hardware, plumbing and heating equipment\nassistant import export specialist in hardware, plumbing and heating equipment\ntrainee import export specialist in hardware, plumbing and heating equipment\nassistant import specialist in hardware, plumbing and heating equipment\nsenior import export specialist in hardware, plumbing and heating equipment\nimport export specialist in hardware, plumbing and heating equipment\ntrainee export specialist in hardware, plumbing and heating equipment\ngraduate export specialist in hardware, plumbing and heating equipment\ncustoms compliance specialist in hardware, plumbing and heating equipment','\n',char(10)),'Import export specialists in hardware, plumbing and heating equipment have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3af4d6be-90b5-41f6-be06-3330dab3df73','key_15887','','','2632','2632.6.1','social work researcher',replace('social research interviewer\nsocial science researcher\nsocial work research manager\nsocial work research assistant\nsocial work research officer\nsocial work researcher\nsocial research worker\ncommunities research worker\nsocial scientist','\n',char(10)),'Social work researchers manage research projects aiming to investigate and provide reports on social issues. They first perform research by gathering information through interviews, focus groups and questionnaires; followed by organising and analysing the gathered information using computer software packages. They analyse social problems and needs, and the different ways and techniques to respond to them.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3afd6af4-0c34-4311-ad73-65adc2571f0c','key_15888','','','1321','1321.2.1.2','clothing operations manager',replace('clothing operations manager\nclothing operations director\nclothing operations area manager\nclothing production manager','\n',char(10)),'Clothing operations managers schedule orders and delivery times in order to ensure the efficient flow of the production system.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3b018148-8e92-4177-9267-5f090386f6a3','key_15889','','','2132','2132.4','fisheries adviser',replace('marine fisheries consultant\nsenior fisheries specialist\nfisheries adviser\nsenior fisheries biologist\nfisheries scientist\nfisheries specialist\nmarine fisheries scientist\nfisheries consultant\nmarine fisheries adviser\nmarine fisheries biologist\nfisheries biologist\nfisheries sustainability consultant','\n',char(10)),'Fisheries advisers provide consultancy on fish stocks and their habitats. They manage the costal fishing business modernisation and provide improvement solutions. Fisheries advisers develop plans and policies for fisheries management. They may provide advice on protected farms and wild fish stock.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3b129d8d-268e-465a-b0c5-bf32cae59a41','key_15890','','','2164','2164.2','mobility services manager',replace('mobility manager\nsmart city planner','\n',char(10)),'Mobility services managers are responsible for the strategic development and implementation of programs that promote sustainable and interconnected mobility options, reduce mobility costs and meet the transportation needs of customers, employees and the community as a whole such as bike sharing, e-scooter sharing, carsharing and ride hailing and parking management. They establish and manage partnerships with sustainable transport providers and ICT companies and develop business models in order to influence the demand of the market and  promote the concept of mobility as a service in urban areas. ','','Does not include Transport planners.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3b1ea27c-781c-41eb-821f-214285260dd2','key_15891','','','2519','2519.3','ICT change and configuration manager',replace('devops engineer\nconfiguration manager\ndevops specialist\napplication lifecycle manager\nsoftware change and configuration manager','\n',char(10)),'ICT change and configuration managers organise and implement a process to manage changes throughout the lifecycle of ICT assets such as software, applications, ICT systems, etc. ICT change and configuration managers have a sound knowledge of the main technologies and processes used in system engineering and to manage the lifecycle of ICT systems and subsystems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3b2229b4-807c-482a-89ca-2d0a542be196','key_15892','','','2635','2635.3.14','gerontology social worker',replace('social care gerontologist\nsocial worker\nsocial work gerontologist\ngeriatric social worker\ngerontological social worker\nolder adults social worker\nolder adults community care social worker\nseniors social worker\ngerontology social worker','\n',char(10)),'Gerontology social workers provide assistance to elderly individuals and their families helping them to cope with their biopsychosocial needs. They help connecting the elderly with community resources by gathering information about the array of services available to them. Gerontology social workers assess their clients'' needs, functional capacity and health problems and liaise with medical professionals when necessary.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3b43dafb-8c6b-4906-9fe5-bc218aacad55','key_15893','','','3422','3422.3.1','football coach',replace('football trainer\nfootball team manager\nsoccer coach\nassociation football coach\nfootball instructor\nfootball team coach\nfootball team trainer\nfootball manager\nfootball team instructor','\n',char(10)),'Football coaches train amateur or professional football teams of either youths or adults. Football coaches develop and execute training plans and improve or maintain their players physical condition, football technique and tactical abilities. They prepare their team for competitions and choose the line-up and tactics for a game. During a game coaches may give instructions from the sidelines and are in charge of substituting players. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3b46ee4d-a220-4c83-ad90-937456596889','key_15894','','','8160','8160.40','kettle tender',replace('chewing gum production operative\ngum sheeting machine operator\nkettle tender\nchewing gum mixer operator\nchewing gum production machinist','\n',char(10)),'Kettle tenders operate machines that mix chewing gum base with sugar or sweetener. They follow procedures to place gum base in containers and then direct it to flow into mixers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3b4eb248-6b40-42fe-9d10-cba8ccfe6613','key_15895','','','7231','7231.8','vehicle maintenance attendant',replace('heavy vehicle maintenance attendant\nlight vehicle service technician\nLGV maintenance attendant\nbus maintenance attendant\ncoach maintenance attendant\nbus service technician\ncoach service technician\nLGV service technician\nheavy vehicle service technician\nvehicle maintenance attendant\nfast-fit maintenance attendant\nlight vehicle maintenance attendant\nbus and coach service technician\nPCV service technician\nbus and coach maintenance attendant\nfast-fit service technician\nPCV maintenance attendant','\n',char(10)),'Vehicle maintenance attendants execute basic tasks such as changing oil, changing filters, changing spark plugs at a vehicle maintenance station.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3b6bea7d-36f4-44fb-ac73-d1cc8a198dcb','key_15896','','','2654','2654.3.2','video and motion picture producer',replace('video & motion picture producer\nmovie producer\nvideo producer\nvideo and motion picture producer\ntelevision producer\nfilm maker\nmotion picture producer\nexecutive producer\naudiovisual producer\ntelevision and motion picture producer\nfilm producer','\n',char(10)),'Video and motion picture producers supervise the entire production of a movie or television programme. They select the scripts that will be turned into motion pictures or series. Video and motion picture producers find the financial means to make a movie or television series. They have the final decision on the whole project, from development and editing to distribution. During large-scale productions, video and motion picture producers can be part of a team of producers and may be responsible for some of these tasks.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3b6d4443-4518-4fb3-ae44-188a958e328d','key_15897','','','3311','3311.3.5','stock trader',replace('equity trader\nstock trader\ninvestment dealer\nstock exchange floor trader','\n',char(10)),'Stock traders use their technical expertise of financial markets performance to advise and make recommendations to asset managers or shareholders for a profitable investment strategy, keeping in mind the company’s performance. They use stock market trading operations and deal with a wide array of taxes, commissions and fiscal obligations. Stock traders buy and sell bonds, stocks, futures and shares in hedge funds. They perform detailed micro- and macroeconomic and industry specific technical analysis.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3b755cda-c4b0-4fa1-8621-372f28ccc2e9','key_15898','','','2341','2341.1.1','Steiner school teacher',replace('Steiner school teaching practitioner\nSteiner school teacher\nWaldorf school teacher\nSteiner Waldorf school educator\nWaldorf school instructor\nSteiner school instructor\nteacher in Steiner school\nSteiner Waldorf school teacher\nWaldorf school educator\nSteiner teacher','\n',char(10)),'Steiner school teachers educate students using approaches that reflect the (Waldorf) Steiner philosophy and principles. They focus on practical, hands-on activities in the curriculum and instruct their classes in a manner that emphasises the development of the students'' social, creative and artistic capacities. Steiner school teachers instruct students in similar subjects to the ones in standardised education, although using a different approach, and with the exception of a higher amount of classes focused on creative and artistic practice and theory. They use teaching techniques that support the (Waldorf) Steiner school philosophy, evaluate students'' learning progress and communicate with other school staff.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3bdf6206-e952-4177-a17b-2a03bfee2144','key_15899','','','7523','7523.5','wood sander',replace('floor sanding machine worker\nsanding machine technician\nsanding machine specialist\nsanding machinist\nwood sanding machine tender\nsanding machine worker\nhand wood sander\nsanding machine operative\nwood sanding machine setter\nsanding machine operator\nwood sander, hand\nfloor sander\nproduction woodworker\nsanding machine setter\nfloor sanding machine specialist\nwood sanding machine operative\nwood sanding machine worker\nsanding machine tender\nfloor sanding machine operator\nfloor sanding machine technician\nwood sander\nwoodworker/sander\nwood sanding machine operator','\n',char(10)),'Wood sanders smoothen the surface of a wooden object using various sanding instruments. Each applies an abrasive surface, usually sandpaper, to the workpiece to remove irregularities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3c0af499-39f6-4468-9ba4-b13a26d5f888','key_15900','','','8122','8122.1','abrasive blasting operator',replace('abrasive blasting cabinet operative\nsand blast operative\nabrasive sandblaster\nsander operative\nsand mill grinder\nsander\nabrasive blaster\nshot blaster\nsand blasting operator\nsandblaster\nsand blaster\nsand-blaster\nsand blast operator\nabrasive blasting operative\nsander operator\nsand mill blaster\nabrasive shot baster\nabrasive blasting operator\ngrit blaster','\n',char(10)),'Abrasive blasting operators use the proper equipment and machinery to smoothen rough surfaces by abrasive blasting. Abrasive blasting is commonly used in the finishing process of metal workpieces and for blasting building materials used in masonry such as bricks, stones and concrete. They operate blasters or sand cabinets which forcibly thrust a stream of abrasive material such sand, soda or water, under high pressure, propelled by a centrifigal wheel, in order to shape and smoothen surfaces.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3c0c688b-f7ca-40dc-8222-ab011d4e2449','key_15901','','','3119','3119.17','tanning technician',replace('tanning professional\ntanning process coordinator\ntanning expert\ntanning technologist','\n',char(10)),'Tanning technicians have knowledge of the technical management of all tannery production departments, from beamhouse to tanning, post-tanning and finishing of leather. They ensure compliance with product specifications and secure consistent leather quality, fitness for use and process and product sustainability.  ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3c1041c1-7a20-41fd-8cb2-529d7f671226','key_15902','','','1420','1420.4.20','fish and seafood shop manager',replace('fish stall manager\nfisheries store manager\nfish and seafood shop manager\nfresh fish counter manager\nfresh seafood shop manager\nfish market sales manager\nfisheries shop manager\nfish shop manager\nfishmongers shop manager\nseafood shop manager','\n',char(10)),'Fish and seafood shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3c136897-4e0e-41ef-bb57-d8152323b93a','key_15903','','','1219','1219.1','business service manager',replace('actuarial advisory services manager\ncorporate service manager\nbusiness service manager\nservice operations manager\nprofessional services manager\nbusiness service executive\npost-office manager\nprofessional services consultant\ncorporate services consultant\nprofessional services executive','\n',char(10)),'Business service managers are reponsible for the provision of professional services to companies. They organise the provision of services tailored to the needs of the client and liaise with clients to agree on the contractual obligations for both parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3c1b7871-87f9-427b-adcb-fe16be8f9f24','key_15904','','','7312','7312.5','musical instrument technician',replace('musical instrument restoration technician\nmusical instrument technician\norgan repairer\nviolin repairer\ninstrument repairman\nviolin tuner\npiano tuner\ninstrument fixer\nmusical instrument restoration specialist\nmusical instrument restorer\ninstrument tuner\nmusical instrument repairer\nmusical instrument fixer\norgan tuner\nmusical instrument repair specialist\nmusical instrument repairman\ninstrument repairer\npiano repairer\nmusical instrument tuner\nmusical instrument repair technician','\n',char(10)),'Musical instrument technicians maintain, tune and repair musical instruments such as pianos, pipe organs, band instruments, violins and other instruments.','','Includes people producing smaller <a resource="http://data.europa.eu/esco/skill/29d4aaba-99f9-4733-bd74-ad558f2ae3a7" typeOf="http://data.europa.eu/esco/model#Skill" href="http://data.europa.eu/esco/skill/29d4aaba-99f9-4733-bd74-ad558f2ae3a7"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">musical instruments</span></a> or accesories such as metronomes, tuning forks or musical boxes.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3c2da05f-eb24-41f7-8307-46c96660fee0','key_15905','','','3154','3154.1','air traffic controller',replace('air traffic controller\nflight controller\nair traffic control officer\nair controller\nair traffic controller instructor\ncontroller of air traffic\nair traffic control specialist','\n',char(10)),'Air traffic controllers assist pilots by providing information concerning the height, speed and course. They assist pilots in order to facilitate a safe take off and landing of aircrafts. They are responsible for maintaining a secure and orderly movement of aircraft along major air routes up in the sky and around airports. They control air traffic in and within vicinity of airports according to established procedures and policies to prevent collisions and to minimise delays arising from traffic congestion.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3c305542-a51b-4b96-8d45-af11ca05eaa2','key_15906','','','2310','2310.1.43','veterinary medicine lecturer',replace('veterinary science teacher\nveterinary science lecturer\nclinical lecturer in veterinary medicine\nlector in veterinary medicine\nveterinary medicine teacher\nveterinary science instructor\nveterinary science docent\nveterinary medicine lector\nuniversity clinical lecturer in veterinary medicine\nveterinary medicine docent\nveterinary clinician\nveterinary medicine lecturer\nlecturer in veterinary medicine\nlecturer of veterinary medicine\nveterinary science professor\nuniversity veterinary medicine lecturer\nveterinary medicine professor\ninstructor in veterinary medicine\nteacher of veterinary medicine\nveterinary medicine instructor\nprofessor of veterinary medicine\nuniversity lecturer in veterinary medicine','\n',char(10)),'Veterinary medicine lecturers are subject professors, teachers, or lecturers, and often doctors who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, veterinary medicine, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for grading papers and exams, for leading laboratory practices, and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of veterinary medicine, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3caa57c9-d360-4ce4-8000-34a983666ba7','key_15907','','','8160','8160.13','cellar operator',replace('fermentation cellar supervisor\nfermentation cellar operative\nfermentation cellar machine minder\nfermentation inspector\nfermentation operator\nvinegar production operator\nfermentation equipment operator\nfermentation cellar operator\ncellar operator\ncellar worker','\n',char(10)),'Cellar operators take charge of fermentation and maturation tanks. They control fermentation process of wort inoculated with yeast. They tend equipment that cools and adds yeast to wort as to produce beer. For the purpose, they control the flow of refrigeration that goes through cool coils regulating the temperature of hot wort in the tanks.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3cbbdb83-7c36-4ae5-8c45-6c284186f477','key_15908','','','2163','2163.1.6','model maker',replace('model fabricator\nmodel expert\nmodel designer\nmodeller\nmodel builder\nmodel maker\nmaker','\n',char(10)),'Model makers create three-dimensional scale models or various designs or concepts and for various purposes, such as models of human skeletons or organs. They also mount the models on display stands so that they can be used for their final purpose such as inclusion in education activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3cc51201-cd20-47f8-92ad-4d5861f963cf','key_15909','','','9621','9621.2','hotel porter',replace('bellboy\nhotel porter\nbaggage carrier\nbellhop\nporter\ncarrier\nbellman','\n',char(10)),'Hotel porters welcome guests to accommodation facilities, help them carry their luggage and provide services such as occasional cleaning.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3cc87d40-f402-4f57-9353-4b36a031cfc5','key_15910','','','5249','5249.3','vehicle rental agent',replace('vehicle booking worker\nvehicle hire desk agent\nvehicle rental desk agent\nvehicle booking desk assistant\nvehicle booking assistant\nvehicle booking desk supervisor\nvehicle rental manager\nvehicle booking manager\nvehicle hire desk manager\nvehicle booking supervisor\nvehicle hire desk worker\nvehicle rental supervisor\nvehicle booking desk clerk\nvehicle booking desk agent\nvehicle rental desk supervisor\nvehicle rental assistant\nvehicle rental agent\nvehicle booking team leader\nvehicle rental desk clerk\nvehicle booking desk worker\nvehicle rental worker\nvehicle rental team leader\nvehicle booking desk manager\nvehicle hire desk assistant','\n',char(10)),replace(' \n Vehicle rental agents represent businesses involved in renting out vehicles and determine short periods of usage. They document transactions, insurances and payments. \n \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3ce4691e-ecea-4278-939e-b89ab01087b8','key_15911','','','1330','1330.9','telecommunications manager',replace('telecommunications development manager\ntelecommunications technician supervisor\ntelecommunications installation manager\ntelecommunications equipment manager\ntelecommunications project manager\ntelecommunications systems manager\nICT manager\ntelecoms manager\ntelecommunications managers\ntelecommunications service manager\ntelecommunications services manager\ncommunications manager\ntelecommunications manager\ncustomer service manager, telecommunications\nnetwork and telecommunications manager\nnetwork manager, telecommunications\ntelecom manager\ntelecommunications commissioning manager','\n',char(10)),'Telecommunications managers coordinate telecommunications staff activities for installing, troubleshooting, repairing and maintaining telecommunications equipment and infrastructure. They oversee the research, evaluation and implementation of new technologies and ensure a safe working environment for the employees. They supervise the inventory of supplies as well as user and customer assistance actions.','','Excludes people performing ICT development and soldering activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3ce9c89d-6f1a-48b5-942d-386e46e2fd06','key_15912','','','3115','3115.1.5','heating, ventilation, air conditioning and refrigeration engineering technician',replace('HVAC systems technician\nHVACR technician\nheating, ventilation, air conditioning and refrigeration engineering technician\nair conditioning technician\nHVAC project technician','\n',char(10)),'Heating, ventilation, air conditioning and refrigeration engineering technicians aid in the design of devices which provide heating, ventilation, air conditioning and possibly refrigeration in buildings. They ensure the equipment complies with environmental standards. They handle hazardous materials used in the systems, and ensure safety precautions are in place.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3cee6bea-4a19-4511-9bbb-fdf820857555','key_15913','','','3355','3355.1','criminal investigator',replace('forensic officer\nfingerprint officer\ncrime scene investigator\nforensic science officer\ncriminal investigator\nscenes of crime officer','\n',char(10)),'Criminal investigators examine and process the scenes of crimes and the evidence found in them. They handle and protect the evidence compliant with rules and regulations, and isolate the scene from outside influence. They photograph the scene, ensure the maintenance of the evidence, and write reports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3d0f86c1-9bdd-4e84-bc62-93a716128c8d','key_15914','','','2359','2359.11','learning mentor',replace('specialist learning mentor\npastoral support mentor\npastoral manager\nsupport mentor\npastoral support officer\nsenior learning mentor\nlearning support mentor\npastoral mentor\nlearning mentor','\n',char(10)),'Learning mentors support underperforming students both inside and outside of the classroom in order to increase their academic success. They assist students experiencing (multiple) disadvantages, such as learning difficulties, behavioural issues, and attendance problems, and also assist gifted students who are under-challenged. They may also work with adult students in the further education system. Learning mentors develop schedules and action plans with the students in order to plan the necessary mentoring activities and monitor progress. They also liaise with the students'' teachers, educational psychologists, school social workers and, if necessary, with the student''s parents, in order to improve the student''s educational development.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3d190639-90f8-4402-b1b3-a104a17e0d67','key_15915','','','2521','2521.4','database integrator',replace('data integrator\ndatabase development specialist\ndatabase integrator\ndatabase integrators\ndata base integrators\ndata base integrator','\n',char(10)),'Database integrators perform integration among different databases. They maintain integration and ensure interoperability.','',replace('Excludes people performing managerial activities. \n Includes people performing programming and coding activities.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3d2637f9-e56a-4769-b3a9-e413f95c20db','key_15916','','','3331','3331.2.1.3','import export specialist in beverages',replace('graduate import export specialist in beverages\ntrainee export specialist in beverages\nimport export specialist in beverages\nassistant import specialist in beverages\ngraduate import specialist in beverages\nsenior import specialist in beverages\ntrainee import export specialist in beverages\ntrainee import specialist in beverages\nsenior import export specialist in beverages\ncustoms compliance specialist in beverages\nassistant import export specialist in beverages\ngraduate export specialist in beverages\nsenior export specialist in beverages\nassistant export specialist in beverages\ninternational trade compliance specialist in beverages','\n',char(10)),'Import export specialists in beverages have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3d616092-b4fd-4c14-b0e9-a9119bbbd171','key_15917','','','2269','2269.9.4','music therapist','music therapist',replace('Music therapists use music-therapeutic interventions to treat patients with behavioural disorders and pathogenic conditions to prevent, mitigate or eliminate symptoms and to change behaviours and attitudes requiring treatment. They promote and maintain or restore the development, maturity and health of the patient/client by music-therapeutic interventions. Music therapy especially provides help for people with with emotional, somatic, intellectually or socially induced behavioural disorders and pathogenic conditions, such as psychoses (schizophrenic disorders, bipolar disorders) and personality development disorders. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3d7a1284-824c-47d9-8387-b8e1cd5888b5','key_15918','','','2310','2310.1.12','dentistry lecturer',replace('professor of dentistry\nuniversity clinical lecturer in dentistry\ndental clinician\nclinical lecturer in dentistry\ndental science lecturer\nuniversity lecturer in dentistry\nlecturer in dentistry\ndental science lector\ndentistry lecturer\ndentistry professor\nprofessor of dentistry studies\ndentistry instructor\ndentistry docent\ndental sciences lector\ndental sciences lecturer\nuniversity dentistry lecturer\ndentistry lector\ndentistry teacher','\n',char(10)),'Dentistry lecturers are subject professors, teachers, or lecturers, and often doctors who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, dentistry, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for grading papers and exams, for leading laboratory practices, and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of dentistry, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3d7b36e2-bfb8-4b67-9585-6787c1dda13e','key_15919','','','7515','7515.1','coffee taster',replace('coffee drinks taster\ncoffee taster\ncoffee tasting expert\ncoffee tasting specialist\ncoffee beverage taster','\n',char(10)),'Coffee tasters taste coffee samples in order to evaluate the features of the product or to prepare blending formulas. They determine the product''s grade, estimate its market value, and explore how these products may appeal to different consumer tastes. They write blending formulas for workers who prepare coffee products for commercial purposes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3d924c0d-ffee-4f11-adc7-c2bfb76fcf73','key_15920','','','8157','8157.1.1','laundry ironer',replace('presser\nfur ironer\nlaundry worker\nlaundry assistant\nsteam presser\ndry cleaner\ndry cleaning worker\nlaundry presser\nclothes ironer\nlaundry ironer\nlaundry attendant\nclothes presser','\n',char(10)),'Laundry ironers re-shape clothing items and linen and remove creases from them by using irons, presses and steamers. They clean and maintain the ironing and drying area and organise the items accordingly.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3d974e0c-8ed9-47a7-aec2-ebdb34391c87','key_15921','','','9333','9333.1','airport baggage handler',replace('freight and luggage handler\nfleet service agent\nbaggage handler\nluggage handler\nramp attendant\nfleet service clerk\nairport baggage agent\nramp service agent\nbaggage agent\nairport luggage handler\nairport baggage handler\nramp agent','\n',char(10)),'Airport baggage handlers receive and return the luggage of passengers at airport terminals. They prepare and attach baggage claim checks, stack baggage on carts or conveyors and may return baggage to patrons on receipt of claim check.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3d999108-f37a-4087-a936-b2911240b8c2','key_15922','','','1212','1212.3','volunteer manager',replace('volunteer engagement coordinator\nvolunteer program manager\nvolunteer program coordinator\ncommunity engagement coordinator\nvolunteering development manager','\n',char(10)),'Volunteer managers work across the non-profit sector to recruit, train, motivate and supervise volunteers. They are in charge of designing volunteer assignments, recruiting volunteers, reviewing the tasks undertaken and impact made, providing feedback and managing their overall performance against the objectives of the organisation. Volunteer coordinators might also manage online volunteering activities, sometimes known as cyber-volunteering or e-volunteering.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3da8ee8c-ab60-4feb-ab5b-3ba31ef9ec1f','key_15923','','','8155','8155.1','leather production machine operator',replace('warehouse machine operator\nfleshing machine operator\nfur-shaving machine tender\nmeasuring leather machine operator\nleather production splitting machine operator\nretanning machine operator\nleather processing machine operator\nsammying machine operator\nshaving machine operator\nde-salting machine operator\nspraying machine operator\nleather sorting machine operator\nleather finishing operator\nstretching machine operator\nsplitting and sammying machine operator\nbeating machine tender\nmachine leather trimmer\nbrushing machine operator\nleather production machinist\ntanning machine operator\nstaking machine operator\nglazing machine operator\nhide machine operator\nicing machine operator\nleather production machine operator\nbeamhouse machine operator\ncarding machine operator\nde-hairing machine operator\nleather production worker\nspray finishing machine operator','\n',char(10)),'Leather production machine operators use the tannery machinery and programs according to specific requirements in order to meet the department''s standards. They do routine maintenance of the machinery.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3dab5c3f-84d0-4d52-901d-dd262eaea509','key_15924','','','2149','2149.2.8','research engineer',replace('engineering science researcher\nagricultural research engineer\nengineering analyst\nresearch engineer\nengineering scholar\nengineering scientist\nengineer researcher\nengineering researcher\nbionic engineer\nengineering research scientist\nengineering research analyst','\n',char(10)),'Research engineers combine research skills and knowledge of engineering principles to assist in the development or design of new products and technology. They also improve existing technical processes, machines and systems and create new, innovative technologies. The duties of research engineers depend on the  branch of engineering and the industry in which they work. Research engineers generally work in an office or laboratory, analysing processes and conducting experiments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3db35c88-bcb7-4de4-914a-9a9839a3c911','key_15925','','','2320','2320.1.6','business administration vocational teacher',replace('vocational teacher of business administration\nvocational instructor in business management\nvocational business administration teacher\nbusiness management teacher\nbusiness administration educator\nbusiness administration teacher\nvocational instructor in business administration\nbusiness administration instructor\nbusiness administration vocational teacher\ninstructor in business administration\nbusiness management instructor','\n',char(10)),'Business administration vocational teachers instruct students in their specialised field of study, business administration, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for a business administration-related profession, such as accountant or banker. Business administration vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of business administration through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3dbd758c-6dc0-41fe-8248-2250c9bfa4ab','key_15926','','','2112','2112.1.2','climatologist',replace('climate science researcher\nclimate research scientist\nclimatology analyst\nclimatologist\nclimatology researcher\nclimatology scientist\nclimate scholar\nclimatology scholar\nclimatologists\nclimate research analyst\nclimate scientist\nclimate researcher\nclimate analyst\nclimatology research analyst\nclimatology research scientist\nclimatology science researcher','\n',char(10)),'Climatologists study the average change in weather and climate from a long-term perspective. They research and analyse historic weather conditions in order to forecast climatic conditions trends such as changes in temperature, global warming, or regional evolutive weather conditions. They use these findings for advising on environmental policy, construction, agricultural projects, and societal matters.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3df28a39-9bfe-4e36-b394-a189ef967a74','key_15927','','','7126','7126.1','bathroom fitter',replace('replacement bathroom installer\nbathroom joiner and fitter\nreplacement bathroom fitter\nbathroom installation worker\nbathroom refurbishment team member\nbathroom carpenter and joiner\nbathroom fitting  specialist\nbathroom furniture fitter\nbathroom fitter\nbathroom installer\nbathroom installation specialist\nbathroom refurbishment team worker','\n',char(10)),'Bathroom fitters install bathroom elements. They take the necessary measurements, prepare the room, removing old elements if necessary, and install the new bathroom equipment, including the connection of water, gas and sewage pipes and electricity lines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3e0b4b9b-b624-4ceb-9bc9-9627cdfb0b81','key_15928','','','8156','8156.3','leather goods machine operator',replace('leather sewing machine operator\nleather production worker\nleather production machine operator\nleather goods machine operator\nleather products machine operator\nleather products machine tender\nleather products machinist\nleather production machinist','\n',char(10)),'Leather goods machine operators tend specific machines in the industrial production of leather goods products. They operate machinery for cutting, closing, and finishing luggage, handbags, saddlery and harness products. They also perform routine maintenance of the machinery.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3e1a2f3c-20d6-4d33-aa6f-63cd2a32698d','key_15929','','','1211','1211.1.4','corporate treasurer',replace('corporate treasury manager\ntreasury analyst\ncorporate financial officer and treasurer\ngroup treasurer\ncorporate treasury officer\nforeign currency treasurer\ncorporate treasury controller\ncompany treasurer\ncorporate treasurer','\n',char(10)),'Corporate treasurers determine and oversee the financial strategic policies of a company or organisation. They use cash management techniques like account organisation, cash flow monitoring, liquidity planning and control, risk management including currency and commodity risks and maintain close connection with banks and rating agencies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3e213d52-e7a0-4980-99d9-23da1f717f50','key_15930','','','2432','2432.6','political campaign officer',replace('campaign strategy consultant\npolitical advisor\nstrategy manager\npolitical campaign officer\npolitical consultant\npolitical aide\nstrategy advisor\npolitical campaign consultant','\n',char(10)),'Political campaign officers provide support during political campaigns, advising the candidate and campaign management staff on campaign strategies and campaign staff coordination, as well as developing advertising and research strategies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3e524c3e-3ee8-47dd-8463-2c2a41b548bf','key_15931','','','8212','8212.2','electrical equipment assembler',replace('electrical equipment constructor\nassembler of electrical equipment\nfabricator of electrical equipment\nconstructor of electrical equipment\nelectrical equipment production assembler\nelectrical assembler\nelectrical equipment assembler\nelectrical products assembler\nassembler in electrical equipment production\nelectrical fabricator','\n',char(10)),'Electrical equipment assemblers are responsible for the assembly of electrical equipment. They assemble product components and wiring according to the blueprints.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3e626058-f669-4503-84ec-6ae1a6077a61','key_15932','','','3132','3132.1','incinerator operator',replace('incinerator worker\nincinerating machine operative\nwaste-burning plant operator\nincinerator operator\nwaste incinerating machine operative\ndomestic waste incineration operative\nrefuse worker\nwaste incinerating machine operator\nincineration process operative\nrefuse operative\nincinerating machine worker\nincinerating machine operator\nincinerator operative\nwaste incinerator operative\nwaste incinerating machine worker\nwaste incinerator worker\nincineration process worker\nwaste-burning plant operative\nincineration process operator','\n',char(10)),'Incinerator operators tend incineration machines which burn refuse and waste. They ensure the equipment is maintained, and that the incineration process occurs in accordance with safety regulations for the incineration of waste.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3e6488b6-3ca5-46b9-b380-5cd251d24824','key_15933','','','3423','3423.2.3','pilates teacher',replace('pilates teacher & personal trainer\npilates teacher and personal trainer\npilates trainer\nPilates teacher\npilates and movement specialist\npilates matwork teacher\npilates coach\nmatwork pilates teacher\npilates matwork instructor\npilates instructor\nteacher of pilates\nyoga and pilates matwork teacher','\n',char(10)),'Pilates teachers plan, teach, and adapt exercises based on the work and principles of Joseph Pilates. They collect and analyse information for each client to ensure that programmes are safe, appropriate and effective. They apply the principles of Pilates through the planning and teaching of supportive, non-competitive lesson. They motivate and encourage clients to ensure their adherence to regular sessions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3e7bf729-4442-4b9f-ad5e-83111963795c','key_15934','','','3512','3512.4','ICT technician',replace('ICT technician\nIT officer\ncomputer operator\nIT engineer\nIT technician\nICT engineer','\n',char(10)),'ICT technicians install, maintain, repair and operate information systems and any ICT related equipment (laptops, desktops, servers, tablets, smart phones, communications equipment, printers and any piece of computer related peripheral networks), and any type of software (drivers, operating systems, applications).','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3e9c1262-4fe2-4eb1-bf94-4fe64498393a','key_15935','','','3422','3422.3','sports coach',replace('sports instruction practitioner\nsport coach for performance\nsports instructor\nsports trainer for the elderly\ncoach of sport\nsport coach for participation\nchildren sports trainer\ncoach in sports\nsports trainer\ninstructor in sport\nsports coach\nsport coach\npractitioner of sports coaching\nsports coach for children','\n',char(10)),'Sports coaches provide instruction in the sport of their specialisation in a recreational context to non-age-specific and age specific participants. They identify already acquired skills and implement suitable training programmes for the groups or individuals they teach in order to develop participants'' physical and psychological fitness. They create the most optimal environment for the growth of participant skills and enable them to maximise their performance, while fostering good sportsmanship and character in all participants. Sports coaches also track the participant progress and provide personalised instruction where needed. They supervise sports facilities and changing rooms and maintain uniforms and equipment.','',replace('Includes people performing scouting and recruiting activities. \n Includes people performing instruction activities of non-team sports such as martial arts or yoga. \n Excludes <a resource="http://data.europa.eu/esco/occupation/11695786-9fb7-4895-b161-0480419107e4" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/11695786-9fb7-4895-b161-0480419107e4"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">sports instructor</span></a>.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3e9c6f2a-dc20-4366-aeb9-99dffd635b4c','key_15936','','','8112','8112.4','stone planer',replace('stone slab planer\nstone planing machine setter\nplaner of stones\nstone planing machine operative\nstone planing machine tender\nstone planer\nstone planing machine operator','\n',char(10)),'Stone planers operate and maintain planing machines that are used for stone blocks and slabs finishing. They manipulate the stone and ensure that the required parameters are according to specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3f0093ab-651b-401a-b542-bba84ff46deb','key_15937','','','3324','3324.4.17','wholesale merchant in hides, skins and leather products',replace('wholesale merchant in hides, skins and leather products\ntrainee wholesale merchant in hides, skins and leather products\nwholesale buyer in hides, skins and leather products\nbusiness to business sales consultant in hides, skins and leather products\nwholesale merchandiser in hides, skins and leather products\nB2B sales consultant in hides, skins and leather products\nassistant wholesale merchant in hides, skins and leather products\nsenior wholesale merchant in hides, skins and leather products\nwholesale trader in hides, skins and leather products\nwholesaler in hides, skins and leather products\nbusiness to business seller in hides, skins and leather products\ngraduate wholesale merchant in hides, skins and leather products','\n',char(10)),'Wholesale merchants in hides, skins and leather products investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3f277813-3cab-4c5d-bfeb-613e449b31c5','key_15938','','','3311','3311.2.2','mortgage broker',replace('officer for mortgage loan\nadministrator of mortgage loans\nmortgage administrator\nmortgage professional\nmortgage officer\nmortgage coordinator\nmortgage broker\nmortgage loans officer\nmortgage analyst','\n',char(10)),'Mortgage brokers handle mortgage loan applications from clients, collect loan documentation and search for new mortgage lending opportunities. They complete and close mortgage loan processes for their clients.','Mortgage credit intermediary means a natural or legal person who is not acting as a creditor or notary and not merely introduce, either directly or indirectly, a consumer to a creditor or credit intermediary, and who, in the course of his trade, business or profession, presents or offers credit agreements to consumers, assists consumers by undertaking preparatory work or other pre-contractual administration in respect of credit agreements other than as referred to in point , or concludes credit agreements with consumers on behalf of the creditor.','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3f2e63fb-dc50-4370-9f6a-85ddc338dcde','key_15939','','','3331','3331.2.1.13','import export specialist in flowers and plants',replace('assistant import specialist in flowers and plants\ncustoms compliance specialist in flowers and plants\ngraduate export specialist in flowers and plants\nsenior import export specialist in flowers and plants\ntrainee export specialist in flowers and plants\ngraduate import export specialist in flowers and plants\nsenior import specialist in flowers and plants\ntrainee import export specialist in flowers and plants\ntrainee import specialist in flowers and plants\nassistant export specialist in flowers and plants\ninternational trade compliance specialist in flowers and plants\nimport export specialist in flowers and plants\ngraduate import specialist in flowers and plants\nsenior export specialist in flowers and plants\nassistant import export specialist in flowers and plants','\n',char(10)),'Import export specialists in flowers and plants have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3f32394b-f1b1-48ef-96ee-74405fb7c6b6','key_15940','','','5230','5230.2','lottery cashier',replace('lottery retailer cashier\nlottery clerk\nlottery agent\nlottery selling agent\nlottery cashier\nclerk\nlottery vendor','\n',char(10)),'Lottery cashiers exchange the registration of set of numbers or symbols for money and give tickets to the players. They pay out prizes and obtain customers'' signatures and identification. They audit and count money in the cash register, enforcing regulations to prevent money laundering.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3f3748a7-4bb3-40b4-9ae3-1c5040c70f52','key_15941','','','2142','2142.1.8','rail project engineer',replace('railway project engineer\nengineering project manager\nrailway engineering project manager\nrail engineering project manager\nrail engineering specialist\nengineer in railway projects\nproject engineer in railways\nrail project engineer','\n',char(10)),'Rail project engineers maintain a safe, cost-effective, high-quality, and environmentally responsible approach across the technical projects in railway companies. They provide project management advice on all construction projects including testing, commissioning and site supervision. They audit contractors for safety, environment and quality of design, process and performance as to ensure that all projects follow in-house standards and relevant legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3f54ceba-3c4e-4ba1-8ddb-9e49a881a60a','key_15942','','','3112','3112.3','construction safety manager',replace('safety manager\nhealth and safety manager\nsafety officer\nhealth ans safety supervisor\nconstruction safety supervisor\nconstruction safety manager\nconstruction health and safety supervisor\nsafety advisor\nhealth and safety manager on construction sites\nsafety supervisor\nsafety manager on  construction sites','\n',char(10)),'Construction safety managers inspect, enforce and control health and safety measures at construction sites. They also manage workplace accidents and take action to make sure that safety policies are correctly implemented.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3f6fc9db-18c1-45e8-819e-baf7b6cfe372','key_15943','','','1349','1349.10','court administrator',replace('legal administrator\ncourt administrator\ncourt administration manager\ncourt administrative officer\nmanager of court administration','\n',char(10)),'Court administrators perform managerial tasks in a court institution, such as supervision of staff, administration, communicating with judges and reviewing procedures. They also manage the court''s finances, and oversee maintenance of the facility and equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3f73120d-cece-4848-9fb4-61fdbaa7eba5','key_15944','','','3118','3118.3.13','rolling stock engineering drafter',replace('rolling stock engineering drafter\nrolling stock draftsperson\nrolling stock engineering draughter\nrolling stock engineering drafting consultant\nrolling stock engineering drafting specialist\nrolling stock engineering draughtsperson\nrolling stock draughtsperson\nrolling stock drafter\nrolling stock engineering drafting expert\nrolling stock draughter\nrolling stock engineering draftsperson\nrolling stock engineering drafting adviser','\n',char(10)),'Rolling stock engineering drafters convert the rolling stock engineers’ designs into technical drawings usually using software. Their drawings detail dimensions, fastening and assembling methods and other specifications used in the manufacture of rail vehicles such as locomotives, multiple units, carriages and wagons.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3f74aad8-2d60-4be2-b059-8dba5568d530','key_15945','','','5311','5311.1.3','child day care worker',replace('childcarer\nchildren and families care worker\nnursery nurse\ndaycare worker\nchild caregiver\nchildcare worker\nchild day care worker','\n',char(10)),'Child day care workers provide social services to children and their families in order to improve their social and psychological functioning. They aim to maximise family''s well-being by caring of children during the day.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3f7b7ce5-94b6-4e0c-98e7-a4ee5a6accf4','key_15946','','','2131','2131.4.13','toxicologist',replace('toxicology studies researcher\ntoxicology scientist\ntoxicology studies analyst\ntoxicology science researcher\ntoxicology analyst\ntoxicology studies scholar\ntoxicology studies research scientist\ntoxicology scholar\ntoxicologist\ntoxicology research scientist\ntoxicology studies research analyst\ntoxicology studies scientist\ntoxicology research analyst\ntoxicology researcher\ntoxicologists','\n',char(10)),'Toxicologists study the effects that chemical substances or biological and physical agents have in living organisms, more specifically, on the environment and on the animal and human health. They determine doses of the exposure to substances for arising toxic effects in environments, people, and living organisms, and also perform experiments on animals and cell cultures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3f863faa-c045-4f05-a419-b19182aea866','key_15947','','','2146','2146.2','drilling engineer',replace('oilfield driller\noffshore drilling engineer\ndrilling and completion engineer\nmineral driller\nwells engineer\ndrilling engineer\ngasfield driller\ndrilling expert\ngas rig driller\nrig driller\noil rig driller\ndrilling technician\nearth driller','\n',char(10)),'Drilling engineers develop and supervise the drilling of gas and oil wells. They assist in the designing, testing and creation of wells and are employed on land or offshore platforms. Drilling engineers work with other mining professionals and oversee the drilling progress and safety of the site.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3fa31588-2e4d-49c3-b839-4b615cc62742','key_15948','','','2422','2422.7','humanitarian advisor',replace('humanitarian advisor\nnon governmental organisation officer\ncharity worker\nhumanitarian affairs officer\nhumanitarian affairs advisor\nhumanitarian assistance advisor\nhumanitarian consultant','\n',char(10)),'Humanitarian advisors ensure strategies to reduce the impact of humanitarian crises on a national and/or international level. They provide professional advice and support and this in collaboration with different partners.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3fa9a480-ceb3-4c2a-8a91-0508e1bb6ba5','key_15949','','','7536','7536.2.3','footwear CAD patternmaker',replace('CAD pattern maker\nfootwear pattern maker\nCAD pattern-maker\nfootwear CAD pattern-maker\nfootwear CAD pattern cutter footwear pattern maker CAD operator\nfootwear CAD pattern cutter\nfootwear pattern-maker CAD operator\nfootwear CAD patternmaker\nfootwear CAD operator','\n',char(10)),'Footwear CAD patternmakers design, adjust and modify patterns for all kinds of footwear using CAD systems. They check laying variants using nesting modules of the CAD system and material consumption. Once the sample model has been approved for production, these professionals make series of patterns (grading) to produce a range of the same footwear model in different sizes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/3ff2fdf4-af54-4517-873d-73e9353f112e','key_15950','','','5223','5223.7.10','computer games, multimedia and software specialised seller',replace('computer games salesperson\ncomputer software and multimedia specialised seller\ncomputer software and multimedia salesperson\nsoftware specialised seller\ncomputer software salesperson\ncomputer games and software specialised seller\nsoftware multimedia salesperson\ncomputer games specialised seller\nsoftware salesperson\ncomputer games and software salesperson\ncomputer games, multimedia and software specialized seller\ncomputer software specialised seller\nmultimedia software specialised seller','\n',char(10)),'Computer games, multimedia and software specialised sellers sell software products in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/40260ee8-c94a-4dc8-a562-f9fbc2fe13b1','key_15951','','','3112','3112.1.12','sewerage maintenance technician',replace('sewerage investigative cctv operative\nsewer pipe maintenance technician\nsewerage investigative technician\nsewerage system maintenance technician\ncctv operative\nsewerage maintenance technician\nsewerage trenchless technology operative\ncctv technician\nsewerage investigation technician\ncctv operator','\n',char(10)),'Sewerage maintenance technicians inspect sewers and pipeline systems. They do this by using moving video cameras, whose recordings tell them whether they need to perform any maintenance and repair on these types of systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/403be630-8d1e-43b7-9f07-098aca5bb689','key_15952','','','2619','2619.9','mediator',replace('alternative dispute resolution coordinator\nmediator\nadvice worker\nlabour mediator\nfamily mediator\nlegal intermediary\nconciliator\nmediation commissioner\nlabor mediator\narbitrator\nconflict mediator','\n',char(10)),replace('\nMediators resolve disputes between two parties by examining the case, interviewing both parties, and advising on a solution which would be the most beneficial for them. They listen to both parties in order to facilitate communication and find a fair agreement and organise meetings. They aim at resolving disputes through dialogue and alternative solution without having to take the case to litigation and courts. Mediators ensure that the resolution is compliant with legal regulations and is also enforced.','\n',char(10)),'',replace('Excludes people performing resolution of legal process by making the final decision. \n Excludes <a resource="http://data.europa.eu/esco/occupation/666846b2-01ce-43f6-8cb7-f3be09fac4a2" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/666846b2-01ce-43f6-8cb7-f3be09fac4a2"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">ombudsman</span></a>.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/404a50e9-61ce-448d-a695-bbfc697a0727','key_15953','','','1111','1111.2','government minister',replace('minister of state\nsecretary of state\ngovernment minister\nassembley minister\nprime minister\nminister\nmember of parliament','\n',char(10)),'Government ministers function as decision-makers in national or regional governments, and head government ministries. They perform legislative duties and supervise the operation of their department.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/40517ed4-2769-4283-a15b-4b0d939bd2d3','key_15954','','','3122','3122.4','production supervisor',replace('consumer goods production controller\nconsumer goods production foreman\nconsumer goods production forewoman\nproduction team leader\nproduction forewoman\nproduction process supervisor\nconsumer goods production overseer\nproduction controller\nproduction plant supervisor\nconsumer goods production supervisor\nconsumer goods manufacturing supervisor\nproduction supervisor\nmanufacturing supervisor\nquality control operator\nproduction line supervisor\nproduction quality supervisor\nmanufacturing supervisor consumer goods\nconsumer goods production quality supervisor\nproduction schedule supervisor\nproduction overseer\nconsumer goods quality control operator\nproduction foreman\nproduction machine supervisor','\n',char(10)),'Production supervisors coordinate, plan and direct manufacturing and production processes. They are responsible for reviewing production schedules or orders as well as dealing with staff in these production areas.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/40b77b1b-6f9c-4d25-8b08-8b3cb0aaf7e7','key_15955','','','2146','2146.3','explosives engineer',replace('ordnance engineer\nmunitions engineer\nblasting engineer\nexplosives engineer','\n',char(10)),'Explosives engineers design drilling patterns and determine the amount of explosives required. They organise and supervise controlled blasts and report and investigate misfires. They manage explosives magazines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/40d76b2c-b917-4bf1-95b3-191f11472e67','key_15956','','','7536','7536.2.2','footwear 3D developer',replace('3D footwear product designer and developer\nfootwear 3D computer aided design developer\n3D footwear developer\n3D footwear expert','\n',char(10)),'Footwear 3D developers design footwear models, make, adjust and modify patterns using computer aided design systems. They focus on the sustainable design of the model, the selection and design of lasts and components, the proper and efficient use of materials, the pattern making, the selection of the bottom and the elaboration of technical data sheets. They may supervise the development and evaluation of prototypes, the preparation of samples, the implementation of the necessary quality control tests on the samples, and the management of the technical documentation of the product.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/40da2fc6-8cb4-4cd3-8fe3-899715905070','key_15957','','','3435','3435.5','body artist',replace('tattoo studio assistant\nhenna artist\ntattooist\ntattoo artist\nbody piercer\ntattooer\ntattoo studio attendant\nbody artist','\n',char(10)),'Body artists decorate clients'' skin temporarily or permanently. They use various techniques such as tattooing or piercing. Body artists follow clients'' preferences in terms of tattoo''s or piercing''s design and body surface and apply it safely. They also advise on methods to avoid infection following the procedures on clients'' bodies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/41535a7d-f2ed-4937-9b7e-3b14c8c755b3','key_15958','','','8171','8171.1','bleacher operator',replace('paper bleacher specialist\nbleach stock preparation specialist\nbleacher specialist\npulp bleacher specialist\nbleach stock preparation technician\npulp bleacher technician\nbleach stock preparation operator\nbleacher operator\npaper bleacher technician\nbleacher technician\npaper bleacher\npulp bleacher operator\npaper bleacher operator','\n',char(10)),'Bleacher operators tend a machine that bleaches wood pulp to serve in the production of white paper. Different bleaching techniques are used to complement the various pulping methods, and to obtain different grades of whiteness.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4163b31e-440b-4991-b5c3-48d0515250ef','key_15959','','','2431','2431.13','network marketer',replace('advertising manager\ncommercial network development manager\nmarketing promotions manager\nnetwork marketer\nnetwork marketing specialist\nnetwork marketeer\npromotion strategy manager\ncommercial marketing specialist\nmulti-level marketer','\n',char(10)),'Network marketers apply various marketing strategies, including ​network marketing strategies to sell products and convince new people to also join in and start selling these products. They use personal relations to attract customers and sell various types of products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/418f3a43-a6b2-4101-aaa0-832fe7d82885','key_15960','','','3119','3119.10','metal additive manufacturing operator',replace('metal AM technician\nAM operator\nmetal additive manufacturing technician\nadditive manufacturing technician\nmetal AM operator\nAM technician','\n',char(10)),'Metal additive manufacturing operators operate machines using additive manufacturing processes, such as fitting and setting up, maintenance and repair. They have factual and broad understanding in the field of metal additive manufacturing process. They are able to develop solutions on basic and specific problems related with additive manufacturing machines and processes and self-manage the handling of feedstock (approval, storage, contamination, traceability). ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/41a51397-089d-459d-9b7e-f1cdee5ad6bb','key_15961','','','7316','7316.1','decorative painter',replace('pottery painter\ndecorative glass artist\ntraditional glass painter\ninterior decorative artist\ndecorative painter\ndecorative artist\nglass painter\ntole painter\nceramic painter\nspecialist decorator','\n',char(10)),'Decorative painters design and create visual art on different kinds of surfaces such as pottery, casings, glass and fabric. They use diverse materials and a variety of techniques to produce decorative illustrations ranging from stenciling to free-hand drawing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/41a8e7c8-e1d8-4984-9b3c-dbbad1699f83','key_15962','','','7123','7123.1','ceiling installer',replace('ceiling installer\ndomestic ceiling fixer\ndomestic ceiling installer\nindustrial ceiling installer\nceiling fixer\nceiling tile fixer\ncommercial ceiling installer\ncommercial ceiling fixer\nsuspended ceiling installer\nsuspended ceiling fixer\nceiling tile installer','\n',char(10)),'Ceiling installers install ceilings in buildings. They apply different techniques as the situation requires-for example when fire resistance is especially important, or when space is needed between the dropped ceiling and the next floor-or specialise in one.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/41beead9-98ad-4c89-b6e0-a14149f4eeab','key_15963','','','5245','5245.1','fuel station specialised seller',replace('retail forecourt specialised seller\nfuel site salesperson\ngas station salesperson\nfuel station specialized seller\npetrol site salesperson\nretail fuel manager\npetrol site specialised seller\ngas station specialised seller\nsupermarket filling station salesperson\nfuel retail specialised seller\ngarage specialised seller\npetrol station salesperson\npetrol station specialised seller\nfuel site specialised seller\nretail fuel forecourt specialised seller\ngarage salesperson\nretail forecourt salesperson\nretail fuel forecourt salesperson\nfuel retail salesperson\nsupermarket filling station specialised seller','\n',char(10)),'Fuel station specialised sellers sell fuel, ​lubricating and cooling products for motor vehicles and motorcycles in a fuel station.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/41d4be58-3111-48b5-814a-83117983dd16','key_15964','','','8113','8113.1','derrickhand','derrickhand','Derrickhands guide the positions and movements of drill pipes. They control the automated pipe-handling equipment. They are often responsible for the condition of drilling fluids, or "mud".','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/41d609a1-62df-4cec-ac71-6434e545a729','key_15965','','','3112','3112.4','corrosion technician',replace('surface technician\npipeline corrosion control technician\ncorrosion supervisor\ncathodic protection technician\ncorrosion technician\ncorrosion monitoring technician\noffshore corrosion technician\npipeline operations technician\ncorrosion control technician\nsurface coating technician','\n',char(10)),'Corrosion technicians monitor the integrity from a pipeline and provide repairs to it if needed. They ensure that the pipelines are properly connected and are in line with the health and safety regulations. Corrosion technicians inspect cathodic protection systems and pipeline connection points for corrosion. They may also assist in the designing of pipelines, analyse soil and write reports on technical issues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/42381741-f3a7-4b26-b35c-da6fad9f1cfd','key_15966','','','3119','3119.15','product development engineering technician',replace('product development scientist\nproduct development engineer\nproduct development conservation engineer\nproduct development equipment engineer\nproduct development test engineer\nproduct development mechanical engineer\nproduct development engineering technician\nproduct development research engineer\nproduct development  research engineer\nproduct development consultant engineer\nproduct development specialist\nproduct development systems engineer\nproduction test operator\nproduct development automation engineer\nproduct development electrical engineer','\n',char(10)),'Product development engineering technicians improve efficiency of product development, set up equipment and develop and test solutions to solve technical problems. They work closely with engineers and technologists, inspect products, conduct tests and collect data.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/423f4021-322b-4257-9a4e-d6ee5d166fbc','key_15967','','','7534','7534.3.2','marine upholsterer',replace('boat upholsterer\nmarine upholstery finisher\nmarine furniture maker\nmarine interior furniture maker\nship upholsterer\nmarine upholstery repairer\nmarine soft furnishing maker\nmarine upholstery maker\nmarine upholstery assembler\nmarine upholsterer','\n',char(10)),'Marine upholsterers manufacture, assemble and repair interior components for all types of boats. They use power tools, hand tools and shop equipment to prepare and fasten materials and apply finishes. They also inspect incoming materials and prepare the boat interior for new components.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/425ed170-bf90-42c9-b5bd-148282ed96ff','key_15968','','','1221','1221.3.1','destination manager',replace('destination development manager\ndestination marketing manager\ntourism strategy manager\ndestination manager\ndestination management organisation (DMO) manager\norganisation manager','\n',char(10)),'Destination managers are in charge of managing and implementing the national/regional/local tourism strategies (or policies) for destination development, marketing and promotion.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/425f4904-3a7a-4ad0-ac5b-42b38392f768','key_15969','','','8111','8111.4','underground heavy equipment operator',replace('mine machine operator\nminers\nunderground miner operator\nheavy equipment operators\nunderground heavy equipment operator\ncoalface miner\nunderground miner equipment operator\nunderground miner (heavy equipment)','\n',char(10)),'Underground heavy equipment operators control heavy-duty mining equipment such as cutting and loading equipment to excavate and load ore and raw mineral at underground mines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/426c8cca-31f7-4a88-af60-6f5365ab3a62','key_15970','','','8342','8342.4','grader operator',replace('grader driver\nheavy plant operator, grader\nmotor-grader driver\ngrader operator\ngrader operative\nplant operator, grader\ngrader worker\nconstruction plant operator, grader','\n',char(10)),'Grader operators work with a heavy piece of mobile equipment that creates a flat surface by slicing off the top soil with a large blade. Graders usually provide a flat finish on the heavy earthmoving work performed by the scraper and bulldozer operators.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/42a96d07-1e1d-49ae-9399-c1f4be27ddea','key_15971','','','3333','3333.1','employment agent',replace('employment agent\njob placement officer','\n',char(10)),'Employment agents work for employment services and agencies. They match job seekers with advertised job vacancies and provide advice on job search activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/42abcbcb-721b-4da6-9c06-f6951969d056','key_15972','','','1324','1324.3.2.27','import export manager in perfume and cosmetics',replace('senior import export manager in perfume and cosmetics\ngraduate import export manager in perfume and cosmetics\nassistant import export manager in perfume and cosmetics\nassistant export manager in perfume and cosmetics\nsenior import manager in perfume and cosmetics\ntrainee import manager in perfume and cosmetics\ninternational trade compliance manager in  perfume and cosmetics\ninternational trade manager in  perfume and cosmetics\ngraduate export manager in perfume and cosmetics\nsenior export manager in perfume and cosmetics\nimport export manager in perfume and cosmetics\nassistant import manager in perfume and cosmetics\ngraduate import manager in perfume and cosmetics\ntrainee import export manager in perfume and cosmetics\ncustoms compliance manager in  perfume and cosmetics\ntrainee export manager in perfume and cosmetics','\n',char(10)),'Import export managers in perfume and cosmetics install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/42c769cd-4c60-4bac-94d2-29a492b82b74','key_15973','','','3343','3343.5','scopist',replace('scopist\nfreelance scopist','\n',char(10)),'Scopists edit the transcripts created by court reporters in order to make of them a readable professional legal document. They listen or read to the reports given to them in order to apply punctuation, missing words, format, and improve the accuracy of the document.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/42dbb769-4c91-47dc-bd16-ec8e70c4180f','key_15974','','','2149','2149.9.8','solar energy engineer',replace('solar energy engineer\nsolar energy technology engineering specialist\nsolar energy engineering expert\nsolar energies engineer\nsolar energy engineering specialist\nsolar energy systems engineer\nsolar engineer\nsolar energy technology engineering adviser\nsolar energy engineering adviser\nsolar power engineer\nsolar energy technology engineer\nsolar energy technology engineering consultant\nsolar energy technology engineering expert\nsolar energy engineering consultant','\n',char(10)),'Solar energy engineers design systems which generate electrical energy from sunlight, such as photovoltaic systems. They design and construct systems which optimise the energy output from solar power, and the sustainability of the production process of solar systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/42e9d1c5-76f1-41ef-996e-38d5ac711a31','key_15975','','','3115','3115.1.21','rolling stock inspector',replace('railway carriage and wagon supervisor\nsupervisor of rolling stock\nrailway inspector\ncarriage supervisor\ninspector of rolling stock\nrolling stock supervisor\ncarriage inspector\nrolling stock inspector\nrail car inspector\nrailway carriage and wagon inspector','\n',char(10)),'Rolling stock inspectors inspect wagons and carriages to assess their technical condition while grouped together and before they are used for transportation activities. They check technical devices, ensure complete and correct operation of the rolling stock, and prepare required technical documents and/or checklists. Depending on work organisation they are also responsible for limited ad-hoc maintenance or exchange work and the performance of brake tests.','','Excludes <a resource="http://data.europa.eu/esco/occupation/84219dd7-eb68-426e-9ec3-431eceb61c96" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/84219dd7-eb68-426e-9ec3-431eceb61c96"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">rolling stock engine inspector</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/42ff9e52-1f56-427d-b772-b9328c39b98a','key_15976','','','2651','2651.6','drawing artist',replace('cartoon artist\nstreet artist\ndrawing artist\nvisual art expert\nartist\nillustrator','\n',char(10)),'Drawing artists express concepts by providing a drawn representation corresponding to the idea.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4305327d-3537-4dfb-a913-bce6614362f1','key_15977','','','7133','7133.4','chimney sweep supervisor',replace('smoke-stack sweeper supervisor\nchimney sweeper supervisor\nchimney sweep supervisor\nlump sweeper supervisor\nflue sweeper supervisor\nfunnel sweeper supervisor\nchimney-sweeping chief','\n',char(10)),'Chimney sweep supervisors oversee and coordinate the activities of chimney sweeps. They do quality checks and ensure compliance with safety regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4311b9e0-064e-41bc-b120-5eb21f337780','key_15978','','','1324','1324.3.2.7','import export manager in coffee, tea, cocoa and spices',replace('international trade compliance manager in coffee, tea, cocoa and spices\ntrainee import manager in coffee, tea, cocoa and spices\nimport export manager in coffee, tea, cocoa and spices\ntrainee export manager in coffee, tea, cocoa and spices\nassistant import manager in coffee, tea, cocoa and spices\ninternational trade manager in coffee, tea, cocoa and spices\nsenior import export manager in coffee, tea, cocoa and spices\ncustoms compliance manager in coffee, tea, cocoa and spices\nassistant import export manager in coffee, tea, cocoa and spices\ngraduate import manager in coffee, tea, cocoa and spices\nassistant export manager in coffee, tea, cocoa and spices\nsenior export manager in coffee, tea, cocoa and spices\ntrainee import export manager in coffee, tea, cocoa and spices\ngraduate import export manager in coffee, tea, cocoa and spices\nsenior import manager in coffee, tea, cocoa and spices\ngraduate export manager in coffee, tea, cocoa and spices','\n',char(10)),'Import export managers in coffee, tea, cocoa and spices install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/431f0055-43ca-406c-99f0-fb10a73d684e','key_15979','','','8172','8172.5.2','planer thicknesser operator',replace('thickness specialist\nthickness operator\njointer technician\nthicknesser specialist\nplaner operator\nplaner specialist\nplaner technician\nplaner thicknesser specialist\nplaner thicknesser\nthickness planer technician\njointer operator\nthickness planer operator\njointer specialist\nthicknesser technician\nplaner thicknesser worker\nthickness planer specialist\nover-and-under technician\nover-and-under operator\nthickness technician\nover-and-under specialist\nplaner thicknesser operator\nthicknesser operator\nplaner thicknesser technician','\n',char(10)),'Planer thicknesser operators use machinery to shave wood planks to a uniform thickness. The machine usually planes both sides of the plank in one operation. They feed the plank into the machine carefully to prevent excess planing at the edge known as ''snipe''.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/432cd69f-6323-4147-94f2-342de3cb0a65','key_15980','','','3122','3122.4.11','optical instrument production supervisor',replace('optical equipment assembly manager\noptical instrument production supervisor\noptical instrument production manager\noptical instrument production line manager\noptical equipment manufacturing supervisor\nsupervisor of optical instrument production\noptical equipment production manager\nmanager of optical instrument production\noptical instrument manufacturing supervisor\noptical instrument assembly supervisor','\n',char(10)),'Optical instrument production supervisors coordinate, plan and direct the optical instrument production process. They make sure the optical glass is processed properly and optical equipment is assembled according to specifications. They manage labourers working on the production line, oversee the quality of the assembled goods, and perform cost and resource management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4370b117-d9ec-424c-80de-fd92ba740f65','key_15981','','','8342','8342.7','road roller operator',replace('roller-compactor operator\nrough roller operator\nvibrating roller driver\nride on road roller operator\nroad roller operator\nwalk-behind roller operator\nride on roller driver\nroller operator\nroller operative\nride on roller operator\nroad roller driver\nwalk-behind road roller operator\nroller-compactor driver\nroller driver\nvibrating roller operator','\n',char(10)),'Road roller operators work with equipment to compact a variety of materials, such as soil, gravel, concrete or asphalt, in the construction of roads and foundations. They walk behind, or sit on top of the road roller, depending on the type and size, and roll over the area to be compacted.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/43841fa5-f8ae-43ce-834d-5482ec23ef9e','key_15982','','','7513','7513.1','dairy products maker',replace('cream producer\ncheese producer\ncream maker\nfarmhouse dairy products maker\ncheese maker\ncraft dairy products maker\nartisan dairy products maker\nbutter producer\nheritage dairy products maker\ncheese spread maker\ncheese spread producer\nice-cream producer\nice-cream maker\ndairy products maker\ntraditional style diary products maker\nyoghurt maker\nyoghurt producer\nbutter maker\nartisanal dairy products maker','\n',char(10)),'Dairy products makers artisanally process raw milk to make dairy products such as butter, cheese, cream and milk.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/439c52ae-23ce-4d1e-bea1-bac135497977','key_15983','','','2165','2165.4.1','hydrographic surveyor',replace('hydrographer\nhydrographic research scientist\nhydrographic analyst\nhydrographic researcher\nfield hydrographer\nhydrographic surveyor\nhydrographic research analyst\nhydrography surveyor\nhydrographic scientist\nhydrographic science researcher','\n',char(10)),'Hydrographic surveyors measure and map, by means of specialised equipment, marine environments. They collect scientific data in order to study the underwater topography and morphology of bodies of water.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/43c86ca8-7357-4e8e-9a1c-bae427f61c5e','key_15984','','','8181','8181.1','auger press operator',replace('auger extrusion press operator\nauger press worker\nflue tile press operator\ndrain tile press operator\nauger press operator, manual control\nauger press operative\nsewer pipe press operator\nmanual control auger press operator\nmanual control auger press worker\nauger press operative manual control\nauger press worker, manual control\nauger press operator\nmanual control auger press operative','\n',char(10)),'Auger press operators control and adjust an auger-press in order to perform clay forming, extrusion and cutting operations on products according to specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/43d49e27-302b-439d-bce6-86555527edb5','key_15985','','','3257','3257.5.1','healthcare inspector',replace('healthcare facilities inspector\nhealthcare environment inspector\ncare commission inspector\nhealth care facilities inspector\nhospital inspector\nhealthcare inspectors\nhealthcare enforcement inspector\nhealthcare inspector\nhealth facilities surveyor\nhealth care enforcement inspector\nmedical facilities inspector\nhealth care environment inspector\nhealth care inspector','\n',char(10)),'Healthcare inspectors visit healthcare facilities to ensure proper care is given to all patients in accordance with legal requirements. They also inspect if equipment, processes and staff operate adequately to prevent the spreading of infections and diseases.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/43eb6228-8241-47d6-875e-7ccc214291d5','key_15986','','','7543','7543.3','battery test technician',replace('battery test lab technologist\nbattery test lab technology specialist\nbattery test lab technician\nbattery diagnostic testing technician\nbattery test technologist\nbattery tech\nbattery test engineer\nbattery test technician\nbattery technician\nbattery test technology expert\nbattery test laboratory technologist\nbattery test laboratory technician\nbattery test lab technology expert\nbattery test tech\ntechnician in battery testing\nbattery inspector\nbattery quality technician\nbattery testing technician\ntester of batteries\nbattery test laboratory technology expert\nbattery inspection technician\ntechnician in battery inspection\nbattery test technology specialist\nbattery diagnostic test technician\ntest technician, batteries\nbattery test laboratory technology specialist\nbattery tester','\n',char(10)),'Battery test technicians use positive and negative wired plugs to test battery''s resistance capacity. They also test rejected batteries to determine their flaws.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/43ee9c1a-59c2-48ee-afcd-c384dca92162','key_15987','','','9621','9621.1','doorman/doorwoman',replace('doorwoman\nhotel doorman\ndoorman/doorwoman\nclub doorman\nbouncer\ndoorman\nclub doorman/doorwoman\nhotel doorwoman\nhotel doorman/doorwoman\ndoor security staff\nclub doorwoman\ndoor keeper','\n',char(10)),'Doormen/doorwomen welcome guests to a hospitality establishment and provide additional services related to assistance with luggage, guests'' safety whilst ensuring security.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/441a5b76-2f51-4704-9df7-3b8e715dd3ad','key_15988','','','2632','2632.3','behavioural scientist',replace('behavioural science researcher\nbehavioural science analyst\nbehavioural science scholar\nbehavioural science research analyst\nbehavioral scientist','\n',char(10)),'Behavioural scientists research, observe and describe human behaviour in society. They draw conclusions on the motives that stir actions in humans, observe the various circumstances for different behaviours, and describe different personalities. They advise organisations and governmental institutions on this field. They may also analyse the behaviour of animals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/444c9aa9-578d-4a9a-9949-99ef1bacb20e','key_15989','','','3115','3115.1.4','automotive engineering technician',replace('bus engineering technician\nbus design checker\nautomotive design checker\ntruck design checker\nlorry engineering technician\nautomotive engineering technician\nequipment and vehicle technician\nmotorcycle engineering technician\ncar engineering technician\nmotor vehicle design checker\nvehicle production design checker\ncar design checker\ncoach design checker\ncoach engineering technician\nlorry design checker\ntruck engineering technician\nvehicle production engineering technician\nmotorcycle design checker','\n',char(10)),'Automotive engineering technicians work with automotive engineers to operate, repair, maintain and test equipment used in motor vehicles. In some environments, such as an airport they are responsible for keeping equipment and vehicles serviceable. They review blueprints and designs to determine test specifications and procedures. Automotive engineering technicians use software to make sure that parts of a motor vehicle are functioning properly. They record test procedures and results, and make recommendations for changes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/44508e7b-f56b-4bfb-9cd6-879838aecd94','key_15990','','','2164','2164.1','land planner',replace('city planning engineer\ncity planner\nplanning technician\nland planner\nurban land planner\nurban planner\nplanner\nland manager\nland use planner\ntown planner\nplanning engineer\nland developer\ncivil engineer','\n',char(10)),'Land planners visit sites in order to create projects and plans for land usage and development. They collect and analyse data about the land. Land planners provide advice on the efficiency and safety of development plans.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/447cfc9c-c0bf-4553-8f47-91f7ce5e51bc','key_15991','','','8181','8181.9','glass polisher',replace('glass production polisher\nglass finisher\nglass grinder\nglass edge grinder\nplate glass polisher\nglass polisher','\n',char(10)),'Glass polishers finish plate glass to make a variety of glass products. They polish the edges of the glass using grinding and polishing wheels, and spray solutions on glass or operate vacuum coating machines to provide a mirrored surface.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/449f6aef-a8e6-4edc-aea4-802a7b773e00','key_15992','','','1324','1324.3.1.6.2','agricultural raw materials, seeds and animal feeds distribution manager',replace('senior agricultural raw materials, seeds and animal feeds distribution manager\nagricultural raw materials, seeds and animal feeds distribution manager\nagricultural raw materials, seeds and animal feeds logistics planner\nagricultural raw materials, seeds and animal feeds supply chain planner\nagricultural raw materials, seeds and animal feeds logistics manager\nagricultural raw materials, seeds and animal feeds supply chain manager\ngraduate agricultural raw materials, seeds and animal feeds distribution manager\ntrainee agricultural raw materials, seeds and animal feeds distribution manager\nagricultural raw materials, seeds and animal feeds distribution planner\nassistant agricultural raw materials, seeds and animal feeds distribution manager','\n',char(10)),'Agricultural raw materials, seeds and animal feeds distribution managers plan the distribution of agricultural raw materials, seeds and animal feeds to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/44b0f118-2a70-4eec-b301-89d7fd84a3d0','key_15993','','','7521','7521.1','wood drying kiln operator',replace('dry kiln technician\nwood-drying kiln specialist\ndrying kiln operator\ndrying kiln specialist\nwood-drying kiln operator\nwood drying kiln operative\ndry kiln operator\nwood drying kiln specialist\nwood drying kiln technician\nwood drying kiln worker\ndrying kiln technician\nwood-drying kiln technician\nwood drying kiln operator\ndry kiln specialist','\n',char(10)),'Wood drying kiln operators control the process of applying heat to moist or ''green'' wood in order to obtain usable dry wood. Depending on the type of kiln, the drying operator will be responsible for moving the wood into and out of the kiln, temperature control, and ventilation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/44cd9d91-5552-4735-86b8-4b2c5d39efc8','key_15994','','','1439','1439.5','laundry and dry cleaning manager',replace('laundry and dry-cleaning coordinator\ndry-cleaning and laundry supervisor\nlaunderette manager\nlaundry and dry cleaning service manager\nlaundry service manager\nlaundry and dry cleaning coordinator\ndry cleaning and laundry supervisor\nlaundry and dry-cleaning service manager\nlaundry manager\ndry-cleaning service manager\nlaundry and dry cleaning manager\ndry cleaning service manager\nlaundromat manager','\n',char(10)),'Laundry and dry cleaning managers oversee the laundry operations in an institutional laundry. They supervise laundry and dry cleaning staff, plan and enforce safety procedures, order supplies and oversee the laundry''s budget. Laundry and dry cleaning managers ensure the quality standards and that customers'' expectations are met.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/44e0c015-309d-4f67-98a3-1e036966ed7f','key_15995','','','1211','1211.1.3','budget manager',replace('budget coordinator\nbudget counselor\nmoney manager\nbudget and planning manager\nbudget and policy manager\nbudget and analysis manager\nbudgeting manager\nbudget manager','\n',char(10)),'Budget managers assess financial proposals of different departments before granting financial resources to projects. They monitor the implementation of budget policies and procedures. They work closely with other departments in the evaluation of programs, their impact in the organisation, the revenue that they can yield, and the financial efforts required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/44ec4c04-c2d4-4b92-bb27-e56f5b096d97','key_15996','','','1349','1349.1','airside safety manager',replace('aviation safety manager\nairport safety manager\naviation safety director\ndirector of airside safety\nairport safety director\nairside safety director\nairside safety manager\nmanager of airside safety','\n',char(10)),replace('Airside safety managers supervise the airside operations'' safety and security and advise the civil aviation authorities on the airport information systems changes. They write safety analysis reports, ensure compliance with standards and regulations and  improve the airside safety procedures.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/45037d43-a8f5-4f46-b332-b2935bc305f4','key_15997','','','8332','8332.2','cargo vehicle driver',replace('courier delivery driver\nteamster\ndelivery driver\nheavy goods vehicle driver\ntrucker\nvan driver\nHGV driver\ntruckie\ncargo vehicle driver\ndriver of cargo vehicles\nlorry driver\ntruck driver\ncourier\nfreight driver\npackage delivery driver','\n',char(10)),'Cargo vehicle drivers operate vehicles such as trucks and vans. They may also take care of the loading and unloading of cargo.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/45170a38-b3ae-4a96-90ff-eb45e55600b6','key_15998','','','2652','2652.1.1','music arranger',replace('arranger and orchestrator\narranger\narranger & orchestrator\narrangement expert\nmusical arrangement specialist\nmusical arrangement consultant\nmusic arranger\norchestrator','\n',char(10)),'Music arrangers create arrangements for music after its creation by a composer. They interpret, adapt or rework a composition for other instruments or voices, or to another style. Music arrangers are experts in instruments and orchestration, harmony, polyphony and composition techniques.','','Excludes composer.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4542ff68-f809-4ba1-857c-fd26d0914eaa','key_15999','','','8111','8111.1','dewatering technician',replace('dewatering operator\ndewatering equipment operator\nfilter operator\ndewatering equipment technician\ndewatering technician\nsurface dewaterer\ninfrastructure technician\nfilter operators\ndewatering techicians\nsite services technician\ndewatering filter operator','\n',char(10)),'Dewatering technicians install and operate pumps, spares, pipe ranges, and vacuum dewatering systems to collect and remove liquids and chemicals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4543bf7b-8ffb-4ade-88b9-58da34366f04','key_16000','','','2523','2523.1','ICT capacity planner',replace('IT capacity planner\nICT capacity planners\nICT performance manager\nIT capability planner\nICT capability manager\nICT capacity planner','\n',char(10)),'ICT capacity planners ensure that the capacity of ICT services and ICT infrastructure is able to deliver agreed service level targets in a cost effective and timely manner. They also consider all resources required to deliver the appropriate ICT service, and plan for short, medium, and long term business requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/45517bc3-0887-416d-95fe-6ace1b90bddd','key_16001','','','1321','1321.2.1.7','metallurgical manager',replace('metallurgy specialist\nmetallurgy manager\nchief metallurgist\nmetallurgical manager\nplant metallurgist\nhead metallurgist','\n',char(10)),'Metallurgical managers coordinate and implement short and medium term metallurgical or steel-making production schedules, and coordinate the development, support and improvement of steel-making processes, and the reliability efforts of the maintenance and engineering departments. They also partner with ongoing remediation initiatives.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4552095e-3396-4831-86ad-4fc34ec39a19','key_16002','','','6130','6130.1','farm manager',replace('farming operations manager\nfarmer\nagricultural operations manager\nagricultural manager\nfarm manager','\n',char(10)),'Farm managers plan and organise the daily operations, resourcing and business management of animal and crops producing farms.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4553ecf8-1f0c-4bf1-8718-d8a6b95d6fc2','key_16003','','','2320','2320.1.2','air traffic instructor',replace('air traffic control trainer\nair traffic control instructor\nair traffic trainer\nair traffic instructor\ninstructor of air traffic control','\n',char(10)),'Air traffic instructors train people in all the matters regarding with the operations in air navigation services such as the management of flight traffic and the communication for navigation in aerodromes. They teach all the directives issued by air traffic control for the purpose of passing to trainees the sense of safety and expeditious flow of air traffic.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4568a282-3904-4dfc-a6ec-e847b3ca90e8','key_16004','','','3122','3122.4.13','plastic and rubber products manufacturing supervisor',replace('plastics and rubber products manufacturing manager\nrubber goods manufacturing supervisor\nplastics and rubber goods manufacturing coordinator\nplastics and rubber goods manufacturing supervisor\nplastic and rubber products manufacturing supervisor\nrubber goods production supervisor\nplastics and rubber goods production supervisor\nplastic products manufacturing supervisor\nrubber products manufacturing supervisor\nplastics and rubber products manufacturing coordinator\nplastic goods manufacturing supervisor\nplastics and rubber goods manufacturing manager\nplastic and rubber goods production supervisor\nplastics products manufacturing supervisor\nplastic goods production supervisor\nplastics and rubber products manufacturing supervisor','\n',char(10)),'Plastic and rubber products manufacturing supervisors manage and coordinate the activities of personnel involved in plastic or rubber products manufacturing, making sure the production is efficiently, safely and cost-effectively processed. They are responsible for the installation of new production lines and for the provision of trainings.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/456ed487-02f2-45c9-9213-d0fd8e9a069d','key_16005','','','2514','2514.1','ICT application configurator',replace('app configurator\nICT application configurator\nsoftware application configurator\napplication software configurator\napplication configurators','\n',char(10)),'ICT application configurators identify, record, and maintain user-specific application configurations based upon user requirements and business rules. They configure generic software systems in order to create a specific version applied to an organisation''s context. These configurations range from adjusting basic parameters through the creation of business rules and roles in the ICT system to developing specific modules (including the configuration of Commercial off-the-shelf systems (COTS)). They also document configurations, perform configuration updates, and ensure the configurations are correctly implemented in the application.','','Excludes people performing managerial activities. Includes people performing statistical and consultation activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4588cd69-e6a5-4668-94c7-f02a3d86a649','key_16006','','','2356','2356.1.1','digital literacy teacher',replace('digital literacy educator\ncomputer literacy instructor\ndigital literacy trainer\ndigital literacy teacher\ndigital literacy instructor\ncomputer literacy teacher\ndigital literacy teaching practitioner\ndigital literacy lecturer\nteacher of digital literacy\neducator in digital literacy','\n',char(10)),'Digital literacy teachers instruct students in the theory and practice of (basic) computer usage. They teach students digital literacy and, optionally, more advanced principles of computer science. They prepare the students with knowledge of software programmes ensure that computer hardware equipment is properly used. Digital literacy teachers construct and revise course content and assignments, and update them according to technological developments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/45a28fe1-30a4-4175-af19-e451ada60858','key_16007','','','1346','1346.3','credit union manager',replace('credit union specialist\ncredit union manager\ncredit union branch manager\ncredit union examiner\ncredit union field examiner\ncredit union principal examiner\nfinancial cooperative manager\ncredit unions supervisor\ncredit unions examiner','\n',char(10)),'Credit union managers oversee and manage member services, supervise staff and operations of credit unions. They inform staff about the latest credit union procedures and policies and prepare financial reports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/45f8504d-24ac-4bcd-8692-a93acc74204c','key_16008','','','3119','3119.16.3','leather goods quality control laboratory technician',replace('leather goods QC laboratory specialist\nleather goods quality assurance laboratory specialist\nleather goods quality assurance laboratory technologist\nleather goods quality control laboratory technician\nleather goods QC laboratory technologist\nQC laboratory technician leather goods\nleather goods quality control laboratory technologist\nleather goods QA/QC laboratory technologist\nleather goods QC laboratory technician\nleather goods quality control laboratory specialist\nQC/QA laboratory technician leather goods\nleather goods QC/QA laboratory technician','\n',char(10)),'Leather goods quality control laboratory technicians perform laboratory control tests according to the national and international standards. During laboratory control tests they prepare samples, address test procedures, analysis and interpretation of the results and comparison with guidelines and standards and prepare reports. They make the linkage with outsourced laboratories for the tests which cannot be performed inside the company. They propose corrective and preventive measures.','',replace('Excludes people performing managerial activities. \nIncludes people performing activities which require a broad understanding of the leather goods processes and products, related materials and technology and specific knowledge on standards for quality control laboratory tests.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/461959ed-6a80-4c33-a75e-26aaeb52a5a7','key_16009','','','7126','7126.2','drain technician',replace('sewer inspector\nsewer and drain inspector\nsewer pipe inspector\nsewer pipe repairer\nenvironmental technician, drainage\nproject technician, drainage\nstorm water drain technician\ndrainage system technician\nwater resources technician\nsewer and drain repairer\nflood risk technician\nsewer line repairer\nplumbing and drainage technician\nwater and drainage technician\npublic works inspector\ndrainage systems technician\nservice drain inspector\nsewer cleaner\ndrain technician\ndrain cleaner\nsewer line inspector\nsewer technician','\n',char(10)),'Drain technicians install and maintain drainage equipment used in sewer systems, such as pipes and valves. They analyse the design and ensure the proper installation of the drainage system, and perform maintenance and repair duties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/46354077-416f-4440-a0c3-dddee9031c05','key_16010','','','2320','2320.1.10','electricity and energy vocational teacher',replace('electricity and energy instructor\nteacher of electricity and energy\nvocational teacher in electricity and energy\ninstructor in energy and electricity\nelectricity and energy vocational teacher\nelectricity and energy vocational trainer\neducator in electricity and energy\nteaching practitioner in electricity and energy','\n',char(10)),'Electricity and energy vocational teachers instruct students in their specialised field of study, electricity and energy, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for a electricity and energy-related profession. Electricity and energy vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of electricity and energy through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/463558cd-6e02-43a8-a675-d4e65af70431','key_16011','','','3230','3230.1','herbal therapist',replace('iridologist\nherbal practitioner\nnaturopathic physician\nherbal medicine practitioner\nherbalist\npractitioner of herbal medicine\nherbal therapist\nmedical herbalist\nherbal medicine therapist\npractitioner of herbal therapy\nphytotherapist','\n',char(10)),'Herbal therapists use herbal medicine to relief pain and treat illnesses such as allergies, chronic physical conditions or stress-related conditions. They study the properties and benefits of plants and create a personalised treatment plan for the patients.','','Includes doctors of medicine and pharmacist or other healthcare professionals (e.g. midwives  <a resource="http://data.europa.eu/esco/occupation/95cda6a9-a70a-4a93-9442-11d573cb4a02" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/95cda6a9-a70a-4a93-9442-11d573cb4a02"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">midwife</span></a>.) who perform herbal therapy.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/463a687a-c7e9-469a-bb38-dcdfe1e7e35f','key_16012','','','1420','1420.4.3','audio and video equipment shop manager',replace('audio and video equipment shop manager\nhi-fi shop manager\naudio-visual store manager\nhi-fi store manager\nhome entertainment store manager\nhome entertainment shop manager\nhome entertainment superstore manager\nmultimedia equipment shop manager\naudio and video shop manager\nmultimedia store manager\naudio and video equipment store manager\nmultimedia superstore manager\naudio-visual shop manager\naudio and video store manager','\n',char(10)),'Audio and video equipment shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4666cccf-cfbc-4296-95b5-67dad2f620d2','key_16013','','','5165','5165.1.1','bus driving instructor',replace('bus driving instructor\nbus driver training  instructor\ndriver education instructor\nPCV instructor\ndriving instructor\ndriver training instructor\nPCV driving instructor\ncoach driver training  instructor\nbus driving training  instructor\nbus driver instructor\nCat D driving instructor\ncoach driving instructor\nCat D instructor','\n',char(10)),'Bus driving instructors teach people the theory and practice of how to operate a bus safely and according to regulations. They assist students in developing the skills needed to drive and prepare them for the driving theory tests and the practical driving test.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/46bbecf4-368a-44a8-9262-dcd775ae2c46','key_16014','','','8113','8113.9','well-digger',replace('well-digger\nwater well-digger\nwater borehole driller\nwater borehole digger\ndrillers offsider\nborehole digger\ndrilling supervisor\nsurface driller\ngeotechnical driller\nenvironmental driller\nwell technician\nwater well driller\nwaterwell driller','\n',char(10)),'Well-diggers operate drilling machinery and equipment to create and maintain wells for use in extracting ore and other liquids and gases. They record operations, maintain equipment, seal unused wells and prevent ground contamination.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/46d0568d-7415-4643-94aa-89d2470fbe3c','key_16015','','','4229','4229.1','contact tracing agent',replace('contact tracing specialist\ncoronavirus field agent\nclinical contact caseworker \ncovid disease investigator\ncall centre contact tracing representative\ncontact tracing volunteer\ncontact tracing representative\ncontact tracing support\ncovid contact tracer','\n',char(10)),'Contact tracing agents assess the exposure of individuals to infectious diseases, advise them and their contacts about measures to contain the spread of the illnesses and follow up with them on a regular basis. They use texting, emailing or calling people who test positive to inquire about the persons with whom they have had contact with. Contact tracing agents can also do field visits, to check physically if people are respecting the measures of self-isolation or quarantine, as recommended by the authorities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/46db7e2e-d44d-412a-9a80-02d43111aca8','key_16016','','','2355','2355.5','photography teacher',replace('teacher of photography\nmedia arts teacher\nphotography teacher\neducator in photography\nteacher of media arts\nphotography tutor\nphotography educator\ninstructor in photography\nphotography instructor\ntutor in photography','\n',char(10)),'Photography teachers instruct students in the various techniques and styles of photography, such as (group) portrait, nature, travel, macro, underwater, black and white, panoramic, motion, etc. They provide students with a notion of photography history, but mainly focus on a practice-based approach in their courses, in which they assist students in experimenting with and mastering different photography techniques and encourage them to develop their own style. Photography teachers monitor students'' progress and set up exhibitions to show of students'' work to the public.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/46f9d58f-11bd-4d96-b000-ff4490d3009b','key_16017','','','2320','2320.1.16','industrial arts vocational teacher',replace('industrial arts teacher\nindustrial arts instructor\ntechnical handicraft teacher\nindustrial arts educator\neducator in industrial arts\nvocational instructor in industrial arts\ntechnical handicraft instructor\nindustrial arts vocational teacher\nvocational teacher in industrial arts\nindustrial arts vocational instructor','\n',char(10)),'Industrial arts vocational teachers instruct students in their specialised field of study, industrial arts, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for an industrial arts-related profession, working with wood and metal, such as carpenter or sheet metal worker. Industrial arts vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of industrial arts through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/47300afa-5496-484c-8f65-14247a98b2b8','key_16018','','','9312','9312.1','civil engineering worker',replace('site worker\ngroundworker\ncivils operative\ncivil engineering company worker\nconstruction worker\ncivils worker\ncivils ground worker\nground worker\nbuilding worker\ncivil engineering worker\ncivil engineering contract worker\ncivils groundworker','\n',char(10)),'Civil engineering workers perform tasks concerning the cleaning and preparation of construction sites for civil engineering projects. This includes the work on building and maintenance of roads, railways and dams.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/476420bb-2577-4f79-ad32-186f19c4fd02','key_16019','','','3153','3153.2.4','helicopter pilot',replace('helicopter pilot\ncommercial helicopter pilot\npilot of helicopters\nairline helicopter transport pilot\nhelicopter operator','\n',char(10)),'Helicopter pilots fly helicopters in order to transport passengers and cargo from one place to another. They plan flights using aeronautical charts and navigation instruments. Prior to departure, they inspect helicopters following checklists to detect leaking hydraulic fluid, inoperative control, low fuel level, or other unsafe conditions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4779055b-f769-4c6d-8e0e-088bb74cfce9','key_16020','','','9129','9129.3','swimming facility attendant',replace('pool attendant\nswimming pool cleaner\nattendant in swimming pool\nswimming facility attendant\nswimming pool attendant\nbeach attendant','\n',char(10)),'Swimming facility attendants handle the daily activities of a swimming facility such as a swimming pool, beach and lake. They clean the facility, maintain a good attitude towards the clients and ensure the overall safety within the facility.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4791e217-d607-40d5-9f84-df1d0a071cbc','key_16021','','','2431','2431.10','marketing consultant',replace('marketing adviser\nmarketing specialist\nmarketing advisor\nstrategic marketing consultant\nmarketing analyst\ndigital marketing strategist\nmarketeer\nair transport marketer\nsales and marketing consultant\nmarketing campaign planner','\n',char(10)),'Marketing consultants advise companies in the development of marketing strategies for specific purposes. They can advise and develop strategies for the entry of a brand in the market, for the re-launch of a product, for the introduction of a new product, or for the positioning of a commercial image. They perform previous studies of the position of the company and perception of customers in order to define the marketing approach.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4795d4e3-c623-47ff-abab-7de97fcb764e','key_16022','','','2431','2431.2','advertising media buyer',replace('advertising media buyer\nadvertising media specialist\nspace buyer\nmedia buyer\npress buyer\nradio buyer\nmedia buyer and planner\nTV buyer\nmedia space buyer\nsenior media buyer\nmedia buyer & planner\ninternational media buyer\nmedia and advertising buyer\nadvertisment media buyer\nradio media buyer','\n',char(10)),'Advertising media buyers purchase, on behalf of their clients, advertising space in the print, broadcast and online media. They analyse the effectiveness and appropriateness of the different channels depending on the good or service, providing advice for the decision-making. They try to negotiate the best price, without compromising the quality of the advertisements. They support the development and implementation of marketing and advertising plans through the most suited media outlet.','','Includes people working with the advertising media planner.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/479ede81-bd51-4f3b-9d30-4910699fd211','key_16023','','','2310','2310.1.21','journalism lecturer',replace('professor of journalism\njournalism and media docent\nlecturer in journalism and media studies\njournalism lector\njournalism lecturer\nmedia studies lecturer\njournalism and media professor\njournalism and media teacher\njournalism and media instructor\nuniversity journalism lecturer\nprofessor of media studies\njournalism and media lecturer\nspecialist lecturer in journalism\nhigher education journalism teacher\nmedia studies teacher\njournalism teacher\nuniversity lecturer in journalism\nlecturer in journalism\nmedia studies instructor\nlecturer in journalism and media\nmedia studies docent\njournalism docent\nmedia studies professor\nlecturer of journalism\njournalism instructor\ninstructor in journalism\njournalism professor','\n',char(10)),'Journalism lecturers and media are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, journalism and media, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of journalism and media, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/47ae3ed9-e03c-450e-9ea8-aab317545adc','key_16024','','','3134','3134.1','gas processing plant control room operator',replace('process control operator\nmonitoring panel operator\nprocess plant operator\ngas processing control room manager\ngas processing plant control room operator\ncontrol panel monitor\ngas processing control room technician\npanel board operator\ngas processing plant operator\ngas processing control room supervisor\ngas process operator\npanel controller\nprocess control room operator\nplant monitoring operator','\n',char(10)),'Gas processing control room operators perform a range of tasks from the control room of a processing plant. They monitor the processes through electronic representations shown on monitors, dials, and lights. They make changes to variables and communicate with other departments to make sure processes keep running smoothly and according to established procedures. They take appropriate actions in case of irregularities or emergencies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/47af27c4-5b45-4cce-ba01-4e96f8cf97b9','key_16025','','','3131','3131.1','offshore renewable energy plant operator',replace('wave energy plant operator\nmarine wave energy operator\nmarine renewable energy plant operator\nwave farm operator\nocean energy plat operator\nORE plant operator\nmarine wind energy technician\nwave energy operator\nmarine wind energy operator\nmarine tidal stream operator\noffshore wind energy operator\ntidal farm operator','\n',char(10)),'Offshore renewable energy plant operators operate and maintain equipment which produce electrical energy from marine renewable sources such as offshore wind power, wave power, or tidal currents. They monitor measuring equipment to ensure the safety of operations, and that the production needs are met. They also react to system problems, and repair faults.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/47b747bb-5bbf-40d5-aa3e-8d4a79238502','key_16026','','','1420','1420.4.27','jewellery and watches shop manager',replace('jewellery shop manager\ndiamond shop manager\ndiamonds and precious metals jewellery shop manager\ndiamonds and precious metals store manager\njewelry and watches shop manager\ndiamond store manager\njewellery store manager\njewellery and watches store manager\nluxury watches and diamond jewellery shop manager\nluxury watches and diamond jewellery store manager','\n',char(10)),'Jewellery and watches shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/47c27a07-4657-42b7-adee-d805902c1ff8','key_16027','','','8342','8342.2','dredge operator',replace('dredde operator\ndredge operator\nport dredge operator\nport dredger operator\ndredger operator\ndredge operatr','\n',char(10)),'Dredge operators work with industrial equipment to remove underwater material in order to make the area accessible to ships, to establish ports, to lay cables or for other purposes, and move the material to the desired location.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/47dd863b-b124-4bb1-bb42-a9498492a8cb','key_16028','','','2146','2146.4','liquid fuel engineer',replace('fuel specialist\nfuel chemist\nnatural gas specialist\nbiofuel engineer\ngas liquids engineer\nnatural gas engineer\nchemical engineer\nbiofuel specialist\nfuel engineer\nfuel system engineer\nliquid fuel engineer\nliquid fuel system maintenance engineer\npetrol specialist\npetrol engineer','\n',char(10)),'Liquid fuel engineers evaluate liquid fuel extraction sites. They design and develop methods for extracting liquid fuels from underneath the earth surface, these fuels include petroleums, natural gas, liquefied petroleum gas, non-petroleum fossil fuels, biodiesel and alcohols. They maximise the recovery of hydrocarbon at a minimum cost, pursuing minimal impact on the environment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/47e4135b-95d1-4462-920c-afcbd7c4d9be','key_16029','','','3521','3521.1.3','performance rental technician',replace('performance rental monitor\nload cell hire technician\nrental equipment performance technician\nrental equipment performance engineer\nperformance rental technician\nsound equipment hire technician\nlighting hire equipment technician\nvenue performance monitor\nequipment technician\nvenue technician (rental equipment)\nequipment hire technician\nweight report technician\nperformance rental engineer\nspecial effects hire equipment technician','\n',char(10)),'Performance rental technicians prepare, maintain, issue, transport, set‐up, program, operate, take in, check, clean and store audiovisual, performance and event equipment. Their work is based on plans, instructions and order forms.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/47e81c7f-2d04-4a60-a8ff-9913c36d8344','key_16030','','','2431','2431.10.3','marketing assistant',replace('marketing practitioner\ngeneral marketing assistant\nmarketing operations assistant\nmarketing assistant\nmarketing executive assistant\nmarketing associate\nmarketing officer','\n',char(10)),replace('Marketing assistants support all the efforts and operations carried out by marketing managers and officers. They prepare reports in relation to the marketing operations needed by other departments, especially account and financial divisions. They ensure that resources needed by the managers to perform their job are in place. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/480d2853-db07-43d4-847f-7ffdd8d368f5','key_16031','','','3431','3431.2','photojournalist',replace('visual journalist\nphotographic journalist\nphoto-journalist\nmulti media image journalist\nphoto reporter\nphoto correspondent\nnews photographer\nphoto journalist\nphotojournalist\nimage journalist\ninvestigative photographer\nnewspaper photographer','\n',char(10)),'Photojournalists cover all kinds of news events by taken informative images. They tell stories by taking, editing and presenting images for newspapers, journals, magazines, television and other media.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/48318e0f-b54b-4654-b296-47c8e544da23','key_16032','','','8189','8189.1','edge bander operator',replace('adhesive bonding machine setter\nedgebander operative\nedgebander tender\nbanding machine tender\nedge bander operator\nedge bander tender\nedgebander setter\nadhesive bonding machine worker\nedge bander setter\nadhesive bonding machine operator\nedge bander operative\nadhesive bonding machine tender\nbanding machine setter\nedge bander worker\nadhesive bonding machine operative\nbanding machine operative\nbanding machine worker\nbanding machine operator\nedgebander worker','\n',char(10)),'Edge bander operators tend machines that veneer materials, mostly wood, to increase durability and make them aesthetically pleasing. They also regulate the speed of the belt, set temperature control to regulate heat, brush glue on edges of panels and start and regulate the machine.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/484ca14a-8e11-4ae1-ba9c-901cd86df56b','key_16033','','','3324','3324.4.28','wholesale merchant in pharmaceutical goods',replace('wholesale trader in pharmaceutical goods\nbusiness to business sales consultant in pharmaceutical goods\ntrainee wholesale merchant in pharmaceutical goods\nwholesaler in pharmaceutical goods\ngraduate wholesale merchant in pharmaceutical goods\nwholesale merchandiser in pharmaceutical goods\nB2B sales consultant in pharmaceutical goods\nsenior wholesale merchant in pharmaceutical goods\nwholesale buyer in pharmaceutical goods\nassistant wholesale merchant in pharmaceutical goods\nbusiness to business seller in pharmaceutical goods\nwholesale merchant in pharmaceutical goods','\n',char(10)),'Wholesale merchants in pharmaceutical goods investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4874fa37-0cd1-4a68-aed8-a838851f242d','key_16034','','','2152','2152.1.4','language engineer',replace('language technology engineering consultant\nlanguage technology engineering adviser\ncomputer-aided translation engineer\nlanguage engineer\nlanguage engineering consultant\nlanguage technology engineering expert\nlanguage technology engineer\ncomputer-assisted translation engineer\nlinguistic engineer\nmachine translation engineer\nCAT engineer language engineering expert\nlanguage engineering adviser\nlanguage technology engineering specialist\nlanguage engineering specialist\nlanguage technologist','\n',char(10)),'Language engineers work within the field of computing science, and more specifically in the field of natural language processing. They aim to close the gap in translation between accurate human translations to machine-operated translators. They parse texts, compare and map translations, and improve the linguistics of translations through programming and code.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4887c8a5-35d3-44c3-be69-346027e485f0','key_16035','','','1411','1411.2','hospitality entertainment manager',replace('hospitality entertainment manager\nentertainments manager\ncruise entertainment manager\nholiday entertainment manager\nholiday camp entertainment manager\namusement manager\nentertainment manager\nresort entertainment manager','\n',char(10)),'Hospitality entertainment managers are in charge of managing the team which creates entertainment activities for the guests of a hospitality establishment.','','Excludes people performing activities focused culture aspects. Includes people perfoming entertainment and leisure activities for guests.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/489b9a38-89a2-425c-95b9-79cec66379e1','key_16036','','','5142','5142.8','pedicurist',replace('mani-pedi attendant\npedicurist\nfoot care attendant','\n',char(10)),'Pedicurists offer cosmetic treatment and care for their clients'' feet and toenails. They cut and shape toenails, give footbaths and exfoliation treatments and apply nail polish.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/48b336d2-7ffb-49a3-a010-444b68ddd12d','key_16037','','','7511','7511.1.1','halal butcher',replace('halal meat boner\nhalal butcher\nhalal meat butcher\nhalal meat slicer','\n',char(10)),'Halal butchers order, inspect and buy meat to prepare it and sell it as consumable meat products in accordance with Islamic practices. They perform activities such as cutting, trimming, boning, tying, and grinding beef and poultry meat. They prepare halal meat for consumption.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/48c8eccb-4bd0-487e-a187-4953cbbe956e','key_16038','','','2166','2166.3','animator',replace('animators\ncomputer animation artist\nanimation designer\n3D animation artist\ncartoon animator\n2D animator\nhand-drawn animator\ncomputer animator\n3D animation specialist\nanimation specialist\n3D animation designer\n3D computer-generated imagery animator\n2D computer animation artist\n2D hand-drawn animator\n3D computer animation artist\ntraditional animator\n2D animation artist\n2D animation specialist\nanimator\nCGI animator\n2D animation designer\nanimation artist','\n',char(10)),'Animators use software to create animations, these are rapidly sequenced together images to create an illusion of movement.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/48cff102-9fe2-4e0f-8156-88fd8a686e6c','key_16039','','','8151','8151.2.3','yarn spinner',replace('cotton spinner\nthread spinner\nspinning machine tender\nspinning-frame tender\nspinning mechanic\nyarn spinners\nstaple yarn spinner\nyarn spinner\nspinning mechanics','\n',char(10)),'Yarn spinners convert fibres into yarns.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/49023c69-302f-4671-b26f-7229d1a53915','key_16040','','','2310','2310.1.10','communications lecturer',replace('lecturer in communication and media\ncommunications instructor\nlecturer in communications\ncommunications teacher\nuniversity communications teacher\nprofessor of communications\ncommunication and media lecturer\ncommunications docent\ncommunications lecturer\ncommunications lector\nprofessor of communication\nuniversity lecturer in communications\nuniversity communications lecturer\ncommunications professor\nlecturer of communications\nhigher education communications teacher','\n',char(10)),'Communications lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, communications, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, grading papers and exams and leading review and feedback sessions for the students. They also conduct academic research in their field of communications, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/490d0c6f-0dc8-4825-9f17-207749fb1785','key_16041','','','7421','7421.1','ATM repair technician',replace('ATM servicer\ncashpoint repair technician\nATM repair technician\ncash machine repair technician\nautomated teller machine repair technician\nautomatic teller machine repair technician','\n',char(10)),'ATM repair technicians install, diagnose, maintain and repair automatic teller machines. They travel to their clients'' location to provide their services. ATM repair technicians use hand tools and software to fix malfunctioning money distributors.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4910419f-b4af-4f59-b544-9dbebc8a74f0','key_16042','','','7411','7411.1','electrician',replace('electrical maintenance technician\nmaintenance electrician\nelectrical installer\nelectrical services installer\nelectrical maintenance worker\nwirewoman\nelectrical worker\ninstallation electrician\nwireman\nelectrician\nelectrical systems installer','\n',char(10)),'Electricians fit and repair electrical circuits and wiring systems. They also install and maintain electrical equipment and machinery. This work can be performed indoors as well as outdoors, in nearly every type of facility.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/491cf76d-eb0d-4f15-90f0-3f5c9b11472a','key_16043','','','2120','2120.4','gambling quality assurance engineer',replace('gambling, betting, and lottery quality assurance engineer\ngaming quality assurance engineer\nbetting quality controller\nlottery quality assurance engineer\nlottery quality controller\nbetting quality assurance engineer\ntest operator in gambling\ngambling quality controller\ngambling engineer','\n',char(10)),'Gambling quality assurance engineers design and plan methods to test the gambling, the lottery or betting games. They execute the tests. These games can be both for public or for private audience, and the tests are for both online and land-based gaming.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/49607371-2712-4b0a-8d7c-1b4bc22d9581','key_16044','','','1324','1324.3.1.6.32','wood and construction materials distribution manager',replace('wood and construction materials logistics planner\ntrainee wood and construction materials distribution manager\nsenior wood and construction materials distribution manager\nwood and construction materials supply chain manager\nassistant wood and construction materials distribution manager\nwood and construction materials supply chain planner\nwood and construction materials distribution manager\nwood and construction materials distribution planner\nwood and construction materials logistics manager\ngraduate wood and construction materials distribution manager','\n',char(10)),'Wood and construction materials distribution managers plan the distribution of wood and construction materials to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/49713da4-4e97-4f82-bd1c-16ce7f9179b7','key_16045','','','5142','5142.5','make-up artist',replace('prosthetics maker\nspecial effects & make-up artist\nspecial effects and make-up artist\ntheatre makeup artist\nmake up artist\nspecial effects and make up artist\nprosthetics make up artist\nprosthetics and make up artist\nspecial effects & make up artist\nspecial effects make-up artist\nprosthetics & make-up artist\nprosthetics and make-up artist\nprosthetics make-up artist\nprosthetics & make up artist\nmake-up artist\nmake-up specialist\nfashion makeup artist\nmakeup stylist\nprosthetics builder\nmake up consultant\nspecial effects make up artist\nmake up specialist\nmake-up consultant','\n',char(10)),replace(' Make-up artists assist and support artists before, during and after the performance and filming of movies or television programs to ensure the make-up is in line with the artistic vision of the director and the artistic team. They create images and characters through make-up and prosthetics. They maintain, check and repair prosthetics and assist with quick changes. \n  \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4975c4cc-a89f-4ea8-82aa-501258dfb2f9','key_16046','','','3321','3321.3.1','property insurance underwriter',replace('property underwriter\nhome office insurance underwriter\nproperty insurance underwriter\nproperty and automobile underwriter\nproperty insurance assessor','\n',char(10)),'Property insurance underwriters assess and determine the risk and coverage of a client''s property insurance. They analyse and review underwriting policies according to legal regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/497a65bf-04e5-48ed-939e-2a68419f8144','key_16047','','','2145','2145.1.7','oenologist',replace('winery cellar master\nwine producer\noenologist\nwine maker\nwine advisor\nwine production expert\nwine production specialist','\n',char(10)),'Oenologists track the wine manufacturing process in its entirety and supervise the workers in wineries. They supervise and coordinate production to ensure the quality of the wine and also give advice by determining the value and classification of wines being produced.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/499f80e3-860c-48ef-8286-be929a93e254','key_16048','','','5131','5131.2.2','sommelier',replace('sommelier\nwine manager\nwine waiter\nrestaurant sommelier','\n',char(10)),'Sommeliers stock, prepare, advise on and serve wine and other alcoholic beverages.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/49b21ca8-04be-4912-a2f7-0170550e18fa','key_16049','','','7323','7323.1','bindery operator',replace('bookbinder\nbinder\nbinder machine operator\nperfect binding operator\nbinding machine operator\nbinding machine tender\nbindery worker\nbindery technician\nperfect binder specialist\nbookbinding machine operator\nbinder technician\nbindery specialist\nperfect bindery specialist\nperfect binder technician\nbindery operator\nbinder machine tender\nbindery operative\npaper binder\nbindery machine operator\nperfect binder operator\nprinting stitcher operator\nperfect bindery technician\nbinding operator\nbindery machine specialist\nbinder operator\nperfect bindery operator\nbinder specialist\nbindery machine technician','\n',char(10)),'Bindery operators tend machines that bind printed or unprinted paper into volumes using staples, twine, glue or other binding technologies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/49c3db8c-c586-42d1-9726-f65d8303c0b1','key_16050','','','2643','2643.3','lexicographer',replace('bilingual dictionary editor\nlinguistic lexicographer\nlexicographer\nsenior lexicographer\nbilingual dictionaries lexicographer\nlexicologist\ndictionary editor\ncomputational lexicographer','\n',char(10)),'Lexicographers write and compile the content for dictionaries. They also determine which new words are common use and should be included in the glossary.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/49ca9fe4-4513-4657-b27f-eaad11e6158a','key_16051','','','1431','1431.2.4','lottery manager',replace('lottery shop general manager\nlottery shop senior administrator\nlottery manager\nlottery managing director\nlottery shop managing director\nlottery shop chief executive officer\nlottery chief executive officer\nsenior lottery administrator\nlottery general manager\ngeneral manager\nlottery shop manager','\n',char(10)),'Lottery managers organise and coordinate the activities of a lottery organisation. They oversee daily operations and facilitate communications between staff and customers. They review lottery procedures, arrange prices, train staff and strive to improve the profitability of their business. They take responsibility for all lottery activities and ensure that relevant lottery rules and regulations are followed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/49e28648-4fa8-4b36-ac4d-781c108547bf','key_16052','','','4323','4323.6','freight transport dispatcher',replace('dispatcher of freight\nfreight dispatching operator\nair freight dispatcher\ndispatcher\nfreight dispatcher\nfreight transport dispatcher\ndispatching officer\nfreight dispatching officer\nfreight transportation dispatcher\nfreight logistics dispatcher','\n',char(10)),'Freight transport dispatchers receive and transmit reliable messages, track vehicles and equipment, and record other important information. They oversee the planning operations of dispatching by coordinating different modes of transport. Freight transport dispatchers structure routes or services and determine the appropriate mode of transport. They are also responsible for equipment and vehicle maintenance and workers dispatching. The freight transport dispatchers provide the legal and contractual documentation for the transporting parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/49e4e358-8ba3-4a05-8ef9-abb73156f5f7','key_16053','','','2269','2269.1','chiropractor',replace('Licensed chiropractor\netiopath\nmusculoskeletal therapist\nneuromusculoskeletal therapist\nchiropractor','\n',char(10)),'Chiropractors are responsible for the diagnosis, treatment and prevention of disorders related to the neuromusculoskeletal system and the effects of these disorders on general health. They are independent primary health care professionals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/49f1e9aa-189b-4417-afaa-373cc4ffeea1','key_16054','','','8181','8181.4','drawing kiln operator',replace('drawing machine worker\ndrawing machine operative\ndrawing kiln operator\ndrawing kiln worker\nglass production operative\nglassmaker\nflat glass kiln operator\ndrawing kiln operative\nglass kiln operator\nglass operative','\n',char(10)),'Drawing kiln operators are responsible for continuous sheet flat glass production by manipulating the drawing kiln that processes molten glass.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4a1125ee-5728-4b1f-b655-7b8eb2e27660','key_16055','','','5246','5246.2','quick service restaurant team leader',replace('fast food team leader\nfast service team leader\nfast food shift team leader\nfast service shift team leader\nquick service restaurant team leader','\n',char(10)),'Quick service restaurant team leaders manage operations in a quick service restaurant.','','Includes <a resource="http://data.europa.eu/esco/occupation/4a1125ee-5728-4b1f-b655-7b8eb2e27660" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/4a1125ee-5728-4b1f-b655-7b8eb2e27660"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">quick service shift team leaders</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4a13bafa-a2ac-4864-a278-176ea5969280','key_16056','','','2433','2433.6.3','technical sales representative in hardware, plumbing and heating equipment',replace('assistant technical sales representative in hardware, plumbing and heating  equipment\ntechnical saleswoman in hardware, plumbing and heating equipment\ntechnical salesperson in hardware, plumbing and heating equipment\ntechnical sales representative in heating equipment\ntechnical salesman in hardware, plumbing and heating equipment\ntrainee technical sales engineer in hardware, plumbing and heating  equipment\nsenior technical sales representative in hardware, plumbing and heating  equipment\ngraduate technical sales representative in hardware, plumbing and heating  equipment\ntechnical sales representative in hardware\nhardware sales representative\nsenior technical sales engineer in hardware, plumbing and heating  equipment\ntechnical sales representative in hardware, plumbing and heating equipment\nhardware, plumbing and heating sales representative\ntrainee technical sales representative in hardware, plumbing and heating  equipment\nheating product sales representative\ngraduate technical sales engineer in hardware, plumbing and heating  equipment\nassistant technical sales engineer in hardware, plumbing and heating  equipment\ntechnical sales representative in plumbing','\n',char(10)),'Technical sales representatives in hardware, plumbing and heating equipment act for a business to sell its merchandise while providing technical insight for customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4a1d28b0-eca5-4c0a-a49d-ce974e7d04ce','key_16057','','','8122','8122.11','surface grinding machine operator',replace('back grinding machine operator\nsurface grinding machine operator\nfilling and grinding machine operator','\n',char(10)),'Surface grinding machine operators set up and tend surface grinding machines designed to apply abrasive processes in order to remove small amounts of excess material and smoothen metal workpieces by an abrasive grinding wheel, or wash grinder, rotating on a horizontal or vertical axis.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4a215d87-8363-4db2-a661-3c0d816b2a70','key_16058','','','8111','8111.3','surface miner',replace('mine operative\nsurface miner\nsurface mining operative\nquarry stationary plant operative\ncollier','\n',char(10)),'Surface miners perform a wide range of ancillary surface mining operations, often involving a high level of spatial awareness, such as pumping, dust suppression and the transport of materials including sand, stone and clay to the point of production.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4a246568-007e-46a8-a151-b98082ed6b50','key_16059','','','8156','8156.2.6','sole and heel operator',replace('sole stitcher\nsole blacker\nshoe cementer\nsole and heel operator\nheel nailing machine operator\nheel nailing machine tender\nsole cementer','\n',char(10)),'Sole and heel operators attach soles or heels to the footwear by stitching, cementing or nailing. They may work with several machines, for example for slipping the lasts, or for roughing, dusting or attaching heels. They also operate various machines both for stitched or cemented constructions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4a29eab1-5f02-4723-a863-e2d5a0614dfa','key_16060','','','1342','1342.2','healthcare institution manager',replace('healthcare general manager\ngeneral manager healthcare\nhealthcare operations manager\nhospital operations supervisor\nhealth service manager\nhealthcare operations director\ngeneral manager\nhospital operations manager\nhealthcare director\nhealthcare institution manager','\n',char(10)),'Healthcare institution managers supervise the operations of healthcare institutions, such as hospitals, rehabilitation facilities, home care services and elderly care institutions to ensure the organisation meets requirements, the patients and residents are cared for, the organisation is maintained and the necessary equipment present. They also supervise the staff and ensure record maintenance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4a30b421-2fda-42d6-b7e1-1c31753f061d','key_16061','','','8142','8142.8','insulating tube winder',replace('hand winder, insulating tubes\nwinding technician\ninsulating tube winder\nwinder, insulating tubes\ntube winder\ntube winding machine operative\nconvolute tube winder\ntube winding machine tender\ntube winding machine setter\ntube winding machine operator','\n',char(10)),'Insulating tube winders set up and operate a machine to wind up insulating tubes and cut them to specified dimensions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4a375b68-88a0-4e5f-99ce-9b01341dfb81','key_16062','','','2149','2149.11','materials engineer',replace('construction materials engineering adviser\nmaterials engineering expert\nconstruction materials engineering specialist\nadvanced materials engineer\nbuilding materials engineering specialist\nmaterials engineering consultant\nconstruction materials engineering consultant\nconstruction materials engineering expert\nconstruction materials engineer\nmaterials engineering adviser\nbuilding materials engineering expert\nmaterial engineer\nbuilding materials engineering adviser\nbuilding materials engineer\nmaterials engineering specialist\nceramic engineer\nmaterials engineer\nbuilding materials engineering consultant','\n',char(10)),'Materials engineers research and design new or improved materials for a diverse number of applications. They analyse the composition of materials, conduct experiments, and develop new materials for industry-specific use that can range from rubber, to textiles, glass, metals, and chemicals. They advise companies in damage assessments, quality assurance of materials, and recycling of materials.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4a39fddb-17e7-4431-be03-613edab8d1c8','key_16063','','','3352','3352.1','tax compliance officer',replace('enforcement agent\nrevenue compliance officer\ntax payment enforcement officer\ntax enforcement officer\ntax compliance officer\ntaxpayer compliance officer\ncouncil tax officer\ntax compliance agent\ntax officer','\n',char(10)),'Tax compliance officers collect fees, debt, and taxes on behalf of government institutions in cities, municipalities and other jurisdictions. They perform administrative duties and communicate with other officials and institutions to ensure operations are correct and compliant with policies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4a3f40a8-0587-494c-b8d3-7098b8c5992f','key_16064','','','0210','0210.1','army corporal',replace('special forces corporal\nregimental corporal\ncorporal\nlance corporal\nbatallion corporal\nmaster corporal\narmy corporal\nroyal marines corporal\narmed forces corporal','\n',char(10)),'Army corporals supervise sections of soldiers and perform instruction duties. They also command equipment such as heavy machinery and weaponry.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4a5b3686-eea4-4ba0-b446-33f7b6a95aed','key_16065','','','2131','2131.8.1','anatomical pathology technician',replace('assistant pathology technician\nanatomical pathology technician\ntechnician in anatomical pathology\npathology technician\npathology laboratory services technician\nassistant in anatomical pathology\nanatomical pathology technologist\nmortuary technician\nanatomical pathology technical assistant\npathology laboratory technician','\n',char(10)),'Anatomical pathology technicians assist specialised doctors in pathology in conducting post-mortem examinations, keeping records of the samples, specimens, organs and the respective findings and taking care of their appropriate disposal under supervision, following the orders of the doctor of medicine.','',replace('Excludes people performing patients treatment. \nExcludes people performing assistance in medical treatment.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4a5ceaae-0f05-4091-bdbf-59f250ce6374','key_16066','','','8142','8142.10','plastic heat treatment equipment operator',replace('heat treat operator\nprocess engineer, metallurgical\nheat treating operator\nmaterials engineer\nplastic heat treatment equipment operator\nheat treat technician\nheat treating equipment setter\nheat treatment equipment engineer\nmetallurgical process engineer\nheat treating equipment operator','\n',char(10)),'Plastic heat treatment equipment operators manipulate plastic products using machinery such as furnaces or flame-hardening machines in order to temper, anneal or heat-treat. They set up the machinery and read the production instructions to determine the furnace temperature. Plastic heat treatment equipment operators remove products from machines, let them cool down, examine and test products to see if they conform to specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4ac3c3a0-eb56-491f-8d21-1e5539a05b54','key_16067','','','5164','5164.2.1.2','horse trainer',replace('dressage instructor\nequine trainer\nhorse riding trainer\nhorse riding coach\nracehorse trainer\nriding school trainer\nequestrian centre instructor\nhorse riding instructor\nriding instructor\nhorse trainer\nriding school instructor\nequestrian centre trainer\njockey trainer','\n',char(10)),'Horse trainers train animals and/or riders for general and specific purposes, including assistance, security, leisure, competition, transportation, obedience and routine handling, entertainment and education, in accordance with national legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4ad4024e-d1d3-4dea-b6d1-2c7948111dce','key_16068','','','7231','7231.10','vehicle technician',replace('auto mechanic\nPCV technician\nautomotive technician\ncar mechanic\nbus technician\nheavy vehicle technician\nvehicle technician\ncoach technician\nmotorcycle mechanic\nautomotive maintenance technician\nmotor vehicle mechanic\nroad vehicle mechanic\nsenior bus technician\nmoped mechanic\nbus and coach technician\nLGV technician\nmoped technician\ntruck mechanic\nmotorbike mechanic\nautomotive mechanic\nmoped repairer\nmotorbike technician\nvehicle mechanic\nlight vehicle technician\nvan mechanic\nbus mechanic\nmotorbike repairer','\n',char(10)),replace('Vehicle technicians inspect, test and maintain vehicles, motorcycles, engine tune-ups and tyre replacements. They repair engine failures, lube replacements. Vehicle technicians replace vehicle components and evaluate warranty options. \n \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4ae3ad16-2d33-406c-8497-c66f7d61b914','key_16069','','','5223','5223.7.30','second-hand goods specialised seller',replace('army surplus goods salesperson\nsecond hand bookshop salesperson\nvintage clothes salesperson\nsecond-hand goods specialized seller\nthrift shop specialised seller\narmy surplus goods specialised seller\nsecond-hand goods salesperson\ncharity shop specialised seller\nthrift shop salesperson\nvintage clothes specialised seller\ncharity shop salesperson\nsecond hand goods specialised seller','\n',char(10)),'Second-hand goods specialised sellers sell second-hand goods such as books, clothes, appliances etc. in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4ae470fc-bcf8-4704-abd5-e086ebb1552c','key_16070','','','2431','2431.12','membership manager',replace('membership sales manager\nmembership director\nmembership manager\nmember records manager\nsubscription manager\nmembership supervisor\nmembership account manager\nmembership management officer\nmembership coordinator\nregional membership manager\nclub manager\nmembership and subscription manager','\n',char(10)),'Membership managers oversee and coordinate the membership plan, support existing members and engage with possible new members. They analyse the market trend reports and develop marketing plans accordingly. Membership managers monitor and ensure the efficiency of processes, systems and strategies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4b05bef2-ded4-4b09-ab94-f4c6a555a775','key_16071','','','2635','2635.3','social worker',replace('community counsellor\nsocial  services worker\ncommunity rehabilitation worker\ncultural mediator\nsocial outreach worker\ncommunity service worker\ncommunity liaison worker\nsocial and community services worker\nsocial services worker\nwelfare worker\ncommunity centre worker\nassistant, social services\ncommunity worker\ncommunity and social services worker\nchild and youth worker\nsocial worker\ncommunity services officer\nsocial services assistant\ncommunity development worker','\n',char(10)),'Social workers are practice-based professionals who promote social change and development, social cohesion, and the empowerment and liberation of people. They interact with individuals, families, groups, organisations and communities in order to provide various forms of therapy and counselling, group work, and community work. Social workers guide people to use services to claim benefits, access community resources, find jobs and training, obtain legal advice or deal with other local authority departments.','','Includes people performing social policy formulation, analysis, advocacy and political interventions activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4b1b1c9c-579c-45c8-8a34-db39f544c031','key_16072','','','5223','5223.7.20','furniture specialised seller',replace('home furnishing salesperson\nhome furnishings salesperson\nhome furnishing specialised seller\nlightning equipment and accessories salesperson\nfurniture and lighting sales person\nfurniture salesperson\nfurniture and lighting specialised seller\nfurniture specialized seller\nlightning equipment and accessories specialised seller\nhome furnishings specialised seller\nhome articles salesperson\nhome articles specialised seller','\n',char(10)),'Furniture specialised sellers sell furniture and other household articles in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4b214899-ed00-4156-b269-a23df0fb1093','key_16073','','','1349','1349.13','environmental protection manager',replace('environmental impact assessment manager\nenvironmental manager\nenvironmental field office manager\nenvironmental safety specialist\nenvironmental protection manager\nenvironmental protection specialist','\n',char(10)),'Environmental protection managers provide advice on the development of environmental policies to governmental and official institutions. They analyse possible threats for the well being of the people and the environment in a region and manage campaigns aimed at tackling problems such as waste collection, landfills, and preservation of green areas.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4b3201cb-ffc5-4f61-9fc3-5c0f0038fa5f','key_16074','','','2511','2511.8','ICT business analysis manager',replace('IT business analysis director\nIT business analysis manager\nIT commercial analysis manager\nlead IT business analyst\nICT business analysis managers\nICT commercial analysis manager\nICT business analysis manager','\n',char(10)),'ICT business analysis managers identify areas where information system changes are needed to support business plans and monitor the impact in terms of change management. They contribute to the general ICT functional requirements of the business organisation. ICT business analysis managers analyse business needs and translate them into ICT solutions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4b536ea7-ebac-4f75-8356-94eb639f1ecc','key_16075','','','3122','3122.4.15','print studio supervisor',replace('print studio team leader\nprint senior specialist\nprint senior technician\nprint production supervisor\nsuperintendent in the printing industry\nprint studio supervisor\nprint senior operator\nmaster bookbinder\nprint studio teamleader\nproduction supervisor in the printing industry\nprint and production supervisor\nprint shop supervisor\nmaster printer','\n',char(10)),'Print studio supervisors organise the activity of one or more teams of machine operators in printing, binding of books and finishing of printed materials. They aim to optimise production processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4b6a55a8-8ec9-4ae2-9f62-ebcea1fa93d0','key_16076','','','8171','8171.5','paper machine operator',replace('paper machine specialist\npaper making machine specialist\npaper making machine technician\npaper machine hand\npaper making machine operator\npaper machine technician\npaper maker\npaper-making machine specialist\npaper machine operator\npaper-making machine operator\npaper-making machine technician','\n',char(10)),'Paper machine operators tend a machine that takes in pulp slurry, spread it out over a screen, and drains out the water. The drained slurry is then pressed and dried to produce paper.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4b6b09e6-e1f6-46d8-b16a-d3522c367983','key_16077','','','8122','8122.4','cylindrical grinder operator',replace('disc grinder\nbillet grinder\nedge grinder\nautomatic grinder operative\nblade grinder\nbench grinder\nroll grinder\nrolling grinder\ndrill grinder\nautomatic grinder operator\nbroach grinder\nair grinder\nbit grinder\ncylindrical grinder operator\nplate grinder\nbearing grinder\nrail grinder\nemery grinder\ncrankshaft grinder\nrazor grinder\nmetal grinder','\n',char(10)),'Cylindrical grinding operators set up and tend cylindrical grinding machines designed to apply abrasive processes in order to remove small amounts of excess material and smoothen metal workpieces by multiple abrasive grinding wheels with diamond teeth as a cutting device for very precise and light cuts, as the workpiece is fed past it and formed into a cylinder.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4b709f9e-6112-4d58-81b0-2d6e6a69d61b','key_16078','','','2145','2145.1.4','food technologist',replace('food technology specialist\nfood technologist\nfood technology expert\nfood tech expert\nfood tech specialist','\n',char(10)),'Food technologists develop processes for manufacturing foodstuffs and related products based on chemical, physical, and biological principles and technology. They design and plan layouts or equipment, oversee staff, engage in controlling, and improve food technologies in food production processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4b72d306-9781-434d-8522-1fcaf892cdce','key_16079','','','3324','3324.3','non-vessel operating common carrier',replace('shipment consolidator\nfreight forwarder\nnon-vessel operating common carrier\nNVOCC','\n',char(10)),'Non-vessel operating common carriers (NVOCC) are consolidators in ocean trades who will buy space from a carrier and sub-sell it to smaller ship­pers. They issue bills of lading, publish tariffs and otherwise conducts themselves as ocean common carriers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4b759f0e-8fec-4cf5-80d4-d112a9fd4e51','key_16080','','','2114','2114.1.8','oceanographer',replace('oceanography scientist\nmarine physicist\noceanography researcher\noceanography science researcher\noceanography analyst\noceanography research scientist\nmarine geologist\nmarine chemist\nmarine research scientist\noceanographers\noceanography research analyst\noceanographer','\n',char(10)),'Oceanographers study and perform research on matters related to the sea and oceans. Oceanographers divide their expertise in different branches of research which are physical oceanographers whose research focus on waves and tides, chemical oceanographers whose study address the chemical constitution of sea waters, and geological oceanographer whose research refers to the bottom of seas and their plaques.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4b8dced3-ac9e-445d-8787-e07daf52e295','key_16081','','','3111','3111.6','leather laboratory technician',replace('leather chemical laboratory technician\nleather chemical technician\nleather tester\nleather chemist\nleather chemical tester\nleather dye and chemical coordinator\nleather testers\nleather laboratory technician\nleather laboratory technicians','\n',char(10)),'Leather laboratory technicians perform and report chemical analyses and physical tests of leather. They also perform and report chemical analyses of auxiliaries, environmental emissions and discharges. They ensure that the tests are carried out according to appropriate national, international or customer standards.','',replace('Includes people working in a laboratory environment. \nIncludes people performing subcontracting specialised testing to the third-party testing facilities.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4bc4170d-f4df-44e4-a3be-394242ae3017','key_16082','','','3311','3311.3.4','securities trader',replace('securities tellers\nsecurities sales agent\nsecurities sales directors\nsecurities teller\nsecurities sales director\nsecurities clerks\nsecurities clerk\nsecurities trader\nsecurities sales agents','\n',char(10)),'Securities traders purchase and sell securities such as stocks, bonds and shares on their own account or on their employers account based on their expertise in the financial markets. They monitor the performance of the securities traded, assessing their stability or speculative tendencies. They record and file all securities transactions and take care of their financial documents.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4bdc4e52-1c9c-4d80-898a-18522d905fee','key_16083','','','2635','2635.3.26','youth offending team worker',replace('youth resettlement officer\njuvenile probation social worker\nyouth resettlement worker\nyouth offending team leader\nyouth offending team officer\nyouth offending team worker\nyouth offender social worker\nyouth justice worker\njuvenile probation officer\nyouth offender team worker','\n',char(10)),'Youth offending team workers support young offenders by preventing them from reoffending, counselling them for behavioural changes, referring them to agencies that provide housing, helping them back into education, involving them in constructive activities, visiting them when located in secure institutions and assessing future risks.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4be03a63-0423-4e50-acbf-aec405bb0741','key_16084','','','3154','3154.1.1','aeronautical information service officer',replace('aeronautical information officer\nofficer in aeronautical information\naeronautical information service officer\naeronautical data service officer','\n',char(10)),'Aeronautical information service officers maintain the operational timing from sunrise to sunset in order to ensure that the information passed by agencies is authentic. They strive to ensure safety, regularity and efficiency.','','Excludes  <a resource="http://data.europa.eu/esco/occupation/f470b785-643c-46f9-8b31-6085427ab7b8" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/f470b785-643c-46f9-8b31-6085427ab7b8"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">aeronautical information specialist</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4be368e3-badb-4b9f-9d0a-4d0f7565540a','key_16085','','','2149','2149.10.1','fire prevention and protection engineer',replace('fire protection engineer\nfire prevention and protection engineering adviser\nfire protection and prevention engineer\nfire prevention engineer\nfire prevention and protection technology engineering adviser\nfire prevention and protection technology engineering specialist\nfire prevention and protection engineering specialist\nfire prevention and protection engineering expert\nfire prevention and protection technology engineering expert\nfire prevention and protection technology engineer\nfire prevention and protection engineer\nfire prevention and protection technology engineering consultant\nfire prevention and protection engineering consultant','\n',char(10)),replace('Fire prevention and protection engineers study, design, and developed innovative solutions aimed to the prevention of fire and the protection of people, natural sites, and urban areas. They propose suitable materials for construction, clothing, or other applications and they design detection systems aiming to prevent fire or the propagation of it. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4be4ea31-1211-4f0c-82bb-f6fe10791f4d','key_16086','','','1120','1120.3.1','airport chief executive',replace('airport general manager\nairport manager\nairport chief executive\nairport CEO\ngeneral director of airport\nairport chief operating  officer','\n',char(10)),'Airport chief executives lead a group of airport directors who are responsible for all areas of the airport. They envision and make decisions on the strategic direction of the airport based on the information provided by their team of managers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4bef1707-a251-427e-9d8b-96595ec38d45','key_16087','','','2641','2641.1','book editor',replace('manuscripts editorial director\nbook editorial director\nbook editor and publisher\nacquisitions editor\nsenior book editor\nbook editor and consultant\neditorial director of publications\nchildren''s book editor\nbook editor and project manager\ncommissioning editor\nbook editor','\n',char(10)),'Book editors find manuscripts that can be published. They review texts from writers to evaluate the commercial potential or they ask writers to take on projects that the publishing company wishes to publish. Book editors maintain good relationships with writers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4c313f2a-704e-4d3c-a7c2-98e1b93c9e94','key_16088','','','1324','1324.3.2.25','import export manager in office furniture',replace('graduate import export manager in office furniture\nsenior export manager in office furniture\ntrainee import export manager in office furniture\nsenior import manager in office furniture\ngraduate export manager in office furniture\nassistant import export manager in office furniture\ntrainee export manager in office furniture\ntrainee import manager in office furniture\nassistant export manager in office furniture\ninternational trade compliance manager in office furniture\ninternational trade manager in office furniture\ngraduate import manager in office furniture\nimport export manager in office furniture\nassistant import manager in office furniture\ncustoms compliance manager in office furniture\nsenior import export manager in office furniture','\n',char(10)),'Import export managers in office furniture install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4c40c1e8-0584-4a77-8ca3-ec82538f9488','key_16089','','','2635','2635.3.1','benefits advice worker',replace('advice worker, benefits\nwelfare benefits advice worker\nwelfare rights worker\nsocial benefits social worker\nbenefits advice worker\nwelfare rights adviser\nwelfare benefits adviser\nwelfare rights counsellor\nwelfare benefits social worker\nemployment and benefits advice worker\nbenefit advisor\nwelfare rights officer\nsocial security social worker','\n',char(10)),'Benefits advice workers guide individuals in the social work area to help them solve specific problems in their personal life by addressing personal and relationship issues, inner conflicts, depression and addictions. They attempt to empower individuals to achieve change and improve the quality of their life. They may also support and advise clients on demanding their social security benefits.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4c6b3657-316e-43b5-872a-ea763411190d','key_16090','','','3322','3322.1.1','electricity sales representative',replace('residential electricity sales representative\nelectric power sales consultant\nelectricity sales consultant\nelectricity seller\nelectricity sales representative\nelectricity saleswoman\nelectricity salesman\nelectricity sales promoter\nelectric power sales promoter\nelectricity salesperson\nelectric power sales negotiator\nelectric power seller\nelectricity sales negotiator\nelectricity sales rep','\n',char(10)),'Electricity sales representatives assess clients'' energy needs, and recommend the purchase of electricity supply from their corporation. They promote their corporation''s services, and negotiate terms of sale with clients.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4c715719-3c3f-4058-ba00-9b2ef34848e8','key_16091','','','2412','2412.6.4','investor relations manager',replace('investors relation manager\ninvestment strategy manager\ninvestor relations manager\ninvestor relations director\ninvestor relations supervisor','\n',char(10)),'Investor relations managers disseminate the investment strategy of the company and monitor the reactions of the investment community towards it. They use marketing, financial, communications, and security law expertise to ensure transparent communication to the larger community. They respond to inquiries from shareholders and investors in relation to the company''s financial stability, stocks, or corporate policies.','',replace('Includes people working in public relations departments. \nIncludes people working with larger companies'' relations with shareholders and investors through marketing and communication.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4c86c7c1-0839-456e-856f-65396d1f39d7','key_16092','','','8151','8151.4','winding machine operator',replace('core winder machine operator\nwinding operator\nwinder operator\nwinding machine operator\nmachine bobbin winder\nwinding machinist\nwinding machine tender\ncore winding operator\nwinder tender\nbobbin winder','\n',char(10)),'Winding machine operators tend machines that wrap strings, cords, yarns, ropes, threads onto reels, bobbins, or spools. They handle materials, prepare them for processing, and use winding machines for that purpose. They also perform routine maintenance of the machinery.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4c89c65c-4e35-44e3-8fa0-5d73e9e15325','key_16093','','','3412','3412.4.5','family support worker',replace('family service worker\nfamily support worker\nearly intervention worker (families)\nfamily advice worker\nearly help family worker\nfamily social care worker\nfamily worker (early intervention)\nintensive family support worker\nfamily engagement worker\nchildren and families support worker\nfamily intervention worker','\n',char(10)),'Family support workers provide actionable advice and emotional support to families that go through difficulties such as addictions, disabilities, sickness, imprisoned parents, marital and financial difficulties. They provide advice on the best solution for the children in relation to their stay with their families or not, based on the assessment of the family situation. Family support worker also provide information on the available services based on the family''s specific needs and the recommendations of the social worker. ','Excludes family social worker.','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4c9ca706-3973-4926-854d-44011662ca83','key_16094','','','3112','3112.1.7','fire protection technician',replace('fire suppression engineer\nfire risk assessor\nfire and security systems engineer\nfire protection service engineer\nfire alarm service technician\nsenior fire engineer\nfire protection technician\nfire and security maintenance engineer','\n',char(10)),'Fire protection technicians install and maintain fire protection equipment, such as fire extinguishers, fire alarms, fire detection systems, or sprinkler systems in facilities to ensure compliance to safety standards and protection from fire hazards. They inspect the equipment to ensure its functionality, and perform repairs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4ca8ba6c-dc55-40c0-ae6c-d55c94dd2e03','key_16095','','','2422','2422.8','intelligence officer',replace('counter-terrorism analyst\nintelligence research specialist\nresearch and intelligence officer\ncyber investigator\ncriminal intelligence analyst\nintelligence investigator\nintelligence research officer\nintelligence officer\nhousing intelligence officer\nresearch & intelligence officer\nmilitary intelligence officer\nintelligence analyst','\n',char(10)),'Intelligence officers develop and execute plans to gather information and intelligence. They investigate the lines of enquiry which would provide them with the necessary intelligence, and contact and interview people who may provide intelligence. They write reports on their results, and perform administrative duties to ensure records maintenance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4cbe5a17-056c-489a-9f10-f9d816905000','key_16096','','','5419','5419.8','street warden',replace('neighbourhood warden\nenvironmental warden\nstreet warden\nbylaw enforcement officer\ncommunity support officer\nspecial constable\ncommunity safety warden\ncountryside ranger\nwarden\nneighborhood warden','\n',char(10)),'Street wardens patrol designated areas to ensure a sense of security with the public, and provide general support. They monitor suspicious behaviour and cooperate with the police and other local institutions to maintain the community''s safety and well-being, and enforce law and issue penalties when necessary.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4cc98057-8ed5-4b45-855b-7a67bc2e5bae','key_16097','','','2642','2642.1.11','foreign correspondent',replace('on-the-scene reporter\nforeign correspondent\nforeign journalist\nforeign news correspondent\ninternational correspondent\nforeign affairs correspondent\nforeign reporter','\n',char(10)),'Foreign correspondents research and write news stories of international importance for newspapers, journals, magazines, radio, television and other media. They are stationed in a foreign country.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4cdfac8c-b000-4279-985e-31bc1fbe2447','key_16098','','','8111','8111.5','underground miner',replace('hewer\ncoal digger\nunderground miner\nunderground mine worker\nunderground mining operative\ncoal cutter\ncoal excavator\nunderground mineworker\nunderground mine operative\ncoal hewer\ncollier','\n',char(10)),'Underground miners perform a wide range of ancillary underground mining operations such as inspections, conveyor attendance and the transport of equipment and consumable materials from the surface to the point of extraction underground.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4ce42c3c-8a46-40b6-9274-1f5cee3c5cba','key_16099','','','2250','2250.7','official veterinarian',replace('official vet\npublic health veterinarian\nveterinary inspector\nstate vet\nofficial veterinarian\ngovernment vet\ngovernment veterinary officer','\n',char(10)),'Official veterinarians are professionals with a comprehensive scientific education. They have the authority to carry out, in an independent, ethical and personally responsible capacity all veterinary activities with particular focus on the health and welfare of animals and public health in accordance with national and international legislation. They are responsible for national animal disease eradication programmes, the protection of public health and food or animal inspection, animal movements, and import and export of live animals and their products.','','','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4cf7be91-fed9-47a7-9ca9-e74c7eb6becb','key_16100','','','3118','3118.1','3D printing technician',replace('3D printing operator\nprinting machine technician\n3D print service technician\nprinter repairman\n3D printing repairer\n3D printing technician\n3D printing repairman\nprint service technician\nprinting field service technician\n3D printer operator\n3D printer repairman\nprinter technician\n3D printing prototyping technician\nprinter repairer\nprototyping technician\n3D printer repairer\n3D printing field service technician','\n',char(10)),'3D printing technicians assist in the designing and programming of products, ranging from prosthetic products to 3D miniatures. They may also provide 3D printing maintenance, check 3D renders for customers and run 3D printing tests. 3D printing technicians can also repair, maintain and clean 3D printers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4d102082-8d43-4c59-81ab-08a7509f3c40','key_16101','','','3521','3521.1.1','boom operator',replace('shotgun operator\nboom holder\nfilm boom operator\nboom operator\nproduction sound mixer assistant\nsound equipment technician\nshotgun microphone operator\nTV boom operator','\n',char(10)),'Boom operators set up and operate the boom microphone, either by hand, on an arm or on a moving platform. They make sure that every microphone is correctly stationed on set and in the best position to capture the dialogues. Boom operators are also responsible for the microphones on the actors clothing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4d27152a-a8ee-4f5a-9f93-a2fb4fb2b2e3','key_16102','','','5312','5312.1','early years teaching assistant',replace('assistant in early years education\nnursery teaching aide\nkindergarten teaching assistant\npreschool teaching aide\nearly childhood teaching aide\nearly years TA\nassistant in early years teaching\nreception teaching assistant\nnursery teaching assistant\npreschool teaching assistant\nearly years education assistant\nearly childhood teaching assistant','\n',char(10)),'Early years teaching assistants support the early years teacher in an early years or nursery school. They assist in class instruction, in classroom supervision in absence of the head teacher, and in organising, developing and putting into practice of the daily schedule. Early years teaching assistants monitor and help students in group as well as individually, and tend to focus on the students in need of extra care and attention the early years teacher cannot provide.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4d34a9ee-2653-4da0-bef7-f59294577224','key_16103','','','2152','2152.1','electronics engineer',replace('electronic technology engineering consultant\nelectro engineer\nelectronics engineering expert\nelectronics engineering consultant\nelectronics engineering specialist\nelectronic technology engineer\nelectronic technology engineering expert\nelectronics engineering adviser\nelectronics engineer\nelectronic technology engineering specialist\nelectronic technology engineering adviser\nelectronic engineer','\n',char(10)),'Electronic engineers research, design, and develop electronic systems such as circuits, semiconductor devices, and equipment that use electricity as a source of power. They work with capacitators, transistors, diodes or resistors to create electronic circuits and applications of use in fields such as telecommunications, acoustics, instruments, and control.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4d4b243f-6c3b-48c3-a564-2e7f2d592a3c','key_16104','','','2654','2654.1.5','production designer',replace('TV production designer\nproduction design manager\ntelevision production designer\nfilm production designer\nproduction designer\nanimation production designer\nart department manager\nart department lead\nart department supervisor','\n',char(10)),'Production designers are responsible for the complete look (style, colouring and locations) of television programmes, series, motion pictures and commercials. They create the visual concept for the entire production such as set design, lighting, costumes and camera angles. Production designers work together with the director, designers and oversee the art department. They also create sketches, drawings, do colouring and location research and advise props and stage settings to the director.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4d5bd738-9202-4fd7-bd8c-7dbe914048de','key_16105','','','2144','2144.1.2','agricultural engineer',replace('agricultural consultant engineer\nagricultural engineering consultant\nagricultural engineering adviser\nagricultural technology engineering expert\nagricultural engineer\nagriculture engineer\nagricultural technology engineering adviser\nagricultural technology engineering consultant\nagricultural landscape engineer\nagricultural engineering expert\nagricultural technology engineering specialist\nagricultural technology engineer\nagricultural engineering specialist','\n',char(10)),'Agricultural engineers intervene in a variety of matters within the agriculture field in combination with engineering concepts. They design and develop machinery and equipment for an efficient and sustainable exploitation of the land. They advise on the use of resources in agricultural sites comprising the usage of water and soil, harvesting methods, and waste management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4d6d9057-5167-4158-8bc1-cc2f89d73fd7','key_16106','','','2320','2320.1.5','beauty vocational teacher',replace('cosmetology vocational teacher\nhair and beauty educator\nbeauty vocational teacher\neducator in beauty\nhair and beauty teacher\ncosmetology teacher\nvocational hair and beauty teacher\neducator in hair and beauty\nvocational cosmetology teacher\nvocational beauty teacher\nbeauty teacher\nbeauty educator\nbeauty instructor\nhair and beauty instructor','\n',char(10)),'Beauty vocational teachers instruct students in their specialised field of study, beauty, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for a cosmetology-related profession, such as manicurist and make-up and hair designer. Beauty vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of cosmetology through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4d750b7f-f40d-447c-ba90-f5fb0ee9ef27','key_16107','','','3353','3353.2','social security officer',replace('civil servant\nbenefits officer\nhealthcare insurance adviser\nemployment advisor\nsocial security claims officer\nsocial security officer\ngovernment administrative officer\ninvalidity adviser\nsocial security adviser','\n',char(10)),'Social security officers advise clients on social security benefits and ensure they claim the benefits they are eligible for, as well as providing advice on promotions and other available support services such as employment benefits. They aid clients in applications for benefits such as sickness, maternity, pensions, invalidity, unemployment and family benefits. They investigate the client''s right to benefits by reviewing their case and researching legislation and the claim, and suggest an appropriate course of action. Social security advisers also determine the aspects of a specific benefit.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4d7d2628-1405-4ff2-b5b5-bd0d57ed9bbd','key_16108','','','3412','3412.4.10','residential childcare worker',replace('special needs support worker\nchildcare worker\nchildren''s home worker\nresidential home children care worker\nresidential childcare worker\nresidential care worker\nchildren''s support worker','\n',char(10)),'Residential childcare workers counsel and support children who have physical or mental disabilities. They monitor their progress and provide them with care in a positive living environment. They liaise with the families in order to arrange their visits.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4daee8bd-8400-4da6-ba52-30d4c193d1a9','key_16109','','','2149','2149.4','aviation ground systems engineer',replace('airport ground systems engineer\naviation ground systems engineer\naviation ground systems technician\nengineer of aviation ground systems\nairport ground systems technician','\n',char(10)),'Aviation ground systems engineers are in charge of supervising the maintenance of the equipment of the airport, for example, the visual aids, airport electrical systems, luggage systems, security systems, pavements, drainage, maintenance of unpaved areas and equipment and vehicles.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4dc6e03f-bfb0-4078-a453-01bd1cb735aa','key_16110','','','5223','5223.7.9','computer and accessories specialised seller',replace('computer specialised seller\nIT salesperson\nIT specialised seller\ncomputer hardware salesperson\ncomputer and accessories specialized seller\nconsumer electronics specialised seller\ncomputer and accessories salesperson\nICT specialised seller\ncomputer hardware specialised seller\ncomputer and peripheral units salesperson\nconsumer electronics salesperson\ncomputer salesperson\nICT salesperson\ncomputer and peripheral units specialised seller','\n',char(10)),'Computer and accessories specialised sellers sell computers and other peripheral units in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4dceca8d-9faf-487b-b342-c9d7dc209533','key_16111','','','3423','3423.1.2.1','assistant outdoor animator',replace('outdoor activities facilitator\noutdoor adventures facilitator\nassistant open-air activities instructor\nassistant open-air activities teacher\njunior instructor\nassistant outdoor animator\noutdoor activities assistant instructor\nassistant outdoor activities teacher','\n',char(10)),'Assistant outdoor animators assist in planning outdoor activities, outdoor risk assessment and equipment monitoring. They manage the outdoor resources and groups. Assistant outdoor animators may help with office administration and maintenance hence they may work indoors.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4e0c14d6-b170-40f1-bcdc-703c0b92109b','key_16112','','','5141','5141.1.1','barber',replace('barber\nhaircutter','\n',char(10)),'Barbers cut, trim, taper and style men''s hair. They also remove facial hair by shaving the specific area. Barbers use tools such as scissors, clippers, razors and combs. They may offer additional services like shampooing, styling, coloring and performing scalp massages.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4e3fede9-5522-4624-91bc-b59c79fada02','key_16113','','','2144','2144.1.16','packing machinery engineer',replace('packing machinery engineering consultant\npacking machine engineer\nmachine packaging engineer\nmachine packaging supervisor\npackaging machine engineer\npacking machinery engineer\npacking machinery engineering specialist\nmechanical packaging engineer\npacking machinery engineering adviser\npackaging engineer\nmachine packaging inspector\nmachine-packing engineering inspector\npackaging machinery engineer\nmachine packing inspector\nmachine packaging technician\nmachine-packing engineer\npacking machinery engineering expert\npacking machinery inspector\nmachine packing engineer\nmechanical packing engineer','\n',char(10)),'Packing machinery engineers are in charge of and responsible for packaging machinery. They maintain and improve technical standards, analyse results, set up improvement plans, and are responsible for machinery maintenance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4e4a1aa1-ca53-406e-9ca6-889d4621a0ed','key_16114','','','3433','3433.2','big data archive librarian',replace('documentation archivist\ncomputer tape librarian\ndigital documentation archivist\narchive librarian\nbig data archive librarian\ndigital archivists','\n',char(10)),'Big data archive librarians classify, catalogue and maintain libraries of digital media. They also evaluate and comply with metadata standards for digital content and update obsolete data and legacy systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4e4a291d-1d38-4c5a-812a-5827d0691b11','key_16115','','','2411','2411.1.4','budget analyst',replace('budget and policy analyst\nbudget officer\nbudget analyst\nbudget accountant\ncost and planning analyst','\n',char(10)),'Budget analysts monitor the spending activities of public and private institutions and companies. They prepare budget reports, review the budget model used in the company and ensure compliance with the budgeting policies and other legal regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4e7a3641-7bd3-4290-9f0c-0e7ca776fb0e','key_16116','','','5249','5249.2.1.7','rental service representative in water transport equipment',replace('rental service worker in water transport equipment\nrental sales assistant in water transport equipment\nrental sales desk clerk in water transport equipment\nrental specialist in water transport equipment\nrental service desk supervisor in water transport equipment\nrental service manager in water transport equipment\nrental service desk agent in water transport equipment\nrental sales desk agent in water transport equipment\nrental service desk clerk in water transport equipment\nrental sales supervisor in water transport equipment\nrental advisor in water transport equipment\nrental sales desk supervisor in water transport equipment\nrental sales team leader in water transport equipment\nrental service supervisor in water transport equipment\nrental sales manager in water transport equipment\nrental service assistant in water transport equipment\nrental service representative in water transport equipment\nrental sales worker in water transport equipment\nrental service team leader in water transport equipment','\n',char(10)),'Rental service representatives in water transport equipment are in charge of renting out equipment and determining specific periods of usage. They document transactions, insurances and payments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4e82464b-e9d7-4d51-9116-294ab40c5169','key_16117','','','2359','2359.5','assistive technologist',replace('educational technology advisor\neducational technologist\nspecial needs technologist\nassistive technology technician\nassistive technology assessor\neducational assistive technologist\nassistive  technology facilitator\nIT disability support advisor','\n',char(10)),'Assistive technologists work to improve access to learning or/and improving independence and participation for individuals with disabilities. They do this through learner support and staff support with activities such as assessments, training and guidance. Assistive technologists have a good understanding of learners’ needs and a wide technology knowledge relevant to learning, living or work context. The role requires knowledge of assistive technology hardware and software such as text to speech, prediction, dictation, vision and physical access tools.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4e8a5d3e-66de-4661-9927-ef3123cc79e9','key_16118','','','2310','2310.1.3','architecture lecturer',replace('university lecturer in architectural studies\narchitecture professor\narchitecture docent\nprofessor of architectural studies\nlecturer of architecture\nuniversity architecture lecturer\nlecturer in architectural studies\nuniversity lecturer in architecture\nlecturer in architecture\ninstructor in architecture\narchitecture instructor\narchitecture lecturer\narchitecture lector\nprofessor of architecture\nuniversity architectural studies lecturer\narchitecture teacher','\n',char(10)),'Architecture lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, architecture, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of architecture, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4ea620f2-5c75-49ee-85a0-b2fb6bbf3624','key_16119','','','1324','1324.3.1.4','road operations manager',replace('road operations supervisor\nroad transport manager\npassenger transport operations manager\nroad operations coordinator\nroad freight operations coordinator\nroad freight operations supervisor\nroad operations manager\nroad freight operations manager\nroad operations superintendent','\n',char(10)),'Road operations managers oversee day-to-day road transportation processes, managing processes, and strive to meet customer expectations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4eada7b3-5026-4030-936b-4a80aed37252','key_16120','','','2320','2320.1.24','travel and tourism vocational teacher',replace('travel and tourism trainer\ntrainer in travel and tourism\ntravel and tourism vocational instructor\ntravel and tourism vocational teacher\nvocational teacher in travel and tourism\nvocational travel and tourism teacher\nvocational instructor in travel and tourism\ninstructor in travel and tourism\ntravel and tourism instructor\ntravel and tourism teacher','\n',char(10)),'Travel and tourism vocational teachers instruct students in their specialised field of study, travel and tourism, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for a travel and tourism-related profession. Travel and tourism vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of travel and tourism through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4ebf2dae-c326-4627-befe-fbc91410f432','key_16121','','','1222','1222.1.1','advertising manager',replace('advertising account planner\npromotions manager\nadvertising manager\nadvertising coordinator\nadvertising executive\nadvertising and promotions manager\ncampaigns coordinator\npromotions coordinator\nadvertising campaign manager\nmarketing communication manager\nadvertising director\nadvertising agency manager','\n',char(10)),'Advertising managers perform the implementation of the advertising initiatives planned in the strategic marketing plan. They organise and prepare the resources needed to launch advertising campaigns and operations in advertising agencies. They prepare and align the communication channels, negotiate contracts, and ensure that operations adhere to budgets.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4ed22a87-8478-4731-be11-5942eacb2e16','key_16122','','','2412','2412.10','public funding advisor',replace('public funding co-ordinator advisor\nbusiness mentor\nbusiness finance advisor\nbusiness advisor\nenterprise mentor\npublic funding advisor\ngrants advisor\nenterprise advisor\npublic funding specialist\nenterprise coach\nbusiness coach','\n',char(10)),'Public funding advisors advise individuals and businesses about funding opportunities given by the government. They analyse the needs of clients, consult them on funds, grants and subsidies that apply to them and help with the application process. Public funding advisors also set up public grant administration in organisations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4ed36f4c-9d5b-4a01-bf59-7f3c7844ce95','key_16123','','','3432','3432.4.1','scenic painter',replace('scenery painter\nscene painter\nscenic painter and artist\nscenic painter & artist\ntheatre scene painter\nscenery artist\nscenic artist\ntheater scene painter\nscenic painter','\n',char(10)),'Scenic painters decorate sets for live performances. They employ a broad variety of crafting and painting techniques such as figurative painting, landscape painting and Trompe-l''œil to create convincing scenes. Their work is based on artistic vision, sketches and pictures. They work in close cooperation with the designers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4ed9ce62-75d4-4b05-80d4-e86528fecdcd','key_16124','','','9612','9612.1','recycling worker',replace('household waste reclamation worker\nvehicle breaker\nwaste recycling site operative\nrecycling site worker\nhousehold waste recycling site worker\nrecycling operative\nrecycling site operative\nreclamation worker\nrecycling worker\nwaste reclamation worker\ncar breaker\nvehicle dismantler\ncar dissasembler\nwaste recycling site worker\ncommercial waste reclamation worker\nhousehold waste recycling site operative','\n',char(10)),'Recycling workers clean materials and remove waste, and ensure the waste and collected materials are sorted in the appropriate recycling containers. They also dismantle vehicles and sort the parts collected, and deposit recyclable materials onto conveyor belts where they can be further sorted.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4edbde9f-218e-49c7-812f-c45262fb4128','key_16125','','','3412','3412.4.7','housing support worker',replace('housing officer\nassistant housing support worker\nhousing outreach worker\nsupport worker (housing)\nsenior housing support worker\nhousing advisor\nhousing advice worker\nhousing support officer\nhousing support worker','\n',char(10)),'Housing support workers provide support and assistance to elderly, people with a physical impairment or learning disability, homeless people, ex-drug addicts, ex-alcohol addicts or ex-offenders.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4efdf62f-a1d9-450e-aebc-2d6a6110f05d','key_16126','','','2659','2659.1','community artist',replace('community arts practitioner\ncommunity drama artist\ncommunity dance leader\ncommunity artist\ndance leader\ncommunity dance artist\ncommunity & participatory artist\nartist & community facilitator\nhospital clown\nartist in mediation\ncommunity arts project leader\ncommunity arts worker\ncommunity dance practitioner\ncommunity arts facilitator','\n',char(10)),'Community artists research, plan, organise and lead artistic activities for people brought together by a shared interest, capacity, environment or condition. They manage and coordinate creative projects with local groups and individuals to foster their artistic creativity and improve their quality of life. Community artists make the arts accessible to the community they work for, and provide opportunities for participants to shape their artistic programme.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4efee3ed-de09-44c6-bd4a-d9eaafd04683','key_16127','','','2146','2146.5.2','process metallurgist',replace('process metallurgy research scientist\nprocess metallurgy research analyst\nprocess metallurgy scholar\nprocess metallurgy specialist\nprocess metallurgy researcher\nprocessing metallurgist\nprocess metallurgy expert\nsteel industry experts\nprocess metallurgy scientist\nprocess metallurgy adviser\nprocess metallurgist\nprocess metallurgy analyst\nprocess metallurgy consultant\nprocess metallurgy science researcher','\n',char(10)),'Process metallurgists study the characteristics of ores including copper, nickel and iron ores and the performance of various metals and alloys. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4f08cd74-71c6-43aa-88d4-841e5bb677f7','key_16128','','','2269','2269.7','podiatry assistant',replace('assistant in chiropody\nfoot care assistant\nassistant practitioner in podiatry\nassistant in podiatry\nchiropody assistant\npodology assistant\nassistant in podiatric foot care\npodiatry foot care assistant\nassistant in podology\npodiatry assistant','\n',char(10)),'Podiatry assistants help the podiatrist by carrying out supportive tasks such as assisting in diagnosing and treating the disorders, diseases and deformities of the feet, advising people on foot care techniques and the importance of foot care for promoting better mobility and general well-being. They also perform small tasks such as cutting toe nails, applying plasters and general clerical duties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4f1bb8b4-3fff-4e68-b427-8c892534a181','key_16129','','','7122','7122.2','hardwood floor layer',replace('installer of harwood floors\ntimber floor layer\nparquet floor installer\nhardwood floor installer\nhardwood floor layer\nparquet floor layer\nwood floor layer','\n',char(10)),'Hardwood floor layers install floors made of solid wood. They prepare the surface, cut parquet or board elements to size, and lay them in a predetermined pattern, straight and flush.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4f2f2539-3e38-4393-9dac-a9a48af7cd32','key_16130','','','2654','2654.1.2','casting director',replace('executive of casting\ntalent booker\nmovie casting director\ntalent director\ncasting recruiter\nmotion picture casting director\nTV casting director\ncasting coordinator\nextras casting director\nfilm casting director\ncasting director','\n',char(10)),'Casting directors select actors for all roles in a motion picture or television series. They co-operate with the producer and director to determine what they are looking for. Casting directors contact talent agents and organise interviews and auditions for the parts. They  determine the fees and contracts for the actors and extras.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4f49df98-98b8-4a9e-98ad-9c5557817eba','key_16131','','','1213','1213.2.2','strategic planning manager',replace('strategic planning manager\nstrategic manager\nbusiness continuity strategy director\ncorporate strategist\nbusiness continuity planning director\nstrategy manager\nstrategic development manager\ninnovation and strategy specialist\nplanning manager\nstrategy adviser\nstrategic business planning manager','\n',char(10)),'Strategic planning managers create, together with a team of managers, the strategic plans of the company as a whole, and provide coordination in the implementation per department. They help to interpret the overall plan and create a detailed plan for each one of the departments and branches. They ensure consistency in the implementation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4f4d33a1-0b41-4dd9-8a42-aeba5a3974ab','key_16132','','','2152','2152.1.15','sensor engineer',replace('engineer specialised in sensor technology\nengineer in sensor technology\nsensor technology engineering consultant\nsensor technology engineer\nsensor engineering consultant\nsensor development engineer\nengineer of sensors\nsensor technology engineering adviser\nsensors engineer\nactuator and sensor engineer\nsensor engineering expert\nelectronic sensor engineer\nengineer of sensors and actuators\nsensor design engineer\nsensor engineers\nsensor engineer\nsensor engineering adviser\nsensor systems engineer\nelectronics sensor engineer\nspecialist sensor engineer\nsensor technology engineering specialist\nsensor developer\nsensor designer\nsensor technology engineering expert\nsensor engineering specialist\nsensor and actuator engineer','\n',char(10)),'Sensor engineers design and develop sensors, sensor systems and products that are equipped with sensors. They plan and monitor the manufacture of these products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4f5bc9a5-e989-409b-bfb5-973b472163cd','key_16133','','','2634','2634.2.2','educational psychologist',replace('educational psychology specialist\npractitioner in school psychology\nschool psychologist\npsychologist in education institution\neducational psychologist\nstudents'' psychology assistant\nspecialist in educational psychology\neducational psychology practitioner\npractitioner of educational psychology\nstudents psychologist\nstudents psycology specialist\npractitioner in educational psychology\nschool psychology practitioner\npsychologist, educational','\n',char(10)),'Educational psychologists are psychologists employed by educational institutions to provide psychological and emotional support to students in need. They are specialised in the provision of direct support and interventions to students, conducting psychological testing and assessment, and consulting with families, teachers and other school-based student support professionals, such as school social workers and educational counsellors, about the students. They may also work with the school administration to improve practical support strategies in order to improve the students'' well-being.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4f5e4fb1-58bd-4abd-bb06-4af9203a1518','key_16134','','','8122','8122.9','filing machine operator',replace('stapling machine operator\nmetal finishing machine tender\nfiling and stapling machine tender\nfiling plant operator\nband filing machine operative\nwood filer\nfiling machine operator\nwood filing machine operator\nmetal finishing machine operative\nfiling and stapling machine technician\nbench filing machine operator\nreciprocating filing machine operative\nmetal finishing machine setter\nband filing machine tender\nbench filing machine operative\nfiling and stapling machine operator\nmetal finishing machine operator\nband filing machine operator\nreciprocating filing machine setter\nmetal filer\nplastic filing machine operator\nmetal filing machine operative\nfiling and stapling machine operative\nreciprocating filing machine operator\nreciprocating filing machine tender\nstapling machine operative\nfiler\nbench filing machine tender\nfiling plant operative\nfiling room operative\nmetal filing machine operator\nmetal filing machine tender\nband filing machine setter\nfiling machine operative\nfiling machinist\nfiling machine worker\nmetal filing machine setter\nfiling and stapling machine apprentice\nstapling machine tender\nfiling machine technician\nreciprocating file operator\nfiling machine tender\nbench filing machine setter\nfiling machine specialist\nplastic filer\nfiling room operator','\n',char(10)),'Filing machine operators set up and tend filing machines such as band files, reciprocating files and bench filing machines in order to smoothen metal, wood or plastic surfaces by precisely cutting and removing small amounts of excess material.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4f7e7482-39e7-4f3a-bf11-763620f4b8e0','key_16135','','','7322','7322.6','paper embossing press operator',replace('paper embossing press tender\npaper embossing machine tender\npaper embossing/debossing machine operator\npaper embossing press worker\npaper embossing press technician\npaper embosser\npaper embosser operator\npaper embossing machine operator\npaper embosser specialist\npaper embossing press specialist\npaper embosser worker\npaper embossing and debossing machine operator\npaper embossing and debossing press operator\npaper embossing press operator\npaper embosser technician\npaper embossing & debossing press operator\npaper paper embossing press operator\npaper embossing & debossing machine operator\npaper embossing/debossing press operator','\n',char(10)),'Paper embossing press operators use a press to raise or recess certain areas of the medium, so as to create relief on the print. Two matching engraved dies are placed around the paper and pressure is applied to change the surface of the material.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4f89b0d2-b666-4890-af01-25d1d60da1f3','key_16136','','','2633','2633.3','political scientist',replace('peace researcher\npolitical research scientist\npolitical analyst\npublic administration researcher\npolitical scientist\npolitical science researcher\npolitical studies researcher\npundit\nwar researcher\npolitical researcher\npolitical scholar\npeace and conflict researcher\npolitical studies research scientist\npolitics scienctist\npolitical studies scholar\npolitical studies scientist','\n',char(10)),'Political scientists study political behaviour, activity and systems, including the elements that fall therein. Their study of the field ranges from the origins and evolution of various political systems to topical matters such as decision-making processes, political behaviour, political trends, society, and power perspectives. They advise governments and institutional organisations on governance matters.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4fa660b3-ab97-4a99-ad73-ce3c2612f2c3','key_16137','','','3412','3412.5','social work assistant',replace('assistant social worker\nsocial work assistant team member\nsocial work assistant\nsupport worker assistant\nsocial work team assistant\nsocial work assistant practitioner\nassistant support worker\nsupport work assistant practitioner\nsupport work assistant team member','\n',char(10)),'Social work assistants are practice-based professionals who promote social change and development, social cohesion, and the empowerment and liberation of people. Social work assistants assist guiding staff, helping clients to use services to claim benefits, access community resources, find jobs and training, obtain legal advice or deal with other local authority departments. They assist and work together with social workers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4fe4b1f1-e84d-4056-b062-83d5a64fc571','key_16138','','','1312','1312.1','aquaculture harvesting manager',replace('aquaculture harvesting supervisor\nfish farm harvesting manager\nharvesting operations manager\naquaculture harvesting manager\nmanager of aquaculture harvesting','\n',char(10)),'Aquaculture harvesting managers control harvesting operations of aquatic organisms which entails understanding and knowledge of techniques and equipment used in harvesting processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/4fef1907-cf82-413b-841a-02ef57e299a6','key_16139','','','3141','3141.2','scientific laboratory technician',replace('laboratory research assistant\nlaboratory technology expert\nlab assistant\nchemistry laboratory assistant\nlaboratory assistant\nlaboratory technologist\nlaboratory technology specialist\nscientific laboratory technician','\n',char(10)),'Scientific laboratory technicians carry out laboratory-based research, analysis and testing and support life science professionals. They sample, test, measure, research and analyse in areas such as biology, biotechnology, environmental science, forensic science and pharmacology. Scientific laboratory technicians also observe and monitor laboratory activities, record test sequences and analyse the results.','','Excludes  <a resource="http://data.europa.eu/esco/occupation/0044c991-c26f-4261-a213-4bd1c0564a4c" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/0044c991-c26f-4261-a213-4bd1c0564a4c"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">medical laboratory assistant</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5001506f-dd7d-4801-92ac-6f7e93b45133','key_16140','','','3139','3139.2','industrial robot controller',replace('industrial robot operative\nindustrial robot controller\ndelta robot controller\nrobotic equipment operative\nindustrial robotics controller\nrobotic equipment controller\nrobotic welder programmer\nindustrial robot operator\nrobot welder programmer\nindustrial robot test technician\nscara robot controller\nindustrial robot setter\nindustrial robot technician\nrobotic equipment operator','\n',char(10)),'Industrial robot controllers operate and monitor industrial robots used in automation processes to perform various manufacturing activities such as lifting, welding and assembling. They ensure that the machines are working correctly and in sync with other industrial robots, maintain and repair defective parts, assess risks and perform tests.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/500b7b14-507c-4c49-816d-c1d0b05b85e4','key_16141','','','3214','3214.3.1','prosthetic-orthotics technician',replace('prosthetic devices maker\nprosthetics maker\northotic and prosthetic technician\northotic appliance maker\nprosthetics assembler\nprosthetic support maker\nO&P technician\nsurgical appliance  maker\nprosthetic appliance maker\northotic technician\northopaedic appliance maker\nprosthetic-orthotics technician\nprosthetic brace maker\northotics technician\northopaedic technician\nsurgical appliance technician','\n',char(10)),'Prosthetic-orthotics technicians design, create, fit and repair supportive devices, such as braces, joints, arch supports, and other surgical and medical appliances.','',replace('Includes people producing orthopaedic devices. \nExcludes people producing electrical devices such as hearing aids.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/501a3798-08bf-4475-88d2-0e8c9befb101','key_16142','','','7522','7522.3','cooper',replace('wooden barrel builder\ncooper\nwooden barrel assembler\nmaster cooper\nwooden barrel constuctor\nbarrel constuctor\nwooden barrel maker\nbarrel assembler\nmaster barrel builder\nbarrel maker','\n',char(10)),'Coopers build barrels and related products made of segments of wood, like wooden buckets. They shape the wood, fit hoops around them, and shape the barrel to hold the product, which contemporarily is usually premium alcoholic beverages.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/502fe307-ae65-46cb-a485-e559b9a35266','key_16143','','','8142','8142.7','injection moulding operator',replace('injection moulding setter\nplastic moulding operative\nplastic injection moulding operator\ninjection moulding operative\nplastic molding operator\nplastic molding worker\nthermoplastic moulding worker\ninjection molding worker\ninjection molding setter\ninjection moulding machine operator\ninjection molding operator\nthermoplastic moulding operative\nthermoplastic molding worker\nplastic moulding worker\nplastic moulder\nthermoplastic moulder\ninjection molding operative\nwax injection operator\ninjection moulder\nplastic molding operative\ninjection molding machine operator\nthermoplastic molding operator\nplastic injection moulding machine operator\nplastic injection molding machine operator\nthermoplastic molding operative\ninjection moulding worker\nplastic injection molding operator','\n',char(10)),'Injection moulding operators operate and monitor injection moulding machines, in order to cast products from thermoplastic materials. They regulate the temperature, pressure, and volume of plastic, according to pre-defined specifications. Injection moulding operators also remove finished products and cut-away excess material, using a knife or other hand tools.','','Includes people producing jewellery and bijouterie.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/503ea267-93e9-4b8c-ae5e-817fe7e4a245','key_16144','','','9613','9613.1','street sweeper',replace('street sweeper driver\nstreet sweeper\nsweeper of city streets\nstreet maintenance worker\nleaf-sucker operator\nleaf vacuum operator','\n',char(10)),'Street sweepers operate sweeping equipment and machinery to remove waste, leaves or debris from streets. They maintain records of sweeping operations and maintain, clean and perform minor repairs to the equipment used.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/50609eb5-9957-4cbf-9b91-6f46ce49cfad','key_16145','','','3359','3359.5','freight inspector',replace('freight & cargo inspector\nprincipal surveyor\nmarine and cargo surveyor\ninspector of freight\ncargo surveyor\ncargo inspector\nfreight and cargo inspector\nfreight surveyor\nfreight inspector','\n',char(10)),'Freight inspectors determine if freight is secure and comes with the proper documentation. They inspect, manage and document freight shipments while verifying that the content comply with local, national and international regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/50743f62-3d5b-4ef6-b706-00d2b1adfcc3','key_16146','','','3412','3412.4.12','residential home older adult care worker',replace('residential home older adult care worker\nolder adult services worker\nresidential support worker\nresidential support worker (older adults)\nsupport worker (older adults in care)\nsupported living support worker','\n',char(10)),'Residential home older adult care workers counsel and support elderly who have physical or mental disabilities. They monitor their progress and provide them with care in a positive living environment. They liaise with the clients'' families in order to arrange their visits.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/50760da6-abc3-424c-b159-8ddf0818e016','key_16147','','','2412','2412.1','business valuer',replace('chartered appraiser\nbusiness evaluator\naccredited business appraiser\nbusiness valuation specialist\nchartered business valuator\nvaluator of businesses\nbusiness valuation consultant\nbusiness valuation analyst\nbusiness valuers\nbusiness valuer\nbusinesses evaluator\nbusiness appraiser\nbusiness valuation officer\nbusinesses valuator\nbusiness valuator\nvaluator of business','\n',char(10)),'Business valuers provide valuation assessements of business entities, stock and other securities and intangible assets, in order to assist their clients in strategic decision-making procedures such as mergers and acquisitions, litigation cases, bankruptcy, taxation compliance and general restructuring of the companies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/509f9452-ed18-4fbf-8a4c-d45de85aa2f7','key_16148','','','7544','7544.1.1','pesticides sprayer',replace('spray technician\ntrees sprayer\npesticides sprayer\nsprayer of pesticides\npesticides applicator\nlawn care chemical applicator','\n',char(10)),'Pesticides sprayers mix pesticides, insecticides, fungicides, and other chemical solutions, and apply them via technical equipment such as sprayers on trees, plants and lawns. They clean and maintain operating machinery and ensure the overall safety in designated areas.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/50a66df6-c141-4b4a-937a-d3bc55737ae3','key_16149','','','4214','4214.1','debt collector',replace('credit control agent\nthird-party collector\nfield debt collector\nbill and account recovery agent\nassistant debt collector\nbill collector\nrevenue agent\ndebt collector\nbill and account collector\ncollection agent\nsenior debt collector\ndebt collection officer\ndebt recovery agent\ncollections associate\ndebt collection agent\npayment collector\ndebt collection operative','\n',char(10)),replace(' \n Debt collectors compile debt owned to the organisation or third parties, mostly in cases when the debt is past its due date. \n \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/50af07f0-7a75-424e-a66d-5a9deea10f4c','key_16150','','','2519','2519.7.2','ICT accessibility tester',replace('ICT accessibility tester\nusability tester\naccessibility test specialist\naccessibility testers','\n',char(10)),'ICT accessibility testers evaluate websites, software applications, systems or user interface components with regards to friendliness, operability of the navigation and visibility to all types of users, especially including those with special needs or disabilities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/50b41847-6dad-4a2e-9427-a0374c2963fd','key_16151','','','3435','3435.25.4','stage machinist',replace('flying system technician\nstage machinist\nstage & lighting technician\ntheater mechanist\nset mechanics supervisor\nflying system operator\ntheatre machinist\nstage and lighting technician\ntheatre mechanist\ntheater machinist\nstage mechanist\nscene-shifter\nlighting & stage technician\nflying operator\nset technician\nlighting and stage technician\nflyman\nchief stage technician','\n',char(10)),'Stage machinists manipulate sets and other elements in a performance based on the artistic or creative concept, in interaction with the performers. Their work is influenced by and influences the results of other operators. Therefore, the stage machinists work closely together with the designers, operators and performers. Stage machinists prepare and perform the setup, execute changeovers and operate manual fly bar systems. Their work is based on plans, instructions and calculations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/50e00fda-ac7c-4bc5-9f19-69a33ff3d6e7','key_16152','','','2422','2422.12.6','fiscal affairs policy officer',replace('fiscal policy analyst\ntax and development policy officer\npolicy officer, fiscal affairs\nfiscal policy officer\nfiscal policy researcher\nfiscal policy specialist\nfiscal policy consultant\nfiscal policy developer\nfiscal affairs policy officer\ntax policy officer','\n',char(10)),'Fiscal policy officers research, analyse and develop policies related to taxation and government spending in public policy sectors, and implement these policies to improve the existing regulation around the sector. They work closely with partners, external organisations or other stakeholders and provide them with regular updates.   ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/511c8e9f-b19e-4ae8-9959-c047e7410441','key_16153','','','3112','3112.9','railway infrastructure inspector',replace('railway infrastructure surveyor\nrailway infrastructure safety inspector\nrailway infrastructure auditor\nrailway infrastructure examiner\nrailway infrastructure inspector','\n',char(10)),'Railway infrastructure inspectors are responsible for checking the conditions of railways. They monitor compliance to health and safety standards and inspect the infrastructure to detect damage or flaws. They analyse and report on their findings to ensure railway conditions are maintained at a safe level.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/511f366c-ba41-4826-9622-8cda6d6e3b91','key_16154','','','4412','4412.1','mail clerk',replace('mail room clerk\nsorting clerk\nmail sorter\npost office mail clerk\nmail clerk','\n',char(10)),'Mail clerks perform mail handling, sorting, recording and other duties in connection with mail services from post offices or related organisations. They keep records of the incoming and outgoing packages and letters in the establishment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5157706c-3fce-420e-9e7f-863000af5397','key_16155','','','7532','7532.6','leather goods patternmaker',replace('leatherware modelist\nleather goods modelist\nleather goods pattern-maker\nleather goods pattern maker\nleather goods patternmaker\npattern maker leather goods\nleatherware pattern-maker','\n',char(10)),'Leather goods patternmakers design and cut patterns for various kinds of leather goods using a variety of hand and simple machine tools. They check nesting variants and estimate material consumption.','','Includes people performing activites starting after the sample model is approved for production.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/517c01aa-e875-4690-bf38-17307a57eafe','key_16156','','','7533','7533.5','sewing machinist',replace('sewing worker\nseamstress\nsew on machinist\nsewing operator\nsewing machinist\nsewer','\n',char(10)),'Sewing machinists sew components of wearing apparel together. They can repair and renovate wearing apparel by hand or by using different sewing machines. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/518348e6-0095-4f37-ad46-5ef1b8b5ee2f','key_16157','','','1321','1321.3','textile operations manager',replace('textile production operations manager\ntextile operations manager\ntextile operations supervisor\ntextile operation supervisor\ntextile operations coordinator\ntextile production manager','\n',char(10)),'Textile operations managers schedule orders and delivery times in order to assure the efficient flow of the production system.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/51c7015c-dc81-4fcb-b15f-619a4656a9fe','key_16158','','','3113','3113.1.2','electromechanical engineering technician',replace('electromechanical engineering specialist\nwind energy technician\nship electrical technician\nelectrical engineering technician\nelectromechanical technician\nelectromechanical engineering technologist\nelectro-mechanical technician\ntechnician in electromechanical systems\nspecialist technician in electromechanical systems\nelectromechanical maintenance technician\nelectromechanical engineering technician','\n',char(10)),'Electromechanical engineering technicians collaborate with electromechanical engineers in the development of electromechanical equipment. Electromechanical engineering technicians are responsible for building, installing, testing, monitoring, and maintaining the electromechanical equipment, circuits and systems. They test this by the use of test instruments such as oscilloscopes and voltmeters. Electromechanical engineering technicians also use soldering equipment and hand tools to repair electromechanical equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/51ca3c6e-1c9b-49e4-aa09-9e77d1a59547','key_16159','','','5223','5223.7.19','fruit and vegetables specialised seller',replace('organic fruit and vegetable specialised seller\norganic fruit salesperson\norganic fruit specialised seller\ngreengrocers shop salesperson\ngreengrocers store specialised seller\norganic vegetable salesperson\ngreengrocers store salesperson\nvegetable salesperson\norganic fruit and vegetable salesperson\norganic vegetable specialised seller\nfruit specialised seller\nfruit salesperson\nfruiterers shop salesperson\ngreengrocers shop specialised seller\nfruit and vegetables specialized seller\ngreengrocery counter salesperson\ngreengrocery salesperson\ngreengrocery counter specialised seller\nfruiterers shop specialised seller\ngreengrocery specialised seller\nvegetable specialised seller','\n',char(10)),'Fruit and vegetables specialised sellers sell fruit and vegetables in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/51cd3a7a-eae6-4dc6-9f24-179d7ec6d230','key_16160','','','2421','2421.5','logistics analyst',replace('distribution consultant\nlogistics analyst\nlogistics and supply chain analyst\nlogistics consultant\ndistribution and logistics consultant\ndistribution analyst\ndistribution and logistics analyst\nlogistics and distribution analyst\nanalyst of logistics\nsupply chain analyst\nsupply chain consultant','\n',char(10)),'Logistics analysts streamline product manufacturing, transportation, storage and distribution. They assess production and supply chain problems to determine economically efficient solutions. They assists company managers in decision-making processes and direct programs designed to provide subcontractors, managers and customers with logistic technology.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/51ec004e-aec8-4f89-b114-952bcc56b0b0','key_16161','','','2642','2642.1.17','vlogger',replace('lifestyle vlogger\nbeauty vlogger\non-line video columnist\nvideo weblogger\nvlogger and blogger\nvideo blogger\nvlogger\nvideo podcaster\nvlogger & blogger\nfashion vlogger\ntravel vlogger\nweb video columnist','\n',char(10)),'Vloggers make online videos to talk about a wide range of subjects such as politics, fashion, economics and sports. They can relate objective facts, but often they also give their opinion on the related topic. Vloggers post these videos online on social media or streaming platforms, often accompanied with written text. They also interact with their viewers via comments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/521bbfd5-c179-46e3-879b-e74bd6db367c','key_16162','','','3214','3214.1','audiology technician',replace('audiology specialist service technician\nhearing aid technician\nhearing help technician\naudiology specialist technician\nhearing aid service technician\naudiology technician\nhearing instrument specialist\naudio prosthetist\naudiology service technician\nhearing aid acoustician\naudiology assistant','\n',char(10)),'Audiology technicians create and service hearing aids and hearing protection products. They dispense, fit and provide hearing aids for those who need them.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/52207284-2681-40d3-a317-ec81ca1b3e41','key_16163','','','2162','2162.1','landscape architect',replace('landscape expert\nlandscape architect\nlandscape specialist\nlandscape design specialist\nlandscape engineer\nlandscape artist\nlandscapes architect\nlandscape design expert','\n',char(10)),'Landscape architects plan and design the construction of gardens and natural spaces. They determine the specifications and distribution of the space. They combine an understanding of the natural space with a sense of aesthetics in order to create a harmonious space.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/522c55ca-ee4a-407d-9fc6-f810c5266523','key_16164','','','3153','3153.2.2.1','airline transport pilot',replace('aircraft captain\ncaptain\nairline aeroplane captain\nairline pilot\nairline transportation pilot\nairline transport pilot','\n',char(10)),'Airline transport pilots fly large aircrafts with a maximum take-off weight of more than 5700 kilograms, to transport passengers, mail, or freight on long or short-haul flights for leisure, business or commercial purposes. They have the overall responsibility for the safe and efficient operation of aircrafts and the safety of crew and passengers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/523ab5a0-5e72-418d-9d61-a6126a446123','key_16165','','','3153','3153.2.2','commercial pilot',replace('commercial pilot\npilot of commercial aircraft\npilot of commercial aeroplanes\nairline pilot\ncaptain','\n',char(10)),'Commercial pilots navigate flight of fixed-wing and multi-engine aircrafts for the transport of passengers and cargo.','',replace('Includes regional, national, and international <a resource="http://data.europa.eu/esco/occupation/522c55ca-ee4a-407d-9fc6-f810c5266523" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/522c55ca-ee4a-407d-9fc6-f810c5266523"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">airline transport pilots</span></a>. \n Includes <a resource="http://data.europa.eu/esco/occupation/b3b32930-05f0-4b2e-93bf-34d1e3f89a3b" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/b3b32930-05f0-4b2e-93bf-34d1e3f89a3b"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">flight instructors</span></a> of airline pilots.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/526ba8e1-fc71-4657-8d27-52f03e8ca81d','key_16166','','','1341','1341.1','child care coordinator',replace('vacation camp manager\nvacation camp activities manager\nchild care coordinator\npre-school activities coordinator\nvacation camp coordinator\nvacation activities coordinator\npre-school activities manager\nchild day care vacation coordinator\nvacation activities manager\nchild day care vacation manager\nafter school activities coordinator\nafter school activities manager\nvacation camp activities coordinator','\n',char(10)),'Child care coordinators organise child care services, activities and events after the school hours and during school hoildays. They assist in the development of children by implementing care programmes. Child care coordinators also entertain children and maintain a safe environment for the children.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/528e63a7-91be-4477-be2f-69ce82deb726','key_16167','','','3135','3135.1','metal furnace operator',replace('blast furnace operator\nplant manager\nsteel furnace operator\nsmelting furnace operator\nsmelter\nmetal operations manager\nmetal furnace operator\nfoundry operations manager\nsmelter operator\nfoundry plant manager\nfoundry production manager','\n',char(10)),'Metal furnace operators monitor the process of making metal before it is cast into forms. They control metal making furnaces and direct all activities of furnace operation, including the interpretation of computer data, temperature measurement and adjustment, loading vessels, and adding iron, oxygen, and other additives to be melted into the desired metal composition. They control the chemicothermal treatment of the metal in order to reach the standards. In case of observed faults in the metal, they notify the authorised personnel and participate in the removal of the fault.','','Includes people working in foundries, steel mills and metal refining plant.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/528f90ed-e250-48bd-aacc-ffb7b1de5654','key_16168','','','5223','5223.7.35','textile specialised seller',replace('mercers specialised seller\ntextile specialized seller\ntextile salesperson\nfabric specialised seller\ndrapery specialised seller\nhouse linen salesperson\ndrapers salesperson\nmercer\ndrapers specialised seller\nfabric salesperson\nhouse linen specialised seller\nmercers salesperson\ndrapery salesperson','\n',char(10)),'Textile specialised sellers sell textiles, fabrics and haberdashery, etc. in specialised shops.','','Excludes people performing clothing sales.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/52a769ab-c899-4e8c-b31d-e5c2a01f32e3','key_16169','','','3423','3423.1.2.2.1','survival instructor',replace('instructor in survival techniques\neducator in survival techniques\nsurvival techniques instructor\nsurvival instructor\nwilderness survival trainer\nsurvival trainer\nwilderness survival instructor\nwilderness instructor\nsurvival education instructor','\n',char(10)),'Survival instructors guide groups into vast, natural areas, and assist them in a self-directed instruction of basic survival needs without any comfort facilities or modern gear to fall back on. They coach the participants into mastering survival skills such as fire making, producing primitive equipment, shelter construction and procurement of water and nourishment. They ensure the participants are aware of certain safety measures without diminishing the level of adventure, of environment protection and of risk management. They encourage efforts of leadership from the group and mentor the participants individually so as to push their limits responsibly and help overcome potential fears.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/52b9e8dc-de6c-41dd-b29a-e1291c273f6f','key_16170','','','2654','2654.3','producer',replace('tour production manager\nexecutive producer\nmotion picture manager\nmusic producer\nfilm producer\nfilm director\nproducer\nvideo producer','\n',char(10)),'Producers are responsible for managing the production of music, motion pictures or series. They plan and coordinate all aspects of the production such as the direction, publication and financing. Producers oversee the production and manage all technical and logistic aspects of recording and editing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/52c25b47-c36e-49ff-92ce-8a8c5413fde6','key_16171','','','8212','8212.4','surface-mount technology machine operator',replace('surface mount technology machine operator\noperator of SMT machine\nelectronics surface mount soldering machine operator\nsurface-mount technology machine operator\noperator of surface-mount technology machine\nSMT machine operator\nelectronics surface mount soldering operative\nsoldering machine operative\nSMT operator','\n',char(10)),'Surface-mount technology machine operators use surface-mount technology (SMT) machines to mount and solder small electronic components onto printed circuit boards to create surface-mounted devices (SMD).','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/52d303a7-630b-4e3f-9f62-becd9b2fcb46','key_16172','','','1324','1324.3.1.6.22','metals and metal ores distribution manager',replace('metals and metal ores supply chain planner\ntrainee metals and metal ores distribution manager\nsenior metals and metal ores distribution manager\nmetals and metal ores logistics planner\nmetals and metal ores distribution planner\ngraduate metals and metal ores distribution manager\nmetals and metal ores logistics manager\nassistant metals and metal ores distribution manager\nmetals and metal ores distribution manager\nmetals and metal ores supply chain manager','\n',char(10)),'Metals and metal ores distribution managers plan the distribution of metals and metal ores to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/52d5edc9-7902-4cd3-8b06-6bd77c98d440','key_16173','','','2519','2519.7.4','ICT system tester',replace('ICT systems testers\nICT systems tester\nICT systems test engineer\nsystems tester\nIT system tester\nICT system tester','\n',char(10)),'ICT system testers perform testing activities and some test planning activities. They may also debug and repair ICT systems and components although this mainly corresponds to designers and developers. They ensure that all systems and components function properly before delivering them to internal and external clients.','','Excludes people performing managerial activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/52ded7d7-11df-42e3-b90a-d7f4b70fb4b9','key_16174','','','2634','2634.2','psychologist',replace('expert counselling psychologist\ncounselling psychology practitioner\npractitioner of counselling psychology\nexpert in counselling psychology\nspecialist counselling psychologist\noccupational psychologist\nsport and exercise psychologist\npsychologist\npsychology researcher\npractitioner of sport and exercise psychology\npractitioner of occupational psychology','\n',char(10)),'Psychologists study the behaviour and mental processes in humans. They provide services to clients who deal with mental health issues and life issues such as bereavement, relationship difficulties, domestic violence, and sexual abuse. They also provide counselling for mental health issues such as eating disorders, post-traumatic stress disorders, and psychosis in order to help the clients rehabilitate and reach a healthy behaviour.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/52df9d56-efd4-48d0-ad93-59231943fc4c','key_16175','','','2166','2166.3.1','3D animator',replace('3D animators\ncomputer-generated imagery animator\n3D animator\nCGI animator\nCGI designer\n3D designer','\n',char(10)),'3D animators are in charge of animating 3D models of objects, virtual environments, layouts, characters and 3D virtual animated agents.','','Excludes people performing managerial activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/52fcfdb9-9dcf-4591-aba5-45337f791a10','key_16176','','','2433','2433.6.5','technical sales representative in mining and construction machinery',replace('senior technical sales engineer in mining and construction machinery\nassistant technical sales representative in mining and construction machinery\nassistant technical sales engineer in mining and construction machinery\nmining and construction machinery technical field sales representative\nmining and construction machinery  inside technical sales representative\nmining and construction machinery outside technical sales representative\nmining and construction machinery  field saleswoman\ntechnical sales engineer in mining and construction machinery\ntechnical sales representative in mining and construction machinery\ngraduate technical sales representative in mining and construction machinery\nsenior technical sales representative in mining and construction machinery\ntrainee technical sales representative in mining and construction machinery\nmining and construction machinery field salesperson\nmining and construction machinery field salesman','\n',char(10)),'Technical sales representatives in mining and construction machinery act for a business to sell its merchandise while providing technical insight for customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5318abf2-2e7b-4d09-ae57-6bd500689ebc','key_16177','','','7211','7211.1.3','mouldmaker',replace('foundry sand moulder\nmold maker\nhand-moulded casting worker\nmould maker\nmoldmaker','\n',char(10)),'Mouldmakers manually create moulds for the production of metal products. They mix sand and hardening materials to obtain a specialised mixture. They then use a pattern and one or more cores to produce the right shape impression in this material. The shaped material is then left to set, later to be used as a mould in the production of ferrous and non-ferrous metal castings.','',replace('Excludes people producing moulds for non-metallic materials. \n Excludes people using machines. Includes people producing jewellery and bijouterie.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/531e98aa-2a03-489d-b811-ebbb7389864a','key_16178','','','8159','8159.3','canvas goods assembler',replace('canvas sail maker\ncanvas goods assembler\ncanvas goods producer\ncanvas goods production worker\ncanvas tent maker\ncanvas product assembler\ncanvas products maker\ncanvas goods production operative','\n',char(10)),'Canvas goods assemblers construct products made from closely woven fabrics and leather such as tents, bags or wallets. Artists also use it as painting surface.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/534d18fe-eae4-4a02-af4d-f78e92faa5d5','key_16179','','','7126','7126.6','irrigation technician',replace('irrigation worker\nirrigation machinery operative\nsprinkler system maintenance technician\nirrigation technician\nirrigation operative\nirrigation system technician\nirrigation pipework technician\nirrigation machinery technician\nsprinkler system operator\nirrigation system maintenance technician\nsprinkler system technician\nirrigation machinery worker\nirrigation maintenance technician\nirrigation machinery operator','\n',char(10)),'Irrigation technicians specialise in the installation, maintenance and repair of sprinklers, pipes and other irrigation systems. They operate machinery used for irrigation systems handling, and ensure compliance with environmental standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/53be4843-8f31-4645-b25c-9a5bbb04f6b6','key_16180','','','3119','3119.14','process engineering technician',replace('production supervisor\nresident inspector\nmanufacturing  engineering supervisor\nprocess engineering technician\nmanufacturing engineering operations supervisor\nmanufacturing processing supervisor\nresident technical support\nengineering operations inspector\nengineering operational inspector\nmanufacturing processing engineer\nproduction inspector\nprocess engineering supervisor','\n',char(10)),'Process engineering technicians work closely with engineers to evaluate the existing processes and configure manufacturing systems to reduce cost, improve sustainability and develop best practices within the production process.','','Excludes people performing quality control of products.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/53c0086b-9b54-4c29-8bba-69ce8256cb91','key_16181','','','8156','8156.4','pre-stitching machine operator',replace('sole edge-inking operator\nleather goods pre-stitching machine operator\npre-stitching machine operator\ngluing operator\nburnishing machine operator\nedge-painting machine operator\nskiver\nedge inker\nburnisher\nburnishing machine tender\nshoemaking pre-stitching folding machine operator\nedge-inking machine operator\nshoemaking pre-stitching folding machine tender\nskiving machine operator\nshoemaking pre-stitching folding operator\nedge-painting machine tender\nedge-inking machine tender\nskiving machine tender\nskiving operator','\n',char(10)),'Pre-stitching machine operators handle tools and equipment for splitting, skiving, folding, punching, crimping, placking, and marking the uppers to be stitched and, when needed, apply reinforcement strips in various pieces. They may also glue the pieces together before stitching them. Pre-stitching machine operators perform these tasks according to the instructions of the technical sheet. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/53c7756d-4114-4c7f-90b0-26717f458ac5','key_16182','','','7233','7233.13','pneumatic systems technician',replace('vacuum system installer\nvacuum systems assembler\nPneumatic systems inspector\npneumatic systems technician\ngas system inspector\npneumatic vacuum system inspector\npneumatic air system inspector\npneumatic system installer\npneumatic gas system inspector\npneumatic system assembler\nair system inspector','\n',char(10)),'Pneumatic systems technicians use blueprints and technical documentation to assemble and install equipment operated by gas or air under pressure. They set up the systems according to engineering specifications and test them to ensure good functioning order. They may also perform maintenance and repair work on installed pneumatic equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/53cdcc84-e5ee-488c-aa09-88084e06d6d7','key_16183','','','2262','2262.1.2.2','industrial pharmacist',replace('industrial pharmacist\npharmaceutical industry pharmacist\nindustrial pharmacy consultant\nindustrial pharmacy specialist','\n',char(10)),'Industrial pharmacists are involved in the research and creation of medications. They develop new medicines, perform tests, assure quality and ensure that medication complies with regulations.','','','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/53cdd34e-22f2-41bd-b3c7-de22bf9bbcae','key_16184','','','1411','1411.1','accommodation manager',replace('resort manager\nresident manager\nhotel general manager\nhospitality establishment director\naccommodation manager\nrestaurant manager\nhotel director\nyouth hostel manager\nhotel manager\ncoffee shop manager','\n',char(10)),'Accommodation managers are in charge of managing the operations and overseeing the strategy for a hospitality establishment. They manage human resources, finances, marketing and operations through activities such as supervising the staff, keeping financial records and organising activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/53d2052f-edc0-4f44-8be1-164526412bfe','key_16185','','','2141','2141.4','industrial engineer',replace('industrial engineers\nindustrial technology engineering consultant\nindustrial engineering consultant\nindustrial technology engineer\nindustrial technology engineering specialist\nindustrial engineer\nindustrial waste engineer\nindustrial engineering specialist\nindustrial engineering expert\nindustrial technology engineering expert\nindustrial technology engineering adviser\nindustrial engineering adviser','\n',char(10)),'Industrial engineers design a vast array of production systems aiming to present efficient and effective solutions. They integrate a varied number of variables such as workers, technology, ergonomics, production flows, and product specifications for the design and implementation of production systems. They can specify and design for microsystems as well.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/53d55c80-6a82-4187-b59f-b737aa4cdaab','key_16186','','','8153','8153.1.4','leather goods stitching machine operator','leather goods stitching machine operator','Leather goods stitching machine operators join the cut pieces of leather and other materials to produce leather goods, using tools and a wide range of machines, such as flat bed, arm and one or two columns. They also handle tools and monitor machines for preparing the pieces to be stitched, and operate the machines. They select threads and needles for the stitching machines, place pieces in the working area, and operate with machine guiding parts under the needle, following seams, edges or markings or moving edges of parts against the guide.','','Includes joining, decorating and reinforcing pieces of leather good products using single, double or multiple-needle stitching machines.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/53db8c0d-7aed-47aa-a339-eb9d485d25dc','key_16187','','','1345','1345.1.8','secondary school head teacher',replace('high school director\nhead of secondary school\nsecondary school headmaster\nsecondary school principal\nsecondary school headteacher\nsecondary school director\nhead teacher of secondary school\nhigh school principal\nhigh school head teacher\nheadteacher of secondary school\nsecondary school head','\n',char(10)),'Secondary school head teachers are responsible for meeting curriculum standards, which facilitate academic development for the students. They manage staff, working closely with the different department heads, and evaluate the subject teachers in a timely manner in order to secure optimal class performance. They also ensure the school meets the national education requirements set by law and cooperate with local communities and governments. They may also work in vocational schools.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/53e61b71-60a3-4873-bc14-d6e28693818c','key_16188','','','1324','1324.3.1.6.28','textiles, textile semi-finished and raw materials distribution manager',replace('textiles, textile semi-finished and raw materials supply chain manager\ntextiles, textile semi-finished and raw materials supply chain planner\ngraduate textiles, textile semi-finished and raw materials distribution manager\ntrainee textiles, textile semi-finished and raw materials distribution manager\nsenior textiles, textile semi-finished and raw materials distribution manager\nassistant textiles, textile semi-finished and raw materials distribution manager\ntextiles, textile semi-finished and raw materials distribution planner\ntextiles, textile semi-finished and raw materials distribution manager\ntextiles, textile semi-finished and raw materials logistics planner\ntextiles, textile semi-finished and raw materials logistics manager','\n',char(10)),'Textiles, textile semi-finished and raw materials distribution managers plan the distribution of goods to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/53ed8787-3423-4843-8c63-7be1877d29e2','key_16189','','','5131','5131.2.1','head waiter/head waitress',replace('table service manager\nwaiter service manager\nchief waitress\nchief waiter\nhead waiter/head waitress\nhead waitress\nfront of house manager\nhead waiter','\n',char(10)),'Head waiters/waitresses manage the food and beverage service in a hospitality outlet or unit. They are responsible for the customer''s experience. Head waiters/waitresses coordinate all actions involving customers such as welcoming guests, ordering, delivering the food and beverages and supervise financial transactions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/54361270-bc5e-40fb-8f84-a0023a85cdf7','key_16190','','','2310','2310.1.17','fine arts instructor',replace('fine arts educator\nfine arts instructor\nfine arts conservatory trainer\nprofessor of fine arts\nfine arts conservatory instructor\nconservatory instructor in fine arts\nfine arts conservatory teacher\ninstructor in fine arts\nfine arts conservatory lecturer','\n',char(10)),'Fine arts instructors educate students in specific theory and, primarily, practice-based fine arts courses at a specialised fine arts school or conservatory at a higher education level, including drawing, painting and sculpturing. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master in the fine arts. Fine arts instructors monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the fine arts through, often practical, assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/544cd9cf-e2ee-4ac0-8a02-7147af6b97d7','key_16191','','','1213','1213.5','business manager',replace('general manager\ncorporate service manager\nbusiness unit manager\nbusiness coordinator\nvice-president\nteam manager\noperations manager\nbusiness manager','\n',char(10)),'Business managers are responsible for setting the objectives of the business unit of a company, creating a plan for the operations, and facilitating the achievement of the objectives and implementation of the plan together with employees of the segment and stakeholders. They keep an overview of the business, understand detailed information of the business unit and support the department, and make decisions based on the information at hand.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/546ec458-e2a4-4cb1-ac17-553d314ac2e2','key_16192','','','1219','1219.5.4','water treatment plant manager',replace('water facility manager\nwater treatment manager\nwater treatment plant manager\nwater treatment facility manager\nwater plant treatment manager\nwater treatment specialist\nwater treatment plant maintenance manager\ndrinking water treatment plant manager\nmanager, water treatment plant\nwater plant superintendent\nwater storage plant manager\nwater distribution plant manager\nwater treatment engineer\nwater plant engineer\nwater treatment plant supervisor','\n',char(10)),'Water treatment plant managers supervise water treatment, storage and distribution in a water plant. They ensure the plant''s operations are compliant with regulation, and supervise staff. They also implement new policies, and oversee equipment maintenance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/547b304b-9a72-4717-9b24-b7046df5f64e','key_16193','','','5223','5223.7','specialised seller',replace('specialized sales advisor\nspecialized sales manager\nspecialized sales assistant\nspecialized trader\nspecialised trader\nspecialised sales assistant\nspecialised vendor\nspecialized salesperson\nspecialized seller\nspecialised salesperson\nspecialized vendor\nspecialised sales advisor','\n',char(10)),'Specialised sellers sell goods in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/54cba39a-5bb8-4e4c-89cd-d6097d3c6c3a','key_16194','','','4419','4419.1','advertising assistant',replace('executive advertising assistant\nadvertising space clerk\nadvertising trainee\nadvertising assistant\npublicity assistant\nadvertising campaign assistant\nadvertising clerk','\n',char(10)),'Advertising assistants provide support in all the operations carried out in advertising departments or advertising agencies. They ensure that administrative activities are handle and sort out. They communicate with the staff of the advertisement department and with external clients. Moreover, they have understanding of the operations in advertising as to assist with the pragmatic implementation of plans and tasks carried out by managers and consultants and for being involved in creating content for use both on-line or in print.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/54ffd31f-ed90-4af4-b877-cd246351ad39','key_16195','','','7321','7321.1','prepress technician',replace('prepress technologist\nprepress engineer\nprepress worker\nprepress technician\npress preparation technician\ngraphics technician\nprepress specialist\npress preparation engineer\npress preparation technologist\npress preparation specialist\npress preparation worker','\n',char(10)),'Prepress technicians prepare printing processes by formatting, setting and composing text and graphics into a suitable form. This includes the capture of text and image and processing it electronically. They also prepare, maintain and troubleshoot printing presses.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/55076d82-52a7-47eb-b882-f12322c42ecd','key_16196','','','2113','2113.1.6','textile chemist',replace('textile colour formulator\ntextile chemist and colourist\ntextile chemical technician\ntextile chemical supervisor\ntextile chemical coordinator\ntextile chemist','\n',char(10)),'Textile chemists coordinate and supervise chemical processes for textiles like yarn and fabric forming such as dyeing and finishing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/55167849-82cf-484e-b818-fa702a4e69e9','key_16197','','','2114','2114.1.5','hydrologist',replace('hydrology researcher\nhydrology science researcher\nhydrology research analyst\nhydrologists\nhydrological engineer\nhydrologist\nhydrology scientist\nwater analysis specialist\nhydrogeologist\nhydrobiologist\nsurface hydrologist\nhydrology analyst\nhydrology research scientist','\n',char(10)),'Hydrologists research and study the quality, current challenges, and distribution of water in the Earth. They study water supply from rivers, streams, and springs to determine their adequate and sustainable use. Together with a cross functional team of professionals, they plan and develop how water can be supplied to cities and urban areas while ensuring efficiency and resource conservation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/551a6797-3ca9-41fc-9dca-9a982a8fd8a6','key_16198','','','2149','2149.15','robotics engineer',replace('robotic engineer\nrobotics engineering consultant\nengineer of robotics\nrobotics engineering specialist\nautonomous systems engineer\nrobotics engineer\nengineer in robotics\nrobotic technology engineering specialist\nautomation systems engineer\nengineer of robotic systems\nrobotics engineering expert\nrobotic technology engineering expert\nrobotic technology engineer\nrobotic technology engineering adviser\nrobotic systems engineer\ncontrol systems engineer\nrobotics engineering adviser\nrobotic technology engineering consultant','\n',char(10)),'Robotics engineers design and develop robotic devices and applications in combination with mechanical engineering principles. They use pre-established designs and current developments for improving or inventing systems, machinery and equipment. They combine several knowledge fields such as computing, engineering, and electronics in the development of new engineering applications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5520bfd0-49d3-47e5-abe0-d72354711bc0','key_16199','','','8160','8160.10','cacao beans cleaner',replace('cacao beans cleaner\ncocoa bean treatment system operator\ncocoa bean shelling machine operator\ncoca bean winnower controller\ncocoa bean cleaning machine operator','\n',char(10)),'Cacao beans cleaners operate machines for the removal of foreign materials such as stones, string and dirt from cacao beans. They operate silos as to move beans from there to hoppers. They direct the cleaned beans to specified silos. They operate air-cleaning system in order to remove further foreign materials.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/555235ce-8c45-409b-a6c9-83e69c2d2bce','key_16200','','','0310','0310.5','military engineer',replace('defence technology engineering adviser\nballistics engineer\ndefence technology engineering consultant\narmy engineer\ndefence engineering expert\ndefence technology engineer\nweaponary developer\narmy engineer officer\ndefence engineering consultant\ncombat technician\nmilitary engineer\ndefense engineer\nfield engineer\ndefence technology engineering specialist\ndefence engineering adviser\ndefence engineering specialist\ncombat engineer\ndefence technology engineering expert\ndefence technician','\n',char(10)),'Military engineers perform technical and scientific functions in the military, such as the development of concepts for military technical equipment, support of the manufacturing of military equipment, and technical research, maintenance, and quality assurance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/555ad9ba-72cf-4bc0-9ea6-c36041462be5','key_16201','','','4323','4323.8','gas transmission system operator',replace('gas transmission system operative\nnatural gas transmission system operative\ngas distribution system operator\ngas distribution system worker\ngas distribution system operative\ngas transmission system operator','\n',char(10)),'Gas transmission system operators transport energy in the form of natural gas. They receive natural gas from the production station, transport it through pipelines, ensuring delivery to gas distribution stations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/557f9ccc-4e46-4bb2-8d20-272cd978b152','key_16202','','','2250','2250.9','veterinary scientist','veterinary researcher','Veterinary scientist develop and do research in animal models, compare basic biology across animals, and translate research findings to different species, including humans.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5589e8e0-48d9-4193-8c85-86ba9f17e0e9','key_16203','','','7531','7531.2','dressmaker',replace('tailor\nseamstress\ncustom dressmaker\nseamster\ndressmaker\nbespoke dressmaker\ncouture dressmaker','\n',char(10)),'Dressmakers design, make or fit, alter, repair tailored, bespoke or hand-made garments from textile fabrics, light leather, fur and other material for women and children. They produce made-to-measure wearing apparel according to customer''s or garment manufacturer''s specifications. They are able to read and understand size charts, details surrounding finished measurements, etc.','','Excludes <a resource="http://data.europa.eu/esco/occupation/978ae3a2-0831-415a-8396-b84dde656fc5" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/978ae3a2-0831-415a-8396-b84dde656fc5"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">tailor</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/55a785db-0636-41b8-950f-0b65abe5e81b','key_16204','','','2310','2310.1.31','pharmacy lecturer',replace('university pharmacology lecturer\nlecturer of pharmacy\nlecturer in pharmaceutics lecturer\nlecturer in pharmaceutical sciences\nlecturer in pharmacy\npharmacy docent\nuniversity pharmacy lecturer\npharmaceutics lecturer instructor\npharmaceutics lecturer professor\nuniversity lecturer in pharmacy\nuniversity pharmaceutical sciences teacher\npharmacy lecturer\nprofessor of pharmacology\npharmacy teacher\nuniversity lecturer in pharmacology\nuniversity pharmaceutical sciences lecturer\npharmaceutics lecturer lector\nhigher education pharmaceutical sciences teacher\npharmacy instructor\npharmaceutics lecturer teacher\nprofessor of pharmaceutical sciences\nlecturer in pharmacology\npharmacy lector\npharmaceutics lecturer docent\nuniversity lecturer in pharmaceutical sciences\npharmacy professor\nprofessor of pharmacy\npharmaceutics lecturer\nhigher education pharmacy teacher\nuniversity pharmacy teacher','\n',char(10)),'Pharmacy lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, pharmacy, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for leading laboratory practices, grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their field of pharmacy, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/55af6be8-039a-419c-b278-55b3f7d9ef92','key_16205','','','3423','3423.1.2.2','outdoor activities instructor',replace('outdoor pursuits group leader\noutdoor activities instructor\ninstructor in outdoor activities\noutdoor activities instruction practitioner\npractitioner of instruction in outdoor activities\ninstructor in outdoor pursuits\noutdoor pursuits instructor','\n',char(10)),'Outdoor activities instructors organise and lead recreational outdoor trips through which the participants learn skills such as hiking, climbing, skiing, snowboarding, canoeing, rafting, rope course climbing etc. They also provide team-building exercises and activity workshops for disadvantaged participants. They ensure the safety of the participants and the equipment and explain safety measures for the participants to understand themselves as well. Outdoor activities instructors should be prepared to deal with the consequences of bad weather conditions, accidents and should responsibly manage possible anxiety from participants concerning certain activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/55b4faa3-286f-4fc8-ac67-8bbb97d08820','key_16206','','','3343','3343.1.2','education administrator',replace('university administrator\nspecialist administrator in education\nhigher education administrator\nschool secretary\ncollege administrator\neducation administration practitioner\nadministrator in education\nfurther education administrator\neducation secretary\nschool receptionist\neducation administrator\nspecialist education administrator\nschool administrator\nschool executive assistant','\n',char(10)),'Education administrators organise and manage the administration, support systems and (student) activities of an education institution. They perform a range of administrative, secretarial, financial and otherwise supportive tasks to enable the efficient and cost-effective running of the school. They may assist in student recruitment, alumni relations, funding, work on committees including academic boards and quality insurance.','','Includes <a resource="http://data.europa.eu/esco/occupation/2a04a1df-a3b9-403e-a245-e7f93fdddd44" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/2a04a1df-a3b9-403e-a245-e7f93fdddd44"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">executive secretary</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/55d2eb3e-1e49-404a-b4bf-be4829694a59','key_16207','','','2643','2643.4.1','lawyer linguist',replace('legal linguistics expert\nlawyer linguist\nlegal linguist\nlegal linguistics specialist\nfreelance legal translator\nlegal linguistic expert\nlinguistic lawyer\nlegal linguistic specialist\nlinguist lawyer\nlinguistics lawyer\nlegal interpreter\nlegal translator\njurilinguist','\n',char(10)),'Lawyer linguists interpret and translate legal pieces from one language to another. They provide legal analysis and help in understanding technicalities of the content expressed in other languages.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/55d60ead-0343-4008-b9e4-fb4b5a179c83','key_16208','','','2342','2342.1','early years teacher',replace('preprimary school teacher\nkindergarten teacher\nreception teacher\npreschool teacher\nnursery teacher','\n',char(10)),'Early years teachers instruct students, primarily young children, in basic subjects and creative play with the aim of developing their social and intellectual skills in an informal way in preparation for future formal learning. They create lesson plans, possibly in accordance with a fixed curriculum, for an entire class or smaller groups and test the students on the content. These lesson plans, based on basic subjects, can include the instruction of number, letter, and colour recognition, days of the week, categorisation of animals and transport vehicles etc. Early years teachers also supervise students outside the classroom on school grounds and enforce rules of behaviour there as well.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/55f001af-fa48-4c0c-a7e5-fc69babe3d45','key_16209','','','2131','2131.4.6','botanist',replace('garden intendant\nbotanist\nhead botanist\nhorticulture manager\nbotany manager\ndirector of horticulture\ngarden supervisor\ndirector of horticulture learning and operations\ndirector of science\ngarden overseer\nhead gardener\ngarden manager','\n',char(10)),'Botanists are occupied with the maintenance of a range of plants from around the world, often in a botanic garden. They conduct scientific studies and travel in order to study plants growing in the wild. Botanists are responsible for the maintenance and development of the botanic garden.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/55f6380c-7511-4c33-8fc4-c74f10f524fa','key_16210','','','5141','5141.1.2','hairdresser assistant',replace('hair dryer\nassistant of hairdressers\nhair salon attendant\nhair salon assistant\nhairdresser assistant\nshampooer\nhair stylist assistant','\n',char(10)),'Hairdresser assistants clean the hair of clients, apply hair conditioner and dying treatment in a beauty salon. They apply shampoo, rub scalp and rinse the hair. They may also do scalp treatment, bleaching, tinting, and massaging for their clients. Hairdresser assistants use specialized lotions, shampoos, conditioners, and other hair care equipment, according to their client''s needs and preferences.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/56245067-16f0-43e7-84fa-4a1e794a6ac0','key_16211','','','5249','5249.2.1.5','rental service representative in office machinery and equipment',replace('rental sales worker in office machinery and equipment\nrental sales supervisor in office machinery and equipment\nrental sales desk agent in office machinery and equipment\nrental advisor in office machinery and equipment\nrental service supervisor in office machinery and equipment\nrental service representative in office machinery and equipment\nrental service assistant in office machinery and equipment\nrental sales desk supervisor in office machinery and equipment\nrental specialist in office machinery and equipment\nrental sales team leader in office machinery and equipment\nrental service team leader in office machinery and equipment\nrental sales manager in office machinery and equipment\nrental service manager in office machinery and equipment\nrental service worker in office machinery and equipment\nrental service desk clerk in office machinery and equipment\nrental sales desk clerk in office machinery and equipment\nrental sales assistant in office machinery and equipment\nrental service desk supervisor in office machinery and equipment\nrental service desk agent in office machinery and equipment','\n',char(10)),'Rental service representatives in office machinery and equipment are in charge of renting out equipment and determining specific periods of usage. They document transactions, insurances and payments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/562dd713-5745-4457-929e-5b909da084ca','key_16212','','','4323','4323.10','pipeline route manager',replace('pipeline division supervisor\npipeline division manager\npipeline route manager\npipeline operations supervisor\npipeline operations manager\npipeline route supervisor','\n',char(10)),'Pipeline route managers oversee the day-to-day operational aspects of transporting goods via pipeline infrastructures. They have an overview of the network and plan the different routes to transport goods from one site to another. They strive for the most efficient and less costly route. They troubleshoot arising issues in networks and sites, ensure implementation of regulations, and follow up the achievement of transport targets.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/565987b2-b980-4881-891c-cc4d5ab61d07','key_16213','','','7113','7113.1.1','stone engraver',replace('stone carver\nstone hand engraver\nengraver of stone\nstone engraver\nlaser engraver\nstone etcher\nhand carver\ndecorative stonemason\nbespoke stone engraver','\n',char(10)),'Stone engravers use hand tools, machines and chemical products to etch and carve patterns and inscriptions on stone surfaces.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5684182e-7b4c-4eea-97d9-4c2b88b9f906','key_16214','','','8350','8350.7','sailor',replace('mariner\ncrewmember\nsailor','\n',char(10)),'Sailors assist the ship captain and any crew higher in hierarchy to operate ships. They dust and wax furniture and polish wood trim, sweep floors and decks, and polish brass and other metal parts. They inspect, repair, and maintain sails and rigging, and paint or varnish surfaces. They make emergency repairs to the auxiliary engine. Sailors may stow supplies and equipment and record data in log, such as weather conditions and distance travelled. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/56a535ff-189b-45da-97d4-775f78744f83','key_16215','','','5223','5223.7.15','eyewear and optical equipment specialised seller',replace('contact lens and glasses salesperson\noptical equipment salesperson\neyewear specialised seller\nopticians salesperson\noptical equipment specialised seller\noptometrists salesperson\neyewear and optical equipment specialized seller\noptometrists specialised seller\nspectacle specialised seller\ncontact lens and glasses specialised seller\nopticians specialised seller\neyewear salesperson\nspectacle salesperson','\n',char(10)),'Eyewear and optical equipment specialised sellers sell glasses in specialised shops.','','Excludes <a resource="http://data.europa.eu/esco/occupation/1084e2d6-b153-4090-8065-c9231f71d4f0" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/1084e2d6-b153-4090-8065-c9231f71d4f0"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">optical technician</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/56afc09a-75a0-49c7-a5cb-256df45d8f2d','key_16216','','','2631','2631.2.2','economic adviser',replace('economics adviser\neconomic policy researcher\npolicy analyst\neconomic adviser\neconomics consultant\neconomic policy advisor\nfinancial and economic policy analyst\neconomic development officer','\n',char(10)),'Economic advisers research economic developments and advise on economic problems. They forecast trends and behaviour in economics, and advise on finance, trade, fiscal and other related matters. They advise companies and organisations on techniques to gain economic profit.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/56bff5e1-3c76-4f45-b50e-e3a59bf1053e','key_16217','','','2654','2654.2','post-production supervisor',replace('post-production technical coordinator\naudio-video post-production supervisor\npost-production editing supervisor\npost-production team leader\npost-production supervisor\npost-production workflow supervisor\nhead of post-production\npost-production manager\npost production supervisor\npost-production coordinator','\n',char(10)),'Post-production supervisors oversee the whole post-production process. They work together with the music editor and video and motion picture editor. Post production supervisors help plan the production workflow to make sure the post production phase is included and budgeted for. They ensure that the final product is delivered and distributed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/56fa783a-cca4-42ef-8862-1fb6e43f215c','key_16218','','','3331','3331.3','international forwarding operations coordinator',replace('import-export manager\nimport-export officer\ninternational forwarding operations director\nimport-export clerk\nimport-export coordinator\ninternational forwarding operations coordinator\ninternational logistics director\ninternational logistics coordinator\ninternational logistics manager\ninternational forwarding operations manager','\n',char(10)),'International forwarding operations coordinators implement and monitor international forwarding operations by resolving problems and taking decisions related to transport and support activities. They deal with administrative burdens associated with international operations such as regulations in different national contexts for imports and exports. They provide business support, project coordination, evaluation and management of current systems and international supply chain management and procedures as required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/570d7e74-5c65-4a9a-95ba-f8c27025c5f4','key_16219','','','2330','2330.1.16','physics teacher secondary school',replace('physics tutor in high school\nphysics teacher in high school\nhigh school physics teacher\nhigh school physics tutor\nphysics tutor in secondary school\nteacher of physics in secondary school\nphysics teacher high school\nsecondary school physics tutor\nteacher of physics in high school\nphysics teacher\nsecondary school physics teacher','\n',char(10)),'Physics teachers at secondary schools provide education to students, commonly children and young adults, in a secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, physics. They prepare lesson plans and materials, monitor the students'' progress, assist individually when necessary, and evaluate the students'' knowledge and performance on the subject of physics through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5727b567-993d-4d41-a13d-81a6af1a9a8e','key_16220','','','2635','2635.1','social counsellor',replace('rehabilitation support counsellor\ncrisis intervention counsellor\ngerontology counsellor\ncrisis intervension counsellor\nsocial psychologist\ncrisis situation counselor\npersonal counsellor\ncrisis counsellor\nsocial therapist\ncrisis situation counsellor\nyouth counsellor\ncrisis counselor\nyouth counseller\nrelationship advisor\nsocial counselling worker\nsocial counselor','\n',char(10)),'Social counsellors provide support and guidance to individuals in the social work area, to help them solve specific problems in their personal life. It involves addressing personal and relationship issues, dealing with inner conflicts, crisis moments such as depression and addiction, in an attempt to empower individuals to achieve change and improve their life quality.','','Excludes people performing counselling services in the medical therapeutic sense.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/572c51ef-531b-4331-8e66-b1641173e54e','key_16221','','','2641','2641.2','dramaturge',replace('playwright & dramaturge\nmusic theater dramaturge\nproduction dramaturge\ndramatic advisor\nplaywright\nmusic theatre dramaturge\ndramaturge & playwright\ndramaturg\ndramaturgist','\n',char(10)),'Dramaturges read new plays and works and propose them to the stage director and/or art council of a theatre. They gather documentation on the work, author, problems addressed, times and described environments. They also participate in the analysis of themes, characters, dramatic construction, etc.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/572c9810-e1ee-4f7a-a71f-820f17c0c25b','key_16222','','','7213','7213.4','sheet metal worker',replace('duct fabricator\nfabricator/welder\nroofing fabricator\nsheet-metal worker\nsheet metal fabricator\nsheet metal worker\nconstruction fabricator','\n',char(10)),'Sheet metal workers in construction use sheet metal to construct roofs, ducts for heating, ventilation and air-conditioning, gutters and other metal structures. They read plans and determine the type and amount of materials to be used, then measure, bend, cut, shape, and attach pieces of sheet metal to create the required structure.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5734feab-60be-4f11-8e3f-e983ca6fb07a','key_16223','','','6121','6121.2','equine yard manager',replace('riding school manager\nequine stable manager\nstable manager\nhorse stable manager\nequine yard manager\nhorse yard manager','\n',char(10)),'Equine yard managers are responsible for the day to day running of the yard including managing staff, care of the horses, all aspects of health and safety and dealing with clients and owners.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/574f3232-1d07-4034-8cac-c57ddbfff192','key_16224','','','2113','2113.1.1','analytical chemist',replace('analytical chemistry analyst\nchemistry scholar\nanalytical chemistry research analyst\nanalytical chemistry scientist\nchemistry scientist\nchemistry science researcher\nchemistry research analyst\nchemist\nchrystalographer\nanalytical chemistry research scientist\nchemistry research scientist\nanalytical chemistry scholar\nchemistry researcher\nchemistry analyst\nanalytic chemist\nanalytical chemist\nanalytical chemistry science researcher\nanalytical chemistry researcher','\n',char(10)),'Analytical chemists research and describe the chemical composition of substances. Moreover, they draw conclusions related to the behaviour of such substances in different conditions. Analytical chemists play an important role in looking at the relationship between chemistry and the environment, food, fuel, and medicine. They use a range of techniques such as electro-chromatography, gas and high performance liquid chromatography and spectroscopy.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/574f46e2-e48d-4cde-a7b1-877004ea4c52','key_16225','','','1420','1420.4.26','hardware and paint shop manager',replace('paint and accessories shop manager\nhardware store manager\nhardware and paint do it yourself store manager\nhardware and paint DIY shop manager\nhardware and paint DIY store manager\nhardware and paint shop manager\nglass and mirror shop manager\nhardware, paint and glass shop manager\nhome decorating shop manager\nhardware and paint do it yourself shop manager\nhome decorating store manager\nhardware, paint and glass store manager\nhardware and paint DIY superstore manager\npaint and accessories store manager\nhardware superstore manager\nhardware shop manager\nglass and mirror store manager','\n',char(10)),'Hardware and paint shop managers assume responsibility for activities and staff in specialised shops. They manage employees, monitor the sales of the store, manage budgets, order supplies when a product is out of supply and perform administrative duties if required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5779aa3b-b32f-42f7-9581-ef5ce2092621','key_16226','','','1420','1420.4.29','meat and meat products shop manager',replace('senior retail butcher\nshop manager butchery\nmeat shop manager\nbutchery store manager\npurveyor of meat and meat products\nmeat products shop manager\nmeat market shop manager\nmeat purveyor\nretail butcher\nmeat and meat products shop manager\npie shop manager\nbutchers shop manager\nbutchery department manager\nmeat counter manager\nmeat store manager\nmeat and meat products store manager\nmeat and meat products purveyor\nmeat pie shop manager\nshop manager butchery department\npurveyor of meats\nmeat trader','\n',char(10)),'Meat and meat products shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/578470d7-1848-44cd-a769-96cdd64b2c37','key_16227','','','3342','3342.1','civil registrar',replace('civil registrar\nsenior registrar\nregistrar of births, deaths, marriages and civil partnerships\ncivil registration officer\nregistrar of births, marriage, civil partnership and deaths\ncivil status officer','\n',char(10)),'Civil registrars collect and record acts of birth, marriage, civil partnership and death.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5791a961-4717-4230-a245-a4082a675f04','key_16228','','','3119','3119.2','automation engineering technician',replace('technician in automated systems\ntechnologist in automated systems\nautomation inspector\nautomation engineering assistant\ntechnician in automation engineering\nmarine sensor technician\nassistant in automation engineering\nvessel automation technician\nautomated systems technician\nmarine automation technician\nmarine automation engineer\nautomation engineering controller\nautomation technician\nautomation control inspector\nautomation engineering technician','\n',char(10)),'Automation engineering technicians collaborate with automation engineers in the development of applications and systems for the automation of the production process. Automation engineering technicians build, test, monitor, and maintain the computer-controlled systems used in automated production systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/579254cf-6d69-4889-9000-9c79dc568644','key_16229','','','2144','2144.1','mechanical engineer',replace('mechanical engineering expert\nmechanics engineer\nmechanical technology engineering consultant\nmechanical engineering consultant\nmechanical technology engineering expert\nmechanical engineer\nmechanical technology engineering adviser\nmechanical engineering adviser\nmechanical technology engineering specialist\nmachinery engineer\nmechanical engineering specialist\nmechanical technology engineer','\n',char(10)),'Mechanical engineers research, plan and design mechanical products and systems and supervise the fabrication, operation, application, installation and repair of systems and products. They research and analyse data.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5793c124-c037-47b2-85b6-dd4a705968dc','key_16230','','','5412','5412.1','police officer',replace('state police officer\npolicewoman\nlaw enforcement officer\npolice constable\npublic roads surveillance officer\nconstable\nmounted police officer\npoliceman\nrailroad police officer\npolice officer\nhighway patrol officer\nrailway police officer\npatrol officer\nstate trooper','\n',char(10)),'Police officers use investigation methods to prevent crime, to pursue and apprehend criminals, and protect the public from violent and criminal activities. They perform surveillance and patrol activities to prevent crimes and apprehend offenders. They provide support to victimised parties and the public at large, and fulfil administrative duties.','','Includes state <a resource="http://data.europa.eu/esco/occupation/5793c124-c037-47b2-85b6-dd4a705968dc" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/5793c124-c037-47b2-85b6-dd4a705968dc"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">police officer</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/57a09d3f-e3d6-49ad-8c88-d879fc4d0870','key_16231','','','8332','8332.3','concrete pump operator',replace('mobile pump operator\nvolumetric mixer driver\nconcrete mixer driver\nconcrete pump operator\nconcrete pump operative\nLGV mixer driver\nHGV mixer driver\nconcrete pump truck operator\nconcrete mixer truck driver\nconcrete pump driver-operator\ncement mixer driver','\n',char(10)),'Concrete pump operators drive and operate trucks to haul concrete from the plant to project sites, and operate pumps to disperse concrete at the site. They also clean and maintain the truck and its mechanical parts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/57a12047-4f1e-40ed-add5-9736923f231b','key_16232','','','2111','2111.1','astronomer',replace('astronomy scientist\nastronomy analyst\nastrophysicist\nastronomy scholar\nastronomer\nastronomy science researcher\nastronomy research analyst\nastronomy research scientist\nastronomers\nastronomy researcher','\n',char(10)),'Astronomers research the formation, structures, properties, and development of celestial bodies and interstellar matter. They use ground-based equipment and space-based equipment to collect data about the space for research purposes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/57a73b7c-cf10-4852-b067-c25833fa149e','key_16233','','','3211','3211.1','clinical perfusion scientist',replace('medical perfusionist\nmedical perfusion scientist\nclinical perfusion scientist\nclinical perfusion techinician\nclinical perfusion expert\nmedical perfusion techinician\nmedical perfusion expert\nclinical perfusionist','\n',char(10)),'Clinical perfusion scientists operate heart-lung equipment during surgical operations to ensure breathing and blood circulation. They work as part of the surgical team, connect patients to the heart-lung machines in preparation for surgery, monitor their condition during surgery, report to the team on the patients'' status and determine the necessary techniques according to their needs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/57ac359a-ae52-4aea-bc9c-93af7efa502e','key_16234','','','2142','2142.1.7','pipeline engineer',replace('main line station technician\npipeline engineer\noffshore pipeline engineer\nmain line station engineer\npipeline integrity engineer\npipeline field engineer\npipeline field technician\npipeline engineering technician\nsubsea pipeline engineer','\n',char(10)),'Pipeline engineers design and develop the engineering aspects for the construction of pipeline infrastructures in various kinds of sites (e.g. inland, maritime). They envision and create the specifications for pumping systems and for the general transport of goods through pipelines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/57af9090-55b4-4911-b2d0-86db01c00b02','key_16235','','','2514','2514.2.1','embedded systems software developer',replace('embedded system software programmer\nembedded software developerl embedded systems engineer\nembedded software programmer\nembedded system software developer\nembedded systems software developer\nembedded systems designer\nembedded systems software developers','\n',char(10)),'Embedded systems software developers program, implement, document and maintain software to be run on an embedded system.','','Excludes people performing managerial activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/57b459d4-655c-4053-b189-3c249ce33faf','key_16236','','','2434','2434.2','ICT business development manager',replace('IT commercial development manager\nIT business development manager\nICT commercial development manager\nICT business development manager\nICT business development managers','\n',char(10)),'ICT business development managers increase business opportunities for the organisation and develop strategies that will enhance the smooth running of the organisation, product development and product distribution. They negotiate prices and establish contract terms.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/57b89801-0e43-4b19-bf29-ef9a51d65155','key_16237','','','8121','8121.3','metal annealer',replace('metal annealing worker\nannealing kiln worker\nannealing kiln operator\nmetal annealer\nmetal annealing process worker\nheat treatment operator\nmetal annealing operative\nmetal annealing process operative\nannealing operator','\n',char(10)),'Metal annealers operate electric or gas kilns to soften metal so it can be cut and shaped more easily. They heat the metal to a specific temperature and/or colour and then slowly cool it, both according to specifications. Metal annealers inspect the metals through the entire process to observe any flaws.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/57d1be2e-c476-43fb-b014-fa58602cac95','key_16238','','','3123','3123.1.9','dismantling supervisor',replace('disassembly supervisor\ndismantling supervisor\ndisassembly team leader\ndismantling team leader','\n',char(10)),'Dismantling supervisors monitor the operations involved in dismantling activities such as removing and possibly recycling industrial equipment and machinery or decommissioning of plants. The distribute the task among workers and supervise if everything is done according to safety regulations. If problems arise they consult with engineers and take quick decisions to resolve problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/57e87533-e523-4ee5-bf21-94a39fcc279b','key_16239','','','1213','1213.7','health safety and environmental manager ',replace('corporate HSE manager \nHSE and sustainability service manager\nHSE coordinator\nchief HSE officer','\n',char(10)),'Health, safety and environmental managers design and execute corporate policies and procedures related to occupational health and safety and environmental protection. They analyse business processes to ensure compliance with government and environment legislation, conduct risk assessment in the domain of occupational health and safety, evaluate the environmental impact of economic activities and design the appropriate measures for the improvement of work environments and cultures. They coordinate the implementation of an integrated health, safety and environmental management system, defining effective indicators, organizing audits and eventually participating in accident investigation and reporting. They promote an integrated approach to sustainability and occupational health within business organisations, liaising with corporate and line managers and training employees. They are responsible for drafting technical documentation related to the compliance with health and safety and environmental legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/580c98d4-fb97-4597-864c-54140f058f1b','key_16240','','','3341','3341.2','call centre quality auditor',replace('call center quality auditor\ncall centre quality analyst\ncall centre quality controller\ncontact centre quality auditor\ncustomer service centre analyst\ncall centre quality assurance auditor\ncall centre quality control auditor\ncall centre quality audit associate','\n',char(10)),'Call centre quality auditors listen to calls from the call centre operators, recorded or live, in order to assess compliance with protocols and quality parameters. They grade the employees and provide feedback on the issues that require improvement. They interpret and spread quality parameters received by the management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/586fe133-1987-41f4-b89a-f39d4bc58e1e','key_16241','','','2431','2431.10.2','eBusiness manager',replace('eBusiness development manager\ne-business managers\neBusiness manager\neCommerce manager\neBusiness managers\ne-commerce manager\nweb business development manager\neBusiness development officer\ne-commerce development manager\ne-business manager','\n',char(10)),'eBusiness managers create and execute a company''s electronic strategy plan for selling products and services online. They also improve data integrity, placement of online tools and brand exposure and monitor sales for companies that market products to customers using the internet. They collaborate with the marketing and sales management team using ICT tools to reach sales goals and provide accurate information and offerings to business partners.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/589efb6c-9080-40b2-ab6a-144955675967','key_16242','','','5153','5153.1.3','verger',replace('church verger\nsexton\nparish verger\nofficer of the church\nchurch officer\nchurch warden\nverger','\n',char(10)),'Vergers perform administrative duties for churches and parishes, ensure equipment maintenance and support the parish priest or other superiors. They also perform assisting duties before and after church service such as tidying, readying the equipment and supporting the priest.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/58c30193-d29f-4d22-8bf0-fd87346b91dd','key_16243','','','2149','2149.2.5','installation engineer',replace('design engineer\ninstallation engineer\nfield installation engineer\nstructural installation engineer\nstructural engineer\ncommissioning engineer\nbuilding engineer','\n',char(10)),'Installation engineers oversee and manage the installing of structures, which take often many years to design and construct. They ensure safety, avoid risks and they aim to the optimalisation of costs. Installation engineers also create constructive designs of systems and perform installation system testing. They determine the material needed for the construction of these systems and the costs, and use CAD software to design these systems.    ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/58db35e3-fe1f-44d2-8a9e-ab652fc1cec2','key_16244','','','3324','3324.4.22','wholesale merchant in meat and meat products',replace('business to business seller in meat and meat products\nwholesale merchant in meat and meat products\ntrainee wholesale merchant in meat and meat products\ngraduate wholesale merchant in meat and meat products\nwholesale merchandiser in meat and meat products\nB2B sales consultant in meat and meat products\nwholesaler in meat and meat products\nsenior wholesale merchant in meat and meat products\nbusiness to business sales consultant in meat and meat products\nwholesale buyer in meat and meat products\nassistant wholesale merchant in meat and meat products\nwholesale trader in meat and meat products\ntrader, wholesale in meat and meat products','\n',char(10)),'Wholesale merchants in meat and meat products investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/58db3ac6-5217-4d46-8a4c-126598be1d13','key_16245','','','2151','2151.1.1','electric power generation engineer',replace('electric power generation engineer\npower station engineer\nelectricity generation engineer\npower plant project engineer\npower supply engineer\nelectric power station engineer\npower generation networks engineer\npower plant engineer\nelectric power plant engineer\nelectric power plant project engineer\nquality and electric power engineer\nproduction and electric power engineer\nelectric power supply engineer\nenergy generation engineer\ncontrol and electric power engineer\npower generation systems engineer','\n',char(10)),'Electric power generation engineers design and develop systems which generate electrical power, and develop strategies for the improvement of existing electricity generation systems. They strive to conciliate sustainable solutions with efficient and affordable solutions. They engage in projects where supply of electrical energy is required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/58f3f9fc-c9f6-4e14-bedf-a71ace5f27c5','key_16246','','','3422','3422.1','artistic coach',replace('actor\nballet coach\ndancer\nacting coach\nballet teacher\nactress\ndance teacher\nartistic coach\ndance coach','\n',char(10)),replace('Artistic coaches research, plan, organise and lead arts activities for sport practitioners in order to provide them with artistic abilities such as dance, acting, expression and transmission that are important for their sport performance. Artistic coaches make technical, performance or artistic abilities accessible to sports practitioners with the goal of improving their sport performance. \n \n','\n',char(10)),'',replace('Includes <a resource="http://data.europa.eu/esco/occupation/07b91f36-8272-493a-8e1c-5625e77efe68" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/07b91f36-8272-493a-8e1c-5625e77efe68"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">dancer</span></a>., <a resource="http://data.europa.eu/esco/occupation/1eb56b85-af25-43d4-9e35-73d8ee78e228" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/1eb56b85-af25-43d4-9e35-73d8ee78e228"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">dance teacher</span></a>., <a resource="http://data.europa.eu/esco/occupation/189a337d-f6fb-44e2-9e7a-bc27b7b76e0e" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/189a337d-f6fb-44e2-9e7a-bc27b7b76e0e"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">choreographer</span></a>., <a resource="http://data.europa.eu/esco/occupation/26171f39-e85a-448f-bd28-a73a5a99927f" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/26171f39-e85a-448f-bd28-a73a5a99927f"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">actor/actress</span></a>., scene directors, etc. \n Includes people performing rhythmic gymnastics, artistic gymnastics, artistic skaters and synchronised swimmers. Includes people working in diverse contexts.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/58ff0ec2-08ba-4b21-b765-babb6d76ca36','key_16247','','','5111','5111.2','steward/stewardess',replace('steward\nstewardess\nsteward/stewardess\nflight attendant\ncabin crew','\n',char(10)),'Stewards/stewardesses perform food and beverage service activities on all land, sea and air travel services.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/59088d93-bd71-4fd7-a39e-8cc9dd37dfae','key_16248','','','7522','7522.1','antique furniture reproducer',replace('antique furniture carver\nreproduction antique furniture copier\nfurniture reproducer\nreproduction period furniture craftswoman\nreproduction furniture worker\nreproduction furniture craftsman\nreproduction period furniture craftsperson\nantique furniture copier\nreproduction antique furniture craftswoman\nreproduction furniture maker\nreproduction period furniture craftsman\nperiod furniture maker\nperiod furniture reproducer\nantique furniture maker\nreproduction furniture craftsperson\nreproduction furniture craftswoman\nreproduction antique furniture craftsman\nreproduction antique furniture worker\nreproduction antique furniture maker\nreproduction antique furniture craftsperson\nperiod furniture copier\nperiod furniture carver\nreproduction antique furniture carver\nreproduction period furniture maker\nantique furniture reproducer\nreproduction period furniture worker','\n',char(10)),'Antique furniture reproducers duplicate and recreate antique furniture. They prepare drawings and templates of the article, create, fit and assemble parts and finish the article after original specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/591c5063-69ce-4551-ba60-e2304111130f','key_16249','','','3513','3513.1','aviation data communications manager',replace('aviation data communications contractor\naviation data communications  specialist\naviation data communications manager\ndata communications manager in aviation\naviation data communications officer\naviation data communications consultant\naviation sector data manager','\n',char(10)),'Aviation data communications managers perform the planning, implementation and maintenance of data transmission networks. They support data processing systems linking participant user agencies to central computers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/591d1758-3036-407a-91f7-e0e1f5c9deea','key_16250','','','2320','2320.1.11','electronics and automation vocational teacher',replace('electronics and automation educator\nelectronics and automation vocational instructor\nelectronics and automation vocational teacher\nelectronics and automation teacher\nvocational teacher in electronics and automation\nvocational educator in electronics and automation\nelectronics and automation instructor\nelectronics and automation trainer','\n',char(10)),'Electronics and automation vocational teachers instruct students in their specialised field of study, electronics and automation, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for an electronics and automation-related profession, such as electrician or technicians involved in automating production processes. Electronics and automation vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of electronics and automation through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/59207c91-eefa-4f26-baae-9ea215320ed0','key_16251','','','8219','8219.3','firework assembler',replace('firework assembler\nfirework maker\nfireworks maker\npyrotechnics assembler\nfirework assembly worker\nsignal flare assembler\nfireworks assembler','\n',char(10)),'Firework assemblers create explosive devices, coloured lights and set pieces for use as fireworks. They follow blueprints or pictures, fabricate various powders, put powder into casings or tubes, assemble all parts and inspects the final product.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/594170bc-5663-40b0-97ef-c9f1ad51b2a0','key_16252','','','1346','1346.4','insurance agency manager',replace('insurance institution manager\ninsurance services branch manager\ninsurance company manager s\nhome insurance agency manager\ninsurance company manager\ninsurance agency manager\nlife insurance agency manager\ninsurance agency road assistance manager\nrental agency insurance manager\ninsurance agent\ncar insurance agency manager','\n',char(10)),'Insurance agency managers coordinate and oversee the operations of an institution or a branch of an institution that offers insurance services. They provide clients with advice on insurance products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5945cba8-19a4-4632-8a8f-4186e2bad1a3','key_16253','','','1312','1312.3','aquaculture production manager',replace('fishery production manager\naquaculture production manager\nfish farm production manager\nmanager of aquaculture production','\n',char(10)),replace('Aquaculture production managers plan, direct and coordinate the production of fish, shellfish or other forms of aquatic life such as cash crops, in large-scale aquaculture operations to culture and harvest or for release into fresh, brackish or salt water.  \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5979893a-0a30-447b-8f33-1ea405600583','key_16254','','','2144','2144.1.17','powertrain engineer',replace('powertrain expert\npowertrain designer','\n',char(10)),'Powertrain engineer work on the design of propulsion mechanisms across the automotive sector. This includes technical implementation of powertrain components, such as mechanical engineering, the electronics and software used in modern vehicles, as well as coordination and optimisation of multiple energy sources in the powertrain context.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/59883630-45f9-40e7-9732-9e5ff9758d2b','key_16255','','','2133','2133.6','environmental programme coordinator',replace('environmental health officer\nsustainability consultant\nenvironmental assessment coordinator\nprogram coordinator environment\nenvironmental auditor\nenvironmental impact assessor\nenvironmental officer\nenvironmental program coordinator\nenvironment compliance manager\nenvironmental manager\nenvironmental consultant\nenvironment program coordinator\nprogramme coordinator environment','\n',char(10)),'Environmental programme coordinators develop programmes for the improvement of environmental sustainability and efficiency within a organisation or institution. They inspect sites in order to monitor an organisation''s or institution''s compliance with environmental legislation. They also ensure education for the public on environmental concerns.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/599c2ce9-0e1b-4f63-9d65-1f19b3847efb','key_16256','','','7233','7233.5','forge equipment technician',replace('forging and casting inspector\nforging inspector\nmoulding and forging inspector\nforge casting inspector\ncasting and forging inspector\nforge processing inspector\ncasting and forging technical operator\nfoundry operations inspector\nforge equipment technician\nmoulding and casting inspector\nforge operations inspector\nfoundry processing inspector','\n',char(10)),'Forge equipment technicians maintain and repair forge machinery such as presses and material handling equipment. They perform evaluations of the equipment, perform preventative maintenance activities, and repair faults. They also assist in the installation of the equipment and ensure proper functionality.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/599f34f0-bdf7-49e3-b5a9-f5d55f9e6873','key_16257','','','8219','8219.9','plastic products assembler',replace('plastics process operative\nplastic products production worker\nplastic products cutter/assembler\nplastic parts assembler\nassembler of plastic products\nplastics process worker\nplastic products production operative\nassembler of plastic parts\nplastic products trimmer/assembler\nplastic products cutter\nplastics production worker\nplastics process operator\nplastics assembler\nplastic products assembly operator\nplastics products fabricator\nassembler plastic products\nplastic products finisher\nplastic products assembly operative\nplastic products assembler\nplastic products trimmer\nplastic products assembly worker','\n',char(10)),'Plastic products assemblers fit and fasten parts and finished plastic products according to strictly-defined procedures. Plastic products assemblers may also cut and shape plastic parts using hand, power, and machine tools.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/59a02420-91df-4e46-b37b-22e3fd8c11f0','key_16258','','','2310','2310.1.33','physics lecturer',replace('university physics teacher\nsenior lecturer in physics\nlecturer of physics\nspecialist lecturer in physics\nlecturer in physics\nuniversity lecturer in physics\nphysics docent\nhigher education physics teacher\nphysics educator\nphysics instructor\nuniversity physics lecturer\nprofessor of physics\nphysics lector\nphysics teacher\nphysics lecturer\nphysics professor','\n',char(10)),replace('Physics lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, physics, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants \nfor the preparation of lectures and of exams, for leading laboratory practices, grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their field of physics, publish their findings and liaise with&gt;other university colleagues.','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/59bbece0-d1dc-4f4a-845e-1523f68afb2d','key_16259','','','8151','8151.2','spinning machine operator',replace('textile machine operator, spinner\ntextile machine tender, spinner\nspinner\nyarn carding and spinning machine operator\nspinning machine tender\noperator of spinning machine\nspinning machine operator','\n',char(10)),'Spinning machine operators produce yarns, twists, and other fibres by tending spinning, twisting, winding, and reeling machines. They handle raw materials, prepare them for spinning processes, and use machinery for the purpose. They also perform routine maintenance of the machinery.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/59bce29f-5c8b-47cf-bfd4-6ec003f5323a','key_16260','','','2359','2359.14','tutor',replace('private tutor\ntutors\nclassroom tutor\nstudents assistant\ntutor','\n',char(10)),'Tutors provide personalised education to the children of employers or to adults, in addition to the main education system. They help students improve their knowledge and competence of a specific subject, at their own pace. Tutors will teach their students study techniques and strategies in order to ensure their academic development and will assess the student''s progress throughout the tutoring sessions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/59c22779-4d89-4bc9-ba8b-d64bea236723','key_16261','','','2411','2411.1.12','tax advisor',replace('fiscal advisor\ntax specialist\ntax adviser\ntax advisors\ncorporate tax preparer\ntax evaluator\ntax consultant\nchartered tax adviser\nlicensed tax consultant\ntax examiner\ntax expert\ntax advisor\ntax preparer','\n',char(10)),'Tax advisors use their expertise in tax legislation to provide commercially-focused advisory and consultancy services to a wide range of clients from all economic sectors. They explain complicated tax-related legislation to their clients and assist them in ensuring the most efficient and beneficial payment of taxes by devising tax-efficient strategies. They also inform them of fiscal changes and developments and may specialise in tax strategies concerning mergers or multinational reconstruction for business clients, trust and estate taxes for individual clients etc.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/59c724f9-0cfe-4d51-8a7f-9ee182d9e2c2','key_16262','','','2353','2353.2','sign language teacher',replace('sign language teacher\nbritish sign language tutor\nbritish sign language trainer\nassistant sign language teacher\nsign language tutor\nsign language educator\nsign language trainer\nsign language teaching assistant\nbritish sign language teacher','\n',char(10)),'Sign language school teachers educate non-age-specific students in sign language. They teach sign language to both students with or without special educational needs such as deafness. They organise their classes using a variety of lesson materials, work interactively with the group, and assess and evaluate their individual progress through assignments and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/59cc9783-7289-4e1d-b80b-93c1776f49cc','key_16263','','','7111','7111.1','house builder',replace('builder of houses\nbuilder\nhome builder\ndomestic property builder\nresidential property builder\nhouse builder','\n',char(10)),'House builders construct, maintain and repair houses or similar small buildings using a range of techniques and materials of several construction building workers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/59cfa27b-874f-48b7-8880-b65420239559','key_16264','','','3435','3435.20','pyrotechnician',replace('pyrotechnics specialist\nfireworks handler\npyrotechnist\nfireworks operator\nmaster pyrotechnician\npyrotechnician\nfireworks technician\npyrotechnics operator\nfireworks specialist','\n',char(10)),replace('Pyrotechnicians control the pyrotechnical elements of a performance based on the artistic or creative concept, in interaction with the performers. Their work is influenced by and influences the results of other operators. Therefore, the operators work closely together with the designers, operators and performers. Pyrotechnicians prepare the pyrotechnics, supervise the setup, steer the technical crew, program the equipment and operate the pyro system. The use of explosive and combustible material close to performers and audience makes this a high risk occupation. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/59dae100-fdfe-44bd-8bd8-a0d3cd813ce4','key_16265','','','3119','3119.7','footwear production technician',replace('industry forewoman specialised in shoemaking\nfootwear engineer\nindustrial forewoman\nfootwear technician\nindustry foreman\ngeneral forewoman\nfootwear production technician\nindustrial foreman\nindustry foreman specialized in shoemaking\ngeneral foreman\nshoe technician\nindustry foreman specialised in shoemaking\nindustry forewoman specialized in shoemaking\nindustry forewoman','\n',char(10)),'Footwear production technicians perform a wide range of activities regarding footwear manufacturing. They are involved in all phases of the process including product engineering and different types of construction. They aim at maximising productivity and reducing production costs, assuring the functionality and quality of product and customer satisfaction.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/59ee6b71-a48d-4940-bdf1-e806ca60245c','key_16266','','','8183','8183.3','cylinder filler',replace('industrial filling operative\ncylinder filler\ngas cylinder filler\ncylinder refiller\ncylinder operative\nliquefied gas filler\nindustrial filling plant operative\ntechnician/fill operative\ncompressed gas cylinder filler\nyardhand/filler\ntechnician/fill operator\ngas cylinder operative\nfilling plant operative\ncompressed gas filler\ngas filling plant operative\ncylinder refill operator\nyardhand filler\nfill operator','\n',char(10)),'Cylinder fillers operate and maintain the equipment and containers used to fill cylinders with gases in liquefied or compressed state.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5a0ff408-e1df-424b-a2d6-4b475a516f2a','key_16267','','','3435','3435.25.3','scenery technician',replace('scenic technician\nset tehnician\nscene technician\ntheatre scene assembler\ntheatre carpenter\ntheater carpenter\nassistant scenographer\ntheater scene assembler\nscenery technician\nscenery assembler','\n',char(10)),'Scenery technicians setup, prepare, check and maintain pre-assembled sets in order to provide optimal scenery quality for a live performance. They cooperate with road crew to unload, set up and move equipment and sets.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5a16dff6-ca31-4a39-85dd-4b5aa0f45419','key_16268','','','8312','8312.3','shunter',replace('switcher\nshunter attendant\nrail shunter\nshunting assistant\nshunting supervisor\nshunter assistant\nforeman shunter\nshunter driver\nshunting forewoman\nshunting foreman\noperator shunter\nforeman shunter/forewoman shunter\nforewoman shunter\nshunting attendant\nforeman/forewoman shunter','\n',char(10)),'Shunters move shunting units with or without wagons or groups of wagons in order to build trains. They manage the driving of locomotives and are involved in switching wagons, making or splitting up trains in shunting yards or sidings. They operate according to the technical features, such as controlling movement via a remote control device.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5a282a39-5346-454a-b1ef-c1217c50e156','key_16269','','','2351','2351.2','educational researcher',replace('educational science researcher\neducational research scientist\neducational analyst\neducation studies research analyst\neducational researcher\neducational scientist\neducation researcher\neducation methods specialist\neducational research analyst\neducation studies research scientist\npedagogue\neducation studies analyst\neducation studies scientist\neducation studies scholar\neducation studies researcher\neducational scholar','\n',char(10)),'Educational researchers perform research in the area of education. They strive to broaden the knowledge on how education processes, educational systems, and individuals (teachers and learners) work. They foresee areas of improvement and develop plans for the implementation of innovations. They advise legislators and policy makers on educational issues and assist in the planning of educational policies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5a2d0031-5dfd-46e8-8eb4-cf1d03876940','key_16270','','','3324','3324.4.9','wholesale merchant in dairy products and edible oils',replace('business to business seller in dairy products and edible oils\nbusiness to business sales consultant in dairy products and edible oils\ngraduate wholesale merchant in dairy products and edible oils\nwholesale merchandiser in dairy products and edible oils\nwholesale buyer in dairy products and edible oils\nassistant wholesale merchant in dairy products and edible oils\nwholesale trader in dairy products and edible oils\nsenior wholesale merchant in dairy products and edible oils\ntrainee wholesale merchant in dairy products and edible oils\nwholesaler in dairy products and edible oils\nB2B sales consultant in dairy products and edible oils\nwholesale merchant in dairy products and edible oils','\n',char(10)),'Wholesale merchants in dairy products and edible oils investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5a551d36-0e14-40ab-84a2-7aa9b37dc528','key_16271','','','7522','7522.6','furniture restorer',replace('historic furniture restorer\nfurniture renovater\nantique restorer\nfurniture repairer\ncraft furniture restorer\nfurniture restoration expert\nfurniture craftsperson\nfurniture upholsterer\nfurniture refinisher\nre-upholsterer\nfurniture restorer\nfurniture mender\nantique furniture restorer\nheritage furniture restorer','\n',char(10)),'Furniture restorers analyse materials and techniques in order to assess the state of an old piece of furniture and to identify and classify it according to art and cultural history. They use old or modern tools and techniques to restore the piece and give advice to customers as to the restoration, conservation and maintenance of such objects.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5a58da2b-2fa0-40d4-a9f5-fead48e7de88','key_16272','','','2144','2144.1.14','naval architect',replace('boat designer\nship designer\nmarine architect\nsubmarine architect\noutfitting engineer\nnaval engineer/architect\nmaritime architect\nyacht designer\nvessel designer\nnaval architect\nnaval vessel architect\nhull designer','\n',char(10)),'Naval architects design, build, maintain and repair all types of boats from pleasure crafts to naval vessels, including submarines. They analyse floating structures and take various features into account for their designs such as the form, structure, stability, resistance, access and propulsion of hulls.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5a596834-bc8f-4273-8d09-006957b60347','key_16273','','','7541','7541.1','construction commercial diver',replace('offshore diver\nconstruction commercial diver\nconstruction diver\nsurface supplied diver\ncommercial diver in construction industry\nunderwater welder\nsaturation diver\ncommercial diver\nscaphandrier\nHAZMAT diver\ninshore diver','\n',char(10)),'Construction commercial divers work below the surface of the water to install equipment, such as hydraulic structures, waterways and marine facilities. They also inspect, remove and repair the structures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5a80044b-9b2f-40eb-a210-0c1c3c958e0e','key_16274','','','3422','3422.2','lifeguard instructor',replace('lifeguard training instructor\npool lifeguard instructor\nlifeguard instructor\npool lifeguard trainer\nlifeguard training practitioner\nlifeguard trainer\ninstructor of professional lifeguards\ninstructor of lifeguards','\n',char(10)),'Lifeguard instructors teach future (professional) lifeguards the necessary programmes and methods needed to become a licensed lifeguard. They provide training on safety supervision of all swimmers, assessment of potentially hazardous situations, rescue-specific swimming and diving techniques, first aid treatment for swimming-related injuries, and they inform students on preventative lifeguard responsibilities. They ensure students are aware of the importance of checking safe water quality, heeding risk management and being aware of the necessary protocols and regulations regarding lifeguarding and rescuing. They monitor the students'' progress, evaluate them through theoretical and practical tests and award the lifeguard licenses when obtained.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5a8835bf-65c7-48e3-ab2f-7ad98b3fdb59','key_16275','','','3339','3339.3','intellectual property consultant',replace('IP consultant\nIP expert\npatent broker\nintellectual property expert\nintellectual property specialist\nintellectual property adviser\nIP adviser\npatent agent\ninformation broker\nintellectual properties consultant\npatent adviser\nIP broker\nIP specialist\nintellectual property broker\npatent consultant\npatent specialist\nintellectual property consultant\npatent expert','\n',char(10)),'Intellectual property consultants provide advice on the usage of intellectual property assests such as patents, copyrights, and trademarks. They help clients to value, in monetary terms, intellectual property portfolios, to follow adequate legal procedures for protecting of such property, and to perform patent brokerage activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5a91a8bc-eb18-4c7e-847d-96cbcce5a47a','key_16276','','','2641','2641.4.3','speechwriter',replace('speechwriter\nspeechwriting specialist\nministerial speechwriter\nspeechwriting consultant\nspeech writer\nexecutive speechwriter\npolitical speechwriter\nwriter of speeches','\n',char(10)),'Speechwriters research and write speeches on multiple topics. They need to catch and hold the interest of an audience. Speechwriters create presentations in a conversational tone so it looks like if the text was not scripted. They write in a comprehensible manner so the audience gets the message of the speech.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5a940ae0-3fa4-40f9-8ca4-17bf792da243','key_16277','','','5164','5164.1.1','animal groomer',replace('animal groomers\ndog groomer\ndog grooming specialist\nanimal groomer\nhorse groomer\ngroomer of animals\ncat groomer\nanimal grooming service provider\nanimal grooming specialist\npoultry groomer','\n',char(10)),replace(' Animal groomers are in charge of grooming a range of animals, using the correct equipment, materials and methods. It includes use of appropriate and safe handling techniques and the promotion of good hygiene, health and welfare of the animal.  \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5a9acd29-666c-4ca1-b10e-e086584a526b','key_16278','','','3412','3412.4.8','mental health support worker',replace('mental healthcare support worker\nmental health outreach worker\nmental health social care worker\nmental health recovery worker\nmental health support worker\nmental healthcare worker\nintensive support worker\nmental health community development worker\nsupport time and recovery worker\nre-ablement worker\nmental health community outreach worker','\n',char(10)),'Mental health support workers assist and provide treatment to people with mental, emotional, or substance abuse problems. They focus on personalised cases and monitor the recovery process of their clients, providing also therapy, crisis intervention, client advocacy and education.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5ab842f6-1f75-4d84-bf41-b8a9799347df','key_16279','','','7231','7231.2','coachbuilder',replace('coach builder\nvehicle-body builder\nvehicle panel beater\ncoachbuilder\nvehicle coachbuilder\nvintage vehicle coachbuilder\nspecialist coachbuilder\nbus-body builder\nautomotive panel beater\nautomotive coachbuilder\ncoach-body builder\ncommercial vehicle coachbuilder\nvintage vehicle-body builder\ncaravan-body builder\nvehicle builder\ncaravan-body coachbuilder\nheavy vehicle coachbuilder\nautomobile panel beater','\n',char(10)),'Coachbuilders execute work on vehicle bodies and coaches. They have skills to form body parts from panels, manufacture and assemble the frames and parts for vehicles.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5ac377d2-e1d0-4911-a299-b4ef2782a0ba','key_16280','','','4120','4120.1','secretary',replace('secretarial assistant\nexecutive secretary\nsecretary\nschool secretary\noffice receptionist\ncity administrator secretary\nassistant\ntypist\noffice worker\nprivate secretary\nadministrative assistant\npersonal assistant\ncorporate secretary\nfront office secretary\nhead secretary\noffice administrative assistant\nfront office clerk','\n',char(10)),replace('\nSecretaries perform a variety of administrative tasks to help keep an organisation run smoothly. They answer telephone calls, draft and send e-mails, maintain diaries, arrange appointments, take messages, file documents, organise and service meetings, and manage databases.','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5ac65daa-0896-4193-9f0e-8b00746b78a1','key_16281','','','2310','2310.1.41','university research assistant',replace('university research associate\nresearch associate in university\nassistant researcher in higher education\nuniversity assistant\nuniversity research technology expert\nuniversity research support worker\nuniversity research support\nhigher education research assistant\nuniversity research technology specialist\nuniversity research aide\nuniversity research assistant\nuniversity research technician\nuniversity research technologist\nassistant researcher in university\nresearch assistant in university\nresearch associate in higher education\nhigher education research associate\nhigher education assistant researcher','\n',char(10)),'University research assistants conduct academic research for the university or college they are employed in. They may assist professors they are affiliated with, their supervisor, in their research or develop their own in the related field of that professor.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5aeaea2f-6585-4d3d-8f3e-1fdd9903aa32','key_16282','','','6112','6112.2','fruit production team leader',replace('fruit production manager\nfruit production leader\nfruit production team leader\nfruit harvesting leader\norganic fruit farmer\nfruit harvesting team leader\nfruit production supervisor\nfruit picking team leader\nfruit production team supervisor\nstrawberry production team supervisor\napple production team supervisor\napple farmer\napple production manager\nfruit picking manager\nstrawberry farmer\nstrawberry production manager\nfruit farmer\nfruit production team manager\nfruit production team lead','\n',char(10)),'Fruit production team leaders are responsible for leading and working with a team. They organise daily work schedules for the production of fruit crops and participate in the production processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5aff8b2a-298a-456d-957a-274c774e8523','key_16283','','','7233','7233.12','moulding machine technician',replace('mould maker apprentice\nmould filler and drainer\nmould filler\nmould builder\nmould setter\nmould presser\nmolding machine technician\nmould setting operator\nmoulding line operator\nmould stamper\nmould blower\nmould dresser\nmoulding operator\nmould mechanic','\n',char(10)),'Moulding machine technicians service machinery used in the casting and moulding of plastics and other materials. They calibrate the equipment, perform maintenance activities, examine finished products and repair faults.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5b3ef95b-7d99-4af9-b2d0-41c6bc33f58c','key_16284','','','8131','8131.2','chemical mixer',replace('chemical mixer worker\nchemical blender\nchemical plant operative\nchemical batch blender\nchemical mixing machine tender\nchemical mixer setter\nchemical mixing machine operator\nchemical mixer\nchemicals blender\nchemical mixer operative\nchemical mixing machine operative\nchemical plant worker\nchemical plant operator\nchemical production worker\nchemical mixing machine worker\nmixer\nchemical mixer operator\nchemical batch mixer\nchemical mixer tender\nchemical process worker\nchemicals batch blender','\n',char(10)),'Chemical mixers operate and maintain mixing tanks and blenders in order to develop chemical products out of raw materials, making sure the end products are in conformance to the batch tickets specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5b6c0c64-1321-4fe7-a274-3287c13b489c','key_16285','','','2330','2330.1.13','music teacher secondary school',replace('high-school music teacher\nsecondary school music teacher\nsecondary school music tutor\nmusic teacher high school\nteacher of music in secondary schools\nmusic tutor secondary school\nmusic teacher in high school\nteacher of music in high school\nhigh school music tutor','\n',char(10)),'Music teachers at secondary schools provide education toÂ students, commonly children and young adults, in aÂ secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, music. They prepare lesson plans and materials, monitor the studentsâ progress, assist individually when necessary, and evaluate the studentsâ knowledge and performance on the subject of musicÂ through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5b912733-abc8-43f7-825c-00ae21043685','key_16286','','','1324','1324.3.2.35','import export manager in wood and construction materials',replace('senior import manager in  wood and construction materials\ntrainee export manager in  wood and construction materials\nassistant import export manager in  wood and construction materials\nassistant import manager in  wood and construction materials\nimport export manager in wood and construction materials\ngraduate export manager in  wood and construction materials\ntrainee import manager in  wood and construction materials\nassistant export manager in  wood and construction materials\ngraduate import manager in  wood and construction materials\ninternational trade manager in  wood and construction materials\ngraduate import export manager in  wood and construction materials\ninternational trade compliance manager in  wood and construction materials\ncustoms compliance manager in  wood and construction materials\nsenior export manager in  wood and construction materials\ntrainee import export manager in  wood and construction materials\nsenior import export manager in  wood and construction materials','\n',char(10)),'Import export managers in wood and construction materials install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5b942a51-c9a2-4dc7-82cc-57451426cf4d','key_16287','','','7322','7322.10','textile printer',replace('silk screen printer\ntextile screen printer\ntextile printers\ntextile printer\nrotary screen printing operator\nroller printer\ntextile printing machine operator','\n',char(10)),'Textile printers perform textile printing operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5b9a678a-7c35-4c50-a804-754b82652ee6','key_16288','','','4221','4221.6','tour organiser',replace('excursion leader\ntour escort\ntravel and tourism tour manager\ntour organizer\ntour leader\nexcursion escort','\n',char(10)),'Tour organisers are in charge of managing and supervising the itinerary of a touristic voyage and provide practical information to tourists.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5baffa9d-1607-4665-be94-662bad26b9ff','key_16289','','','3122','3122.4.3','distillery supervisor',replace('distillery supervisor\ndistilling supervisor\ndistillery manager','\n',char(10)),replace('Distillery supervisors coordinate the production processes comprised in the production of spirits and manage the workers engaged in the process. They verify distilled liquors being produced at specified amounts and proofs. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5bca96fd-4203-400b-be58-9e9b50bc98da','key_16290','','','1111','1111.5','member of parliament',replace('assembly member\nlegislative member\nmember of european parliament\nmember of parliament','\n',char(10)),'Members of parliament represent their political party''s interests in parliaments. They perform legislative duties, developing and proposing new laws, and communicate with government officials to assess current issues and government operations. They oversee the implementation of laws and policies and function as government representatives to the public to ensure transparency.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5beafebe-9211-4b3a-adf1-6161097a3975','key_16291','','','2250','2250.3','animal physiotherapist',replace('animal physiotherapist\nanimal physical threapist\nveterinary physiotherapist','\n',char(10)),'Animal physiotherapists provide a therapeutic treatment following veterinary diagnosis or referral. They concentrate on the application of physical therapy and exercise procedures to support animal rehabilitation after a surgery or an injury, aiming to return the animals to normal function and/or maintain performance in accordance with national legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5bf823c5-d3fd-46f6-9577-9331de10759f','key_16292','','','8141','8141.1.4','rubber cutting machine tender',replace('rubber production worker\nrubber cutting machine operator\nrubber cutting machine tender\nrubber cutter\nrubber cutter tender\ncutting machine tender\nrubber cutting machine operative\nrubber slab production worker\nrubber slab cutter','\n',char(10)),'Rubber cutting machine tenders operate the machine that cuts rubber stock into slabs. They take the slab of the conveyor and put it on a pallet, spraying a chemical solution on each slab to prevent sticking.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5c082067-ea18-4ccb-8c43-e70b18ad8120','key_16293','','','7223','7223.4','computer numerical control machine operator',replace('CNC machinist\ncomputer numerical control machine operator\ncnc programmer/machinist\nCNC set up technician\ncnc operator\ncnc programmer\nCNC set-up operative\nCNC drill operator\nCNC drill technician\nCNC operator/machinist\nCNC drill operative\nCNC mill operator\nCNC mill operative\nCNC set-up operator\nCNC drilling operator\nCNC machine operative\nCNC set up operative\nCNC drilling operative\nCNC programmer/machinist\nCNC machine operator\ncnc operator/machinist\nCNC programmer\nCNC set-up technician\ncnc machinist\ncomputer numerical control machine programmer/operator\nCNC set up operator','\n',char(10)),'Computer numerical control machine operators set-up, maintain and control a computer numerical control machine in order to execute the product orders. They are responsible for programming the machines, ensuring the required parameters and measurements are met while maintaining the quality and safety standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5c14982e-375d-47c5-9ea1-e9bcf3884643','key_16294','','','3121','3121.1','mine supervisor',replace('mine technical supervisor\nmine supervisor\nresident supervisor\nquarry supervisor\nmine production supervisor\nmine operations supervisor\nquarrying supervisor\nmining supervisor\nmine overman\nmine technician\nmine forewoman\nmine overwoman\nmine overseer\nmine foreman','\n',char(10)),'Mine supervisors coordinate and supervise the activities related to mining and quarrying in underground and surface mines and quarries. They oversee the workers, schedules, processes and organisation in the mines and quarries.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5c1eba1e-7820-4287-9746-4c5906320100','key_16295','','','4311','4311.2','sales support assistant',replace('registered sales assistant\nsales clerical assistant\nsales associate\nsales accounts clerk\nsalesclerk\naccounts assistant\nsales operations assistant\nsales officer\ngroup sales assistant\nsales support assistant','\n',char(10)),'Sales support assistants perform a variety of general sales support tasks, such as supporting the development of sales plans, managing clerical activities of sales efforts, verifying client invoices and other accounting documents or records, compiling data, and preparing reports for other company departments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5c4ba833-da16-4bd0-a4b2-c08a2eaf5884','key_16296','','','8181','8181.10','kiln firer',replace('decorating-kiln firer\nkiln hand\nkiln filler\nkiln fireman\ndecorating-kiln operator\nglost kiln firer\nglost kiln operator\nkiln firer\nkiln woman\nbisque kiln firer\nround kiln firer\ntunnel kiln firer\nkiln man\nbisque kiln operator','\n',char(10)),'Kiln firers operate kilns in order to fire decorations or glazes. They determine the level and uniformity of oven temperature, regulate the temperature, and give directions to a helper in preparing firebox and lighting fires.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5c5b153e-4bf8-4f3d-973d-12fabf306d12','key_16297','','','1112','1112.7','secretary general',replace('international organisation chief officer\nsecretary-general\nsecretary general\ngeneral secretary\nfirst secretary','\n',char(10)),'Secretaries general head international governmental or nongovernmental organisations. They supervise staff, direct policy and strategy development, and function as main representative of the organisation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5c9dea76-c7da-4e58-857c-fc3cbe7b960a','key_16298','','','1439','1439.4','garage manager',replace('automotive repair shop manager\ncar shop repair service director\ngarage manager\ncar repair shop director\nmotor vehicle repair shop director\nmotor vehicle repair shop coordinator\nauto shop service manager\ncar workshop manager\nmotor vehicle repair shop manager','\n',char(10)),'Garage managers oversee the work of road vehicle mechanics and administrative personnel. They organise the daily work and deal with clients.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5cc6d160-3864-43b0-b3eb-77d1208a51b4','key_16299','','','1324','1324.3.2.23','import export manager in metals and metal ores',replace('assistant import manager in metals and metal ores\ninternational trade manager in metals and metal ores\nassistant import export manager in metals and metal ores\ntrainee export manager in metals and metal ores\ngraduate export manager in metals and metal ores\nsenior import export manager in metals and metal ores\ninternational trade compliance manager in metals and metal ores\ntrainee import export manager in metals and metal ores\nsenior export manager in metals and metal ores\ncustoms compliance manager in metals and metal ores\ntrainee import manager in metals and metal ores\nimport export manager in metals and metal ores\ngraduate import manager in metals and metal ores\nassistant export manager in metals and metal ores\nsenior import manager in metals and metal ores\ngraduate import export manager in metals and metal ores','\n',char(10)),'Import export managers in metals and metal ores install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5ccb2c34-e860-43a5-9869-342487518115','key_16300','','','3343','3343.1.6','project support officer',replace('project management officer\nPMO','\n',char(10)),'Project support officers provide different types of services for the successful execution of a project as part of a horizontal project management office. They offer administrative support, assistance and training to project managers and other staff members, manage the project’s documentation and assist the project manager with project scheduling, resource planning, coordination and reporting. Project support officers are responsible for quality assurance activities and for monitoring the adherence to methodology guidelines and other organisational standards. They also offer advice on project management tools and related administrative services.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5cff1eee-9027-4064-8a26-35b1877e4eb3','key_16301','','','1344','1344.1.1','public housing manager',replace('housing authority manager\nhousing policy manager\nhousing officer\npublic housing manager\npublic housing officer\nestates manager\nhousing policy analyst','\n',char(10)),'Public housing managers develop strategies for the improvement of housing policy in a community, as well as providing social housing to those in need. They identify housing needs and issues, and supervise resource allocation. They also communicate with organisations involved in building public housing facilities, and social service organisations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5d009d45-3bb9-4e33-8760-294f59adec8c','key_16302','','','7412','7412.3','electrical mechanic',replace('electrical maintenance mechanic\ninstallation electrician\nelectrical test mechanic\nelectrical mechanic\nelectrical installation mechanic\nelectrotechnical panel mechanic\nelectrical repair mechanic\nelectrotechnical panel builder\nelectrical diagnostic mechanic\nproduction electrician\nmaintenance electrician\nelectrician\ndiagnostic electrician','\n',char(10)),'Electrical mechanics install, repair and maintain mechanical and electrical components of machinery, tools and equipment. They test electrical parts to ensure efficiency and make improvements accordingly.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5d0cc30d-068a-424c-8463-f42872a7149e','key_16303','','','2423','2423.1','career guidance advisor',replace('school careers advisor\ncareers officer\ncareer coach\ncareer guidance advisor\ncareers advisor\ncareer guidance education advisor\neducational advisor\ncareer guidance practitioner\nmentor for prior learning\ncareer guidance counsellor\nschool career guidance advisor\ncounsellor in career guidance','\n',char(10)),'Career guidance advisors provide guidance and advice to adults and students on making educational, training and occupational choices and assist people in managing their careers, through career planning and career exploration. They help identify options for future careers, assist beneficiaries in the development of their curriculum and help people reflect on their ambitions, interests and qualifications. Career guidance advisors may provide advice on various career planning issues and make suggestions for lifelong learning if necessary, including study recommendations. They may also assist the individual in the search for a job or provide guidance and advice to prepare a candidate for recognition of prior learning.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5d0e1510-7248-4eca-abe4-13de830d1614','key_16304','','','8152','8152.3.2','weaving machine supervisor',replace('weaving loom operator\nloom-fixer supervisor\nloom technician\nweaver and loom technician\nweaving supervisor\nweaving inspector\nweaving machine supervisor\nweaver of fabrics\nweave-room supervisor\nweavers','\n',char(10)),'Weaving machine supervisors monitor the weaving process. They operate the weaving process at automated machines (from silk to carpet, from flat to Jacquard). They monitor fabric quality and condition of mechanical machines such as woven fabrics for clothing, home-tex or technical end uses. They carry out maintenance works on machines that convert yarns into fabrics such as blankets, carpets, towels and clothing material. They repair loom malfunctions as reported by the weaver, and complete loom check out sheets.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5d46e448-b6bb-48e5-b245-5aad68e06085','key_16305','','','7223','7223.15','oxy fuel burning machine operator',replace('oxy-fuel burning machine technician\noxy-fuel cutting machine operative\noxy-fuel cutting machine technician\noxy fuel cutting machine operator\noxy fuel burning machine operator\noxy-fuel burning machine operator\noxy-fuel cutting machine operator\ncutting torch operator\noxy fuel cutting machine operative\noxy-fuel burning machine operative\noxy fuel cutting machine technician','\n',char(10)),'Oxy fuel burning machine operators set up and tend machines designed to cut, or rather burn off, excess material from the metal workpiece using a torch that heats the metal workpiece to its kindling temperature and subsequently burns it into a metal oxide upon its reaction with an emitted stream of oxygen, flowing out of the workpiece''s created kerf as slag.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5d549551-d94a-4e98-8444-d6608d70c8d8','key_16306','','','8113','8113.7','tool pusher',replace('toolpushers\nrig manager\npusher\ntool pusher\ntoolpusher\noil and gas rigging technician','\n',char(10)),'Tool pushers assume responsibility over daily drilling operations. They do mostly administrative work. Tool pushers ensure that the oil rig has sufficient materials, spare parts and enough personnel to continue with the daily operations. They conduct drilling activities in accordance with scheduled programme, supervising drilling crew and equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5d5fde2c-47ce-4dcd-99f0-ba651b7b908f','key_16307','','','3344','3344.3','medical transcriptionist',replace('medical transcriber\nclinical transcriptionist\nmedical transcriptionist\nclinical transcription typist\nmedical transcription suite typist\nmedical transcriptionist and proof reader\nmedical secretary-audio typist\nclinical transcriber\nclinical transcription suite typist','\n',char(10)),'Medical transcriptionists interpret dictated information from the doctor or other healthcare professionals and convert it into documents. They create, format and edit medical records for patients based on the provided data and take care to apply punctuation and grammar rules.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5d601b40-7e0e-404e-bbff-bb98e147437c','key_16308','','','2422','2422.12.11','legal policy officer',replace('legal affairs policy officer\npolicy officer, legal and compliance\nlegal policy specialist\nlegal policy officer\npolicy analyst, legal affairs\nlegal policy analyst\nlegal policy advisor\npolicy officer, legal affairs','\n',char(10)),'Legal affairs policy officers research, analyse and develop policies related to the legal sector and implement these policies to improve the existing regulation around the sector. They work closely with partners, external organisations or other stakeholders and provide them with regular updates.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5d758e75-a116-485a-b800-5db08ffd3ba1','key_16309','','','2635','2635.1.2','drug and alcohol addiction counsellor',replace('drug and alcohol addiction counselor\naddictions counsellor\nsubstance abuse counsellor\ndrug and alcohol addiction social counsellor\ndrug counsellor\ndrug and alcohol worker\nalcohol counsellor','\n',char(10)),'Drug and alcohol addiction counsellors provide assistance and counselling to individuals and families dealing with drug and alcohol addictions, monitoring their progress, advocating for them, performing crisis interventions and group therapy. They also assist the persons with the consequences of their addictions which can be unemployment, physical or mental disorders and poverty. Drug and alcohol addiction counsellors may also prepare educational programs for high-risk populations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5d9e63a2-8013-434e-9f92-3b3d54a0c320','key_16310','','','8160','8160.2','baking operator',replace('bread making machine operator\nbaking operator\nbakery worker','\n',char(10)),'Baking operators tend automatic reels or conveyor-type ovens to bake bread, pastries and other bakery products. They interpret work orders to determine the products and the quantities to be baked. They set the operational speed of conveyors, baking times, and temperatures. They supervise the baking process and maintain oven operations in control.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5dbb9cf0-b226-402c-a295-2f42ef05ff8b','key_16311','','','7411','7411.1.1.1','domestic electrician',replace('electrician\nresidential electrician\ndomestic and commercial electrician\ndomestic electrician','\n',char(10)),'Domestic electricians install and maintain electrical infrastructures and domestic machinery in homes and other residential buildings. They perform inspections and repair defective parts to ensure efficiency.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5dd01019-3999-4d90-bc8a-7a04b0b208b0','key_16312','','','5142','5142.4','make-up and hair designer',replace('make up & hair artist\nhair and make-up designer\nhair designer & make up artist\nmake-up and hair artist\nhair designer and make-up artist\nhair and make-up artist\nwig designer\nhair and make up designer\nhair designer and make up artist\nhair & make-up designer\nhair and make up artist\nmake-up and hair designer\nmake-up & hair designer\nhair and makeup artist\nmake up and hair designer\nmake-up, hair and wig designer\nhair & make-up artist\nmakeup & hair artist\nmake-up artist & hair designer\nmake up and hair artist\nmake up artist & hair designer\nmakeup and hair artist\nhair designer & make-up artist\nhair & make up artist\nhair & make up designer\nmake-up artist and hair designer\nmake-up & hair artist\nmake up artist and hair designer\nmake-up, hair and wig artist\nhair & makeup artist\nmake up & hair designer','\n',char(10)),replace('Make-up and hair designers develop a design concept for make-up and hair of performers and supervise the execution of it. Their work is based on research and artistic vision. Their design is influenced by and influences other designs and must be conform with these designs and the overall artistic vision. Therefore, the designers work closely with artistic directors, operators and the artistic team. Make-up and hair designers develop sketches, design drawings or other documentation to support the workshop and performance crew. Make-up designers sometimes also work as autonomous artists, creating make-up art outside a performance context. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5df63943-f1bc-4438-90f1-92768a7a23c8','key_16313','','','7411','7411.1.1.2','industrial electrician',replace('marine electrician\nelectrician\nindustrial and commercial electrician\nindustrial maintenance electrician\nindustrial electrical technician\nindustrial electrician\nelectrical technician','\n',char(10)),'Industrial electricians install and maintain electricity cables and other electrical infrastructure in large industrial and commercial buildings. They perform inspections and repair defective parts of electrical systems to ensure efficiency.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5e175ee2-6b07-4f1d-8f5e-962c36ab7bb6','key_16314','','','3411','3411.4','court bailiff',replace('court administrative officer\ncourt administrator\nusher\nbailiff\ncourt bailiff\ncourt security officer\nenforcement agent','\n',char(10)),'Court bailiffs maintain order and security in courtrooms. They transport offenders to and from the courtroom, ensure necessary supplies are present in the courtroom, and investigate the premises and examine individuals to ensure there are no threats. They also open and close court, and call witnesses.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5e29c263-a3ca-4220-b67e-ee65205db294','key_16315','','','7317','7317.4','toymaker',replace('plaything repairer\ntoy fixer\ntoymaker\nmetal toy maker\nsoft toy maker\ntoy repairman\ngismo repairer\nhand toy maker\nplay thing repairer\npuppet maker\ncraft toymaker\ntoy creator\nplastic toy maker\nmetal toymaker\ntoy repairer\nelectric toy repairman\ntoy finisher\nhand toymaker\nplaything repairman\ncraft toy maker\ngizmo repairer\nmaker of toys\nplastic toymaker\ntoy designer\ntoy maker\nelectric toy repairer\nsoft toymaker','\n',char(10)),'Toymakers create or reproduce hand-made objects for sale and exhibition made of various materials such as plastic, wood and textile. They develop, design and sketch the object, select the materials and cut, shape and process the materials as necessary and apply finishes. In addition, toymakers maintain and repair all types of toys, including mechanical ones. They identify defects in toys, replace damaged parts and restore their functionality.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5e4d156a-1f05-4b2c-b0f0-5abb82d52ed6','key_16316','','','3312','3312.1','bank account manager',replace('bank operations manager\nbanking accounts manager\nconsumer banker\nbanking officer\naccount managers SME\naccount manager SME\naccount managers\ncommercial bank manager\nretail personal banker\nbank relationship manager\nbank account manager\nbank accounts manager','\n',char(10)),'Bank account managers advise prospective clients on the type of banking accounts suitable for their needs. They work with clients to set up the bank account and remain their primary point of contact in the bank, assisting with all necessary documentation. Bank account managers may recommend their clients to contact other departments in the bank for other specific needs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5e5522c5-4b09-4586-a8f0-3b6d66d99969','key_16317','','','8172','8172.3','debarker operator',replace('debarking machinist\ndebarking machine technician\nlog peeler operator\ndebarking machine operator\nlog peeler specialist\nlog peeling machine specialist\ndebarker specialist\ndebarking technician\ndebarker technician\ndebarking machine specialist\ndebarking operator\nlog peeling machinist\nlog peeler technician\nlog peeling machine operator\nlog peeler\ndebarker\nlog peeling machine technician\ndebarker operator\ndebarking specialist','\n',char(10)),'Debarker operators operate debarking machines to strip harvested trees of their bark. The tree is fed into the machine, after which the bark is stripped using abrasion or cutting.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5e5b1b61-1351-4d3b-a293-deb3661715dd','key_16318','','','2411','2411.1.8','financial controller',replace('corporate controller\nfinancial control officer\nfinancial investigator\naccounting controller\naccountant\nfinance officer\nfinance manager\naccounting supervisor\nfinance controller\nfinancial accountant\nfinancial investigation clerk\nfinancial controller\nfinancial control manager\nfinancial administrator','\n',char(10)),'Financial controllers handle all tasks related to the budgeting and accounting aspects of a company or organisation. They implement and ensure compliance with internal financial and accounting procedures, and prepare documentation for external audits. They collect information related to financial statements such as assets, liabilities, equity, and cash flow in order to assess the company''s financial position to prepare annual budgets and forecasts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5e748def-5b90-41e2-aa8b-2ebcc7d25ab0','key_16319','','','7233','7233.9','marine fitter',replace('shipfitter\nship fitter\nnaval fitter\nmaritime fitter','\n',char(10)),'Marine fitters work primarily in fabrication, subassembly, assembly and final assembly of all structural components on commercial and naval vessels, to include but not limited to hulls, superstructures, masts, pilot house, and engine rooms.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5ed024ab-3b6a-4e06-a7fe-8a29f8469a64','key_16320','','','7213','7213.1','boilermaker',replace('boilermaker fitter\nboiler smith\nboiler fabricator\nwelder boilermaker\npressure vessel maker\npressure vessel repairer\nplater\nboiler constructor\nboiler retuber\nsteel fabricator\npressure vessel mender\nboiler mender\nboiler repiper\nboilermaker\nboiler repairer\nsteam boilermaker\nboiler fitter\npressure vessel fabricator','\n',char(10)),'Boilermakers operate a variety of equipment and machinery to create, repipe and retube hot water and steam boilers, producing them in all steps of the production process. They cut, gouge and shape the metal sheets and tubes for the boilers to size, using oxy-acetylene gas torches, assemble them by shielded metal arc welding, gas metal arc welding or gas tungsten arc welding, and finish them by the appropriate machine tools, power tools and coating.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5ee264b2-9bc5-40f6-9db3-c87143af6b2d','key_16321','','','5329','5329.2','phlebotomist',replace('phlebotomy practitioner\nphlebotomist\nspecialist in phlebotomy\nphlebotomy specialist\npractitioner of phlebotomy\nvenipuncturist\nclinical phlebotomist\nphlebotomy technician','\n',char(10)),'Phlebotomists take blood samples from patients for laboratory analysis, ensuring patient safety during the blood collection process. They transport the specimen to the laboratory, following strict instructions from the doctor of medicine.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5efb2c08-ba3a-4659-a2e4-1883f54e77b6','key_16322','','','2330','2330.1','secondary school teacher',replace('general studies teacher\nacademy teacher\nphysical education teacher\ndesign & technology teacher\nacademy school teacher\ngrammar school teacher\nhistory teacher\nsecondary school educator\nart teacher\ngeography teacher\nsecondary school teaching assistant\nreligious education teacher\nscience teacher\nhome economics teacher\nlanguage teacher\ncomprehensive school teacher\nhigh school teacher\nsecondary school schoolteacher\nmaths teacher','\n',char(10)),'Secondary school teachers provide education to students, commonly children and young adults, in a secondary school setting. They are usually specialist subject teachers, who instruct in their own field of study. They prepare lesson plans and materials, monitor the students'' progress, assist individually when necessary and evaluate their knowledge and performance through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5efb4fff-8c5f-4697-9587-034d9f6af094','key_16323','','','2269','2269.8.2','nuclear medicine radiographer','nuclear medicine radiographer','Nuclear Medicine Radiographers plan, prepare and perform nuclear medicine examinations, post-processing and treatment with a wide range of equipment and techniques using X-rays, magnetic resonance imaging and radiopharmaceuticals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5f16819d-50b9-475b-a2d8-607ee6d99a9d','key_16324','','','3351','3351.1','customs officer',replace('customs and excise officer\ncustoms control agent\nborder guard\ndetection officer\nborder force officer\ncustoms and excise duty officer\ncustoms officer\ncustoms & excise officer\ncustoms control officer\ngovernmental customs agent\ncustoms inspector','\n',char(10)),'Customs officers combat the importation of illegal goods, firearms, drugs or other dangerous or illegal items while checking the legality of items brought across national borders. They are government officials who control the documents to ensure entry criteria and custom laws are complied with and control if the custom taxes are paid correctly.','','Excludes <a resource="http://data.europa.eu/esco/occupation/a3ef0728-7540-4676-ad18-cf0a7913b3ce" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/a3ef0728-7540-4676-ad18-cf0a7913b3ce"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">customs and excise officer</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5f45a1ea-4d1b-43c3-b6a8-53b2276b3eb3','key_16325','','','2633','2633.1.1','genealogist',replace('genealogy expert\nancestry expert\nfamily tree researcher\ngenealogist','\n',char(10)),'Genealogists trace history and lineages of families. The results of their effort are displayed in a table of the descent from person to person which forms a family tree or they are written as narratives. Genealogists use analysis of public records, informal interviews, genetic analysis, and other methods to gain input information.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5f50290d-f520-4a69-85c7-1390d2a4a8b6','key_16326','','','2519','2519.7.5','ICT usability tester',replace('ICT usability tester\nusability developer\nusability testers\nuser-friendliness tester\nusability specialist','\n',char(10)),'ICT usability testers ensure compliance with requirements and strive for optimal usability within the software engineering cycle phases (analysis, design, implementation, and deployment). They also work closely with users (analysts) to research for and document user profiles, analyse tasks, workflows, and user scenarios.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5f617498-da0d-454a-9658-5e02d28a4bd7','key_16327','','','7314','7314.3','pottery and porcelain caster',replace('porcelain caster\ncaster of pottery and porcelain ware\npottery and porcelain caster\npottery and porcelain molder\npottery and porcelain ware caster\npottery and porcelain moulder\ncaster of pottery and porcelain\npottery caster','\n',char(10)),'Pottery and porcelain casters fill moulds with clay to cast pottery and porcelain wares. They pour excess slip out of the mould when needed, drain moulds, remove the cast from the mould, smooth the casting surfaces to remove marks and place the castings on boards to dry.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5f68c4c3-9d25-48e2-8efc-5471a80fb155','key_16328','','','8342','8342.6','road construction worker',replace('highway construction worker\nroad construction worcker\nsurfacing worker\nrunway construction worker\nroad constructinn worker\nroad paver\ncivil engineering project worker\nroad construction operative\nroad paviour\npaver\nroad costruction worker\nrad construction worker\nroading construction worker\nroad construction worker\nroad construction project worker\nconstruction worker on roads\nrod construction worker','\n',char(10)),'Road construction workers perform road construction on earthworks, substructure works and the pavement section of the road. They cover the compacted soil with one or more layers. Road construction workers usually lay a stabilising bed of sand or clay first before adding asphalt or concrete slabs in order to finish a road.','',replace('Includes people working with road roller operators. \n Excludes people performing activities which involve pouring concrete to serve as a road surface.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5f770d3d-5c42-4c22-bc06-23bebd182a37','key_16329','','','1420','1420.4.34','pet and pet food shop manager',replace('pet food shop manager\npet and pet food superstore manager\nanimal store manager\nanimal food store manager\npet supplies shop manager\nanimal care products store manager\npet supplies store manager\npet and pet food shop manager\nanimal food shop manager\npet superstore manager\nanimal care products shop manager\npet store manager\npet shop manager\npet  food store manager\nanimal shop manager\npet and pet food store manager','\n',char(10)),'Pet and pet food shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5f84de7d-5008-45c4-a993-8066388e0756','key_16330','','','7516','7516.2','leaf sorter',replace('tobacco leaf separator\ncigar leaf sorter\nleaf inspector\nleaf quality tester\ntobacco leaf sorting attendant\ntobacco leaf selector\ntobacco leaf sorter\nleaf quality inspector\nmanual leaf sorter\nleaf sorter (tobacco)\nwrapper leaf inspector\nleaf quality analyst\nleaf sorter\nhand leaf sorter\nleaf selector','\n',char(10)),'Leaf sorters analyse colour and condition of tobacco leaves in order to determine whether they should be used as cigar wrappers or binders. They select leaves without visible defects taking into account colour variations, tears, tar spots, tight grain, and sizes as per specifications. They fold wrapper leaves into bundles for stripping.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5f9e0588-e819-42f7-9975-f53198666c07','key_16331','','','7125','7125.1','plate glass installer',replace('plate glass window installer\nglazier\nplate glass installer\nplate glass fitter\nglazing installer\nglass installer\nglass pane installer','\n',char(10)),'Plate glass installers fit panes of glass into windows and other structural elements like glass doors, walls, façades and other structures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5fd099da-218a-460e-bd5b-b42f1b76c67c','key_16332','','','2262','2262.1.2.1','hospital pharmacist',replace('head of hospital pharmacy\nsite lead pharmacist\nhospital pharmacist\nhospital pharmacy specialist\nhospital locum pharmacist\nrelief pharmacist\nclinical pharmacist','\n',char(10)),'Hospital pharmacists prepare, dispense and provide medication to patients in hospitals. They collaborate with healthcare personnel such as doctors and nurses to treat patients and also offer advice and information on medications.','','','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5fd2980e-22c4-4ace-a394-190c76f4f539','key_16333','','','1330','1330.4','ICT documentation manager',replace('technical communication manager\nIT documentation manager\ndocumentation manager\nICT documentation manager\nICT documentation managers','\n',char(10)),'ICT documentation managers are in charge of managing the documentation development process in accordance with legal requirements, standards, organisational policies and goals. They schedule, control and direct the resources, people, funding and facilities of the documentation department (including establishing budgets and timelines, risk analysis and quality management). They also develop documentation standards, structuring methods and media concepts to effectively communicate product concepts and usage.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5fd69ee2-2042-4280-85e1-8f1462886987','key_16334','','','2146','2146.6','mine development engineer',replace('mine development expert\nmine development manager\ndevelopment engineer\nfacility manager\nmine development engineer','\n',char(10)),'Mine development engineers design and coordinate mine development operations such as crosscutting, sinking, tunnelling, in-seam drivages, raising, and removing and replacing overburden. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5fdfa822-c1ba-477e-87cc-3d742e59310d','key_16335','','','2310','2310.1.6','biology lecturer',replace('professor of biological sciences\nlecturer in biology\nbiological sciences lector\nbiology lector\nbiology instructor\nuniversity biological sciences lecturer\nuniversity biology lecturer\nuniversity lecturer in biology\nbiological science lecturer\nbiological sciences lecturer\nbiology docent\nprofessor of biology\nuniversity lecturer in biological science\nlecturer in biological sciences\nbiology lecturer\ninstructor in biology\nbiology professor\nbiology teacher\nhigher education biology teacher\nbiological science lector','\n',char(10)),'Biology lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, biology. They work with their university research assistants and university teaching assistants in the preparation of lectures and of exams, leading laboratory practices, grading papers and exams and leading review and feedback sessions for the students. They also conduct academic research in their field of biology, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5fe1b340-9b02-4c40-bbc9-ffd4d5fb5699','key_16336','','','2422','2422.12.3','cultural policy officer',replace('cultural policy officer\ncultural policy worker\nculture programmes officer\narts worker\nculture programes officer\ncultural programs officer\ncultural policy director\nculture policy officer\nculture director\narts administrator\ncultural policy coordinator','\n',char(10)),'Cultural policy officers develop and implement policies to improve and promote cultural activities and events. They manage resources and communicate with the public and media in order to facilitate interest in cultural programs and emphasize their importance in a community.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5ff100c2-00cd-4c64-82ae-8feab6712557','key_16337','','','9510','9510.1','leaflet distributor',replace('door to door leaflet distributor\ndoor to door leaflet distributer\nleaflet distributor\nflyer distributor\nmarketing assistant\nmarketeer\nleafler distributer','\n',char(10)),'Leaflet distributors hand out flyers, leaflet and advertisements in order to inform people or sell products and services. They distribute these leaflets either directly to the people on the streets or via mailboxes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/5ff7a990-a306-40c2-9224-b2bf7f15b2f9','key_16338','','','2654','2654.3.1','music producer',replace('dance music producer\nproducer\nsound recording engineer and arranger\nvocal producer\nrock music producer\nrecording engineer and arranger\npop music producer\nrecord producer\nmusic publisher\nchill-out & lounge music producer\nmusic producer','\n',char(10)),'Music producers are responsible for acquiring music to be published. They listen to demos of songs and determine whether they are good enough to be published. Music producers oversee the production of records. They manage the technical aspects of recording and editing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/60082a99-d8ef-4e84-9290-78902681b6ed','key_16339','','','2421','2421.1','business analyst',replace('business analyst\nstandardisation manager\nmanagement consultant\norganisational developer\nbusiness strategy consultant\nbusiness development analyst\nstandardisation expert\nbusiness strategy analyst\nstandardization expert','\n',char(10)),replace('\nBusiness analysts research and understand the strategic position of businesses and companies in relation to their markets and their stakeholders. They analyse and present their views on how the company, from many perspectives, can improve its strategic position and internal corporate structure. They assess needs for change, communication methods, technology, IT tools, new standards and certifications.','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/601db24d-cc02-48f3-9939-8c8b41b34ef7','key_16340','','','5249','5249.2.1.2','rental service representative in air transport equipment',replace('rental sales worker in air transport equipment\nrental sales assistant in air transport equipment\nrental service desk agent in air transport equipment\nrental service supervisor in air transport equipment\nrental service team leader in air transport equipment\nrental sales manager in air transport equipment\nrental sales desk supervisor in air transport equipment\nrental service worker in air transport equipment\nrental service representative in air transport equipment\nrental specialist in air transport equipment\nrental advisor in air transport equipment\nrental service manager in air transport equipment\nrental service desk clerk in air transport equipment\nrental sales team leader in air transport equipment\nrental sales supervisor in air transport equipment\nrental service assistant in air transport equipment\nrental service desk supervisor in air transport equipment\nrental sales desk clerk in air transport equipment\nrental sales desk agent in air transport equipment','\n',char(10)),'Rental service representatives in air transport equipment are in charge of renting out equipment and determining specific periods of usage. They document transactions, insurances and payments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6040c941-ab83-44b2-84e7-5cbfa1eafcdc','key_16341','','','3433','3433.1','art handler',replace('art preparator\nartifacts conservator\nart handlers\nmuseology technician\nart handler\nmuseum technician\ngallery technician\nart conservator\nfine arts packer','\n',char(10)),'Art handlers are trained individuals who work directly with objects in museums and art galleries. They work in coordination with exhibition registrars, collection managers, conservator-restorers and curators, among others, to ensure that objects are safely handled and cared for. Often they are responsible for packing and unpacking art, installing and deinstalling art in exhibitions, and moving art around the museum and storage spaces.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/60528794-1a68-4440-a141-2df011be98be','key_16342','','','1420','1420.4.2','antique shop manager',replace('collectables shop manager\nantique furniture shop manager\nantiques and collectibles shop manager\nantiquities store manager\nantiques dealer\nantique furniture store manager\nantiques and collectables shop manager\nantiques store manager\nantiques and collectibles store manager\nantiques showroom manager\nantiques gallery manager\nantiquities shop manager\nantiques shop manager\nantique shop manager','\n',char(10)),'Antique shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/605e58d6-ad27-4828-b1f5-16707f327fa0','key_16343','','','2310','2310.1.15','education studies lecturer',replace('professor of education studies\neducation studies lector\neducation studies instructor\nuniversity lecturer in education studies\neducation studies lecturer\nuniversity education studies lecturer\neducation studies teacher\neducation studies docent\neducation studies professor\nsenior lecturer in education studies\nhigher education  studies teacher\nuniversity education studies teacher\nlecturer of education studies\nlecturer in education studies','\n',char(10)),'Education studies lecturers are professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, education studies, so they can become teachers themselves. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, grading papers and exams and leading review and feedback sessions for the students.','','Includes people performing academic research in their field of education studies.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6066ed14-ace3-4352-b656-2b621fd7bc88','key_16344','','','1431','1431.1','cultural facilities manager',replace('cultural facilities program coordinator\ncultural facilities executive\ncultural facilities director\ncultural services manager\ncultural facilities manager','\n',char(10)),'Cultural facilities managers direct the operations of facilities that provide cultural services such as theatre, museums and concert halls. They plan and organise the daily operations of the related staff and facilities and ensure the organisation follows the latest developments in its field. They coordinate the different departments of the facility and manage the correct use of resources, policies and budgets.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/608edf30-fe37-4ae5-bdd1-2d3a8fd347bd','key_16345','','','2152','2152.1.2','flight test engineer',replace('flight test data engineer\nflight test engineering adviser\nflight test engineer\nflight test technician\nflight test instrumentation engineer\nflight test research engineer\nflight test performance engineer\nFTE\nCFD engineer\ntest and evaluation engineer\nflight test engineering expert\nflight test engineering specialist\nflight test technology specialist\nflight test safety engineer\nflight test technology expert\naircraft test engineer\naeroplane test engineer\nflight test technologist\nflight testing engineer\nflight test design engineer\nflight test engineering consultant','\n',char(10)),'Flight test engineers work with other systems engineers to plan the tests in detail and to make sure that the recording systems are installed for the required data parameters. They analyse the data collected during test flights and produce reports for individual test phases and for the final flight test. They are also responsible for the safety of the test operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/60b9b847-d964-4a8a-a756-904080b0f35f','key_16346','','','7223','7223.21','stamping press operator',replace('stamp press operative\npress shop operator\npunch machine operative\ndie-stamping press operative\nmetal press tender\ndie-stamping press worker\nmetal press setter\npress shop worker\nstamping press worker\nstamping press operative\nstamping press tender\ndie-stamping press operator\nmetal press operative\nmetal press operator\ndie-stamping press setter\nmetal die cutter\nmetal press worker\npunch machine hand\npress shop setter\npunch operator\npress shop operative\ndie-stamping press tender\nstamping press setter\nstamp press operator\nmetal die-cutter\nstamping press operator\npress shop tender\npunch press setter','\n',char(10)),'Stamping press operators set up and tend stamping presses designed to form metal workpieces in their desired shape by applying pressure through the up and down movement of a bolster plate and a die attached to a stamping ram on the metal, resulting in the die producing smaller metal parts of the workpiece fed to the press.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/60bc7374-9fa1-4bbd-b936-2676fca0bfb6','key_16347','','','3311','3311.3','financial trader',replace('quantitative trader\nfinancial trading expert\nequity trader\nnatural gas financial trader\nfinancial trading specialist\nproprietry options trader\nderivatives trader\nfinancial instruments trader\nsecurities trader\nfinancial trader trainee\nfinancial trader','\n',char(10)),'Financial traders buy and sell financial products such as assets, shares and bonds for private clients, banks or companies. They monitor the financial markets closely and aim to maximise profit and to minimise risk through their transactions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/60d77070-2cf4-4883-b44d-91e8ce54db86','key_16348','','','3123','3123.1.26','water conservation technician supervisor',replace('water recovery systems installation supervisor\nwater conservation technician supervisor\nmanager for water conservation systems installation\nwater conservation systems installation supervisor\nsupervisor of water conservation systems installation','\n',char(10)),'Water conservation technician supervisors supervise the installation of systems to recover, filter, store and distribute water from different sources such as rainwater and domestic greywater. They assign tasks and take quick decisions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/60f7eb23-a7ea-4cbe-98a5-1248291f62d5','key_16349','','','2622','2622.2','librarian',replace('library network coordinator\nspecial collections librarian\nelectronic resources librarian\nlibrary associate\nliterature researcher\nthesaurus manager\nlibrarian\nlibrary supervisor\nschool librarian\nlibrary assistant\nlibrary technical assistant\nmusic librarian\ncatalogue librarian\nbibliothec','\n',char(10)),'Librarians manage libraries and perform related library services. They manage, collect and develop information resources. They make information available, accessible and discoverable to any kind of user.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/61053604-8091-4a8e-9c63-5151051722e3','key_16350','','','2422','2422.6','healthcare consultant',replace('healthcare management adivsor\nhealthcare advisor\nhealthcare management consultant\nmanagement consultant\nretirement healthcare advisor\nhealthcare consultant','\n',char(10)),'Healthcare consultants advise health care organisations on the development of plans to improve patient care and safety. They analyse health care policies and identify issues, and aid in the development of improvement strategies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/610bee9f-d7f0-4b18-918e-f2a4a91f8912','key_16351','','','8112','8112.3','stone driller',replace('pneumatic drill operator\nslate driller\nsandstone driller\nstone drilling machine operative\nradial drill operator\nmarble driller\nstone driller\nstone drilling worker\ngranite driller\nslate cutter\nstone drilling machine operator','\n',char(10)),'Stone drillers operate the drilling machine that bores holes into stone blocks. They manipulate granit, sandstone, marble and slate according to specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/612430b3-dd43-4786-b823-0466c4953f66','key_16352','','','5230','5230.3','ticket issuing clerk',replace('museum ticket selling agent\nticket selling agent\nmuseumshop selling agent\nconcert ticket selling agent\nticket issuing clerk\nsport ticket selling agent','\n',char(10)),'Ticket issuing clerks provide service to customers, sell tickets and fit the reservation offer to customers'' needs. They sell tickets for all events such as sporting, cultural and leisure activities. They provide customers with information on offers and make the necessary arrangements.','','Excludes <a resource="http://data.europa.eu/esco/occupation/bdf2dd9d-6c92-46f4-a8e7-76c12302d3e7" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/bdf2dd9d-6c92-46f4-a8e7-76c12302d3e7"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">travel tickets clerk</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6140be85-2d45-44b2-b0ba-412aadcead1e','key_16353','','','2131','2131.4.8','geneticist',replace('genetics engineer\ngenetics research scientist\ngeneticists\ngenetics scholar\ngenetics analyst\ngenetics research analyst\ngenetics science researcher\ngeneticist\ncell geneticist\ngenetics scientist\ngenetic scientist\nembryologist\nmolecular geneticist\nmolecular biologist\ngenetics researcher','\n',char(10)),'Geneticists study and focus their research on genetics. They analyse the fashion in which genes interact, operate, and inherit traits and characteristics. Based on their research, they attend patients with inherited diseases and conditions, congenital malformations, and genetic matters at large.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/615920c5-4f63-4eb3-8b60-afaaed3ab1ff','key_16354','','','2141','2141.8','maintenance and repair engineer',replace('mechanical engineer\nequipment engineer\nmaintenance engineer\nmaintenance and repair manager\nsite superintendent\nproduction engineer\nmachine engineer\nrepair engineer\nmaintenance and repair engineer\nmaintenance and repair superintendent\nmanufacturing systems engineer\nplant engineer\nengineering manager\nmanufacturing engineer\nplant maintenance engineer\nplant repair engineer','\n',char(10)),'Maintenance and repair engineers focus on the optimization of equipment, procedures, machineries and infrastructure. They ensure their maximum availability at minimum costs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/61704d4b-f54b-43e5-a7de-bfd7a76f2f0d','key_16355','','','2230','2230.2.2','homeopath',replace('homeopathic practitioner\nspecialist homeopath\nhomeopath\npractitioner of homeopathy\ntechnician in homeopathy\nhomeopathic therapist\ntherapist in homeopathy\nhomeopathy practitioner\nexpert homeopath\nhomeopathy technician','\n',char(10)),replace('Homeopaths treat clients based on the selection and prescription of homeopathic remedies using the homeopathy principle of “like cures like.” \n ','\n',char(10)),'','Includes doctors of medicine who perform homeopathy.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6175b828-026d-4fb4-8be7-418394e1add0','key_16356','','','7322','7322.1','digital printer',replace('digital press operator\ndigital printer\ndigital printer specialist\nretoucher and printer\ndigital print machine specialist\ngraphic designer/digital printer\ndigital print supervisor\ndigital printer technician\ndigital and lithographic printer\ndigital print machine operator\nlarge format digital printer\ndigital web printer\nprint operator\ndigital print specialist\ndigital print operator\ndigital printer and finisher\ndigital print technician\ndigital print machine technician\ndigital printer operator','\n',char(10)),'Digital printers work with machines that print directly to the medium, without an intervening plate. Digital printers usually use laser or inkjet technology to print individual pages without lengthy or labour intensive technical steps intervening between the finished digital product and the print.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6188bf20-5cdb-4702-af72-2cb67ec39810','key_16357','','','7412','7412.12','theme park technician',replace('ride maintenance engineer\naquatic technician\nride technician\nengineering supervisor\nride mechanical engineer','\n',char(10)),'Theme park technicians work to maintain and repair amusement park attractions. They need a strong technical knowledge and have specialised knowledge of the rides they are assigned to maintain. Theme park technicians usually keep records of the maintenance and repairs performed as well as uptime and downtime for each serviced attraction. Attention to safety is especially important in the maintenance and repair of amusement park rides.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/618a854a-4ecd-4535-84e6-350e1fe0aa0f','key_16358','','','7313','7313.1','jeweller',replace('earring maker\nring maker\njewellery fabricator\nbrooch maker\njewelry design consultant\nfine metalworker\njewellery design consultant\nring fabricator\njewellery design artist\njewellery metalworker\njewelry metalworker\ncraft jeweller\njewelsmith\njewel smith\njewelry designer\njewellery maker\nfiligree jewelery worker\njewelry design artist\njeweler\nbench jeweller\nearrings fabricator\nearrings maker','\n',char(10)),'Jewellers fabricate and repair various jewellery articles. They create models from wax or metal and they may undertake the casting process (place wax model in casting ring, create moulds, pour molten metal into mould, or operate centrifugal casting machine to cast articles). Jewellers also cut, saw, file, and solder pieces of jewellery together, using a soldering torch, carving tools and hand tools and polish the article.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/61a61a3b-6fcd-4525-9f5c-0a1415a9a51f','key_16359','','','1324','1324.3.1.6.29','tobacco products distribution manager',replace('tobacco products logistics planner\ntobacco products logistics manager\ntrainee tobacco products distribution manager\ntobacco products supply chain manager\ntobacco products supply chain planner\ngraduate tobacco products distribution manager\nsenior tobacco products distribution manager\nassistant tobacco products distribution manager\ntobacco products distribution planner\ntobacco products distribution manager','\n',char(10)),'Tobacco products distribution managers plan the distribution of tobacco products to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/61b859ed-24d8-48cf-9c1b-67d7a6c62bb5','key_16360','','','3331','3331.2.1','import export specialist',replace('senior export specialist\ntrainee international trade compliance specialist\nsenior customs compliance specialist\nsenior international trade compliance specialist\ngraduate international trade compliance specialist\nimport export specialist\nassistant customs compliance specialist\nsenior import specialist\ntrainee customs compliance specialist\nassistant import export specialist\ntrainee import export specialist\nsenior import export specialist\ntrainee export specialist\nassistant international trade compliance specialist\nassistant export specialist\ngraduate import specialist\ngraduate export specialist\ntrainee import specialist\nassistant import specialist\ngraduate import export specialist\ngraduate customs compliance specialist','\n',char(10)),'Import export specialists have and apply deep knowledge of import and export goods including customs clearance and documentation. They declare goods that cross the border, inform customers about customs and give advice concerning disputes related to customs legislation. They prepare the documents needed and make sure they are delivered to customs. They check and process duty and make sure VAT payments are done as applicable.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/61d9270c-491d-438d-8d41-77ba2e0ef023','key_16361','','','7543','7543.10','product quality inspector',replace('product inspector\nquality inspector\nproduct quality monitor\nQS inspector\nquality control inspector\nQA inspector\nquality assurance inspector\nproduct quality inspector','\n',char(10)),'Product quality inspectors check products in order to assess their compliance in reference to a standard or reference guideline. They observe, measure, test, and form questions in order to fill in forms and present levels of compliance with comments after the inspection process. Product quality inspectors are employed or work externally for companies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/61e278a3-2c27-4c18-a827-11e2326b33ae','key_16362','','','2659','2659.2.5','street performer',replace('street theater artist\nstreet performing artist\nbusker\nstreet puppeteer\nstreet singer\nstreet circus artist\nstreet arts performer\nstreet entertainer\nstreet magician\nstreet theatre artist\nstreet musician\nstreet performer','\n',char(10)),'Street performers create street arts performances for outdoor spaces, using space and audience as a creative resource. They create their performance through playful exploration and experimentation with the purpose of entertaining and possibly also of sharing critical opinions concerning societal issues. They stimulate audience participation as a component of their performance whilst respecting the audience''s safety and integrity.','',replace('Excludes <a resource="http://data.europa.eu/esco/occupation/080bce18-066b-4431-90ed-e07608f73fe0" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/080bce18-066b-4431-90ed-e07608f73fe0"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">street artists</span></a>. \n Includes people performing activities solo or in group.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/61ed67cd-67d7-44d8-bc2d-47fc79110a59','key_16363','','','4323','4323.17','taxi controller',replace('taxi controller\nminicab controller\nprivate hire controller\ntaxi dispatcher\nprivate hire dispatcher\nminicab dispatcher','\n',char(10)),'Taxi controllers take bookings, dispatch vehicles, and are responsible for coordinating drivers while maintaining customer liaison.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/62001bb7-4611-4b61-903e-d98589c0821a','key_16364','','','1223','1223.2.1.3','games development manager',replace('gambling development director\ngaming development manager\ngames development director\ngames project and product development director\ngambling project and product development director\ngaming project and product development director\ngaming development director\ngambling project and product development manager\ngames project and product development manager\ngambling development manager\ngaming project and product development manager\ngames development manager','\n',char(10)),'Games development managers supervise and coordinate the creation, development, distribution, and selling of games. They communicate with manufacturers to ensure the production of games.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6219f0ef-6414-4d01-9221-41ac25dcb409','key_16365','','','3252','3252.1.1','clinical coder',replace('clinical coder\nmedical coding officer\nclinical records technician\nmedical coder\nclinical coding expert\naccredited medical coder\ndiagnostic coder\naccredited clinical coder\nclinical coding officer','\n',char(10)),'Clinical coders read patients'' medical records. They analyse and interpret medical statements about diseases, injuries and procedures. Clinical coders convert this information into health classification codes in order to calculate treatment reimbursements, to produce statistics and to monitor health care performance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/621b7aa1-b678-49ee-8caa-91b496f8bc5b','key_16366','','','3331','3331.2.1.25','import export specialist in office furniture',replace('customs compliance specialist in office furniture\ninternational trade compliance specialist in office furniture\nimport export specialist in office furniture\nassistant import specialist in office furniture\ngraduate import export specialist in office furniture\nassistant import export specialist in office furniture\ntrainee import export specialist in office furniture\ngraduate import specialist in office furniture\nsenior export specialist in office furniture\ntrainee export specialist in office furniture\ntrainee import specialist in office furniture\nsenior import export specialist in office furniture\nassistant export specialist in office furniture\nsenior import specialist in office furniture\ngraduate export specialist in office furniture','\n',char(10)),'Import export specialists in office furniture have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/62204bd8-98b5-418f-b189-7333904cbde7','key_16367','','','5164','5164.10','zoo section leader',replace('team leader-great apes\nteam leader-large mammals\nteam leader-African section\nsenior zookeeper\nteam leader-aquarium\nlead zoo keeper\nzoo section leader\nhead of section\nsection leader\nsenior zoo keeper\nsection leader-aquarium\nsenior keeper\nsection leader-great apes\nsenior aquarist\nzoo team leader\nteam leader\nzoo section head','\n',char(10)),'Zoo section leaders are responsible for managing and leading a team of zoo keepers. They will carry out daily care and management of animals as well as, in cooperation with colleagues, long term management and organisation of the species and exhibits in their section. They are also accountable for various aspects of staff management for the keepers in their section. Depending on the size of the zoo and animal section they may have additional responsibility for appointing staff and budgeting.   ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/622b1d5e-bf14-4ec0-b83e-3bfcf8dff624','key_16368','','','8113','8113.2','drill operator',replace('drill operator\noil rig driller\nrig drill operator\nrig driller','\n',char(10)),'Drill operators supervise a team during rigging and drilling operations. They monitor well activity and take measures in case of emergencies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/622ccd55-910f-483d-8661-44c6162156bf','key_16369','','','2352','2352.1.4','special educational needs itinerant teacher',replace('special educational needs itinerant teacher\nspecial needs peripatetic teacher\nspecial educational needs peripatetic teacher\nSEN visiting teacher\nvisiting teacher for special needs\nspecial educational needs visiting teacher\nSEN itinerant teacher\nSEN peripatetic teacher','\n',char(10)),'Special educational needs itinerant teachers instruct disabled or sick children in their homes. They are specialised teachers employed by (public) schools to teach those unable to physically attend school, but also to assist the student, the parents and the school in their communication. They also fulfil the function of a social school worker by helping the students and parents with a student''s potential behavioural issues and enforce, if necessary, school attendance regulations. In case of a possible physical (re)admission to school, visiting teachers advise the school regarding suitable classroom guidance strategies and advisable teaching methods to support the student and make the transition as agreeable as possible.','',replace('Excludes <a resource="http://data.europa.eu/esco/occupation/404a50e9-61ce-448d-a695-bbfc697a0727" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/404a50e9-61ce-448d-a695-bbfc697a0727"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">government minister</span></a>.\n Excludes people performing religious duties and travel to people''s homes.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/623958d6-775c-4586-b928-86582d9d1b69','key_16370','','','3122','3122.2','food production planner',replace('food production planning expert\nfood production planner\nfood production planning specialist\nfood manufacturing planner','\n',char(10)),'Food production planners prepare production plans, evaluate all the variables in the process and strive to ensure that production objectives be achieved.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/624986f9-21ef-4cdf-8cf6-8407a337ec2f','key_16371','','','3112','3112.10','surveying technician',replace('land survey technologist\ngeomatics technician\nengineering technician\narchaeological field technician\nsurveyor\nagricultural global positioning system mapper\nmapping technician\ntransport engineering technician\nsurvey CAD technician\ninstrument woman\nsurveying technician\nstereoplotter operator\ninstrument man\nland survey technician\ntopographic survey technician\nsurvey party chief\nsurvey technician','\n',char(10)),'Surveying technicians carry out technical surveying tasks. They assist surveyors, architects or engineers in surveying related technical tasks such as mapping land, creating construction drawings and operating precise measuring equipment','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/625d316c-2b93-4a9c-ada2-b19efa7c611b','key_16372','','','1324','1324.3.1.6.6','clothing and footwear distribution manager',replace('assistant clothing and footwear distribution manager\nclothing and footwear supply chain planner\nclothing and footwear logistics planner\nsenior clothing and footwear distribution manager\nclothing and footwear logistics manager\nclothing and footwear distribution manager\ntrainee clothing and footwear distribution manager\nclothing and footwear distribution planner\nclothing and footwear supply chain manager\ngraduate clothing and footwear distribution manager','\n',char(10)),'Clothing and footwear distribution managers plan the distribution of clothing and footwear to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6293a718-3fcd-433f-a010-0899821a51b7','key_16373','','','3112','3112.1.1','bridge inspector',replace('highway structures inspector\nbridgemaster\nbridges and structures inspector\nbridge inspector\nbridge engineer\ninspector of bridges\nstructures inspector','\n',char(10)),'Bridge inspectors inspect bridge structures for joint break, cracks, rust and other faults. They also carry out or organise the maintenance of the structures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6296faea-1909-4943-b0e0-662b43ad855d','key_16374','','','3351','3351.3','immigration officer',replace('customs inspector\nimmigration officer\npassport checking officer\nimmigration official\nborder force official\nimmigration guard\ncustoms and excise official\nborder force officer','\n',char(10)),'Immigration officers monitor the eligibility of people, food, electronic devices and merchandise entering a country via an entry point. They use surveillance methods and check identification and documents to ensure entry criteria and custom laws are complied with. They can also conduct interviews with prospective immigrants to verify eligibility and inspect cargo to identify and detect violations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/62975936-0853-4a5d-9f59-bf943aca3cc6','key_16375','','','3422','3422.4.6','swimming teacher',replace('swimming and diving teacher\nbaby swimming teacher\nswimming coach\nswimming teacher\nswimming instructor\nsenior swimming teacher\neducation swimming teacher\nswimming and diving coach\nshaw method swimming teacher\nswimming and diving instructor','\n',char(10)),'Swimming teachers train and advise groups or individuals on swimming. They plan trainings and teach different swimming styles such as front crawl, breaststroke and butterfly. They help to improve their students performance. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/62979364-6fac-41f2-87ca-202bca19a6ab','key_16376','','','3118','3118.3.5','computer-aided design operator',replace('CAM drafter\n3D CAM technician\ncomputer aided designer\ncomputer-aided manufacturing draftswoman\nCAD design designer\ncomputer-aided design operator\nCAD design specialist\ncomputer aided drafting and design technician\nCAD design technician\nCADD Designer\nCAD draughtsman\ncomputer aided design draughtsman\ncomputer-aided manufacturing draftsman\ncomputer aided design technician\nCAM equipment operative\n3D computer-aided manufacturing technician\nCAM draftsman\ncomputer aided drafting and design draftsman\ncomputer aided design designer\ncomputer-aided manufacturing equipment operator\nCAM draftswoman\ncomputer-aided manufacturing technician\ncomputer aided manufacturing operator\ncomputer aided industrial manufacturing operator\ncomputer aided design specialist\nCAD drafting and design technician\nCAD drafting and design draftsman\nCAM technician\nCAID operator\nCAM equipment operator\ncomputer-aided manufacturing drafter\nCAM operator\ncomputer-aided manufacturing equipment operative','\n',char(10)),'Computer-aided design operators use computer hardware and software in order to add the technical dimensions to computer aided design drawings. Computer-aided design operators ensure all additional aspects of the created images of products are accurate and realistic. They also calculate the amount of materials needed to manufacture the products. Later the finalised digital design is processed by computer-aided manufacturing machines that produce the finished product.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/62d3f108-ade2-44b5-beeb-d63d0b21268f','key_16377','','','2146','2146.9','mineral processing engineer',replace('mineral processing engineers\nsenior mineralogist\nmaterials expert\nsenior metallurgist\nsurface manager\nmineral processing engineer\nmineral engineer\nsenior processing engineer','\n',char(10)),'Mineral processing engineers develop and manage equipment and techniques to successfully process and refine valuable minerals from ore or raw mineral.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/62f53135-e091-4aa6-8895-5851d72271b9','key_16378','','','8160','8160.19','clarifier',replace('oil extractor operative\nclarifier machine controller\noil extractor operator\nclarifier\nclarifier equipment operator','\n',char(10)),'Clarifiers operate equipment to remove sediments and moisture from oleo and oils. They heat clarifying tank with steam and strainers for the clarification process. They remove foreign matters from the surface of hot oleo or oil stacks using skimmers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/63042e8f-dd59-47fe-87f3-3b2ce21f196a','key_16379','','','7223','7223.4.4','lathe and turning machine operator',replace('automatic spinning lathe setter\nlathe and turning machine operator\nturning machine operator\nCNC lathe hand\nCNC brake drum operator\nCNC lathe tender\ncnc lathe tender\nturning machine tender\nCNC lathe setter\nCNC lathe turner\nCNC tool lathe setter\nCNC turning machine setter\nautomatic spinning lathe operator\nCNC turning machine tender\ncomputer numerical controlled lathe and turning machine setter\ncomputer numerical controlled lathe and turning machine operator','\n',char(10)),'Lathe and turning machine operators set up, program and tend lathe and turning machines designed to cut excess metal from a metal workpiece using a hardened cutting tool moved by computer-controlled motors. They read lathe and turning machine blueprints and tooling instructions, perform regular machine maintenance, and make adjustments to the lathe controls, such as the depth of cuts and the rotation speed.','','Includes people using lathe and turning machines to cut both metal and plastic materials.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/631aaef2-2d67-45c6-93a6-1b1abdf57542','key_16380','','','4213','4213.1','pawnbroker',replace('deposit lender\nloaner\npawnshop operator\npawnshop owner\npawnbroker\npawn broker\npawn agent\npawnshop manager','\n',char(10)),'Pawnbrokers offer loans to clients by securing them with personal objects or items. They assess the personal items given in exchange for the loan, they determine their value and amount of loan available and keep track of inventory assets.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/631e504f-0db4-4e03-bf90-3f00d550170f','key_16381','','','3114','3114.1.10','sensor engineering technician',replace('assistant in sensor engineering\nelectronics sensor technician\nsensor technician\nsensor systems technician\nsensor engineering technician\nsensor design technician\ntechnician in electronic sensors\nsensor engineering assistant\ntechnician in sensor engineering\nsensor and actuator technician\nsensor development technician\nelectronic sensor technician\nelectronic sensor technologist','\n',char(10)),'Sensor engineering technicians collaborate with sensor engineers in the development of sensors, sensor systems, and products that are equipped with sensors. Their role is to build, test, maintain, and repair the sensor equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/632e61c8-4e4b-44c7-b213-6dd5a174b143','key_16382','','','1324','1324.3.1','distribution manager',replace('senior distribution manager\ngraduate distribution planner\ndistribution manager\ntrainee logistics manager\ngraduate distribution manager\nassistant supply chain manager\nassistant distribution manager\ngraduate supply chain manager\ntrainee supply chain manager\nassistant distribution planner\nassistant traffic office manager\ntrainee distribution manager\nsenior distribution planner\ntrainee traffic office manager\ntrainee distribution planner\nassistant logistics manager\ngraduate traffic office manager\nsenior traffic office manager\nsenior logistics manager\ngraduate logistics manager\nsenior supply chain manager','\n',char(10)),'Distribution managers plan the distribution of goods to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6336c205-46bd-42f6-9e67-6b0be6e7618c','key_16383','','','2114','2114.1.4','hydrogeologist',replace('hydrogeologist\nhydrogeological consultant\nconsultant hydrogeologist\npore pressure engineer\nhydrologist\nconsultant hydrologist\nformation evaluation engineer','\n',char(10)),'Hydrogeologists in mining study the distribution, quality and flow of water in order to keep mine workings free of nuisance water and to ensure an adequate supply of process water. They supply and evaluate information which will protect ground and surface water from contamination.','','Excludes <a resource="http://data.europa.eu/esco/occupation/55167849-82cf-484e-b818-fa702a4e69e9" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/55167849-82cf-484e-b818-fa702a4e69e9"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">hydrologists</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6353b3e8-e8f2-4ecb-b230-186927139892','key_16384','','','4132','4132.1','data entry clerk',replace('data-entry clerk\ndata steward\ndata entry officer\npayment entry clerk\ndata-entry officer\nstatistical clerk\ndata entry operator\ndata entry inspector\ndata entry clerk','\n',char(10)),'Data entry clerks update, maintain and retrieve information held on computer systems. They prepare source data for computer entry by compiling and sorting information, process customer and account source documents by reviewing data for deficiencies and verify entered customer and account data.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/63c92723-ee86-42cb-89bf-d93b65398bef','key_16385','','','1324','1324.3.2.34','import export manager in watches and jewellery',replace('senior import export manager in watches and jewellery\nsenior import manager in watches and jewellery\nsenior export manager in watches and jewellery\ncustoms compliance manager in watches and jewellery\ngraduate export manager in watches and jewellery\ngraduate import export manager in watches and jewellery\ntrainee import manager in watches and jewellery\ninternational trade compliance manager in watches and jewellery\nassistant export manager in watches and jewellery\nimport export manager in watches and jewelry\nassistant import export manager in watches and jewellery\nassistant import manager in watches and jewellery\ntrainee import export manager in watches and jewellery\ngraduate import manager in watches and jewellery\ntrainee export manager in watches and jewellery\ninternational trade manager in watches and jewellery','\n',char(10)),'Import export managers in watches and jewellery install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/63d99701-1a6c-4c57-87c8-4b0d5c0b4408','key_16386','','','5223','5223.7.5','beverages specialised seller',replace('alcoholic beverages store specialised salesperson\nalcoholic drinks store specialised seller\noff-licence store specialised seller\nsoft and alcoholic beverages store specialised seller\noff-sales store specialised salesperson\nbeverages store specialised salesperson\noff-sales store specialised seller\noff-licence specialised salesperson\nbeverages and alcoholic drinks store specialised seller\nbeverages specialized seller\nalcoholic drinks store specialised salesperson\noff-licence store specialised salesperson\nbeverages store specialised seller\nsoft and alcoholic beverages store specialised salesperson\noff-licence specialised seller\nalcoholic beverages store specialised seller\nbeverages and alcoholic drinks store specialised salesperson','\n',char(10)),'Beverages specialised sellers sell beverages in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6426ada1-4d8c-4612-99e3-91bc569805ee','key_16387','','','1321','1321.2','manufacturing manager',replace('manufacturing plant manager\nmanufacturing production manager\nindustrial production supervisor\nmanufacturing director\nmanufacturing manager','\n',char(10)),'Manufacturing managers plan, oversee and direct the manufacturing process in an organisation. They ensure products and services are efficiently produced within the timeframe and budget given.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/643a2225-6a01-4899-81b0-e867ca09b9c9','key_16388','','','2422','2422.5','government planning inspector',replace('planning inspector\nexamining inspector\nnational infrastructure examining inspector\nplanning and development surveyor\ngovernment planning inspector\nhousing and planning inspector\nplanning officer','\n',char(10)),'Government planning inspectors monitor the development and implementation of government plans and policies, as well as processing planning and policy proposals, and performing inspections of planning procedures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/64421dde-df97-4c42-a25a-b3bc55b39ad6','key_16389','','','3331','3331.2.1.18','import export specialist in household goods',replace('assistant import export specialist in household goods\nassistant import specialist in household goods\ngraduate export specialist in household goods\ntrainee import export specialist in household goods\ncustoms compliance specialist in household goods\ngraduate import specialist in household goods\ninternational trade compliance specialist in household goods\ngraduate import export specialist in household goods\nimport export specialist in household goods\nsenior import specialist in household goods\nsenior import export specialist in household goods\ntrainee import specialist in household goods\nsenior export specialist in household goods\nassistant export specialist in household goods\ntrainee export specialist in household goods','\n',char(10)),'Import export specialists in household goods have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6474e4d4-e3a6-4e88-83ee-971ebbf954e9','key_16390','','','3152','3152.2','helmsman',replace('submarine helmsman\nhelmsman\nsteerswoman\nhelm\nhelmswoman\nsteersman','\n',char(10)),'Helmsmen are members of the crew on the highest rank of the operational level on an inland vessel. They perform a variety of duties concerned with the operation and upkeep of deck department areas, the engine and other equipment, mooring and unmooring, as well as the steering of the ship as the main task.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6482664d-48f4-4ff4-9346-584ac0e2f729','key_16391','','','1321','1321.2.1.4','footwear production manager',replace('footwear and sportswear production manager\nfootwear production manager\nproduction planning manager\nfashion production manager\nhead of footwear production','\n',char(10)),'Footwear production managers plan, distribute, and coordinate all necessary activities of the different footwear manufacturing phases ensuring the adherence to quality standards and production and productivity pre-defined goals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/64a68a43-24be-4929-95ce-5b14617199d1','key_16392','','','7315','7315.1','glass-blower',replace('glass-blower\nglass designer\nglass designer and maker\ndecorative glass artist\nglass designer & maker\nglass designer & blower\nglass blower\nglass designer and blower\nscientific glass maker\nglassblower','\n',char(10)),'Glass-blowers design, produce and decorate glass artefacts such as stained glass windows, mirrors and architectural glass. Some glass-blowers specialise in restoring, renovating and repairing original pieces. They may also work as scientific glass-blowers, designing and repairing laboratory glass.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/64b136da-aedf-48a2-96a0-72eb850bfcd0','key_16393','','','1349','1349.20','rescue centre manager',replace('rescue centre coordinator\nrescue coordination centre manager\nrescue centre supervisor\nrescue center manager','\n',char(10)),'Rescue centre managers supervise operations of a rescue centre, performing administrative duties and supervision of staff. They ensure that the centre operates according to policies and staff perform rescue missions in a safe, efficient and compliant manner.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/64c180d0-029b-4dc7-b582-77899d171186','key_16394','','','8160','8160.5','blender operator',replace('manufacturers of soft-drinks\nsoft drinks manufacturer\nbeverage manufacturer\nsoft drinks supplier\ntank tender\nsoft drinks producer\nsoft drinks retailer\nblender operator','\n',char(10)),'Blender operators produce non-alcoholic flavoured waters by managing the administration of a large selection of ingredients to water. They handle and administer ingredients such as sugar, fruits juices, vegetable juices, syrups based on fruit or herbs, natural flavours, synthetic food additives like artificial sweeteners, colours, preservatives, acidity regulators, vitamins, minerals, and carbon dioxide. They manage the quantities depending on the product.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/64c7d461-152c-477f-a8e2-c2c537e9d617','key_16395','','','3513','3513.2','ICT network technician',replace('ICT network technician\nIT network technician\nnetwork maintenance professional\nassistant network administrator\nnetwork technician\nsystem engineer\nnetwork engineer\nnetwork technicians\nnetworks technician','\n',char(10)),'ICT network technicians install, maintain and troubleshoot networks, data communications equipment and network installed devices such as printers and storage area networks. They also analyse and fix network-related problems reported by users.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/64e38ce7-3901-4261-bfee-77c7a77397f2','key_16396','','','1213','1213.2','policy manager',replace('technical services manager\npolicy coordinator\npolicy manager\npolicy advocacy manager\ncompliance coordinator\nsustainability manager\nenvironmental policy manager\nethics manager\npolicy advocacy coordinator\ncompliance director\nbusiness ethicist\nadvocacy coordinator','\n',char(10)),'Policy managers are responsible for managing the development of policy programs and ensuring that the strategic objectives of the organization are met. They oversee the production of policy positions, as well as the organization''s campaign and advocacy work in fields such as environmental, ethics, quality, transparency, and sustainability.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/64e909ad-1bd0-493c-a8dc-1afbed0c9c2f','key_16397','','','3258','3258.1','emergency ambulance driver',replace('technician in emergency medical care\nambulance emergency medical technician\nassistant in emergency care\nambulance technician\nECA\nemergency medical vehicle assistant\nmedical emergency assistant\nambulance emergency care assistant\nemergency ambulance driver','\n',char(10)),'Emergency ambulance drivers use emergency vehicles to respond to medical emergencies and support the work of paramedics, move patients safely, take note of changes in the patient''s vital signs and report to the paramedics in charge, ensuring the medical equipment is well stored, transported and functional, under supervision and on order of a doctor of medicine.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/650c4c7b-8cd8-4671-a38e-6e37e45e0c8e','key_16398','','','3324','3324.4.20','wholesale merchant in machinery, industrial equipment, ships and aircraft',replace('senior wholesale merchant in machinery, industrial equipment, ships and aircraft\nB2B sales consultant in machinery, industrial equipment, ships and aircraft\nbusiness to business seller in machinery, industrial equipment, ships and aircraft\ngraduate wholesale merchant in machinery, industrial equipment, ships and aircraft\nwholesale merchant in machinery, industrial equipment, ships and aircraft\nwholesale merchandiser in machinery, industrial equipment, ships and aircraft\nassistant wholesale merchant in machinery, industrial equipment, ships and aircraft\nwholesale trader in machinery, industrial equipment, ships and aircraft\nbusiness to business sales consultant in machinery, industrial equipment, ships and aircraft\nwholesaler in machinery, industrial equipment, ships and aircraft\nwholesale buyer in machinery, industrial equipment, ships and aircraft\ntrainee wholesale merchant in machinery, industrial equipment, ships and aircraft','\n',char(10)),'Wholesale merchants in machinery, industrial equipment, ships and aircraft investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/652b847f-837a-4a08-bd5e-36397fe56455','key_16399','','','1321','1321.2.2.3','textile quality manager',replace('textile quality supervisor\ntextile quality manager\ntextile quality control manager','\n',char(10)),'Textile quality managers implement, manage and promote quality systems. They make sure that the textile products adhere to the quality standards of the organisation. Textile quality managers therefore inspect textile production lines and products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/653374a9-a96e-4c5b-849f-62ae67b0ed62','key_16400','','','2359','2359.12','prison instructor',replace('prison training officer\nprison teacher\nprison and correctional instructor\ninstructor in prisons\ninstructional officer\ncorrectional training instructor\nteacher in prisons\nprison educator\nprison instructor','\n',char(10)),'Prison instructors educate legal offenders, including prisoners, on social rehabilitation and correctional behaviour. They assist prisoners in gaining skills which could facilitate their reintegration into society and increase their chances at finding employment after release. Prison instructors analyse the students'' individual learning needs, plan and prepare teaching materials and sessions and update their learning records. They also ensure the working area and materials are safe from danger and accounted for, and supervise the students at all times.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/654ce97c-9ed1-4614-bc76-36f4a1f67557','key_16401','','','2145','2145.3','synthetic materials engineer',replace('polymers quality engineer\nsynthetic materials quality engineer\nplastics development engineer\npolymers research engineer\nplastics research engineer\nplastics engineer\nplastics production engineer\nsynthetic materials engineer\npolymers production engineer\nsynthetic materials production engineer\nsynthetic materials research engineer\nplastics quality engineer\nsynthetic materials development engineer\npolymers engineer\npolymers development engineer','\n',char(10)),'Synthetic materials engineers develop new synthetic materials processes or improve existing ones. They design and construct installations and machines for the production of synthetic materials and examine samples of raw materials in order to ensure quality.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6565dc02-14e4-4819-b99c-44c352000f43','key_16402','','','8212','8212.3','electronic equipment assembler',replace('electronics assembler\nelectronic equipment products production operative\nelectrical and electronic equipment assembler\nassembler of electronics\nelectronic equipment fabricator\nelectrical and electronic equipment fabricator\nelectronic assembler\nelectronic equipment products assembler\nelectronic equipment products assembly worker\nelectronic equipment production worker\nfabricator of electronic equipment\nelectronic equipment assembler\nelectronic equipment products production worker\nassembler electronic equipment products\nassembler of electronic equipment\nelectronic equipment products wirer/assembler\nelectronic equipment products assembly operative\nelectronic equipment production operative\nelectronic equipment products wirer','\n',char(10)),'Electronic equipment assemblers are responsible for the assembly of electronic equipment and systems. They assemble electronic components and wiring according to blueprints and assembly drawings. They may assist in quality inspection and equipment maintenance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/656674ac-9d4e-4a91-963f-c0b6edfd55a4','key_16403','','','4323','4323.4','bus route supervisor',replace('bus dispatcher\nbus route supervisor\ntransit supervisor\ncoach route supervisor\nsupervisor of bus routes\nbus route manager\nbus service manager\ncoach route manager','\n',char(10)),replace('Bus route supervisors coordinate vehicle movements, routes and drivers, and may supervise loading, unloading, and checking of baggage or express shipped by bus. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/657eeac9-5bb9-449d-a353-aa7d5f47e2b5','key_16404','','','3116','3116.1.3','colour sampling technician',replace('leather dyer\ncolor kitchen worker\ncolour sampling technicians\npelt dyer\ndyer\ncolour kitchen technician\ncolor sampling technician\ncolour kitchen worker\ntanning solution preparer','\n',char(10)),'Colour sampling technicians prepare recipes of colours and dyeing mixes. They ensure consistency in colour while using materials from different sources.','','Includes people performing activities in colour kitchen, spraying and coating lines.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6581aa0d-c807-4091-84cc-bc041f962ab1','key_16405','','','2652','2652.3.2','singer',replace('lead singer\nmusical theatre performer\nsoprano singer\nopera solo singer\njazz singer\nmusical theater actor\nmusical theater performer\nsession singer\nopera singer\nsolo singer\nrapper\nopera choir singer\nbaritone singer\nsinger songwriter\nmusical theater actress\nsinger\nmusical theatre actor\nvocalist\nmusical theatre actress\nbass voice singer\ncontralto singer\nchoir singer\njingle singer\nlead vocalist\ntenor singer','\n',char(10)),'Singers are professional musicians, skilled in the use of their voice as a musical instrument, with different vocal ranges. They perform for live audiences and for recordings in different musical genres.','','Excludes people using <a resource="http://data.europa.eu/esco/skill/29d4aaba-99f9-4733-bd74-ad558f2ae3a7" typeOf="http://data.europa.eu/esco/model#Skill" href="http://data.europa.eu/esco/skill/29d4aaba-99f9-4733-bd74-ad558f2ae3a7"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">musical instruments</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/659b0ffd-337c-4a1f-82e3-2730812279de','key_16406','','','2412','2412.11','venture capitalist',replace('venture capitalist\nseed capital investor\nfinancier\nangel investor\ninvestor','\n',char(10)),'Venture capitalists invest in young or small start-up companies by providing private funding. They research potential markets and particular product opportunities to help business owners develop or expand a business. They provide business advice, technical expertise, and network contacts based on their experience and activities. They do not assume executive managerial positions within the company, but have a say in its strategic direction.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/65a9366c-d4d3-484d-a577-c63d335987da','key_16407','','','5164','5164.2.1.1.1','guide dog instructor',replace('GDMI\nguide dog trainer\nguide dog mobility instructor\nhearing dog instructor\nguide dog for the blind instructor\nassistance dog instructor\nguide dog instructor\nseeing-eye dog trainer','\n',char(10)),'Guide dog instructors train dogs to be responsible in guiding blind people to travel effectively. They plan the training sessions, match guide dogs with their clients and ensure overall routine care of the training dogs. Guide dog instructors also provide advice to the blind people in the use of techniques that facilitate dogs'' travel skills and mobility.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/65b88636-2ebb-4049-b509-2f6da5d6ea1c','key_16408','','','8143','8143.3','envelope maker',replace('envelope machine technician\nenvelope making machine specialist\nenvelope maker\nenvelope making machine operator\nenvelope machine specialist\nenvelope making machine worker\nenvelope machine operator\nenvelope machine worker\nenvelope machine operative\nenvelope making machine technician','\n',char(10)),'Envelope makers tend a machine that takes in paper and executes the steps to creat envelopes: cut and fold the paper and glue it, then apply a weaker food-grade glue to the flap of the envelope for the consumer to seal it.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/65bd2342-f7de-4932-9164-eb480e35a791','key_16409','','','9212','9212.4','livestock worker',replace('livestock worker\nanimal husbandry worker\nhusbandry worker\nstable worker\nstockman\nfarm animal worker','\n',char(10)),'Livestock workers maintain the health and welfare of animals. They oversee the breeding/production and day-to-day care such as feeding and watering of animals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/65beb253-61b0-4f8f-9c3c-49419913fd18','key_16410','','','1324','1324.3.2.9','import export manager in dairy products and edible oils',replace('assistant import manager in dairy products and edible oils\ninternational trade compliance manager in dairy products and edible oils\nassistant export manager in dairy products and edible oils\ntrainee import export manager in dairy products and edible oils\ninternational trade manager in dairy products and edible oils\nsenior import export manager in dairy products and edible oils\ngraduate import export manager in dairy products and edible oils\nassistant import export manager in dairy products and edible oils\ntrainee import manager in dairy products and edible oils\ntrainee export manager in dairy products and edible oils\ngraduate export manager in dairy products and edible oils\nsenior export manager in dairy products and edible oils\nimport export manager in dairy products and edible oils\ncustoms compliance manager in dairy products and edible oils\ngraduate import manager in dairy products and edible oils\nsenior import manager in dairy products and edible oils','\n',char(10)),'Import export managers in dairy products and edible oils install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/65fb0b53-b8c4-4839-b099-c80b6de5a158','key_16411','','','2422','2422.3','employment programme coordinator',replace('program coordinator employment\nprogramme coordinator employment\nemployment programme manager\njob creation advisor\nemployment program manager\nemployment program coordinator\nreturn to work advisor','\n',char(10)),'Employment programme coordinators research and develop employment programmes and policies to improve employment standards and reduce issues such as unemployment. They supervise promotion of policy plans and coordinate implementation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6630d23f-4c8e-4855-abb0-8ca1e72c74ba','key_16412','','','9212','9212.2','equine worker',replace('horse and pony worker\nequine worker\nhorse and pony technician\nhorse worker\nequine technician','\n',char(10)),'Equine workers provide caring activities for horses and ponies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6648103b-6984-4880-b8c3-eacb9b0d2bc9','key_16413','','','3324','3324.4.15','wholesale merchant in furniture, carpets and lighting equipment',replace('business to business seller in  furniture, carpets and lighting equipment\nassistant wholesale merchant in  furniture, carpets and lighting equipment\nsenior wholesale merchant in  furniture, carpets and lighting equipment\nwholesaler in  furniture, carpets and lighting equipment\ngraduate wholesale merchant in  furniture, carpets and lighting equipment\nbusiness to business sales consultant in  furniture, carpets and lighting equipment\nwholesale buyer in  furniture, carpets and lighting equipment\nwholesale merchant in furniture, carpets and lighting equipment\ntrainee wholesale merchant in  furniture, carpets and lighting equipment\nB2B sales consultant in  furniture, carpets and lighting equipment\nwholesale merchandiser in  furniture, carpets and lighting equipment\nwholesale trader in  furniture, carpets and lighting equipment','\n',char(10)),'Wholesale merchants in furniture, carpets and lighting equipment investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/664f7498-8e4b-48f8-9fe2-436a692f1204','key_16414','','','1420','1420.4.21','floor and wall coverings shop manager',replace('interior fitting store manager\ninterior decoration store manager\ncarpet store manager\nfloor and wall coverings shop manager\ncarpet and rug shop manager\ncarpet and rug store manager\nwallpaper shop manager\nwallpaper store manager\nwallpaper and accessories store manager\ncarpet superstore manager\nwallpaper and accessories shop manager\nfloor and wall store manager\ninterior decoration shop manager\ninterior fitting shop manager','\n',char(10)),'Floor and wall coverings shop managers assume responsibility for activities and staff in specialised shops for wall and floor coverings.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/665266cb-50dd-44a5-a920-ab04c67e251a','key_16415','','','3413','3413.2','pastoral worker',replace('parish worker\nlay worker\nchurch pastoral worker\nministry pastoral worker\npastoral worker\nreligious worker','\n',char(10)),'Pastoral workers support religious communities. They provide spiritual education and guidance and implement programmes such as charity works and religious rites. Pastoral workers also assist ministers and help participants in the religious community with social, cultural or emotional problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/66527cdc-b7a5-4996-9448-7e327b68d08d','key_16416','','','2310','2310.1.4','art studies lecturer',replace('higher education art studies teacher\nart studies instructor\nart studies professor\narts studies lector\nspecialist educator in art studies\nart studies lecturer\nuniversity art studies lecturer\narts studies lecturer\nlecturer in art studies\nuniversity lecturer in art studies\nsenior lecturer in art studies\nart studies teacher\nprofessor of art studies\nart studies docent\nart studies lector','\n',char(10)),'Art studies lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, art studies, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, grading papers and exams and leading review and feedback sessions for the students. They also conduct academic research in their field of arts studies, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/66542dd6-05a6-4d9e-97ff-3f4858c31ff9','key_16417','','','3431','3431.1','photographer',replace('studio photographer\nschool photographer\ndocumentary photographer\ncorporate photographer\nsports photographer\nvideo photographer\ninterior photographer\nindustrial photographer\nphotographer\npress photographer\nscene photographer\nstill shooting photographer\nwildlife photographer\nstill life photographer\nart photographer\nphotography assistant\nreportage photographer\nphotographer retoucher\ntechnical photographer\nunderwater photographer\nphotography studio manager\nfood photographer\narchitecture photographer\nportrait photographer\nadvertising photographer\nproduct photographer\nfashion photographer\nphoto designer\nphotography-based artist\ngraphical industry photographer\nphotographic artist\nmicrophotographer\nportrayist\naerial photographer\nsocial photographer\nillustration photographer\nbackstage photographer\ntravel photographer\nscientific photographer\nevent photographer\nwedding photographer\nlandscape photographer\nitinerant photographer\nculinary photographer\nmedical photographer\nphotography technician\ncommercial photographer\ncultural photographer','\n',char(10)),'Photographers take pictures using digital or film cameras and equipment. Photographers may develop negatives or use computer software in order to produce finished images and prints.','',replace('Includes scientific photographers. \n Includes aerial photographers. \n Excludes <a resource="http://data.europa.eu/esco/occupation/480d2853-db07-43d4-847f-7ffdd8d368f5" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/480d2853-db07-43d4-847f-7ffdd8d368f5"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">photojournalist</span></a>.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6656337f-67eb-4f48-b1e4-ecccdfa44dd2','key_16418','','','7412','7412.2','automotive electrician',replace('automotive electrician\nauto electrical technician\nautoelectrical technician\nmotor vehicle electrical technician\nvehicle body electrician\ntruck electrician\nvan electrician\nelectrical installation technician\nauto electrician\nmotor vehicle electrician\nmotor vehicle technician, auto electrics\ncar electrician\nauto technician','\n',char(10)),'Automotive electricians install, maintain and repair electrical and electronic systems in motor vehicles such as air conditioning systems, lamps, radios, heating systems, batteries, electrical wiring and alternators. They use diagnostic testing equipment to inspect vehicles and find faults. To do repair work, they use hand tools and specialised electrical instruments and machines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6662e56e-bc3d-4d9b-bed2-79d510d7c4ff','key_16419','','','3332','3332.2.2','wedding planner',replace('special events planner\nwedding planner\nmatrimony planner\nwedding organizer\nevents planner\nwedding consultant\nspecial events organizer','\n',char(10)),'Wedding planners assist with all logistical details required concerning their client''s wedding ceremony. Based on their client''s requirements, they make arrangements for floral decorations, wedding venue and catering, guest invitations, etc., coordinating activities both before and during the wedding.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6667bf29-a3e3-4513-a71b-82c67206045b','key_16420','','','3412','3412.4.13','residential home young people care worker',replace('residential home young people care worker\nresidential support worker (children)\nresidential support worker (young adults)\nsupport worker (young people in care)\nresidential support worker (young people)\nresidential home adolescent care worker\nchildren''s services worker','\n',char(10)),'Residential home young people care workers provide assistance and support to young people who face complex emotional needs expressed in challenging behaviours. They support young adults with learning disabilities coping with school, encourage them to household activities and help them to take responsibility.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/666846b2-01ce-43f6-8cb7-f3be09fac4a2','key_16421','','','2619','2619.11','ombudsman',replace('children''s ombudsman\nombudsman\nombudswoman\ninformation commissioner','\n',char(10)),'Ombudsmen resolve disputes between two parties where there is a power imbalance, as an impartial mediator. They interview the parties involved and investigate the case in order to come to a resolution beneficial to both parties. They advise on conflict resolution and offer support to clients. The claims are mostly against public institutions and authorities.','','Excludes <a resource="http://data.europa.eu/esco/occupation/403be630-8d1e-43b7-9f07-098aca5bb689" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/403be630-8d1e-43b7-9f07-098aca5bb689"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">mediator</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/668943cc-c454-4a1b-a296-2fcb524a6222','key_16422','','','3111','3111.5','hydrographic surveying technician',replace('mapping technician\noceanographic scientist\nhydrographic surveyor\nhydrographic surveying technician\noceanographer\nmarine surveyor\nsurveying technician\noceanographic surveyor\nmarine surveying technician\nhydrographic survey technologist','\n',char(10)),'Hydrographic surveying technicians perform oceanographic and surveying operations in marine environments. They assist hydrographic surveyors, using specialised equipment to map and study underwater topography and morphology of bodies of water. They assist in the installation and deployment of hydrographic and surveying equipment and report about their work.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/669353c2-3f5a-4bc4-939b-6afb6a0c7d13','key_16423','','','7223','7223.11','metal nibbling operator',replace('metal nibbling operator\nhand held nibbler drill operator\nhand held nibbling drill operator\nhand held nibbler drill technician\nnibbling drill operative\nautomatic nibbling drill technician\nautomatic nibbling drill operator\nhand held nibbling drill operative\nnibbling drill technician\nautomatic nibbling drill operative\nhand held nibbler drill operative\nhand held nibbling drill technician\nnibbling drill operator','\n',char(10)),'Metal nibbling operators cut detailed patterns from metal surfaces by using manual or powered metal nibblers, such as a handheld nibbling drill or a nibbling machine.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/66ca3643-e6f3-4c75-9300-40d813185b3e','key_16424','','','3259','3259.3','chiropractic assistant',replace('chiropractic assistant\nassistant neuromusculoskeletal therapist\nassistant to chiropractic\nchiropractic''s assistant\nassistant osteopath\nassistant etiopath\nassistant chiropractic\nassistant to licensed chiropractor\nassistant musculoskeletal therapist\nchiropractic support worker','\n',char(10)),'Chiropractic assistants perform routine and administrative tasks to support patient care and work under the sole direction and supervision of a Chiropractor or Specialized Chiropractor. They may interview patients, keep records of the case history, assist Chiropractors or Specialized Chiropractors in providing patients examination and perform administrative tasks. Chiropractic assistants work within defined contexts using agreed protocols and procedures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/66d22b81-1b5e-42a1-b904-ac67b4a896c6','key_16425','','','8112','8112.2','mineral processing operator',replace('blender tender\nfurnace charger\ncalciner operator\nmineral processing technican\nore separator tender\nmineral processing operator\nprocess operator\nbrine maker\ngravel processing operator\nprocess technician\nmineral and stone processing dryer operator\nsalt processing operator\nsand processing operator\ncement mixer\ncoal preparation plant operator\nprocess plant operator','\n',char(10)),'Mineral processing operators operate a variety of plants and equipment to convert raw materials into marketable products. They provide the appropriate information on the process to the control room.','','Includes people performing washing, crushing and sizing, separating, sampling, mineral refining and milling.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/66dc9349-6ca3-4ce4-9865-40686234d752','key_16426','','','3123','3123.1.21','roofing supervisor',replace('foreman roofer\nhouse roofing supervisor\nroof tiling supervisor\nroofing carpentry supervisor\nfelt roofing supervisor\nroofing supervisor\nroof slating supervisor\ncladding installation supervisor\ntinsmith supervisor\nasphalt roofing supervisor','\n',char(10)),'Roofing supervisors monitor the work on roofing a building. They assign tasks and take quick decisions to resolve problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/66fb9704-0d56-4673-8e69-7823816b73e1','key_16427','','','2431','2431.11','market research analyst',replace('marketing research coordinator\nmarketing research analyst\nmarket analyst\nmarket research analyst\nmarketing researcher\nmarket researcher\nmarket research manager\nmarket research specialist\ncustomer insight manager','\n',char(10)),'Market research analysts collect the information gathered in the market research and study it to draw conclusions. They define the potential customers of a product, the target group and the way they can be reached. Market research analysts analyse the position of products in the market from different perspectives such as features, prices and competitors. They analyse cross selling and interdependencies between different products and their placement. Market research analysts prepare information helpful for the development of marketing strategies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6724e558-2d75-4116-a473-91a4c14d4c69','key_16428','','','3353','3353.2.1','pensions administrator',replace('pension plans clerk\npension administrator\npension agent\npensions administrator\npension scheme administrator\npensions technician\ninvestments administrator\nadministrator of pensions\npensions administration officer\nresponsible for pensions administrations\npension fund administrator','\n',char(10)),'Pensions administrators perform administrative duties in the management of pension schemes, ensuring the correct calculation of client''s pension benefits, compliance with legal requirements, drafting reports and communicating relevant information to customers. They work either in the private or the public sector.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6725a5bb-b79d-4128-9494-089389ae2a48','key_16429','','','8160','8160.9','cacao bean roaster',replace('cacao bean roaster\ncocoa roaster\ncocoa bean processor\nbean roaster\ncocoa processor','\n',char(10)),'Cacao bean roasters set up and operate cacao processing equipment such as continuous roasters, crackers fanners, drying and grinding equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6729aa5a-285f-4baf-a466-c2ccd2f2d96f','key_16430','','','7313','7313.1.7','precious stone setter',replace('precious stone setter\ndiamond setter\nprecious stone craftworker\nprecious stone craftswoman\ngem stone setter\ngem setter\njewelery stone setter\ngemsetter\njewellery stone setter\nprecious stone craftsman\ngemstone setter','\n',char(10)),'Precious stone setters use tools to insert diamonds and other gemstones into jewellery settings according to specifications. The setting of the gemstone depends on its size and its shape.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/673f3266-ab1a-447d-9b28-fc5838dc07e3','key_16431','','','3257','3257.5','occupational health and safety inspector',replace('occupational health and safety inspector\nfactory inspector\nHSE inspector\nregulatory health and safety inspector\nenvironmental health inspector\nenvironmental health officer\nhealth and safety inspector\nworkplace health and safety inspector\nsafety officer','\n',char(10)),'Occupational health and safety inspectors perform workplace audits to ensure compliance with government and environment legislation. They also investigate work accidents. Occupational health and safety inspectors interview employees to ensure the work environment is compliant with health and safety rules, inspect the physical work site and analyse legal paperwork.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/67aee472-533b-4569-a7a5-e7bc34b2cdce','key_16432','','','1324','1324.3.1.6.31','watches and jewellery distribution manager',replace('trainee watches and jewellery distribution manager\nwatches and jewellery logistics planner\nassistant watches and jewellery distribution manager\nwatches and jewellery supply chain manager\nwatches and jewelry distribution manager\ngraduate watches and jewellery distribution manager\nwatches and jewellery distribution planner\nwatches and jewellery logistics manager\nsenior watches and jewellery distribution manager\nwatches and jewellery supply chain planner','\n',char(10)),'Watches and jewellery distribution managers plan the distribution of watches and jewellery to various points of sales.Watches and jewellery distribution managers plan the distribution of watches and jewellery to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/67b31696-9722-4b41-84d4-b25657266d59','key_16433','','','8219','8219.5','jewellery assembler',replace('jewelry craftsperson\njewellery craftsperson\njewellery craftswoman\nbrooch assembler\nassembler jewelry\nearring assembler\njewellery bench worker\njewellery craftsman\nnecklace assembler\njewelry assembler\njewelry craftsman\njewelry bench worker\nassembler of jewelry\njewelry craftswoman\nchain assembler\nbracelet assembler\njewelry repairer\nassembler of jewellery\nassembler jewellery','\n',char(10)),'Jewellery assemblers assemble jewelry parts to form different types of jewellery such as bracelets, necklaces or earrings. They grip links with pliers or position the link in the slot of a linking ring, twist link joints open using pliers, attach all parts together and repair broken chains.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/67d8c64f-e0d9-4e0b-be8b-510f9e20e698','key_16434','','','3423','3423.1.2','outdoor animator',replace('outdoor instructor\nclimbing teacher\nski teacher\nkayak & canoe instructor\nrafting teacher\noutdoor animator\nquad bike teacher\nkayak and canoe instructor\nriding teacher\noutdoor adventure instructor\noutdoor activity instructor\nski instructor\nrafting instructor\ncanoeing teacher\nquad bike instructor\noutdoor recreation instructor\nclimbing instructor\npaddlesports instructor\noutdoor activities trainer\nriding instructor','\n',char(10)),replace('Outdoor animators are responsible for planning and organising outdoor activities. They may at times be involved in aspects of administration, front office tasks and tasks related to the activity base and maintenance of equipment. The workplace of an outdoor animator is mostly “in the field”, but can also take place indoors. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/67f12d6c-295f-4315-8c5c-8e477c7f7019','key_16435','','','8160','8160.38','honey extractor',replace('honey extraction operative\nextractor\nhoney production worker\nhoney extractor\nhoney process worker','\n',char(10)),replace('Honey extractors operate machines to extract liquid honey from honeycombs. They place decapped honeycombs in honey-extracting machine baskets to empty honeycombs. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/680831bb-64fc-4fc8-ae58-c450327d9700','key_16436','','','1219','1219.5','manufacturing facility manager',replace('manufacturing production manager\nmanufacturing facility manager\nmanufacturing director\nmanufacturing plant manager\nmanufacturing facility maintenance manager\nmanufacturing coordinator','\n',char(10)),'Manufacturing facility managers foresee the maintenance and routine operational planning of buildings intended to be used for manufacturing activities. They control and manage health and safety procedures, supervise the work of contractors, plan and handle buildings maintenance operations, fire safety and security issues, and oversee buildings'' cleaning activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6818c837-072a-4120-b913-bd360b7b14d0','key_16437','','','2141','2141.4.1','manufacturing engineer',replace('manufacturing engineering adviser\nmanufacturing engineer\nmanufacturing engineering consultant\nmanufacturing technology engineering specialist\nmanufacture engineer\nmanufacturing technology engineering consultant\nmanufacturing technology engineering adviser\nmanufacturing technology engineering expert\nMfgE\nmanufacturing engineering expert\nmanufacturing engineering specialist\nmanufacturing technology engineer\nmanufacturing process engineer','\n',char(10)),'Manufacturing engineers design manufacturing processes for different kinds of production processes. They integrate those specificities and constraints posed by the industry or the product being produced with general and wide-spread manufacturing engineering principles into the design and planification of manufacturing processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6833077e-e521-4605-bc1a-893f4b3d811c','key_16438','','','2163','2163.4','textile product developer',replace('print design development assistant\ntextile product specialist\ntextile product development engineer\ntextile business developer\ntechnical application manager\nfabric product development specialist\ntextile product engineer\ntextile and apparel product developer\ntextile product developer','\n',char(10)),'Textile product developers innovate and perform product design of apparel textiles, home textiles, and technical textiles (e.g. agriculture, safety, construction, medicine, mobile tech, environmental protection, sports, etc.). They apply scientific and technical principles to develop innovative textile products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6837611a-7093-40fb-9e7f-74947bf6f54f','key_16439','','','8181','8181.3','clay kiln burner',replace('retort kiln operator\nkiln burner\nroofing tile kiln operative\nretort kiln operative\nclay kiln burner\ngas kiln operative\nfield kiln operator\ngas kiln operator\nretort kiln burner\nkilnman\nbrick kiln operator\nroofing tile burner\nbrick kiln operative\nbrick kiln burner\nperiodic kiln burner\ndry kiln operator\ndry kiln burner\ndry kiln operative\nkilnwoman\nfield kiln operative\nroofing tile kiln operator\nfield kiln burner','\n',char(10)),'Clay kiln burners bake clay products such as brick, sewer pipe or tiles using periodic or tunnel kilns. They regulate valves, observe thermometers, watch for fluctuations, and maintain the kilns.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/68450507-b4a0-40e0-895b-385fe705be10','key_16440','','','4223','4223.1.1','emergency medical dispatcher',replace('EMD\ncall handler\nambulance services dispatcher\ndispatcher of emergency medical vehicles\nemergency call operator\nmedical dispatcher\nemergency call handler\nemergency medical services dispatcher\ndispatcher of emergency medical services\ncontrol room worker\nemergency medical dispatcher','\n',char(10)),'Emergency medical dispatchers respond to urgent calls made to the control center, take information about the emergency situation, the address and other details and dispatch the nearest ambulance or paramedic helicopter.','',replace('Excludes people performing patients treatment. \n Excludes people performing assistance in medical treatment.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/684fd8d5-9b40-4b02-a680-438f0082d923','key_16441','','','2310','2310.1','higher education lecturer',replace('teaching fellow\nhigher education lecturer\nuniversity reader\nprofessor\nhigher education senior lecturer\nuniversity lecturer\nprincipal lecturer\nresearch lecturer\nHE lecturer\nhigher education instructor\nreader\nuniversity professor','\n',char(10)),'Higher education lecturers instruct students who have obtained an upper secondary education diploma in their own specialised field of study, which is predominantly academic in nature. They may have job titles such as senior lecturer or professor. They work with their teaching and research assistants for the preparation of lectures and exams, for grading papers and exams, for leading laboratory practices, and for leading review and feedback sessions for the students. They also conduct academic research in their respective field, publish their findings and liaise with their academic colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/68ad860f-c5cd-420f-840f-993e3e813508','key_16442','','','7543','7543.7.2','consumer goods inspector',replace('consumer goods quality control inspector\nconsumer products assembly inspector\ntoys inspector\nconsumer goods inspector\nconsumer goods assembly inspector\nconsumer products assembly line inspector\nsurgical instruments inspector\nconsumer goods compliance inspector\nstock parts inspector\nmaterial inspector\nconsumer goods quality inspector\nfinal assembly inspector\nconsumer products compliance inspector\nconsumer products quality control inspector\nassembly inspector\nconsumer goods assembly line inspector\nconsumer goods quality compliance inspector\nconsumer products quality inspector','\n',char(10)),'Consumer goods inspectors evaluate assembled parts of consumer goods and products for compliance with specifications and defects in-line with clients'' requirements and organisational policies. Consumer goods inspectors provide results and findings for reports. Inspected defects can be identified as being cracks, scratches, errors in sanding, and defects of moving parts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/68bc2af1-96ea-4ec2-8b2f-5cde76472135','key_16443','','','3259','3259.5','dietetic technician',replace('medical dietetics technician\ntechnician in dietetics\nnutrition assistant practitioner\ndietetic assistant\ntechnician in medical dietetics\nassistant nutrition practitioner\ndietetic technician\nclinical dietetic technician\nclinical diet technician\ndietetic science technician\ndiet technician','\n',char(10)),'Dietetic technicians assist dietitians in planning nutritional programmes and in planning menus for patients as part of an overall treatment plan under supervision of a dietitian. Under supervision of a dietitian, dietetic technicians assist dietitians in patient reviews, enteral feeding discharge planning, and clinical auditing which includes nutritional screening.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/68d973df-bf10-4bf7-9a1b-fbd9f604b9db','key_16444','','','2131','2131.3','bioinformatics scientist',replace('bioinformatics analyst\nenvironmental scientist\nresearch scientist\ncomputer scientist\nbiotechnology scientist\nsenior bioinformatics scientist\nbioinformatics researcher\nIT researcher\ndata scientist\nbioinformatics scientist\ncomputer analyst\nbiology researcher\nIT expert\nbioinformatician\njunior bioinformatics scientist','\n',char(10)),'Bioinformatics scientists analyse biological processes using computer programmes. They maintain or construct databases containing biological information. Bioinformatics scientists gather and analyse biological data and may also assist scientists in various fields, including in biotechnology and pharmaceutics. They perform scientific research and statistical analyses, and report on their findings. Bioinformatics scientists may also collect DNA samples, discover data patterns and conduct genetic research.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/68dd0dfc-c8b0-4fd0-9a6e-b0864925fa56','key_16445','','','2320','2320.1.20','occupational railway instructor',replace('trolley driver instructor\nrailway instructor\npractitioner of railway instruction\ntrain driver instructor\nmetro driving instructor\noccupational railway instructor\nrailway instruction practitioner\nengine driver instructor\ntram driver instructor','\n',char(10)),'Occupational railway instructors teach future occupational drivers of railway transportation, such as train, tram, metro and trolley drivers how to operate their railway vehicle according to regulations. They teach their students theory and techniques on how to optimally drive and maintain their specific railway vehicle, observe and evaluate the students'' practice. They also focus on non-driving related subjects such as customer service (in case of person transportation) and safety measure regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/68f4fdd9-182c-4c0d-a73d-f767b8a8355e','key_16446','','','2131','2131.4.1','aquaculture biologist',replace('expert in aquaculture biology\naquaculture biology researcher\naquaculture biology research analyst\nconsultant aquaculture biologist\naquaculture biology scholar\naquaculture biology research scientist\naquaculture biology specialist\nspecialist in aquaculture biology\naquaculture biology expert\naquaculture biologist\naquaculture biology science researcher\naquaculture biology scientist\naquaculture biology analyst','\n',char(10)),replace('Aquaculture biologists apply knowledge gained from research about aquatic animals and plant life and their interactions with each other and the environment, in order to improve aquaculture production, prevent animal health and environmental problems and to provide solutions if necessary. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/68ffc53b-6067-4fe7-bd85-c96f4a6c1e45','key_16447','','','2131','2131.5','food biotechnologist',replace('food biotech technician\nfood biotechnologist\nfood biotech specialist','\n',char(10)),replace('Food biotechnologists study the life cycle of food from its preservation up to the spoilage and the food-borne pathogens. They research and understand food-borne diseases in order to prevent them. They ensure that food products abide by government regulations regarding food health and safety. \n','\n',char(10)),'','Includes people performing the establishment of food legislation.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/691c86d9-d88f-440f-b885-7ed9726f782c','key_16448','','','1420','1420.4.12','computer software and multimedia shop manager',replace('computer software and multimedia shop manager\nmultimedia equipment store manager\ncomputer software and multimedia store manager\ncomputer software store manager\nmultimedia software shop manager\nsoftware shop manager\ncomputer software and multimedia storekeeper\ncomputer software shop manager','\n',char(10)),'Computer software and multimedia shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/691d30cc-6b2c-4025-89b4-3e719fe664c7','key_16449','','','2621','2621.6','exhibition curator',replace('art gallery curator\nexhibits coordinator\nexhibition overseer\nexhibits manager\nexhibition superintendent\nmuseum curator\nexhibits curator\ncurator of exhibitions\nexhibits preparator\nexhibition curator','\n',char(10)),'Exhibition curators organise and display artworks and artefacts. They work in and for museums, art galleries, museums for science or history, libraries and archives, and in other cultural institutions. In general, exhibition curators work in artistic and cultural exhibition fields and events of all kinds.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/691ec040-f5ad-47ef-8698-03212d66dbbf','key_16450','','','7214','7214.3','structural ironworker',replace('structural iron and steel worker\nstructural steel fitters\nsteel fitters\nrebar workers\nsteel erector\nstructural ironworker\nstructural steel erector','\n',char(10)),'Structural ironworker in construction install iron elements into structures. They erect steel frameworks for buildings, bridges and other construction projects. They set metal rods, or rebar, to form reinforced concrete.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/699057b7-7aeb-4b5c-bb90-01d935ff32c1','key_16451','','','8341','8341.1','forestry equipment operator',replace('logger\nchainsaw operator\nwoodland equipment operator\nmobile sawmill operator\nwoodcutter\nwoodman\nlogging equipment operator\nlumberjack\nforestry equipment operator','\n',char(10)),'Forestry equipment operators carry out operations with specialised equipment in the forest to maintain, harvest, extract and forward wood for the manufacturing of consumer goods and industrial products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/69bcbb0a-8d80-4ecd-b0a4-9adea2a40de2','key_16452','','','2166','2166.9','graphic designer',replace('commercial graphic designer\ncommercial designer\ngraphic designer\ngraphic design expert\ngraphics designer\nadvertising artist\ngraphic artist\ngraphic design specialist','\n',char(10)),'Graphic designers create text and images to communicate ideas. They make visual concepts by hand or using computer software, intended for publishing in paper or online media such as advertisements, websites, and magazines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/69c24e54-567c-4415-b59b-cda97fdbc50c','key_16453','','','7534','7534.3.4','railway car upholsterer',replace('train coach upholsterer\nmultiple unit upholsterer\ntrain  upholstery worker\ntrain carriage upholsterer\ncarriage upholsterer\ntrain carriage upholstery worker\ncarriage works trimmer\nrailway car upholsterer\ntrain coach upholstery worker','\n',char(10)),'Railway car upholsterers create manufacturing templates, manufacture and assemble interior components for train carriages. They use power tools, hand tools and CNC machines to prepare and fasten materials. They also inspect incoming materials and prepare the vehicle interior for trim items.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/69e4e877-9d7a-4df0-973b-34defd318e26','key_16454','','','9112','9112.1','aircraft groomer',replace('aeroplane cleaner\ncabin cleaner\naeroplane groomer\nplane cleaner\naircraft groomer\ngroomer for aircraft','\n',char(10)),'Aircraft groomers clean aircraft cabins and airplanes after usage. They vacuum or sweep the interior of cabin, brush debris from seats, and arrange seat belts. They clean trash and debris from seat pockets and arranged in-flight magazines, safety cards, and sickness bags. They also clean galleys and lavatories.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/69e6db0b-30b4-4e0e-b95b-173f8bc3da73','key_16455','','','2133','2133.1','airport environment officer',replace('airport environmental expert\nairport environment officer\nairport environmental planning expert\nairport environmental officer','\n',char(10)),'Airport environment officers monitor environmental issues such as emissions, contamination, and wildlife activity in the premises of airports. They report environmental attractors for animals such as nearby rubbish dumps or wetland areas. They can engage in studying the environmental impact that airports have in the surrounding communities in reference to the diverse contamination that airports produce. They implement the rules to ensure the sustainable development of the airport.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6a0b0c4f-b57a-4978-a637-2c0adff65996','key_16456','','','7514','7514.2','oil mill operator',replace('oil field mill worker\noil mill operator\noil mill operative\noil milling operative','\n',char(10)),'Oil mill operators tend mills to extract oil from oilseed following artisanal techniques.','',replace('Includes people using artisanal oils. \nExcludes people using electrical equipment, chemical or industrial processes using hydraulic presses.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6a163fef-dee8-4583-82b0-13b76d8a27b2','key_16457','','','5223','5223.7.25','motor vehicles specialised seller',replace('salesperson motor dealership\nprincipal salesperson\nused car salesperson\nsalesperson motor trade\nspecialised seller motor trade\nsenior specialised seller\nused car specialised seller\nmotor vehicles specialized seller\nprincipal specialised seller\ndealership salesperson\nspecialised seller motor dealership\nfranchise salesperson\nnew car specialised seller\nsenior salesperson\nfranchise specialised seller\ndealership specialised seller\nnew car salesperson','\n',char(10)),replace('Motor vehicles specialised sellers sell cars and motors vehicles in specialised shops. \n \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6a167ceb-f847-4ef0-af47-89454c53770b','key_16458','','','2635','2635.3.3','clinical social worker',replace('clinical social support worker\nclinical social worker','\n',char(10)),'Clinical social workers provide therapy, counselling, and intervention services to clients. They treat clients with personal struggles, namely mental illness, addiction, and abuse, advocating for them and helping them gain access to the necessary resources. They also focus on the impact of medical and public health issues within social aspects.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6a2270bc-3f69-4cfd-b99b-fdc2c70d1779','key_16459','','','5111','5111.2.2','ship steward/ship stewardess',replace('ship guest services associate\ncruise ship guest services officer\nferry steward\nship stewardess\nship steward\ncruise ship steward\ncruise ship guest services professional\nship steward/ship stewardess\nship guest services professional\nship guest services officer\ncruise ship guest services associate\nferry stewardess\ncruise ship stewardess','\n',char(10)),'Ship stewards and ship stewardesses work on board the vessel to provide services to passengers such as serving meals, housekeeping, welcoming passengers and explaining safety procedures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6a29d804-f775-446c-bec7-35fc4e28ac97','key_16460','','','2421','2421.2','business consultant',replace('business consultant\nbusiness process consultant','\n',char(10)),'Business consultants analyse the position, structure and processes of businesses and companies and offer services or advice to improve them. They research and identify business processes such as financial inefficiencies or employee management and devise strategical plans to overcome these difficulties. They work in external consulting firms where they provide an objective view on a business and or company''s structure and methodological processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6a3d3d28-0ab6-49b0-9a32-697e5e104bf1','key_16461','','','2133','2133.11','soil scientist',replace('soil scientist\nsoil testing technician\nsoil biologist\nsenior soil scientist\nsoil biology specialist\nlead senior soil scientist\nsoil wetland scientist\nsoil analyst\nacidity tester\nsoil technician','\n',char(10)),'Soil scientists research and study scientific disciplines concerning soil. They advise on how to improve soil quality to support nature, food production or human infrastructure using surveying techniques, irrigation techniques and erosion reduction measures. They make sure to conserve and restore land suffering from intense farming or human interaction.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6a67114c-60a3-4cec-af26-c7df6fe6e9b5','key_16462','','','3123','3123.1.5','construction painting supervisor',replace('commercial decorating supervisor\npainting and decorating supervisor\ncommercial painting and decorating supervisor\ndecorating supervisor\nconstruction painting supervisor\nspecialist painting supervisor\nconstruction decorating supervisor\nconstruction painting and decorating supervisor\nindustrial painting supervisor','\n',char(10)),'Construction painting supervisors plan, direct and oversee the work of the crew of painters assigned to a certain project or location. They supervise and evaluate the work of painters.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6a6ce738-b049-4521-b402-8e1f346c7eca','key_16463','','','2635','2635.3.15','homelessness worker',replace('homelessness case worker\nhomeless care worker\nhomeless case worker\nhomeless guidance worker\nhomeless advice worker\nhomeless team worker\nhomelessness support worker\nhomelessness team worker\nhomelessness advice worker\nhousing options and advice worker\nhomeless support worker\nhomeless social worker\nhomelessness care worker\nadvice and guidance worker, homelessness\nhomelessness worker\nhomelessness social worker\nhomelessness guidance worker','\n',char(10)),'Homelessness workers provide on-the-spot assistance, counselling and advice to people who have housing problems or live on the streets. They present them with services available to homeless people starting from hostel vacancies to financial aid services. They may have to cope with persons with mental health problems, addictions or victims of domestic or sexual abuse.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6a6e174e-cffa-49a9-9f2f-aa39c81cdee5','key_16464','','','4211','4211.2','post office counter clerk',replace('mail services counter clerk\npost office customer service assistant\npost office supervisor\nmail service clerk\npost office counter clerk\npost office customer service consultant\ncounter clerk, post office','\n',char(10)),'Post office counter clerks sell products and services at a post office. They assist customers with picking up and sending mail. Post office counter clerks also sell financial products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6a7212c9-40da-4346-a190-3896bdd9a925','key_16465','','','2635','2635.3.21','probation officer',replace('prison department social worker\nparole officer\nprobation officer\nprobation services officer','\n',char(10)),'Probation officers supervise offenders after their release, or who were sentenced to penalties outside of incarceration. They write reports providing advice on the offender''s sentence and analysis concerning possibilities of reoffense. They aid the offenders during the rehabilitation and reintegration process and ensure the offenders carry out their community service sentence when necessary.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6aa78e46-ce22-421f-8a75-e915ac63c9d0','key_16466','','','3341','3341.3','call centre supervisor',replace('contact centre manager\ncall center supervisor\ninbound call center supervisor\ntele-sales supervisor\ncustomer contact centre supervisor\ncustomer centre supervisor\ncontact centre supervisor\ntelephone sales supervisor\ncustomer service supervisor\ndirect sales supervisor\ntelemarketing manager\ninbound call centre supervisor\ntelemarketing supervisor\noutbound call centre supervisor\ncustomer service centre manager\noutbound call center supervisor','\n',char(10)),'Call centre supervisors oversee call centre employees, manage projects and understand technical aspects of the call centre activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6aef7360-8c4a-4e1a-a402-b1e207101dc8','key_16467','','','1439','1439.2','camping ground manager',replace('campgrounds manager\ncampground manager\ncamping ground manager\ncamp-site manager\ncountryside manager\ncamping manager\ncamping pitch manager','\n',char(10)),'Camping ground managers plan, direct, or coordinate all campsite facilities and manage employees.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6afe39e2-2674-45db-b05c-a6cab02fe25b','key_16468','','','2359','2359.9','further education teacher',replace('further education lecturer\nsixth form teacher\nfurther education practitioner\ntutor of further education\nfurther education teacher\npractitioner in further education\ncontinuing education teacher\nsixth form tutor\nteacher of further education','\n',char(10)),'Further education teachers organise and teach programmes designed specifically for adult learners. They impart a wide spectrum of subjects, ranging from academic areas such as mathematics and history, to trainings for personality development, technical expertise or practical courses like languages and ICT. They teach and support adults aspiring to broaden their knowledge and their personal and professional skills and/or to achieve further qualifications. Further education teachers consider the previous knowledge and the work and life experience of the learners. They individualize their teaching and involve the students in the planning and executing of their learning activities. Further education teachers design reasonable assignments and examinations suitable to their adult learners.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6b0ad7c0-a37f-45c6-a486-ee943a11429e','key_16469','','','5161','5161.1','astrologer',replace('astrology expert\nastrologist\nastrologer\nhoroscope teller\nhoroscope expert','\n',char(10)),replace('Astrologers analyse constellation and motions of celestial objects and specific stellar and planetary alignments. They present this analysis together with their own interpretations about clients'' temperament, predispositions related to their health, love and marriage issues, business and job opportunities and other personal aspects. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6b304370-0112-4881-a1bd-27d8df645d00','key_16470','','','2320','2320.1.22','police trainer',replace('law enforcement trainer\nlaw enforcement instructor\ninstructor in law enforcement\npolice instructor\npolice trainer\ntrainer of police\npolice tutor\npolice firearms trainer\nspecialist police trainer\npolice officer trainer\ntrainer of police officers','\n',char(10)),'Police trainers train probationary, new academy recruits, or cadets, and seasoned veterans on the theory and practice necessary to become a police officer. They conduct theoretical lectures on academic subjects such as law, government regulations, community relations and human diversity. Police academy instructors also provide more hands-on, practical instruction regarding physical training, the care of firearms, first aid, self defensive tactics and vehicle operations. They also prepare and develop lesson plans and new training programmes as new law enforcement-related regulations and issues arise. The instructors monitor the students'' progress, evaluate them individually and prepare performance evaluation reports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6b46ea2f-403a-473c-b2e5-9fcf0c2bd634','key_16471','','','7233','7233.3','fisheries assistant engineer',replace('fisheries engineer''s assistant\nassistant naval mechanic\nassistant ship mechanic\nfishing vessel assistant engineer\nfisheries assistant engineer','\n',char(10)),'Fisheries assistant engineers assist the marine chief engineer with checking operations and maintenance of the propulsion plant, machinery and auxiliary equipment of the ship. They collaborate on security, survival and health care on board, observing the national and international standards of application.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6b5e5aa6-9bf7-47e6-90d2-4d2187d47d11','key_16472','','','2422','2422.12.9','immigration policy officer',replace('immigration policy officer\nrefugee policy officer\nasylum policy officer\ngovernment policy manager\nimmigration policy analyst','\n',char(10)),'Immigration policy officers develop strategies for the integration of refugees and asylum seekers, and policies for the transit of people from one nation to another. They aim to improve international cooperation and communication on the subject of immigration, as well as efficiency of immigration and integration procedures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6ba25e0f-7fb8-4879-a1f9-d4f8945966be','key_16473','','','2654','2654.1.3','director of photography',replace('photography director\ndirector of photography\nsenior director of photography\ncamera and lighting director\ncinematographer\nhead of camera and lighting crew\nlighting cameraman','\n',char(10)),'Directors of photography are responsible for the visual interpretation of the script and all photography components of the film, including framing, colouring, lightning, style and locations. They create the visual look of the film or television programme and select the filming equipment, including the lens and filters. Directors of photography manage the visual equipment operators and lighting technicians. They work together with the video and motion picture director to achieve the desired effect.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6c2b32b4-2f62-4288-a359-3406022c3610','key_16474','','','3324','3324.4.29','wholesale merchant in sugar, chocolate and sugar confectionery','wholesale merchant in sugar, chocolate and sugar confectionery','Wholesale merchants in sugar, chocolate and sugar confectionery investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6c39cf1c-7332-41c1-86c3-6ed3daf3c479','key_16475','','','7511','7511.3','fish trimmer',replace('fish trimmer\nfish preparation worker\nfish processor','\n',char(10)),'Fish trimmers cut off fish heads and remove organs from the body for fish and seafood production. They remove organs by scraping and washing, cut out areas presenting defects, and package the processed fish in appropriate containers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6c3fd65e-2d24-47d8-bc22-9e93512bdcc2','key_16476','','','1120','1120.3','chief executive officer',replace('managing director\nsenior executive officer\nCEO\nchief executive officer\npresident\nchairman','\n',char(10)),'Chief executive officers hold the highest ranking in a pyramidal corporate structure. They are able to hold a complete idea of the functioning of the business, its departments, risks, and stakeholders. They analyse different kinds of information and create links among them for decision-making purposes. They serve as a  communication link with the board of directors for reporting and implementation of the overall strategy.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6c7d333a-b2a9-4325-b25b-566d1ecc956d','key_16477','','','3359','3359.4','forestry inspector',replace('chief forestry inspector\nforestry operations monitor\nforestry auditor\nforestry assessor\nforestry inspector\nurban forestry inspector\nforestry operations compliance inspector\nforestry operations inspector\narboriculture inspector','\n',char(10)),'Forestry inspectors monitor forestry operations to ensure that workers and their activities comply with proper legislation and standards. They perform inspections to examine operations, wages, costs and health and safety measures. Forestry inspectors also analyse and report on their findings.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6c999fc7-c6b7-4ef3-a4b9-af124a1783a2','key_16478','','','4110','4110.1','office clerk',replace('office clerk\ncorrespondence clerk\nclerical assistant','\n',char(10)),'Office clerks are responsible for performing clerical and administrative duties in an office setting and support of business operations within a department. They assist all the administrative staff, secretaries, and assistants by sorting mail, filing forms and documents, answering phones, greeting clients and scheduling meetings.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6cc19251-763b-4de1-b4f7-526c9ff732d2','key_16479','','','1222','1222.1.2','public relations manager',replace('public relations specialist\ncommunications manager\nspokesperson\ncommunications officer\npublic information manager\nmarketing and communications manager\n​public relations consultant\npublic affairs manager\npublicity manager\nmedia relations manager\npress office manager\npublic information coordinator\ncorporate profiler\ncorporate affairs manager\npublic relations manager','\n',char(10)),'Public relations managers strive to convey and maintain a desired image or reputation of a company, individual, governmental institution, or organisation in general to the public and stakeholders at large. They use all sorts of media and events to promote the positive image of products, humanitarian causes or organisations. They attempt to ensure that all public communications portray clients the way they want to be perceived.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6ccb925a-264a-4e00-aa3a-503e53c5db2b','key_16480','','','8141','8141.1.9.2','V-belt finisher',replace('belt finisher\nfriction belt finisher\nbelt measurer\nV-belt finisher\nV-belt finishing operator\nbelt notcher\nbelt brander','\n',char(10)),'V-belt finishers operate machines to make V-belts flexible. They also position belts on machine that measures the length of the belt and stamps identifying information on it.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6cd08cbe-7611-4d70-8886-6c59c0385442','key_16481','','','2632','2632.1.1','communication scientist',replace('communication studies research analyst\ncommunication scholar\ncommunication studies scholar\ncommunication scientist\ncommunication researcher\ncommunication studies research scientist\ncommunication research analyst\ncommunication research scientist\ncommunication studies analyst\ncommunication studies researcher\ncommunication analyst\ncommunication science researcher\ncommunications scientist','\n',char(10)),'Communication scientists research the different aspects of the planning, collecting, creating, organizing, preserving, using, evaluating and exchanging information through verbal or non-verbal communication. They study the interactions between groups, individuals, and individuals with technologies (robots).','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6ce3c372-408b-4c46-86d8-ad6b9255d686','key_16482','','','1221','1221.6','tourism product manager',replace('tourism product developer\ntourism distribution manager\ntourism product manager','\n',char(10)),'Tourism product managers analyse the market, research potential offers, develop products, plan and organise the distribution and marketing processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6ce86c90-36cc-43ef-a1e0-885d8151f976','key_16483','','','3151','3151.4','ship duty engineer',replace('duty engineer on a ship\nship engineering officer\nship duty engineer\nmaritime duty engineer\nmaritime engineering officer','\n',char(10)),'Ship duty engineers share responsibility for most of the content of the ship''s hull. They ensure operation of the main engines, steering mechanism, electrical generation and other major subsystems. They communicate with the ship chief engineer to perform technical operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6cee3765-2b3b-48b0-af2b-1eb512d071fd','key_16484','','','1111','1111.4','mayor',replace('cabinet leader\nlord mayor\nlady mayoress\nmayor\ncivic leader\nleader of the council\ncouncil leader\nmayoress','\n',char(10)),'Mayors chair their jurisdiction''s council meetings and act as main supervisor of the local government''s administrative and operational policies. They also represent their jurisdiction in ceremonial and official events and promote activities and events. They, together with the council, hold the local or regional legislative power and oversee development and implementation of policies. They also supervise staff and perform administrative duties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6cfba9ca-f9a0-4a50-a8d3-064d88c928b1','key_16485','','','7215','7215.1','event scaffolder',replace('scaffolding team member\nscaffolding crew member\nscaffolding installer\nevent scaffolder\nscaffolding worker','\n',char(10)),'Event scaffolders set up and dismantle temporary seating, stages and structures which support performance equipment, artists and the audience. Their job can include rope access, working above colleagues and lifting heavy loads, which makes it a high risk occupation. Their work is based on instruction, plans and calculations. They work indoors as well as outdoors.','','Includes people working in event and rental companies.  Excludes people performing industrial or construction scaffolding.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6d03eaef-1ce5-4a1e-bb41-0908e1af1b65','key_16486','','','2423','2423.5','occupational analyst',replace('corporate business consultant\ncorporate business analyst\njob analyst\norganizational structure analyst\noccupational analyst','\n',char(10)),'Occupational analysts collect and analyse occupational information within one field or company in order to make recommendations for reducing costs and general business improvements. They provide technical assistance to employers in dealing with problematic staff recruitment and development and with staff restructuring. Occupational analysts study and write job descriptions and prepare occupational classification systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6d16c41d-81a7-44a9-836d-7aed46ccb955','key_16487','','','7316','7316.1.2','glass painter',replace('painter on glass\ncrystal painter\npattern designer on glass\nwindow painter\nstained glass painter\nglass object pattern designer\nglass painter','\n',char(10)),'Glass painters design and create visual art on glass or crystal surfaces and objects such as windows, stemware and bottles. They use a variety of techniques to produce decorative illustrations ranging from stenciling to free-hand drawing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6d1e2801-3ab0-4d74-ac27-918339e8db64','key_16488','','','3112','3112.2','construction safety inspector',replace('construction health and safety advisor\nsite safety advisor\nsafety controller\nsafety controller on construction sites\nhealth and safety advisor on construction sites\nhealth and safety advisor\nconstruction safety inspector\nconstruction site safety inspector\nsite safety inspector\nhealth & safety inspector\nsafety inspector\nconstruction health and safety inspector\nsafety inspector on construction sites\nsafety advisor\nconstruction safety controller\nhealth and safety inspector','\n',char(10)),'Construction safety inspectors monitor construction sites and their conformity to health and safety regulations. They perform inspections, identify safety hazards and report on their findings.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6d421d34-baa1-44f9-9ea0-231d76e325fd','key_16489','','','2636','2636.4','religion scientific researcher',replace('religious studies scholar\ntheology studies researcher\nreligion scientific researcher\ntheology studies scholar\ntheology scholar\nreligious studies researcher\ntheologist\ntheologian\ntheology researcher','\n',char(10)),'Religion scientific researchers study concepts related to religions, beliefs and spirituality. They apply rationality in the pursue of morality and ethics by studying of scripture, religion, discipline, and divine law.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6d50f735-dca9-4a02-96dc-e4310ed98ae2','key_16490','','','5152','5152.2','domestic housekeeper',replace('domestic housekeeper\nhouse caretaker couple\nhousekeeper\nprivate housekeeping director\ndomestic couple','\n',char(10)),'Domestic housekeepers are responsible for all the household activities in a private house. They oversee and execute duties according to the needs of the employer such as cooking, cleaning and washing activities, taking care of children and gardening. They order supplies and are in charge of expenditures allocated. Domestic housekeepers may supervise and instruct household staff in large households.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6d72138d-2ebb-4a89-b2b2-8c22365b89bf','key_16491','','','7212','7212.1','brazier',replace('brazier\nconversion brazer\nhand brazer assembler\nmachine operative brazer\nhand brazer operative\nhand brazer\nbrazer technician\nbrazer production operative\nbrazer production operator\ncombination welder\ncopper smith','\n',char(10)),replace('Braziers operate various equipment and machinery such as torches, soldering irons, fluxes and welding machines in order to join two metal pieces together, by heating, melting and forming a metal filler between them, often brass or copper. Brazing can join metals such as aluminum, silver, copper, gold, and nickel. Brazing is a similar process to soldering but requires higher temperatures.  \n \n \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6d765c21-267d-4d3d-a989-9b7596670477','key_16492','','','2163','2163.3','textile colourist',replace('textile colourists\ncolourist\ncolorist dyer\ntextile color maker\ncolorist\ntextile colorist\ncolor matcher\ncolourist dyer\ntextile colour maker\ncolour matcher\ndyeing range operator','\n',char(10)),'Textile colourists prepare, develop and create colours for textile applications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6d823dbb-4008-4fb7-b98d-f9d934478647','key_16493','','','3131','3131.3.8','solar power plant operator',replace('solar electricity power plant operative\nsolar array operative\nsolar farm operative\nsolar power plant safety operator\nsolar power plant maintenance operator\nsolar energy plant operative\nsolar farm worker\nsolar energy plant operator\nsolar power plant maintenance operative\nsolar electric power plant operator\nsolar array worker\nsolar farm operator\nsolar electricity power plant operator\nsolar power plant operator\nsolar power plant safety operative\nsolar energy plant worker\nsolar array operator\nsolar electric power plant operative','\n',char(10)),'Solar power plant operators operate and maintain equipment which produce electrical energy from solar power. They monitor measuring equipment to ensure the safety of operations, and that the production needs are met. They also react to system problems, and repair faults.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6da80214-d360-49ec-b1e7-172ab0a0cb0c','key_16494','','','8152','8152.1','knitting machine operator',replace('knitting machinist\nknitter operator\nwarp knitting machine operator\nnet-knitting machine operator\nknitting machine setter\nknitting machine tender\nknitting machine operator\nmachine knitting mender','\n',char(10)),'Knitting machine operators set up, operate and monitor knitting machines. They work with specialised machinery, techniques and materials to process threads of yarn into knitted products such as clothing, carpets or rope. They maintain and repair knitting machinery and ensure operations run without problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6db9eeae-aef5-4f56-913d-4650e4c720c6','key_16495','','','1349','1349.9','correctional services manager',replace('chief prison officer\ncorrectional institution director\nsenior superintendent jail\ncorrectional services manager\nsuperintendent jail\nprison governor\nchief detention officer\nsuperintendent of jail\nsenior superintendent of jail','\n',char(10)),'Correctional services managers manage the operations of a correctional facility. They supervise personnel, develop and oversee correctional procedures, and ensure the operation is compliant with legal regulations. They perform administrative duties and facilitate cooperation with external institutions and staff providing aid to the facility.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6dbca2aa-f83b-4567-a4e7-b9a21fb948bf','key_16496','','','8121','8121.5','metal rolling mill operator',replace('steel rolling mill technician\nrolling mill control operator\npowder mill operator\nmetal rolling mill operative\nmetal rolling mill operator\ngrinder mill operative\nhammer mill operator\ngrinder mill operator\nhammer mill operative\nmetal rolling mill technician\npowder mill operative\nhammer mill technician\nstopper grinder\nsteel mill operative','\n',char(10)),'Metal rolling mill operators set up and tend metal rolling mills designed to form metal workpieces into their desired shape by passing them through one or several pairs of rolls in order to decrease the metal''s thickness and to make it homogeneous. They also take into account the proper temperature for this rolling process.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6df6c5cb-c1a2-42c8-acd0-1ee5563c36c1','key_16497','','','2131','2131.4.2','biochemist',replace('biochemistry studies scholar\nbiochemistry studies research analyst\nbiochemistry studies researcher\nbiochemistry science researcher\nbio-chemist\nbiochemistry research scientist\nbiochemistry scientist\nbiochemistry research analyst\nchemical biotechnologist\nbiochemist\nbiochemistry studies scientist\nbiochemistry studies research scientist\nbiochemistry scholar\nbiochemistry analyst\nbiochemistry studies analyst\nbiochemistry researcher\nbiological chemist','\n',char(10)),'Biochemists study and perform research on the reactions caused by chemicals in living organisms. This includes performing research for the development or improvement of chemical-based products (e.g. medicine) aimed at improving the health of living organisms and at better understanding their reactions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e0e068b-cd5c-4400-8899-54056087fa14','key_16498','','','3254','3254.1','optician',replace('contact lens optician\noptician\ncorrective lens dispenser\ndispensing optician\noptician expert\nqualified optician\nexpert optician\ncontact lens specialist\nspectacle dispensing optician','\n',char(10)),'Opticians help to improve and correct an individual''s vision. They fit spectacle lenses and frames, contact lenses, and other devices according to the specifications of the individual. Their scope of practice varies according to national regulations and they might operate according to prescriptions provided by a specialised doctor in ophthalmology or an optometrist in the countries where requested.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e102a30-2c0a-4a31-aee3-ae7b1fac5466','key_16499','','','8156','8156.2.3','footwear stitching machine operator',replace('shoe sewing machine operator\nbinding stitcher\nshoe sewing machine tender\nfootwear stitching machine operator\nshoe stitcher','\n',char(10)),'Footwear stitching machine operators join the cut pieces of leather and other materials to produce the uppers. They use several tools and a wide range of machines such as flat bed, arm and one or two columns. They select threads and needles for the stitching machines, place pieces in the working area, and operate with the machine guiding parts under the needle. They follow seams, edges, markings or moving edges of parts against the guide. Finally, they cut excess thread or material from shoe parts using scissors or dyes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e11692c-fc72-4ef4-8bcf-6bd19a6b0ea3','key_16500','','','8160','8160.12','carbonation operator',replace('sparkling machine operator\ngasifier operator\ndrinks gasification processor\ncarbonation operator\nbeverage carbonation worker','\n',char(10)),'Carbonation operators perform the injection of carbonation into beverages.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e18b923-7c5f-42d7-8ff0-de8d8ba30dbf','key_16501','','','8142','8142.14','vacuum forming machine operator',replace('thermoforming machine operator\nthermoforming machine operative\nthermo-forming machine setter\nthermoforming operator\nmolder operator\nplastic moulding machine tender\nvacuum forming machine operative\nvacuum molding machine operator\nvacuum molding machine operative\nthermo-forming machine worker\nvacuum forming machine setter\nvacuum former\nvacuum forming machine tender\nmoulder operator\nvacuum moulding machine operator\nthermo-forming machine operative\nplastic molding machine tender\nvacuum moulding machine operative\nplastic molding machine setter\nvacuum forming machine worker\nthermoforming operative\nthermo-forming machine tender\nthermoforming machine tender\nvacuum forming machine operator\nplastics production process worker\nplastic moulding machine setter','\n',char(10)),'Vacuum forming machine operators tend, control, and maintain machines that heat sheets of plastic before moving them around a mould, using vacuum-suction; when these sheets become cool, they are permanently set in the shape of the mould.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e1b1bd7-9eec-4f10-bce7-94a27d9023e8','key_16502','','','2359','2359.3','admissions coordinator',replace('school admissions coordinator\nadmissions officer\ncoordinator of admissions\nstudent admissions officer\ncollege admissions coordinator\nadmissions coordinator\nuniversity admissions coordinator\nadmissions coordinator in education\ncoordinator of student admissions\nstudent admissions coordinator\nadmissions coordinator in university','\n',char(10)),'Admissions coordinators are in charge of the students'' applications and admissions to a (private) school, college or university. They assess possible future students'' qualifications and subsequently approve or deny their application, based on the regulations and desires set by the board of directors and the school administration. They also assist the accepted students in their enrollment in the programme and courses of their choice.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e298596-882b-4687-a0cf-3eb37f8fd328','key_16503','','','3423','3423.1.1','outdoor activities coordinator',replace('outdoor activities coordinator\nsports inclusion coordinator\nactivities coordinator\noutdoor activities supervisor\nsenior outdoor instructor\noutdoor activities manager\nactivities manager','\n',char(10)),'Outdoor activities coordinators organise and manage work programmes and resources (especially staff) to deliver the organisation’s products and services. They supervise and manage staff. They might train and develop the staff, or the planning and management of this process through others. They are highly aware of their responsibilities towards clients, technical issues, environmental issues, and safety issues. The role of an outdoor animation coordinator/supervisor is often “in the field”, but there may also be aspects of management and administration.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e36bf67-9df2-4d68-8f78-fca1d34acc0b','key_16504','','','5413','5413.1','juvenile correctional officer',replace('correctional youth security officer\nyoung offenders detention officer\nyouth detention counselor\nyouth correctional officer\njuvenile corrections officer\ndetention centre youth worker\nyouth detention worker\ndetention officer\nyoung offenders officer\njuvenile correctional officer\njuvenile detention officer','\n',char(10)),'Juvenile correctional officers monitor and provide security to juvenile offenders. They ensure the safety of the facility compliant with rules and regulations, compile reports on the daily activities as well as in the case of incidents, and report unusual activity. They also supervise rehabilitation procedures of the offenders.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e591f17-65c1-4a24-af2a-ca7862d4625e','key_16505','','','2434','2434.4','technical sales representative in electronic and telecommunications equipment',replace('electronic and telecommunications equipment  field saleswoman\nsenior technical sales engineer in electronic and telecommunications equipment\ngraduate technical sales representative in electronic and telecommunications equipment\nelectronic and telecommunications equipment outside technical sales representative\nelectronic and telecommunications equipment  inside technical sales representative\ntrainee technical sales engineer in electronic and telecommunications equipment\nelectronic and telecommunications equipment field salesman\nelectronic and telecommunications equipment field salesperson\ngraduate technical sales engineer in electronic and telecommunications equipment\ntechnical sales representative in electronic and telecommunications equipment\ntrainee technical sales representative in electronic and telecommunications equipment\nassistant technical sales representative in electronic and telecommunications equipment\nassistant technical sales engineer in electronic and telecommunications equipment\nelectronic and telecommunications equipment technical field sales representative\nsenior technical sales representative in electronic and telecommunications equipment','\n',char(10)),'Technical sales representatives in electronic and telecommunications equipment act for a business to sell its merchandise while providing technical insight for customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e5ceba0-df96-465f-adb4-2e06ae715d43','key_16506','','','2514','2514.4','numerical tool and process control programmer',replace('numerical tool maker\nnumerical tool and process control programer\nnumerical tool setting operator\nnumerical process control tool setting operator\nnumerical tool machine set up operator\nnumerical process control  set up operator\nnumerical tool setter\nnumerical tool l programme setter\nnumerical tool programmer\nnumerical process control supervisor\nnumerical process control operator\nnumerical tool machine operator\nnumerical process control programmer','\n',char(10)),'Numerical tool and process control programmers develop computer programs to control automatic machines and equipment involved in manufacturing processes. They analyse blueprints and job orders, conduct computer simulations and trial runs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e6839b6-099c-4802-906e-7f2c8203ee69','key_16507','','','3341','3341.7','office manager',replace('administrative supervisor\noffice manager\nadministrative manager\noffice coordinator\ngeneral office administrator\noffice supervisor\nadministrative services manager','\n',char(10)),'Office managers oversee the administrative work that clerical workers are commissioned to perform in various types of organisations or associations. They perform micromanagement and maintain a close view of administrative processes such as such as controlling correspondence, designing filing systems, reviewing and approving supply requisitions, assigning and monitoring clerical functions. They report to managers within the same department or to general managers in companies, depending on their size.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e6b62d7-4c9c-482a-9d59-7e20de0d8527','key_16508','','','3324','3324.4.30','wholesale merchant in textile industry machinery',replace('senior wholesale merchant in textile industry machinery\nwholesale buyer in textile industry machinery\nbusiness to business seller in textile industry machinery\ngraduate wholesale merchant in textile industry machinery\ntrainee wholesale merchant in textile industry machinery\nassistant wholesale merchant in textile industry machinery\nB2B sales consultant in textile industry machinery\nwholesale trader in textile industry machinery\nwholesaler in textile industry machinery\nbusiness to business sales consultant in textile industry machinery\nwholesale merchant in textile industry machinery\nwholesale merchandiser in textile industry machinery','\n',char(10)),'Wholesale merchants in textile industry machinery investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e6e44ba-d9f0-403b-93d4-a8d4bd67787a','key_16509','','','4224','4224.2','night auditor',replace('night time customer auditor\nnight customer care manager\nnight auditor\novernight customer care manager\novernight auditor','\n',char(10)),'Night auditors oversee night customer care in hospitality establishment and perform a wide variety of activities from front desk to bookkeeping.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e785f05-969e-4940-aedb-d0f896bb0230','key_16510','','','4323','4323.2','aircraft dispatcher',replace('airline dispatcher\nairplane dispatcher\nhelicopter dispatcher\naeroplane dispatcher\ndispatcher of aircrafts\nflight information expediter\naircraft information expediter\nflight dispatcher\naircraft dispatcher','\n',char(10)),'Aircraft dispatchers authorise, regulate, and control commercial airline flights according to governmental and company regulations. They expedite and ensure flight flow by preparing logs of flights, delays, cancellations, and changes in schedules or flight plans.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e873bb0-ff0d-4800-a898-362b9a2e1c47','key_16511','','','1324','1324.3.2.31','import export manager in textiles and textile semi-finished and raw materials',replace('senior import manager in textiles and textile semi-finished and raw materials\ninternational trade compliance manager in textiles and textile semi-finished and raw materials\ngraduate import manager in textiles and textile semi-finished and raw materials\ninternational trade manager in textiles and textile semi-finished and raw materials\ngraduate import export manager in textiles and textile semi-finished and raw materials\nsenior export manager in textiles and textile semi-finished and raw materials\ntrainee import export manager in textiles and textile semi-finished and raw materials\nassistant import export manager in textiles and textile semi-finished and raw materials\ntrainee export manager in textiles and textile semi-finished and raw materials\nassistant export manager in textiles and textile semi-finished and raw materials\ngraduate export manager in textiles and textile semi-finished and raw materials\ncustoms compliance manager in textiles and textile semi-finished and raw materials\nsenior import export manager in textiles and textile semi-finished and raw materials\ntrainee import manager in textiles and textile semi-finished and raw materials\nimport export manager in textiles and textile semi-finished and raw materials\nassistant import manager in textiles and textile semi-finished and raw materials','\n',char(10)),'Import export managers in textiles and textile semi-finished and raw materials install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e8917c8-d883-4475-954c-ec241215ca18','key_16512','','','1420','1420.4.35','photography shop manager',replace('camera shop manager\nphotographic articles shop manager\nphotography store manager\nphotographic accessories shop manager\nphotography goods store manager\ncamera accessories shop manager\nphotographic shop manager\nphotographic articles store manager\nphotography equipment store manager\nphotography shop manager\nphotographic goods shop manager\nphotographic equipment store manager','\n',char(10)),'Photography shop managers are responsible for activities and staff in a photography store. ​They manage employees, monitor the sales of the store, manage budgets and order supplies when a product is out of supply and perform administrative duties if required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6e92d4b5-3167-4166-802f-da7753951a31','key_16513','','','2511','2511.12','ICT research consultant',replace('ICT research advisor\nIT research consultant\nIT research advisor\nICT research consultant\nICT research consultants\nICT research specialist\nIT research specialist','\n',char(10)),'ICT research consultants perform targeted ICT research and provide a final report to the client. They also use ICT tools to design questionnaires for surveys, analyse the results, write reports, present the results and make recommendations based on the research findings.','',replace('Includes people performing outsourced activities. \nExcludes people performing in-house research.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6eaf90cb-144d-486c-86e7-3d2bf4242525','key_16514','','','9312','9312.1.5','road sign installer',replace('road sign team worker\nroad sign worker\ntraffic sign installer\nhighway sign installer\ntraffic sign erector\nroad sign installer\nroad sign erector\nhighway sign erector','\n',char(10)),'Road sign installers take road signs to the specified location and erect it. The installers may drill a hole into the ground, or remove existing paving to access the soil. They may anchor heavy signs in concrete.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6eb85dd0-1188-4081-bf54-1345fb5de3ca','key_16515','','','3133','3133.1.2','biogas technician',replace('biomass gas generation technician\nbiogas equipment technician\nbiogas technician\nbiogas generation technician\nbiogas equipment maintenance technician\nanaerobic digester technician\nbiogas production technician\nlandfill gas plant technician\nbiomass plant technician','\n',char(10)),'Biogas technicians work in the derivation of gas from organic matter and produced as landfill gas or digested gas. They operate equipment in biogas plants, perform tests and maintenance tasks, and take action in the event of a failure.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6ebe4e2e-7deb-4923-8b47-ca6fc03fa13a','key_16516','','','3122','3122.4.12','paper mill supervisor',replace('operations controller at paper mill\npaper mill supervisor\npaper mill controller\npaper mill operations controller\ncontroller at paper mill\npaper mill overseer\npaper mill senior manager\nmanager at paper mill\noverseer at paper mill\npaper mill manager\nsupervisor at paper mill','\n',char(10)),'Paper mill supervisors coordinate and monitor the operations at a paper mill during production of products such as corrugated board, cardboard boxes or padded envelopes. They ensure that production targets, such as quantity and quality of products, timeliness and cost-effectiveness can be achieved. They have a clear overview of ongoing processes, and take quick decisions to resolve problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6f74ebc4-ab7e-42a8-ad94-921f3d0ef0c6','key_16517','','','7126','7126.10','sewerage network operative',replace('sewerage network operative\nsewerage network repair operative\nsewerage network clearing  operative\nwastewater infrastructure operator\nwastewater infrastructure operative\nwastewater system operative\nsewerage network operator\nwastewater treatment works operative\nwastewater distribution equipment operative\nwastewater systems operative\nwastewater network operative\nsewerage system maintenance operative\nsewerage network maintenance operative\nsewerage network inspection operative','\n',char(10)),'Sewerage network operatives maintain sewerage systems which remove and transport wastewater and sewerage. They inspect pipes, pumping stations, and mains, looking for leaks or other faults, and repair any problems. They clear blockages, and perform maintenance duties, using network maps and specialised software.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6f7c1e84-8162-4c20-a575-b57ff624eef5','key_16518','','','8322','8322.7','taxi driver',replace('driver of taxi\nprivate hire driver\nprivate driver\ncab driver\nchauffeur\ntaxi driver\nminicab driver','\n',char(10)),'Taxi drivers operate a licensed private passenger transportation vehicle, looking after customers, taking fares and managing vehicle servicing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6f8f1da1-9160-4b9f-ba30-e3396d3b2f92','key_16519','','','7522','7522.7','recreation model maker','recreation model maker','Recreation model makers design and construct recreation scale models from various materials such as plastic, wood, wax and metals, mostly by hand.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6f9398cc-c4cc-415e-a757-fd855f55fda2','key_16520','','','8159','8159.6','nonwoven staple machine operator',replace('non-woven staple machinist\nnon woven staple machinist\nproducer of felts\nnonwoven staple machine operator\nnon-woven fabric machine operator\nnon-woven worker\nnon woven fabric machine operator\nnon-woven staple processing machine operator\nnon woven staple processing machine operator\nnonwoven staple machine operators\nnon woven worker','\n',char(10)),'Nonwoven staple machine operators perform physical nonwoven processing operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6fc25650-e69b-43c7-b4d5-3ca95acd7696','key_16521','','','3324','3324.4.7','wholesale merchant in coffee, tea, cocoa and spices',replace('wholesale trader in coffee, tea, cocoa and spices\nwholesale merchandiser in coffee, tea, cocoa and spices\nsenior wholesale merchant in coffee, tea, cocoa and spices\nbusiness to business seller in coffee, tea, cocoa and spices\ntrainee wholesale merchant in coffee, tea, cocoa and spices\nB2B sales consultant in coffee, tea, cocoa and spices\nwholesaler in coffee, tea, cocoa and spices\nassistant wholesale merchant in coffee, tea, cocoa and spices\nbusiness to business sales consultant in coffee, tea, cocoa and spices\nwholesale buyer in coffee, tea, cocoa and spices\nwholesale merchant in coffee, tea, cocoa and spices\ngraduate wholesale merchant in coffee, tea, cocoa and spices','\n',char(10)),'Wholesale merchants in coffee, tea, cocoa and spices investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6fc8f605-98b9-4218-b5e2-91c4c9c55c4d','key_16522','','','2149','2149.2.4','design engineer',replace('product engineer\ndesign engineer\nindustrial specialist\nindustrial expert\nproduct design engineer\ndesigning engineer','\n',char(10)),'Design engineers develop new conceptual and detailed designs. They create the look for these concepts or products and the systems used to make them. Design engineers work with engineers and marketers to enhance the functioning and efficiency of existing devices.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6fcf4638-e7c7-4978-9302-9a7b63a3d57c','key_16523','','','1221','1221.3.2','marketing manager',replace('sales and marketing manager\npromotions manager\ntelemarketing manager\nmarket development manager\nmarketing director\nmarketing and promotions manager\nmarketing manager\nadvertising sales manager\nmarketing executive\nmarket manager\nproduct marketing manager','\n',char(10)),'Marketing managers carry out the implementation of efforts related to the marketing operations in a company. They develop marketing strategies and plans by detailing cost and resources needed. They analyse the profitability of these plans, develop pricing strategies, and strive to raise awareness on products and companies among targeted customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6fe645b0-463a-445d-ab8f-c0a5132beb68','key_16524','','','7514','7514.1','fruit and vegetable preserver',replace('fruit and veg preservation operative\nfruit and vegetable preservation operative\nfruit and vegetable preserver\nfruit and vegetable preservation operator\nfruit and veg preservation operator','\n',char(10)),'Fruit and vegetable preservers tend machines to prepare and preserve fruit and vegetable products. They aim to keep preserved perishable foods in a stable form. Thus, they perform duties such as freezing, preserving, packing after sorting, grading, washing, peeling, trimming and slicing agricultural products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6fed206a-ac4d-492b-a81a-5a3a0f2b67bc','key_16525','','','3521','3521.1.2','camera operator',replace('cameraman\ncamerawoman\ncamera crane operator\ntowercam operator\nrailcam operator\nremote head operator\nvideographer\nsteadicam operator\ncamera equipment operator\nvideo camera operator\ncamera operator','\n',char(10)),'Camera operators setup and operate digital film cameras to shoot domestic motion pictures or television programs. They work together with the video and motion picture director, the director of photography, or the private client. Camera operators give advice on how to shoot scenes to actors, the video and motion picture director and other camera operators.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/6fefa90d-9a71-49c6-be92-52397e9795e6','key_16526','','','4212','4212.7','odds compiler',replace('odds manager\nrisk trader\nodds compiler\nsports odds compiler\nhorse racing odds compiler\nfootball odds compiler\nbetting expert\nodds expert\nodds trader\nsports trader','\n',char(10)),'Odds compilers are in charge of counting the odds in gambling. They are employed by a bookmaker, betting exchange, lotteries and digital/on-line as well as casinos who set the odds for events (such as sporting outcomes) for customers to place bets on. Apart from pricing markets, they also engage in any activity regarding the trading aspects of gambling, such as monitoring customer accounts and the profitability of their operations. Odds compilers may be required to monitor the financial position the bookmaker is in and adjust their position (and odds) accordingly. They may also be consulted as to whether to accept a bet or not.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7005c266-d1a9-42ac-8c36-b42d5d755463','key_16527','','','5329','5329.3','sterile services technician',replace('sterile services technician\ndisinfection assistant','\n',char(10)),'Sterile services technicians ensure decontamination of medical devices in compliance with strict hygienic procedures. They dismantle and re-assemble sophisticated medical equipment through sterilising, cleaning, and repackaging it for further usage, under supervision, following the orders of the doctor of medicine or other qualified medical personnel.','',replace('Excludes people performing patients treatment. \nExcludes people performing assistance in medical treatment.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/70182f44-e444-4bf3-940f-0c054c1d5917','key_16528','','','2149','2149.17','textile, leather and footwear researcher',replace('textile, leather and footwear research worker\ntextile, leather technologist\ntextile, leather and footwear researcher\ntextile and clothing researcher\ntextile, leather and footwear research analyst\ntextile and leather researcher\nclothing and apparel researcher','\n',char(10)),'Textile, leather and footwear researchers combine knowledge of material sciences, chemistry, physics, process technologies, management, and engineering to contribute to future innovations within the fields of textile, wearing apparel, leather, and footwear. They collaborate in multidisciplinary projects in order to combine findings from a diverse array of science fields for product development.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/701f9161-72e4-4ff3-a9e8-8e6352664ef0','key_16529','','','5164','5164.5','cattle pedicure',replace('cattle pedicure specialist\ncattle hoof trimmer\ncattle pedicure','\n',char(10)),'Cattle pedicures are specialists in taking care of hooves of cattle, in compliance with any regulatory requirement set by the national legal authority.  ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7052fd94-f563-46a9-8e2d-cba6c20f3e71','key_16530','','','7413','7413.1.2','overhead line worker',replace('high voltage lineworker\nlinesman\noverhead transmission and distribution linesperson\noverhead transmission and distribution linesman\noverhead lineworker\noverhead line technician\noverhead linesworker\nlow voltage lineworker\noverhead linesman\noverhead line engineer\noverhead power line worker\noverhead line worker','\n',char(10)),'Overhead line workers construct and maintain power supply and control cables in overhead power lines. They also make and repair electrical cables connecting customers to the electricity network.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/706e8b26-befb-429b-b71d-f72fad1bae12','key_16531','','','8160','8160.4','blanching operator',replace('blanching operator\nblancher\nblanching process worker\nblanching operative\nblanching worker','\n',char(10)),'Blanching operators remove outer coverings or skins from almonds and nuts in general. They cut leaves and impurities of raw material and control the flow of nuts, seeds, and/or leaves in the process. They use pressure and temperature to blanch the raw material if necessary.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7086d0ca-1e77-4690-89c9-7ed1a0478fa3','key_16532','','','2519','2519.1','data quality specialist',replace('data quality specialists\ndata quality expert\ndata integrity officer\ndata quality specialist\ndata quality officer\ndata integrity specialist','\n',char(10)),'Data quality specialists review organisation''s data for accuracy, recommend enhancements to record systems and data acquisition processes and assess referential and historical integrity of data. They also develop documents and maintain data quality goals and standards and oversee an organisation''s data privacy policy and monitor compliance of data flows against data quality standards.','','Excludes people performing managerial and development activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/709170ae-2b9a-4e7a-8201-260da7211898','key_16533','','','8160','8160.31','fish canning operator',replace('fish canning operative\nfish canning operator\nfish canner','\n',char(10)),'Fish canning operators brine, cook and package fish. They sterilise fish canning lines and introduce fishes in tanks once heads and visceras have been removed from the body. They tend cooking stoves to preheat the fish, and fill the cans either manually or mechanically with fish, olive oil or other products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/70b5896f-988e-4084-9c19-2c82bd5cc9a5','key_16534','','','2642','2642.1.16','sports journalist',replace('sports journalist\nradio sports journalist\nsports correspondent\nsport journalist\nsports commentator\non-line sports journalist\nsports journalist and commentator\nsports journalist & commentator\ndigital sports journalist\nsports reporter\nsports broadcast journalist','\n',char(10)),'Sports journalists research and write articles about sport events and athletes for newspapers, magazines, television and other media. They conduct interviews and attend events.','','Includes people working in live sport events.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7109cdfd-1618-43b6-8317-8dc90fa583e6','key_16535','','','8172','8172.6','wood fuel pelletiser',replace('wood pelletiser worker\nwood pellet machine worker\nwood pellet machine operator\nwood pelletiser technician\nwood pellet machine technician\nwood fuel pelletiser\nwood pelletiser operator\nwood pelletiser\nwood pellet machine specialist\nwood pelletiser specialist','\n',char(10)),'Wood fuel pelletisers operate a hammer mill to turn wood waste products into pellets for use as fuel. The milled product is afterward pressed through a die, producing the standardised shape and size of a pellet.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7153a12d-85e0-4123-87e9-46bb1a2dc929','key_16536','','','4221','4221.1','ground steward/ground stewardess',replace('passenger service supervisor\nground attendant supervisor\npassenger services supervisor\nground attendant\npassenger services agent\nmeet and assist agent\nground steward\npassenger service attendant\nground stewardess\nground steward/ground stewardess','\n',char(10)),'Ground stewards and ground stewardesses assist rail passengers before they board. They check in passengers and also perform customer service duties such as booking train tickets and helping passengers to apply for refunds after a delay or cancellation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/715b902f-b1b6-4366-afc6-ddb8ab715229','key_16537','','','3359','3359.1','agricultural inspector',replace('agricultural auditor\nfarm assessor\nagricultural assessor\nagricultural inspector\nfarm auditor\nagriculture assessor\nagriculture inspector\nagricultural examiner\nagriculture monitor\nagricultural monitor\nfarm inspector\nfarm monitor\nagriculture auditor\nagricultural production inspector','\n',char(10)),'Agricultural inspectors monitor agricultural operations in farms and other agricultural facilities. They inspect activities such as health and safety measures, costs and production processes to ensure that workers and their activities comply with proper legislation and standards. Agricultural inspectors also analyse and report on their findings.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/716df735-77eb-47c8-8c06-d86e46903989','key_16538','','','2151','2151.2','smart home engineer',replace('smart home project engineer \nhome automation specifier\nsmart home software engineer\nhome automation integrator\nhome automation platform engineer\ndomotics engineer \nsmart home designer','\n',char(10)),'Smart home engineers are responsible for the design, integration and acceptance testing of home automation systems (heating, ventilation and air conditioning (HVAC), lighting, solar shading, irrigation, security, safety, etc.), which integrate connected devices and smart appliances within residential facilities. They work with key stakeholders to ensure the desired project outcome is achieved including wire design, layout, appearance and component programming. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7180a7be-68cd-4636-8c12-706722ad8862','key_16539','','','8132','8132.2','photographic developer',replace('photography lab technician\nphotograph developer\nphotography developer\nphotographic developer\nphoto developer\nphotograph laboratory technician\nphoto retoucher\nphotograph printer\nphotograph lab technician\nphoto lab technician\nphotography laboratory technician\nphoto laboratory technician','\n',char(10)),'Photographic developers use chemicals, instruments, and darkroom photographic techniques in specialised rooms in order to develop photographic films into visible images.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/71898b81-fc2f-4a97-826d-f45e49a8160a','key_16540','','','3435','3435.2','assistant video and motion picture director',replace('director''s right hand man\ndirector''s right-hand person\nassistant director\nassistant video and motion picture director\nsecond assistant director\nassistant motion picture director\nassistant video director\nfirst assistant director\ndirector''s right hand woman\nthird assistant director\ndirector''s right-hand man\ndirector''s right-hand woman\ndirector''s right hand person','\n',char(10)),'Assistant video and motion picture directors are responsible for the organisation, scheduling and planning of the cast, crew and activities on a set. They assist the video and motion picture directors, maintain budgets and ensure that all production activities go according to schedule.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/718bba63-75b7-4b3a-9912-e201b729fe4c','key_16541','','','8212','8212.3.1','battery assembler',replace('battery builder\nbattery constructor\nbattery production operative\nconstructor of batteries\nbattery fabricator\nassembler of batteries\nassembler of battery components\nbattery production assembler\nbattery component welder\nbattery component assembler\ndry cell production operator\nbattery maker\nbattery component production assembler\ndry cell production worker\nfabricator of batteries\nbattery assembly operator\nbattery assembly worker\nbattery production worker\nbattery assembler\nwelder specialised in battery components\nbattery production operator\nbattery assembly operative\ndry cell production operative\nbattery welder','\n',char(10)),'Battery assemblers are welding and assembling the battery components such as electronics parts, wiring, and casing around the cells.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/71a98a24-e02b-4ba6-b4df-6556d7ae98cc','key_16542','','','8143','8143.8','paper stationery machine operator',replace('paper stationery machine technician\npaper stationery machinist\npaper puncher specialist\npaper products machine technician\npaper creaser operator\npaper products machine operator\npaper stationery machine operative\npaper perforator technician\npaper creaser specialist\npaper creaser technician\npaper puncher technician\npaper perforator operator\npaper stationery machine specialist\npaper perforator specialist\npaper products machine specialist\npaper stationery machine worker\npaper stationery machine operator\npaper puncher operator','\n',char(10)),'Paper stationery machine operators work with machines that perform one or more operations on paper to make it suitable for specific markets, such as punching holes, perforating, creasing, and collating with a carbon coated sheet.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/71bac871-7594-4d86-a19f-592079bd8154','key_16543','','','5223','5223.7.7','building materials specialised seller',replace('building materials salesperson\nbuilders merchants store salesperson\nconstruction materials shop salesperson\ndo it yourself shop salesperson\nbuilding materials DIY specialised seller\ndo it yourself shop specialised seller\nbuilding equipment shop salesperson\nbuilding materials DIY salesperson\nbuilding equipment shop specialised seller\ntrade counter specialised seller\nconstruction materials shop specialised seller\nbuilders merchants shop specialised seller\nconstruction materials salesperson\nbuilding materials shop salesperson\nDIY shop salesperson\nbuilding materials specialized seller\nbuilders merchants shop salesperson\nbuilding materials do it yourself specialised seller\nbuilders merchants store specialised seller\nbuilding materials do it yourself salesperson\nconstruction materials  specialised seller\nbuilding materials shop specialised seller\nDIY shop specialised seller\ntrade counter salesperson','\n',char(10)),'Building materials specialised sellers sell building materials in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/71c3a49a-e55e-42ce-bd3f-31f37d3cfab6','key_16544','','','3411','3411.8.1','store detective',replace('SD\nstore detective\nloss prevention agent\nretail loss investigator\nasset protection officer\nloss prevention officer','\n',char(10)),'Store detectives monitor the activities in the store in order to prevent and detect shoplifting. Once the individual is caught red-handed, they take all the legal measures, including announcing the police.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/71e4891f-48a6-435f-8897-208969fb4e44','key_16545','','','3123','3123.1.10','dredging supervisor',replace('dredge operator supervisor\ndredging operations supervisor\ndredging supervisor\ndredging site supervisor\ndredge foreman\ndredging project supervisor\ndredging foreman','\n',char(10)),'Dredging supervisors monitor dredging operations. They make sure the operation proceeds per regulations and take quick decisions to resolve problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/71f09c8b-a172-408c-b9e7-32e580e39ff6','key_16546','','','2211','2211.1','general practitioner','general practitioner','General practitioners promote health, prevent, identify ill health, diagnose and treat diseases and promote recovery of physical and mental illness and health disorders of all kinds for all persons regardless of their age, sex or type of health problem.','','','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/72149e37-f4f4-4570-8c17-27444160aa16','key_16547','','','5223','5223.7.31','shoe and leather accessories specialised seller',replace('leather accessories salesperson\nchildrens shoes salesperson\nfootwear specialised seller\nsports shoes salesperson\nsports shoes specialised seller\nleather accessories specialised seller\nchildrens shoes specialised seller\nshoe and leather accessories specialized seller\nfootwear salesperson\nshoe and leather accessories salesperson','\n',char(10)),'Shoe and leather accessories specialised sellers sell footwear in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7226c10f-701b-4711-8843-140aace12c6f','key_16548','','','1111','1111.1','city councillor',replace('local government councillor\nlocal councillor\ncouncillor\ncounty councillor\nelected representative\ncity councilor\ncabinet member\ntown councillor','\n',char(10)),'City councillors represent residents of a city in the city''s council and perform local legislative duties. They examine the concerns of the residents and respond to them in an appropriate manner, and represent their political party''s policies and programs in the city council as well. They communicate with governmental officials to ensure the city and its agenda are represented and supervise all operations which fall under the city council''s responsibility.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7235d075-ecf6-42ba-8a60-d7e79bbce152','key_16549','','','8160','8160.35','fruit-press operator',replace('fruit-press operator\nfruit press worker\nfruit presser\nfruit squeezer\nfruit press operative','\n',char(10)),'Fruit-press operators tend power presses to extract juice from fruits. For the purpose, they spread fruit evenly in cloth before tending the press and keep filter bags between sections in the machines ready for the extraction process. They are in charge of removing filter bags or pull cart from press and dump fruit pulp residue into containers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/72381086-cb6e-455e-a40b-ccb26550aab6','key_16550','','','3131','3131.3','power production plant operator',replace('electric power station operative\npower plant operator\nelectricity generation plant worker\nelectricity generating plant worker\nelectric power station operator\nelectric power station worker\nbiomass plant operator\nelectricity generation operator\nwind plant operator\npower station worker\nelectricity generation worker\nelectricity generation operative\npower production plant operator\nelectricity generation plant operator\npower station operator\npower station operative worker\nelectricity generating plant operative\ngenerating station operator','\n',char(10)),'Power production plant operators maintain and operate the equipment in power stations and other energy production plants. They repair faults, operate machinery directly or from a control room, and handle materials related to electricity production in compliance with safety and environmental procedures. They facilitate interaction between electrical energy facilities, ensuring that distribution occurs safely.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/72413d91-f212-4ad6-bf95-8b78a37c041a','key_16551','','','5142','5142.3','hair removal technician',replace('depilation specialist\nhair removal specialist\nwax depilation specialist\nbeauty treatment specialist\nbody waxing technician\nelectrolysis technician\nlaser hair removal specialist\nlaser hair removal technician\nepilation specialist\nhair removal technician','\n',char(10)),'Hair removal technicians provide cosmetic services to their clients by removing unwanted hair on various body parts. They can use different techniques for temporary hair removal, such as epilation and depilation techniques, or permanent hair removal methods, such as electrolysis or intense pulsed light.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/725090a7-9943-4dae-8079-a6999d21ddba','key_16552','','','2431','2431.6','client relations manager',replace('client relations manager\nclient relationship manager\ncustomer service executive\nclient manager\nclient service manager\ncustomer service representative\nclient service representative\ncustomer service manager\nconsumer relations manager','\n',char(10)),replace('\nClient relations managers act as the middle person between a company and its customers. They ensure that the customers are satisfied by providing them with guidance and explanation on their accounts and services received by the company. They also have possible other tasks such as developing plans or delivering proposals.','\n',char(10)),'',replace('Excludes <a resource="http://data.europa.eu/esco/occupation/9c9752b7-3e3b-4a08-8553-b63a013f8072" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/9c9752b7-3e3b-4a08-8553-b63a013f8072"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">customer experience managers</span></a>. \n Excludes people working with short term customers in the hospitality, recreation or entertainment industry.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7259dc48-004a-4ceb-aa4e-5bbd548c2397','key_16553','','','7126','7126.4','heating technician',replace('oil fired heating inspector\nheating engineer\nsolid-fuel fired heating inspector\ncoal fired heating inspector\nHVAC heating mechanic\nelectrical heating engineer\nsolid fuel heating inspector\ncoke fired heating inspector\nHVAC inspector','\n',char(10)),'Heating technicians install and maintain gas, electric, oil, solid-fuel and multiple fuel heating and ventilation equipment as stand-alone heating and ventilation systems or build into machinery and transport equipment. They follow instructions and blueprints, perform maintenance on systems, perform safety checks and repair the systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/72628f3a-fd49-499a-9741-870b0a1fcef2','key_16554','','','2120','2120.5','mathematician',replace('mathematics science researcher\nlogic scientist\nmathematics scientist\nlogic research scientist\nmathematics researcher\nlogic science researcher\nmathematics scholar\nlogic researcher\nmathematics research scientist\nmathematician\nlogician\nlogic scholar\nmathematicians','\n',char(10)),'Mathematicians study and deepen existing mathematical theories in order to expand the knowledge and find new paradigms within the field. They can apply this knowledge to challenges presented in engineering and scientific projects in order to assure that measurements, quantities, and mathematic laws prove their viability.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7284ccbf-e2fd-4e2f-8bbf-bc54eab610d5','key_16555','','','2412','2412.8','personal trust officer',replace('personal trust manager\npersonal trust supervisor\ntrustee officer\npersonal trust officer','\n',char(10)),'Personal trust officers monitor and administer personal trusts. They interpret trust and testamentary documentation accordingly, interact with financial advisors to define the investment goal for the achievement of trust objectives, coordinate the purchase and sale of securities with account executives and review clients'' accounts regularly.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/728b9e5b-3da4-4dbd-ac36-614ebdb6c8c0','key_16556','','','8171','8171.3','froth flotation deinking operator',replace('froth flotation deinking tank specialist\nfroth flotation deinking operator\nfroth flotation deinking tank technician\nfroth flotation deinking tank operator\nfroth flotation deinking worker\nfroth flotation deinking technician\nfroth flotation deinking tank worker\nfroth flotation deinking specialist','\n',char(10)),'Froth flotation deinking operators tend a tank that takes in recycled paper and mixes it with water. The solution is brought to a temperature around 50°C Celsius, after which air bubbles are blown into the tank. The air bubbles lift ink particles to the surface of the suspension and form a froth that is then removed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/72913ee1-1696-4d74-b26f-681d17887c58','key_16557','','','2263','2263.1','emergency response coordinator',replace('emergency coordinator\nemergency preparedness coordinator\naid worker\nhumanitarian aid worker\ndisaster preparedness coordinator\ndisaster coordinator\ncharity coordinator\nemergency planning officer\nemergency response coordinator','\n',char(10)),'Emergency response coordinators analyse potential risks such as disasters and emergencies for a community or institution and develop a strategy for reacting to these risks. They outline guidelines for the response to an emergency in order to decrease the effects. They educate the parties at risk on these guidelines. They also test response plans and ensure that the necessary supplies and equipments are in place in compliance with health and safety regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/72add02e-ae39-4dfb-8047-ced4c1a347f5','key_16558','','','2642','2642.1.14','picture editor',replace('photograph editor\nimage editor\npicture selector and organizer\nphoto editor\npicture editor\npicture selector and organiser','\n',char(10)),'Picture editors select and approve photographs and illustrations for newspapers, journals and magazines. Picture editors make sure that the photographs are delivered on time for publication.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/72bcb223-da77-4ed5-a03a-796fd5ece728','key_16559','','','2355','2355.6','visual arts teacher',replace('educator in visual arts\nspecialist visual arts teacher\ninstructor of arts and crafts\nsculpture teacher\ninstructor of visual arts\narts and crafts instructor\nvisual arts educator\nvisual arts teacher\narts and crafts teacher\nvisual arts instructor\npainting teacher\nteacher of visual arts\nspecialist teacher in visual arts','\n',char(10)),'Visual arts teachers instruct students in various styles of visual arts, such as drawing, painting, and sculpting, in a recreational context. They provide students with an overview of art history, but primarily utilise a practice-based approach in their courses, in which they assist students to experiment with and master different artistic techniques, and encourage them to develop their own style.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/72d03aad-2003-404e-ae71-a37206e3566b','key_16560','','','7318','7318.5','leather goods artisanal worker',replace('leather artisan\nleather craft worker\nleather goods artisanal worker\nbespoke leather craftswoman\nleather repair worker\nleather articles artisanal worker\nleather craftsman\nleather craftswoman\nleatherwear artisanal worker\nbespoke leather craftsman\nleather accessories artisanal worker\nluxury leather artisan','\n',char(10)),'Leather goods artisanal workers manufacture leather goods or parts of leather goods by hand according to the specifications of the customer or their own design. They do repairs of leather goods such as shoes, bags and gloves.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/72d1e9ca-294c-4349-90ed-f99541d447d0','key_16561','','','1324','1324.3.1.6','specialised goods distribution manager','specialized goods distribution manager','Specialised goods distribution managers plan, coordinate and manage the distribution of specialised goods to various points of sales. They oversee employees and ensure that operations run efficiently such as the storage, transportation and delivery of specialised goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/72d4b5a3-5458-41e5-8b4e-cb4fb3fd0fbc','key_16562','','','3331','3331.2.1.29','import export specialist in sugar, chocolate and sugar confectionery',replace('graduate import specialist in sugar, chocolate and sugar confectionery\ntrainee export specialist in sugar, chocolate and sugar confectionery\nsenior import specialist in sugar, chocolate and sugar confectionery\ntrainee import export specialist in sugar, chocolate and sugar confectionery\ninternational trade compliance specialist in sugar, chocolate and sugar confectionery\nassistant export specialist in sugar, chocolate and sugar confectionery\nassistant import export specialist in sugar, chocolate and sugar confectionery\nassistant import specialist in sugar, chocolate and sugar confectionery\nsenior export specialist in sugar, chocolate and sugar confectionery\ngraduate import export specialist in sugar, chocolate and sugar confectionery\nsenior import export specialist in sugar, chocolate and sugar confectionery\ngraduate export specialist in sugar, chocolate and sugar confectionery\ntrainee import specialist in sugar, chocolate and sugar confectionery\ncustoms compliance specialist in sugar, chocolate and sugar confectionery\nimport export specialist in sugar, chocolate and sugar confectionery','\n',char(10)),'Import export specialists in sugar, chocolate and sugar confectionery have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/72f4abfe-5337-49be-b9b8-32096fc2ca1f','key_16563','','','7312','7312.2','instrument technician',replace('senior instrument technician\ninstrument technician\nguitar technician\ndrum technician\nroad crew member\nroadie\npiano technician\nbass technician','\n',char(10)),replace('Instrument technicians assist and support musicians before, during and after the performance to ensure the instruments and the connected equipment, the backline, are setup properly. They maintain, check, tune and repair instruments and assist with quick changes. \n ','\n',char(10)),'','Excludes musical instrument technician.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7309ab2d-e2ba-4ccb-b658-6b0a8bd8c4eb','key_16564','','','8160','8160.43','malt kiln operator',replace('malt kiln worker\nmalt kiln operative\nmalt kiln operator\nmalt kiln specialist\nmalt worker','\n',char(10)),'Malt kiln operators tend kiln machines and equipment while supervising that the grain roasting operation maintains in specified roasting parameters.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/731ecac4-06e8-4ec2-a559-101fecbd9183','key_16565','','','9112','9112.4','room attendant',replace('rooms person\nhousekeeping worker\nchamber maid\nhousekeeper\nroom attendant\nchambermaid','\n',char(10)),'Room attendants clean, tidy and restock guest rooms as well as other public areas as directed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/731f38f1-b08f-4dd5-a25e-f8627c038273','key_16566','','','2519','2519.7.3','ICT integration tester',replace('integration testers\nmodule compatibility tester\ncompatibility tester\nICT compatibility tester\nICT integration tester\nmodule integration tester\nIT compatibility tester','\n',char(10)),'ICT integration testers perform tests in groups of system components, units or even applications. They group them in larger aggregates and apply integration test plans on them. They oversee the complexity of relations between different components.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7324e9f5-0f94-497e-af2f-42142e939a94','key_16567','','','7533','7533.2','embroiderer',replace('embroidery worker\nhand decorator\nembroidery operator\nembroidery digitiser\nembroiderer\nembroidery puncher\nembroidery finisher','\n',char(10)),'Embroiderers puch designs and decorate textile surfaces by hand or by using an embroidery machine. They apply a range of traditional stitching techniques to produce intricate designs on clothing, accessories, and home decor items. Professional embroiderers combine traditional sewing skills with current software programs to design and construct embellishments on an item.','',replace('Excludes people performing patients treatment. \n Excludes people performing assistance in medical treatment.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/73270b73-8c71-445f-a8d3-4647b1ae38d0','key_16568','','','8143','8143.4','paper bag machine operator',replace('paper bag machine technician\npaper bag constructing machine operator\npaper bag machine worker\npaper bag machine operator\npaper bag making machine operator\npaper bag machine specialist\npaper bag manufacturing machine operator\npaper bag machinist','\n',char(10)),'Paper bag machine operators tend a machine that takes in paper, folds it and glues it to produce paper bags of various sizes, shapes, and grades of strength.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/73357956-f607-4baa-9ce6-03beb9b39da2','key_16569','','','5223','5223.5','sales processor',replace('sales processor\norder processor\norder processing representative\nsales order co-ordinator\ne-commerce sales processor\nsales order assistant\nsales order processor\ntrainee order processor\norder processing salesperson\norder entry and transfer processor\norder processing sales person\nsenior order processor\nsales order administrator\ngraduate sales processor\ngraduate sales order processor\ne-commerce sales order processor\nsenior sales processor\norder entry and transfer co-ordinator','\n',char(10)),'Sales processors handle sales, select channels of delivery, execute orders and inform clients about dispatching and procedures. They communicate with clients in order to address missing information and/or additional details.','','Excludes people performing manually processing orders.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/733d65ac-250b-45d7-ba3c-cc5f48e9e11d','key_16570','','','7223','7223.10','laser marking machine operator',replace('laser engraving drill operator\nlaser engraving mill technician\nlaser marking machine operative\nlaser marking machine set-up operative\nlaser set up operative\nlaser marking machine operator\nCNC laser marking set-up technician\nlaser set up operator\nlaser engraving drill technician\nlaser engraving drill operative\nlaser engraver mill operator\nlaser engraving mill operative\nlaser marking operator\nlaser engraving drill set-up technician\nlaser marking machine set-up operator\nlaser engraving operator','\n',char(10)),'Laser marking machine operators set up and tend laser marking or engraving machines designed to precisely carve a design in the surface of a metal workpiece by use of a moving controller and an engraving laser beam point attached to it that traces patterns on the metal workpiece''s surface. They make adjustments to the machine in terms of laser beam intensity, direction and speed of movement. They also ensure the laser table, used to perform laser machine engravings on and guides the laser beam, is properly set up.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/734bb387-ea3a-4c62-abc1-05f894d51921','key_16571','','','2320','2320.1.18','medical laboratory technology vocational teacher',replace('medical laboratory technology teacher\neducator in medical laboratory technology\nmedical laboratory technician instructor\nmedical laboratory technology instructor\ninstructor in medical laboratory technology\nteacher in medical laboratory technology\nvocational instructor in medical laboratory technology\nvocational teacher in medical laboratory technology\nmedical laboratory technology vocational teacher\nmedical laboratory technology educator','\n',char(10)),'Medical laboratory technology vocational teachers technology instruct students in their specialised field of study, medical laboratory technology, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for a medical laboratory technology-related profession. Medical laboratory technology vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of medical laboratory technology through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/737c1795-844b-43ad-95b2-1bf0e8c14ddf','key_16572','','','8182','8182.2','steam plant operator',replace('steam plant safety operator\nsteam jet refrigeration operator\nsteam power plant maintenance operator\nsteam heating operator\nsteam power plant operator\nsteam boiler operator\nsteam plant maintenance operator\nsteam plant worker\nsteam power heating plant operator\nsteam plant operative\nsteam refrigeration operator\nsteam plant operator','\n',char(10)),'Steam plant operators operate and maintain mechanical equipment such as stationary engines and boilers to provide utilities for domestic or industrial use. They monitor proceedings to ensure compliance with safety regulations, and perform tests to ensure quality.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/738257d3-7e56-4745-9b2e-014e60d41d48','key_16573','','','2635','2635.3.16','hospital social worker',replace('public health social worker\nhospital discharge social worker\nhospital social worker\nmedical social worker\npalliative care social worker\nsocial worker\nsocial worker, palliative care','\n',char(10)),'Hospital social workers provide counselling to patients and their families helping them to better cope with the illness, the emotions surrounding diagnosis, and with social and financial problems. They work in cooperation with doctors, nurses and other health professionals sensitising them on the emotional aspects of a patient. They act as link between patients and medical staff. Hospital social workers also support the patients and their families with the discharge from the hospital.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/73856e9e-eca2-44c2-bb3c-aaedf4b25979','key_16574','','','1223','1223.1.1','insurance product manager',replace('insurance sales manager\nassurance relationship manager\nassurance office manager\ninsurance product development executive\ninsurance product manager\nassurance account manager\ninsurance lead\ninsurance office manager\nassurance lead\nassurance branch manager\ninsurance relationship manager\ninsurance account manager\ninsurance product developer\ninsurance product development manager\nassurance sales manager','\n',char(10)),'Insurance product managers set and direct the development of new insurance products, following the product lifecycle policy and the general insurance strategy. They also coordinate the marketing and sales activities related to the specific insurance products of the company. Insurance product managers inform their sales managers (or the sales department) about their newly developed insurance products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/73b10c97-b003-45dc-a36d-c1d585c04be1','key_16575','','','1120','1120.6','social entrepreneur',replace('green entrepreneur\nsocial business founder\neco-entrepreneur \nsocial enterprise founder\nsocial business venture founder','\n',char(10)),'Social entrepreneurs create innovative products or service models to tackle social and environmental challenges, pursuing through their profits a social mission that benefits a wider community or the environment. They often use a more democratic decision-making system by involving closely their stakeholders, and strive to achieve change at a systems level, by influencing policies, market evolutions and even mentalities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/73bcde7c-bc34-485d-a7af-7b94fc43f61b','key_16576','','','7313','7313.2','precious stone cutter',replace('faceter\ndiamond cutter\nlapidarist\njewelery fabricator\ngemstone cutter\nprecious stone cutter\nopal polisher\ngem cutter\nlapidary\ngemcutter','\n',char(10)),'Precious stone cutters use cutting machines and tools to cut or carve diamonds and other gemstones according to diagrams and patterns while considering different specifications. They are experts at fabricating jewellery such as rings, brooches, chains and bracelets from gemstones.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/73bdadf0-2175-4097-8f4d-b6dabeb0a030','key_16577','','','1324','1324.3.1.6.14','fruit and vegetables distribution manager',replace('senior fruit and vegetables distribution manager\nassistant fruit and vegetables distribution manager\nfruit and vegetables distribution manager\nfruit and vegetables supply chain manager\nfruit and vegetables distribution planner\nfruit and vegetables logistics planner\nfruit and vegetables supply chain planner\ngraduate fruit and vegetables distribution manager\ntrainee fruit and vegetables distribution manager\nfruit and vegetables logistics manager','\n',char(10)),'Fruit and vegetables distribution managers plan the distribution of fruit and vegetables to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/73c3f018-71ce-4dc1-abdd-14cea7873717','key_16578','','','1111','1111.7','senator','senator','Senators perform legislative duties on a central government level, such as working on constitutional reforms, negotiating on bills of law, and settling conflicts between other governmental institutions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/73ceb2f9-18e3-4cfe-9c06-e9fb31ece440','key_16579','','','1223','1223.2','research and development manager',replace('research and development supervisor\nresearch and development associate\nmarket research manager\nproduct research development manager\nresearcher\nresearch and development manager\nresearch and development director','\n',char(10)),'Research and development managers coordinate the efforts of scientists, academical researchers, product developers, and market researchers towards the creation of new products, the improvement of current ones or other research activities, including scientific research. They manage and plan research and development activities of an organisation, specify goals and budget requirements and manage the staff.','','Includes people performing academical research work, product development research and business research and development.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/73d3bee2-7c5e-4de2-8dd9-67241615dfd3','key_16580','','','8219','8219.10','rubber goods assembler',replace('rubber goods production operative\nrubber goods finishing operator\nrubber goods assembly worker\nrubber goods assembler\nrubber products assembler\nrubber goods finisher\nrubber goods production assembler','\n',char(10)),'Rubber goods assemblers manufacture rubber products such as water bottles, swim fins, and rubber gloves. They fasten ferrules, buckles, and straps to rubber goods, and also wrap fabric tape around closures and ferrules.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/73d3ce3c-296c-4f85-9381-2eed6f3ae5fa','key_16581','','','1324','1324.3.1.1','inland water transport general manager',replace('inland water transport general manager\nmanager of inland water transport\nwaterway transportation general manager\ninland water transport director\ninland waterway transport manager\ninland water transportation director\ninland water transportation general manager','\n',char(10)),'Inland water transport general managers coordinate the administrative and dispatching activities that occur in the context of inland water transport. They check availability, liaise with clients, manage budgets, and oversee operations from an economic point of view.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/73d77554-2016-46fa-ac3d-44a02e8d1a89','key_16582','','','7126','7126.11','sewer construction worker',replace('waste water system construction worker\nwaste water infrastructure construction worker\nsewage system worker\nwaste water pipelayer\nsewage infrastructure construction worker\nwaste water system worker\nsewage pipelayer\nwaste water pipe layer\nsewer infrastructure construction worker\nsewer construction worker\nsewage system construction worker\nsewer system construction worker\nsewage pipe layer','\n',char(10)),'Sewer construction workers install sewer pipes to transport wastewater out of structures and to a body of water or treatment facility. They dig trenches and insert the pipes, making sure they have the correct angle and are connected watertight. Sewer construction workers also construct other elements of sewage infrastructure, such as manholes, and maintain and repair existing systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/73e776fb-4d99-4031-bad4-7716f121155d','key_16583','','','3432','3432.1','interior designer',replace('facility interior designer\ninterior & spacial designer\nspace planner & interior decorator\nspatial design specialist\nspatial design expert\ninterior design specialist\ninterior & spatial designer\ndesigner of interiors\nspatial designer\ninterior design expert\ninterior architectural designer\ninterior designer & planner\ninterior space planner\nspace planner\ninterior designer\nspacial designer\ninteriors designer','\n',char(10)),'Interior designers design or renovate of internal spaces, including structural alterations, fixtures and fittings, lighting and colour schemes, furnishings. They combine the efficient and functional use of space with an understanding of aesthetics.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/74332fd0-4a50-464e-9116-98fa9b8127ac','key_16584','','','3131','3131.3.1','electrical power distributor',replace('power line worker\ndistribution control operator\nelectrical power systems power dispatcher\nelectrical power distributor','\n',char(10)),'Electrical power distributors operate and maintain equipment which deliver the energy from the transmission system to the consumer. They supervise power line maintenance and repairs, and ensure the distribution needs are met. They also react to faults in the distribution system which cause problems such as outages.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7443811e-6760-44a2-af95-1b9c791733a8','key_16585','','','7132','7132.1','lacquer spray gun operator',replace('lacquer spraying machine technician\nlacquer sprayer\nlacquer spray machine technician\nlacquer coating machine operator\nlacquer spray painter\nmachine sprayer operative\npaint booth operator\nmastic sprayer\nlacquer coating machine technician\nlacquer spraying machine operator\npaint booth technician\nlacquer spraying machine specialist\nspray gun operator\nmachine sprayer\nlacquer spray machine specialist\nlacquer spray gun specialist\nlacquer spray gun operator\nlacquer spray gun technician\nlacquer spray gun operative\nautomatic paint booth operator\nlacquer paint sprayer\nlacquer coating machine specialist\nautomatic paint sprayer operative\nlacquer spray machine operator\nautomatic paint sprayer operator\nlacquer-sprayer\nmachine sprayer operator\nautomatic paint booth operative','\n',char(10)),'Lacquer spray gun operators operate lacquer spray guns designed to provide otherwise finished metal, wooden or plastic workpieces with a hard, durable finishing coat, through lacquer coating or paint that is either matte, sheen or highly glossy, but is always meant for hard surfaces.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/745c8bb3-b50b-4380-9377-1b68a6576986','key_16586','','','3119','3119.18','textile process controller',replace('textile process controllers\nquality assurance supervisor\ntextile process controller\ntextile process supervisor\ntextile process coordinator\ntextile process inspector\ntextile process checker','\n',char(10)),'Textile process controllers perform textile process operations, technical functions in various aspects of design, production and quality control of textile products, and cost control for processes. They use computer aided manufacturing (CAM), and computer integrated manufacturing (CIM) tools in order to ensure conformity of entire production process to specifications. They compare and exchange individual processes with other departments (e.g. cost calculation office) and initiate appropriate actions. They analyse the structure and properties of raw materials used in textiles and assist to prepare specifications for their production, analyse and interpret test data.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/74cbe6fb-336f-4f3f-8c8f-7645b279a231','key_16587','','','5152','5152.2.1','domestic butler',replace('major-domo\ndomestic butler\nbutler\ndomestic house manager','\n',char(10)),'Domestic butlers serve at official meals, monitor meal preparations and table setting and manage the household staff. They may also offer personal assistance in booking travel arrangements and restaurants, valeting and clothing care.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/74d47633-8e9e-4e08-9cba-d2292c211218','key_16588','','','4323','4323.5','dangerous goods safety adviser',replace('dangerous goods safety person\ndangerous goods transport person\nhazardous goods safety adviser\ndangerous goods transport adviser\ndangerous goods transport guide\ndangerous goods safety adviser\ndangerous goods safety guide\ndangerous goods safety consultant','\n',char(10)),'Dangerous goods safety advisers inspect and make transport recommendations in line with the European regulations regarding the transport of dangerous goods. They may advise on the transport of dangerous goods by road, rail, sea and air. Dangerous goods safety advisers also prepare safety reports and investigate safety infringements. They provide individuals with the procedures and instructions to follow during the loading, unloading and transporting of these goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/753939ad-b03e-47cd-913b-56f30b5ae8fc','key_16589','','','7543','7543.7','product assembly inspector',replace('assembled products quality inspector\nconsumer goods production inspector\nconsumer goods quality control inspector\nassembly inspector\nassembly quality compliance inspector\nproducts\nproducts assembly inspector\nassembled products quality control inspector\ngoods compliance inspector\nconsumer goods quality inspector\nproduct assembly inspector\nproduct compliance inspector\nassembled products production inspector','\n',char(10)),'Product assembly inspectors evaluate products for compliance with specifications and defects in-line with clients'' requirements and organisational policies. They use measuring and testing equipment to ensure conformity to engineering and manufacturing specifications, quality, and safety standards and regulations. They also provide detailed inspection documentation and recommend action where problems are discovered.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/754c349d-67d5-48e5-81c1-e84d2fab59d0','key_16590','','','2230','2230.1','aromatherapist',replace('aromatherapist\naromatherapy practitioner\ntraditional aromatherapist\nmaster aromatherapist\nrelaxation therapist\ntraditional practitioner of aromatherapy\npractitioner of aromatherapy\naromatherapy specialist\nclinical aromatherapy practitioner\nspecialist aromatherapist','\n',char(10)),'Aromatherapists use essential oils extracted from herbal products at the level of the interface skin and mucous membranes to improve the clients` well-being. They treat a variety of illnesses and physical or psychological conditions, under supervision and on a doctor''s order.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/754ce08f-b4d9-4b9c-b9d2-06f15258a3ce','key_16591','','','1346','1346.1','bank manager',replace('regional bank manager\ncorporate bank manager\nbanking operations manager\nbank manager\nbank executive officer\nfinancial institution branch manager\nbank branch manager\nfinancial services branch manager\nbank branch administrative manager\nbank director\nbank business development manager\nretail bank manager','\n',char(10)),'Bank managers oversee the management of one or several bank activities. They set policies which promote safe banking operations, ensure the economic, social and commercial targets are met and that all the bank departments, activities and commercial policies are in compliance with legal requirements. They also manage employees and maintain an effective working relationship among the staff.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7556c748-15b4-4e44-97d9-00d885d4cd7d','key_16592','','','2621','2621.3','conservator',replace('furniture conservator\nconservator\nfurniture conservation officer\nbuilding conservation officer\nbooks conservator','\n',char(10)),'Conservators organise and valorise works of art, buildings, books and furniture. They work in a wide range of areas such as creating and implementing new collections of art, preserving heritage buildings by applying restoration techniques as well as foreseeing the conservation of literary works, films, and valuable objects.','','Includes people performing restoration.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/755c5c5f-2ee3-4487-94db-30ec2ba2e883','key_16593','','','3112','3112.1.10','rail maintenance technician',replace('rail inspector\nrail maintenance technician\nrail track inspector\nrail repairer\nrail quality inspector\nrail track maintenance inspector\npermanent way inspector\ntrack quality inspector','\n',char(10)),'Rail maintenance technicians execute routine inspections of railway tracks, powerlines, signage stations, switches, and other railway infrastructure. They are also sent out to repair defects quickly, safely, and at any time of the day or night.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7569f423-2c37-49aa-947e-41acb66ec909','key_16594','','','2146','2146.10','petroleum engineer',replace('petroleum engineers\npetroleum engineer\npetroleum geologist\npetroleum production technician\nreservoir engineer\noil and gas engineer\ngas engineer','\n',char(10)),'Petroleum engineers evaluate gas and oil fields. They design and develop methods for extracting oil and gas from underneath the earth''s surface. They maximise the recovery of hydrocarbon at a minimum cost, pursuing minimal impact on the environment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/757da94f-d1f3-48ad-ac61-74e22bc18cca','key_16595','','','2643','2643.1','graphologist',replace('handwriting expert\nforensic handwriting expert\ngraphologists\nhandwriting specialist\ngraphologist\nforensic document examiner','\n',char(10)),'Graphologists analyse written or printed materials in order to draw conclusions and evidence about traits, personality, abilities and authorship of the writer. They interpret letter forms, the fashion of writing, and patterns in the writing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7591db5b-ee01-4b6b-b626-ddaf892458d4','key_16596','','','2635','2635.3.9','crisis situation social worker',replace('emergency social worker\ndisaster social worker\ncrisis situation worker\ncrisis situation social worker\ncrisis intervention counsellor\ncrisis response social worker\ncrisis situation counsellor\ncrisis intervention social worker','\n',char(10)),'Crisis situation social workers provide emergency support and assistance to persons with physical or mental disorders by addressing their distress, impairment, and instability. They assess the level of risk, mobilise client resources, and stabilise the crisis.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/75b63949-1b93-4bf2-a777-ccf978dc3e8a','key_16597','','','7411','7411.1.4','solar energy technician',replace('solar operations and maintenance technician\nphotovoltaic energy installer\nsolar energy installation technician\nsolar energy system technician\nsolar energy panel installer\nphotovoltaic cell technician\nsolar energy array technician\nsolar energy harvesting technician\nsolar energy system maker\nsolar energy panel technician\nPV field technician\nsolar energy technician\nsolar energy system builder\nsolar farm technician\nsolar energy system installer\nsolar roofer\nsolar energy system constructor\nsolar O and M technician\nsolar energy plant technician\nphotovoltaic field technician\nsolar energy generation technician\nphoto-voltaic cell technician\nphotovoltaic energy panel installer\nsolar energy system designer\nsolar energy farm technician\nsolar technician\nphotovoltaic system technician','\n',char(10)),'Solar energy technicians install and maintain systems that collect solar energy. They prepare the necessary fixtures, often on roofs, install solar panels, and plug them into an electronic system including an inverter to connect the solar energy systems to the electricity lines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/75c5bab4-2546-4ba3-996d-42a9a1bab55d','key_16598','','','7421','7421.6','railway electronic technician',replace('rail electrician\ntelecommunication electronic technician\nrail electronic inspector\nrailway overhead power engineer\nelectrician electric engines\nrailway electronic technician\nelectrician diesel engines\nelectrical train electrician\nelectrical rail electrician\noverhead power engineer\nrailway line side equipment engineer\nrailway electronic inspector\ntelecommunication electronic inspector\nlocomotive electrician','\n',char(10)),'Railway electronic technicians are responsible for installing, inspecting, testing, and maintaining train control systems, radio, radar, electronic and electromagnetic components.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/75f4c7aa-019f-4545-9ce1-2d163fe78580','key_16599','','','7126','7126.7','pipeline maintenance worker',replace('oil pipeline maintenance centre technician\nwater pipeline network technician\ngas  pipeline worker\ngas pipeline maintenance operator\ngas pipeline network technician\ngas pipeline operator\noil pipeline network technician\nwater pipeline maintenance operator\nwater pipeline maintenance centre technician\noil pipeline worker\npipeline maintenance worker\npipeline technician\noil pipeline maintenance operator\npipeline maintenance operator\noil pipeline technician\nwater pipeline technician\nwater  pipeline worker\ngas pipeline maintenance centre technician\npipeline maintenance centre technician\npipeline network technician\noil pipeline operator','\n',char(10)),replace('Pipeline maintenance workers operate different equipment to keep the suitability of the pipelines. They perform checks for deviations and administer chemicals according to the needs and cleaning aim (e.g. corrosion shunning) \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/75fb7771-0403-4116-9c01-9c5717bec600','key_16600','','','1420','1420.4.25','furniture shop manager',replace('outdoor furniture shop manager\nlighting equipment and accessories shop manager\nfurniture and lighting store manager\noutdoor furniture store manager\nhome furnishings shop manager\nhome articles shop manager\nfurniture store manager\nfurniture shop manager\nhome articles store manager\nhome furnishings store manager\ngarden furniture shop manager\ngarden furniture store manager\nlightning equipment and accessories store manager\nfurniture and lighting shop manager','\n',char(10)),'Furniture shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/760ad7dd-f29d-4e4d-be1f-15100550ab80','key_16601','','','7532','7532.1','clothing CAD patternmaker',replace('fabric CAD patternmaker\nfabric CAD pattern maker\nclothing CAD patternmaker\ncomputer patternmaker\nclothing CAD pattern maker','\n',char(10)),'Clothing CAD patternmakers design, evaluate, adjust and modify patterns, cutting plans and technical files for all kinds of wearing apparel using CAD systems, acting as interfaces with digital printing, cutting and assembly operations, being aware of the technical requirements on quality, manufacturability and cost assessment. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7639a601-6db0-41ed-9fb0-813d9b8beb05','key_16602','','','2269','2269.8','radiographer',replace('nuclear medicine technologist\nmedical radiation technologist\nclinical radiography practitioner\nnuclear medicine technician\npractitioner of radiography\nspecialist radiographer\nclinical radiographer\nexpert radiographer\nradiographer','\n',char(10)),'Radiographers use a range of technologies to examine, treat and care for patients. They work in the fields of Medical Imaging, Radiotherapy and Nuclear Medicine and apply ionising radiation, ultrasound, magnetic resonance imaging and radioactive sources.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/763f82c2-81e3-48d2-9cdf-f532b5016a1f','key_16603','','','5142','5142.12','weight loss consultant',replace('health coach\nnutritionist\npersonal care consultant\nhealth and fitness consultant\nweight loss counsellor\nweight loss consultant\nfitness and nutrition consultant\nweight loss coach\nweight loss advisor\nnutrition and weight loss consultant\nhealth and wellness consultant','\n',char(10)),'Weight loss consultants assist clients in obtaining and maintaining a healthy lifestyle. They advise on how to lose weight by finding a balance between healthy food and exercise. Weight loss consultants set goals together with their clients and keep track of progress during weekly meetings. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7642cbcc-9839-47fb-b2cf-d4bb831d6c93','key_16604','','','3521','3521.1','audio-visual technician',replace('audiovisual assistant\naudio & video operator\naudio and video operator\naudio technician\naudio-video operator\nvideo technician\naudio-visual assistant\naudio-video technician\naudio-visual technician\naudio-visual engineer\naudio-visual operator\naudio-visuals technician','\n',char(10)),'Audio-visual technicians set up, operate and maintain equipment to record and edit images and sound for radio and television broadcasts, at live events and for telecommunication signals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7664f3e6-b469-4e1a-b52c-8a7ac866eb5a','key_16605','','','8121','8121.7','wire weaving machine operator',replace('wire mesh operator\nwire worker\nwire bending operator\nwire weaving operative\nwire winding machine operator\nwire winding machine operative\nwire weaving operator\nwire weaving loom operative\nwire preparation weaving operative\nwire worker apprentice\nwire weaving machine operator\nwire mesh knitter\nwire weaving loom operator\nwire bender\nwire preparation weaving operator','\n',char(10)),'Wire weaving machine operators set up and tend wire weaving machines, designed to produce woven metal wire cloth out of the alloys or ductile metal that can be drawn into wire.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/76884a72-d8b8-4819-bd40-71d6420a9a42','key_16606','','','2619','2619.3','court jury coordinator',replace('court usher\nsolicitor\ncourt adminsistrative officer\nbailiff\njury consultant\ncourt consultant\ncoordinators for court jury\nlegal executive\ntrial assistant\nparalegal\nlegal secretary\ntrial administator\ncourt jury coordinator\ntrial consultant\ncoordinator for court jury\ncourt legal advisor','\n',char(10)),'Court jury coordinators aid lawyers in the preparation of the trial by researching jury members. They aid in the development of trial strategies, analyse the jury''s behaviour during a trial, and advise lawyers on proceedings. They also assist in preparing witnesses and constructing arguments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/769159bd-2929-4d40-8aeb-a99a387a0829','key_16607','','','8211','8211.4','motorcycle assembler',replace('cycle constructor\nbicycle constructor\nmoped builder\nmotorcycle assembler\nbicycle builder\ncycle assembler\nmoped assembler\nbike constructor\nscooter constructor\nmoped constructor\nscooter builder\nmotorbike assembler\nscooter assembler\nbike builder\nmotorbike constructor\ncycle builder\nmotorbike builder','\n',char(10)),'Motorcycle assemblers fasten motorcycle parts and components together such as frames, wheels, engines etc. To do so, they use hand tools, power tools and other equipment such as CNC machines or robots. They read technical plans and use automated assembling equipment to build motorcycles. They inspect individual parts for malfunctions and check the quality of assemblies to make sure the standards are met and the specifications respected.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7696b15a-c52e-4ac5-b38d-9eeb010c5fa3','key_16608','','','0110','0110.6','artillery officer',replace('munitions officer\ninfantry officer\ngunner\nartillery technician\nartillery officer','\n',char(10)),'Artillery officers offer support in battle in the fields of target acquisition, coordination of information, surveillance and weaponry. They are in charge of tactics, and deployment of weaponry on the field. They also lead soldiers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/76a1a76c-b014-4600-8901-56cb936de7ed','key_16609','','','2519','2519.2','ICT auditor manager',replace('IT audit manager\nICT auditor managers\nICT audit manager\nICT auditor manager\ninformation technology audit manager','\n',char(10)),'ICT auditor managers monitor ICT auditors responsible for auditing information systems, platforms, and operating procedures in accordance with established corporate standards for efficiency, accuracy and security. They evaluate ICT infrastructure in terms of risk to the organisation and establish controls to mitigate loss. They determine and recommend improvements in the current risk management controls and in the implementation of system changes or upgrades.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/76a950cb-3def-47f4-895d-555ad138657e','key_16610','','','2342','2342.3','Montessori school teacher',replace('Montessori assistant to infancy\nMontessori multi-age classroom teacher\nMontessori educator\nMontessori teacher\nMontessori school teacher\nMontessori elementary teacher\nmulti-age classroom teacher\ncomposite class Montessori teacher\nMontessori primary teacher\nteacher in a Montessori school\nMontessori school instructor\nMontessori school teaching practitioner','\n',char(10)),'Montessori school teachers educate students using approaches that reflect the Montessori philosophy and principles. They focus on constructivist and "learning through discovery" teaching models, through which they encourage students to learn from first-hand experience rather than through direct instruction and thus provide the students with a relatively high level of freedom. They adhere to a specific curriculum that respects the students'' natural, physical, social and psychological development. Montessori school teachers also teach classes with students differing up to three years in age in rather large groups, manage, and evaluate all the students separately according to the Montessori school philosophy.','','Includes people working in institutions at other education levels.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/76abbb82-c103-4d7a-a4c0-14dba4d6199a','key_16611','','','2149','2149.6','commissioning engineer',replace('installation and commissioning engineer\ntest and commissioning engineer\ncommissioning engineer\ncommissioning engineer machines\ncommissioning and qualification engineer','\n',char(10)),'Commissioning engineers supervise the final stages of a project when systems are installed and tested. They inspect the correct functioning of the equipment, facilities and plants to make sure they meet the requirements and specifications. They perform the necessary verifications and give approval to finalise the project.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/76bded28-876a-40b2-9efb-3123c8f14cf6','key_16612','','','3214','3214.2','dental technician',replace('dental technician\nclinical dental technician\nsenior dental technologist\ndental prosthetist\nmaxillo-facial prosthetic technician\ndental technologist\nmaxillo-facial prosthetic technologist\nchief dental technician\nprosthetic technologist\nchief dental technologist\northodontic technologist\nsenior dental technician\northodontic technician','\n',char(10)),'Dental technicians manufacture dental custom-made devices like bridges, crowns, dentures and appliances under the supervision of dental practitioners following their directions and specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/76deb33d-f9bc-4ca2-a5cc-49c8f5a17f38','key_16613','','','2659','2659.2.6','variety artist',replace('ventriloquist\nhypnotist\nfantasist\nmusician\nvariety artist\nescape artist\nvariety performer\nlookalike artist\nmagical performer\nburlesque performer\nburlesque artist\nillusionist\nsword swallower\nmusical hall artist\nmagician\npantomime actor\nvariety arts perfomer\nmime artist\nentertainer','\n',char(10)),'Variety artists are multi-disciplinary artists who master in at least two of the following disciplines: comedy, dance, singing, circus arts, object manipulation and illusionism. They perform solo or collectively, may appear in music variety shows, cabaret, musicals and other entertainment events. Their artistic performance is characterised by the blending of arts, styles and disciplines.  ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/76ef0a87-6afe-4560-b5d0-9a086abe45c5','key_16614','','','2529','2529.4','ethical hacker',replace('cybersecurity specialist\nsystem security tester\npenetration tester\nvulnerability analyst\nICT security tester\nethical hacker\nnetwork security tester','\n',char(10)),'Ethical hackers perform security vulnerability assessments and penetration tests in accordance with industry-accepted methods and protocols. They analyse systems for potential vulnerabilities that may result from improper system configuration, hardware or software flaws, or operational weaknesses.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7706342e-c07b-41d3-bc2e-e5c6e96f016f','key_16615','','','7313','7313.1.4','jewellery mounter',replace('jewelry mount maker\nbench jeweler\njewellery mount maker\nbench jeweller\njewelry mounter\njeweller/mounter\ngem stone mounter\ngemstone mounter\ndiamond mounter\nprecious stone mounter','\n',char(10)),'Jewellery mounters create the framework for a piece of jewellery, on which the precious stones are added to later.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/771244bf-b204-4e7f-8b25-ee523ae7b791','key_16616','','','2267','2267.2','orthoptist',replace('expert orthoptist\northoptics specialist\nspecialist orthoptist\northoptics practitioner\northoptics expert\nqualified orthoptist\northoptist\npractitioner of orthoptics\nspecialist in orthoptics','\n',char(10)),replace('Orthoptists diagnose and treat anomalies of binocular vision. They examine, assess and treat vision impairments, squint, amblyopia and eye motility disorders. Orthoptists apply these methods in particular in the fields of paediatrics, neurology, neuro-ophthalmology, ophthalmology, orthoptics, optometry, pleoptics and strabology assessing functional diseases of the eye for improving functional disorders of the visual system. They also provide counselling, preventive measures and training activities and may refract and prescribe glasses, such as prism corrective glasses. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/771955d9-e988-47f3-b60f-207adb2cd571','key_16617','','','1431','1431.2','recreational facilities manager',replace('leisure facility manager\nrecreational facilities director\nfitness manager\nrecreational facilities coordinator\nvacation facilities manager\ncinema manager\nrecreational camp manager\ncultural facility manager\namusement park manager\nsports facility manager\nrecreational facilities manager\nrecreational facilities executive\ntheatre manager\nrecreation establishment manager','\n',char(10)),'Recreational facilities managers direct the operations of facilities that provide recreational services such as gardens, spas, zoos, gambling and lottery facilities. They plan and organise the daily operations of the related staff and facilities and ensure the organisation follows the latest developments in its field. They coordinate the different departments of the facility and manage the correct use of rescources and budgets.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/771a47c9-9cf4-4368-9752-2a1ea5a783d7','key_16618','','','3411','3411.8','private detective',replace('inspector\nsleuth\nprivate dick\nprivate detective\nprivate investigator\nprivate eye\nPI','\n',char(10)),replace('Private detectives research and analyse information to uncover facts for personal, corporate or legal reasons, depending on their clients. They conduct surveillance activities, which includes taking photos, doing background checks and interviewing individuals. Private detectives may help in criminal and civil cases, child custody, financial fraud, on-line harassment and may search for missing people. They compile all the information into a file and hand it to their clients for further action.\n\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7720f392-6ea0-4455-a805-7b7eef38a709','key_16619','','','5112','5112.3.1','passenger fare controller',replace('ticket controller\nfare controller\nticket inspector\npassenger ticket attendant\nticket conductor\npassenger fare controller\ncontroller\ncontroller of cable vehicle fares\npassenger ticket controller\ncable vehicle attendant','\n',char(10)),'Passenger fare controllers collect tickets, fares, and passes from passengers. They answer questions from passengers concerning transport rules, station, and timetable information.','','Excludes drivers.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/772e57c5-fb25-4b0c-8cc8-f8944a9edfbc','key_16620','','','8142','8142.5','fibreglass machine operator',replace('fiberglass-spraying machine operator\nlead machine operator\nfibreglass-spraying machine operator\noperator of fibreglass-spraying machine\noperator of fiberglass-spraying machine\nfiberglass machine operator\nfibreglass spraying machine technician','\n',char(10)),'Fibreglass machine operators control and maintain the machine that sprays a mix of resin and glass fibers onto products such as bathtubs or boat hulls to obtain strong and lightweight composite end-products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/773753ad-c4f7-4052-b3b4-440435199d6b','key_16621','','','5223','5223.7.12','cosmetics and perfume specialised seller',replace('beauty and aroma salesperson\ncosmetics and perfume specialized seller\nbeauty and aroma specialised seller\nbeauty salesperson\nbeauty specialised seller\ncosmetics salesperson\nperfume specialised seller\ncosmetics specialised seller\ncosmetics and toiletries specialised seller\ncosmetics and toilet articles specialised seller\ncosmetics and toiletries salesperson\ncosmetics and perfume salesperson\ncosmetics and toilet articles salesperson\nmake-up and cosmetics specialised seller\nmake-up and cosmetics salesperson\nperfume salesperson','\n',char(10)),'Cosmetics and perfume specialised sellers sell cosmetics and toilet articles in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/773cf1de-50a4-42e7-bab1-e68dcbae3f54','key_16622','','','9215','9215.1','forest worker',replace('woodland worker\nsilviculture worker\nwoodland assistant\nforest worker\nforestry worker\nforestry assistant','\n',char(10)),'Forest workers carry out a variety of jobs to care for and manage trees, woodland areas and forests. Their activities include planting, trimming, thinning and felling trees and protecting them from pests, diseases and damage.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/773e5ea3-235b-4e72-afc5-290dd841eed8','key_16623','','','3432','3432.1.1','interior planner',replace('interior space planner\ncommercial space planner\nspatial planner\ninterior adviser\ninterior and spatial planner\ninterior design consultant\ninterior architect\ninterior designer\ninterior planner','\n',char(10)),'Interior planners help clients in planning their interiors for commercial and private use.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/774ad663-2124-4ac2-a287-5fb2e478906e','key_16624','','','3111','3111.10','nuclear technician',replace('nuclear safety technician\nnuclear equipment operation technician\nradiation safety technician\nnuclear monitoring technician\nnuclear equipment technician\nnuclear technician\nnuclear plant technician\nradioactive equipment technician\nnuclear power plant technician','\n',char(10)),'Nuclear technicians function as aids to physicists and engineers in nuclear laboratories and power plants. They monitor the procedures to ensure safety and quality control, and maintain equipment. They also handle and control radioactive equipment and measure radiation levels to ensure safety.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7754d570-9519-48c2-b1c9-8e165f8bca0f','key_16625','','','2529','2529.8','ICT security manager',replace('ICT security manager\nICT security chief\nICT technical security expert\nIT security manager\ninformation security manager\nsecurity coordinator\nIT security chief','\n',char(10)),replace('ICT security managers propose and implement necessary security updates. They advise, support, inform and provide training and security awareness and take direct action on all or part of a network or system.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/776837b1-6f6b-4014-8330-d56918303e93','key_16626','','','3422','3422.4.5','snowboard instructor',replace('snowboard coach\nsnowboard and ski instructor\nsnowboard instructor\nski and snowboard instructor\nski & snowboard instructor\nsnowboard teacher\nsnowboard trainer\nsnowboard & ski instructor','\n',char(10)),'Snowboard instructors teach groups or individuals how to ride a snowboard. They instruct students of all ages and skill level individually or in groups. Snowboard instructors teach basic and advanced techniques of snowboarding by demonstrating exercises and giving feedback to students. They give advice on safety and on snowboarding equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/778c1d37-69f2-4184-ac98-03b7dbfba332','key_16627','','','2149','2149.12.1','photonics engineer',replace('engineer in photonic systems\nphotonic systems test engineer\nengineer in photonics\nphotonics engineering expert\nphotonics engineering specialist\nelectro-optical engineer\nphotonic technology engineering consultant\nphotonic technology engineer\nphotonic technology engineering specialist\nphotonics engineering consultant\nphotonics systems engineer\nphotonics engineering adviser\nphotonic technology engineering expert\nengineer specialised in photonic systems\nphotonic technology engineering adviser\nlaser engineer\nphotonics engineer\nphotonic engineer\noptical systems engineer','\n',char(10)),'Photonics engineers are concerned with the generation, transmission, transformation, and detection of light. They conduct research, design, assemble, test and deploy photonic components or systems in multiple application fields, from optical communications to medical instrumentation, material processing or sensing technology.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/779d13be-60b4-48f3-a339-fe483be96c7f','key_16628','','','2619','2619.12','regulatory affairs manager',replace('regulatory compliance manager\nregulatory affairs manager\nregulatory affairs officer\ncompliance manager\nchief compliance officer\nregulatory affairs specialist\nregulatory affairs portfolio leader\nbank compliance officer\nregulatory manager\nregulatory compliance officer','\n',char(10)),'Regulatory affairs managers are in charge of regulatory and legal affairs in several sectors such as the healthcare, energy and banking industries. They oversee the development of products and services from inception to market release by making sure everything complies with the local legislation and meets regulatory requirements. They have experience in the different phases of regulatory processes and act as an interface between business and government legislation or regulatory boards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/77a05858-f682-4648-baa5-9b86deeffbc1','key_16629','','','7523','7523.3','wooden furniture machine operator',replace('wood furniture machine specialist\ntimber furniture machine operative\nwooden furniture machinist\ntimber furniture machinist\nwood furniture machine technician\ntimber furniture machine tender\nwooden furniture machine technician\ntimber furniture machine setter\ntimber furniture machine worker\nwooden furniture machine tender\nwooden furniture machine setter\nwooden furniture machine operator\nwood furniture machine worker\nwood furniture machine operator\nwooden furniture machine worker\nwooden furniture machine operative\nwooden furniture machine specialist\nwood furniture machinist','\n',char(10)),'Wooden furniture machine operators run machines that manufacture wooden furniture parts, according to the established operating procedure. They ensure the machine works smoothly and repair parts if necessary.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/77a25ee0-94ad-443e-91ea-3e6d3506181b','key_16630','','','7223','7223.24','thread rolling machine operator',replace('thread rolling machine operator\nthread rolling machinist\nthread rolling operator\nthread machine rolling operator\nthread machine roller\nthread-mill roller\nthread roller\nthread machine rolling machinist\nthread mill rolling machinist\nthread rolling machine technician\nthread mill roller','\n',char(10)),'Thread rolling machine operators set up and tend thread rolling machines designed to form metal workpieces into external and internal screw threads by pressing a thread rolling die against metal blank rods, creating a larger diameter than those of the original blank workpieces.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/77abfaec-a250-4765-95fa-6091e8da1bba','key_16631','','','2151','2151.1.3','electromechanical engineer',replace('mechanicals engineer\nelectromechanical assembly engineer\nelectromechanical technology engineering specialist\nelectromechanic engineer\nelectromechanical design engineer\nelectro-mechanical engineer\nengineer in electro-mechanics\nengineer of electro-mechanics\nspecialist in electromechanics\nengineering technician\nelectro-mechanical assembly engineer\nelectro mechanic engineer\nelectromechanical technology engineering adviser\nelectromechanical equipment engineer\nspecialist in electromechanical systems\nelectromechanical systems engineer\nelectro-mechanics engineer\nengineer of electromechanics\nengineer in electromechanical systems\nelectromechanical development engineer\nelectro mechanical engineer\nelectromechanical engineering consultant\nelectromechanical technology engineering expert\nelectric machinery engineer\nelectro mechanics engineer\nelectro-mechanical designer\nelectromechanical designer\nelectromechanical engineering specialist\nelectromechanical technology engineering consultant\nengineer in electromechanics\nelectro-mechanical equipment engineer\nelectromechanical technology engineer\nelectromechanical prototype developer\nelectromechanical engineering adviser\nelectro-mechanic engineer\nelectrical machinery engineer\nelectro-mechanical design engineer\nelectromechanical engineering expert\nelectro-mechanical prototype developer\nelectromechanical engineer\nelectro-mechanical development engineer\nelectromechanical systems control engineer','\n',char(10)),'Electromechanical engineers design and develop equipment and machinery that use both electrical and mechanical technology. They make draughts and prepare documents detailing the material requisitions, the assembly process and other technical specifications. Electromechanical engineers also test and evaluate the prototypes. They oversee the manufacturing process.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/77bfd6e7-5598-4818-84cb-31e2651eb046','key_16632','','','2163','2163.1.3','fashion designer',replace('fashion designers\ntextile expert\nchildrenswear designer\napparel stylist\ntextile specialist\naccessory designer\nfootwear designer\nsportswear designer\nhigh fashion designer\napparel designer\nfashion stylist\nswimwear designer\nfashion designer\nfashion specialist\nfashion expert\nfashion design specialist\nfashion design expert\nfashion artist','\n',char(10)),'Fashion designers work on designs for the haute couture and/or ready-to-wear, high street fashion markets, and more generally on items of clothing and fashion ranges. Fashion designers can operate in a specialised area, such as sportswear, childrenswear, footwear or accessories.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/77de6c7d-27bf-4bc2-8c80-5d6f6d9811f4','key_16633','','','7322','7322.8','reprographics technician',replace('print room technician\nprint room operator\nmailroom specialist\nreprographics technician\nreprographic process specialist\nreprography technician\nreprographics operator\nreprographics administrator\nreprographics assistant\nreprographic process technician\nreprographics associate\nreprography operator\nreprography specialist\nprint room specialist\ncopy machine operator\nmailroom technician\nreprographics specialist','\n',char(10)),'Reprographics technicians are responsible for the full or partial process of reproduction of graphical documents through mechanical-driven or digital means, such as photography, scanning or digital printing. These activities are commonly performed for purposes of maintaining archives or other structured catalogues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7819669b-30c2-42e6-a1c4-0bcd6f0379ff','key_16634','','','2422','2422.9','international relations officer',replace('international relations officer\ninternational development officer\ninternational relations director\ninter governmental cooperation officer\ninternational manager','\n',char(10)),'International relations officers ensure the development of cooperation between international public organisations and governments. They facilitate communication between their organisation and foreign organisations and develop collaboration strategies, promoting a collaborative relationship beneficial for both parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/78216000-edc1-474b-9569-28ed6a657da2','key_16635','','','2166','2166.4','desktop publisher',replace('desktop-publishing editor\nweb publications designer\nfinancial desktop publisher\ndesktop publishing graphic designer\nDTP operator\nadvanced desktop publilsher\nlayout editor\ndesktop publisher\nsenior desktop publisher\nelectronic-publishing specialist\npublications specialist\nelectronic pre-press technician\ndesktop designer\nelectronic publisher','\n',char(10)),'Desktop publishers are responsible for the layout of publications. They use computer software to arrange texts, photographs and other materials in a pleasing and readable finished product.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/782ced63-e234-42a4-a7bd-a2d9a12ee61a','key_16636','','','8131','8131.1','capsule filling machine operator',replace('pharmaceutical machine operative\npharmaceutical machine operator\ncapsule filler\nfilling machine operator\ncapsule machine operator\ncapsule filling machine operator\ncapsule machine operative','\n',char(10)),'Capsule filling machine operators control the filling of gelatine capsules with the specific medicinal preparations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7833d5cd-873d-4fdd-b2f8-9762d68494a7','key_16637','','','3119','3119.2.1','robotics engineering technician',replace('technician in robotic systems\ntechnician in robotics engineering\nrobotics engineering technologist\nrobotics systems technologist\nrobotics mechanic\nrobotics engineering technician\nautomation engineering inspector\nrobotics inspector\ntechnician in robotics\nrobotics technician\nrobot engineering technologist\nrobot technician\nrobotics engineering mechanic\nrobot engineering inspector\nrobotics technologist\nrobot technologist\ntechnologist in robotics\nrobotic systems technician\nautomation control engineering inspector\ntechnologist in robotics engineering\nrobot inspector','\n',char(10)),'Robotics engineering technicians collaborate with engineers in the development of robotic devices and applications through a combination of mechanical engineering, electronic engineering, and computer engineering. Robotics engineering technicians build, test, install and calibrate robotic equipment.','','Excludes <a resource="http://data.europa.eu/esco/occupation/5791a961-4717-4230-a245-a4082a675f04" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/5791a961-4717-4230-a245-a4082a675f04"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">automation engineering technician</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7843a4c6-a9f6-4095-94b9-45b384d4bd57','key_16638','','','4227','4227.1','market research interviewer',replace('market research interviewer\nresearch interviewer\nmarket research analyst\nsurvey interviewer\npublic opinion researcher\nmarket research representative\nmarketing recruiter\nmystery shopper\nphone interviewer','\n',char(10)),'Market research interviewers strive to collect information on the perceptions, opinions, and preferences of customers in relation to commercial products or services. They use interview techniques to draw as much information as possible by contacting people via telephone calls, by approaching them face-to-face or by by virtual means. They pass this information to experts for drawing analysis.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/784c98c2-96bd-4610-8da3-87d80f136285','key_16639','','','1324','1324.3.4.1','finished leather warehouse manager',replace('finished leather warehouse supervisor\nfinished leather warehouse manager\nfinished leather warehouse forewoman\nfinished leather warehouse foreman\nfinished leather warehouse operations manager\nfinished leather warehouse managers','\n',char(10)),'Finished leather warehouse managers organise and monitor the finished leather warehouse, stock levels and organise product packing and dispatch. They maximise the warehouse space and manage time to control the orders.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/78625dd8-257b-483b-bca0-172dd9b02754','key_16640','','','8211','8211.5.3','motor vehicle parts assembler',replace('automotive parts assembler\ncar parts assembler\nlorry parts assembler\nauto parts assembler\nmotor vehicle parts tester\nmotor vehicle parts inspector\nmotor vehicle parts assembler','\n',char(10)),'Motor vehicle parts assemblers fasten motor vehicle parts and components together. They use hand and tools power tools to connect wiring and cables, position and adjust parts. Motor vehicle parts assemblers also set up programmable devices or robots. They test electrical assemblies and equipment and inspect individual parts for malfunctions. They check the quality of assemblies to make sure the standards are met and the specifications are complied with.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/788a9895-a39a-4a83-be90-e006e2f8dce5','key_16641','','','1324','1324.6','pipeline superintendent',replace('supervisor of pipeline\ndistribution network planner\npipeline project director\npipeline project manager\ndistribution network strategy planner\npipeline superintendent\ndistribution network monitoring manager\ndistribution network manager','\n',char(10)),'Pipeline superintendents manage the direction and overall development of pipeline transport projects. They envision the planning, the route selection, the management of resources, and the daily operations. They develop long-term vision safeguarding efficiency of the infrastructure.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/789517fb-95ee-43d1-9530-c2860be1a439','key_16642','','','2263','2263.3','health and safety officer',replace('occupational health officer\noccupational health and safety officer\nworkplace health and safety advisor\noccupational health nurse\npublic health inspector\nenvironmental and occupational heath officer\noccupational hygienist\nhealth and safety advisor\nhealth and safety officer','\n',char(10)),'Health and safety officers execute plans for the improvement of work environments and cultures. They assess risks and interview employees to ensure the work environment is compliant with health and safety rules, as well as ensuring the interaction within the workplace occurs in a positive and productive manner. If employed in a healthcare facility the health and safety officer has to investigate infections spreading across a facility and advice all employees how to combat and prevent infections. Furthermore health physics is applied at facilities where people are exposed to ionizing radiation such as nuclear power plants and research institutions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/789a1a35-8e03-42da-857f-4216c4d2ac68','key_16643','','','3324','3324.4.33','wholesale merchant in waste and scrap',replace('senior wholesale merchant in waste and scrap\nbusiness to business sales consultant in waste and scrap\nbusiness to business seller in waste and scrap\nwholesale buyer in waste and scrap\ntrainee wholesale merchant in waste and scrap\nwholesale trader in waste and scrap\nwholesale merchant in waste and scrap\ngraduate wholesale merchant in waste and scrap\nwholesaler in waste and scrap\nassistant wholesale merchant in waste and scrap\nwholesale merchandiser in waste and scrap\nB2B sales consultant in waste and scrap','\n',char(10)),'Wholesale merchants in waste and scrap investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/789d9781-8724-4367-936a-3a4cb7c54315','key_16644','','','2269','2269.9.3','dance therapist',replace('dance therapist\nmovement therapist','\n',char(10)),'Dance therapists support individuals with their emotional, mental or physical health problems helping them to improve their body awareness, self-esteem, social integration and personal development through dance and movement patterns within a therapeutic environment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/78db356f-a96a-4456-ab29-2de62e52f830','key_16645','','','2411','2411.1.11','public finance accountant',replace('public finance manager\ntreasury accountant\ntreasury manager\npublic finance accountant\nfinance accountant\nfinance accountant of government administration\ntreasury officer\npublic finance officer','\n',char(10)),'Public finance accountants head the treasury department of a governmental institution. They manage the institution''s financial administration, expenditure and income generation, and compliance with taxation and other financial legislation. They perform administrative duties to ensure record keeping, develop plans for budget management and perform financial forecasts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/78ddc0cc-166e-437e-9c41-48aa0a6c6f93','key_16646','','','8160','8160.29','extract mixer tester',replace('spice sifter\nextract mixer tester\ntea blender\nextract mixer\nmechanical sifter operator','\n',char(10)),'Extract mixer testers sift spices using mechanical sifters. They operate mixing machines to blend spices and weigh them until a specified consistency is reached. They compare colours of mixtures with the standard colour chart to ensure that colours of the mixture meet the specifications.','','Includes tea blender.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/78eacaf8-06d6-4b78-920f-edece2e41464','key_16647','','','5311','5311.2','school bus attendant',replace('school bus attendant\nschool bus transportation aide\nschool bus transportation attendant\nschool bus chief attendant\nschool bus transport aide\nschool bus transportation assistant\nschool bus assistant\nschool bus aide','\n',char(10)),'School bus attendants monitor the activities on schoolbuses to ensure and supervise the students'' safety and good behaviour. They help children on and off the bus, support the driver and provide assistance in case of emergency.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/78ed3811-f421-45e5-9574-31364c76af67','key_16648','','','2310','2310.1.29','performing arts school dance instructor',replace('performing arts school dance instructor\ndancing instructor in performing arts school\nprofessor of dance\ndance instructor in performing arts school\ndance school instructor\ndance school teacher\ndance conservatory instructor\nperforming arts dancing instructor\nconservatory dance lecturer\ndance conservatory trainer\nperforming arts dance instructor\nconservatory dance instructor','\n',char(10)),'Performing arts school dance instructors educate students in specific theory and, primarily, practice-based dance courses at a specialised dance school or conservatory at a higher education level. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for dance. Performing arts school dance instructors monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the dance through, often practical, assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/78f474ae-c154-4206-ad0f-d8a00e9c541e','key_16649','','','3412','3412.4.11','residential home adult care worker',replace('residential home adult care worker\nadult services worker','\n',char(10)),'Residential home adult care workers counsel and support vulnerable adults who have physical or mental disabilities or addiction issues. They monitor their progress and provide them with care in a positive living environment. They work with families to support individuals'' development and meet their needs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/78faf623-2543-43a5-acb2-3c43a22d36e4','key_16650','','','2511','2511.14.1','blockchain architect',replace('decentralized ledger technology specialist\nblockchain technology specialist\nblockchain engineer\nDLT specialist','\n',char(10)),'Blockchain architects are ICT system architects that are specialized in blockchain-based solutions. They design architecture, components, modules, interfaces, and data for a decentralized system to meet specified requirements.','','Excludes the development of decentralized systems.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/79082f76-4b11-4594-8437-2db567331f41','key_16651','','','9312','9312.1.4','road marker',replace('road marker\nwhite line marker\nroad marking worker\nconstruction worker\nroad marking machine operator\ncarriageway sign painter\nroad marking operator\nroad painter\nroad marking technician\nconstruction worker (road marking)\nroad marking operative','\n',char(10)),'Road markers apply markings to roads to increase safety, indicate traffic regulations, and help road users find the way. They use different pieces of machinery to paint lines onto the road and install other markings such as reflective cat''s eyes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/790c8dca-f624-4bdc-ac16-27bc13ead188','key_16652','','','3521','3521.1.7','sound designer',replace('sound designer and music composer\nmusic composer & sound designer\naudio engineer and sound artist\naudio designer\naudio engineer and sound designer\nmusic and sound artist\nsound designer & audio engineer\nsound artist and audio engineer\nsound designer and composer\nsound designer\ncomposer and sound designer\nsound designer & music composer\nsound designer and engineer\naudio engineer & sound artist\ncomposer & sound designer\nsound designer & engineer\nsound designer and audio engineer\nsound designer & composer\nmusic composer and sound designer\nsound engineer and designer\nsound engineer & designer','\n',char(10)),'Sound designers develop a sound design concept for a performance and supervise the execution of it. Their work is based on research and artistic vision. Their design is influenced by and influences other designs and must be conform with these designs and the overall artistic vision. Therefore, the designers work closely with artistic directors, operators and the artistic team. Sound designers prepare audio fragments to be used in a performance, which can involve recording, composing, manipulating and editing. Sound  designers develop plans, cue lists and other documentation to support the operators and production crew. Sound designers sometimes also work as autonomous artists, creating sound art outside a performance context.','','Excludes composer.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/79874366-6ec2-4601-aceb-9dbb8e68d015','key_16653','','','1420','1420.4.23','fruit and vegetables shop manager',replace('greengrocers shop manager\nfruiterers store manager\norganic fruit and vegetable store manager\norganic fruit shop manager\norganic fruit store manager\nfruiterers shop manager\ngreengrocery counter manager\nvegetable shop manager\nfruiterer\ngreengrocery store manager\nfruit and vegetables shop manager\nfruit and vegetables store manager\ngreengrocer\ngreengrocery shop manager\norganic vegetable shop manager\norganic fruit and vegetable shop manager\norganic vegetable store manager\ngreengrocers store manager\nfruit shop manager','\n',char(10)),'Fruit and vegetables shop managers assume responsibility for activities and staff in specialised shops for fruit and vegetables.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/79c2e54e-26e8-4cdc-a7f1-908e000bfb83','key_16654','','','5312','5312.3','secondary school teaching assistant',replace('teaching assistant in secondary schools\nsecondary school teaching aide\nhigh school teaching assistant\nsecondary school paraprofessional\nsecondary school assistant\nassistant in secondary school teaching\nsecondary education teaching assistant\nsecondary school instructional assistant\nsecondary school TA','\n',char(10)),'Secondary school teaching assistants provide various support services to secondary school teachers such as instructional and practical support. They help with the preparation of lesson materials needed in class and reinforce instructions with students in need of extra attention. They also perform basic clerical duties, monitor the students'' learning progress and behaviour and supervise the students with and without the teacher present.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/79f435d0-9bc6-4d25-a26a-acbe16569ebb','key_16655','','','7127','7127.1','heating and ventilation service engineer',replace('HVAC mechanic\nair conditioning and refrigeration inspector\nHVAC heating mechanic\nventilation and air-conditioning inspector\nheating, ventilation and air conditioning mechanic\nair conditioning inspector\nHVACR inspector\nheating instrument inspector\nheating, ventilation, air conditioning (and refrigeration) service engineer','\n',char(10)),'Heating and ventilation service engineers install and maintain industrial heating and refrigeration systems. They set up furnaces, thermostats, ducts, vents, and other equipment needed to ensure the controlled passage and treatment of air. They also carry out repairs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/79f8f68c-5509-4119-9373-8bef1c364d31','key_16656','','','4221','4221.7','travel agent',replace('sales agent\nreservations agent\ntravel sales agent\ntravel agent\ntravel booking agent','\n',char(10)),'Travel agents design and market travel programme itineraries for potential travellers or visitors.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/79fed799-ab3a-43d9-bd91-414c2c3b2f57','key_16657','','','2144','2144.1.22','tooling engineer',replace('tooling technology engineering adviser\ntooling engineering adviser\ntooling technology engineering expert\ntools engineer\ntooling technology engineering consultant\npress tooling engineer\ntooling design engineer\ntooling engineer\ntooling technology engineer\ntooling engineering consultant\ntoolmaker\ntooling engineering specialist\ntooling development engineer\ntooling technology engineering specialist\nmanufacturing tooling engineer\ntooling engineering expert\ntoolmaker/tooling engineer','\n',char(10)),'Tooling engineers design new tools for manufacturing equipment. They prepare tooling quotation requests. They estimate costs and delivery time, manage tooling construction follow-up and supervise the routine maintenance of tools. They also analyse data to determine the cause of major tooling difficulties and develop recommendations and action plans for solutions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7a031723-2b2c-4fdd-8ed5-30d67088b8b8','key_16658','','','1221','1221.3.2.1.2','licensing manager',replace('trainee international licensing manager\nsenior licensing manager\nassistant licensing manager\ngraduate international licensing manager\ngraduate brand licensing manager\ntrainee licensing manager\ngraduate licensing and brand manager\nsenior international licensing manager\nassistant licensing and brand manager\nsenior brand licensing manager\nlicensing manager\ntrainee brand licensing manager\ntrainee licensing and brand manager\ngraduate licensing manager\nassistant international licensing manager\nassistant brand licensing manager\nsenior licensing and brand manager','\n',char(10)),'Licensing managers oversee licenses and rights of a company regarding use of its products or intellectual property. They ensure that third parties comply with specified agreements and contracts, and negotiate with and maintain relationships between both parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7a0afb5e-f118-449a-82bd-d8b9230fd49f','key_16659','','','8113','8113.8','tunnel boring machine operator',replace('tunnel boring machine operator\nTBM operator\ntunnelling equipment operative\ntunnelling machine operator\ntunneller\ntunnelling equipment operator','\n',char(10)),'Tunnel boring machine operators work on large pieces of tunnelling equipment commonly known as TBMs. They regulate the operation of the machine, adjusting the torque of the rotating cutting wheel and screw conveyor to maximise stability of the tunnel before tunnel rings are installed. Tunnel boring machine operators then put the reinforced concrete rings in place using remote controls.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7a18e959-44fe-49ae-9757-124f46a7487c','key_16660','','','3435','3435.17','prop maker',replace('scenic painter & prop maker\nproperty modeller and maker\nset dresser & property maker\nscenic artist & prop maker\nproperty maker and scenic painter\nscenic painter and property maker\nprop maker and set dresser\nprop builder\nset dresser and prop maker\nprop maker and modeller\nproperty builder\nproperty maker and designer\nproperty maker\nproperty maker and scenic artist\nproperty maker & modeller\nprop maker and scenic painter\nprop  maker & modeller\nprop maker & set dresser\nproperty maker & designer\nprop modeller & maker\nscenic painter and prop maker\nprop modeller and maker\nset dresser and property maker\nprop & puppet maker\nscenic artist and prop maker\nprop maker\nproperty maker and modeller\nproperty designer & maker\nproperty maker & set dresser\nprop designer and maker\nset dresser & prop maker\nprop maker and designer\nscenic artist & property maker\nproperty maker & scenic painter\nprop maker and scenic artist\nproperty maker & scenic artist\nproperty designer and maker\nprop maker & designer\nscenic artist and property maker\nproperty modeller & maker\nproperty maker and set dresser\nscenic painter & property maker\nprop maker & scenic artist\nprop designer & maker\nprop maker & scenic painter\nproperty & puppet maker','\n',char(10)),'Prop makers construct, build, prepare, adapt and maintain props used on stage and for filming movies or television programs. Props may be simple imitations of real life objects, or may include electronic, pyrotechnical, or other effects. Their work is based on artistic vision, sketches and plans. They work in close cooperation with the designers involved in the production.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7a3996d8-b90d-4b81-bd5e-e37f005b2683','key_16661','','','1213','1213.1','director of compliance and information security in gambling',replace('gaming compliance and information security director\nmanager of compliance and information security in gambling\ngambling compliance and information security manager\nmanager of compliance and information security in gaming\ngaming compliance and information security manager\ndirector of compliance and information security in gambling\ngambling compliance and information security director\nsecurity information director\ncompliance director\nsecurity information manager','\n',char(10)),'Directors of compliance and information security in gambling follow the regulatory compliance for gambling and oversee information  security to ensure secure and safe use of all information technology associated in gambling.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7a523273-9fff-4f11-beb6-110eeaec1436','key_16662','','','2143','2143.1.3','recycling specialist',replace('solid waste division supervisor\nrecycling policy specialist\nrecycling manager\nrecycling director\nrecycling inspector\nwaste recycling co-ordinator\nrecycling programme manager\nrecycling research specialist\nfilling station recycling co-ordinator\nwaste recovery specialist\nrecycling operations manager\nrecycling equipment technician\ngarden centre recycling co-ordinator\nhousehold recycling co-ordinator\nrecycling and reclamation co-ordinator\nrecycling supervisor\ntrainee recycling co-ordinator\ncommunity recycling co-ordinator\nrecycling specialist\nrecycling co-ordinator\nrecycling and waste reduction specialist\ndepartment store recycling co-ordinator\ncommunity recyling coordinator\nrecycling equipment specialist\nsenior recycling co-ordinator\nrecycling coordinator\nrecycling specialist advisor\nhousehold recycling coordinator\nrecycling management specialist\nwaste consultant\nrecycling team supervisor\noutlet centre recycling co-ordinator\nwaste management consultant\nrecycling development co-ordinator\nsupermarket recycling co-ordinator\nrecycling programe manager\nreycling development co-ordinator\nrecycling development coordinator\nwaste recycling specialist\nrecycling policy advisor\nrecycling and reclamation coordinator\nhypermarket recycling co-ordinator','\n',char(10)),'Recycling specialists research recycling policies and legislation, and supervise implementation in an organisation to ensure that waste management occurs according to regulations. They perform inspections, provide recycling equipment, and supervise recycling workers. They also advise organisations on ways they can improve their waste management procedures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7a6ca615-a2bc-499d-8288-d186ce2594ea','key_16663','','','2412','2412.5','financial risk manager',replace('financial risk department manager\nbank risk manager\nfinancial risk analyst\nbudget and debt advisor','\n',char(10)),'Financial risk managers identify and assess potential risk areas threatening the assets or capital of organisations and give advice on how to deal with them. They specialise in either credit, market, operational or regulatory risk analysis. They use statistical analysis to evaluate risk, make recommendations to reduce and control financial risk and review documentation for legal compliance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7aedaa07-3884-4c5b-88f9-80997b2aa54b','key_16664','','','7212','7212.3','welder',replace('maintenance welder\naerospace welder\nboiler welder\nconstruction welder\nrepair welder\noxyacetylene welder\noxy welder\nwelder\nmarine welder\nnaval welder\ngas welder\nfitter\nrepair and maintenance technician\naircraft welder\nrepair fitter\noxy-fuel welder\nmotor vehicle welder\nrepair technician\nunderwater welder\nwelding practitioner\nmaintenance technician\nnaval metalworker\nsteel welder\nproduction welder\narc welder','\n',char(10)),'Welders operate welding equipment in order to join metal workpieces together. They can use fusion welding processes based on different techniques and materials. They also perform simple visual inspection of welds.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7af81505-6681-49b5-b65e-401884a71f1f','key_16665','','','2133','2133.15.1','groundwater monitoring technician',replace('groundwater protection technician\ngroundwater technician\ngroundwater sampling technician\nenvironmental protection technician\ngroundwater monitoring system technician\ngroundwater laboratory  technician\ngroundwater monitoring technician\nenvironmental conservation technician','\n',char(10)),'Groundwater monitoring technician monitor the environment, gathering data in the form of samples and performing tests in a laboratory or field, to investigate potential sources of pollution. They also perform maintenance tasks on the monitoring equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7afeee90-5672-470d-b061-24ffaafb2fc9','key_16666','','','3134','3134.5','refinery shift manager',replace('head of oil refinery\nrefinery coordinator\nrefinery shift supervisor\nshift supervisor\nplant and equipment manager (refinery)\nrefinery manager\nrefinery shift manager\nshift manager','\n',char(10)),'Refinery shift managers supervise staff, manage plant and equipment, optimise production and ensure safety at the oil refinery on a day-to-day basis.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7b1b5da8-573a-49bb-a38e-68725a949f4f','key_16667','','','1330','1330.3','chief technology officer',replace('IT director\nCTO\nchief technology officer\ninformation technology director\nchief technology officers\nchief digital officer','\n',char(10)),'Chief technology officers contribute to a company''s technical vision and lead all aspects of technology development, according to its strategic direction and growth objectives. They match technology with business needs.','','Includes people working in a higher level than the CEO.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7b4744a8-a4e3-4104-9b74-4ec54108af7b','key_16668','','','2152','2152.1.6','microelectronics designer',replace('microelectronics circuit designer\nmicroelectronics circuit design engineer\nmicroelectronics manufacturing design engineer\nmicroelectronics circuit engineer\nmicroelectronics technology design engineer\nmicroelectronics hardware designer\nmicroelectronics packaging designer\nmicroelectronics circuit system engineer\nsilicon engineer\nmicroelectronics hardware engineer\nmicroelectronics system designer\nmicroelectronics packaging design engineer\nmicroelectronics system engineer\nintegrated circuit hardware engineer','\n',char(10)),'Microelectronics designers focus on developing and designing microelectronic systems, from the top packaging level down to the integrated circuit level. Their knowledge incorporates system-level understanding with analogue and digital circuit knowledge, with integrating the technology processes and an overall outlook in microelectronic sensor basics. They work with other engineers, material science specialists and researchers, to enable innovations and continuous development of already existing devices.','',replace('Includes the design of every level of microelectronics systems, from the chip level to the product level.\nExcludes the design at semiconductor and chip level.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7b629a14-09d0-417e-8181-7683923b4e5e','key_16669','','','2422','2422.17','sports programme coordinator',replace('sports development officer\nsports program coordinator\nsports development coordinator\ncommunity development officer\nrecreation programme coordinator\nrecreation officer','\n',char(10)),'Sports programme coordinators coordinate sports and recreation activities and policy implementation. They develop new programs and aim to promote and implement them, as well as ensure the maintenance of sports and recreation facilities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7b74626d-b487-48a1-9e80-47faf9928210','key_16670','','','3213','3213.2','pharmacy technician',replace('technical assistant in pharmacy\npharmacy technician\npharmacy technologist\ntechnician in pharmaceuticals\npharmacy technical assistant\npharmaceutical technician\npharmaceutical technologist\ntechnician in pharmacy\ntechnologist in pharmacy\ntechnologist in pharmaceuticals','\n',char(10)),'Pharmacy technicians, under the supervision of a pharmacist, check incoming goods, control stock, handle and store pharmaceuticals properly. Where permitted by national rules, they dispense medication and provide advice on their appropriate use.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7b89d3e0-c6fb-4c95-bf0d-3b4acf05f9d6','key_16671','','','2619','2619.8','legislative drafter',replace('parliamentary drafter\nparliamentary draftsperson\nparliamentary draftsman\nparliamentary councel\nlegislative drafter\nparliamentary draughtsman\nresearcher','\n',char(10)),'Legislative drafters research pieces of legislations and edit them in order to make them stronger and more clear. Rarely they also add new ideas which have never been incorporated into a law or a bill before.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7b8b6118-6876-4a71-81a6-4b5fa9bfa221','key_16672','','','3259','3259.7','respiratory therapy technician',replace('oxygen therapy technician\nrespiratory care assistant\nbreathing technician\nrespiratory therapist\nrespiratory therapy technician\nrespiratory technician\nrespiratory care supervisor','\n',char(10)),'Respiratory therapy technicians assist doctors and surgeons to treat respiratory problems. They work in hospitals and medical facilities where they operate medical equipment to test patients'' respiratory conditions and aid in their treatment. Respiratory therapy technicians also tend to breathing equipment and machines during surgery.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7b99007f-c6bf-4703-85d3-f168bc6a9f28','key_16673','','','2146','2146.8','mine planning engineer',replace('mine production scheduler\nmine planning engineer\nmine project engineer\nsurface mine planning engineer\nmine planning supervisor\nmining operations scheduler','\n',char(10)),'Mine planning engineers design future mine layouts capable of achieving production and mine development objectives, taking into account the geological characteristics and structure of the mineral resource. They prepare production and development schedules and monitor progress against these.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7bc02fa7-7aaa-41a6-be5b-ab7a7b6684d3','key_16674','','','2145','2145.1.5','gas distribution engineer',replace('gas distribution process engineer\ngas distribution pipeline engineer\nnatural gas transmission system engineer\ngas distribution technology engineering specialist\ngas distribution engineer\ngas distribution technology engineering expert\ngas distribution system engineer\ngas distribution engineering expert\ngas distribution network engineer\ngas distribution technology engineering consultant\ngas distribution technology engineer\ngas distributions engineer\ngas distribution technology engineering adviser\ngas distribution engineering consultant\ngas distribution engineering adviser\ngas transmission engineer\nnatural gas distribution engineer\ngas distribution research engineer\ngas distribution engineering specialist\nLPG distribution engineer\ngas transmission system engineer','\n',char(10)),'Gas distribution engineers design and construct transport systems for natural gas, connecting the gas distribution network to the consumer by designing piping works and mains. They research methods to ensure sustainability, and to decrease environmental impact, as well as optimising cost efficiency.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7bcafcd7-3f35-4a72-849c-c92b334927fd','key_16675','','','3321','3321.2','insurance risk consultant',replace('insurance risk consultant\ninsurance risk surveyor\ninsurance adviser\ninsurance analyst\ninsurance risk associate\nrisk and insurance consultant\ninsurance consultant','\n',char(10)),'Insurance risk consultants prepare reports for insurance underwriters. For this purpose, they undertake surveys in order to determine the potential financial risk for personal products, properties or sites.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7beefadd-ea05-4f59-bc4b-01075f27b937','key_16676','','','2651','2651.11','video artist',replace('visuals artist\ninteractive video artist\nvideo manipulator\nvisual technician\ngraphics and video artist\ngraphics & video artist\nvideo graphics artist\nvideo effects artist\nvideo artist\nvisual manipulator','\n',char(10)),'Video artists create videos using analogue or digital techniques to obtain special effects, animation, or other animated visuals using films, videos, images, computer or other electronic tools.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7bf11599-d0bb-4789-9886-55c87f5da449','key_16677','','','3119','3119.16.4','leather goods quality technician',replace('leather goods quality technologist\nleather goods quality engineer\nquality technologist\nleather goods quality technician\nleather goods quality specialist','\n',char(10)),'Leather goods quality technicians perform tasks related to quality control. As such, they execute laboratory tests (finished products, used materials and components) according to the national and international standards. They analyse and interpret the results of the laboratory tests, prepare reports, advise on corrective and preventive measures. All in all, they contribute to the accomplishment of requirements and objectives with the aim of continuous improvement and customer satisfaction.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7c132147-428b-4ea3-87e1-70f7abb691ce','key_16678','','','2262','2262.1','pharmacist',replace('practitioner of pharmacology\ndispensing chemist\npractitioner of pharmacy\npharmacology practitioner\npharmacologist\npharmacy practitioner\npharmacist\nretail pharmacist','\n',char(10)),'Pharmacists prepare, dispense, and provide prescriptions for, over the counter medication. They offer clinical information on medicines, report suspected adverse reactions, and provide personalised support to patients. Pharmacists formulate and test medications in laboratories, and store, preserve, and distribute them.','','Includes people performing local or national public health campaigns.','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7c205166-d862-45fc-90e5-58a3aa3721dd','key_16679','','','3123','3123.1.20','road construction supervisor',replace('road construction foreman\nsupervisor of road construction\nroad construction project supervisor\nsupervisor, highways\nhighway construction supervisor\nclerk of works\nsupervisor of road construction operations\nroad construction clerk of works\nsurfacing supervisor\nroad construction supervisor','\n',char(10)),'Road construction supervisors monitor the construction and maintenance of roads. They assign tasks and take quick decisions to resolve problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7c2fbf7d-b934-4f62-8167-0ed90fb2a16f','key_16680','','','2144','2144.1.19','rolling stock engineer',replace('rolling stock technology engineer\nrailway vehicle engineer\nrailcar  engineer\nmultiple unit engineer\nrail vehicle engineer\nwagon designer\nrolling stock engineering consultant\nrailcar  designer\nrolling stock engineering adviser\nrolling stock technology engineering consultant\nrolling stock designer\nrolling stock technology engineering specialist\nmultiple unit designer\ntraction engineer\nlocomotive engineer\ncarriage designer\nrolling stock engineering expert\nwagon engineer\nrolling stock technology engineering adviser\nrail vehicle designer\nrolling stock engineering specialist\ncarriage engineer\nlocomotive designer\nrolling stock engineer\nrolling stocks engineer\nrolling stock technology engineering expert\nrailway vehicle designer','\n',char(10)),'Rolling stock engineers design and oversee the manufacturing process and installation of rail vehicles, including locomotives, carriages, wagons and multiple units. They design new trains and electrical or mechanical parts, supervise modifications and resolve technical problems. They also supervise routine maintenance duties to ensure that trains are in good condition and meet quality and safety standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7c320679-131d-4828-87ed-94886614b7ed','key_16681','','','5411','5411.1.4','pump operator',replace('fireman\nfire appliance pump operator\nfirewoman\nfirewoman, pump operator\nfirefighter\nfire fighter\npump operator\nfire fighter, pump operator\nfire engine pump operator\nfireman, pump operator\nfirefighter, pump operator','\n',char(10)),'Pump operators assist firefighting operations by controlling the pumps which supply water and other substances for extinguishing fires. They ensure the substance is delivered in the right amount and under the correct  pressure through the firehose.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7c5e4ca1-e8be-4297-9014-509d5ba57210','key_16682','','','5113','5113.1.3','zoo educator',replace('zoo educator\neducation instructor\nlearning officer\nconservation educator\nschools engagement officer\neducator\nguest experience assistant\nvisitor educator\naquarium educator\nengagement officer','\n',char(10)),replace('Zoo educators teach visitors about the animals living at the zoo/aquarium as well as other species and habitats. They provide information about management of zoos, its collection of animals, and wildlife conservation. Zoo educators can be involved in both formal and informal learning opportunities ranging from production of information signs at enclosures to delivering classroom sessions linked to school or university curricula. Depending on the size of the organisation the education team may be a single person or a large team. Consequently the optional skills required are very broad and will vary from organisation to organisation.  \n \nZoo educators also promote conservation efforts. This can involve work within the zoo but also in the field as part of any zoo outreach project(s).','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7c64479f-7a76-49e9-ac42-bd925f2bf99d','key_16683','','','8131','8131.11','mine control room operator',replace('mine control room operator\nmine monitor\npanel controller\nmining supervisor\nmining control room operator\nmining operations supervisor\nmine operator','\n',char(10)),'Mine control room operators perform a range of tasks from the control room of a mine. They monitor the processes through electronic representations shown on monitors, dials, and lights. Control room operators make changes to variables and communicate with other departments to make sure processes keep running smoothly and according to established procedures. They take appropriate actions in case of irregularities or emergencies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7c6fc2f0-6e16-4429-8190-f6faac0c3853','key_16684','','','7533','7533.3','glove maker',replace('glove manufacturers\nindustrial leather gloves manufacturer\nglove maker\nsports glove manufacturer\nglove manufacturer','\n',char(10)),'Glove makers design and manufacture technical, sport or fashion gloves.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7cb71c5f-c310-481c-b46b-d0044328758c','key_16685','','','9122','9122.1','vehicle cleaner',replace('automotive valeter\nmotor vehicle valeter\nrental fleet vehicle cleaner\nused vehicle cleaner\nvehicle cleaner\nmotor vehicle cleaner\nvehicle body cleaner\ntruck cleaner\nvan cleaner\ncar cleaner\ntruck valeter\nautomotive cleaner\nvan valeter\ncar valeter\nnew vehicle clearer\nvehicle body valeter','\n',char(10)),'Vehicle cleaners clean and polish surfaces of external parts and interiors of vehicles.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7cd070f5-b5dd-4228-bdcf-0419dbacfcf4','key_16686','','','8160','8160.26','distillery miller',replace('distillery grain miller\ndistillery miller\ndistillery mill worker\ndistillery grain mill worker\ndistilling miller','\n',char(10)),replace('Distillery millers tend distillery mills in order to clean and grind whole grain for use in production of distilled liquors. They tend cleaning machines to remove impurities from grains followed by the grinding and weighing of grains. They perform daily maintenance on pumps, air-conveyor chutes and machines. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7cdeb653-8f3d-4921-832b-b95f9d700a86','key_16687','','','3131','3131.3.2','electrical transmission system operator',replace('electricity transmission system technician\nelectricity transmission system worker\nelectricity power line system worker\nelectric powerline system worker\nelectrical transmission system operator','\n',char(10)),'Electrical transmission system operators transport energy in the form of electrical power. They transmit electrical power from generation plants over an interconnected network, an electrical grid, to electricity distribution stations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7ceb2340-339c-4367-bc4e-e08f57c992f6','key_16688','','','5164','5164.3','animal shelter worker',replace('animal helper\nanimal shelter worker\nanimal shelter employee\nanimal shelter assistant\nanimal shelter helper\nanimal washer','\n',char(10)),'Animal shelter workers provide animal care routine services at the animal shelter. They receive animals brought to the shelter, respond to calls about lost or injured animals, nurse animals, clean cages, handle papers for adoption of animals, transport animals to the veterinarian and maintain database with the animals present in the shelter.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7d092380-1c2e-48fe-81de-0d191b4e96d6','key_16689','','','2269','2269.8.1','diagnostic radiographer',replace('senior diagnostic radiographer\nradiologic technologist\ndiagnostic radiography practitioner\nspecialist diagnostic radiographer\nexpert diagnostic radiographer\npractitioner of diagnostic radiography\ndiagnostic radiography technician\ndiagnostic radiographer\ndiagnostic imaging radiographer\ndiagnostic imaging radiology technologist','\n',char(10)),'Diagnostic radiographers plan, prepare and perform diagnostic imaging examinations and post processing, with a wide range of equipment and techniques using X-rays, strong magnetic fields or Magnetic Resonance Imaging (MRI) and high frequency sound (Ultrasound).','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7d27600b-5cff-4ebc-b259-cc2d47572035','key_16690','','','3435','3435.10','head of workshop',replace('deputy workshop manager\nhead of workshop\nworkshop coordinator\ntheater crafstmanship coordinator\nstage workshop supervisor\ntheatre craftsmanship coordinator\ndeputy workshop supervisor\nhead of scenic workshop\nhead of set workshop\nscenic workshop manager','\n',char(10)),'Heads of workshop coordinate specialised workshops that construct, build, prepare, adapt and maintain elements used on stage. Their work is based on artistic vision, schedules and overall production documentation. They liaise with the designers involved in the production, the production team and other services of the organisation.','','Includes people working in event and rental companies','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7d4266b3-2108-4330-b27b-430a96012c21','key_16691','','','8153','8153.1','sewing machine operator',replace('computer stitcher\nserger machine operator\nedging machine operator\ncustom sewer\ngarment maker\njoiner\npleating machine operator\nclothing machinist\nbartack machinist\nstitching machinist\nhook and eye machine operator\nfastener sewing machine operator\nsewing machine operator\nsewing machine tender\nflat sewing machine operator','\n',char(10)),'Sewing machine operators tend specific sewing machines in the industrial production chain of wearing apparel. They perform operations such as joining, assembling, reinforcing, repairing, and altering of wearing apparel.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7d599049-d15a-4fda-afe9-b334b0614ec7','key_16692','','','7232','7232.3','aircraft gas turbine engine overhaul technician',replace('aircraft gas turbine engine installation technician\naeroplane engine mechanic\naeroplane  gas turbine engine maintenance technician\naeroplane gas turbine engine repair technician\naircraft gas turbine engine repair  technician\naircraft gas turbine engine maintenance technician\naviation gas turbine engine repair  technician\naviation  gas turbine engine maintenance technician\naircraft gas turbine engine overhaul technician','\n',char(10)),'Aircraft gas turbine engine overhaul technicians perform overhaul, maintenance and repair work on gas turbine engines. They disassemble, inspect, clean, repair and reassemble the engines using engine-specific tooling.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7d7ea936-a1c6-499d-9aa7-054e943b019b','key_16693','','','1349','1349.15','interpretation agency manager',replace('interpretation agency director\nlanguage translation agency director\nlanguage interpreter\nlanguage interpretation service manager\ntranslation agency manager\ninterpretation agency manager\nlanguage interpretation agency manager\nlanguage translator\ntranslation service manager\nlanguage translation agency manager\nlanguage interpretation agency supervisor\nlanguage interpretation agency director\nlanguage translation agency supervisor\nlanguage translation service manager\ninterpretation agency supervisor','\n',char(10)),'Interpretation agency managers oversee operations in the delivery of interpretation services. They coordinate the efforts of a team of interpreters who understand and convert spoken communication from one language to another. They ensure the quality of the service and the administration of the interpretation agency.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7d8b3c73-20f8-467e-91e4-00378673a2cd','key_16694','','','3257','3257.3','food safety inspector',replace('food quality inspector\nquality controller\nfood safety officer\nfood safety inspector\nquality inspector','\n',char(10)),'Food safety inspectors carry out inspections in food processing environments from a food safety standpoint. They are part of official control bodies who check and control food products and processes to ensure compliance with regulations and laws governing safety and health.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7d9242d3-2a55-45e3-9d67-be76f879de0b','key_16695','','','7223','7223.4.4.1','metalworking lathe operator',replace('brake drum operator\nmetal working lathe operator\ntool lathe setter\ntool lathe\nspinning lathe operator\nmetal spinning operator\nmetalworking lathe operator\nlathe scalper operator','\n',char(10)),'Metalworking lathe operators set up and tend a metalworking lathe manually, which is responsible for cutting metal to its desired size and shape by usage of a gear train or a swapping gear that propels the main lead-screw at a variable speed ratio, thus rotating the metal workpiece on its axis, facilitating the cutting process. They check the lathe equipment for wear and handle the metal workpieces as they have been cut by the lathe.','','Excludes <a resource="http://data.europa.eu/esco/occupation/63042e8f-dd59-47fe-87f3-3b2ce21f196a" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/63042e8f-dd59-47fe-87f3-3b2ce21f196a"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">lathe and turning machine operator</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7d99ed04-fda7-4f35-9f02-0db18078159c','key_16696','','','8155','8155.1.2','leather finishing operator',replace('plating machine operator\nfinisher\nspray booth operator\nmilling drum operator\nfinishing range operator\nfinishing machinist\nleather finishing coating machine operator\nleather finishing operator\nfinishing operators\nleather embossing machine operator','\n',char(10)),'Leather finishing operators use machines for finishing leather according to the required specifications for surface characteristics, as provided by the client. Surface characteristics refer to colour nuance, quality, pattern and special properties, such as waterproofness, antiflame retardance, antifogging of the leather. They arrange the dosage of finishing mixes to apply to the leather and do the routine maintenance of the machinery.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7d9c060a-f280-441d-baf9-7d4a6b520a2a','key_16697','','','1324','1324.3.2.8','import export manager in computers, computer peripheral equipment and software',replace('graduate export manager in computers, computer peripheral equipment and software\ninternational trade manager in computers, computer peripheral equipment and software\nassistant import export manager in computers, computer peripheral equipment and software\ngraduate import export manager in computers, computer peripheral equipment and software\nimport export manager in computers, computer peripheral equipment and software\nassistant export manager in computers, computer peripheral equipment and software\nassistant import manager in computers, computer peripheral equipment and software\nsenior export manager in computers, computer peripheral equipment and software\ntrainee import manager in computers, computer peripheral equipment and software\ngraduate import manager in computers, computer peripheral equipment and software\ntrainee import export manager in computers, computer peripheral equipment and software\nsenior import export manager in computers, computer peripheral equipment and software\ninternational trade compliance manager in computers, computer peripheral equipment and software\nsenior import manager in computers, computer peripheral equipment and software\ntrainee export manager in computers, computer peripheral equipment and software\ncustoms compliance manager in computers, computer peripheral equipment and software','\n',char(10)),'Import export managers in computers, computer peripheral equipment and software install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7d9e2a65-758c-4cbd-b66d-3a595d3d63b1','key_16698','','','2634','2634.1','polygraph examiner',replace('polygraph instructor\nsignals analyst\nlie detector operator\npolygraph operator\npolygraphist\nlie detector examiner\npolygraph technician\npolygraph examiner','\n',char(10)),'Polygraph examiners prepare individuals for polygraph testing, conduct the polygraph exam and interpret the results. They pay close attention to detail and use a range of instruments to monitor respiratory, sweat and cardiovascular responses to questions addressed during the process. Polygraph examiners write reports on the basis of the results and can provide courtroom testimony.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7daa8fed-9a78-4fe2-afc2-153fc423040b','key_16699','','','2131','2131.1','animal behaviourist',replace('ethologist\nanimal behaviour expert\nanimal behavior expert\nanimal behaviorist\nanimal behaviour specialist\nanimal behaviour therapist\nanimal behaviorists\nanimal behavior specialist\nanimal behavior therapist','\n',char(10)),'Animal behaviourists work with animals and people to study, observe, assess and understand animal behaviour in relation to specific factors and to prevent or address inappropriate or problematic behaviours within individual animals through the development of suitable environments and management regimes, in accordance with national legislation. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7db415ec-0437-4a5e-95ea-622ea9f13dd0','key_16700','','','3122','3122.4.10','metal production supervisor',replace('metal fabricating manager\nmetal-production superintendent\nmelter supervisor\nmechanical supervisor\nmechanical operations supervisor\nmetal fabrication manager\nmetal fabricating supervisor\nmetal fabrication supervisor\nmelter manager\nmetal production superintendent\nmetal production overseer\nmetal production supervisor','\n',char(10)),'Metal production supervisors oversee the day-to-day working process and activities of the labourers in a metal fabrication factory. They supervise staff, create work schedules, maintain a safe work environment and serve as the first, most accessible management representative for the workers to contact when there is need.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7dbb116c-a4e7-41e8-9f28-c8a738d93e28','key_16701','','','8160','8160.3','beverage filtration technician',replace('drinks filtration worker\nbeverage filtration technician\nbeverage filtration expert\ndrink filter operator\nbeverage filtration worker','\n',char(10)),'Beverage filtration technicians operate machines that clarify beverages prior to filtering. For the purpose, they transfer fermented beverages from settling casks into clarifying tanks and spread chemicals over the surface of beverages to aid their clarification. Then, they pump beverages to transfer them to filtering tanks.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7dc05fcc-b079-45ed-b5d4-74c75f99976c','key_16702','','','2652','2652.1','composer',replace('music producer and composer\ntheatre music composer\nelectronic music producer\nmusic composer\ncomputer composer\njingle writer\nsinger-songwriter\nsongwriter\nelectro musician\nsound track composer\ncomposer\nmusic composer & producer\nfilm music composer\nmusic producer\ntheater music composer\nmusic producer & composer\nmusic composer and producer','\n',char(10)),'Composers create new music pieces in a variety of styles. They usually note down the created music in musical notation. Composers may work independently or as part of a group or ensemble. Many create pieces to support film, television, games or live performances.','','Excludes sound artist, lyricist and music arranger.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7dea476a-dffe-4919-9ee2-fb222e283f41','key_16703','','','2310','2310.1.35','psychology lecturer',replace('psychology instructor\npsychology teacher\npsychology docent\nlector in psychology\npsychology lecturer\nlecturer of psychology\nhigher education psychology teacher\npsychology professor\nuniversity lecturer in psychology\nsenior lecturer in psychology\nlecturer in psychology\ninstructor in psychology\npsychology lector\nprofessor of psychology\nuniversity psychology lecturer\nspecialist lecturer in psychology','\n',char(10)),'Psychology lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, psychology, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of psychology, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7dec82e0-3bfc-4c48-812d-c2095bfe05bb','key_16704','','','1420','1420.1','department store manager',replace('senior department store manager\ngraduate trainee department store manager\ndepartment store general store manager\nmanager in department store\ndepartment store manager\ngeneral retail manager\nassistant department store manager\ndepartment store general merchandise manager\ntrainee department store manager\ndepartment shop manager\nretail sales manager\noutlet store manager','\n',char(10)),'Department store managers organise and control the operations and staff of establishments which provide retail services.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7e08d38d-30c9-4a6f-b88e-b28e87c0b259','key_16705','','','7523','7523.2','wood boring machine operator',replace('wood boring machine worker\nwood borer\nwood boring machine specialist\nwood boring mill operator\nwood boring specialist\nwood boring technician\nwood boring worker\nwood drilling machine operator\nwood drilling machine specialist\nwood boring mill technician\nwood boring machine tender\nwood drilling machine technician\nwood boring machine operative\nwood boring machine setter\nwood boring machine technician\nwood boring mill specialist\nwood boring machine operator','\n',char(10)),'Wood boring machine operators use milling machines or specialise boring jigs to cut holes in wood workpieces. Wood boring differs from routing mainly in that the main movement is into the workpiece as opposed to across its surface.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7e17f4ea-9565-41e0-b408-b52b5c9492a3','key_16706','','','5414','5414.1.4','door supervisor',replace('nightclub doorman\ndisco doorman\ndoor supervisor\nbouncer\nbar doorman','\n',char(10)),'Door supervisors ensure that the people entering public places such as bars, restaurants and concert venues are suitable and that they do not represent potential problems. They enforce legal regulations by checking individuals'' legal age to enter a bar, manage crowds and emergencies, monitor dress codes and handle aggressive and abusive behaviours.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7e33b166-4a6d-40f3-8048-83b3a9d3ef49','key_16707','','','7511','7511.5','meat preparations operator',replace('meatworker\nabbatoir worker\nmeat worker\nmeat processing worker\nmeat preparations operator','\n',char(10)),'Meat preparations operators prepare fresh meat with ingredients such as spices, herbs or additives in order to make ready-for-sale meat preparations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7e46c066-2b98-4eaf-bed6-9848c79902e0','key_16708','','','3334','3334.3.2','real estate investor',replace('real estate entrepreneurs\ninvestor in real estate\nland entrepreneur\nland investor\nreal estate investing expert\nproperty investor\nreal estate investor\nreal estate entrepreneur\nproperty entrepreneur','\n',char(10)),'Real estate investors buy and sell own real estate such as appartements, dwellings, land and non-residential buildings to make a profit. They might actively invest in these properties to increase its value by repairing, renovating or improving the facilities available. Their other tasks may include researching the real estate market prices and undertaking property research.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7eadbe33-d62e-4a82-8e3a-e89df5aefb2c','key_16709','','','8219','8219.11','wood products assembler',replace('staircase assembler\nstair assembler\nassembler, wood products\nwindows assembler\nwood products production worker\nwood products assembler\nwood products builder\nwood products maker\nwindow assembler\nwood products joiner\nwood products fabricator\ncabinet assembler\ndoor assembler\nwood products assembly worker\nwood products constructor','\n',char(10)),'Wood products assemblers put together products from premade pieces of wood. They operate machines, often hydraulic, that bind the various elements of a product together using joints, glue or other fasteners. Assemblers put the elements in place, operate the machine and look out for problems.','','Excludes people performing furniture assembly.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7ed2e153-8da8-4c0d-b4c5-3402c623f34b','key_16710','','','4412','4412.2','postman/postwoman',replace('in house courier\nmotorcycle messenger\npostman/postwoman\nmailwoman\nbike courier\nbike messenger\ndelivery woman\nmail carrier\npostwoman\nmailman\npostal service worker\nbicycle messenger\npostman\nin house messenger\ndelivery man\npostal carrier\ndriver messenger\nurban bike messenger\ncourier','\n',char(10)),'Postmen/Postwomen deliver mails and parcel posts to residences and businesses. They perform mail delivery and collect signatures from the recipients. They perform other duties in connection with mail services from post offices or related organisations.','','Includes people using bicycles and motorcycles.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7ef064b1-aab8-4818-a468-8f17acbfd995','key_16711','','','3113','3113.2','hydropower technician',replace('hydropower plant technician\nhydropower technician\nmechanical engineer, hydropower\nhydroelectric technician\nhydropower mechanical engineer\nhydropower mechanical technician\nmarine energy technician\nhydroelectric plant technician\nhydroelectric mechanical engineer\ntidal power technician\nwave power technician','\n',char(10)),'Hydropower technicians install and maintain systems in hydropower plants. They perform inspections, analyse problems and carry out repairs. They ensure the turbines operate in compliance with regulations, and assist the hydropower engineers in the construction of turbines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7f0b46be-8b7a-4675-987d-89a8a7193730','key_16712','','','1420','1420.4.32','music and video shop manager',replace('cd store manager\nmusical instrument store manager\nrecord shop manager\nmusic shop manager\nmusic superstore manager\nsheet music store manager\nvideo shop manager\nmusic and video shop manager\ncd shop manager\nmusic and video store manager\nmusical instrument shop manager\nrecord store manager','\n',char(10)),'Music and video shop managers assume responsibility for the activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7f0d69cf-8b02-4a84-865a-37585dea9b43','key_16713','','','3435','3435.6','broadcasting programme director',replace('broadcasting programme manager\nradio program director\ncontent director\ntelevision programme director\nbroadcasting program manager\nradio programme director\nprogramme controller\nbroadcasting program director\nTV programme director\nTV program director\nprogramme planner\ntelevision program director\nbroadcasting program coordinator\nprogram controller\nprogram planner','\n',char(10)),'Broadcasting programme directors make the programme schedule. They decide how much broadcast time a programme gets and when it is aired, based on a few factors like, ratings, viewer demographics, etc.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7f0fe7d3-54c6-41e9-88ba-9d4f0f1a46b4','key_16714','','','2152','2152.1.10','microsystem engineer',replace('specialist in microelectromechanical systems\nmicrosystem engineering adviser\nmicrosystem technology engineering consultant\nengineer in microsystems\nmicrosystem technology engineering specialist\nmicro-system engineer\nmicro systems engineer\nmicrosystem engineering consultant\nmicro system engineer\nmicrosystem technology engineer\nmicrosystem technology engineering expert\nadvanced MEMS engineer\nmicroelectromechanical systems engineer\nmicrosystem engineering specialist\nmicrosystem technology engineering adviser\nmicro-systems engineer\nengineer in micro-systems\nmicrosystems engineer\nsenior MEMS engineer\nmicrosystem engineering expert\nengineer of microsystems\nmicrosystem engineer\nMEMS engineer','\n',char(10)),'Microsystem engineers research, design, develop, and supervise the production of microelectromechanical systems (MEMS), which can be integrated in mechanical, optical, acoustic, and electronic products.','',replace('Excludes <a resource="http://data.europa.eu/esco/occupation/8c0f59c9-9a47-42e7-8287-aab19df4e6ab" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/8c0f59c9-9a47-42e7-8287-aab19df4e6ab"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">microelectronics engineer</span></a>. \n Includes people using materials such as quartz, plastics and ceramics.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7f2761eb-4edc-48ad-9fbf-eed0a0db9a21','key_16715','','','2111','2111.3','physicist',replace('physics research scientist\nmedical physicist\nneurologist\nphysics scholar\nphysicist\nphysics researcher\nbiophysicist\nnuclear physicist\nphysics science researcher\nforensic physicist\npetrophysicist\nphysics analyst\nphysics scientist\nballist physicist','\n',char(10)),'Physicists are scientists who study physical phenomena. They focus their research depending on their specialisation, which can range from atomic particle physics to the study of phenomena in the universe. They apply their findings for the improvement of society by contributing to the development of energy supplies, treatment of illness, game development, cutting-edge equipment, and daily use objects.','','Includes people performing specialised activities within nuclear, molecular, optical physics, astrophysics, medical physics, economical physics and matter physics.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7f346896-a4fb-47a1-9572-13398bc297a6','key_16716','','','9629','9629.7','usher',replace('usher\nticket taker\ngroomsman\nusher supervisor\nevent usher\ncourt usher\nchurch usher','\n',char(10)),'Ushers assist visitors by showing their way in a big building such as a theatre, stadium or concert hall. They check visitors'' tickets for authorized access, give directions to their seats and answer questions. Ushers may take on security monitoring tasks and alert security personnel when required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7f35043b-ebb5-40bf-9974-46a29915f6bf','key_16717','','','7323','7323.2','book-sewing machine operator',replace('thread book sewing machine technician\npaper stitcher\nstitching machine operator\nbook-sewing machine technician\nbook-sewing machine operator\npaper stitching machine technician\nthread book sewing machine operator\nbook-sewing machine tender\npaper stitching machine specialist\nthread book sewing machine specialist\nbook-sewing machine specialist\npaper stitching machine operator\nthread book-sewing machine tender\nthread book-sewing machine operator','\n',char(10)),'Book-sewing machine operators tend a machine that stitches paper together to form a volume. They check that signatures are inserted the right way and the machine does not jam.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7f46db3a-754b-4bda-b287-5932fa062ee9','key_16718','','','2152','2152.1.12','power electronics engineer',replace('power electronics technology engineer\nengineer of power electronics\npower electronics engineering specialist\npower electronics engineering expert\npower electronics engineering consultant\npower electronics design engineer\npower electronics testing engineer','\n',char(10)),'Power electronics engineers design and test circuits for use in power electronics systems. They find solutions for known flaws in mechanical designs and collaborate with other engineers to perform cross-functional tasks when testing designs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7f7d2bfe-f038-436f-95ff-29bc90726430','key_16719','','','5419','5419.1','aircraft marshaller',replace('aircraft marshaller\nmarshaller of aircraft\nmarshaller\naircraft ground handler\naeroplane ground handler\naeroplane marshaller\nground marshaller\naircraft ground marshaller\naircraft signal crewman\naeroplane signal crewman','\n',char(10)),'Aircraft marshallers signal pilots to assist them in operations such as turning, slowing down, stopping, and shutting down engines. They lead aircrafts to their parking stands or to the runway. They also indicate directions to the pilots by driving a "follow-me" car.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7f800e7d-9d86-406a-9116-b5eca7526869','key_16720','','','3132','3132.6','water plant technician',replace('water plant operator\nwater plant technician\nwater treatment technician\nwater plant worker\nwater plant measurement technician\nwater plant monitoring technician\nwater treatment plant worker\nwater plant equipment maintenance technician\nwater plant quality technician\nwater plant repair technician\nwater plant maintenance technician\nwater treatment plant operator\nwater treatment plant operative\nwater plant operative','\n',char(10)),'Water plant technicians maintain and repair water treatment and supply equipment in a water plant. They ensure the provision of clean water by measuring the water quality, ensuring it is filtered and treated correctly, and maintaining distribution systems.','','Excludes water treatment systems operator <a resource="http://data.europa.eu/esco/occupation/1ab184b6-de00-4476-ba1d-c6a71c668be3" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/1ab184b6-de00-4476-ba1d-c6a71c668be3"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">water treatment systems operator</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7fbe45c6-1914-43ba-9cfb-e0a4e051da92','key_16721','','','7322','7322.3','gravure press operator',replace('gravure printing press tender\ngravure press rolltender\ngravure press operator\ngravure press tender\nrotogravure operator\nrotogravure press technician\ngravure specialist\ngravure printing machine tender\ngravure press worker\nrotogravure printing machine tender\nrotogravure technician\ngravure technician\nengraving press operator\ngravure press operative\nengraving press tender\ngravure press technician\ngravure printing machine operator\ngravure press specialist\nrotogravure specialist\nrotogravure press specialist\nrotogravure printing press operator\ngravure operator\nrotogravure press operator\nrotogravure press rolltender','\n',char(10)),'Gravure press operators work with gravure presses, where the image is engraved directly on a roll. They set up the press and monitor it during the operation, taking care of safety and resolving problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7fddf2b5-1ba7-4327-86f9-e5da8ddb28a6','key_16722','','','3324','3324.4.26','wholesale merchant in office machinery and equipment','wholesale merchant in office machinery and equipment','Wholesale merchants in office machinery and equipment investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/7ff7ab7d-12a6-4d87-ac91-8a8f3540b1c0','key_16723','','','1324','1324.3.1.6.17','hides, skins and leather products distribution manager',replace('trainee hides, skins and leather products distribution manager\nhides, skins and leather products distribution planner\nhides, skins and leather products supply chain manager\nassistant hides, skins and leather products distribution manager\nhides, skins and leather products supply chain planner\ngraduate hides, skins and leather products distribution manager\nhides, skins and leather products distribution manager\nsenior hides, skins and leather products distribution manager\nhides, skins and leather products logistics manager\nhides, skins and leather products logistics planner','\n',char(10)),'Hides, skins and leather products distribution managers plan the distribution of hides, skins and leather products to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8021f3a2-e3de-43c0-b366-075da74dc5b7','key_16724','','','2266','2266.2','speech and language therapist',replace('specialist speech and language therapist\npaediatric speech and language therapist\nlogopaedics therapist\nlanguage therapy practitioner\nspeech-language pathologist\npractitioner in speech and language therapy\nlanguage therapist\nlogopaedist\npsychomotor specialist\nspeech pathologist\nspeech and language therapist\nspeech therapy practitioner\nspeech therapist\npaediatric speech therapist','\n',char(10)),'Speech and language therapists focus on the aetiology, assessment, diagnosis, treatment, and prevention of communication and swallowing disorders in people of all ages in order to help them maintain, promote, improve, initiate, or recover the ability to communicate both verbally and nonverbally. They address the development of language, speech, vocal and hearing functions, and disorders and disabilities in the cranial, facial, and oral area.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8022c821-1ed0-414e-b440-63cc88943a89','key_16725','','','3324','3324.4.34','wholesale merchant in watches and jewellery',replace('assistant wholesale merchant in watches and jewellery\nwholesale merchandiser in watches and jewellery\nbusiness to business seller in watches and jewellery\nbusiness to business sales consultant in watches and jewellery\nwholesale buyer in watches and jewellery\nB2B sales consultant in watches and jewellery\nwholesaler in watches and jewellery\nwholesale merchant in watches and jewelry\ngraduate wholesale merchant in watches and jewellery\nsenior wholesale merchant in watches and jewellery\ntrainee wholesale merchant in watches and jewellery\nwholesale trader in watches and jewellery','\n',char(10)),'Wholesale merchants in watches and jewellery investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/802a0e5d-5625-4166-a1f0-d13e2586df55','key_16726','','','2651','2651.8','printmaker',replace('printmaking etcher\nprintmaker\nillustrator & printmaker\nscreen printer\nrelief printer\nsilk screen printer\nillustrator and printmaker\nprintmaking artist\nartist printmaker\nlithographer\ndesigner printmaker','\n',char(10)),'Printmakers engrave or etch metal, wood, rubber or other materials to create images which are transported onto surfaces, generally using a printing press. Printmakers often use tools such as etcher-circuit processors, pantograph engravers, and silk screen etchers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/802d3a2f-b966-42e3-94df-683adfcff20c','key_16727','','','6221','6221.9','aquaculture rearing technician',replace('aquaculture rearing technician\ntechnician in aquaculture rearing\nfish culturist\nfish rearing technician','\n',char(10)),'Aquaculture rearing technicians operate in aquatic organisms'' production. They are specialists in the rearing, weaning and production of juveniles.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/804f1ae2-429c-48a9-9858-d5d42df144eb','key_16728','','','8160','8160.28','dryer attendant',replace('dryer attendant\ndrying attendant\ndrying worker\ndryer\nfood products dryer operator','\n',char(10)),replace('Dryer attendants tend rotary dryers to remove moisture from raw materials or food products in transformation. They observe instruments to verify dryer temperature and regulate steam pressure in order to determine if products have the specified moisture content. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8050a89e-563b-4073-943b-84b0c4286c1f','key_16729','','','6122','6122.2','poultry sexer',replace('chicken sexer\nchick sexer\nsexer\nturkey sexer\npoultry gender tester\npoultry sexer\npoultry sex identification specialist','\n',char(10)),'Poultry sexers are specialists working in poultry farms determining the sex of the animals to separate the male from the female birds. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/807a1ac3-4f56-41f3-a68c-d653d558eb0a','key_16730','','','3153','3153.2.1','co-pilot',replace('aircraft co-pilot\nco-pilot\naircraft first officer\nmulti-crew pilot','\n',char(10)),'Co-pilots are responsible for assisting captains by monitoring the flight instruments, handling radio communications, watching for air traffic, and taking over for the pilot as needed. They adhere to the pilot''s commands, flight plans, and regulations and procedures of aviation national authorities, companies, and airports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/808b5940-8092-4f08-bc37-9b60ee5761d8','key_16731','','','2146','2146.1','assayer',replace('geo-science technician\nsilver assayer\nprecious metal assayer\nassay laboratory  technician\nfire assayer\nmineralogy assayer\nassayer\nchemical analyst\nmetal assayer\nmining assayer\nmetal tester\ngold assayer','\n',char(10)),'Assayers test and analyse precious metals such as silver and gold to determine the value and properties of components using chemical and physical techniques. They also seperate precious metals or other components from other materials.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/808becc8-5a4e-4e71-a043-c2ea32b56ae3','key_16732','','','9333','9333.3','materials handler',replace('storage worker\nmaterials handler','\n',char(10)),'Materials handlers execute the handling and storage of materials through activities such as loading, unloading and moving articles in a warehouse or storage room. They work according to orders to inspect materials and provide documentation for the handling of items. Materials handlers also manage inventory and ensure the safe disposal of waste.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8099a96a-753b-49fc-a36a-d70b8aa9fdcf','key_16733','','','3331','3331.2.1.19','import export specialist in live animals',replace('senior import specialist in live animals\ncustoms compliance specialist in live animals\ntrainee export specialist in live animals\ngraduate import specialist in live animals\nsenior export specialist in live animals\ngraduate export specialist in live animals\nassistant import specialist in live animals\nassistant export specialist in live animals\nsenior import export specialist in live animals\nassistant import export specialist in live animals\nimport export specialist in live animals\ntrainee import export specialist in live animals\ninternational trade compliance specialist in live animals\ngraduate import export specialist in live animals\ntrainee import specialist in live animals','\n',char(10)),'Import export specialists in live animals have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/80a2aba7-ef7d-46ef-a8ec-15ff6131c0f1','key_16734','','','2230','2230.2.3','traditional chinese medicine therapist',replace('traditional chinese medicine therapist\ntherapist in TCM\npractitioner of traditional chinese medicine\nTCM practitioner\ntraditional chinese medicine practitioner\nTCM therapist\nspecialist in traditional chinese medicine\nchinese medicine therapist\nTCM specialist\ntherapist in traditional chinese medicine','\n',char(10)),'Traditional Chinese medicine therapists use alternative medicine approaches to cure illnesses in all its forms. They also use various therapies such as the use of herbal medicines, acupuncture, massages and dietary therapies, to increase the general health of the patient and to prevent a disease from occurring.','','Includes doctors of medicine or other healthcare professionals (e.g. <a resource="http://data.europa.eu/esco/occupation/95cda6a9-a70a-4a93-9442-11d573cb4a02" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/95cda6a9-a70a-4a93-9442-11d573cb4a02"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">midwife</span></a>).','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/80a648c5-ecb7-40e6-a4d4-7680291ffa74','key_16735','','','3115','3115.1.20','rolling stock engine tester',replace('rolling stock engine tester\ndiesel engine tester\nelectric engine tester\nlocomotive engine tester\nrail vehicle engine tester\nrailcar  engine tester\nmultiple unit engine tester\nrailway vehicle engine tester','\n',char(10)),replace('Rolling stock engine testers test the performance of diesel and electric engines used for locomotives. They position or give directions to workers positioning engines on the test stand. They use hand tools and machinery to position and connect the engine to the test stand. They use computerised equipment to enter, read and record test data such as temperature, speed, fuel consumption, oil and exhaust pressure.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/80c72c55-556a-41b1-87ef-a29c3d2afa6e','key_16736','','','2330','2330.1.3','business studies and economics teacher secondary school',replace('business studies and economics teacher\nteacher of business studies and economics in high school\nteacher of business studies and economics in secondary schools\nhigh school business studies and economics teacher\nhigh school economics teacher\nbusiness studies and economics teacher high school\nsecondary school business studies teacher\nhigh school business studies teacher\nteacher of economics in high school\nteacher of business studies in high school\nsecondary school business studies and economics teacher\nsecondary school economics teacher','\n',char(10)),'Business studies and economics teachers at secondary schools provide education toÂ students, commonly children and young adults, in aÂ secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, business and economics. They prepare lesson plans and materials, monitor the studentsâ progress, assist individually when necessary, and evaluate the studentsâ knowledge and performance on the subject of business and economicsÂ through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/80d880ec-3bae-45a2-a2d3-41345ee0b319','key_16737','','','2133','2133.9','nature conservation officer',replace('countryside ranger\nnature conservation officer\nwildlife control agent\nforester\nenvironmental enforcement officer\nconservation officer\nheritage coast ranger\nheritage coast warden\nfish and game warden\nconservation programmes officer\nzoo field conservation officer\npark ranger','\n',char(10)),'Nature conservation officers manage and improve the local environment within all the sectors of a local community. They promote awareness of and understanding about the natural environment. This work can be very varied and involve projects related to species, habitats and communities. They educate people and raise overall awareness of environmental issues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/80db1e93-6dad-49aa-b465-d64a4a73141b','key_16738','','','1324','1324.8.2.1','leather raw materials purchasing manager',replace('hide purchasing expediter\nskin, wet-blue, crust purchasing manager\nleather raw materials procurement manager\nleather raw materials purchasing managers\nleather raw materials purchasing manager\nleather raw materials purchase officer\nhide raw materials purchasing executive\nleather raw materials buyer\nleather purchasing and inventory co-ordinator\nhide purchasing manager\nleather raw materials purchaser','\n',char(10)),'Leather raw materials purchasing managers plan and purchase supplies of hides, skins, wet-blue or crust in coordination with the production requirements. They negotiate processes and forecast the levels of demand for products to meet business needs and keep constant check on stock levels and quality to maximise business efficiency. They identify potential suppliers, visit existing suppliers, and develop business relationships with them.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/80ec8f3e-2cda-4ea6-a382-ece99589ccdd','key_16739','','','2330','2330.1.1','art teacher secondary school',replace('secondary school art teacher\nhigh school art teacher\nteacher of art in high school\nart teacher\nart tutor in high school\nhigh school art tutor\nteacher of art in secondary school\nsecondary school art tutor\nart teacher in high school\nart teacher high school\nart tutor in secondary school','\n',char(10)),'Art teachers at secondary schools provide education toÂ students, commonly children and young adults, in aÂ secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, art. They prepare lesson plans and materials, monitor the studentsâ progress, assist individually when necessary, and evaluate the studentsâ knowledge and performance on the subject of artÂ through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/80fe3002-3ae4-4d3b-8969-0f2ce1049d7d','key_16740','','','5223','5223.7.22','jewellery and watches specialised seller',replace('luxury watches and diamond jewellery specialised seller\ndiamonds and precious metal jewellery salesperson\ndiamonds and precious metal jewellery specialised seller\nluxury watches, diamonds and jewellery salesperson\njewelry and watches specialized seller\njewellery specialised seller\ndiamond specialised seller\nluxury watches and diamond jewellery salesperson\njewellery salesperson\ndiamond salesperson\njewellery and watches salesperson','\n',char(10)),'Jewellery and watches specialised sellers sell, maintain and clean jewels and watches in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/810e5e67-acd5-499a-b307-cf5bea330859','key_16741','','','1330','1330.1.1','ICT information and knowledge manager',replace('IT knowledge manager\nICT information and knowledge manager\nIT information and knowledge manager\nIT information manager\nICT knowledge manager\nchief technology officer\nICT information manager','\n',char(10)),'ICT information and knowledge managers contribute to the definition of organisational information strategy and apply information and knowledge creation, editing, storage, and distribution policies. They manage the maintenance and evolution of structured and unstructured information. They create digital structures to enable exploitation and optimisation of information and knowledge, manage data analysis and enable business intelligence.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/811595cc-3100-4ca0-b860-3a77349342ae','key_16742','','','2144','2144.1.7','fluid power engineer',replace('fluid power assembler\nfluid power technology engineering consultant\nfluid power engineering operator\nfluid power engineering inspector\nfluid power engineer\nfluid power engineering expert\nfluid power technology engineer\nfluid power maintenance supervisor\nfluid power engineering adviser\nfluid power assembly supervisor\nfluid power technology engineering expert\nfluid power testing engineer\nfluid power engineers\nfluid power technology engineering specialist\nfluid power engineering specialist\nfluid power technology engineering adviser\nfluid power maintenance engineer\nfluid power engineering supervisor\nfluid power engineering consultant\nfluid power engineer installer\nhydraulic fluid power engineer\nfluid power assembly engineer\nfluid power tester','\n',char(10)),'Fluid power engineers supervise the assembly, installation, maintenance, and testing of fluid power equipment in accordance with specified manufacturing processes. They create designs with schematics and assembly models, make drawings and bills of materials for components, and analyse equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/812276dd-95ea-428b-b05e-a61c4089063c','key_16743','','','3422','3422.4.4','ski instructor',replace('ski coach\nskiing instructor\nski & snowboard instructor\nski instructor\nski trainer\ncross-country ski instructor\nski and snowboard instructor\nski teacher','\n',char(10)),'Ski instructors teach individuals or groups to ski and advanced skiing techniques. They advise their students on the choice of equipment, instruct skiers in alpine safety rules and plan and prepare ski instruction. Ski instructors demonstrate exercises and techniques during ski lessons and give feedback to their students on how to improve their level.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/812c217d-32b7-4f6a-9faf-993d3577ac7f','key_16744','','','2529','2529.9','knowledge engineer',replace('knowledge engineer\nknowledge officer\ninformation engineer\nlinked data engineer\ntaxonomist\nknowledge engineers','\n',char(10)),'Knowledge engineers integrate structured knowledge into computer systems (knowledge bases) in order to solve complex problems normally requiring a high level of human expertise or artificial intelligence methods. They are also responsible for eliciting or extracting knowledge from information sources, maintaining this knowledge, and making it available to the organisation or users. To achieve this, they are aware of knowledge representation and maintenance techniques (rules, frames, semantic nets, ontologies) and use knowledge extraction techniques and tools. They can design and build expert or artificial intelligence systems that use this knowledge.','','Excludes people performing programming and coding activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/81309031-dad2-4a7a-bde6-7f6e518f89ff','key_16745','','','3412','3412.4.9','residential care home worker',replace('residential care home worker\ncare home manager\nsleep in support care worker\ncare home worker\ncare home assistant\ncare support worker\ncare home supervisor','\n',char(10)),'Residential care home workers follow a specific plan to provide day-to-day care to clients. They develop a client-focused environment in the care homes where they work. They look after the physical and mental wellbeing of clients by providing social care in residential centres','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/814205e9-8bd1-4653-bdbc-fd2f9e9a8225','key_16746','','','1420','1420.4.28','kitchen and bathroom shop manager',replace('bathroom superstore manager\nkitchen showroom manager\nkitchen and bathroom shop manager\nkitchen shop manager\nbathroom showroom manager\nbathroom store manager\nkitchen superstore manager\nkitchen store manager\nkitchen and bathroom showroom manager\nhome improvements superstore manager\nbathroom shop manager\nkitchen and bathroom store manager\nhome improvements store manager\nhome improvements shop manager\nDIY superstore manager\nkitchen and bathroom superstore manager','\n',char(10)),'Kitchen and bathroom shop managers are responsible for activities and staff in specialised shops that sell kitchens and bathrooms. ​They manage employees, monitor the sales of the store, manage budgets and order supplies when a product is out of supply and perform administrative duties if required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/81480b40-a318-47f3-9b2c-56b69ed67d95','key_16747','','','2522','2522.1.1','ICT network administrator',replace('network specialist\nnetwork support\nnetworks administrator\nICT network administrator\nnetwork administrators\nnetwork manager','\n',char(10)),'ICT network administrators maintain operation of a reliable, secure, and efficient data communications network, including LAN, WAN, intranet, and internet. They perform network address assignment, management and implementation of routing protocols such as ISIS, OSPF, BGP, routing table configurations and certain implementations of authentication. They perform maintenance and administration of servers (file servers, VPN gateways, intrusion detection systems), desktop computers, printers, routers, switches, firewalls, phones, IP communications, personal digital assistants, smartphones, software deployment, security updates and patches as well as a vast array of additional technologies inclusive of both hardware and software.','','Includes people performing managerial activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8188136d-df82-4910-91e1-b185ec17c3c9','key_16748','','','7513','7513.2','milk reception operator',replace('milking operator\nmilk dairy worker\nmilk reception operative\nmilk reception operator\nmilk worker','\n',char(10)),replace('Milk reception operators use devices that ensure the correct qualitative and quantitative reception of the raw milk. They perform initial cleaning operations, storage and distribution of raw material to the different processing factory units. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/81a1e1f5-cce3-444d-bfc3-9771236d57cd','key_16749','','','3153','3153.1','aircraft maintenance engineer',replace('air transportation engineer\naviation systems technician\naircraft maintenance engineer','\n',char(10)),'Aircraft maintenance engineers make preflight and postflight inspections, adjustments, and minor repairs to ensure safe and sound performance of aircrafts. They inspect aircraft prior to takeoff to detect malfunctions such as oil leaks, electrical or hydraulic problems. They verify passenger and cargo distribution and amount of fuel to ensure that weight and balance specifications are met.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/81cc1583-3614-4207-8e54-00b8cd4ddf09','key_16750','','','1219','1219.5.1','chemical plant manager',replace('manager of  chemical processing plant\nchemical plant supervisor\nchemical production process manager\nchemical plant superintendent\nchemical plant manager\nchemical processing plant manager\nmanager of chemical plant\nchemical plant operations executive\nchemical production plant managing director\nchemical plant technial manager\nchemical plant operations manager\nchemical production plant manager\nchemical plant technical director\nplant managing director\nchemical plant managing director\nchemical production facility manager','\n',char(10)),'Chemical plant managers coordinate the day-to-day production of chemical products ensuring the quality of products and equipment, the safety of personnel and protection of the environment. They define and implement the investment budget, deploy industrial objectives and manage the unit as a profit center representing the company in its economic and social environment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/81f6a814-1f4e-47d4-9c75-47977c7267ee','key_16751','','','8311','8311.1','train driver',replace('passenger engine driver\nengine driver\npassenger train driver\nlocomotive engineer\ntrain driver\ndriver of trains\ntrain operator\nmetro driver','\n',char(10)),'Train drivers operate trains providing passenger or cargo transport services. They are responsible for driving the locomotive in safe manner, respecting all relevant safety, operational and communication regulations, and have full responsibility for the safety of passengers and cargo. The task involves cooperation and communication with the relevant staff under the infrastructure manager, as well as with staff on board the train.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/81f8d141-3f67-4fb0-a349-db0147ef7b3a','key_16752','','','2320','2320.1','vocational teacher',replace('vocational teaching assistant\ntechnical institute teacher\nvocational college teacher\ncareer and technology teacher\nvocational university tutor\ntechnological institute teacher\nvocational educator\nvocational college lecturer\nvocational lecturer\nvocational university teacher\nvocational education teacher\nvocational teacher\nvocational tutor\nvocational instructor','\n',char(10)),'Vocational teachers instruct students in their specialised field of study, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques that the students must subsequently master in the specialised vocation of their choice and aid in the development of the according attitudes and values. Vocational teachers monitor the students progress, assist individually when necessary, and evaluate their knowledge and performance on the subject through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/81f8ea60-41d4-434a-b0fc-f870e83e7e27','key_16753','','','8131','8131.2.1','fertiliser mixer',replace('fertiliser mixing machine operator\nmixer of fertilizer\nfertilizer mixing machine operator\nmixer of fertiliser\nfertilizer mixer\nfertilizer mixing machine operative','\n',char(10)),'Fertiliser mixers operate and maintain machines that mix chemical ingredients in order to produce fertilisers, making sure the end product is according to specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/82025c02-9082-416c-998b-f51eb0359ee6','key_16754','','','4224','4224.1','hospitality establishment receptionist',replace('reception worker\nhotel receptionist\nhospitality establishment receptionist\nreception assistant\nreservations clerk\nreception area clerk','\n',char(10)),'Hospitality establishment receptionists provide first point of contact and assistance to the guests of a hospitality establishment. They are also responsible for taking bookings, processing payments and giving information.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8213b4bc-60ce-4ffc-9e00-9758fb2003c6','key_16755','','','2422','2422.10','monitoring and evaluation officer',replace('M&E officer\nsocial impact assessment analyst\neconomic impact assessment officer\nresearch and evaluation officer\nsocial impact assessment specialist\nevaluation Officer\nevaluator\nmonitoring, evaluation and learning officer\nMEL officer\nimpact assessment manager\nmonitoring evaluation, impact and learning officer\nMEIL officer\nmonitoring and evaluation specialist\nMEL Project Officer\nmonitoring and reporting analyst','\n',char(10)),'Monitoring and evaluation officers are responsible for the conceptualisation, design, implementation and follow-up of the monitoring and evaluation activities of projects, programmes, policies, strategies, institutions or processes, along the relevant programming cycle. They develop monitoring, inspection and evaluation methods and instruments needed to collect and analyse data, and report on results by applying structured M&E frameworks, theories, approaches and methodologies. M&E officers inform decision-making through reporting, learning products or activities and knowledge management. They can also engage in capacity development activities by providing training and capacity building support within their organizations or for clients and partners.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/822bbc2d-bc6a-4fb5-afb8-89170120c6d8','key_16756','','','3119','3119.1','airport maintenance technician',replace('maintenance technician at airports\nmaintenance specialist at airports\nspecialist in airport maintenance\nairport maintenance technician\ntechnician in airport maintenance\nairport maintenance technical officer\nairport maintenance specialist\nairport maintenance officer','\n',char(10)),'Airport maintenance technicians are in charge of the maintenance of all equipment necessary for ensuring the functionality of the airport, for example, visual aids, airport electrical systems, luggage systems, security systems, pavements, drainage, and maintenance of unpaved areas.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8234eee9-1647-460d-8d62-7fe02cb884b0','key_16757','','','3115','3115.1.17','refrigeration air condition and heat pump technician',replace('air conditioning service technician\nair conditioning technician\nair conditioning installation technician\nrefrigeration service technician\nrefrigeration mechanic\nheat pump installation technician\nheat pump technician\nrefrigeration installation technician\nheat pump service technician','\n',char(10)),'Refrigeration air condition and heat pump technicians have the competence and ability to safely and satisfactory perform design, pre-assembling, installation, putting into operation, commissioning, operating, in-service inspection, leakage checking, general maintenance, circuit maintenance, decommissioning, removing, reclaiming, recycling refrigerant and dismantling of refrigeration, air condition and heat pump systems, equipment or appliance, and to work with electrical, electrotechnical and electronical components of refrigeration, air conditioning and heat pump systems. ','','Refrigeration air condition and heat pump technicians do not perform works on heating and ventilation systems.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/823db06f-25e1-4658-b063-d6ef956b41f5','key_16758','','','1324','1324.3','logistics and distribution manager',replace('logistics and distribution manager\nlogistics and distribution director\nlogistics director\nlogistics coordinator\ndistribution & logistics manager\nlogistics & distribution manager\ndirector of logistics and distribution\nmanager of logistics and distribution\ndistribution logistics manager\ndistribution coordinator\ndistribution and logistics manager\nlogistics supply chain coordinator\nsupply chain logistics manager\nlogistics manager\nlogistics operations manager','\n',char(10)),'Logistics and distribution managers take decisions on logistic services, operations and provisions. They take internal and external variables into consideration for effective and successful organisational logistic services. They give appropriate support to all the activities of the supply chain from the beginning to the end. These professionals organise the storage and distribution of goods and ensure that the right products are delivered to the right location on time and at a good cost.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8249697a-3dc8-417d-9acf-3a7e0864069a','key_16759','','','3123','3123.1.24','terrazzo setter supervisor',replace('epoxy terrazzo setter supervisor\nterrazzo worker supervisor\nterrazzo setter foreman\nterrazzo setter supervisor\nterrazzo flooring supervisor\nterrazzo team foreman\nterrazzo team supervisor\nterrazzo layer supervisor\nconcrete flooring supervisor\npoured flooring supervisor','\n',char(10)),'Terrazzo setter supervisors monitor terrazzo setting operations. They assign tasks and take quick decision to resolve problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/825ab049-2f0c-4acf-b402-9c6bb51acca2','key_16760','','','1431','1431.3','sport facility manager',replace('sport venue manager\nfitness club manager\nswimming pool manager\ndirector\nsport facility manager\nstadium manager\nsport hall manager','\n',char(10)),'Sport facility managers lead and manage a sport facility or venue, including its operations, programming, sales, promotion, health and safety, development, and staffing. They ensure it provides excellent customer service whilst achieving business, financial and operational targets.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/82616669-cc82-4455-9332-a2df209e9f3e','key_16761','','','2152','2152.1.9','microelectronics smart manufacturing engineer',replace('microelectronics technology consultant\nmicroelectronics manufacturing engineer\nmicroelectronics technology engineer\nmicroelectronics production adviser\nsmart production expert\nsmart manufacturing engineer\nmicroelectronics production supervisor\nmicroelectronics manufacturing professional\nmicroelectronics productions specialist\nmicroelectronics technology expert\nmicroelectronics production engineer\nengineer of microelectronics technology','\n',char(10)),'Microelectronics smart manufacturing engineers design, plan and supervise the manufacturing and assembly of electronic devices and products, such as integrated circuits, automotive electronics or smartphones, in an Industry 4.0 compliant environment.','',replace('Includes manufacturing at the semiconductor level, \nExcludes the manufacturing of electronic devices, appliances, systems.\nIncludes the aspects of microelectronics manufacturing.\n','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8269720b-49c7-4185-ad7e-05d8d31ac041','key_16762','','','5223','5223.7.37','toys and games specialised seller',replace('games specialised seller\nkids toy shop salesperson\ntoy salesperson\ntoys  and games specialized seller\ngames and toys specialised seller\ntoy specialised seller\nkids toy shop specialised seller\ngames salesperson\ntoy superstore salesperson\ntoy superstore specialised seller\ngames and toys salesperson\nkids toys salesperson\nkids toys specialised seller\nmodels and games specialised seller\nkids toys and games specialised seller\nkids toys and games salesperson\nmodels and collectibles salesperson\nmodels and collectibles specialised seller\nmodels and games salesperson\ntoy and game superstore salesperson\ntoy and game superstore specialised seller\nmodels and collectibles shop salesperson','\n',char(10)),'Toys and games specialised sellers sell toys and games in specialised shops.','','Excludes people performing video-games sales.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8289625a-c1c1-4a49-8ddd-37be4c3cd3fc','key_16763','','','2320','2320.1.4','auxiliary nursing and midwifery vocational teacher',replace('auxiliary nursing and midwifery instructor\nauxiliary nursing and midwifery teacher\ninstructor in auxiliary nursing and midwifery\nvocational educator in auxiliary nursing and midwifery\nauxiliary nursing and midwifery vocational instructor\ntrainer in auxiliary nursing and midwifery\nauxiliary nursing and midwifery vocational trainer\neducator in auxiliary nursing and midwifery\nauxiliary nursing and midwifery vocational teacher\nvocational teacher in auxiliary nursing and midwifery','\n',char(10)),'Auxiliary nursing and midwifery vocational teachers instruct students in their specialised field of study, auxiliary nursing and midwifery, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master. Auxiliary nursing and midwifery vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of auxiliary nursing and midwifery through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/82cbe20d-967c-4161-9ecd-eafc724d171c','key_16764','','','2269','2269.5','occupational therapy assistant',replace('occupational therapy assistant\nOT support worker\nOT assistant\noccupational therapy rehabilitation assistant\nsupport worker in occupational therapy\ntechnician in occupational therapy\nassistant in occupational therapy','\n',char(10)),'Occupational therapy assistants support occupational therapists by working with people and communities to enhance their ability to engage in the occupations they want to, need to, or are expected to do, or by modifying the occupation or the environment to better support their occupational engagement. They work under the supervision of an occupational therapist. ','',replace('Includes people working with students. \nIncludes people performing staff supervision.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/82d75f3b-644a-4582-ad6b-a2a1833e44cb','key_16765','','','7543','7543.5','non-destructive testing specialist',replace('non-destructive testing technician\nnondestructive test specialist\nnon-destructive testing technologist\nnon-destructive testing specialist\nnon-destructive testing assistant\nnon-destructive testing technology specialist\nnon-destructive testing technology expert\nnon-destructive test specialist\nnondestructive testing specialist','\n',char(10)),'Non-destructive testing specialists carry out testing of vehicles, vessels, other manufactured objects, and construction structures without having to damage them. They use special equipment such as X-ray, ultrasound, radiographics, or infrared instruments to perform testing activities and report based on the observed results.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/82dea303-26e1-4bbc-a07c-cff3cac6b50e','key_16766','','','5141','5141.1.3','hair stylist',replace('hair artist\nhair stylist\nhair & wig artist\nhair and wig artist\nhair & wig stylist\nhair and wig stylist\nhair stylist & make up  artist','\n',char(10)),'Hair stylists wash, dry, cut and style the hair of singers and presenters and various types of actors, including stage, movie, tv and music video actors. They work together with the art director to design the look of every person. Hair stylists also dress wigs and hairpieces. They standby during these artistic activities to touch up the actors'' hair or wigs.','','Excludes <a resource="http://data.europa.eu/esco/occupation/099c6bb0-22d3-4c5d-8bf5-70910af381ef" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/099c6bb0-22d3-4c5d-8bf5-70910af381ef"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">hairdressers</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/82e37849-30ec-4461-a40e-263d4c984f88','key_16767','','','2114','2114.1.9','palaeontologist',replace('ichnologist\npalaeontology analyst\npalaeoecologist\npalaeontology scholar\npalaeontology research analyst\npalaeontology science researcher\npalaeontology researcher\npalaeontology scientist\npaleontologist\npalaeontology research scientist\nmicropalaeontologist','\n',char(10)),'Palaeontologists research and analyse forms of life that existed in ancient eras of the planet Earth. They strive to define the evolutionary path and the interaction with different geological areas of all kinds of once organisms and such plants, pollen and spores, invertebrate and vertebrate animals, humans, traces such as footprints, and ecology and climate.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/82ea66b0-0f9c-44ed-94bc-0c5b829531b3','key_16768','','','7312','7312.6.1','guitar maker',replace('guitar inspector\nguitar craftsman\nstringed instrument builder\nstringed instrument inspector\nguitar quality inspector\nstringed instrument tester\nstringed instrument quality inspector\nguitar quality tester\nguitar craftswoman\nguitar maker\nguitar technician\nguitar builder\nstringed instrument quality tester\nguitar tester','\n',char(10)),'Guitar makers create and assemble parts to build guitars according to specified instructions or diagrams. They work wood, measure and attach strings, test quality of strings and inspect the finished instrument.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/82f90e87-de92-4678-adae-61d3e5f7e1e4','key_16769','','','1330','1330.2','chief information officer',replace('chief information officers\nCIO\nIT director\nchief information officer','\n',char(10)),'Chief information officers define and implement the ICT strategy and governance. They determine necessary resources for the ICT strategy implementation, anticipate ICT market evolutions and company business needs. They contribute to the development of the organisation''s strategic plan and ensure that the ICT infrastructure supports the organisation''s overall operations and priorities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/82fe0140-7069-47f9-a7d4-d63a611cb5bd','key_16770','','','2432','2432.2','election agent',replace('electorate officer\ncampaign manager\nparliamentary agent\nelections officer\nelection agent','\n',char(10)),'Election agents manage a political candidate''s campaign and oversee the operations of elections to ensure accuracy. They develop strategies to support candidates and persuade the public to vote for the candidate they represent. They conduct research to gauge which image and ideas would be most advantageous for the candidate to present to the public in order to secure the most votes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/83191970-020d-4350-b2e0-be971533d286','key_16771','','','3122','3122.4.5','electronics production supervisor',replace('supervisor of electronics production line\nmanager in electronics manufacturing\nelectronics production line supervisor\nelectronics manufacturing manager\nproduction supervisor in electronics manufacturing\nelectronics production supervisor\nelectronics manufacturing supervisor\nelectronics production manager\nsupervisor in electronics manufacturing\nproduction line supervisor','\n',char(10)),'Electronics production supervisors coordinate, plan and direct the electronics production process. They manage labourers working on the production line, oversee the quality of the assembled goods, and perform cost and resource management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/834aab9b-2246-4c3e-ab4a-cf3de1267e1a','key_16772','','','5165','5165.1.2','car driving instructor',replace('car driver instructor\ndriving instructor\ncar driving training  instructor\ncar driving instructor\ncar driver training instructor\nautomobile driving instructor\ndriver education instructor','\n',char(10)),' Car driving instructors teach people the theory and practice of how to operate a car safely and according to regulations. They assist students in developing the skills needed to drive and prepare them for the driving theory tests and the practical driving test.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/83588a0e-b63d-4483-a884-06f1e3a1a4be','key_16773','','','1349','1349.23','translation agency manager',replace('language translation agency manager\ntranslation service manager\ninterpretation agency supervisor\nlanguage interpretation agency director\ntranslation agency manager\nlanguage translation service manager\nlanguage translation agency supervisor\nlanguage translation agency director\nlanguage interpretation agency manager\ninterpretation agency director\nlanguage interpretation agency supervisor\nlanguage interpretation service manager','\n',char(10)),'Translation agency managers oversee operations in the delivery of translation services. They coordinate the efforts of a team of translators who translate written material from one language to another. They ensure the quality of the service and the administration of the translation agency.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/835d118e-11db-4b11-b055-d78bfe426cac','key_16774','','','7224','7224.2','tool grinder',replace('lead tool grinder\ngrinder\ntool grinder\ntool cutter and grinder\noperator of tool and cutter grinder\nprecision tool grinder\ntoolgrinder\ntool sharpener\nCNC operator of tool and cutter grinder','\n',char(10)),'Tool grinder perform precision grinding processes on metal objects and tools. They grind, sharpen or smoothen metal surfaces using the appropriate tools and instruments. Tool grinders follow tooling instructions and assure the processed workpiece meets the necessary specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/836f0a96-7546-4003-9fc8-714fc79199d4','key_16775','','','2149','2149.2.3','contract engineer',replace('subcontractor engineer\ncontract engineering specialist\ncontract engineering expert\ncontractor engineer\ncontract engineering adviser\ncontract engineer\ncontract engineering consultant','\n',char(10)),'Contract engineers combine technical knowledge of contracts and legal matters with understanding of engineering specifications and principles. They ensure that both parts are aligned in the development of a project and foresee the compliance of all the engineering specifications and matters as defined in contracts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/837502a6-57d9-4df4-903a-8a67dd53927f','key_16776','','','8350','8350.3','engine minder',replace('engine minder on vessels\nnarrowboat crew member\ncanal narrowboat engine minder\nnarrow boat crew member\ncanal boat engine minder\nnarrowboat engine minder\nvessel engine operator\nengine minder\ncanal boat crew member\ncanal narrowboat crew member\nnarrow boat engine minder\non board engine minder\nbarge crew member\nbarge engine minder\nship engine operator','\n',char(10)),'Engine minders perform work related to the deck department of an inland water transport vessel. They use their experience on-board a motorised inland navigation vessel as an ordinary crewmember and have a basic knowledge of engines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/839d738b-8b54-4408-868a-6d8af102d2de','key_16777','','','8211','8211.6','rolling stock assembler',replace('railcar assembler\nlocomotive assembler\ncarriage assembler\nwagon assembly worker\ncarriage assembly worker\nrailway vehicle assembly worker\nmultiple unit assembly worker\nrailcar assembly worker\nrolling stock assembler\nrailway vehicle assembler\nrail vehicle builder\nrolling stock builder\nmultiple unit assembler\nmultiple unit builder\nwagon builder\nwagon assembler\nrail vehicle assembler\ncarriage builder\nrailway vehicle builder\nrail vehicle assembly worker\nlocomotive assembly worker\nlocomotive builder\nrolling stock assembly worker\nrailcar builder','\n',char(10)),'Rolling stock assemblers use hand tools, power tools and other equipment such as lifting equipment or robots to construct, fit and install prefabricated parts to manufacture rolling stock subassemblies and body structures. They read and interpret blueprints. They operate control systems to determine functional performance of the assemblies and adjust accordingly.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/83b30677-6468-4a04-b20c-15daf6bc8fb5','key_16778','','','3331','3331.2.1.8','import export specialist in computers, peripheral equipment and software',replace('senior import specialist in computers, peripheral equipment and software\ncustoms compliance specialist in computers, peripheral equipment and software\ngraduate import specialist in computers, peripheral equipment and software\nassistant export specialist in computers, peripheral equipment and software\ntrainee export specialist in computers, peripheral equipment and software\nassistant import export specialist in computers, peripheral equipment and software\ntrainee import specialist in computers, peripheral equipment and software\ngraduate export specialist in computers, peripheral equipment and software\ntrainee import export specialist in computers, peripheral equipment and software\nsenior export specialist in computers, peripheral equipment and software\ninternational trade compliance specialist in computers, peripheral equipment and software\ngraduate import export specialist in computers, peripheral equipment and software\nimport export specialist in computers, peripheral equipment and software\nassistant import specialist in computers, peripheral equipment and software\nsenior import export specialist in computers, peripheral equipment and software','\n',char(10)),'Import export specialists in computers, peripheral equipment and software have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/83dc2d94-9e30-493a-bd93-6346e05fe146','key_16779','','','2635','2635.1.3','family planning counsellor',replace('family planning social counsellor\nfamily planning clinic advisor\npregnancy advisor\nsenior sexual health advisor\nsexual health advisor\nreproductive health advisor\nsexual health lead\nfamily planning counselor\nfamily planning advisor','\n',char(10)),'Family planning counsellors provide support and counselling to adults and adolescents on issues such as reproduction, contraceptive methods, pregnancy or termination of pregnancy, in compliance with the legislation and practices. They also provide information about maintaining optimum health practices, sexual disease prevention and treatment recommendation referrals, working in collaboration with the professional doctors.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/83de6811-975d-4f7c-b81e-604c0c17e800','key_16780','','','8160','8160.32','fish production operator',replace('fish products production operative\nfish products manufacture specialist\nfish products operator\nfish production operator\nfish production worker','\n',char(10)),'Fish production operators regulate and supervise fish product manufacturing facilities. They keep stock of all required ingredients and equipment for mass manufacturing. They also perform processes such as moulding, breading, frying, freezing, setting the system''s temperature and checking speeds.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/83e45d86-1bce-45c5-ba8b-097738f9f28b','key_16781','','','2656','2656.2','news anchor',replace('news presenter\nnewscaster\nnews broadcaster\nnewsreader\nbroadcast news analyst\nanchorwoman\nTV news anchor\nchief anchor\ntelevision news anchor\nanchor\nnews anchor\nTV news presenter\nanchorman\nbusiness news anchor\nradio news presenter\nlead anchor','\n',char(10)),'News anchors present news stories on radio and television. They introduce pre-recorded news items and items covered by live reporters. News anchors are often trained journalists.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/84219dd7-eb68-426e-9ec3-431eceb61c96','key_16782','','','3115','3115.1.19','rolling stock engine inspector',replace('rolling stock engine inspector\nrailway vehicle engine inspector\nrail workshop repair inspector\ntraction engineering technician\nwagon engine inspector\nlocomotive engine inspector\nrail vehicle engine inspector\nrail vehicle repair inspector\ncarriage engine inspector\nrail depot repair inspector\nrailway depot repair inspector\nmultiple unit engine inspector\ndepot repair inspector\nworkshop repair inspector\nrailway workshop repair inspector\nrolling stock repair inspector\nrailcar  engine inspector','\n',char(10)),'Rolling stock engine inspectors inspect diesel and electric engines used for locomotives to ensure compliance with standards and regulations. They conduct routine, post-overhaul, pre-availability and post-casualty inspections. They provide documentation for repair activities and technical support to maintenance and repair centers. They review administrative records, analyse the operating performance of engines and report their findings.','',replace('Excludes <a resource="http://data.europa.eu/esco/occupation/42e9d1c5-76f1-41ef-996e-38d5ac711a31" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/42e9d1c5-76f1-41ef-996e-38d5ac711a31"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">rolling stock inspector</span></a>.\n Includes people performing inspections on engines only.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/842d4d83-7e18-493a-a4a6-3f2a36504b18','key_16783','','','5414','5414.1.5','gate guard',replace('gatewoman\ngateman\nplant gatekeeper\ngate keeper\nsecurity entrance attendant\nsecurity gate attendant\ngate attendant\ngate guard','\n',char(10)),'Gate guards control the access and exit to buildings, warehouses or other type of property to prevent unauthorized presence and unwanted incidents. They prevent and identify theft of corporate property, investigate suspicious activities and write reports. Gate guards may assist employees or visitors with requests or indications. They use handheld radio stations to communicate and operate alarm systems and computers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8467dda9-34c6-4c6f-aa37-e7bddffac007','key_16784','','','2250','2250.5','aquatic animal health professional',replace('aquatic marine health specialist\naquatic animals health professional\naquaculture health professional\naquatic animal health expert\naquatic marine health consultant\naquaculture health adviser\naquatic marine health adviser\naquaculture health expert\nmarine animal health expert\naquatic animals health specialist\naquatic animal health adviser\naquatic animal health specialist\naquatic animal health consultant\naquaculture health specialist\naquaculture health consultant\naquatic animal health professional','\n',char(10)),'Aquatic animal health professionals diagnose, prevent and treat diseases, injuries and dysfunctions of aquatic animals by implementing appropriate sampling protocols. They supervise the use of medicines, including vaccines, and collect data on fish health, making regular reports to the appropriate personnel. They may provide care to a wide range of aquatic animals or specialise in the treatment of a particular group or in a particular speciality area. They may provide advice, support and training to farm staff on best practice with regard to the health and welfare of the cultured organisms.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/84871c2a-abd1-45eb-b03a-65e51a26fd84','key_16785','','','2411','2411.1.5','cost analyst',replace('cost accounting clerk\ncost analyst\ncost accountant\ncost recovery estimator\nquantity surveyor\ncost accounting officer\ncost and budget analyst\ncost estimator','\n',char(10)),'Cost analysts prepare regular costs, budgeting analyses and reports in order to contribute to the overall cost planning and forecasting activities of a business. They review and reconcile key balance sheets and identify new opportunities to save costs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/84931290-ad27-45ad-b611-9556c5facbdb','key_16786','','','3332','3332.1','event assistant',replace('event assistant\nevents assistant\nspecial event assistant\nevent coordinator\nconference and event coordinator\nconference and event assistant','\n',char(10)),'Event assistant implement and follow plans detailed by event managers and planners. They specialise in a part of the planning either the coordination of the catering, transportation, or the facilities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/849a656c-8559-4471-91ed-442c42b0b641','key_16787','','','3118','3118.2','clothing CAD technician',replace('CAD fashion designer\nclothing CAD operator\nclothing computer aided designer technician\nclothing CAD technician\nclothing computer-assisted designer technician\nfashion CAD technician\nclothing CAD specialist','\n',char(10)),'Clothing CAD technicians use software to create design plans for clothing products. They work in 2D design which is known as surface modelling, or 3D design which is called solid modelling. They use surface modelling to draw a flat representation of the clothing product. In solid modelling, they create a 3D display of a structure or component in order to take a virtual look of the clothing product.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/84cb6e91-ce11-409e-9306-23c9698631af','key_16788','','','3111','3111.14','soil surveying technician',replace('field survey technician\nsurvey technician\nmineral technologist\nsurveyor\nland survey technician\nsoil scientist\nmineral technician\nfield surveyor\ngeological technician\ngeologist\nsoil surveying technician\ngeological technologist\nsoil technician','\n',char(10)),'Soil surveying technicians analyse soil by carrying out technical surveying tasks, using soil surveying techniques. They focus on the process of classifying soil types and other soil properties. Soil surveying technicians operate surveying equipment and utilise programs to retrieve and interpret relevant data, and perform computations as required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/84cdc2ef-ee7c-42b1-be19-5e1c6b537f8f','key_16789','','','5164','5164.9','pet sitter',replace('animal sitter\ndog walker\ndog visitor\npet sitters\npet sitter\nanimal carer\ncompanion animal sitter\ndog sitter\ncat sitter','\n',char(10)),'Pet sitters provide animal-sitting services including dog walking, home-boarding, pet/home sitting, day boarding and animal transportation services.  They maintain records, use appropriate and safe handling techniques and do routine monitoring of the health and welfare of the animal.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/84d45916-eef7-4c58-a73c-3639c9965014','key_16790','','','8142','8142.9','plastic furniture machine operator',replace('plastic furniture machine worker\nplastic furniture manufacturer\nplastic furniture machine operative\nplastic furniture assembler\nplastic furniture maker\nplastic furniture machine setter\nplastic furniture assembly worker\nplastic furniture production operative\nplastic furniture machine tender\nplastic furniture machine operator\nplastic furniture production process worker','\n',char(10)),'Plastic furniture machine operators tend plastic processing machines that produce pieces such as plastic chairs and tables. They inspect each resulting product, detect abnormalities and remove inadequate pieces. In some cases, they may assemble different plastic parts to obtain the final product.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/84dfc210-db07-4a83-87fe-d6131907ea83','key_16791','','','8160','8160.47','prepared meat operator',replace('prepared meat operator\nmeat processor\nmeat manufacturer\nmeat process worker\nmeat machine operator','\n',char(10)),'Prepared meat operators process meat either by hand or using meat machines such as meat grinding, crushing or mixing machines. They perform preservation processes such as pasteurising, salting, drying, freeze-drying, fermenting and smoking.  Prepared meat operators strive to keep meat free from germs and other health risks for a longer period than fresh meat.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/84e4c780-ee17-44dd-af6f-d1c06a3e353d','key_16792','','','1324','1324.3.2.24','import export manager in mining, construction and civil engineering machinery',replace('international trade manager in mining, construction and civil engineering machinery\nsenior export manager in mining, construction and civil engineering machinery\ngraduate import manager in mining, construction and civil engineering machinery\ngraduate export manager in mining, construction and civil engineering machinery\ntrainee import manager in mining, construction and civil engineering machinery\ntrainee import export manager in mining, construction and civil engineering machinery\nimport export manager in mining, construction and civil engineering machinery\nassistant import manager in mining, construction and civil engineering machinery\ntrainee export manager in mining, construction and civil engineering machinery\nsenior import manager in mining, construction and civil engineering machinery\nassistant import export manager in mining, construction and civil engineering machinery\ngraduate import export manager in mining, construction and civil engineering machinery\nsenior import export manager in mining, construction and civil engineering machinery\ncustoms compliance manager in mining, construction and civil engineering machinery\ninternational trade compliance manager in mining, construction and civil engineering machinery\nassistant export manager in mining, construction and civil engineering machinery','\n',char(10)),'Import export managers in mining, construction and civil engineering machinery install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/84f20f5d-149a-4512-b319-18a81d3d6026','key_16793','','','3435','3435.24','stunt performer',replace('equine stunt performer\ndaredevil\nstunt artist\nfight performer\nstunt double\nfilm and television stunt performer\nstunt fight performer\nfilm & TV stunt performer\nstuntwoman\nfilm and TV stunt performer\nstunt man\nstunt performer\nstunt woman\nstunt action performer\nfilm & television stunt performer','\n',char(10)),'Stunt performers execute actions that are too dangerous for actors to perform, that they are not physically able to do or require specialised skills such as fight scenes, jumping from building, dancing and others.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/84fabf82-4abf-4f41-99aa-55718a2e500b','key_16794','','','4312','4312.7','tax clerk',replace('tax collection clerk\ntax revenue officer\ntax compliance officer\ntax collection assistant\ntax collection officer\nfiscal clerk\ntax clerk','\n',char(10)),'Tax clerks collect financial information in order to prepare accounting and tax documents. They also perform clerical duties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8529edc5-96ad-4cde-b34a-f86ad3753e4d','key_16795','','','2433','2433.4','sales engineer',replace('PCV sales technician\nlight vehicle sales engineer\nheavy vehicle sales technician\nbus and coach sales technician\nlight vehicle sales technician\ncoach sales engineer\nLGV sales engineer\nsales engineer\nPCV sales engineer\nheavy vehicle sales engineer\nLGV sales technician\nbus sales engineer\nbus and coach sales engineer\ncoach sales technician\nbus sales technician','\n',char(10)),'Sales engineers provide technical customisation of products based on customers requests and needs (mainly heavy duty), such as building equipment. They take care of business to business contact and assume responsibility for complex repairs and maintenance process.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/853698f5-0f1e-42ae-9301-e5411a26dae2','key_16796','','','2320','2320.1.3','armed forces training and education officer',replace('military training and education officer\nmilitary academy instructor\narmed forces trainer\ntraining and education officer in armed forces\narmed forces educator\narmed forces training and education officer\nmilitary instructor\narmed forces instructor\nmilitary trainer','\n',char(10)),'Armed forces training and education officers train probationary, new academy recruits, or cadets, on the theory and practice necessary to become a soldier or a military officer, and need experience as a military officer themselves as a requirement to instruct. They prepare and present theoretical courses and materials during training on subjects such as law, national and international regulations, defense and offense models, world affairs etc. They also conduct the physical training of the cadets, teaching them care and usage of weapon and machinery, first aid, self defense and offense techniques, military vehicle operations, and putting them through a series of heavy drills and physical training. Armed forces training and education officers manage training plans by developing and updating the curriculum and field training exercises when necessary. They also assist senior officers in preparation for a promotion and generally monitor the cadets'' progress and evaluate their performance through a series of theoretical and physical tests. They prepare performance and evaluation reports for each cadet individually.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/85482504-c83d-4038-80d9-265198d18b48','key_16797','','','2635','2635.3.4','community care case worker',replace('domiciliary care supervisor\nindependent living coordinator\ncommunity care case worker\ndomiciliary care manager\ncommunity care social worker','\n',char(10)),'Community care case workers perform assessment and care management. They organise domiciliary services to support vulnerable adults who are living with physical impairment or convalescing, aiming to improve their lives in the community and enabling them to live safely and independently at their own home.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/857ed740-f4ae-43d0-8295-d6181f1d7eed','key_16798','','','5163','5163.4','funeral services director',replace('cemetery manager\nfuneral services director\nbereavement manager\nburial ground custodian\ncemetery caretaker\nmortician\ncrematorium technician\nfuneral director\ncrematorium supervisor\nfuneral home worker\ncrematorium operator','\n',char(10)),replace('Funeral services directors coordinate the logistics of funerals. They support the deceased family by arranging the details concerning the location, dates and times of memorial services. Funeral services directors contact representatives of the cemetery to prepare the site, plan transportation for the deceased person, advise on the types of memorials and legal requirements or paperwork. \n \nFuneral services directors organise the daily operations of the crematorium. They oversee the staff''s activities in the crematorium and ensure that they deliver services according to legal requirements. They monitor the crematorium service revenue budget and develop and maintain operational rules within the crematorium.','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/85acc7e9-1fac-4e91-962b-8b8031f39487','key_16799','','','2141','2141.11','textile technologist',replace('analytical textile technologist\nfabric technologist\ntextile engineer\ntextile technology officer\ntextile manufacturing department manager\ntextile technologist','\n',char(10)),'Textile technologists are in charge of the optimisation of the textile manufacturing system management, both traditional and innovative. They develop and supervise the textile production system according to the quality system: processes of spinning, weaving, knitting, finishing namely dyeing, finishes, printing with appropriate methodologies of organisation, management and control and using emerging textile technologies. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/85fdd1cb-98d3-4c40-a868-1078ce770440','key_16800','','','7543','7543.10.4','textile quality inspector',replace('textile quality analyst\ntextile quality assurance inspector\nquality textile inspector\ntextile quality inspector\ntextile products inspector','\n',char(10)),'Textile quality inspectors ensure that the manufacture of textile products is compliant with predetermined specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8606c0fd-c7cb-404d-887f-3a30ef7a81b4','key_16801','','','2113','2113.2','sensory scientist',replace('sensory and consumer research scientist\nsensory science specialist\nhaircare sensory scientist\nperfumery sensory scientist\nsensory science consultant\nconsumer and sensory scientist\nsensory scientist\nsensory research scientist\nbakery scientist\nsensory science advisor\nchocolate scientist','\n',char(10)),'Sensory scientists carry out sensory analysis in order to compose or improve flavours and fragrances for the food, beverage and cosmetics industry. They base their flavour and fragrance development on sensory and consumer research. Sensory scientists carry out research and analyse statistical data to meet customers expectations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/862ed9e5-96ec-410d-a0fd-5206a05a7954','key_16802','','','8122','8122.5','deburring machine operator',replace('deburring and tooling machine technician\nburr bench operator\nburring wheel operative\ntooling machine technician\ndeburr machine operative\nburr tooling operator\nburr bench hand\ndeburring machine operator\ndeburring and tooling machine operator\ntooling machine operator\nburring wheel technician\nburring wheel operator\nburring bench operator\ndeburrer','\n',char(10)),'Deburring machine operators set up and tend mechanical deburring machines designed to strip metal workpieces of their rough edges, or burrs, by hammering over their surfaces in order to smoothen them or to roll over their edges in case of uneven slits or sheers in order to flatten them into the surface.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/865adac9-9bb7-4680-a13d-62b2942974ac','key_16803','','','8172','8172.5','sawmill operator',replace('cross cut operator\nsawmill operative\nsawmill tender\nsawing machine operator\nsawmill operator\nsawmill supervisor\nsawmill machine operator\nsawmill worker','\n',char(10)),'Sawmill operators work with automated lumber mill equipment which saws timber into rough lumber. They also handle various sawing machines which further process the lumber in various shapes and sizes. These processes are nowadays often computer controlled.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/866c7813-2c03-47d7-9bdc-192cfbace57c','key_16804','','','2512','2512.5','user interface developer',replace('user interface developers\nUI developers\nfront end developer\nuser interface engineer\nUI programmer\nUI developer\nuser interface developer','\n',char(10)),'User interface developers implement, code, document and maintain the interface of a software system by using front-end development technologies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/869e3a85-6e4c-4e3c-b14f-a8399c1abd2d','key_16805','','','3139','3139.1','automated assembly line operator',replace('production line maintenance worker\nautomated assembly line team member\nautomated assembly line operative\nproduction line worker\nproduction line maintenance operative\nautomated assembly line worker\nproduction line operative\nautomated assembly line operator','\n',char(10)),'Automated assembly line operators operate, maintain and clean production machines. They are responsible for the assembly of a whole product or a part of a product. Automated assembly line operators perform all tasks in a production process via a rotation system.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/86a0c9ab-c3fa-41a3-ad3e-d0083036b3d6','key_16806','','','2114','2114.3','mineralogist',replace('environmental mineralogist\nmineralogy expert\noffshore mineralogist\nprocess mineralogist\nprocess mineralogy consultant\nmineralogist\napplied mineralogist\nmineralogy consultant','\n',char(10)),'Mineralogists study the composition, structure and other physical aspects of the earth. They analyse various minerals and use scientific equipment to determine their structure and properties. Their work mostly focuses on the classification and identification of minerals by taking samples and performing further tests, analysis and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/86c49d9c-b37d-4a14-8e9d-d0357005a48d','key_16807','','','8113','8113.6','roustabout',replace('roustabout\noil rig leasehand','\n',char(10)),'Roustabouts maintain and repair oil field equipment and machinery using hand and power tools. They do general labor, such as cleaning, digging trenches, scraping and painting rig components.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/86c892cf-09a3-44c8-b9d1-814d93dbcbb4','key_16808','','','0210','0210.4','warfare specialist',replace('warfare specialist\nelectronic warfare operator\nsurveillance operator\ndefence specialist\ndefence analyst\nelectronic warfare specialist','\n',char(10)),'Warfare specialist perform strategic duties, such as making tactical decisions, detecting and identifying potential threats, and locating targets and objectives. They communicate with their and other teams to ensure the efficiency of the operation, and also oversee the safety of the team.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/86ca306c-ab99-420a-9e2a-aa73c5c4de22','key_16809','','','2151','2151.1','electrical engineer',replace('electrical engineer\nelectrical engineering consultant\nadvanced electrical engineer\nelectrical technology engineering expert\nelectrical engineering practitioner\nelectric engineer\nelectrical equipment engineer\nelectrical technology engineering specialist\nelectrical technology engineering adviser\nelectrical technology engineer\npractitioner of electrical engineering\nengineer in electrical systems\nelectrical technology engineering consultant\nelectrical systems engineer\nengineer in electrical design\nelectrical design engineer\nelectrical engineering specialist\nelectrical engineering adviser\nelectricity engineer\nsenior electrical engineer\nelectrical engineering expert','\n',char(10)),'Electrical engineers design and develop electrical systems, electrical equipment, components, motors, and equipment with the feature of energy transmission. They engage in large scale projects such as the design and maintenance of power stations, and the distribution of power to smaller applications such as household appliances.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/86da9311-1f7a-40bb-af65-ed2a7178fcae','key_16810','','','8114','8114.3','concrete products machine operator',replace('concrete precast moulder\ncement and concrete plant worker\ncement plant worker\npre-cast concrete products machine operator\nconcrete pipe machine operator\nconcrete moulding machine operator\nconcrete products machine operator','\n',char(10)),'Concrete products machine operators tend machines used to produce moulded concrete products. They perform greasing, assembling and stripping of moulds. They also participate in the cement mixing process.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/86e8e172-234d-4e85-b9ae-6a4aecc2c321','key_16811','','','3423','3423.4','mountain guide',replace('outdoor leader\nmountain guide\nmountain leader\nexpedition guide\nski guide\nalpine guide\nrock climbing guide\nmountain holiday leader\nhiking guide\nski-touring guide\nclimbing guide\nmountain safety coach\noutdoor activities centre worker\nmountain walking guide\nexpedition leader','\n',char(10)),'Mountain guides assist visitors, interpret natural heritage and provide information and guidance to tourists on mountain expeditions. They support visitors with activities such as hiking, climbing and skiing in addition to ensuring their safety through monitoring both weather and health conditions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8712c91f-defb-4483-9e99-d9f896664a11','key_16812','','','5223','5223.7.14','domestic appliances specialised seller',replace('home and electrical appliances shop salesperson\nelectrical home  appliances specialised seller\ndomestic appliances superstore specialised seller\ndomestic appliances salesperson\nelectrical superstore specialised seller\nelectrical home appliances salesperson\nelectrical appliances specialised seller\nelectrical appliances salesperson\nelectrical household goods specialised seller\nelectrical household goods salesperson\nhome and electrical appliances shop specialised seller\ndomestic appliances superstore salesperson\ndomestic appliances specialized seller\nelectrical superstore salesperson','\n',char(10)),'Domestic appliances specialised sellers sell domestic appliances in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/87426cf2-9e36-46a4-aa4f-6a8d20dd93d6','key_16813','','','1324','1324.3.1.6.4','chemical products distribution manager',replace('chemical products logistics manager\nchemical products logistics planner\ntrainee chemical products distribution manager\nchemical products distribution planner\ngraduate chemical products distribution manager\nsenior chemical products distribution manager\nassistant chemical products distribution manager\nchemical products distribution manager\nchemical products supply chain manager\nchemical products supply chain planner','\n',char(10)),'Chemical products distribution managers plan the distribution of chemical products to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/874a2080-a9b6-46a9-8662-c9b7d4208f73','key_16814','','','3432','3432.5','set designer',replace('exibition designer\nscenic designer and artist\nscenic artist\ntheatre designer\nscene designer\nscenographer\ntheater designer\nscenery artist\nscenic artist & designer\nscenic designer\nset designer, builder and dresser\nscenic artist and designer\nset designer, builder & dresser\nscenic designer & artist\nset designer','\n',char(10)),'Set designers develop a set concept for a performance and supervise the execution of it. Their work is based on research and artistic vision. Their design is influenced by and influences other designs and must be conform with these designs and the overall artistic vision. Therefore, the designers work closely with artistic directors, operators and the artistic team. During rehearsals and performance they coach the operators to obtain optimal timing and manipulation. Set designers develop sketches, design drawings, models, plans or other documentation to support the workshop and performance crew. They may also design exhibition stands for fairs and other events. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/87580fa5-39d2-4b7e-9274-b744e684f17c','key_16815','','','3123','3123.1.3','carpenter supervisor',replace('joinery shop supervisor\njoiner supervisor\njoinery supervisor\ncarpentry foreman\ncarpentry overseer\ncarpenter supervisor\ncarpentry supervisor\ntimber workshop supervisor\nframer supervisor\nsupervisor of carpentry operations','\n',char(10)),'Carpenter supervisors monitor carpentry operations in construction. They assign tasks and take quick decisions to resolve problems. They pass their skills on to apprentice carpenters.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/87775d69-ae88-449e-ad71-6f9875711816','key_16816','','','3111','3111.1','chemistry technician',replace('production chemistry technician\nmanufacturing chemistry technician\nradiochemistry technician\nnuclear chemistry technician\nchemistry assistant\nchemical test technician\nchemistry laboratory technician\nchemistry technician\nchemistry laboratory assistant\nchemistry lab technician\nchemical analysis technician\nnuclear laboratory technician','\n',char(10)),'Chemistry technicians monitor chemical processes and conduct tests to analyse chemical substances for manufacturing or scientific purposes. They work in laboratories or production facilities where they assist chemists in their work. Chemistry technicians perform laboratory activities, test chemical substances, analyse data and report about their work.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/87afe3f0-c891-45de-a498-ad2608471c86','key_16817','','','9321','9321.1','clothing finisher',replace('clothing packer\nclothing finisher\nclothes finisher','\n',char(10)),'Clothing finishers set haberdasheries, e.g. bottoms, zips, and ribbons and cut threads. They weigh, pack, label materials and products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/87cb6ef5-fa94-4af4-9511-6fe64c463835','key_16818','','','8160','8160.25','dairy products manufacturing worker',replace('dairy plant employee\ndairy plant worker\ndairy products manufacturing worker\ndairy products worker\ndairy production worker','\n',char(10)),replace('Dairy products manufacturing workers set up, operate and tend equipment to process milk, cheese, ice cream and other dairy products. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/87d0795a-d41f-47ee-979f-0ab7d73836e7','key_16819','','','5321','5321.1.1','nurse assistant',replace('assistant nursing practitioner\nhealthcare nurse\nstate enrolled nurse\nnurse auxiliary\nnurse assistant\nnursing assistant\nnursing auxiliary\nSEN\nassistant nurse\nauxiliary nurse','\n',char(10)),'Nurse assistants provide basic patient care under direction of nursing staff. They perform duties such as feed, bathe, dress, groom, move patients or change linens and may transfer or transport patients.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/87e40e79-16fb-4391-9dcb-c15f40e026c1','key_16820','','','1431','1431.2.2','betting manager',replace('betting manager\nbingo manager\nbetting shop coordinator\ncasino manager','\n',char(10)),'Betting managers organise and coordinate the activities of a betting shop. They oversee daily operations and facilitate communication between staff and customers. They carry out cashier duties, train staff and strive to improve the profitability of their business. They take responsibility for all betting activities and ensure that relevant betting rules and regulations are followed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/87e644b2-8bc1-42d4-bb14-cc4abb0bc715','key_16821','','','3435','3435.25.2','light board operator',replace('lighting board programmer & operator\nlighting operator\nsound & lighting board operator\nlight board controller\nLX operator\nlighting board controller\nboard operator\nsound and lighting board operator\nlighting board programmer and operator\nLX board operator\nlight board operator\nlighting board operator and programmer','\n',char(10)),replace('Light board operators control the lighting of a performance based on the artistic or creative concept, in interaction with the performers. Their work is influenced by and influences the results of other operators. Therefore, the operators work closely together with the designers, operators and performers. Light board operators prepare and supervise the setup, steer the technical crew, program the equipment and operate the lighting system. They may be responsible for conventional or automated lighting fixtures and, in some instances, controlling video as well. Their work is based on plans, instructions and other documentation. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/87fd9791-0922-4da0-a984-f518aadf85ec','key_16822','','','1420','1420.4.1','ammunition shop manager',replace('gun shop manager\nfire weapon shop manager\nammunition shop manager\nweapon shop manager\nhunting equipment shop manager\nfirearms store manager\nfirearm shop manager\nammunition store manager\nfire weapon store manager\nweapon store manager\nammunition storekeeper\nhunting equipment store manager','\n',char(10)),'Ammunition shop managers are responsible for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8801ef16-92a4-4889-8f80-9b78206c7ec0','key_16823','','','2619','2619.6','human rights officer',replace('human rights programme officer\nhuman rights lawyer\nhuman rights program officer\nhuman rights officer\nhuman rights project officer','\n',char(10)),'Human rights officers investigate and handle human rights violations, as well as develop plans to reduce violations and to ensure compliance to human rights legislation. They investigate complaints by examining information and interviewing victims and perpetrators, and communicate with organisations involved with human rights activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/880a6cd1-2356-4994-8983-ac364763beaf','key_16824','','','8322','8322.4','parking valet',replace('parking valet\ncar parker','\n',char(10)),replace('Parking valets provide assistance to clients by moving their vehicles to a specific parking location. They may also help with handling clients'' luggage and provide information on parking rates. Parking valets maintain a friendly attitude towards their clients and follow company policies and procedures.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/884beda8-d242-4fc3-a097-dc11098a7c3d','key_16825','','','8154','8154.3','textile dyer',replace('beam dye operator\ntextile color formulator\npackage dyer\ndye colorist\ntextile dye operator\ntextile colourist dyer\ndyeing machine tender\ntextile dye colourist\ncloth dyer\nfabric dyer\nbeam dyer\ntextile dye colorist\ntextile dyer\ntextile colorist dyer','\n',char(10)),'Textile dyers tend dye machines making sure that the setting of machines are in place. They prepare chemicals, dyes, dye baths and solutions according to formulas. They make samples by dyeing textiles and calculating the necessary formulas and dyes upon all kind of yarn and textiles.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/88589d68-b870-440e-8444-32e9935cbbbc','key_16826','','','7233','7233.10','marine mechanic',replace('mechanic on a ship\nship machinist\ncargo ship mechanic\ncontainer ship mechanic\nmerchant ship mechanic\npassenger ship mechanic\nmarine mechanic\ncruise ship mechanic','\n',char(10)),'Marine mechanics are in charge of the engines and mechanical parts of the vessel and replace defective equipment and parts. They communicate with other crew members on the operational level. Marine mechanics maintain and repair the engines and other machinery of vessels such as boilers, generators and electrical equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8862c29b-3810-4fbc-85c7-c9ee87535f73','key_16827','','','3123','3123.1.11','electrical supervisor',replace('electrical service supervisor\nforeman electrician\nelectrical maintenance manager\nelectrical supervisor\nelectrical maintenance supervisor\nelectrical systems supervisor\nelectrician supervisor\nelectrical installation supervisor','\n',char(10)),'Electrical supervisors monitor the operations involved in installing and servicing electricity cables and other electrical infrastructure. They assign tasks and take quick decisions to resolve problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8870032b-9be3-4fd4-b9fb-1d8d9de33c5f','key_16828','','','8171','8171.9','wash deinking operator',replace('wash deinking specialist\nwash deinking operator\ndeinking specialist\ndeinking operator\ndeinking technician\ndeinking operative\nwash deinking technician\nwash deinking operative\nwash deinking worker\ndeinking worker','\n',char(10)),'Wash deinking operators operate a tank where recycled paper is mixed with water and dispersants to wash out printing inks. The solution, called a pulp slurry, is then dewatered to flush out the dissolved inks.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/88721b6c-d2ce-41c6-8878-73c0d3aacf39','key_16829','','','5411','5411.1','firefighter',replace('operational firefighter\nfirefighter\nsenior firefighter\nairfield firefighter\nfireman\nfirewoman\nfire brigade officer','\n',char(10)),'Firefighters are responsible for emergency response in case of a fire outbreak or other hazardous situations. They oversee the evacuation of the endangered premises and ensure victims are rescued and taken care of by the appropriate professionals. Once the premises are clear of victims, they ensure the fire is extinguished, compliant with health and safety regulations. They also regulate the clean-up of the scene and assess the damage.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/88760821-9df4-47a6-a1c2-ac08b9759396','key_16830','','','3432','3432.3','miniature set designer',replace('miniature set constructor\nminiature set designer\nmodel designer and maker\nminiature model set designer\nminiature model set designer and maker\nminiature maker\nminiature model maker\nmodel designer and builder\nmodel set designer and builder\nminiatures builder\nminiature designer and maker\nmodel set designer\nminiature set designer and builder\nminiature set builder\nminiature model set designer and builder','\n',char(10)),'Miniature set designers design and build miniature props and sets of motion pictures. They build models used for visual effects that meet the look and requirements of the production. Miniature set designers cut material using hand tools to construct three-dimensional props and sets.','','Includes people producing props for broadcasting programs.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/88990bdf-4f6b-4411-82c9-9eecad1db8fb','key_16831','','','2310','2310.1.27','music instructor',replace('music school instructor\nmusic school teacher\nmusic conservatory instructor\nmusic conservatory teacher\nmusic conservatory lecturer\nmusic instructor\nmusic school lecturer\nmusic conservatory professor\nconservatory music instructor\ninstructor of music in higher education\nspecialist music educator\nmusic conservatory trainer\nhigher education music instructor\nspecialist practitioner in music instruction\nconservatory music teacher\nspecialist instructor of music','\n',char(10)),'Music instructors educate students in specific theory and, primarily, practice-based music courses at a specialised music school or conservatory at a higher education level, including musical instruments and vocal training. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master in music. Music educators monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance of music practice through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/88a34087-311e-497e-ad81-a6843b9b461e','key_16832','','','8211','8211.7','vessel engine assembler',replace('marine engine assembler\nboat engine assembler\nmarine engine builder\nelectric motor assembler\ngas turbine engine assembler\nboat engine builder\nnuclear reactor assembler\nvessel engine assembler\ndual fuel engine assembler\nboat engine assembler/tester\nmarine steam engine assembler\nfour-stroke diesel engine assembler\noutboard motor assembler\ntwo-stroke diesel engineer assembler\nmarine engine assembler/tester\nLNG engine assembler\nliquid natural gas engine assembler','\n',char(10)),'Vessel engine assemblers build and install prefabricated parts to form engines used for all types of vessels such as electric motors, nuclear reactors, gas turbine engines, outboard motors, two-stroke or four-stroke diesel engines and, in some cases, marine steam engines. They review specifications and technical drawings to determine materials and assembly instructions. They inspect and test the engines and reject malfunctioning components.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/88b21b2f-350c-4f41-9680-a53f8ea708ed','key_16833','','','8142','8142.6','filament winding operator',replace('filament winder operator\nfiberglass tank winding operator\nfiberglass pipe winding operator\nfiberglass container winding operator\nfibreglass pipe winding operator\nfilament winding machine operative\nGRP pipe winding operator\nfilament winding operator\nfilament winder operative\nwinding machine operative\nfilament winding machine tender\nfibreglass container winding operator\nfilament winding machine operator\nfibreglass tank winding operator\nFRP winding machine operator','\n',char(10)),'Filament winding operators tend, control and maintain machines that coat filament, usually fibreglass or carbon, in resin and wind them around a rotation mould to produce pipes, containers, tubes and other hollow cylindrical products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/88c55070-444a-45db-abe3-7b95c40bc9ec','key_16834','','','3423','3423.2.1','leisure attendant',replace('club attendant\nleisure club assistant\nweight room attendant\nsports attendant\nfitness attendant\nsports assistant\nwellness & fitness attendant\nfitness assistant\nleisure centre assistant\npool attendant\nleisure attendant\nfitness club attendant\nsports club attendant\nwellness attendant','\n',char(10)),'Leisure attendants promote health and fitness participation for new and existing members. They provide a clean, safe and friendly environment that promotes regular member attendance and satisfaction. They are source of information and encouragement for all members and actively assist fitness instructors and other workers wherever possible.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/88c56310-14bc-4956-8450-ce0e178ae847','key_16835','','','8181','8181.3.1','clay products dry kiln operator',replace('drying tunnel kiln operative\ndryer kiln operator\nclay kiln operator\nclay dryer kiln operator\nclay roaster\ntunnel drier operator\ndrying tunnel kiln operator\nclay drying tunnel operator\nkiln tender\ndryer operator\nclay products dry kiln operator\nclay dryer kiln operative\nkiln worker\ndrying tunnel operator\ndryer kiln operative','\n',char(10)),'Clay products dry kiln operators manage drying tunnels that are meant for drying clay products prior to their treatment in kiln.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8908d5aa-ac58-465e-9b75-7e02ed4af3a9','key_16836','','','4323','4323.20','water traffic coordinator',replace('water traffic planner\ndock traffic coordinator\nmarine traffic planner\nport traffic coordinator\nwater traffic coordinator\nmarine traffic co-ordinator\ncoordinator in water traffic\nwaterway traffic planner','\n',char(10)),'Water traffic coordinators manage the movements of a vessel in ports and waterways. They ensure the efficient deployment of vessels of a shipping company.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8914badc-df5e-4671-908e-de5bc795658b','key_16837','','','1324','1324.3.2.11','import export manager in electronic and telecommunications equipment and parts',replace('graduate import export manager in electronic and telecommunications equipment and parts\ntrainee export manager in electronic and telecommunications equipment and parts\ninternational trade compliance manager in electronic and telecommunications equipment and parts\ntrainee import export manager in electronic and telecommunications equipment and parts\nsenior import manager in electronic and telecommunications equipment and parts\ngraduate export manager in electronic and telecommunications equipment and parts\ngraduate import manager in electronic and telecommunications equipment and parts\nassistant export manager in electronic and telecommunications equipment and parts\ntrainee import manager in electronic and telecommunications equipment and parts\ninternational trade manager in electronic and telecommunications equipment and parts\nimport export manager in electronic and telecommunications equipment and parts\nassistant import export manager in electronic and telecommunications equipment and parts\ncustoms compliance manager in electronic and telecommunications equipment and parts\nassistant import manager in electronic and telecommunications equipment and parts\nsenior export manager in electronic and telecommunications equipment and parts\nsenior import export manager in electronic and telecommunications equipment and parts','\n',char(10)),'Import export managers in electronic and telecommunications equipment and parts install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8918dda6-d99b-4572-a8ba-2a786bf5e4e9','key_16838','','','7533','7533.1','doll maker',replace('dolls clothes maker\nporcelain doll maker\ndoll maker\ndolls house furniture maker\nplastic dollmaker\ndolls fashion designer\nantique doll repairer\nwooden dollmaker\ndollmaker\ndolls house maker\nwooden doll maker\ndoll repairer\ndolls accessories maker\nporcelain dollmaker\nplastic doll maker','\n',char(10)),'Doll makers design, create and repair dolls using various materials such as porcelain, wood or plastic. They build moulds of forms and attach parts using adhesives and handtools.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/891af43e-0946-4fa9-8115-a2b9be849cb0','key_16839','','','3331','3331.2.1.34','import export specialist in watches and jewellery',replace('assistant import export specialist in watches and jewellery\ntrainee import specialist in watches and jewellery\ncustoms compliance specialist in watches and jewellery\nsenior export specialist in watches and jewellery\ngraduate import specialist in watches and jewellery\ntrainee import export specialist in watches and jewellery\ntrainee export specialist in watches and jewellery\nassistant import specialist in watches and jewellery\nsenior import export specialist in watches and jewellery\nimport export specialist in watches and jewelry\ngraduate import export specialist in watches and jewellery\nassistant export specialist in watches and jewellery\ngraduate export specialist in watches and jewellery\ninternational trade compliance specialist in watches and jewellery\nsenior import specialist in watches and jewellery','\n',char(10)),'Import export specialists in watches and jewellery have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/89330b57-6a30-40e1-b623-50f47a4b0c34','key_16840','','','2411','2411.1.10','grants management officer',replace('grants management officer\nproposals manager\ngrants officer','\n',char(10)),replace('Grants management officers work professionally in the administration and management of grant funds. They look at grant applications from individuals, charities, community groups or university research departments and decide whether to award funding given out by charitable trusts, government or public bodies or not. However, sometimes they may refer the grant application to a senior officer or committee. \n ','\n',char(10)),'','Excludes <a resource="http://data.europa.eu/esco/occupation/bb4ca5c1-879d-44e8-840d-24f320206bff" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/bb4ca5c1-879d-44e8-840d-24f320206bff"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">grant administrators</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/89860c2a-d217-432a-8636-84aa17f09c15','key_16841','','','2145','2145.1.6','gas production engineer',replace('gas production engineering expert\ngas production engineering specialist\ngas production technology engineering consultant\ngas production technology engineering adviser\ngas production technology engineer\ngas production engineering consultant\ngas production engineering adviser\npetroleum production engineer\ngas production engineer\ngas production technology engineering specialist\ngas production engineers\ngas production technology engineering expert','\n',char(10)),'Gas production engineers develop methods to optimise the extraction and production of gas for energy and utilities. They design systems for gas production, supervise production operations and develop improvements on existing systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/898f99f7-8c0a-4212-9e58-9ab00379e5ae','key_16842','','','7223','7223.7','fitter and turner',replace('tool turner\ntool engineering fitter\nmachine tool turner\nassembly line turner\nassembly line fitter\ntool and die fitter\ntool engineering turner\nmachine tool fitter\nelectrical turner\ntool and die turner\ntool fitter\nengineering turner\nfitter and turner','\n',char(10)),'Fitters and turners use machine tools to create and modify metal parts according to set specifications in order to fit components for machinery. They ensure the finished components are ready for assembly.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/89a0af2a-edf4-4d1a-8526-f4a8f476cea2','key_16843','','','3435','3435.11','location manager',replace('location manager\nfilm location coordinator\nfilm and TV location manager\nlocation director\nfilm location manager\nTV location manager\nlocation coordinator\nlocation scout\nseries location manager\nset location manager\nfilm & TV location manager\nlocation assistant','\n',char(10)),'Location managers are responsible for procuring locations for filming outside of the studio and all the logistics this includes. They negotiate site use, and manage and maintain the site during shooting. Location managers manage the safety and security of the film crew on site.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/89a3b3af-7264-4e00-9a14-21e5fbd4ee87','key_16844','','','2330','2330.1.7','geography teacher secondary school',replace('secondary school teacher of geography\ngeographical studies teacher in secondary school\ngeography teacher high school\nsecondary school geography tutor\ngeography tutor secondary school\nteacher of geography in secondary schools\nhigh school geography teacher\ngeographical studies high school teacher\ngeography teacher','\n',char(10)),'Geography teachers at secondary schools provide education toÂ students, commonly children and young adults, in aÂ secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, geography. They prepare lesson plans and materials, monitor the studentsâ progress, assist individually when necessary, and evaluate the studentsâ knowledge and performance on the subject of geographyÂ through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/89a6d6e5-8362-4b99-b745-1c68bffbda77','key_16845','','','7126','7126.9','septic tank servicer',replace('septic tank servicer\nseptic tank cleaner\nseptic tank emptying machine operator\nseptic tank emptying machine worker\nseptic tank emptier\nseptic tank service operator\nseptic tank repairer\nseptic tank emptying machine operative\nseptic tank service worker','\n',char(10)),'Septic tank servicers clean and maintain septic systems. They repair damages and faults, and ensure the tanks are cleaned and maintained, operating cleaning and maintenance machinery, in compliance with safety procedures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/89b85c45-ddc7-4fe1-be5a-d3db2be11f3b','key_16846','','','2434','2434.1','ICT account manager',replace('IT account manager\nICT account manager\nICT key account manager\nIT key account manager\nICT account managers','\n',char(10)),'ICT account managers build business relationships with customers to facilitate the sale of hardware, software, telecommunications or ICT services. They also identify opportunities and manage sourcing and delivery of products to customers. They achieve sales targets and maintain profitability.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/89b87aa2-4008-4cc7-96f8-e2b5ffe59f28','key_16847','','','2133','2133.5','ecologist',replace('ecologist\nenvironmental scientist\necologists\nconservationist ecologist\nwildlife ecologist\nconservationist scientist\naquaculture ecologist','\n',char(10)),'Ecologists carry out assessments of the health and distribution of organisms, namely people, plants, and animals, and the relationship between organisms and their environment. Ecologists usually have a specialisation area, e.g. freshwater, marine, terrestrial, fauna, and flora about which they conduct research and perform related tasks. ','','Work includes the analysis and testing of living organisms, the development and application of processes resulting from research in areas such as natural resource management, environmental protection and microbiology.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8a007cd3-ba40-4c55-b238-587bd59e74b2','key_16848','','','7313','7313.1.2','goldsmith',replace('goldsmiths\ngold jewellery fabricator\ngold ring fabricator\ngold jewel smith\ngold ring maker\ngold earrings maker\ngold jewellery maker\ngoldsmith\nsilver smith\nboutique goldsmith\ngold earrings fabricator','\n',char(10)),replace('Goldsmiths design, manufacture and sell jewellery. They also adjust, repair and appraise gems and jewellery for customers using experience in the working of gold and other precious metals.  \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8a0c1556-f616-4d41-a03c-8a093a644807','key_16849','','','3119','3119.16.2','footwear quality technician',replace('footwear quality engineer\nfootwear quality control technician\nfootwear quality analyst\nfootwear quality technician','\n',char(10)),'Footwear quality technicians manage all the standards and techniques related to processes and products. They perform tasks using quality systems based on national and international standards. They analyse and interpret the results, prepare reports, advise on corrective measures, contribute to the achievement of requirements and objectives for the benefit of continuous improvement and customer satisfaction.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8a3498c7-470a-4067-b48e-f330d9b702e8','key_16850','','','3311','3311.2.4.1','securities underwriter',replace('security underwriter\nsecurities administrator\nsecurities underwriter\ndistribution administrator','\n',char(10)),'Securities underwriters administer the distribution activities of new securities from a business company. They work in close connection with the issuing body of the securities in order to establish the price and buys and sells them to other investors. They receive underwriting fees from their issuing clients.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8a42240b-2b5c-4f6f-8787-f0547fbc46c5','key_16851','','','3153','3153.2.5','private pilot',replace('light aircraft pilot\npilot of light aircraft\nlight aircraft charter pilot\nlight air craft charter pilot\nprivate airplane pilot\nprivate aircraft captain\npilot of light air-craft\npilot of private aeroplanes\nnon commercial flights pilot\npilot of private aircraft\nair-taxi pilot\ncharter pilot\nprivate aeroplane captain\nprivate pilot\nprivate aeroplane pilot\ncorporate pilot\nair taxi pilot\nprivate airplane captain','\n',char(10)),'Private pilots operate non-commercial airplanes for leisure with a limited amount of seats and engine horsepower. They also provide private transport for people.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8a451ae2-3c31-4f35-90af-6275b1b02f93','key_16852','','','2654','2654.1.6','stage director',replace('theatre director\nscene director\nstage director\ncircus director\nstage supervisor\ntheater director','\n',char(10)),'Stage directors oversee and orchestrate the mounting of a performance production by unifying various endeavours and aspects of a theatrical production. They ensure the quality and completeness of the theatrical production and lead the members of the creative team into realising their artistic vision for it. ','','Includes people working in a play, an opera, a musical, a circus show or a devised piece of work.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8a53f8d3-d995-4c7b-a70d-d79f76bdcb3f','key_16853','','','2265','2265.1','dietitian',replace('public health nutritionist\nspecialist dietician\ndietician\ndietitian','\n',char(10)),'Dietitians assess specific nutritional requirements of populations or individuals throughout their lives and translate this into advice which will maintain, reduce risk to, or restore people’s health. Using evidence-based approaches, dietitians work to empower individuals, families and groups to provide or select food which is nutritionally adequate, safe, tasty and sustainable. Beyond healthcare, dietitians improve the nutritional environment for all through governments, industry, academia and research.','','Includes people performing education activities to develop healthy and balanced food habits in people''s daily lives.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8a87e8b3-aecf-48a7-9837-4e022c3b9bb9','key_16854','','','3111','3111.1.1','chromatographer',replace('high performance liquid chromatographer\nthin layer chromatographer\nchromatography machine operative\nchromatography machine operator\nmass spectrometry specialist\nion-exchange chromatographer\nchromatographer\nchromatography technician\ngas chromatographer\nmass spectrometry technician\nLC-MS specialist\nliquid chromatographer\nchromatography machine technician\ngel permeation chromatographer','\n',char(10)),'Chromatographers apply the corresponding chromatography techniques (such as gas, liquid or ion exchange techniques) to identify and analyse samples'' chemical compounds. They calibrate and maintain the chromatography machinery and prepare the equipment and solutions. Chromatographers may also develop and apply new chromatography methods according to samples and chemical compounds that need to be analysed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8a925aca-f636-437c-8962-5deae170e246','key_16855','','','2141','2141.2','food and beverage packaging technologist',replace('food and beverage packaging technologist\nfood and beverage packaging expert\nfood and beverage packaging specialist\nfood and drinks packaging technologist\nfood and drinks packaging expert','\n',char(10)),'Food and beverage packaging technologists assess appropiate packaging for various food products. They manage matters in relation to packaging while ensuring customer specifications and company targets. They develop packaging projects as required.','','Excludes people performing commercial tasks.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8ae1e637-840b-4be0-a5df-8653375d8949','key_16856','','','3112','3112.1.9','landfill supervisor',replace('solid waste collection supervisor\nsolid waste disposal supervisor\nwaste disposal compliance supervisor\nwaste disposal site compliance supervisor\nlandfill supervisor\nwaste treatment equipment supervisor\nsolid waste processing supervisor\nsolid waste remediation supervisor\nlandfill site supervisor\nwaste sortation supervisor\nsolid waste treatment supervisor','\n',char(10)),'Landfill supervisors coordinate the activities and operations of landfills and landfill staff. They research legislation concerning waste management and ensure the operations of the landfill are compliant, and direct waste disposal operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8afab6c4-6145-4ea1-a0ae-d2c3438bda7e','key_16857','','','7214','7214.2','shipwright',replace('ship repair engineer\nboatbuilder\nshipbuilder\nboat construction worker\nboat repair engineer\nboat fitter\nboatfitter\nboat repairer\nship repairer\nship construction worker\nshipfitter\nboat builder\nship fitter\nsubmarine shipwright\nship builder\nshipwright\nnaval vessel shipwright\nnaval engineer/shipwright','\n',char(10)),'Shipwrights build and repair small type of water vessels from pleasure craft to naval vessels. They prepare preliminary sketches and create templates. They use hand and power tools to construct smaller boat themselves or supervise a team of shipbuilders. They also construct cradles and slipways for the ship’s construction, transportation, launching and slipping. Depending on the vessels, they might work with different materials such as metal, wood, fibreglass, aluminium etc.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8b128a08-606f-4bb1-806e-07e0ed98b40e','key_16858','','','2310','2310.1.39','space science lecturer',replace('higher education space science teacher\nprofessor of astrophysics\nuniversity space science teacher\nspace science instructor\nprofessor of planetary science\nspace sciences lector\nspace science lector\nlecturer in space science\nspace science docent\nlector in space science\nlecturer in space sciences\nspace science lecturer\nuniversity lecturer in space science\nspace science professor\nprofessor of astronomy\nlector in space sciences\nsenior lecturer in space science\nspace science teacher\nuniversity space science lecturer','\n',char(10)),'Space science lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, space science, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, grading papers and leading review and feedback sessions for the students. They also conduct academic research in their field of space science, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8b26eec6-7497-4218-8364-9b5983b04ae7','key_16859','','','1219','1219.3','defence administration officer',replace('defence administration manager\nadministration manager of defence\nadministration officer of defence\ncivil servant\ndefense administration officer','\n',char(10)),'Defence administration officers perform managerial duties and administrative tasks in defense institutions, such as maintenance of records, management of staff, and handling of accounts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8b273f5e-bdc5-4b73-80bd-3e324a20309b','key_16860','','','8131','8131.2.3','paint mixer',replace('paint production assistant\npaint maker\npaint mixer operative\npaint mixer\npaint mixer worker\nmaker of paint\npaint production worker\npaint mixer operator','\n',char(10)),'Paint mixers operate and maintain machines that mix lacquers, solvents and paints in order to produce paint, making sure the end product is according to the specified formula.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8b2d562c-8407-49cd-9843-636c352b8c82','key_16861','','','7316','7316.3','metal engraver',replace('precious metal engraver\nengraving drill operative\nengraving mill operative\nengraving mill operator\nhand metal engraver\nengraving drill operator\nmetal hand engraver\nengraving set up technician\nengraving drilling operative\nengraving set-up operative\nengraving set up operative\nengraving set-up operator\nengraving drill technician\nengraving set-up technician\nengraving set up operator\nengraving mill technician\nmetal engraver','\n',char(10)),replace('Metal engravers make incisions of a design onto a metal surface by carving grooves into it, usually for decorative purposes, including metal weaponry. To cut the design into the surface they use tools such as gravers or burins. \n \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8b304664-ba25-45fe-a5bf-80c727943a86','key_16862','','','6112','6112.6','vineyard manager',replace('vineyard manager\nmanager of a vineyard\nmanager of a winery\nwinery manager','\n',char(10)),replace(' Vineyard managers orchestrate the conduct of the vineyard and the winery, in some cases also the  administration and marketing. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8b490c1a-2f8c-4693-bcfa-ecfc88838a8e','key_16863','','','3115','3115.1.12','motor vehicle engine inspector','motor vehicle engine inspector','Motor vehicle engine inspectors inspect diesel, gas, petrol and electric engines used for cars, buses, trucks etc. in assembly facilities such as factories and mechanic shops to ensure compliance with safety standards and regulations. They conduct routine, post-overhaul, pre-availability and post-casualty inspections. They provide documentation for repair activities and technical support to maintenance and repair centres. They review administrative records, analyse the operating performance of engines and report their findings.','','Excludes <a resource="http://data.europa.eu/esco/occupation/b50ae5f6-3f67-4cc7-92bb-6cec7214bfc4" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/b50ae5f6-3f67-4cc7-92bb-6cec7214bfc4"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">motor vehicle engine tester</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8b6388a4-4904-471b-9331-d3b1211f5525','key_16864','','','1330','1330.7','ICT project manager',replace('ICT project managers\nweb project manager\nIT project manager\nICT project manager\nIT projects manager\nweb projects manager','\n',char(10)),'ICT project managers schedule, control and direct the resources, people, funding and facilities to achieve the objectives of ICT projects. They establish budgets and timelines, perform risk analysis and quality management, and complete project closure reports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8b828b9f-e41a-48d1-9d35-2c4378279a79','key_16865','','','3119','3119.13','pipeline compliance coordinator',replace('roadside pipeline coordinator\npipeline compliance inspector\npipeline compliance coordinator\npipeline safety inspector\npipeline inspector\npipeline compliance and safety inspector\npipeline compliance and safety coordinator\npipeline safety coordinator','\n',char(10)),'Pipeline compliance coordinators track, compile and summarise all the compliance and conformance activities in pipeline infrastructures and fields. They ensure that works are carried out within regulatory frameworks. They strive to develop and implement compliance policies and recommend ways to minimize risk. They inspect sites, collect evidence, and report compliance needs to the management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8b8ae0a5-342c-43ae-b856-e1fea6fd6d1c','key_16866','','','7412','7412.10','power tool repair technician',replace('power tool equipment inspector\npower tool repair technician\npower compressor repairman\nmachine repairer\npower tool repairman\npower tool equipment repairer\npower compressor mechanic\npower tool craftsman\npower tool equipment operative\npower compression repairer\ntool repair technician\npower compressor inspector\nmachine tool repair technician\npower tool maintainer\npower tool equipment operator\npower tool maintenance operative\ntool repair inspector\npower tool mechanic\npower tool maintenance worker\nhand tool repair technician','\n',char(10)),'Power tool repair technicians identify defects and repair electrically powered tools such as drills, chainsaws, lawn mowers and grinding machines. They disassemble, test and replace defective parts in tools. Power tools repair technicians advise customers on maintenance products and methods for their equipment. They may sell such products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8b97d85f-4a7a-4908-aa72-9c947c0cb0cd','key_16867','','','7318','7318.1','carpet handicraft worker',replace('traditional carpet worker\ncarpet maker\nhandicraft worker, carpets\ncarpet weaver\ntraditional carpet maker\nhand carpet maker\nhandemade carpet worker\ncarpet worker\ncarpet handicraft worker','\n',char(10)),'Carpet handicraft workers use handicraft techniques to create textile floor coverings. They create carpets and rugs from wool or other textiles using traditional crafting techniques. They can use diverse methods such as weaving, knotting or tufting to create carpets of different styles.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8b98dbc3-11f7-4720-86de-dcecee368be1','key_16868','','','8142','8142.1','blow moulding machine operator',replace('plastic process machine operator\nplastics production machine operative\nplastics process machine operative\nblow moulding technician\nplastic blow molding machine operator\nplastic blow moulding machine operator\nblow moulding machine operative\nplastic blow moulding machine operative\nblow molding technician\nblow molding machine operator\nplastic blow molding machine operative\nplastics production machine operator\nblow molding machine operative','\n',char(10)),'Blow moulding machine operators operate and monitor blow moulding machine to mould plastic goods, according to requirements. They regulate temperature, air pressure and volume of plastic, according to specifications. Blow moulding machine operators remove finished products and cut away excess material, using a knife. They regrind surplus material and rejected workpieces for reuse, using a grinding machine.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8ba8acb8-c0f2-4679-ae15-3a4f6e876c6f','key_16869','','','8142','8142.11','plastic rolling machine operator',replace('plastic roll-forming machine tender\nplastic rolling machine tender\nplastic roll-forming machine setter\nplastic rolling machine worker\nplastic rolling machine operator\nplastic rolling machine operative\nplastic rolling machine setter/operator\nplastic processing machine operator\nplastic roll-forming machine operative\nplastic roll-forming machine operator\nplastic rolling machine setter\nplastics processing machine operative','\n',char(10)),'Plastic rolling machine operators operate and monitor machines to produce plastic rolls, or to flatten and reduce the material. They examine raw materials and finished products to make sure they are according to specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8bbac392-3d62-4c46-a56e-3f244fdda216','key_16870','','','1420','1420.4.33','orthopaedic supply shop manager',replace('orthopaedic articles store manager\northohouse manager\nOrthopedic supply shop manager\northopedic supply shop manager\northopaedic articles shop manager\nsurgical supplies store manager\nsurgical suppliers shop manager\northopaedic supplies store manager','\n',char(10)),'Orthopaedic supply shop managers assume responsibility for activities and staff in specialised shops.They manage employees, monitor the sales of the store, manage budgets and order supplies when a product is out of supply and perform administrative duties if required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8bc48c43-d976-458a-9e3c-ee784572351d','key_16871','','','2144','2144.1.9','industrial tool design engineer',replace('industrial tool engineering specialist\nindustrial tool design quality control supervisor\nindustrial tool design developer\ntool design engineer\nindustrial tool technology engineering specialist\nindustrial tool design producer\nindustrial tool production supervisor\nindustrial tool developer\nindustrial tool technology engineering adviser\nindustrial tool designer\nindustrial tool production designer\nindustrial tool production specialist engineer\nindustrial tool technology engineering expert\nindustrial tool development engineer\nindustrial tool technology engineering consultant\nindustrial tool test engineer\nindustrial tool engineering expert\nindustrial tool graphic designer\nindustrial tool engineer\nindustrial tool design engineer\nindustrial tool engineering consultant\nindustrial tool technology engineer\nindustrial tool engineering adviser','\n',char(10)),'Industrial tool design engineers design various industrial tools in accordance with customer needs, manufacturing requirements, and building specifications. They test the designs, look for solutions to any problems, and oversee production.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8bc845cb-0702-47e4-aabc-4036032b0a8d','key_16872','','','3331','3331.2.2','shipping agent',replace('ship representative\ncargo agent\nshipping representative\nwater clerk\nship local representative\nport agent\nshipping proxy\nowners charterers agent\nliner agent\nships agent\nboarding clerk\nshipping agent','\n',char(10)),'Shipping agents represent the ship owner in a foreign port. They make sure customs is cleared in a timely manner so that the cargo doesn''t have to stay too long in the port. Shipping agents also ensure that insurance, licences and other formalities are in order.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8bd74371-1818-47d5-a253-d703c51c5f26','key_16873','','','2642','2642.1.5','copy editor',replace('language editor\neditorial assistant\ncopy-editor\nsenior copy editor\ncopy editor','\n',char(10)),'Copy editors ascertain that a text is agreeable to read. They ensure that a text adheres to the conventions of grammar and spelling. Copy editors read and revise materials for books, journals, magazines and other media.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8befd482-70e2-4d1e-8a09-091e15fe85c3','key_16874','','','2342','2342.2','Freinet school teacher',replace('Freinet school teaching practitioner\nFreinet pedagogy teacher\nteacher in Freinet primary school\nMouvement de l''École Moderne teacher\nteacher in Freinet school\nFreinet primary school teacher\nFreinet school educator\nFreinet school teacher\nFreinet class school teacher\nModern School Movement teacher\nModern School Movement methodology teacher\nFreinet teacher\nFreinet school instructor','\n',char(10)),'Freinet school teachers educate students using approaches that reflect the Freinet philosophy and principles. They focus on enquiry-based, democracy-implementing and cooperative learning methods. They adhere to a specific curriculum that incorporates these learning methods through which students use trial and error practices in order to develop their own interests in a democratic, self-government context. Freinet school teachers also encourage students to practically create products and provide services in and outside of class, usually handcrafted or personally initiated, implementing the ''pedagogy of work'' theory. They manage and evaluate all the students separately according to the Freinet school philosophy.','','Includes people working in institutions at other education levels.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8bf76ba6-e661-4574-9887-4f2cb943835a','key_16875','','','2422','2422.13','political affairs officer',replace('prime minister adviser\nlobbyist\npolitical aide\nadvocacy and political affairs officer\ncampaigner\npolitical campaign officer\npresident adviser\npolitical-military affairs officer\npolitical advisor\npolitical affairs officer\npolitical consultant\nsenior political affairs officer','\n',char(10)),'Political affairs officers analyse developments in foreign politics and other policy matters, monitor conflicts and consult on mediation measures, as well as other developmental strategies. They write reports to ensure communication with governmental bodies, and develop policies and implementation methods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8c0524b8-f878-4c68-8293-698d07c1807c','key_16876','','','3324','3324.4.2','wholesale merchant in agricultural raw materials, seeds and animal feeds',replace('wholesaler in agricultural raw materials, seeds and animal feeds\nwholesale merchandiser in agricultural raw materials, seeds and animal feeds\nB2B sales consultant in agricultural raw materials, seeds and animal feeds\ntrainee wholesale merchant in agricultural raw materials, seeds and animal feeds\nassistant wholesale merchant in agricultural raw materials, seeds and animal feeds\ngraduate wholesale merchant in agricultural raw materials, seeds and animal feeds\nsenior wholesale merchant in agricultural raw materials, seeds and animal feeds\nwholesale buyer in agricultural raw materials, seeds and animal feeds\nbusiness to business sales consultant in agricultural raw materials, seeds and animal feeds\nbusiness to business seller in agricultural raw materials, seeds and animal feeds\nwholesale trader in agricultural raw materials, seeds and animal feeds\nwholesale merchant in agricultural raw materials, seeds and animal feeds','\n',char(10)),'Wholesale merchants in agricultural raw materials, seeds and animal feeds investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8c0cc417-6b09-4082-8062-2f0f7123a573','key_16877','','','1324','1324.3.2.26','import export manager in office machinery and equipment',replace('international trade compliance manager in office machinery and equipment\nassistant import manager in office machinery and equipment\nsenior export manager in office machinery and equipment\ninternational trade manager in office machinery and equipment\ntrainee export manager in office machinery and equipment\nsenior import manager in office machinery and equipment\nassistant import export manager in office machinery and equipment\ngraduate import manager in office machinery and equipment\nsenior import export manager in office machinery and equipment\ngraduate import export manager in office machinery and equipment\nimport export manager in office machinery and equipment\ngraduate export manager in office machinery and equipment\ntrainee import manager in office machinery and equipment\ncustoms compliance manager in office machinery and equipment\ntrainee import export manager in office machinery and equipment\nassistant export manager in office machinery and equipment','\n',char(10)),'Import export managers in office machinery and equipment install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8c0f59c9-9a47-42e7-8287-aab19df4e6ab','key_16878','','','2152','2152.1.7','microelectronics engineer',replace('microelectronics technology engineering adviser\nmicroelectronic technology engineering consultant\nengineer of microprocessors\nmicroelectronic technology engineering expert\nengineer of microelectronics\nmicroelectronic technology engineer\nmicroprocessor engineer\nmicroelectronic engineering specialist\nmicroelectronic engineering expert\nmicroelectronics engineer\nengineer of micro-electronics\nmicro-electronic engineer\nmicro-electronics engineer\nmicroelectronic engineering consultant\nmicroelectronic technology engineering specialist\nmicro electronics engineer\nmicroelectronic engineering adviser\nmicro electronic engineer\nmicroelectronics design engineer\nmicroelectronic engineer','\n',char(10)),'Microelectronics engineers design, develop, and supervise the production of small electronic devices and components such as micro-processors and integrated circuits.','',replace('Excludes <a resource="http://data.europa.eu/esco/occupation/7f0fe7d3-54c6-41e9-88ba-9d4f0f1a46b4" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/7f0fe7d3-54c6-41e9-88ba-9d4f0f1a46b4"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">microsystem engineer</span></a>. \n Excludes people using materials such as quartz, plastics and ceramics.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8c3f536e-ba66-4321-ba40-363dc39f129b','key_16879','','','2161','2161.1','architect',replace('building designer\narchitectural planner\narchitect\narchitectural designer\ncommercial architect\nurban architect\narchitects\nresidential architect','\n',char(10)),'Architects investigate, design, and oversee the construction and development of buildings, urban spaces, infrastructure projects, and social spaces. They design in accordance with the surroundings and regulations applicable in specific geographic areas, taking into account factors that include function, aesthetics, costs, and public health and safety. They are aware of social contexts and environmental factors, which include the relationships between people and buildings, and buildings and the environment. They engage in multidisciplinary projects aimed at developing the social fabric of a geographic area and advancing in social urbanism projects. ','','','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8c57af09-719c-42b3-be40-6ed4946236cc','key_16880','','','2521','2521.1','database administrator',replace('database manager\ndatabase administrators\ndatabase administrator\ndata administrator\ndata manager\ndatabase configurator\nDBA\ndata base administrator\ndata base administrators','\n',char(10)),'Database administrators test, implement and administer computer databases. They use their expertise in database management systems to plan, coordinate and implement security measures to safeguard computer databases. They also use scripts and configuration files to tailor a database to users'' needs.','','Includes people performing managerial activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8c778238-98a4-4fec-a828-a4e978fb7a64','key_16881','','','7215','7215.4','tent installer',replace('installer\ntent installation worker\ntent builder\ntent installation crew member\ninstallation crew member\nrecreational tent installer\ntent installer/warehouse worker\ntent installer\nparty tent installer\ncommercial tent installer\nfield installer\ntent installer & warehouse worker\ntent crew installer','\n',char(10)),'Tent installers set up and dismantle temporary shelters, tents and circus tents with the associated accommodation for events and performances. Their work is based on instruction, plans and calculations. They work mostly outdoors and can be assisted by local crew.','','Includes people working in event and rental companies.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8c8b5cba-14b2-4728-b22c-eb90f1786e00','key_16882','','','3344','3344.2','medical practice manager',replace('medical practice supervisor\nhealth centre administrator\ngeneral practice administrator\npractice administrator\nhealth centre manager\ngeneral practice manager\nmedical practice director of operations\nmedical practice manager\nmedical practice administrator\npractice manager','\n',char(10)),'Medical practice managers manage the day-to-day operations of a medical practice. They oversee the staff and business side of the practice.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8c8ec7a2-adbb-4d1a-9a56-a7048d66c00b','key_16883','','','1349','1349.5','aviation surveillance and code coordination manager',replace('manager of aviation surveillance and code coordination\naviation surveillance and code compliance manager\naviation surveillance and code coordinator\naviation surveillance and code coordination manager\naviation surveillance and code coordination officer\naviation surveillance and code compliance co-ordinator\naviation surveillance and code compliance officer\naviation surveillance and code compliance supervisor','\n',char(10)),'Aviation surveillance and code coordination manager ensure that all components of surveillance infrastructures, both ground and airborne, operate in a safe, cohesive and interoperable manner.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8cb59bc4-1f39-477e-beea-effed12d186c','key_16884','','','2641','2641.4.4','technical communicator',replace('scientific communicator\ntechnical author\nadvanced technical communicator\ndocumentation specialist\ntechnical communicators\ntechnical illustrator\ne-learning author\ntechnical writer\nsenior technical communicator\ntechnical communicator\ntechnical documentation specialist\ntechnical documentation author\ninformation architect\nAPI writer\ntechnical content developer\nusability expert\nuser experience designer\ntechnical editor\nscientific and technical communicator\ntech communicator','\n',char(10)),'Technical communicators prepare clear, concise and professional communication from product developers to users of the products such as online help, user manuals, white papers, specifications and industrial videos. For this, they analyse products, legal requirements, markets, customers and users. They develop information and media concepts, standards, structures and software tool support. They plan the content creation and media production processes, develop written, graphical, video or other contents, generate media output, release their information products and receive feedback from the users.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8cc90646-fc23-445f-a95f-e28e6b5d5a05','key_16885','','','2659','2659.4','tourist animator',replace('tourist animator\ncruise entertainment attendant\nresort entertainment attendant\nentertainment attendant\nhospitality entertainment officer\nholiday entertainer\nhospitality entertainer\nholiday camp entertainment attendant','\n',char(10)),'Tourist animators develop and organise entertainment activities for guests of a hospitality establishment. They set up and coordinate activities to entertain customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8cce40c1-4479-47e7-b5fc-c976631d919f','key_16886','','','3343','3343.4','management assistant',replace('management assistant\noffice assistant\noffice administrator\nproject assistant\nmanagement aide\nassistant manager','\n',char(10)),'Management assistants perform varied types of jobs, usually under general supervision. They carry out administrative tasks and support managers to ensure the entire department functions smoothly.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8cf68522-dfc9-43c0-9f65-892703f4705e','key_16887','','','7412','7412.5','ground lighting officer',replace('airport ground lighting engineer\nairfield ground lighting engineer\nairfield lighting officer\nleader of airport lighting team\nairport lighting officer\nground lighting officer\nairport lighting supervisor\nairfield ground lighting officer\nground lighting engineer\nairport lighting team leader\nairport lighting team manager','\n',char(10)),'Ground lighting officers carry out the inspection and maintenance of airports'' lighting systems. They record their findings and formulate the actions to be followed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8d2df08e-0b2e-4837-8096-66595bbaab15','key_16888','','','8141','8141.1.1','belt builder',replace('belt  maintenance technician\nbelt splicer\nconveyor belt builder\nconveyor belt fitter\nbelt technician\nrubber belt builder\nbelt builder\nconveyor belt technician\ntransmission belt builder\nbelt maintenance engineer\nconveyor belt vulcaniser','\n',char(10)),'Belt builders make transmission and conveyor belts by building up plies of rubberised fabric. They cut the ply to requird length with scissors and bond plies together with rollers and stitchers. Belt builders insert the finished belt between the pressure rollers. They measure the finished belt to check if it conforms to specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8d315e32-73f6-463d-a335-c200e0bac97d','key_16889','','','2330','2330.1.2','biology teacher secondary school',replace('teacher of biology in high school\nhigh school biology tutor\nbiology teacher high school\nhigh school biology teacher\nsecondary school biology tutor\nbiology teacher\nsecondary school teacher of biology\nhigh school teacher of biology\nteacher of biology in secondary school\nhigh school tutor of biology\nsecondary school biology teacher\nbiology tutor in secondary school','\n',char(10)),'Biology teachers at secondary schools provide education toÂ students, commonly children and young adults, in aÂ secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, biology. They prepare lesson plans and materials, monitor the studentsâ progress, assist individually when necessary, and evaluate the studentsâ knowledge and performance on the subject of biologyÂ through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8d3cc890-5902-4fac-9438-6098a3ee437c','key_16890','','','7223','7223.25','upsetting machine operator',replace('upsetter apprenticeship\nupset forging machine operative\ncrank press operator\nheading machine operative\nupsetter\nupset operator\nupsetting machine operative\nupsetting machine operator\nupset forging machine operator\nheading machine operator','\n',char(10)),'Upsetting machine operators set up and tend upsetting machines, primarily crank presses, designed to form through forging processes metal workpieces, usually wires, rods, or bars, into their desired shape by having split dies with mulitiple cavities compress the workpieces'' length and hereby increasing their diameter.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8d3e8aaa-791b-4c75-a465-f3f827028f50','key_16891','','','2221','2221.2','nurse responsible for general care',replace('practice nurse\nlearning disability nurse\ncommunity staff nurse\nhealthcarer\nadult nurse\nchildren''s nurse\nmental health nurse\ndistrict nurse\nchildren''s community nurse\nward nurse\nnurse responsible for general care\nhealthcare nurse\nward sister','\n',char(10)),'Nurses responsible for general care are in charge of promoting and restoring patients` health by providing physical and psychological support to patients, friends, and families. They also supervise assigned team members.',replace('A nurse responsible for general care is a health care professional qualified to practise nursing both individually and in cooperation with others.\\n','\n',char(10)),'','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8d3fd879-986d-42e7-bf67-c3c415592fe3','key_16892','','','8114','8114.5','precast moulder',replace('hand tile-moulder\npre-cast moulder\nprecast molder\npre-cast molder\ncement-tile maker\nbrick veneer maker','\n',char(10)),'Precast moulders handcast decorative and structural concrete building products such as fireplace units, blocks or coloured tiles. They use a portable concrete-mixing machine.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8d410283-865d-4cbb-acae-ac7f69d9cf26','key_16893','','','2144','2144.1.3','automotive engineer',replace('automotive design specialist\nauto engineer\nlorry designer\nvehicle production designer\nmotor vehicle engineer\nautomotive design expert\nbus designer\ntruck production process engineer\nvehicle production production process engineer\ncoach production process engineer\nACES engineer\ncoach designer\nmotor vehicle designer\ncar production process engineer\nautomotive designer\nautomotive engineer\nlorry production process engineer\nAutomated, Connected, Electric and Shared engineer\nbus production process engineer\nmotorcycle production process engineer\nmotorcycle designer','\n',char(10)),'Automotive engineers design and oversee the manufacturing process and operation of motor vehicles such as motorcycles, cars, trucks, buses and their respective engineering systems. They design new vehicles or mechanical parts, supervise modifications and resolve technical problems. They make sure the designs comply with cost specifications and other constraints. They also conduct research studying environmental, energy and safety aspects.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8d4ce4a2-3453-4aa3-befb-7a24595cd68f','key_16894','','','8322','8322.1','armoured car driver',replace('armored car driver\narmoured truck driver','\n',char(10)),'Armoured car drivers drive the armoured car to transfer valuable items, such as money, to different locations. They never leave the car. They work in cooperation with the armoured car guards who deliver the valuables to their final recipients. Armoured car drivers ensure vehicle security at all times by following company policies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8d586ee9-0ab1-4155-a3b0-2ca786c8e48c','key_16895','','','2413','2413.1','financial analyst',replace('company analyst\nstructured finance analyst\nfinancial markets researcher\nfinancial researcher\nm&a analyst\nbanking analyst\neconomic analyst\nfinancial analyst\nfinancial markets report writer\nfinancial markets analyst\ncredit analyst\ntreasury analyst\nstock market analyst\ncapital markets analyst\nfinancial markets specialist\nfinancial markets forecaster','\n',char(10)),replace('\nFinancial analysts conduct economic research and elicit valuable analyses on financial matters such as profitability, liquidity, solvency, and asset management. They provide recommendations on financial matters for decision-making processes. Financial analysts work in both the public and the private sector.','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8d703e8f-1e57-4246-8a6b-cb87a760d4ab','key_16896','','','3412','3412.4','social care worker',replace('care worker\nsocial care assistant\ncommunity social care worker\nsenior social care worker\nsenior care worker\nsocial care worker','\n',char(10)),'Social care workers provide support and help people with care services. They help people to live full and valued lives in the community. They assist babies, young children, adolescents, adults and older adults.They attend to the psychological, social, emotional and physical needs of service users. They work in a large variety of settings with individuals, families, groups, organisations and communities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8d765d88-c8d6-406e-8dfe-e37c5aa89be6','key_16897','','','8113','8113.5','roughneck',replace('roughneck\nchainhand','\n',char(10)),'Roughnecks make or break connections when the drill pipe is tripped in or out of the drilling hole. They assemble and disassemble pipes and drills, and collect core samples. They maintain and repair the equipment on the drilling floor.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8d9ec84d-cf2d-4179-87bc-335cda54a427','key_16898','','','2521','2521.2','database designer',replace('data model designer\ndata architect\ndatabase designer\ndata base designer\ndatabase designers\ndata base designers\ndatabase architect\ndatabase design engineer','\n',char(10)),'Database designers specify the database''s logical structure, processes, and information flows. They design data models and databases to serve data acquisition.','','Excludes people performing managerial, programming, coding, and setting of a system''s activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8db28ebf-8818-4639-aa49-30324901836e','key_16899','','','2262','2262.1.2','specialist pharmacist',replace('clinical pharmacist\npharmacy expert\nspecialist pharmacist\nadvanced pharmacist\nexpert in pharmacy\nclinical pharmacologist\nspecialist in pharmacy\npharmacy specialist\nexpert pharmacist','\n',char(10)),'Specialist pharmacists provide specialist services for companies in the pharmacy industry and in hospital pharmacies. The role of the specialist pharmacist varies throughout Europe, subject to national rules and training. ','','','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8dded776-dc04-4099-a32c-7c5a41ee00e1','key_16900','','','5249','5249.2.1.4','rental service representative in construction and civil engineering machinery',replace('rental service desk supervisor in construction and civil engineering machinery\nrental service team leader in construction and civil engineering machinery\nrental specialist in construction and civil engineering machinery\nrental sales assistant in construction and civil engineering machinery\nrental service desk clerk in construction and civil engineering machinery\nrental sales desk agent in construction and civil engineering machinery\nrental service worker in construction and civil engineering machinery\nrental service supervisor in construction and civil engineering machinery\nrental sales supervisor in construction and civil engineering machinery\nrental sales worker in construction and civil engineering machinery\nrental service desk agent in construction and civil engineering machinery\nrental service manager in construction and civil engineering machinery\nrental sales desk clerk in construction and civil engineering machinery\nrental sales desk supervisor in construction and civil engineering machinery\nrental service assistant in construction and civil engineering machinery\nrental advisor in construction and civil engineering machinery\nrental sales manager in construction and civil engineering machinery\nrental sales team leader in construction and civil engineering machinery\nrental service representative in construction and civil engineering machinery','\n',char(10)),'Rental service representatives in construction and civil engineering machinery are in charge of renting out equipment and determining specific periods of usage. They document transactions, insurances and payments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8de99659-5b10-4825-ac67-0c50189bcc3c','key_16901','','','2141','2141.9','packaging production manager',replace('senior packaging technologist\nsenior mail and packaging manager\ntechnical packaging manager\npackaging technologist\npackaging development manager\ngraduate packaging manager\nassistant mail and packaging manager\nassistant operations manager\ntrainee packaging manager\nsenior packaging manager\ntrainee mail and packaging manager\npackaging production manager\ntrainee operations manager\npackaging manager\ngraduate operations manager\npackaging production engineer\npackaging consultant\npackaging specialist\nassistant packaging manager\nsenior operations manager\ngraduate mail and packaging manager','\n',char(10)),'Packaging production managers define and analyse package units in order to avoid damages or loss of quality of the goods packed. They also design the packaging according the specifications of the product and offer solutions to solve packaging problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8e185c49-704e-49c0-8584-a7e0b618f628','key_16902','','','3312','3312.5','loan officer',replace('loans consultant\ncredit officer\nloans controller\nloans adviser\nloan officer\ncommercial lender\nloan processor\nloans agent','\n',char(10)),'Loan officers assess and authorise the approval of loan applications for individuals and businesses. They ensure complete transactions between loan organisations, borrowers, and sellers. Loan officers are specialists in consumer, mortgage, or commercial lending.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8e24c53a-9dc6-46d1-986b-48a93f20df2a','key_16903','','','6113','6113.3','horticulture production team leader',replace('horticultural foreman\nhorticultural production team leader\nhorticultural team manager\nhorticulture production team leader','\n',char(10)),replace('Horticulture production team leaders are responsible for leading and working with a team. They organise daily work schedules for the production of horticulture crops and participate in the production. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8ec6b513-3d44-4e94-b187-d55734a587a4','key_16904','','','3311','3311.2.5','stock broker',replace('stock broker\nstockbroking dealer\ninstitutional stock broker\nstockbroker\nstocks and shares broker','\n',char(10)),'Stock brokers act on behalf of their individual or institutional clients in order to buy and sell stocks and other securities. They are in close contact with their clients and ensure that what they buy or sell through the stock exchange market is according to their clients'' wishes. Stock brokers undertake analyst research to make recommendations to their clients and expand their client base through various methods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8ec8df02-e9dd-43b7-b416-5846ae0414ab','key_16905','','','3334','3334.3','real estate agent',replace('estate agent\ncommercial building rental agents\nreal estate salesperson\nassociate real estate brokers\ncommercial real estate salespersons\nproperty rental agents\nreal estate dealer\ntitle examiners\ncommercial real estate salesperson\nreal estate dealers\nrealtor\ncommercial real estate agent\nproperty agent\nreal estate salespersons\nreal estate sales agents\nresidential real estate agents\nreal estate sales representatives\nlettings agents\napartment rental agents\nestate agents\nreal estate agent\nreal estate broker\nreal estate rental agents\nproperty agents\nassociate real estate broker\nlettings agent\napartment rental agent\nletting agents\nreal estate sales agent\nreal estate rental agent\ntitle examiner\nestate managers\nproperty rental agent\nreal estate brokers\nreal estate sales representative\nrealtors\nreal estage agent mediators\ncommercial building rental agent\nresidential real estate agent\ncommercial real estate agents\nreal estage agent mediator\nletting agent','\n',char(10)),'Real estate agents administer the sales or letting process of residential, commercial properties or land on behalf of their clients. They investigate the property''s condition and assess its value in order to offer the best price to their clients. They negotiate, compose a sales contract or a rental contract and liaise with third parties in order to realize the stated objectives during transactions. They undertake research to determine the legality of a property sale before it is sold and make sure the transaction is not subject to any disputes or restrictions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8edc768c-0bc6-4d65-b559-50f9fb2cc93f','key_16906','','','8141','8141.1.9','V-belt builder',replace('V-belt forming machine operative\nv-belt builder\nV-belt builder\nV-belt former and cutter\nv-belt forming machine operator\nrubber V-belt skiver\nV-belt skiver','\n',char(10)),'V-belt builders form V-belts out of calendered rubber rolls. They measure the amount of rubber needed and cut it with scissors. V-belt builders brush rubber cement on sides of the belt. They put belts on the drum to compress materials together and cut the belt to specified width with a knife.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8edd8bff-cb59-4c9c-ab0f-59e77d18be48','key_16907','','','2152','2152.1.13','predictive maintenance expert','predictive maintenance engineer','Predictive maintenance experts analyse data collected from sensors located in factories, machineries, cars, railroads and others to monitor their conditions in order to keep users informed and eventually notify the need to perform maintenance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8f10ea9b-bfdf-4a4c-9fa7-b6e02daa93e5','key_16908','','','3434','3434.1.2','pastry chef',replace('pastry chef\nbaker\npastry decorator\nglacier\nconfectionary chef\ndessert chef\ndécorateur\nconfiseur\nconfectioner\npatissier\npastry cook','\n',char(10)),'Pastry chefs are responsible for preparing, cooking and presenting desserts, sweet products and bakery products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8f324afc-6ae8-48f5-af68-0fac08024fd1','key_16909','','','2621','2621.7','exhibition registrar',replace('museum cataloguer\nmuseum objects cataloguer\nexhibition registrar\nmuseum registrar','\n',char(10)),'Exhibition registrars organise, manage and document movement of museum artefacts to and from storage, display and exhibitions. This happens in collaboration with private or public partners such as art transporters, insurers and restorers, within the museum and outside.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8f592e05-8035-4646-b33e-a4ea2c2314e3','key_16910','','','7536','7536.1.1','leather goods finishing operator','leather goods finishing operator','Leather goods finishing operators organise leather goods products to be finished applying different types of finishing, e.g. creamy, oily, waxy, polishing, plastic-coated, etc. They use tools, means and materials to incorporate the handles and metallic applications in bags, suitcases, and other accessories. They study the sequence of operations according to the information received from the supervisor and from the technical sheet of the model. They apply techniques for ironing, creaming or oiling, for the application of liquids for waterproofing, leather washing, cleaning, polishing, waxing, brushing, burning tips, remotion of glue waste, and painting the tops following technical specifications. They also check visually the quality of the finished product by paying close attention to the absence of wrinkles, straight seams, and cleanliness. They correct anomalies or defects that can be solved by finishing and reported to the supervisor.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8f654b67-89f8-4669-8c9c-a4875bf22699','key_16911','','','8171','8171.4','laminating machine operator',replace('laminating machine technician\ncoter\ncoating machine specialist\nlaminater\npapermaking laminate coating machine technician\nlaminator operator\nlaminating machine operator\nlaminator technician\nlaminating assistant\nlaminator specialist\npaper making laminate coating machine operator\nlaminating machine specialist','\n',char(10)),'Laminating machine operators tend a machine that applies a plastic layer to paper to strenghten it and protect it from wetness and stains.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8f71761e-6fa5-4237-a240-4d0c7feff026','key_16912','','','8131','8131.3','coking furnace operator',replace('coke quencher operator\ncoke oven operator\ncoking furnace technician\ncoking oven operator\ncoking furnace operator\ncoker\nindustrial coke oven worker','\n',char(10)),'Coking furnace operators monitor the operation of a usually automatic industrial oven that takes in crushed, washed, and blended bituminous coal and heats it to temperatures between 1000 and 2000 °C without the presence of oxygen in order to obtain cokes. The finished cokes are then quenched with water or air and prepared for transport.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8f8455a9-d2ac-4f1c-ac83-4f4aa82b1eb7','key_16913','','','5223','5223.7.27','orthopaedic supplies specialised seller',replace('Orthopedic supplies specialized seller\nsurgical supplies specialized seller\northohouse  salesperson\northopaedic articles specialized seller\northohouse  specialized seller\nsurgical supplies salesperson\northopaedic articles  salesperson\northopaedic supplies salesperson\northopaedic supplies specialized seller','\n',char(10)),'Orthopaedic supplies specialised sellers sell orthopaedic goods in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8f928d49-da89-4d07-9123-2151a4d276d4','key_16914','','','1324','1324.4','move manager',replace('company move manager\nindustrial move manager\ncorporate move manager\nremoval coordinator\nmoving manager\nmove manager\nremoval manager\nrelocation manager','\n',char(10)),'Move managers coordinate all the resources and activities comprised in moving services. They work together with clients to define the requirements of the service and adapt it to their demands. They manage communication with shipping companies, planners, and movers to assure compliance, business efficiency, and client satisfaction.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8f9b20ff-86b3-478c-8054-73a220973d6f','key_16915','','','8343','8343.1','automated cable vehicle controller',replace('cable propelled vehicle controller\nautomated cable vehicle controller\ncontroller of automated cable vehicles\ncable propelled vehicle operator\ncable car operator\ncable car controller\nautomated cable vehicle operator','\n',char(10)),'Automated cable vehicle controllers operate systems and control boards keeping under control the operations of a varied array of modes of transport operated by cable. They can operate aerial cabins, telphers, funiculars, etc. They ensure continuous operations and intervene in the operations when unforeseen situations occur.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8fc3d559-d61c-418c-ad4a-bf4c6b2909e3','key_16916','','','5419','5419.5','crowd controller',replace('steward\ncrowd control worker\ncrowd controller\nspectator controller','\n',char(10)),'Crowd controllers keep constant watch of the crowd during a specific event such as public speeches, sporting events or concerts, in order to prevent and react quickly to incidents. They control the entry to the venue, monitor the behaviour of the crowd, handle aggressive behaviour and conduct emergency evacuations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/8fdce434-caaa-42c7-a77c-7ba8d1cbc4be','key_16917','','','8114','8114.4','electrolytic cell maker',replace('electrolytic cell maker\ncover maker\ncell maker\nconcrete electrolytic cell maker\nelectrolytic cell operations worker\nelectrolytic cell builder\npolymer concrete electrolytic cell maker ','\n',char(10)),'Electrolytic cell makers create, finish and test electrolytic cells using equipment, tools and concrete mixers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/903dc1bf-2da8-477d-83b2-687d360cf1da','key_16918','','','2432','2432.4','intercultural communication consultant',replace('intercultural communications consultant\nintercultural communication adviser\nintercultural consultant\nintercultural communication expert\nintercultural communication consultant\nintercultural communication specialist\nintercultural mediator','\n',char(10)),'Intercultural communication consultants specialise in social interaction between parties of different cultures, advise organisations on international interactions in order to optimise their performance, and facilitiate cooperation and positive interaction with organisations and individuals from other cultures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/90569478-805c-4d14-a1ba-e9c9c4ac2818','key_16919','','','2635','2635.3.25','victim support officer',replace('victim support social worker\nvictims caseworker\nsupport coordinator\nrelief worker\ndomestic abuse support worker\nvictim care officer\nvictim service manager\nvictim assistance social worker\nvictim support officer\nvictime and witness support officer\nvictim service officer\nsenior victims support officer\nrestorative justice officer','\n',char(10)),'Victim support officers provide assistance and counselling to people who were victim of or have witnessed crimes such as sexual assault, domestic abuse or anti-social behaviour. They develop solutions according to the different needs and feelings of persons.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/90a73720-642d-4f45-88e7-8f593a5e0df2','key_16920','','','2320','2320.1.23','transport technology vocational teacher',replace('transportation technology vocational instructor\nvocational teacher in transportation technology\nteacher in transportation technology\ntransportation technology teacher\ninstructor in transportation technology\ntransportation technician instructor\nautomotive technology instructor\nautomotive technology teacher\ntransport technology vocational teacher\nvocational instructor in transportation technology','\n',char(10)),'Transport technology vocational teachers instruct students in their specialised field of study, transport technology, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for a transport technology-related profession, such as automotive brake technician. Transport technology vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of transport technology through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/90f75f67-495d-49fa-ab57-2f320e251d7e','key_16921','','','5120','5120.1','cook',replace('fry cook\npantry supervisor\nkitchen chef\nfood cook\nsaucemaker\nsoup cook\njunior cook\nfish cook\nsauté cook\nunder chief of the kitchen\nfood keeper\nsecond kitchen chef\ngrill cook\nculinary operative\nvegetable cook\nchief of the group\nsenior chef\ndeputy kitchen chef\nchief of the kitchen\ncommunard\napprentice\nroast cook\ncook\nentrée preparer','\n',char(10)),'Cooks are culinary operatives who are able to prepare and present food, normally in domestic and institutional environments.','',replace('Includes people working in institutional settings. \n Includes people working in the preparation of fast food.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/91373a70-79b6-47a2-aa50-07dfe20dd258','key_16922','','','7232','7232.5','aircraft maintenance technician',replace('avionics line inspector\naeroplane preventative maintenance technician\nairworthiness technician\ncabin mechanic\naviation maintenance technician\naeroplane systems maintenance technician\naircraft systems maintenance technician\naircraft instrument operator\naircraft maintenance technician\naeroplane technician\naircraft engineer\navionics instrument inspector\naviation line inspector\navionics mechanical technician\naviation instrument inspector\naircraft technician\naircraft preventative maintenance technician\naircraft instrument operative','\n',char(10)),'Aircraft maintenance technicians perform preventive maintenance to aircrafts, aircraft components, engines and assemblies, such as airframes and hydraulic and pneumatic systems. They perform inspections following strict protocols and aviation laws.','','Excludes people performing maintenance and repair of navigation and communication systems.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9161b2df-624c-4f5e-ad6c-0392439fa5c3','key_16923','','','8160','8160.51','starch extraction operator',replace('refinery starch extractor\nstarch extraction operator\nrefinery starch operator','\n',char(10)),replace('Starch extraction operators use equipment to extract starch from raw material such as corn, potatoes, rice, tapioca, wheat, etc. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/916d989e-0c5b-4c35-8d43-62ba65bebd30','key_16924','','','3331','3331.2.1.1','import export specialist in agricultural machinery and equipment',replace('graduate import specialist in  agricultural machinery and equipment\ntrainee import export specialist in  agricultural machinery and equipment\nassistant import export specialist in  agricultural machinery and equipment\ntrainee import specialist in  agricultural machinery and equipment\nsenior import specialist in  agricultural machinery and equipment\nassistant import specialist in  agricultural machinery and equipment\nsenior export specialist in  agricultural machinery and equipment\ncustoms compliance specialist in  agricultural machinery and equipment\ntrainee export specialist in  agricultural machinery and equipment\nimport export specialist in agricultural machinery and equipment\ngraduate import export specialist in  agricultural machinery and equipment\nsenior import export specialist in  agricultural machinery and equipment\nassistant export specialist in  agricultural machinery and equipment\ninternational trade compliance specialist in  agricultural machinery and equipment\ngraduate export specialist in  agricultural machinery and equipment','\n',char(10)),'Import export specialists in agricultural machinery and equipment have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/91ba2935-f5c9-426d-8573-997b8b91e5ed','key_16925','','','2320','2320.1.1','agriculture, forestry and fishery vocational teacher',replace('vocational educator in agriculture, fishery and forestry\nagriculture, forestry, and fishery vocational trainer\ninstructor in agriculture, forestry and fishery\ntrainer in agriculture, forestry and fishery\nvocational teacher in agriculture, fishery, and forestry\nvocational teacher of agriculture, forestry and fishery\nagriculture, forestry, and fishery teacher\nagriculture, forestry, and fishery vocational instructor\nagriculture, forestry and fishery vocational teacher\nagriculture, fishery, and forestry educator\nagriculture, forestry, and fishery instructor','\n',char(10)),'Agriculture, forestry and fishery vocational teachers instruct students in their specialised field of study, agriculture, forestry and fishery, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for an agriculture, forestry or fishery profession. Agriculture, forestry and fishery vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of agriculture, forestry and fishery through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/91d0f74b-8612-49cc-a7da-83ee3c4b94d9','key_16926','','','2141','2141.5','leather goods industrial engineer',replace('industrial engineering supervisor\nleather goods engineer\nmanufacturing operations manager\nindustrial process engineer\nleather goods manufacturing technician\nprocess improvement engineer\nindustrial engineering technician\nleather goods industrial engineer\nengineering technician\nhead leather goods manufacturing operations\nmanufacturing engineer\nindustrial engineer','\n',char(10)),'Leather goods industrial engineers analyse product technical specifications, define productive operations and their sequence, refine working methods and calculate operative times by using time measurement techniques. They address human and technological resources to each operation and define distribution of work according to production capacity. All their activities and tasks have the objective of maximising productivity and reducing production costs, assuring the functionality and quality of product and customer satisfaction.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/91e35fd9-ac59-48ef-8416-c06a201b2da8','key_16927','','','3118','3118.4','printed circuit board designer',replace('printed circuit board designer\nprinted circuit board (PCB) designer\nPCB design specialist\nPCB designer\nprinted circuit board design specialist\nPCB design expert\nprinted circuit board design expert\ncircuit board designer','\n',char(10)),'Printed circuit board designers diagram and design the construction of circuit boards. They envision the logical placing of conductive tracks, coppers, and pin pads in the board. They use computer programmes and specialised software for the designs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/91f93dc4-1b71-4053-b6fb-c579ec19303a','key_16928','','','1213','1213.3','procurement department manager',replace('head of procurement unit\nhead of procurement department\nprocurement director ','\n',char(10)),'Procurement department managers ensure that the organisation''s policy goals are transformed into concrete actions and support their teams to achieve the best results for their clients and the public. They oversee the public procurement professionals in the organisation to deliver on their objectives.  ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/920c22e1-10a1-4afa-86b3-c43da8488391','key_16929','','','2310','2310.1.37','social work lecturer',replace('university lecturer in social work and social policy\nsocial work docent\nlecturer in social work\nprofessor of social policy\nuniversity social work lecturer\nsocial work teacher\nsenior lecturer in social work\nuniversity social work and social policy lecturer\nlecturer in social work and social policy\nuniversity lecturer in social work\nsocial work professor\nprofessor of social work\nuniversity social work teacher\nprofessor of social work and social policy\nacademic social work educator\nsocial work instructor\nlector in social work\nsocial work lecturer\nhigher education social work teacher\nsocial work lector','\n',char(10)),'Social work lecturers are professionals who exercise dual roles, on one hand they practice the academic discipline that provides social services, such as counselling, therapy or advocacy, to individuals or groups of people. On the other hand they are part of the academic world providing professional education, engaging also in research and knowledge development, contributing to solutions concerning complex social problems and innovative approaches to ameliorate those problems. They teach social work knowledge, skills and values for preparing students to engage in culturally competent social work practice with diverse populations and communities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/92371348-ef8f-41ce-9666-c903b57cc8b4','key_16930','','','9333','9333.5','rail intermodal equipment operator',replace('transfer table operator\nrailroad equipment operator\nrailroad heavy equipment operator\nintermodal helper\nintermodal equipment handler\nintermodal railroad equipment handler\nrail equipment operator\nchassis estimator\ncontainer and chassis estimator\nintermodal rail equipment operator\nrail intermodal equipment operator\nintermodal equipment operator\nrailway intermodal equipment operator\noperator of rail intermodal equipment\nrailway equipment operator','\n',char(10)),'Rail intermodal equipment operators assist in the loading of trailers and containers on and off railcars and chassis. They manoeuvre tractor-trailer combinations around tight corners and in and out of parking spaces. They use an on-board computer peripheral to communicate with yard management computer system and to identify railcars.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/928dd410-c74b-4dab-94b8-fcbdcf541bd1','key_16931','','','7532','7532.2','clothing cutter',replace('bespoke clothing cutter\ncloth cutting machine operator\ngarment cutter\nclothing cutter operator\nfur cutter\nclothing cutter\nfabric cutter\nleather pattern cutter\nclothing marker\nproduction pattern cutter\ncreative pattern cutter','\n',char(10)),'Clothing cutters mark, cut, shape, and trim textile or related materials according to blueprints or specifications in the manufacture of wearing apparel.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/929b5c9d-2fbf-44f2-980d-2ac6c0dc212f','key_16932','','','3214','3214.3','prosthetist-orthotist',replace('prosthetist-orthotist\northotist\nexpert prosthetist\nartificial limb technician\nspecialist prosthetist\nclinical prosthetist\nprosthetic technician\nprosthetic technologist\ntechnologist in prosthetics\ncertified prosthetist','\n',char(10)),'Prosthetist-orthotists design and custom fit prostheses and orthoses for individuals who are missing a limb from accident, disease or congenital conditions or for individuals who have impairments, deficiences or weaknesses due to injury, pathology or congenital malformation. They mix patient care with the design and fabrication of these devices to address their patients'' needs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/929ef41d-afd0-4d5a-b85b-69eab68fa368','key_16933','','','3411','3411.7','legal assistant',replace('law clerk\ncorporate legal assistant\njudicial assistant\nlegal aide\ncorporate law assistant\nlegal assistant\npublic administration legal assistant\nparalegal\nparalegal specialist','\n',char(10)),'Legal assistants work closely together with lawyers and legal representatives in the research and preparation of cases brought to courts. They assist in the paper work of cases and management of the administrative side of court affairs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/92b7776f-5d90-413d-b05f-51e429e28346','key_16934','','','4224','4224.1.1','camping ground operative',replace('camp-site worker\ncampgrounds operative\ncamp-site operator\ncamp-site staff\ncamping facility worker\ncamping pitch operative\ncamping ground operative\ncountryside operative\ncamp-site operative','\n',char(10)),'Camping ground operatives perform customer care in a campsite facility and other operational work.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/92b7a42c-b437-4412-ac21-1a750883cf21','key_16935','','','7412','7412.4','geothermal technician',replace('geothermal energy installer\ngeothermal installer\ngeothermal energy technician','\n',char(10)),'Geothermal technicians install and maintain geothermal power plants and commercial and residential geothermal heating installations. They perform inspections, analyse problems and carry out repairs. They participate in the initial installation, testing and maintenance of geothermal equipment and ensure compliance with safety regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/92bb3d8c-8a9f-4cd7-80ec-247bbf173004','key_16936','','','2152','2152.1.1','computer hardware engineer',replace('engineer in computer hardware and network\ncomputer hardware engineering consultant\nIT hardware specialist\nengineer of PC hardware\ncomputer hardware engineering adviser\ncomputer hardware technology engineering consultant\ncomputer hardware technology engineering adviser\nIT hardware engineer\nengineer of IT hardware\ncomputer network and hardware engineer\ncomputer engineer\ncomputer hardware technology engineering specialist\nhardware engineer\nspecialist IT hardware engineer\nengineer in computer hardware\nPC hardware engineer\ncomputer hardware technology engineering expert\ncomputer hardware and network engineer\ncomputer hardware engineering specialist\ncomputer hardware technology engineer\ncomputer hardware engineer\ncomputer hardware engineering expert','\n',char(10)),'Computer hardware engineers design and develop computer hardware systems and components, such as circuit boards, modems, and printers. They draught blueprints and assembly drawings, develop and test the prototypes, and supervise the production process.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/92be1fac-97c4-4d4d-ada7-f6aa07d6f316','key_16937','','','3252','3252.4','waiting list coordinator',replace('manager of hospital waiting lists\nwaiting list manager\nmanager of waiting lists\nhealthcare waiting list manager\nwaiting list coordinator\nmedical care waiting list coordinator\nhospital waiting list coordinator\nsenior waiting list coordinator\ncoordinator of waiting lists\nsurgical waiting list coordinator\ncoordinator of hospital waiting lists','\n',char(10)),'Waiting list coordinators guarantee the day to day management of waiting list time. They plan when operation rooms are available and call patients in to be operated. Waiting list coordinators make sure to optimise the use of resources.','',replace('Excludes people performing patients treatment. \n Excludes people performing assistance in medical treatment.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/92cba6b4-8fca-49bb-b68c-45c5096fedc9','key_16938','','','9123','9123.1','window cleaner',replace('window cleaner\nwindow washer\nbuilding maintenance and cleaning worker\njanitress','\n',char(10)),'Window cleaners use cleaning tools such as sponges and detergents to clean windows, mirrors and other glass surfaces of buildings, both on the interior and exterior. They use specific ladders to clean taller buildings, using safety belts for support.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/92dc335d-a116-416f-ad2b-257e5382e574','key_16939','','','7534','7534.2','mattress maker',replace('mattress-making machine operative\nmattress-making machine operator\nmattress maker, hand\nmattress-making machine setter\nmattress builder\nmattress-making machine tender operator\nmattress- making machine worker\nmattress builder, hand\nsoft furnishing maker\nmattress maker','\n',char(10)),'Mattress makers form mattresses by creating pads and coverings. They tuft mattresses by hand and cut, spread and attach the padding and cover material over the innerspring assemblies.','','Excludes people producing inflatable rubber mattresses and rubber waterbed mattresses.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/92df4ee3-bce3-45f6-a693-597df1b96f43','key_16940','','','3412','3412.1','crisis helpline operator',replace('distress helpline operator\ntelephone crisis support\ncrisis helpline operator\ntelephone crisis counselor\ndistress hotline operator\ncrisis helpline worker\ncrisis hotline operator','\n',char(10)),'Crisis helpline operators provide advice and support to distraught callers via the telephone. They have to deal with a diverse range of issues such as abuse, depression and financial problems. Helpline operators maintain records of the phone calls according to regulations and privacy policies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/92efb42f-6b0a-44d8-94ea-0141555e6c3b','key_16941','','','2114','2114.1.1','environmental geologist',replace('environmental consultant\ngeologicial engineer\nenvironmental geology studies scholar\nenvironmental geology studies research scientist\nmineralogical analyst\nenvironmental geologist\nenvironmental geology studies research analyst\nenvironmental geology scholar\nenvironment geologist\nenvironmental geology scientist\nenvironmental geology studies researcher\nenvironmental geology geological consultant\nenvironmentalist\nminerals analyst\nenvironmental geology research scientist\nenvironmental geology analyst\nenvironmental geology researcher\nenvironmental geology science researcher\nenvironmental geology research analyst\nenvironmental geology studies analyst\nenvironmental mineralogist\nenvironmental geology studies scientist','\n',char(10)),'Environmental geologists study how mineral operations may impact the composition and physical characteristics of the earth and its resources. They provide advice on issues such as land reclamation and environmental pollution.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/933a0762-1201-4855-909c-94403f6867a9','key_16942','','','3324','3324.4.5','wholesale merchant in china and other glassware','wholesale merchant in china and other glassware','Wholesale merchants in china and other glassware investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/933d02c0-3dfa-418f-9c2d-470b525cc42c','key_16943','','','3351','3351.2','hand luggage inspector',replace('cargo examiner\nhand luggage inspector\nbaggage inspector\nhand-luggage inspector\nhand baggage checker\nbaggage examiner\nhand baggage examiner\nhand baggage inspector\nbaggage checker\nairport customs worker\nhand luggage examiner\ncargo checker\nairport customs agent\nhand luggage checker\nairport customs controller\nairport luggage worker\nairport baggage worker\nairport cargo worker\nairport security worker','\n',char(10)),replace('Hand luggage inspectors check individuals'' luggage to detect potential threatening objects. They comply with public safety regulations and company''s procedure.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/934c3572-37f4-4cdd-a4a4-017f5f7809e4','key_16944','','','2412','2412.6.2','investment fund management assistant',replace('investment fund assistant\ninvestment fund clerk\ninvestment fund office clerk\ninvestment fund management assistant\ninvestment fund specialist','\n',char(10)),'Investment fund management assistants provide clients with financial planning advice on financial products and serve as the main contact point for new and old clients. They assist and perform preparatory work in the creation and administration of funds and help with the implementation of fund management decisions made by the portfolio or fund manager.','','Includes people working with fund managers to assist them in various tasks including monitoring the financial markets.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9358775a-3769-4327-bf25-6c0d9d69d192','key_16945','','','1439','1439.8','travel agency manager',replace('tourism sales manager\ntravel sales manager\ntourism agency manager\ntravel call centre manager\ntravel agency manager','\n',char(10)),'Travel agency managers are in charge of managing employees and activities of a travel agency. They organise, advertise and sell tourist offers and travel deals for specific regions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9388a977-6f79-4509-81aa-026bc49e1919','key_16946','','','8183','8183.1','canning and bottling line operator',replace('canning and bottling operative\ncanning operative\ncanning and bottling worker\ncanning and bottling line operator\ncanner','\n',char(10)),'Canning and bottling line operators observe bottles and cans passing by during the production process. They stand next to conveyors belts to ensure that bottles are filled to standard levels and that there are no major deviations. They discard defective bottles or cans.','','It excludes the handling of fish cans.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/939e05ea-2589-4e3c-a501-eea2bdd6864a','key_16947','','','8152','8152.3','weaving machine operator',replace('weaving loom operator\nweaving tender\nweaver apprentice\nweaving machine operator\nweaving machinist','\n',char(10)),'Weaving machine operators set up, operate and monitor weaving machines .They work with specialised machinery, techniques and materials to process threads of yarn into weaved products such as clothing, home-tex or technical end products. They maintain and repair weaving machinery and ensure operations run without problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/93a8bd5f-c268-46f5-8461-1138a9a96189','key_16948','','','7221','7221.2','drop forging hammer worker',replace('drop forge operative\ndrop forge operator\ndrop forging hammer worker\ndie forger\nforging press operator\nforge operative\nforging roll technician\nforger tender\ndrop forge hand\ndrop forger\ndrop forge set up operator\nforging roll operator','\n',char(10)),'Drop forging hammer workers utilise forging machinery and equipment, specifically machined hammers, in order to form ferrous and non-ferrous metal workpieces to their desired shape. They tend the forging hammers that are dropped onto the workpiece in order to reshape it after the form of the die, which can be closed or open, fully enclosing the workpiece or not.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/93aa611c-a58c-43ff-bc34-6e201dfdd0dd','key_16949','','','7536','7536.2.5','footwear patternmaker',replace('footwear pattern designer\nfootwear pattern designer and maker\nfootwear patternmaker\nfootwear pattern cutter\npattern designer and maker\nfootwear pattern-maker\npattern designer and cutter\nfootwear pattern designer and cutter','\n',char(10)),'Footwear patternmakers design and cut patterns for all kinds of footwear using a variety of hand and simple machine tools. They check various nesting variants and perform material consumption estimation. Once the sample model has been approved for production, they produce series of patterns for range of footwear in different sizes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/93b11f0f-69af-4ece-b9da-f29aab7d38d3','key_16950','','','2144','2144.1.4','container equipment design engineer',replace('container equipment manufacturing engineer\ncontainer equipment assembly line machinist\ncontainer equipment technology engineering adviser\ncontainer equipment technology engineer\ncontainer equipment engineering consultant\ncontainer equipment assembly machinist\ncontainer equipment technology engineering expert\ncontainer equipment electronic engineer\ncontainer equipment bench precision engineer\ncontainer equipment machine engineering apprentice\ncontainer equipment technology engineering specialist\ncontainer equipment design engineer\ncontainer equipment engineer\ncontainer equipment assembly inspector\ncontainer assembly line engineer\ncontainer equipment engineering specialist\ncontainer equipment sub engineer\ncontainer equipment mechanical engineer\ncontainer equipment engineering adviser\ncontainer equipment engineering expert\ncontainer equipment technology engineering consultant\ncontainer equipment designer','\n',char(10)),'Container equipment design engineers design equipment to contain products or liquids, according to set specifications, such as boilers or pressure vessels. They test the designs, look for solutions to any problems and oversee production.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/93dfef83-579c-4bef-b0ba-d41ee9cbda85','key_16951','','','1120','1120.1','animal facility manager',replace('aquarium CEO\nzoo chief executive officer\nzoo CEO\naquarium managing director\nanimal facility manager\naquarium chief operating officer\nzoo managing director\nzoo chief operating officer\nzoo COO\naquarium COO\naquarium chief executive officer','\n',char(10)),'Animal facility managers coordinate and plan all activities of a zoo. They formulate policies, manage daily operations, and plan the use of materials and human resources. They are the driving force and public face of their institution. This often involves representing their institution on  a national, regional and global scale and taking part in coordinated zoo activities.','','Excludes people working in animal research facilities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/93e4e70b-aae4-4fdc-8037-e48e7077564e','key_16952','','','2164','2164.3','transport planner',replace('traffic and highway engineer\ndevelopment planning transport planner\ntransport planning development engineer\ntraffic planner\njunior transport planner\ntransport modeller\nhighway and drainage engineer\ntransport planner\nsenior transport planner\nroad traffic planner\n transportation business change manager\ntraffic engineer','\n',char(10)),'Transport planners develop and implement policies in order to improve transport systems, taking into account the social, environmental and economic factors. They collect and analyse traffic data using statistical modelling tools.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/93f61216-b9c8-4051-bb1e-32839c1566c1','key_16953','','','2320','2320.1.14','hairdressing vocational teacher',replace('hairdressing instructor\nvocational teacher of hairdressing\neducator in hairdressing\nhairdressing educator\nvocational hairdressing teacher\nvocational hairdressing instructor\nhair stylist instructor\nbarbering teacher\nhairdressing vocational teacher\nbarbering instructor\nhair stylist trainer\nbarber instructor\nhairdressing teacher','\n',char(10)),'Hairdressing vocational teachers instruct students in their specialised field of study, hairdressing, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master. Hairdressing vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of hairdressing through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/942211c1-b534-4a14-ae85-38ea44c2d14b','key_16954','','','1346','1346.5','insurance claims manager',replace('claims manager\ninsurance claim supervisor\ninsurance claims coordinator\ninsurance claims manager\ninsurance claims administrator\nclaims director\nhead of insurance claims office\ninsurance and claims manager\ninsurance claims service manager\ninsurance claims director','\n',char(10)),'Insurance claims managers lead the team of insurance claims officers to ensure they handle insurance claims properly and efficiently. They deal with more complex customer complains and assist with fraudulent cases. Insurance claims managers work with insurance brokers, agents, loss adjusters and customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9422c30d-c5ef-4fcc-a493-d67ee42189c0','key_16955','','','7412','7412.8','marine electrician',replace('marine systems electrician\nmarine diagnostic electrician\nvessel electrician\nmarine installation electrician\nvessel electronics installation technician\nnaval electrician\nmarine electronic networks electrician\nmarine design electrician\nmarine repair electrician\nmarine electrician\nvessel electronics design technician\nvessel electronic networks technician\nvessel electronics repair technician\nvessel electronic systems technician','\n',char(10)),'Marine electricians install, maintain and repair electrical and electronic systems in vessels such as air conditioning systems, lamps, radios, heating systems, batteries, electrical wiring and alternators. They use diagnostic testing equipment to inspect vessels and find faults. To do repair work, they use hand tools and specialised electrical instruments and machines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9487f835-b5a9-4742-8ecf-35520911e28c','key_16956','','','3255','3255.1','massage therapist',replace('reflexology massage therapist\nbody massage therapist\nprofessional masseur\nclinical masseur\nmasseur\nmedical masseur\nThai massage therapist\ndeep tissue massage therapist\nmasseuse\nprofessional massage therapist\nmedical massage therapist\nSwedish massage therapist\nmassage therapist\nacupressure treatment specialist\nshiatsu massage therapist','\n',char(10)),'Massage therapists provide therapeutic massage treatments to improve the well-being of their clients. They perform a variety of massage types such as shiatsu and swedish massage, according to their client''s needs and preferences.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/949ab84f-5d76-4139-a568-45691b7ebc53','key_16957','','','1212','1212.2.1','equality and inclusion manager',replace('employment equity director\nequity and diversity manager\nequity programme director\nequality & inclusion worker\nequality and inclusion manager\naccess and equality manager\nequity and inclusion manager\ndiversity, equity and inclusion director\nequity, diversion and inclusion manager\ncivil rights manager\nequal employment opportunity officer\nequal rights manager\nsenior equality and inclusion manager\ndiversity and equality manager\nequality manager\nequity program director\ndiversity and inclusion manager\nequal opportunities manager\nequality and inclusion worker\nequity and inclusion specialist\nequality and inclusion director\ndiversity leader\nmanager equality and inclusion\nanti-discrimination manager','\n',char(10)),'Equality and inclusion managers develop policies to improve affirmative action, diversity and equality matters. They inform staff in corporations on the importance of the policies, and implementation and advise senior staff on corporate climate. They also perform guidance and support duties for employees.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/949bccd9-e1d2-4c57-b787-53b451aa1354','key_16958','','','7317','7317.5','wicker furniture maker',replace('wicker furniture maker\nrattan furniture weaver\nwicker-furniture manufacturer\nrattan furniture maker\nrattan furniture assembler\nwicker furniture assembly worker\nwillow furniture weaver\nwicker furniture weaver\nwicker furniture fabricator\nwicker-furniture producer\nrattan furniture production process worker\nwicker furniture manufacturer\nwicker furniture producer\nwicker furniture creator\nwicker furniture assembler\nwicker-furniture fabricator\nwicker furniture production process worker\nwicker furniture production operative\nrattan furniture assembly worker\nrattan furniture production operative\nwicker-furniture creator\nrattan furniture manufacturer','\n',char(10)),'Wicker furniture makers choose and prepare materials such as softened rattan or willow branches in order to produce wicker furniture such as chairs, tables and couches. They use hand, power or machine tools to cut, bend and weave materials to create the desired objects. Finally, they treat the surface to ensure a finished look and protect it from corrosion and fire using waxes, lacquers and other coatings.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/949ed945-f697-415e-b4c5-51d8abf7cfdf','key_16959','','','2149','2149.11.1','nanoengineer',replace('nanosystems specialist\nnanotechnology specialist\nnanotechnology engineer\nnanoscience expert\nnano-engineer\nnano engineer\nnanotechnologist\nnanoscience specialist\nnanoengineer\nnanoscience engineer\nnanosystems expert\nnanosystems engineer\nnanotechnology expert\nnanoscientist\nnano scientist\nnano systems engineer','\n',char(10)),'Nanoengineers combine the scientific knowledge of atomic and molecular particles with engineering principles for applications in a varied array of fields. They apply findings in chemistry, biology, and materials engineering, etc. They use technological knowledge for the improvement of existing applications or the creation of micro objects.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/949f1dc9-fb19-4212-8ac6-35ce83c88921','key_16960','','','3435','3435.16','prompter',replace('theatre prompter\nprompt\nopera prompter\nprompter\nsouffleur\ntheater prompter','\n',char(10)),'Prompters prompt or cue performers when they forget their lines or neglect to move to the right position on the stage.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/94adbb11-1c90-4092-ae91-781e8a55865d','key_16961','','','3324','3324.1.1','shipbroker',replace('shipbroker\nshipping broker','\n',char(10)),'Shipbrokers act as intermediaries between buyers and sellers of ships, cargospace on ships and charter ships for the transfer of cargo. They inform clients on the shipping market mechanisms and movements, report on vessel and cargospace prices and sales, and negotiate not only the cost of the vessels, cargospace or cargo but also the logistical requirements for the transfer of the vessel or commodity cargo to the buyers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/94d94bd0-9ac3-4bd8-81c0-c3fdf551aa16','key_16962','','','2651','2651.9','sculptor',replace('clay sculptress\nstone sculptress\nwood sculptress\nsculpting artist\nclay sculptor\nmetal sculptress\nice sculptor\nsculptress\nsculptor\nwelded steel sculptor\nice sculptress\nmarble sculptress\nmarble sculptor\nwood sculptor\nmetal sculptor\nstone sculptor\nwelded steel sculptress','\n',char(10)),'Sculptors may use a wide variety of materials to create sculptures, such as stone, clay, glass, wood, plaster, or any material of their choice. Those materials can be carved, modelled, moulded, cast, wrought, welded, and so on, in order to reach the desired shape.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/94e0bedd-e56e-42be-a5a4-b74a260f437c','key_16963','','','1219','1219.4','department manager',replace('business unit coordinator\nunit manager\nunit director\ndepartment manager\nbusiness unit manager\ndivision manager\ndepartment coordinator','\n',char(10)),'Department managers are responsible for the operations of a certain division or department of a company. They ensure objectives and goals are reached and manage employees.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/951b9d94-9bf1-4771-877d-3a26b75d9e53','key_16964','','','2166','2166.2','animation layout artist',replace('layout technical director\nlayout animator\nbackground layout artist\nset up technical director\n2D layout artist\n3D layout artist\n3D layout animator\nsenior layout artist\nanimation layout designer\nanimation layout artist\nlead layout artist\n2D layout animator\nlayout artist','\n',char(10)),'Animation layout artists work with the cameramen and director to coordinate and create optimal 3D animation shots. They translate 2D storyboards into 3D animated shots and are responsible for camera angles, frames and lighting of animation scenes. Animation layout artists decide which action takes place in which animation scene.','','Excludes  <a resource="http://data.europa.eu/esco/occupation/52df9d56-efd4-48d0-ad93-59231943fc4c" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/52df9d56-efd4-48d0-ad93-59231943fc4c"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">3D animator</span></a>. Excludes people performing the organisation of the shots in which the 3D concepts are used.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/952aa13c-731f-4add-98d1-66b59f70e607','key_16965','','','8332','8332.6','live animal transporter',replace('animal conveyance\nanimal transporter\nanimal transport service\nanimal carriage\nanimal travel provider\nlive animal transporter\nanimal travel service','\n',char(10)),replace('Live animal transporters provide transport and transfer for live animals, including the monitoring of the health and welfare of animals, the planning of, and preparation for journeys, and loading and unloading of animals, according to national and international legislation. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/952c766a-db5c-42b2-809d-d14cbb02cdb6','key_16966','','','3122','3122.1','dairy processing technician',replace('dairy technician\ndairy process technician\ndairy processing technician\ndairy employee','\n',char(10)),replace('Dairy processing technicians supervise and coordinate production processes, operations, and maintenance workers in milk, cheese, ice cream and/or other dairy production plants. They assist food technologists in improving processes, developing new food products and establishing procedures and standards for production and packaging. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/95324a4a-a5ea-4d89-8ede-4422ccb46a28','key_16967','','','3141','3141.2.2','biochemistry technician',replace('biochemistry lab technician\nbiology technician\nchemistry technologist\nbiology technologist\nbiochemistry technician\nbiochemistry technologist\nbiologist\nchemistry technician\nbiochemistry laboratory technician\nchemist','\n',char(10)),'Biochemistry technicians provide technical assistance in researching, analysing and testing the reactions caused by chemicals in living organisms. They use laboratory equipment to help develop or improve chemical-based products and also collect and analyse data for experiments, compile reports and maintain laboratory stock.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/955ae83a-4d91-4db8-a5d6-bd489e5058df','key_16968','','','7233','7233.11','mining equipment mechanic',replace('heavy machines operator\nheavy machines operators\nmining processing engineer\nmining equipment mechanic\nmine machinery technicians\nheavy machines mechanic\nmine machinery mechanic\nfitter\nheavy machinery technician\nmining mechanic\nmine operations mechanic\nmining equipment mechanics\nmine machinery technician','\n',char(10)),'Mining equipment mechanics install, remove, maintain and repair mining equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9581c837-fee7-4068-9618-1116fd93d723','key_16969','','','3112','3112.8','engineering assistant',replace('technical support worker\nproject aide\nengineering assistant\ntechnological assistant\ntechnical support\ntechnical supporter\nproject support worker\ntechnical assistant\nproject supporter\nengineering project assistant\ntech assistant\nproject support\ntechnology assistant\ntechnical aide','\n',char(10)),'Engineering assistants ensure the administration and monitoring of technical and engineering files for projects, assignments, and quality matters. They assist engineers with their experiments, participate in site visits, and administer the collection of information.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/95a6ecd2-a140-4861-aad7-dcb8cfc1c9a3','key_16970','','','8189','8189.2','slitter operator',replace('slitting machine tender\npaper slitter operative\nslitter operator\nsplitter rewinder machine operator\nslitter setter\nslitting machine operator\nslitting machine setter\nslitting machine worker\nsteel slitter operative\nsteel slitter operator\naluminium bending operator\nslitting machine operative\nslitting machinist\nshearer\nslitting line setter\nslitting rewind machine operator\nslitter operative\npaper slitter operator\nslitter tender','\n',char(10)),'Slitter operators set up, operate, or tend machines, in order to cut, slit, bend, or straighten sheets of metal, paper, or other materials to specific widths. Slitter operators must also ensure quality, by examining various end-products and observing pre-defined tolerances.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/95a89206-4dc8-4753-a5d9-bc14a0e5f7ca','key_16971','','','7534','7534.3','upholsterer',replace('craft upholsterer\ncraftswoman upholsterer\nseat maker\nchairmaker\nheritage upholsterer\nseat  builder\ncraftsperson upholsterer\nupholsterer\nsoft furnishing maker\ncraftsman upholsterer\nchair maker\ncommercial upholsterer\nmattress maker\nmattress builder\nchair builder','\n',char(10)),'Upholsterers provide objects such as furniture, panels, orthopaedic devices, fixtures or vehicle parts with padding or soft covering. They can install, repair or replace the upholstery of objects with materials such as fabrics, leather, suede or cotton. Upholsterers install the webbings and springs necessary to cover the material.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/95cd3d64-20d0-4681-857d-d6c63d07349f','key_16972','','','2145','2145.1.2','brewmaster',replace('brewmaster\nbrewing manager\nbrewery manager\nbeer production manager\nmaster brewer','\n',char(10)),'Brewmasters ensure brewing quality of current products and create mixtures for the development of new products. For current products, they oversee the whole brewing process following one of many brewing processes. For new products, they develop new brewing formulas and processing techniques or modify existing ones as to come up with potential new products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/95cda6a9-a70a-4a93-9442-11d573cb4a02','key_16973','','','2222','2222.1','midwife',replace('head of midwifery services\npre-natal nurse\nmidwife researcher\nfoetal nurse\ncommunity midwife\nintensive care neonatal nurse\nmidwife\nconsultant midwife\nchildbirth assistant\nobstetric nurse','\n',char(10)),'Midwives assist women in childbirth by providing the necessary support, care and advice during pregnancy, labour and the postpartum period, conduct births and provide care for the new-born. They advise on health, preventive measures, preparation for parenthood, detection of complications in mother and child, accessing of medical care, promoting normal birth and carrying out emergency measures.','','','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/95d0a616-a4a9-4f56-a8fb-4de43cdb4c82','key_16974','','','2635','2635.3.13','family social worker','family social worker','Family social workers provide advice to families about the range of social services available to solve their problems or challenging life situations such as addictions, mental illnesses, medical or financial struggles. They help their users to access these social services and monitor their appropriate usage.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/95d69048-3a4b-4cce-a155-584aaecfe403','key_16975','','','3122','3122.3.3','footwear assembly supervisor',replace('footwear assembly supervisor\nassembling team leader\nassembly line supervisor\nassembly team leader\nassembly room forewoman\nassembly supervisor\nassembly room supervisor\nassembling room supervisor\nfootwear lasting supervisor\nfootwear assembling room supervisor\nassembling room forewoman\nassembly room foreman\nassembling room foreman\nlasting supervisor','\n',char(10)),'Footwear assembly supervisors check and coordinate activities of operators in the lasting room. They are in charge of coordinating lasting room activity with the previous and following activities of the production chain. They examine uppers and soles to be lasted and give instructions to produce them. These supervisors are in charge of supplying the lasting room with uppers, lasts, shanks, counters and small handling tools, and they are also in charge of the quality control of the lasting.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/95d8f19e-f62b-443f-ac53-324ea8f98cf5','key_16976','','','1324','1324.3.2.5','import export manager in china and other glassware',replace('international trade manager in china and glassware\nimport export manager in china and other glassware\ngraduate export manager in china and glassware\nassistant import export manager in china and glassware\ngraduate import manager in china and glassware\ntrainee import manager in china and glassware\ntrainee export manager in china and glassware\nsenior import export manager in china and glassware\ngraduate import export manager in china and glassware\nsenior import manager in china and glassware\nassistant export manager in china and glassware\nassistant import manager in china and glassware\nsenior export manager in china and glassware\ncustoms compliance manager in china and glassware\ninternational trade compliance manager in china and glassware\ntrainee import export manager in china and glassware','\n',char(10)),'Import export managers in china and other glassware install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/95da5e1b-cc1e-46bd-96c4-81d4f7003080','key_16977','','','7119','7119.3','manufactured wooden building assembler',replace('prefab house joiner\nprefabricated building assembler\nprefab house builder\nmanufactured wooden building joiner\nmanufactured wooden building fabricator\nmanufactured wooden building assembler\nmanufactured building assembler\nmanufactured wooden building constructor\nmanufactured wooden building builder\nprefab house assembler\nprefab house constructor\nprefab house fabricator\nprefab home assembler\ntruss assembler','\n',char(10)),'Manufactured wooden building assemblers put together wooden elements for use in construction. The elements, or modules, may consist of walls with windows and doors built-in, or may be as large as whole rooms. The assemblers put together the supporting structure, insulation materials, and covering, and fasten it together to obtain usable modules.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/95f56fc4-99f9-49da-b942-1dfbc857e933','key_16978','','','5111','5111.1','cabin crew manager',replace('chief purser\ncabin service director\nhead of cabin crew\nmanager of cabin crew\ncabin service manager\ncabin crew manager\ncabin crew head\nflight service manager\ninflight service manager\ncustomer service director','\n',char(10)),'Cabin crew managers are responsible for motivating the cabin crew team to surpass passengers'' expectations and for the application of safety regulations on board the plane.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/96186698-f79d-423a-9652-a2a58085d5f1','key_16979','','','7316','7316.1.4','wood painter',replace('decorator of wooden items\nwood painter and designer\nwood painter\nwood painting specialist\nwood painter & designer\nwooden items painter\nwooden items decorator\nwood decorator','\n',char(10)),'Wood painters design and create visual art on wooden surfaces and objects such as furniture, figurines and toys. They use a variety of techniques to produce decorative illustrations ranging from stenciling to free-hand drawing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/961cbd1f-2a9b-4756-b227-67a1c23c94b6','key_16980','','','4212','4212.3','casino cashier',replace('gaming cashier\ncasino head cashier\ngame cashier\ncasino cashier\nbooth cashier\ncasino cage cashier\ncasino income auditor\ncage cashier\ncasino banker\nmain banker','\n',char(10)),'Casino cashiers exchange tokens, coins or chips for money. They arrange pay-outs and obtain customers'' signatures and identification. They audit and count money in a cash register, enforcing money laundering regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/96430121-2757-4e96-9d6f-7517b25db4f7','key_16981','','','5153','5153.1.2','house sitter',replace('house sitter\ncustodian\nestate caretaker\nhouse caretaker','\n',char(10)),'House sitters move in the house of their employers in order to maintain security of the property during their absence. They monitor entrance areas and prevent unauthorized persons from entering the house, inspect conditions of the facility such as plumbing and heating and contact repairers if necessary. House sitters may also do some cleaning activities, forward mail and pay bills.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/96514fed-b9a9-4df3-ab0b-20676369297d','key_16982','','','8142','8142.2','cake press operator',replace('cake press tender\nhydraulic press setter\nplastic cake press operator\nplastic cake press worker\ncake press worker\ncake press setter\nplastic cake press operative\ncake press operator\nplastic production process operator\nhydraulic press operative\nplastic production process worker\nhydraulic cake press operator\nplastic cake press tender\ncake press operative\nhydraulic press worker\nplastic cake press setter\nplastic production process operative\nhydraulic press tender','\n',char(10)),'Cake press operators set up and tend the hydraulic presses that compress and bake plastic chips into cake moulds to produce plastic sheets. They regulate and adjust the pressure and temperature.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9653fb92-ad48-4865-a471-f270302fa93b','key_16983','','','5112','5112.2','railway passenger service agent',replace('railway transportation service representative\nrailway customer transit agent\npassenger representative\nrailway passenger agent\nrailway passenger service agent\nrailway station customer service agent\nrailway station customer service representative\nrailway customer service agent\nrailway customer service representative\ncustomer transit agent','\n',char(10)),'Railway passenger service agents spend time with railway station customers, answer their questions and react quickly and safely to unexpected situations. They provide information, mobility assistance, and security in railway stations. They provide accurate and up to date information on train arrival and departure times, train connections, and help customers plan their travels.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/969f8af0-3c0d-403a-9c16-a6fb76c6b443','key_16984','','','2651','2651.5','conceptual artist',replace('conceptual creator\nconcept artist\nconceptual artist','\n',char(10)),'Conceptual artists choose any material as an artistic tool or/and material to be presented as an artistic experience to the public. Their work, belonging to the fine arts, can be two-dimensional (drawing, painting, collage), three-dimensional (sculpture, installation) or four-dimensional (moving images, performance).','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/96a0d8d3-d4a6-473d-80d6-b5dbadde940a','key_16985','','','9411','9411.2','quick service restaurant crew member',replace('fast food crew member\nquick service restaurant crew member\nquick service crew member\nsnack maker\nfast-food crew member\ncrepes maker\nfast food worker\npancakes maker','\n',char(10)),'Quick service restaurant crew members prepare, cook and serve food and beverages in a quick service operation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/96be6ac3-5932-4bfc-933d-84b267d0d486','key_16986','','','2621','2621.2','collection manager',replace('conservator\ncurator\nmuseum technician\ncollection manager\ncollections manager','\n',char(10)),'Collection managers ensure the care and preservation of objects within cultural institutions, like museums, libraries, and archives. Collection managers, along with exhibition curators, and conservators, play a very important role in collections care. They can be found in most large museums.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/96c870c0-f93a-43c3-9ce4-1fe9e737fece','key_16987','','','1324','1324.3.1.6.20','machinery, industrial equipment, ships and aircraft distribution manager',replace('machinery, industrial equipment, ships and aircraft logistics planner\nmachinery, industrial equipment, ships and aircraft logistics manager\nmachinery, industrial equipment, ships and aircraft supply chain manager\ntrainee machinery, industrial equipment, ships and aircraft distribution manager\nmachinery, industrial equipment, ships and aircraft distribution planner\ngraduate machinery, industrial equipment, ships and aircraft distribution manager\nassistant machinery, industrial equipment, ships and aircraft distribution manager\nsenior machinery, industrial equipment, ships and aircraft distribution manager\nmachinery, industrial equipment, ships and aircraft supply chain planner\nmachinery, industrial equipment, ships and aircraft distribution manager','\n',char(10)),'Machinery, industrial equipment, ships and aircraft distribution managers plan the distribution of machinery, industrial equipment, ships and aircraft to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/96e139de-0756-45ad-b512-f7c2109a72b1','key_16988','','','3122','3122.3.1','aircraft assembly supervisor',replace('helicopter engineering compliance supervisor\naircraft assembly quality inspector\naircraft engineering compliance supervisor\naircraft quality compliance inspector\naircraft quality compliance supervisor\naeroplane assembly safety supervisor\naeroplane engineering compliance supervisor\naeroplane quality compliance inspector\naircraft assembly quality supervisor\naircraft  assembly safety supervisor\naeroplane assembly supervisor\nhelicopter assembly safety supervisor\naeroplane quality compliance supervisor\naircraft assembly supervisor','\n',char(10)),'Aircraft assembly supervisors coordinate the employees involved in aircraft manufacturing and schedule their activities. They prepare production reports and recommend measures to reduce the cost and improve productivity such as hiring, ordering new equipment and implementing new production methods. They train employees in company policies, job duties and safety measures. They oversee the supplies and communicate with other departments to avoid unnecessary interruptions of the production process.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/96e20037-0a25-4bf6-a25e-808d1605d890','key_16989','','','2513','2513.3','user interface designer',replace('user interface designer\nUI designer\naccessibility designer\nuser interface designers','\n',char(10)),'User interface designers are in charge of designing user interfaces for applications and systems. They perform layout, graphics and dialogues design activities as well as adaptation activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/96e88e86-f2c7-42e6-a2b2-17e4f08d4ee0','key_16990','','','1212','1212.1','employee volunteering programme coordinator',replace('corporate volunteering coordinator \ncorporate volunteering manager','\n',char(10)),'Employee volunteering programme coordinators work across sectors and fields to coordinate and manage the employee volunteering (sometimes called corporate volunteering) programme for their employer. They are in charge of connecting with local community organisations to determine needs and arrange for volunteers from within the staff of the company to engage with local entities, such as local authorities or local civil society organisations, to meet those needs. Employee volunteering programme coordinators might also arrange for volunteers to undertake their duties online in collaboration with civil society initiatives meeting identified needs. These roles can exist in the company or setting where the employees are based and also in the civil society organisation who receives the volunteers from the employee or corporate volunteering scheme.','','The employee volunteering programme coordinator does not usually manage the volunteers directly but rather acts as the interface between the employer and the volunteering organisation that the employees will volunteer with or vice versa.  ','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9703fe09-51fd-4d00-a2a1-99ba809521ac','key_16991','','','1420','1420.4.39','sporting and outdoor accessories shop manager',replace('sporting equipment shop manager\nsports goods store manager\nbusiness leader sporting and outdoor equipment shop\nsports equipment shop manager\nsports store manager\nsporting accessories shop manager\nsporting and outdoor accessories shop manager\nsports goods shop manager\nsports shop manager\nsporting equipment store manager\nsports equipment store manager\nsporting articles store manager\nsporting articles shop manager\nstore manager sport and camping equipment\nbranch owner sporting and outdoor equipment shop','\n',char(10)),'Sporting accessories shop managers assume responsibility for the activities and staff in specialised shops. They manage employees, monitor the sales of the store, manage budgets and order supplies when a product is out of supply and perform administrative duties if required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9711dcfc-d9a8-4fba-9c5e-8428d8bcb53e','key_16992','','','2353','2353.1','language school teacher',replace('language school educator\nTESOL teacher\ninstructor in language school\nteacher in a language school\nESOL teacher\nEnglish for speakers of other languages teacher\nlanguage school lecturer\nteacher of english to speakers of other languages\nlanguage school instructor\nTESOL practitioner\nlanguage school teacher','\n',char(10)),'Language school teachers educate non-age-specific students in a language that is not their native language at a specialised school, not bound by a level of education. They focus less on the academic aspect of language teaching, as opposed to language teachers in secondary or higher education, but instead on the theory and practice that will be most helpful to their students in real-life situations since most choose instruction for either business, immigration or leisure reasons. They organise their classes using a variety of lesson materials, work interactively with the group, and assess and evaluate their individual progress through assignments and examinations, putting emphasis on active language skills such as writing and speaking.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9729c0f3-c9bc-482e-ac59-a82ec3b67ba3','key_16993','','','3511','3511.1','data centre operator',replace('data centre operators\ndata center operator\ndata center administrator\ndata centre employee','\n',char(10)),'Data centre operators maintain computer operations within the data centre. They manage daily activities within the centre to solve problems, maintain the system availability, and evaluate the system''s performance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/972ef593-89c3-468b-aad5-81bc575feb58','key_16994','','','1324','1324.3.2.30','import export manager in textile industry machinery',replace('graduate import manager in  textile industry machinery\nsenior import export manager in  textile industry machinery\nassistant export manager in  textile industry machinery\nimport export manager in textile industry machinery\nassistant import manager in  textile industry machinery\nsenior import manager in  textile industry machinery\ntrainee import manager in  textile industry machinery\nassistant import export manager in  textile industry machinery\ninternational trade compliance manager in  textile industry machinery\nsenior export manager in  textile industry machinery\ninternational trade manager in  textile industry machinery\ntrainee import export manager in  textile industry machinery\ncustoms compliance manager in  textile industry machinery\ngraduate export manager in  textile industry machinery\ngraduate import export manager in  textile industry machinery\ntrainee export manager in  textile industry machinery','\n',char(10)),'Import export managers in textile industry machinery install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9740933e-e04a-41f6-af1f-a6d58c7eb0ec','key_16995','','','2143','2143.3','wastewater engineer',replace('wastewater technology engineering consultant\nwaste-water engineer\nwastewater technology engineering adviser\nwastewater pipeline engineer\nsewer pipeline engineer\nwastewater engineer\nwastewater technology engineering expert\nwastewater engineering adviser\nsewage system engineer\nwastewater processing engineer\nwastewater process engineer\nsewer system designer\nwastewater engineering consultant\nwastewater system designer\nwastewater engineering specialist\nwaste water pipeline engineer\nwaste water system designer\nwastewater technology engineer\nwastewater engineering expert\nwaste water system engineer\nwastewater technology engineering specialist\nwastewater system engineer\nsewage engineer\nwaste water engineer','\n',char(10)),'Wastewater engineers design sewage systems and networks for the removal and treatment of wastewater from cities and other residential areas. They design systems which are compliant with environmental standards, and aim to minimise impact on the ecosystem and on citizens in the vicinity of the network.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/974494ee-5f32-4eec-9950-9d431e902dc1','key_16996','','','5223','5223.7.23','meat and meat products specialised seller',replace('meat counter specialised seller\nmeat store specialised seller\nbutchery store salesperson\nmeat and meat products store specialised seller\nmeat and meat products store salesperson\nmeat counter salesperson\nbutchery department salesperson\nbutchery store specialised seller\nbutchers assistant\nmeat store salesperson\nmeat and meat products specialized seller\nmeat specialised seller\nmeat salesperson\nbutchery department specialised seller','\n',char(10)),'Meat and meat products specialised sellers cut up and sell meat in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/97467934-a220-41ca-96df-36fc85258c47','key_16997','','','1420','1420.4.8','bookshop manager',replace('press and bookshop manager\nantiquarian book shop manager\nbook superstore manager\nbookstore manager\nantique book shop manager\nbook and stationery shop manager\nbook and stationery store manager\nstationer\nantiquarian book store manager\ncookery book store manager\nmagazine and bookshop manager\npress and bookstore manager\ncomic book shop manager\nantique book store manager\nmagazine and bookstore manager\nbookshop manager\nbookseller\ncomic book store manager\ncookery book shop manager\nstationer and bookseller','\n',char(10)),'Bookshop managers assume responsibility for activities and staff in this specialised store.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/974d4eab-fbee-4c52-b16e-73bdd8c25b53','key_16998','','','2611','2611.1','lawyer',replace('procurement law lawyer\nproperty law lawyer\ncopyright and media  lawyer\ncommercial and company lawyer\nattorney\nlawyer\npersonal law lawyer\ninheritance law lawyer\nagricultural lawyer\nbuilding  lawyer\nemployment lawyer\nadvocate\nprosecutor assistant\ninternational business law lawyer\ncounsellor\npatent law lawyer\nlabour lawyer\nadministrative law lawyer\nsocial law lawyer\nindustrial property lawyer\nproperty law attorney\nfiscal lawyer\nmedical  lawyer\nbank and capital market lawyer\ninformation technology lawyer\nbuilding and construction dispute lawyer\nintellectual property lawyer\nbarrister\nfamily law lawyer\ntraffic law lawyer\nfinancial lawyer','\n',char(10)),'Lawyers provide legal advice to clients and act on their behalf in legal proceedings and in compliance with the law. They research for, interpret and study cases to represent their clients in a variety of settings such as courts and administrative boards. They create arguments on behalf of their clients for lawsuits in different contexts with the aim of finding a legal remedy.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9757122b-1161-4f4a-8286-a603d4ad7012','key_16999','','','8160','8160.27','distillery worker',replace('distillery production worker\ndistillery worker\ndistiller\nspirit beverage worker\nspirits production worker','\n',char(10)),'Distillery workers operate industrial distillery equipment and machinery. They perform the maintenance and cleaning of the machinery, roll barrels, and stamp barrel heads.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9766cc19-5a64-4b3c-a741-71561285a419','key_17000','','','2310','2310.1.1','anthropology lecturer',replace('anthropology docent\nhigher education anthropology teacher\nprofessor of anthropology\nlecturer in anthropology\nanthropology teacher\nuniversity anthropology lecturer\nanthropology lector\nuniversity lecturer in anthropology\nanthropology professor\ninstructor in anthropology\nanthropology lecturer\nlecturer of anthropology\nanthropology instructor\nspecialist lecturer in anthropology\nuniversity anthropology teacher','\n',char(10)),'Anthropology lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, anthropology, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, grading papers and exams and leading review and feedback sessions for the students. They also conduct academic research in their field of anthropology, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/976c739f-fec7-45c2-b12e-e1b496aa816b','key_17001','','','8172','8172.1','chipper operator',replace('chipper machine operator\nchipper machine technician\nwood chipper technician\nchipper operator\nwood chipper specialist\nchipper specialist\nchipper\nchipper technician\nwood chipper operator\nwood chipper\nchipper machine specialist','\n',char(10)),'Chipper operators tend machines that chip wood into small pieces for use in particle board, for further processing into pulp, or for use in its own right. Wood is fed into the chipper and shredded or crushed using a variety of mechanisms.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/976c7f50-7f9e-4347-bbb9-f0dc842046a3','key_17002','','','2634','2634.2.3','health psychologist',replace('specialist healthcare psychologist\nhealth psychology researcher\nexpert healthcare psychologist\nconsultant in health psychology\nhealthcare psychologist\nhealth psychologist\npractitioner of healthcare psychology\nresearch health psychologist\nconsultant health psychologist','\n',char(10)),'Health psychologists deal with the various aspects of health related behaviour of individuals and groups, by helping individuals or groups prevent illness and promote healthy behaviours by also providing counseling services. They perform tasks for the development of health promotion activities and projects on the basis of psychological science, research findings, theories, methods and techniques. They also engage in research about health related issues to influence public policy on health care issues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/976cbfcc-8958-4800-a435-71d15405a42a','key_17003','','','2352','2352.1','special educational needs teacher',replace('special educational needs teacher\nautism teacher\nspecial education resource teacher\nspecial educational needs professional\nteacher in special education needs\nteacher for hearing impaired children\nspecial needs educator\nSEN teacher\nteacher of special educational needs\neducator in special educational needs\nspecial needs teacher\nspecial education teacher','\n',char(10)),'Special educational needs teachers work with and teach children, young people, and adults with an intellectual or physical disability. They use a range of specialised concepts, strategies and tools to optimise learners'' communication, mobility, autonomy, and social integration. They select teaching methods and support resources to enable individual learners to maximise their potential for independent living.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/976f752c-5f10-42ca-84fb-89aa529af04f','key_17004','','','8322','8322.5','patient transport services driver',replace('driver of patient transport vehicle\nPTS driver\ndriver in patient transport services\npatient transport driver\nmedical transport vehicle driver\nambulance care assistant\npatient transport services driver\ndriver of medical transportation vehicle\nmedical transport service assistant\npatient transportation services driver','\n',char(10)),'Patient transport services drivers transfer disabled, vulnerable and elderly patients to and from health care facilities such as hospitals or social care settings. They drive the ambulance and maintain all the related equipment but under non-emergency circumstances.','',replace('Includes people performing transport of non-emergency patients. \n Excludes people performing patients treatment or medical treatment assistance.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/97896345-d102-4549-97a5-8851979e6938','key_17005','','','7314','7314.2','production potter',replace('abrasive wheel moulder\npot maker\ncraft potter\npotter\nceramics maker\npottery and porcelain modeller\nproduction potter','\n',char(10)),'Production potters process and form clay, by hand or by using the wheel, into end-products pottery, stoneware products, earthenware products and porcelain. They introduce the already shaped clay into kilns, heating them at a high temperature in order to remove all the water from the clay.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/978ae3a2-0831-415a-8396-b84dde656fc5','key_17006','','','7531','7531.5','tailor',replace('outfitter\ngarment worker\ntailoress\ncustom dressmaker\ncustom tailor\nbespoke tailor\ncustom sewer\ncouture alterations dressmaker\ndressmaker\nseamstress\nsartor\ncloakmaker\ndressmaker  of men\ngarment maker\nalterations expert\nalterations sewer\ne-tailor\ncouturier\ntailor\nseamster\nsuit maker','\n',char(10)),'Tailors design, make or fit, alter, repair tailored, bespoke or hand-made garments from textile fabrics, light leather, fur and other material, or make hats or wigs for men. They produce made-to-measure wearing apparel according to customer''s or garment manufacturer''s specifications. They are able to read and understand size charts, details surrounding finished measurements, etc.','',replace('Includes people producing different clothing within their specialisation from design to patternmaking up to sewing and finished custom-made (bespoke) clothes for men. \nIncludes people producing the whole garment or working in a team where each member has a specialism. Excludes <a resource="http://data.europa.eu/esco/occupation/5589e8e0-48d9-4193-8c85-86ba9f17e0e9" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/5589e8e0-48d9-4193-8c85-86ba9f17e0e9"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">dressmaker</span></a>.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/97b3cab1-f4f0-41ed-8c80-e65e6c067e95','key_17007','','','7126','7126.3','gas service technician',replace('gas service technician\ngas system operator\ngas test technician\ngas inspector\ngas service supervisor\ngas technician\ngas service controller\ngas installer\ngas system operative\ngas supply technician\ngas service operator\ngas treater\ngas supply engineer\ngas distributor\ngas service operative\ngas distribution supervisor\ngas plant engineer\ngas equipment service technician\ngas producer\ngas plant inspector\ngas plant dispatcher\ngas distribution inspector\ngas distribution controller','\n',char(10)),'Gas service technicians install and maintain gas service appliances and systems in facilities or buildings. They install the equipment in accordance with regulations, repair faults, and investigate leaks and other problems. They test the equipment and advise on the use and care of appliances and systems which use gas energy.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/97d65832-3817-4ded-946b-cfc4a0237b9a','key_17008','','','2141','2141.6','leather production planner',replace('leather materials planner\nleather orders planner\nleather production control planner\nleather production planners\nleather production scheduler\nleather production planner\nleather production planner and scheduler','\n',char(10)),'Leather production planners are responsible for planning and following production planning. They work with the production manager to follow progress of the schedule. They work together with the warehouse to ensure optimum level and quality of materials are provided, and also with the marketing and sales department to meet customer order requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/97dadd59-d1cb-4fef-81a7-d92eb15572e2','key_17009','','','7212','7212.3.4','spot welder',replace('robotic welder\nbutt welder\nweld technician\nflash welder\nfitter-welder\nconstruction welder\nelectric spot welder\nresistance welder\nelectric arc welder\nstitch welder\nwelder-fabricator\nspot welding machine operator\nwelder-fitter\nsheet metal welder\nwelder fabricator\ntack welder\nspot welder\nultrasonic metal welder\nsheet-metal welder','\n',char(10)),'Spot welders set up and tend spot welding machines designed to press and join metal workpieces together. The metal resistance to the passage of electrical current and the subsequent heat created in the process allows for the local melting and joining of the parts.    ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/97dd9a0c-dd6b-416d-9537-655a08dd2895','key_17010','','','7312','7312.3.3','piano maker',replace('piano maker\npiano maker/tuner\npiano maker/inspector\npiano craftsman\npiano builder\npiano technician\npiano maker/technician\npiano tester\npiano craftswoman','\n',char(10)),'Piano makers create and assemble parts to make pianos according to specified instructions or diagrams. They sand wood, tune, test and inspect the finished instrument.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/980c52f3-0054-45b1-a6f1-20ee8a1e8b9e','key_17011','','','1420','1420.4.10','clothing shop manager',replace('baby fashion shop manager\nclothes shop manager\nwomen''s fashion store manager\nclothes store manager\nplus size fashion shop manager\nmen''s fashion store manager\nfashion store manager\nkids fashion store manager\nclothing store manager\nclothing shop manager\nmaternity fashion store manager\nmen''s fashion shop manager\nkids fashion shop manager\nbridal shop manager\nprofessional wear store manager\nfashion shop manager\nplus size fashion store manager\nchildren''s fashion store manager\nathletic fashion shop manager\nathletic fashion store manager\nchildren''s fashion shop manager\nprofessional wear shop manager\nmaternity fashion shop manager\nbaby fashion store manager\nbridal store manager','\n',char(10)),'Clothing shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/98164d70-93a8-409c-aa9f-7d56314f4d7f','key_17012','','','7223','7223.17','router operator',replace('cnc router operative\nrouter tender\nCNC router setter\nrouter operator\nmulti-spindle routing machine operator\ncnc router setter\nCNC router operative\nCNC router operator\nmulti-spindle routing machine operative\nCNC router tender\nrouter operative\nmulti-spindle routing machine setter\nrouter setter\nmulti-spindle routing machinist\nmulti-spindle routing machine tender\ncnc router tender','\n',char(10)),'Router operators set up and operate multi-spindle routing machines, in order to hollow-out or cut various hard materials such as wood, composites, aluminium, steel, plastics; and others, such as foams. They are also able to read blueprints to determine cutting locations and specific sizes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/98358049-fbcd-479e-a644-e92bde5af2e0','key_17013','','','2432','2432.1','activism officer',replace('activism & events officer\nactivism officer\nactivism and engagement officer\ncampaign worker\nactivist\ncharity officer\nactivism & engagement officer\ncampaigner\nactivism and events officer\nactivism coordinator\nevents officer','\n',char(10)),'Activism officers promote or hinder social, political, economic or environmental change by using different tactics such as persuasive research, media pressure or public campaigning.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/983c2816-a339-4817-97f4-151a9ff3e64d','key_17014','','','3252','3252.2','medical records manager',replace('supervisor of medical records\nhealth records manager\nhealth records supervisor\nmedical records supervisor\nsupervisor of patient records\nmanager of medical records\ndirector of medical records\nmedical records manager\npatient records manager\nmanager of patient records\nsenior medical records officer\npatient records supervisor\nmanager of health records','\n',char(10)),'Medical records managers are responsible for managing activities of medical records units which maintain and secure patient data. They supervise, oversee and train employees while implementing medical department policies.','','Excludes people performing patients treatment or medical treatment assistance.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9851c83c-5670-4da0-964a-1ef0e682e30b','key_17015','','','2632','2632.7','thanatology researcher',replace('fellow in thanatology\nthanatological scholar\nthanatology researcher\nresearcher in the psychology of death and dying\nthanatologist\nthanatological researcher\nresearcher in thanatology','\n',char(10)),'Thanatology researchers study death and the dying in a variety of scientific fields such as psychology, sociology, physiology and anthropology. They contribute to the growth of knowledge on aspects of death such as the psychological phenomena the dying and those around them are experiencing. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/985400a7-362f-4f91-941a-2e149d94ac2a','key_17016','','','5131','5131.2.2.1','beer sommelier',replace('beer sommelier\nbeer selection advisor\nbeer advisor','\n',char(10)),'Beer sommeliers understand and advise on styles, brewing and best pairing of beers with foods in locations such as restaurants, breweries and shops. They know all about their ingredients, the history of the beers, glassware and draught systems. They prepare beer tastings, consult companies and customers, evaluate beer products and write about this subject.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/98588703-5f5c-41c6-a240-3411b6518b83','key_17017','','','3331','3331.2.1.28','import export specialist in pharmaceutical goods',replace('senior import export specialist in pharmaceutical goods\ntrainee export specialist in pharmaceutical goods\nassistant import specialist in pharmaceutical goods\nsenior import specialist in pharmaceutical goods\nassistant export specialist in pharmaceutical goods\ngraduate export specialist in pharmaceutical goods\nimport export specialist in pharmaceutical goods\ngraduate import specialist in pharmaceutical goods\ngraduate import export specialist in pharmaceutical goods\ninternational trade compliance specialist in pharmaceutical goods\ntrainee import specialist in pharmaceutical goods\nsenior export specialist in pharmaceutical goods\ncustoms compliance specialist in pharmaceutical goods\ntrainee import export specialist in pharmaceutical goods\nassistant import export specialist in pharmaceutical goods','\n',char(10)),'Import export specialists in pharmaceutical goods have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/98ad0008-13d9-4e9b-94bf-4f4a5faae764','key_17018','','','1324','1324.3.1.3','rail operations manager',replace('railway operations specialist\nrailway operations manager\ntrain operations manager\nrail operations manager\ntrain operations specialist\nrail control operator','\n',char(10)),'Rail operations managers are concerned with overseeing, designing, and controlling the processes of transport operation. Rail operation managers may work for rail operators concerning tasks such as managing the safe and efficient operation of transport services including customer relation tasks or the management of staff and work organisation in this field. Rail operation managers employed by infrastructure operators manage the efficient and safe use of the network. This includes the planning of timetables and the allocation of slots to customers. They plan the construction and maintenance work. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/98af7387-3974-457a-aa57-5397d1ea3949','key_17019','','','6113','6113.4','interior landscaper',replace('experts in interior landscaping\nindoor gardener\ninterior landscaper\ninterior gardener\ninterior landscapers\nexpert in interior landscaping\nindoor landscaper','\n',char(10)),'Interior landscapers design, install, manage and maintain indoor green spaces to customer requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/98b261ec-def2-4871-b062-56cdd290c844','key_17020','','','2635','2635.3.19','military welfare worker',replace('army social worker\nforces social worker\nforces welfare worker\nnavy social worker\nmilitary welfare worker\nnavy welfare officer\nmilitary and veterans social worker\nmilitary families support worker\nair force social worker\nair force welfare officer\nforces family support worker\narmy welfare officer\nmilitary families welfare worker','\n',char(10)),'Military welfare workers assist families in coping with the deployment in the military of a family member by supporting them through the adjustment process of leaving and returning of the family member. They help teenagers go through the fear of loosing their parents to the military or not recognising their parents at their return. Military welfare workers help veterans to re-adapt to civilian life and help them manage sufferings, trauma disorders or griefs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/98c3d8f8-e3a8-49b7-9e58-236f25a6f6a0','key_17021','','','2522','2522.1.2','system configurator',replace('systems configurator\nsystem setup professional\nsystem configurator\nICT system configurator\nIT system configurator\nsystem configuration professional\nsystem configurators','\n',char(10)),'System configurators tailor a computer system to the organisation''s and users'' needs. They adjust the base system and software to the needs of the customer. They perform configuration activities and scripting and ensure communication with users.','','Excludes people performing managerial activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/98c429ad-128e-467e-803a-9a6dc1ceed72','key_17022','','','7231','7231.7','tyre fitter',replace('tyre inspector\nwheel machinist\nfitter and turner\ntire fitter\nwheel fitter\nmachinist\ngarage assistant','\n',char(10)),'Tyre fitters inspect, maintain, repair and fit tyres to vehicles. They advise clients on different tyre and wheel types. Furthermore they balance the tyres, verify that the wheels are aligned correctly and ensure compliance with safety standards and regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/98e58946-3227-4f6c-a5d1-5892fe76883d','key_17023','','','2433','2433.1','after-sales service technician',replace('after-sales service technician\ncustomer service technician\nworkshop technician\nafter-sales maintenance technician\nfield technician\nafter-sales support technician\nfield support technician\nafter-sales team member\nhelp-desk technician\nafter-sales rectification technician\nsales support technician\nafter-sales repair technician\nafter-sales installation technician','\n',char(10)),'After-sales service technicians provide after-sales service support to customers, such as the installation, maintenance and repair of the sold products. They take corrective actions to ensure customers’ satisfaction, resolve technical product-related issues and write customer summary reports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/98f17395-0f8c-4f51-9072-cb8f66fd6044','key_17024','','','2422','2422.12.1','agricultural policy officer',replace('agriculture policy coordinator\nagricultural consultant\nagriculture policy manager\nagricultural policy officer\npolicy officer agriculture\nagriculture policy advisor\npolicy manager agriculture\nagronomist\nfarming advisor\nagriculture policy officer','\n',char(10)),'Agricultural policy officers analyse and identify agricultural policy issues and develop plans for improvement and new policy implementation. They write reports and presentations in order to communicate and acquire support for the policies from government officials and the public. They also communicate with professionals in agriculture for research and information purposes and perform administrative duties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/98f9909b-016d-4c62-90ae-893b0cbabbca','key_17025','','','1420','1420.4.36','press and stationery shop manager',replace('newsagent retail manager\nstationery shop manager\nstationery store manager\nnewspapers and magazines shop manager\nnewspapers and magazines store manager\nnewspaper kiosk manager\nnewsagents shop manager\nkiosk manager\nnewsagents store manager\nmagazines and stationery shop manager\npress and stationery shop manager\npaper shop manager','\n',char(10)),'Press and stationery shop managers assume responsibility for the activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/98fa571c-dc8f-438f-909f-f426ec322c72','key_17026','','','3324','3324.4.21','wholesale merchant in machine tools',replace('wholesale merchant in machine tools\ntrainee wholesale merchant in machine tools\nwholesaler in machine tools\nbusiness to business sales consultant in machine tools\nwholesale merchandiser in machine tools\nwholesale buyer in machine tools\ngraduate wholesale merchant in machine tools\nsenior wholesale merchant in machine tools\nbusiness to business seller in machine tools\nassistant wholesale merchant in machine tools\nB2B sales consultant in machine tools\nwholesale trader in machine tools','\n',char(10)),'Wholesale merchants in machine tools investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9905cc76-d48b-49b1-94ab-17becb30be1a','key_17027','','','2310','2310.1.18','food science lecturer',replace('professor of food science\nfood sciences lector\nuniversity food sciences teacher\nprofessor of food science and nutrition\nlecturer in food science\nfood science docent\nfood sciences professor\nfood sciences docent\nuniversity lecturer in food science\nfood science teacher\nfood science and nutrition lecturer\nfood sciences teacher\nfood science professor\nfood science lecturer\nhigher education food sciences teacher\nlecturer in food science and nutrition\nuniversity food science lecturer\nfood sciences instructor\nfood science instructor\nfood science lector','\n',char(10)),'Food science lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, food science, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, grading papers and exams and leading review and feedback sessions for the students. They also conduct academic research in their field of food science, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/990738f4-587c-4045-a10f-e3c1c019220e','key_17028','','','8114','8114.2','block machine operator',replace('block-making machine operator\nblock machine operator\nconcrete block production worker\nconcrete block machine operative\nblock machine operative\nconcrete block machine operator\nblock moulding machine operative\nconcrete block production operative\nblock moulding machine operator\nconcrete plant operative','\n',char(10)),'Block machine operators control, maintain and operate concrete blocks casting machine which fills and vibrate molds to compact wet concrete into finished blocks.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/990bfa45-5001-4365-93b6-daf01a03b4cd','key_17029','','','7133','7133.1','asbestos abatement worker',replace('asbestos abatement team operative\nasbestos worker\nhazardous material abatement worker\nasbestos removal team worker\nasbestos abatement operative\nasbestos removal team operative\nhazardous material removal worker\nasbestos abatement worker\nhazardous materials removal worker\nasbestos operative\nasbestos removal operative\nasbestos abatement team worker\nasbestos abatement advisor\nasbestos abatement assessor\nasbestos contamination assessor','\n',char(10)),'Asbestos abatement workers remove asbestos from buildings and other constructions, compliant with health and safety regulations concerning the handling of hazardous materials. They investigate the intensity of the asbestos contamination, prepare the structure for removal, and prevent contamination of other areas.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/99492920-e5a5-4dba-9e5a-93193147198c','key_17030','','','2631','2631.2','economist',replace('macro analyst\neconomics research scientist\neconomics research analyst\nlabour economist\neconometrician\neconomics analyst\neconomist\nsocial economist\neconomics researcher\ninterest analyst\neconomics science researcher\neconophysicist\neconomics scholar\neconomics scientist','\n',char(10)),'Economists perform research and develop theories in the field of economics, whether for microeconomic or macroeconomic analysis. They study trends, analyse statistical data, and to some extent work with economic mathematical models in order to advise companies, governments, and related institutions. They advise on product feasibility, trend forecasts, emerging markets, tax policies, and consumer trends.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/994fe880-dc26-4f0f-a3cd-500709b6a665','key_17031','','','2144','2144.1.18','precision engineer',replace('precision engineering adviser\nprecision engineers\nprecision engineering consultant\nprecision engineer\nprecision engineering expert\nprecision engineering specialist','\n',char(10)),'Precision engineers design processes, machines, fixtures and other equipment that have exceptionally low engineering tolerances, are repeatable and stable over time. They ensure prototypes are built and tested and make sure the designs meet system specifications and operational requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9967986b-570d-4db1-a179-aad7d1623ea8','key_17032','','','3154','3154.1.3','airport operations officer',replace('airport operations chief\nairport operations supervisor\naerodrome operations supervisor\nchief of airport operations\nairport director\noperations officer\nairport operations coordinator\nairport operations officer\nhead of airport operations\nairport operations manager\nground operations officer\naerodrome operations coordinator\naerodrome operations officer','\n',char(10)),'Airport operations officers perform supervisory and administrative work monitoring operational activities on an assigned shift at a large airport. They ensure the safe take-off and landing of aircrafts','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9980a417-f990-495a-ae11-66a112737f3b','key_17033','','','8181','8181.7','glass annealer',replace('lehr operative\nlehr kiln operative\nlehr operator\nlehr kiln tender\nglass annealing kiln worker\nlehr tender\nglass annealing kiln operative\nlehr kiln operator\npot annealer\nglass annealing kiln operator\nglass annealer\nglass annealing oven worker','\n',char(10)),'Glass annealers operate electric or gas kilns used to strengthen the glass products by a heating-cooling process, making sure the temperature is set according to specifications. They inspect the glass products  through the entire process to observe any flaws.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/998a0bf4-b1be-42ab-aee5-0096c9be3319','key_17034','','','3119','3119.16','quality engineering technician',replace('quality technologist\nquality engineering technician\nquality expert\nquality specialist\nquality technology expert\nquality technology specialist\nquality technician','\n',char(10)),'Quality engineering technicians work with quality engineers or managers to analyse and solve quality problems and improve productivity. They examine machines for imperfections and inspect products to make sure they meet the standards. They also provide personnel with training in inspection techniques and prepare inspection plans.','','Excludes people performing the evaluation of the production process.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/99b096eb-a628-4e69-a79b-0563f2ed1b93','key_17035','','','3435','3435.21','script supervisor',replace('commercials script supervisor\nfilm script supervisor\ndigital script supervisor\nTV script coordinator\ncontinuity supervisor\npre-production script supervisor\nscript supervisor\nscript coordinator\ncontinuity script supervisor\ncontinuity coordinator\nTV script supervisor','\n',char(10)),'Script supervisors are responsible for the continuity of the movie or television program. They watch every shot to make sure that it''s according to the script. Script supervisors ensure that during editing the story makes visual and verbal sense and does not contain any continuity errors.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/99d97310-29ea-443e-96a1-892c8d86ce54','key_17036','','','3331','3331.2.1.15','import export specialist in furniture, carpets and lighting equipment',replace('international trade compliance specialist in furniture, carpets and lighting equipment\ntrainee export specialist in furniture, carpets and lighting equipment\ngraduate export specialist in furniture, carpets and lighting equipment\nsenior import export specialist in furniture, carpets and lighting equipment\ngraduate import export specialist in furniture, carpets and lighting equipment\nassistant export specialist in furniture, carpets and lighting equipment\ntrainee import specialist in furniture, carpets and lighting equipment\nimport export specialist in furniture, carpets and lighting equipment\nsenior import specialist in furniture, carpets and lighting equipment\nassistant import export specialist in furniture, carpets and lighting equipment\nsenior export specialist in furniture, carpets and lighting equipment\ntrainee import export specialist in furniture, carpets and lighting equipment\ngraduate import specialist in furniture, carpets and lighting equipment\nassistant import specialist in furniture, carpets and lighting equipment\ncustoms compliance specialist in furniture, carpets and lighting equipment','\n',char(10)),'Import export specialists in furniture, carpets and lighting equipment have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/99ebf2c2-1811-4c77-b2e6-7008cba29ea0','key_17037','','','3240','3240.2','veterinary technician',replace('vet practice technician\nveterinary technician\nveterinary practice technician\nvet''s technician\nveterinary assistant\nveterinary auxiliary\nvet technician\nveterinary technicians','\n',char(10)),'Veterinary technicians provide the technical and administrative support to the veterinarian in accordance with national legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9a20c93e-a345-4a46-a406-9ffbf11daeef','key_17038','','','1321','1321.2.5','waste management officer',replace('waste management coordination officer\nwaste management safety officer\nrecycling officer\nwaste disposal officer\nwaste management development officer\nwaste disposal operative\nwaste management compliance officer\nwaste management officer\nwaste recycling officer\nwaste diversion manager','\n',char(10)),'Waste management officers advise and enforce regulations on facilities managing waste disposal, collection and recycling. They develop and implement rules and evaluate the compliance with existing legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9a2147aa-a72f-4f83-8653-c3f50d4e1e73','key_17039','','','2643','2643.5','sign language interpreter',replace('public service sign language interpreter\ntelevision sign language interpreter\nbusiness sign language interpreter\ncommunity sign language interpreter\nconference sign language interpreter\npolice sign language interpreter\nsworn sign language interpreter\nremote sign language interpreter\nguide sign language interpreter\nsocial sign language interpreter\nsign language interpreter\nmedical sign language interpreter\ncourt sign language interpreter\ninterpreter of sign language','\n',char(10)),'Sign language interpreters understand and convert sign language into spoken language and vice versa. They maintain the nuances and the stress of the message in the recipient language.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9a26d76b-b9e3-46ae-ab4b-057a1262138b','key_17040','','','2351','2351.4','special educational needs coordinator',replace('special educational needs officer\nspecial educational needs coordinator\ninclusive education coordinator\nSEN officer\nSEN coordinator\nspecial educational needs director\nspecial education coordinator\ncoordinator of special educational needs\nSEN director\nspecial education officer','\n',char(10)),'Special educational needs coordinators oversee programmes and activities that provide educational support to children with a variety of disabilities. They ensure they are up to date with the latest developments in the special needs research field with the objective of facilitating the special education processes needed to maximise the growth and learning potential of students with special learning needs, and advise the special education principal of these developments and new programme proposals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9a39a143-d93c-4093-ba3b-47fb67ade318','key_17041','','','4312','4312.4','insurance clerk',replace('insurance administration clerk\ninsurance management assistant\ninsurance clerk\ninsurance accounts clerk\ninsurance office clerk\ninsurance assistant\nindemnity administrator\ninsurance officer\ninsurance agent\npersonal lines insurance clerk\ninsurance reconciliation clerk','\n',char(10)),'Insurance clerks perform general clerical and administrative duties in an insurance company, other service institution, for a self-employed insurance agent or broker or for a government institution. They offer assistance and provide information about insurances to customers and they manage the paperwork of insurance agreements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9a4ba556-e72b-49c2-834c-d6a03a2fc906','key_17042','','','3153','3153.2','aircraft pilot',replace('helicopter chief pilot\naeroplane pilot\naeroplane captain\naircraft second officer\ncargoplane first officer\ncargoplane pilot\naircraft pilot\naircraft captain\nhelicopter pilot\naircraft first officer','\n',char(10)),'Aircraft pilots control and navigate aircraft. They operate the mechanical and electrical systems of the aircraft and transport people, mail and freight.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9a6d8a84-a8a9-4c9b-9ead-59a710e93759','key_17043','','','2320','2320.1.7','business and marketing vocational teacher',replace('business and marketing vocational teacher\nmarketing teacher\nteacher of marketing and business\nbusiness and marketing teacher\ninstructor in sales and marketing\ninstructor in business and marketing\nsales and marketing vocational teacher\nvocational instructor in business and marketing\nbusiness and marketing vocational instructor\nmarketing instructor\nvocational teacher of marketing and business','\n',char(10)),'Business and marketing vocational teachers instruct students in their specialised field of study, sales and marketing, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for a sales and marketing-related profession. Business and marketing vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of sales and marketing through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9a7e780a-b778-4a5a-b4eb-309d394d313d','key_17044','','','2310','2310.1.40','university literature lecturer',replace('literature docent\nliterature lecturer\nlecturer in literature\nliterature instructor\nhigher education literature lecturer\nprofessor of literature\nsenior lecturer in literature\nuniversity literature professor\nHE literature lecturer\nlecturer of literature\nliterature professor\nuniversity lecturer in literature\nliterature lector\nuniversity literature lecturer\nliterature teacher\nhigher education literature teacher\nHE literature teacher','\n',char(10)),'University literature lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, literature, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants in the preparation of lectures and of exams, in grading papers and exams and in leading review and feedback sessions for the students. They also conduct academic research in their respective field of literature, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9a903a4a-282d-4724-a7de-1918b41d2965','key_17045','','','7543','7543.7.5','motor vehicle assembly inspector',replace('assembly inspector\nmotor vehicle production inspector\nmotor vehicle assembly line inspector\nmotor vehicle quality inspector\nmotor vehicle quality compliance inspector\nmotor vehicle quality control inspector\nmotor vehicle assembly inspector\nmotor vehicle compliance inspector','\n',char(10)),'Motor vehicle assembly inspectors use measuring and testing equipment to inspect and monitor motor vehicle assemblies to ensure conformity to engineering and manufacturing specifications, quality, and safety standards and regulations. They detect malfunction and damage and inspect repair work. They also provide detailed inspection documentation and recommend action where problems are discovered.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9a99a5fe-875d-49b2-9b53-cc1b73be1659','key_17046','','','4221','4221.3.1','railway sales agent',replace('railway transportation sales agent\nrailway station counter agent\nrailway sales agent\nrailway tickets saleswoman\nagent for railway ticket sales\ncounter sales agent\nrailway station cashier\nrailway ticket sales representative\nrailway station agent\nrailway tickets salesman','\n',char(10)),'Railway sales agents provide service to customers who visit the ticket counter. They provide information, handle ticket reservations, sales and refunds. They also carry out clerical duties such as maintaining the daily ticket sales balance sheet. They handle requests for seat reservations and examine diagram charts of each car on train to verify the available space on the specified train.','','Excludes people working in road and urban transport industries collecting fares.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9aa2115d-83cb-4db1-af1d-b426c87c2393','key_17047','','','2635','2635.3.8','criminal justice social worker',replace('family court advisor\ncriminal justice social worker\nsocial worker, criminal justice\nfamily court adviser\nprison social worker\nprobation social worker','\n',char(10)),'Criminal justice social workers tackle criminal behaviour and reduce risk of reoffending by promoting and establishing programs to prevent crime within communities. They assist in prosecutions and help to investigate criminal cases. They assist prisoners reinsert into the community after release from custody. They also support and supervise the offenders sentenced to community service and provide support to the victims and the people closely affected by the crime.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9aa2a36f-296e-45fe-9798-b7839e0ed389','key_17048','','','3113','3113.1','electrical engineering technician',replace('technician in electrical systems\nelectrical equipment technician\nelectrical installations inspector\nelectrical engineering assistant\ntechnician in electrical engineering\nelectrical engineering technician\nelectrical technician\nelectrical systems technician','\n',char(10)),replace('\nElectrical engineering technicians work together with electrical engineers in electrical engineering reasearch. They perform technical tasks and aid in the designing, testing, manufacturing and operation of electrical devices and facilities.','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9ab02537-920a-4549-9711-fd999fd40c28','key_17049','','','9623','9623.2','vending machine operator',replace('coin-operated washing machine operator\nweighing machine operator\nphoto booth operator\nvending machine operator\nvending machine monitor\ncoin-operated locker operator\nvending machine technician\nvending route operator\nlaundrette operator\ncoin-operated personal service machine operator\nvending machine supervisor\nvending machine servicer','\n',char(10)),'Vending machine operators remove cash, conduct visual inspections of the machine, provide basic maintenance and refill goods sold for vending and other coin-operated machines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9ab0f0db-1128-4050-becd-b09db5eabdaa','key_17050','','','4415','4415.1','file clerk',replace('archive clerk\nfiling clerk\nfiling office helper\nfile and classification clerk\nrecords clerk\nrecord filing clerk\nfile keeper\ncopying clerk\ndocumentation specialist\nfile clerk\nfiling system clerk\ntechnical records clerk','\n',char(10)),'File clerks are in charge of maintaining records of a company and managing forms and information. They organise and retrieve documents when necessary and ensure an efficient working system.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9ab80ce6-504f-474f-bb91-98163e9d45fd','key_17051','','','5120','5120.1.1','diet cook',replace('health care cook\nnursing home cook\ndietary cook\nnursery cook\nspecial diet cook\ndiet chef\ndietary aide\ndiet cook','\n',char(10)),'Diet cooks prepare and present meals according to special dietary or nutrition needs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9ac10bde-93a0-4570-afc2-e2b6cf46d8d2','key_17052','','','3435','3435.15','performance lighting technician',replace('audio & lighting technician\nlighting and audio technician\nlighting technician manager\nvideo & lighting technician\naudio and lighting technician\nvideo and lighting technician\nlighting technician assistant\nlighting and video technician\nlighting & video technician\nlighting engineer\nlighting & audio technician\nlighting technician\nlight technician\nlighting technician coordinator','\n',char(10)),'Performance lighting technicians setup, prepare, check and maintain equipment in order to provide optimal lighting quality for live performances. They cooperate with road crew to unload, set up and operate lighting equipment and instruments.','',replace('Excludes people performing media or broadcast activities. \nIncludes people working in event and rental companies.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9ae8251e-cb8e-4324-9d77-d967d43e6b46','key_17053','','','8160','8160.30','fat-purification worker',replace('fat purifier\nfat-purification worker\nfat-purification operator\nfats purifier\nfat-purification operative','\n',char(10)),replace(' Fat-purification workers operate acidulation tanks and equipment that help with the separation of undesirable components from oils. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9aeefea6-11c3-4479-b703-902ee84787d7','key_17054','','','8160','8160.24','dairy processing operator',replace('dairy processing operator\ndairy operator\ndairy processor','\n',char(10)),replace('Dairy processing operators set up and operate continuous flow or vat-type equipment to process milk, cheese, ice cream, and other dairy products following specific instructions, methods and formulas. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9af5fd79-d76e-4343-bc13-ff2d172aec62','key_17055','','','5241','5241.2','fashion model',replace('promotional model\nglamour model\nadvertising model\ncommercial model\neditorial model\nfashion model\nphotographic model\nrunway model\nplus-size model\nhair model','\n',char(10)),'Fashion models help promote products such as clothes, cosmetics and appliances by posing for journalists, photographers and in front of an audience on catwalks. They have to make sure their appearance is always in order. Fashion models need to look good in front of a camera and strike the right pose.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9af8907f-56a7-4170-83b1-3c6ba0997cfa','key_17056','','','2165','2165.3','geographic information systems specialist',replace('geographic information systems adviser\ngeographic information systems specialist\nGIS mapping assistant\ngeomatics technician\nGIS mapping technician\nGIS technician\nspecialist in geographic information systems\nEarth Observation expert\nGIS data specialist\nGIS application specialist\nGIS analyst\ngeographic information systems consultant\nGIS specialist\ngeographic information systems expert','\n',char(10)),'Geographic information systems specialists use specialised computer systems, engineering measures, and geological concepts to process land, geographic, and geospatial information into visually detailed digital maps and geomodels of a reservoir. They convert technical information like soil density and properties into digital representations to be used by engineers, governments and interested stakeholders.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9b35eab1-2adc-4057-a620-181862656a19','key_17057','','','4221','4221.4','tourist information officer',replace('tourist point information officer\ntourist assistant\ntourist centre officer\ntourist receptionist\ntourist information officer\ntourist information agent','\n',char(10)),'Tourist information officers provide information and advice to travellers about local attractions, events, travelling and accommodation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9b3c6dd8-4d3b-4a12-8bb8-55bb2e92c3a6','key_17058','','','3323','3323.3','set buyer',replace('set buyer\nset decoration buyer\nproduction buyer\nassistant production buyer\nproperty buyer','\n',char(10)),'Set buyers analyse the script in order to identify the set dressing and the props needed for all individual scenes. They also consult with the production designer and the prop and set making team. Set buyers buy, rent or commission the making of the props. Set buyers make sure sets are authentic and believable.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9b51a2d2-999a-43a6-a814-63dd30a585fa','key_17059','','','2351','2351.3','education inspector',replace('education institution inspector\neducation inspector\nschool inspector\ninspector of education institutions\nOFSTED inspector\ninspector of education\nher majesty''s inspector of education\neducation authority officer\ninspector of schools','\n',char(10)),'Education inspectors visit schools to ensure that the staff perform their tasks compliant with educational rules and regulations, as well as overseeing that the school''s administration, premises, and equipment conform to regulations. They observe lessons and examine records to assess the school''s operation and write reports on their findings. They provide feedback and give advice on improvement, as well as report the results to higher officials. Sometimes they also prepare training courses and organize conferences that the subject teachers should attend.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9b889f07-c39c-464d-b9d9-b2daa650f9ac','key_17060','','','2212','2212.1','specialised doctor','specialized doctor','Specialised doctors prevent, diagnose and treat diseases depending on their medical or surgical specialty.','','','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9ba74e8a-c40c-4228-9998-eb3c7a5c11df','key_17061','','','5223','5223.4','sales assistant',replace('senior sales assistant\nsupermarket sales assistant\ncounter assistant\nsales assistant\nseller\ndepartment store sales assistant\ntrainee sales assistant\nsaleswoman\ngarden centre sales assistant\nfilling station sales assistant\noutlet centre sales assistant\nhypermarket sales assistant','\n',char(10)),'Sales assistants represent the direct contact with clients. They provide general advice to customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9beeb1f0-5aca-4bde-bae9-c74ddb94fac3','key_17062','','','1420','1420.4.41','textile shop manager',replace('draper\ndrapery store manager\ntextile store manager\ntextile shop manager\nfabric shop manager\nmercers store manager\ndrapers shop manager\nhouse linen shop manager\ndrapery shop manager\ndrapers store manager\nhouse linen store manager\nmercers shop manager\nfabric store manager','\n',char(10)),'Textile shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9bf4c8ea-e814-4772-ae31-0f29672dc497','key_17063','','','0110','0110.3','army captain',replace('captain armed forces\ncompany commander\ncaptain armed services\ntroop commander\narmy chief officer\narmy captain\narmed services captain\narmed forces captain\nbattery commander\ncaptain royal marines','\n',char(10)),'Army captains aid in the supervision of large units of soldiers, as well as perform planning and strategic duties in tactical operations. They also ensure equipment maintenance and provide support in logistic matters as well as support during operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9c3cdf9a-f62f-4d73-bb8d-4cfc85c2d271','key_17064','','','5411','5411.1.2','marine firefighter',replace('harbour firefighter\nmarine fire technician\nvessel firefighter\nmarine firefighter\nboatmaster, firefighter\nengineer,firefighter\nship''s firefighter\nshipboard firefighter','\n',char(10)),'Marine firefighters are responsible for emergency response in case of a fire outbreak or other hazardous situations in marine environments. They actively respond to contain fires and the outbreak of other hazardous on vessels, docks and other marine facilities. They ensure the marine installation complies with health and safety regulations. They also regulate the clean-up of the scene and assess the damage.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9c55aa23-e3f2-4c00-8c90-5ed5204951a3','key_17065','','','3412','3412.4.4','disability support worker',replace('learning disability support carer\ndisability carer\nlearning disability support assistant\ndisability support assistant\ndisability care worker\ndisability support carer\ndisability care personal assistant\ndisability care assistant\ndisability support worker','\n',char(10)),'Disability support workers provide personal assistance and support to individuals of all ages with disability conditions, either intellectual or physical disabilities. They work with other health professionals to maximise individuals physical and mental well being. Their duties include bathing, lifting, moving, dressing or feeding people with disability.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9c57263e-a38f-4c45-9265-a3c3e7cc2c5c','key_17066','','','7317','7317.3','brush maker',replace('mop production plant worker\nbroom production plant operative\nbrush production plant worker\nbrush production plant operative\nbroom production plant worker\nbroom production worker\nbroom factory worker\nbrush assembly worker\nbrush factory worker\nbroom factory operative\nmop factory worker\nbroom  maker\nbroom assembly worker\nbrush assembler\nmop production plant operative\nbroom assembler\nmop assembly worker\nmop factory operative\nmop maker\nbrush maker\nbrush production worker\nmop assembler\nmop production worker\nbrush factory operative','\n',char(10)),'Brush makers insert different types of material such as horsehair, vegetable fiber, nylon, and hog bristle into metal tubes called ferrules. They insert a wooden or aluminium plug into the bristles to form the brush head and attach the handle to the other side of the ferrule. They immerse the brush head in a protective substance to maintain their shape, finish and inspect the final product.','','Includes people performing the assembly of mops which is done by attaching yarn to the handle, thread and wrap wire through the yarn to secure it to the handle, trim excess yarn, finish and inspect the final product.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9c61bba4-56b7-4846-9f4e-b1f2decf3e7e','key_17067','','','5414','5414.1.7','security alarm investigator',replace('residential alarm system specialist\nalarm specialist\nalarm signal investigator\nalarm system specialist\nalarm investigator\nfire safety inspector\nsurveillance-system monitor\nsecurity alarm investigator','\n',char(10)),'Security alarm investigators respond to burglar alarm signals and investigate disturbances identified by the alarm system on the clients'' premises. They monitor security alarms and other surveillance devices and contact police in cases of trespassing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9c9752b7-3e3b-4a08-8553-b63a013f8072','key_17068','','','2431','2431.8','customer experience manager',replace('guest relations specialist\nguest relations manager\nhospitality customer experience manager\ncustomer experience manager\nguest experience manager\nguest experience specialist\nguest relations officer','\n',char(10)),'Customer experience managers monitor customer experiences by creating, evaluating and improving the customer''s interaction organisations in the hospitality, recreation or entertainment industry. They develop plans of action to optimise all aspects of the customer''s experience. Customer experience managers strive to ensure customer satisfaction and boost company profits.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9cda1c7c-ba07-4381-b831-1bb346ff865f','key_17069','','','2635','2635.1.4','marriage counsellor',replace('relationship coach\nmarriage counselling team leader\ncouples counsellor\nmarriage counselling team member\nmarriage and family therapist\nmarriage guidance and advice counsellor\nmarriage guidance counsellor\nmarriage guidance advisor\nmarriage guidance worker\nrelationship counsellor\nmarriage guidance and relationship counsellor\nfamily counsellor\nmarriage counselor\nmarital crisis counsellor\nmarriage crisis counsellor\nfamily therapist\nmarriage and family counsellor','\n',char(10)),'Marriage counsellors support and guide couples and families that are going through crises such as depression, substance abuse and relationship problems. They help improving their communication by providing group or individual therapy.','','Excludes <a resource="http://data.europa.eu/esco/occupation/95d0a616-a4a9-4f56-a8fb-4de43cdb4c82" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/95d0a616-a4a9-4f56-a8fb-4de43cdb4c82"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">family social workers</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9ce12690-c3bf-4135-b7a5-b666bb8c68f5','key_17070','','','3117','3117.5','mine surveying technician',replace('mine survey linesman\nmine technician\nmine survey lineswoman\nmine investigator\nmine surveying technician\nmine cartographer\nsurveyors assistant\nassistant mine surveyor\nmine surveyors assistant\nassistant surveyor','\n',char(10)),'Mine surveying technicians conduct boundary and topographic surveys and surveys of the progress of mining operations. They operate surveying equipment and utilise programs to retrieve and interpret relevant data, and perform computations as required.','','Includes people working on the surface or underground.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9cee6106-04cf-401a-ae4c-b4b8652d6915','key_17071','','','5112','5112.1','chief conductor',replace('train crew supervisor\nconductor crew supervisor\ntrain chief conductor\nchief guard\nchief conductor\ntrain conductor\ntrain conductors supervisor\nchief train conductor','\n',char(10)),replace('Chief conductors are responsible for the safe performance of all operational tasks on board of passenger trains outside the drivers cab, such as supervising safe opening and closing of the train''s doors. They supervise and ensure the continuous care for the safety of passengers, especially in the case of technical incidents and emergency situations. They ensure the operational communication to the driver and to traffic control staff as defined in operational regulations. In case of several staff members attending the train, they supervise the team of conductors. They also carry out commercial activities such as ticket control and selling, and provide support and information to passengers as well as gastronomic services. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9d2aae3e-cbfe-409f-86cc-7cef9cd0435a','key_17072','','','3341','3341.4','contact centre supervisor',replace('contact center supervisor\ncustomer service supervisor','\n',char(10)),'Contact centre supervisors oversee and coordinate the activities of contact centre employees. They ensure that daily operations run smoothly through resolving issues, instructing and training employees and supervising tasks.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9db7d74c-b46d-45c0-b611-6bcb7456ecc6','key_17073','','','1223','1223.2.2.1','ICT research manager',replace('ICT innovation manager\ntechnology innovation manager\nIT research manager\nICT research managers\nICT research manager\nIT innovation manager','\n',char(10)),'ICT research managers plan, manage and monitor research activities and evaluate emerging trends in the information and communication technology field to assess their relevance. They also design and oversee staff training on the use of new technology and recommend ways to implement new products and solutions that will maximise benefits for the organisation.','',replace('Includes people performing managerial research activities. \nExcludes people performing development activities.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9dbbeb2c-0d51-4c03-8ef6-8dfa7360db22','key_17074','','','3151','3151.3','ship assistant engineer',replace('junior ship engineer\nassistant shipping engineer\nship assistant engineer\nthird assistant engineer\nshipping assistant engineer\nsecond assistant engineer\nassistant ship engineer\nassistant maritime engineer\nmaritime assistant engineer\njunior engineer\njunior chief engineer','\n',char(10)),'Ship assistant engineers assist the ship chief engineer and the ship duty engineer in the operations of the ship''s hull. They support the operation of the main engines, steering mechanism, electrical generation and other major subsystems. They communicate with maritime engineers about the performance of technical operations. They also ensure appropriate safety and regulatory standards compliance and are able to take on higher level positions if needed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9deee896-be9b-4aac-b55e-ceff1a7a843a','key_17075','','','1321','1321.2.4','sewerage systems manager',replace('wastewater infrastructure manager\nwastewater distribution systems and equipment manager\nsewerage control engineer\nlead engineer in wastewater infrastructure\nsewerage system construction manager\nwastewater manager\nwastewater infrastructure design lead\nwater and sewer manager\nwastewater network manager\nsewerage systems manager\nwastewater systems manager\nhead engineer in wastewater systems\nsewerage network manager\nwater and sewer engineer\nsewerage manager\nwastewater treatment works manager','\n',char(10)),'Sewerage systems managers coordinate and plan pipe and sewer systems, and supervise sewerage construction and maintenance operations. They supervise wastewater treatment plants and other sewage treatment facilities, and ensure operations are compliant with regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9defc0f8-3eed-4a56-80e1-d534983aff4f','key_17076','','','3123','3123.1.8','demolition supervisor',replace('demolition project supervisor\ndemolition foreman\ndemolition supervisor\ndemolition operations supervisor\ndemolition and dismantling supervisor\ndemolition manager\ndemolition project manager\ndemolition site supervisor\ndismantling foreman','\n',char(10)),'Demolition supervisors monitor operations involved in the demolition of buildings and clean up of debris. They take quick decisions to resolve problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9e1a31bf-2963-40f4-95af-cedc3fbac537','key_17077','','','2320','2320.1.9','design and applied arts vocational teacher',replace('educator in design and applied arts\ndesign and applied arts teacher\napplied arts and crafts vocational teacher\nvocational teacher in design and applied arts\ndesign and applied arts vocational teacher\nvocational instructor in design and applied arts\nart and design teacher\nvocational design and applied arts educator\ndesign and applied arts instructor\nart and design instructor\nteacher of applied arts and crafts\nvocational teacher in art and design','\n',char(10)),'Design and applied arts vocational teachers instruct students in their specialised field of study, applied arts and crafts, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for an applied arts and crafts profession, such as graphic designer or interior designer. Design and applied arts vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of applied arts and crafts through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9e1e8379-6377-4969-b141-0d2337218fc9','key_17078','','','2631','2631.2.3','economic development coordinator',replace('economist\nregeneration manager\neconomic development officer\nsocio economic development coordinator\ndevelopment consultant\ndevelopment officer\neconomic development consultant\neconomic development coordinator\nplanning officer','\n',char(10)),'Economic development coordinators outline and implement policies for the improvement of a community''s, government''s or institution''s economic growth and stability. They research economic trends and coordinate cooperation between institutions working in economic development. They analyse potential economic risks and conflicts and develop plans to resolve them. Economic development coordinators advise on the economic sustainability of institutions and economic growth.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9e285322-2cfa-430f-9b66-41be57d698e2','key_17079','','','2432','2432.8','public affairs consultant',replace('public affairs agent\npublic affairs specialist\npublic information consultant\npublic relations consultant\npublic affairs consultant\ncorporate and public affairs consultant\ncommunication and public affairs specialist\npublic affairs officer\npublic affairs activist\npress and public affairs specialist\ncorporate & public affairs consultant\nlobbyist\npublic affairs & communication consultant\nexternal relations consultant\nexternal relations specialist\npublic affairs and communication consultant\npublic relations specialist','\n',char(10)),'Public affairs consultants function as representatives for a client''s goal. They attempt to persuade legislative bodies and policy makers to implement laws or regulation in accordance with the client''s wishes, and negotiate with parties with potentially conflicting interests. They perform analytical and research duties in order to ensure that the client''s cause is being addressed in an appropriate way to the appropriate parties. They also consult their clients on their causes and policies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9e2e6e1e-363b-4e1b-a673-7bc0f7343300','key_17080','','','2522','2522.1','ICT system administrator',replace('IT systems administrator\nsystem and networks administrator\nICT sysadmins\nIT system administrator\nnetwork administrator\nICT system administrator\nserver administrator\nsystem and network administrator\nICT system administrators\nICT systems administrator\nsysadmin\nICT systems administrators\nenterprise administrator\nICT sysadmin','\n',char(10)),'ICT system administrators are responsible for the upkeep, configuration, and reliable operation of computer and network systems, servers, workstations and peripheral devices. They may acquire, install, or upgrade computer components and software; automate routine tasks; write computer programs; troubleshoot; train and supervise staff; and provide technical support. They ensure optimum system integrity, security, backup and performance.','',replace('Excludes people performing managerial activities. \n Includes people performing inventory activities.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9e4269d5-e10e-4f49-ac99-65c833f933a2','key_17081','','','1112','1112.1','central bank governor',replace('central bank managers\ncentral bank directors\ncentral bank manager\ncentral bank governor\nnational bank governors\nmain bank governor\ncentral banker\ncentral bank director\ncentral bankers\nnational bank governor','\n',char(10)),'Central bank governors set the monetary and regulatory policy, determine interest rates, maintain price stability, control the national money supply and issuance and foreign exchange currency rates and gold reserves. They oversee and control the banking industry.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9e47c727-5718-48f1-a567-6a4c09397a21','key_17082','','','2144','2144.1.1.1','aerodynamics engineer',replace('aerodynamic engineering consultant\naerodynamic technology engineering consultant\nair vehicle dynamics engineer\naerodynamics design engineer\naerodynamic engineering specialist\naerodynamics research engineer\naerodynamic technology engineering expert\naerodynamic technology engineer\naerodynamic engineering adviser\naerodynamic technology engineering adviser\naerodynamics performance engineer\naerodynamics engineer\naerodynamic engineer\nCFD engineer\naerodynamic engineering expert\naerodynamic technology engineering specialist\nwind tunnel engineer\naerodynamicist\naerodynamics test engineer\ncomputational fluid dynamics  engineer','\n',char(10)),'Aerodynamics engineers perform aerodynamics analysis to make sure the designs of transport equipment meet aerodynamics and performance requirements. They contribute to designing engine and engine components, and issue technical reports for the engineering staff and customers. They coordinate with other engineering departments to check that designs perform as specified. Aerodynamics engineers conduct research to assess adaptability of equipment and materials. They also analyse proposals to evaluate production time and feasibility.','',replace('Includes people working in aircraft designing. \nIncludes people working in the automotive and rolling stock industry.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9e657046-67ce-4e16-ab2e-ddb918638910','key_17083','','','2356','2356.1','ICT trainer',replace('system trainer\nICT trainer\nblended learning trainer\nICT instructor\nICT trainers\ndistance-learning trainer\nIT trainer','\n',char(10)),'ICT trainers conduct training-needs analysis and design programs to train students in the use of software packages and information systems accordingly. They produce and update existing training materials (content and method), deliver effective training in classroom, online or informally, monitor, evaluate and report effectiveness of training. They maintain and update expertise on specialised ICT subjects and evaluate and report on students'' performance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9e81adde-9983-44fa-b74b-c548d0dbfbdd','key_17084','','','1420','1420.4','shop manager',replace('retail store manager\nshop manager\ndeputy store manager\nboutique manager\nstore manager\nshop general manager\nretail shop manager','\n',char(10)),'Shop managers are responsible for activities and staff in specialised shops.','','Includes people performing activities which are tipically responsibility of the sellers.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9ebaf3f0-0be0-47b7-b2b1-b3b04130fa81','key_17085','','','2513','2513.1','digital games developer',replace('video games developer\ndigital game developer\ngames programmer\ndigital games developers\nvirtual games developer\ndigital games developer\ncomputer games developer','\n',char(10)),'Digital games developers program, implement and document digital games. They implement technical standards in gameplay, graphics, sound and functionality.','','Includes people performing managerial activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9efbe0de-37c0-4cc2-a1e6-a916eb4ecaa2','key_17086','','','7317','7317.1','artisan papermaker',replace('artisan paper fabricator\npaper creator\nartisan paper manufacturer\nsmall-scale paper producer\npaper fabricator\nartisan papermaker\nartisan paper producer\nartisan paper creator\nsmall-scale paper manufacturer','\n',char(10)),'Artisan papermakers create paper slurry, strain it on screens, and dry it manually or using small scale equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9f12704c-5e6c-47b0-a866-3b92663e6a4b','key_17087','','','1324','1324.8.3','resource manager',replace('resource specialist\nresource coordinator\nresource manager\nresources manager\nresource administrator','\n',char(10)),'Resource managers manage resources for all potential and assigned projects. They liaise with the different departments to see that all various resources needs are met, in a timely manner, and communicate any resourcing issues that may impact scheduled deadlines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9f508305-80ce-4111-8722-f2c9b4a44890','key_17088','','','1223','1223.1','product manager',replace('product development manager\nproduct manager\nresearch and development manager\nproduct marketing manager\nproduct developer\nsports product engineer','\n',char(10)),'Product managers are responsible for managing the lifecycle of a product. They research and develop new products in addition to managing existing ones through market research and strategic planning. Product managers perform marketing and planning activities to increase profits.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9f686e81-c236-42cb-b848-1918eb36e16a','key_17089','','','7516','7516.3','leaf tier',replace('cigar leaf tier\nhand leaf tier\ntobacco processing operative\nleaf tier (tobacco)\nleaf tier\ncigarette leaf tier\nleaf bundling attendant\ntobacco leaf binder\ntobacco leaf tier\nmanual leaf tier\ncigar buncher\ncigar bunch maker\ntobacco leaf bundler','\n',char(10)),'Leaf tiers tie tobacco leaves manually into bundles for processing. They select loose leaves by hand and arrange them with butt ends together. They wind tie leaf around butts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9faaae26-804d-48f3-97b0-84f7e1aceae8','key_17090','','','1324','1324.3.2.3','import export manager in beverages',replace('trainee import manager in beverages\nsenior import export manager in beverages\nassistant import export manager in beverages\ngraduate import manager in beverages\ntrainee export manager in beverages\nsenior export manager in beverages\ngraduate export manager in beverages\nassistant export manager in beverages\nsenior import manager in beverages\ninternational trade compliance manager in beverages\ncustoms compliance manager in beverages\nassistant import manager in beverages\ninternational trade manager in beverages\ntrainee import export manager in beverages\ngraduate import export manager in beverages\nimport export manager in beverages','\n',char(10)),'Import export managers in beverages install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/9fcce643-276b-435f-aa94-4516c2414686','key_17091','','','1324','1324.3.1.6.30','waste and scrap distribution manager',replace('senior waste and scrap distribution manager\nwaste and scrap supply chain planner\nwaste and scrap logistics planner\nwaste and scrap distribution planner\nassistant waste and scrap distribution manager\ntrainee waste and scrap distribution manager\nwaste and scrap distribution manager\nwaste and scrap logistics manager\nwaste and scrap supply chain manager\ngraduate waste and scrap distribution manager','\n',char(10)),'Waste and scrap distribution managers plan the distribution of goods to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a001cf0d-b0eb-420a-a110-9408448ba8b8','key_17092','','','8114','8114.8','stone splitter',replace('splitting machine operative\nstone splitting machine worker\nstone splitting machine operative\nstone sawyer\nstone breaker\ncobble stone splitter\nstone fabricator\nblock-maker\nstone splitter\nsplitting machine worker\nstone splitting machine operator','\n',char(10)),'Stone splitters operate and maintain machines that split stone. They manipulate stone into different forms such as blocks, cobbles, tiles and concrete products. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a005f8a2-2d8a-433e-8d89-13575f822fd4','key_17093','','','1349','1349.12','energy manager',replace('environmental sustainability manager\nenergy and sustainability manager\nenergy manager\nenergy demand forecasting manager\nenvironmental compliance manager\nsmart energy specialist\ngreen policy manager\nenergy policy manager\nenergy monitoring manager\nenergy procurement manager','\n',char(10)),replace('Energy managers coordinate the energy use in an organisation, and aim to implement policies for increased sustainability, and minimisation of cost and environmental impact. They monitor the energy demands \nand use, and develop improvement strategies, as well as researching the most beneficial source of energy for the organisation''s needs.','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a02a1117-212f-4a49-b21a-d3b971e00e48','key_17094','','','9411','9411.1','pizzaiolo',replace('pizzaiolo\npizza-chef\npizza chef\npizza-cook\npizza cook\npizza maker','\n',char(10)),'Pizzaiolos are responsible for preparing and cooking pizzas.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a04eedcf-8424-475a-8dc5-f30a9da4b151','key_17095','','','2411','2411.1.9','financial fraud examiner',replace('fraud examiner\nfraud prevention analyst\nforensic accountant\nfraud investigations manager\nfinancial fraud investigator\nfinancial fraud examiner\nfraud analyst\ncertified fraud examiner','\n',char(10)),'Financial fraud examiners undertake anti-fraud investigations including financial statement irregularities, securities fraud and market abuse detection. They manage fraud risk assessments and prepare forensic reports including the analysis and verification of evidence. Financial fraud examiners liaise with regulatory bodies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a05d0a6d-7b60-445a-8dcb-282c7d5a6852','key_17096','','','1349','1349.7','chief fire officer',replace('fire captain\nchief firefighter\nfire chief\nchief fire and rescue officer\nfire department chief\nchief fireman\nfire lieutenant\nchief fire officer\nmarshal','\n',char(10)),'Chief fire officers supervise a fire department. They coordinate the operations of the department, and supervise and lead the fire and rescue staff during firefighting and rescue activities to ensure the safety of the staff and limitation of risks. They perform administrative duties to ensure record maintenance, and implement policies to improve the department''s operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a0872ef2-ecf2-476d-8390-3e81ae45a411','key_17097','','','3324','3324.4.10','wholesale merchant in electrical household appliances',replace('senior wholesale merchant in electrical household appliances\nbusiness to business sales consultant in electrical household appliances\ngraduate wholesale merchant in electrical household appliances\nwholesale trader in electrical household appliances\nB2B sales consultant in electrical household appliances\nbusiness to business seller in electrical household appliances\ntrainee wholesale merchant in electrical household appliances\nassistant wholesale merchant in electrical household appliances\nwholesale merchandiser in electrical household appliances\nwholesale buyer in electrical household appliances\nwholesaler in electrical household appliances\nwholesale merchant in electrical household appliances','\n',char(10)),'Wholesale merchants in electrical household appliances investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a092cb70-f3e3-4224-98a3-b97c0114de77','key_17098','','','3257','3257.1','environmental health inspector',replace('environmental health inspector\npublic health inspector','\n',char(10)),'Environmental health inspectors carry out investigations to ensure that areas, organisations and companies comply with environmental and public health legislation. They evaluate environmental complaints, provide reports on their findings and work to prevent future hazards or noncompliance with current policies. Environmental health inspectors perform consultations to promote public health and safety.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a0ebd342-8f89-4947-893d-fee9468cf001','key_17099','','','3435','3435.19','pyrotechnic designer',replace('pyrotechnician & choreographer\npyro designer\npyrotechnical designer\npyrotechnician and choreographer\npyrotechnic designer\npyrotechnic operator and choreographer\npyro-designer\npyrotechnic operator & choreographer','\n',char(10)),'Pyrotechnic designers develop a pyrotechnical design concept for a performance and supervise the execution of it. Their work is based on research and artistic vision. Their design is influenced by and influences other designs and must be conform with these designs and the overall artistic vision. Therefore, the pyrotechnic designers work closely with artistic directors, operators and the artistic team. During rehearsals and performance they coach the operators to obtain optimal timing and manipulation. Pyrotechnic designers develop plans, cue lists and other documentation to support the operators and production crew. Pyrotechnic designers sometimes also work as autonomous artists, creating pyrotechnical art outside a performance context.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a0f9903e-2ba5-4b9d-bb0a-a8646db15221','key_17100','','','8121','8121.2','extrusion machine operator',replace('extrusion machine operator\nplastic extrusion operator\nextruding machine technician\nextrusion machine worker\nextrusion operator\nextruder tender operative\nextruder machine setting operative\nhot extrusion press operator\nextrusion machine operative\nextruder machine setter\nextruder operative\nextruder machine tendering operator\nextruder machine tender\nextruding machine operator\nplastic extrusion machine operator\ncold extrusion press operator\nextruder tender\nextruder operator\nextrusion machine setter\nextruder operating apprentice\nextrusion machine tender\nextrusion press technician\nmetal extrusion machine operator\nhot extrusion machine operator\nextruder operating technician\nextruder machine operator\ncold extrusion machine operator\nextrusion press operator','\n',char(10)),'Extrusion machine operators set up, monitor and maintain machines which heat or melt raw materials, and pull or push the heated material through a shaped die to form it into a continuous profile with a preset cross section such as tubes, pipes and sheeting. They may also clean and maintain the equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a10222e7-1940-4ca3-86c1-99b115535969','key_17101','','','5223','5223.7.2','audio and video equipment specialised seller',replace('audio-visual shop salesperson\nhome entertainment shop specialised seller\naudio and video salesperson\naudio and video shop salesperson\nhome entertainment shop salesperson\nmultimedia equipment specialised seller\naudio and video specialised seller\naudio and video shop specialised seller\naudio-visual salesperson\nhi-fi specialised seller\nhi-fi shop salesperson\naudio and video equipment salesperson\naudio and video equipment specialized seller\nhome entertainment specialised seller\nhome entertainment salesperson\naudio-visual shop specialised seller\naudio-visual specialised seller\nhi-fi salesperson\nhi-fi shop specialised seller\nmultimedia equipment salesperson','\n',char(10)),'Audio and video equipment specialised sellers sell audio and video equipment such as radio and television, CD, DVD etc. players and recorders in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a10849c1-f0d8-4600-bca6-9549c2517650','key_17102','','','2330','2330.1.15','physical education teacher secondary school',replace('high school PE teacher\nphysical education teacher high school\nPE teacher in secondary school\nphysical education tutor secondary school\nsecondary school teacher of physical education\nhigh school physical education teacher\nPE teacher\nphysical education teacher\nteacher of physical education in secondary schools\nsecondary school physical education tutor','\n',char(10)),'Physical education teachers at secondary schools provide education to students, commonly children and young adults, in a secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, physical education . They prepare lesson plans and materials, monitor the students’ progress, assist individually when necessary, and evaluate the students’ knowledge and performance on the subject of physical education through practical, usually physical, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a10eb17a-3c78-4f7a-a1da-8f31146339d3','key_17103','','','9214','9214.1','garden labourer',replace('garden laborer\nlandscaping labourer\ngarden maintenance trainee\ngardening assistant\nlandscape labourer\nlandscaping operative\ngarden worker\nmanual garden labourer\ngardener\ngardening worker','\n',char(10)),'Garden labourers perform simple tasks in cultivating and maintaining flowers, trees and shrubs. This work can take place in either parks or private gardens.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a1273779-d2d4-4708-a406-62f7cdd26aab','key_17104','','','2149','2149.10','health and safety engineer',replace('environmental health and safety engineer\nhealth and safety specialist\nhealth and safety engineering expert\nhealth and safety expert\nhealth and safety engineer\nsafety case expert\nhealth engineer\noccupational health and safety engineer\nsafety engineer\nsafety case lead author\nhealth and safety engineering consultant\nindustrial health and safety engineer\nhealth and safety consultant engineer\nhealth and safety engineering adviser\nhealth and safety engineering specialist','\n',char(10)),'Health and safety engineers design objects and programs by combining engineering principles and health and safety requirements. They envision the protection and well-being of the people using designed objects or performing work under designed health and safety programs. They assess facilities and the risks that they might pose (e.g. contaminant materials, ergonomics, handling of dangerous substances, etc.) in order to design and improve health and safety measures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a142a645-6e9e-4d86-accd-9816df857c90','key_17105','','','2163','2163.1.3.3','leather goods designer',replace('leatherware drafter\nleather goods fashion designer\nleather goods creator\nleather goods drafter\nleather goods designer\nleather goods designer and developer\nleatherware designer\nleather goods drawer\nfashion leather goods designer\nleather goods CAD designer\nleather goods designer & developer','\n',char(10)),'Leather goods designers are in charge of the creative process of leather goods. They perform fashion trends analysis, accompany market researches and forecast needs, plan and develop collections, create concepts and build the collection lines.  They additionally conduct the sampling, create prototypes or samples for presentation and promote concepts and collections. During the collection development, they define the mood and the concept board, the colour palettes, the materials and produce drawings and sketches. Leather goods designers identify the range of materials and components and define the design specifications. They collaborate with the technical team.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a14e96a7-6c4d-4d69-8c6f-7ccadf77bac5','key_17106','','','2424','2424.3','corporate training manager',replace('learning programmes manager\ncorporate training director\ntraining manager\ncorporate skills and training manager\ntalent development coordinator\nskills development manager\ntraining coordinator\neducation and training manager\ntraining and development coordinator\ncorporate education manager\nworkplace training manager\nmanager of corporate training\ncorporate training manager\nprofessional development training manager\ncorporate training and development manager\nin-company training manager\nlearning manager\ntraining and development manager\nlearning and development manager\ntraining development director\ncorporate sector training manager\nstaff development director\nemployment training manager','\n',char(10)),'Corporate training managers coordinate all the training activities and development programmes in a company. They also design and develop new training modules and supervise all the activities related to the planning and delivery of these programmes. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a198de8c-24b1-4002-982f-a57ae6d6c19c','key_17107','','','3321','3321.1','insurance broker',replace('retail insurance broker\nsales insurance supervisor\ninsurance intermediary\ninsurance sales adviser\ninsurance sales advisor\npersonal lines insurance broker\nlife insurance broker\ninsurance sales representative\nproperty and casualty insurance agent and broker\ninsurance sales agent\ninsurance broker\ninsurance seller\ncommercial insurance broker\ninsurance sales consultant\ninsurance agent\ninsurance sales person\nlife insurance representative\nhousehold insurance broker','\n',char(10)),'Insurance brokers promote, sell and provide advice on various insurance policies such as life insurance, health insurance, accident insurance and fire insurance to individuals and organisations. They also work as intermediaries between individuals or organisations and insurance companies, and negotiate the best insurance policies for their clients, arranging insurance cover where needed. Insurance brokers engage with new prospective clients, provide them with quotes for their policy needs, assist them in the signing of new insurance contracts and propose specific solutions to their specific problems.',replace('Insurance brokers are natural or legal persons who provide insurance. They are also called insurance intermediaries.\n\n','\n',char(10)),'Includes insurance intermediaries.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a1acd539-c359-4af3-8a4a-07f1b611e368','key_17108','','','9212','9212.1','catcher',replace('poultry gatherer\npoultry herder\nchicken gatherer\nchicken catcher\ncatcher\nchicken herder','\n',char(10)),'Catchers are specialists working at poultry farms to catch the poultry.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a1b32703-a2e5-4e0a-910f-7cdd0293b52d','key_17109','','','3423','3423.1.2.3','specialised outdoor animator',replace('outdoor education specialist\noutdoor personal development tutor\nskydiving instructor\nscuba diving instructor\noutdoor education instructor\nspecialized outdoor animator\nspecial needs outdoor instructor\noutdoor instructor for disabled people\nparagliding instructor\npersonal instructor','\n',char(10)),'Specialised outdoor animators plan, organise, and safely deliver outdoor animator activities. They may also support one or more assistant outdoor animators, and be involved in aspects of administration, front office tasks and tasks related to activity base and equipment maintenance. They work with demanding clients, in terms of either their particular needs, abilities or disabilities or at the higher levels of skill and hazardous environments or conditions. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a1b4eb5e-45d4-441b-a7e8-795ac5cf6fbf','key_17110','','','7316','7316.4','sign maker',replace('traffic sign maker\nsign installer\nsignboard writer\nroad sign maker\nsign writer\nsign maintenance worker\nsign maker\nsignboard maker\nsignwriter\nadvertising sign maker\nroad sign installer\nroad sign maintenance worker','\n',char(10)),'Sign makers design and develop signs for a variety of uses such as flyers, traffic signs, billboards and business signs. They use different materials and techniques and if necessary they install the sign on site. Furthermore they also perform maintenance and repairs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a1b86a8b-ad91-4192-b386-bbf89bb11699','key_17111','','','5131','5131.1','restaurant host/restaurant hostess',replace('maître d''hôtel\nrestaurant host\nfront of house hostess\nrestaurant hostess\nrestaurant host/restaurant hostess\nfront of house host\nmeeter and greeter','\n',char(10)),'Restaurant hosts/hostesses welcome customers to a hospitality service unit and provide initial services.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a1ba7574-cc28-446c-9741-12d7fbb325cb','key_17112','','','2422','2422.12.10','labour market policy officer',replace('labour policy officer\nlabor policy officer\npolicy officer at labour group\npolicy officer, labour market\nlabor policy researcher\nlobour policy researcher\nlabour policy consultant\nlabor policy consultant\nlabor policy advisor\npolicy officer, labor\nlabor policy analyst\nlabor market policy officer\npolicy officer, labour\npolicy officer, labor market\nlabour policy advisor\nlabour policy analyst\npolicy officer at labour programme\nlabor market policy analyst\nlabour market policy analyst','\n',char(10)),'Labour market policy officers research, analyse and develop labour market policies. They implement policies ranging from financial policies to practical policies such as improving job searching mechanisms, promoting job training, giving incentives to start-ups and income support. Labour market policy officers work closely with partners, external organisations or other stakeholders and provide them with regular updates.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a1c9f8b7-c4ce-4b15-ac3c-3378c300d8f2','key_17113','','','7223','7223.4.6','milling machine operator',replace('numerical controlled lathe technician\nmilling machine set-up operator\ncomputer numerical controlled milling machine operator\nnumerical controlled lathe operator\nhorizontal machining centre operator\nCNC milling machine operator\nCNC milling operative\nnumerical controlled milling machine operator\nCNC miller\nvertical machining centre operator\nhorizontal machining operator\nmilling machine operator\nCNC milling machine operative\nhorizontal machining operative\ncnc turner/setter\nnumerical controlled milling machine technician\nmilling machine tender\ncnc miller\ncnc setter/operator\ncomputer numerical controlled milling machine operative\nnumerical control milling operator\nnumerical controlled milling operator\nnumerical control milling operative\nnumerical controlled milling machine inspector\ncnc programmer/operator','\n',char(10)),'Milling machine operators set up, program and control milling machines, designed to cut excess material from metal workpieces using a computer-controlled rotary-cutting, milling cutter. They read milling machine blueprints and tooling instructions, perform regular machine maintenance, and make adjustments to the milling controls, such as the depth of cuts or the rotation speed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a1ccbfc4-3afd-4408-8e5a-41832cdef511','key_17114','','','3324','3324.4.31','wholesale merchant in textiles and textile semi-finished and raw materials',replace('wholesale trader in textiles and textile semi-finished and raw materials\nwholesale merchandiser in textiles and textile semi-finished and raw materials\nwholesaler in textiles and textile semi-finished and raw materials\nbusiness to business seller in textiles and textile semi-finished and raw materials\nbusiness to business sales consultant in textiles and textile semi-finished and raw materials\ntrainee wholesale merchant in textiles and textile semi-finished and raw materials\nsenior wholesale merchant in textiles and textile semi-finished and raw materials\ngraduate wholesale merchant in textiles and textile semi-finished and raw materials\nwholesale buyer in textiles and textile semi-finished and raw materials\nassistant wholesale merchant in textiles and textile semi-finished and raw materials\nwholesale merchant in textiles and textile semi-finished and raw materials\nB2B sales consultant in textiles and textile semi-finished and raw materials','\n',char(10)),'Wholesale merchants in textiles and textile semi-finished and raw materials investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a1cf1406-3632-494c-bff3-0bd5d5327d8f','key_17115','','','4227','4227.2','survey enumerator',replace('commercial census taker\nsurvey enumerator\ncensus official\ncensus interviewer\ncensus worker\nenumerator\nstreet surveyor\ncensus clerk\ncensus taker','\n',char(10)),'Survey enumerators perform interviews and fill in forms in order to collect the data provided by interviewees. They can collect information by phone, mail, personal visits or on the street. They conduct and help the interviewees administer the information that the interviewer is interested in having, usually related to demographic information for governmental statistical purposes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a1d0eb6b-20ac-4a2c-a7c1-9b016ea92b24','key_17116','','','5161','5161.2','fortune teller',replace('palm reader\ncrystal gazer\nfortune teller\ntea leaf reader\nnumerologist\ntarot card reader\nmind reader','\n',char(10)),replace('Fortune tellers use their intuition and other skills to foretell future events about a person''s life and provide clients with their interpretation. They often use various techniques such as card reading, palm reading or tea-leaves reading. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a1da839e-9b70-4032-bb83-820790e77003','key_17117','','','5249','5249.2.1.6','rental service representative in trucks',replace('rental service desk agent in trucks\nrental service assistant in trucks\nrental sales worker in trucks\nrental sales assistant in trucks\nrental service supervisor in trucks\nrental sales supervisor in trucks\nrental advisor in trucks\nrental sales desk clerk in trucks\nrental service desk clerk in trucks\nrental service manager in trucks\nrental sales desk supervisor in trucks\nrental sales desk agent in trucks\nrental service team leader in trucks\nrental sales team leader in trucks\nrental sales manager in trucks\nrental service desk supervisor in trucks\nrental service representative in trucks\nrental specialist in trucks\nrental service worker in trucks','\n',char(10)),'Rental service representatives in trucks are in charge of renting out equipment and determining specific periods of usage. They document transactions, insurances and payments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a1e1a788-2352-4172-8fcf-1f985a6968b0','key_17118','','','5142','5142.1','aesthetician',replace('facialist\nbeauty specialist\nesthetician\nskin care technician\nfacial treatment operator\ncosmetician\nskin care specialist\nbeautician','\n',char(10)),'Aestheticians offer skin care treatments. They apply various facial treatments according to their clients'' needs and skin type, such as lotions, scrubs, peels and masks, in order to maintain the skin healthy and attractive. Aestheticians may also give neck massages and body treatments such as wraps. Aestheticians remove unwanted hair on different body parts like eyebrows, upper lip or the bikini area. They perform facial massage and apply make-up for various occasions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a20e4b15-a128-49b9-a06a-e790e3bd5c68','key_17119','','','5131','5131.2.2.2','head sommelier',replace('master sommelier\nhead sommelier\nwine manager\nhead wine waiter\nhead of wines','\n',char(10)),'Head sommeliers manage the ordering, preparing and servicing of wine and other related beverages in a hospitality service unit.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a241eff8-309b-43ae-8890-ac4cfbd3067c','key_17120','','','3133','3133.1','chemical processing plant controller',replace('chemical operator\nchemical plant process operator\nchemical plant and systems operator\nchemical processing plant controller\nchemical plant systems technician\nchemical plant and systems monitor\nchemical plant operations technician\nchemical plant and systems monitoring technician\nchemical plant and system operator\nprocess plant operator','\n',char(10)),'Chemical processing plant controllers control the chemical production process. They operate machines and systems, being responsible to monitor and maintain the equipment and the instruments in control.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a2494c65-11a8-4853-9371-c89333c33be1','key_17121','','','1112','1112.3.2','embassy counsellor',replace('embassy counselor\ndefence attache\nembassy officer\npolitical attache\neconomic attache\ncounsellor of embassy\nattache\ncultural attache','\n',char(10)),'Embassy counsellors supervise specific sections in an embassy, such as economics, defence or political affairs. They perform advisory functions for the ambassador, and perform diplomatic functions in their section or specialty. They develop policies and implementation methods and supervise the embassy section''s staff.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a256838b-0843-4ad2-a2fb-4531f94e63d8','key_17122','','','3434','3434.1.3','private chef',replace('private cook\nprivate culinary expert\nprivate chef\nculinary expert','\n',char(10)),'Private chefs comply with food and sanitation rules to prepare meals for their employers. They take into consideration the employer’s intolerances to specific ingredients or their preferences and cook the meals in the employer''s home. Private chefs may also be asked to organise small dinner parties or other types of celebrations for special occasions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a273ec4c-96fd-48e5-9343-695a6bd08512','key_17123','','','8152','8152.3.3','weaving textile technician',replace('willow machine operator\nwarping machine operator\nmesh weaving technician\ncotton machine operator\ntextile weaver\nrope weaving machine operator\nweaving department manager\nweaving textile technician\njacquard weaving technician\nweaving loom operator\npassementerie production machine operator\nweaving textile supervisor\nweaving textile operator\nlace weaving technician','\n',char(10)),'Weaving textile technicians perform operations related to setting up weaving processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a28dff76-74df-46ee-929f-d1fffbb63cd7','key_17124','','','2519','2519.5','ICT quality assurance manager',replace('ICT quality manager\nIT quality assurance manager\nICT quality assurance manager\nICT principal testing manager\nprincipal test manager\nICT test manager\nICT quality assurance managers','\n',char(10)),'ICT quality assurance managers establish and operate an ICT quality approach through quality management systems, in compliance with internal and external standards and the organisation''s culture. They ensure that the management controls are correctly implemented to safeguard asset, data integrity and operations. They focus on the achievement of quality goals, including the maintenance of the external certification according to quality standards and monitor statistics to forecast quality outcomes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a2a126ac-d90d-4e9f-97d1-9b8dff2ff946','key_17125','','','7515','7515.5','master coffee roaster','master coffee roaster','Master coffee roasters design new coffee styles and ensure the quality of blends and recipes pragmatically. They write blending formulas to guide workers who prepare coffee blends for commercial purposes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a2abd144-2a36-45ea-919c-1fcc6ebc5d45','key_17126','','','7131','7131.3','paperhanger',replace('wallpaper hanger\ndecorator, paperhanger\npaperhanger\ndecorator paper hanger\npaper hanger\nwallpaper decorator\nwall paperer','\n',char(10)),'Paperhangers are specialised in hanging up wallpaper. They apply adhesives to the paper, or to the wall in the case of reinforced wallpaper, and fix the paper straight, well aligned, and avoiding the inclusion of air bubbles.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a2b81132-2147-4c8e-b250-e6290ed906ea','key_17127','','','3315','3315.4','insurance fraud investigator',replace('insurance fraud examiner\ninsurance fraud auditor\ninsurance policy processing worker\ninsurance fraud inspector\ninsurance fraud investigator\ninsurance auditors','\n',char(10)),'Insurance fraud investigators combat fraudulent activities by investigating the circumstances of certain suspicious claims, activities related to new customers, buying insurance products and premium calculations. Insurance fraud investigators refer potential fraud claims to insurance investigators who then undertake research and investigations to support or deny a claimant''s case.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a2bc52c2-9802-4929-a95c-6379d9f19204','key_17128','','','1321','1321.2.1.5','leather finishing operations manager',replace('plating manager\ndrying supervisor\nfinishing manager\nmoistening\nbuffing manager\nfinishing department supervisor\ndrying manager\nfinishing area supervisor\npressing\nleather finishing operations manager\nstaking\nspraying manager','\n',char(10)),'Leather finishing operations managers plan and organise the work of the finishing department. They are in charge of chemical supply and equipment management. They also coordinate the staff.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a2dee758-72f5-4f1c-b760-f2b8e9d81911','key_17129','','','7133','7133.3','chimney sweep',replace('chimney-sweeping technician\nsweeper\nchimney sweeper\nflue sweeper\nchimney sweep\nchimney-sweeping engineer\nfunnel sweeper\nsmoke-stack sweeper\nlump sweeper','\n',char(10)),'Chimney sweeps undertake cleaning activities of chimneys for all types of buildings. They remove ash and soot and perform maintenance on a regular basis, following health and safety regulations. Chimney sweeps may perform safety inspections and minor repairs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a2fa73d0-7e51-456f-95c6-90f126f52ab8','key_17130','','','8312','8312.4','train dispatcher',replace('platform assistant\nplatform dispatch assistant\ntrain dispatch controller\ntrain dispatcher\nengine dispatcher\nrailway dispatcher','\n',char(10)),'Train dispatchers ensure the safe and efficient dispatch of the train services. The safety of customers is their main priority. When carrying out train dispatch duties, they check the traffic signals and communicate promptly with train drivers and conductors that it is safe for the train to pull away.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a2ff6cc3-15fa-4f02-a457-8fd5df7aa29e','key_17131','','','1324','1324.3.3','intermodal logistics manager',replace('manager of intermodal transport logistics\ntransportation logistics manager\nmanager of intermodal transportation logistics\nintermodal logistics manager\nintermodal transport logistics manager\nlogistics and traffic manager\nmulti-modal logistics manager\nmulti-modal services manager\nmanager of multi-modal services\nmanager of intermodal logistics\nmulti-modal transport service manager','\n',char(10)),'Intermodal logistics managers manage and oversee commercial and operational aspects of intermodal logistics for an organisation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a30734bb-2f10-4747-a94c-75f5ec3c63a4','key_17132','','','5329','5329.1','hospital porter',replace('general porter\nhospital portering\nstretcher bearer\nhospital stretcher bearer\nhospital porter\nhospital portering staff member\ngeneral hospital porter\nstretcher carrier\nporter\nhospital portering service provider\nhospital stretcher carrier','\n',char(10)),'Hospital porters are professional healthcare assistants who transport people on stretchers around the hospital site, as well as and items.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a317e9c2-62d5-4a24-8ad4-5cb7b9db3f3b','key_17133','','','2264','2264.1','advanced physiotherapist',replace('senior physiotherapist\nadvanced physiotherapist\nsenior specialist physiotherapist\nlead manipulative physiotherapist\nphysiotherapy manager\nlead respiratory physiotherapist\nspecialist physiotherapist\nlead occupational physiotherapist\nphysiotherapy team lead\nlead neuromuscular physiotherapist\nsenior occupational health physiotherapist\nphysiotherapy team leader\nphysiotherapy lead\nlead cardiovascular physiotherapist\nlead neuromusculoskeletal physiotherapist\nlead neurological physiotherapist\nclinical lead physiotherapist\nlead remedial physiotherapist','\n',char(10)),'Advanced physiotherapists are highly specialised. They make complex decisions and manage risks in unpredictable contexts and within a defined area. They may focus on a specific area of clinical practice, education, research or professional management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a32187fa-bc37-4fc2-8e89-296632bfc271','key_17134','','','3131','3131.2','onshore wind farm technician',replace('onshore wind turbine maintenance technician\nonshore wind farm operator\nwind farm technician\nonshore wind plant technician\nwind turbine service technician\nonshore wind turbine repair technician\nonshore wind plant operator','\n',char(10)),'Onshore wind farm technicians operate and maintain onshore wind farms by performing diagnostic inspections, analysing faults, and performing repair duties. They ensure the wind turbines operate in compliance with regulations and assist the wind engineers in the construction of wind turbines. Onshore wind farm technicians may also test and install hardware and software components of wind turbines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a330b7b6-696e-455c-b6d5-f39f82c1489f','key_17135','','','2132','2132.3','aquaculture recirculation manager',replace('aquaculture recirculation manager\naquaculture recirculation supervisor\nmanager of aquaculture recirculation systems\nfish farm recirculation manager','\n',char(10)),'Aquaculture recirculation managers control the production of aquatic organisms in land-based recirculation systems, manage water re-use processes and supervise complex circulation, aeration and biofilter systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a36c14e6-ed96-467d-8390-6f9cf47ac350','key_17136','','','2310','2310.1.20','history lecturer',replace('history teacher\nuniversity lecturer in history\nmodern history lecturer\nprofessor of ancient history\nuniversity history lecturer\nancient history lecturer\nprofessor of modern history\nlecturer in history\nhistory professor\nlecturer of history\nprofessor of medieval history\nhistory instructor\nprofessor of history\nhistory docent\nuniversity history teacher\nhistory lector\nhigher education history teacher\nmedieval history lecturer\nhistory lecturer','\n',char(10)),'History lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, history, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of history, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a377fafb-7e6e-4925-bc64-8099b58aafb8','key_17137','','','2142','2142.1.3','drainage engineer',replace('drainage engineering adviser\ndrainage systems engineer\nflood risk engineer\nirrigation engineer\ndrainage technology engineering adviser\ndrainage engineering consultant\ndrainage engineering specialist\ndrain engineer\ndrainage engineer\ndrainage technology engineering consultant\ndrainage technology engineer\ndrainage engineering expert\nplumbing and drainage engineer\ndrainage system engineer\ndrainage technology engineering specialist\nwater and drainage engineer\nproject engineer, drainage\nstorm water drain engineer\nsewer engineer\nwater resources engineer\nenvironmental engineer, drainage\ndrainage technology engineering expert','\n',char(10)),'Drainage engineers design and construct drainage systems for sewers and storm water systems. They evaluate the options to design drainage systems that meet the requirements while ensuring compliance with legislation and environmental standards and policies. Drainage engineers choose the most optimal drainage system to prevent floods, control irrigation and direct sewage away from water sources.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a3ef0728-7540-4676-ad18-cf0a7913b3ce','key_17138','','','3331','3331.1','customs and excise officer',replace('clearing agent\nshipping agent\ncustoms clearance agent\ncustoms and excise officer\nexport documentation officer\ncustoms broker\ncustoms agent','\n',char(10)),'Customs and excise officers approve or deny the passage of goods through customs barriers for international business and ensure compliance with consignment legislation. They facilitate communication between import and export trading institutions and government officials, and are responsible for taxation calculation and ensuring payment.','','Excludes  <a resource="http://data.europa.eu/esco/occupation/5f16819d-50b9-475b-a2d8-607ee6d99a9d" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/5f16819d-50b9-475b-a2d8-607ee6d99a9d"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">customs officer</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a41acf2b-fb87-496f-aec3-7c5fdc5e9211','key_17139','','','3257','3257.4','industrial waste inspector',replace('industrial waste inspector\nindustrial waste disposal inspector\nindustrial waste management inspector\nindustrial waste control inspector\nindustrial waste compliance inspector\nindustrial waste tester/inspector','\n',char(10)),'Industrial waste inspectors conduct controls in companies to ensure they comply with waste and environmental regulations. They check the documentation related to waste management, they collect samples for analysis and observe the industrial practices. They may advise or preventive or corrective actions to improve industrial waste management and disposal.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a424aa0f-b3f1-41fb-901f-85e05fed83ed','key_17140','','','2162','2162.1.1','landscape designer',replace('garden designer\nlandscaper\nlandscape designer\nlandscaping architect\ngarden advisor\nlandscaping designer\nlandscape consultant\nlandscape advisor','\n',char(10)),'Landscape designers design and create outdoor public areas, landmarks, structures, parks, gardens and private gardens to achieve environmental, social-behavioral, or aesthetic outcomes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a429e4fe-22c6-4cb5-89ef-ca4d5b4d0363','key_17141','','','7232','7232.1','aircraft de-icer installer',replace('aircraft de-icing equipment installation technician\nspacecraft de-icer installer\naeroplane de-icer installer\naircraft de-icing equipment technician\naircraft de-icing equipment fitter\naircraft de-icing equipment installer\naircraft de-icer installer','\n',char(10)),'Aircraft de-icer installers assemble, install, test, maintain and repair mechanical de-icing and anti-icing systems that prevent ice accumulation or formation on aircrafts and spacecrafts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a44a1dc5-be08-4840-8bd5-770c4ac1ca6d','key_17142','','','3512','3512.3','ICT security technician',replace('ICT security technician\nIT security officer\nICT security officer\nICT security technicians\nIT security technician','\n',char(10)),'ICT security technicians propose and implement necessary security updates and measures whenever is required. They advise, support, inform and provide training and security awareness.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a464015d-a622-44aa-a4fe-bf01e412e120','key_17143','','','7543','7543.7.7','vessel assembly inspector',replace('vessel engineering compliance inspector\nship assembly quality inspector\nship engineering compliance inspector\nboat assembly quality inspector\nship assembly inspector\nvessel assembly quality inspector\nship quality compliance inspector\nvessel quality compliance inspector\nboat assembly safety inspector\nboat quality compliance inspector\nboat assembly inspector\nboat engineering compliance inspector\nvessel assembly inspector\nvessel assembly safety inspector\nship assembly safety inspector','\n',char(10)),'Vessel assembly inspectors use measuring and testing equipment to inspect and monitor boat and ship assemblies to ensure conformity to engineering specifications and to safety standards and regulations. They examine the assemblies to detect malfunction and damage and check repair work. They also provide detailed inspection documentation and recommend action where problems were discovered.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a47f82d3-4cf7-4abf-bb36-2c8cbad28234','key_17144','','','2642','2642.1','journalist',replace('commentator\nreporter\nnews writer\nweb editor\njournalist\npolitical journalist\nfinancial journalist\nsports journalist\nscience journalist\nbusiness journalist\ncorrespondent\ninvestigative journalist\nnews anchor\ncrime journalist','\n',char(10)),'Journalists research, verify and write news stories for newspapers, magazines, television and other broadcast media. They cover political, economic, cultural, social and sport events. Journalists must conform to ethical codes such as freedom of speech and right of reply, press law and editorial standards in order to bring objective information.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a4869ae9-2dfe-48cc-bd97-f803880f3407','key_17145','','','3118','3118.3','drafter',replace('draftsperson\ndraughtsperson\ndrafting expert\nCAD draughter\ncomputer aided design draughter\ndrafting adviser\ndrafting consultant\ndraughter\ndrafting specialist\ndrafter','\n',char(10)),'Drafters prepare and create technical drawings using a special software or manual techniques, to show how something is built or works.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a486b6eb-207c-4aae-ad0b-397d0744b16f','key_17146','','','3324','3324.4.25','wholesale merchant in office furniture','wholesale merchant in office furniture','Wholesale merchants in office furniture investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a48b9a5a-5ded-482d-8cda-7fd097a32c16','key_17147','','','6113','6113.1','groundsman/groundswoman',replace('groundskeeping worker\nlawnswoman\nlandscape worker\ngroundsman/groundswoman\nlandscape gardener\ngroundswoman\ngreens keeper\nlawn care specialist\npark keeper\nlawnsman\ngrounds staff\ngroundsman\nhorticulturist','\n',char(10)),replace('Groundsmen/groundswomen provide landscape and lawn services and maintain grounds for private households, commercial and public facilities, schools, hotels, botanical gardens, golf courses, parks and athletic fields.\\n','\n',char(10)),'',replace('Excludes greenkeeper.\n Includes people performing the planting, care, maintenance and care of municipal grounds, highway greenery and industrial and commercial buildings.\n Includes people maintaining the greenery of gardens, playgrounds, sports terrains, buildings and other parks or lawns for recreational purposes','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a491c5c1-e126-4414-9ac9-3625a4a79513','key_17148','','','5419','5419.10','game keeper',replace('estate supervisor\ngame warden\ngamekeeper\ngame keeper','\n',char(10)),replace('Game keepers manage the habitat and wild game population in a defined area.  \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a49e1479-90c4-41d2-871b-ac58ebc4894b','key_17149','','','1324','1324.3.1.6.18','household goods distribution manager',replace('assistant household goods distribution manager\nsenior household goods distribution manager\nhousehold goods distribution manager\ngraduate household goods distribution manager\nhousehold goods supply chain manager\nhousehold goods logistics planner\nhousehold goods distribution planner\nhousehold goods supply chain planner\ntrainee household goods distribution manager\nhousehold goods logistics manager','\n',char(10)),'Household goods distribution managers plan the distribution of household goods to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a4ab8347-782f-4ca1-940d-44ffea256ebf','key_17150','','','8142','8142.7.1','optical disc moulding machine operator',replace('optical disc moulding machine specialist\noptical disc molding machine operator\noptical disc moulding machine tender\noptical molding machine operator\noptical disc moulding machine operative\noptical disc injection moulding machine specialist\ninjection molding machine tender\noptical disc injection moulding machine technician\nmolding machine tender\noptical disc injection moulding machine operator\ninjection moulding machine tender\noptical disc moulding machine technician\nmoulding machine tender\noptical disc moulding machine worker\noptical disc molding machine tender','\n',char(10)),'Optical disc moulding machine operators tend moulding machines that melts polycarbonate pellets and inject the plastic into a mould cavity. The plastic is then cooled and solidifies, bearing the marks that can be digitally read.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a4c5fb46-1d72-47de-b349-f579a0f1edfb','key_17151','','','7215','7215.3','rigging supervisor',replace('rigging and lifting supervisor\nrigging manager\nlifting and rigging supervisor\nlifting supervisor\nrigging superintendent\nrigging supervisor','\n',char(10)),'Rigging supervisors oversee rigging operations. They manage and coordinate employees who operate lifting and rigging equipment. They organise the day-to-day working activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a4dd6eae-ca75-456f-829d-333671a7c186','key_17152','','','2636','2636.1','chaplain',replace('imam\nchaplain\nuniversity chaplain\nlay representative\nprison chaplain\nhospital chaplain\nrabbi\nmilitary chaplain','\n',char(10)),'Chaplains perform religious activities in secular institutions. They perform counselling services and provide spiritual and emotional support to the people in the institution, as well as cooperate with priests or other religious officials to support religious activities in the community.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a4e96a0c-3a49-4cd3-a45c-ff081c28405d','key_17153','','','9329','9329.2','wood caulker',replace('wooden hull caulker\nwooden boat caulker\nwooden ship caulker\nwood caulker','\n',char(10)),'Wood caulkers drive oakum into the seams between planking in deck or hull of wooden ships to make them watertight. They mainly use hand tools to heat marine glue and force it into the seams. They can also hammer hemp ropes and cotton lines into the seams and smear hot pitch over them.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a504e79e-f146-4773-a1cf-2b15b9d614db','key_17154','','','8332','8332.7','moving truck driver',replace('moving van driver\nmoving truck operator\nremoval truck driver\nremoval lorry driver\nremoval van driver\nremoval van operator\nmoving lorry driver\nmoving truck driver','\n',char(10)),'Moving truck drivers operate lorries or trucks intended for relocating and transporting goods, belongings, machinery, and others. They assist in placing goods in the truck for efficient use of space and security compliance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a50d64e7-2dba-4418-81a7-92b2ba2e508f','key_17155','','','7114','7114.2','terrazzo setter',replace('terrazzo layer\nconcrete flooring worker\nterrazzo setter\nterrazzo team member\nterrazzo flooring worker\nterrazzo team worker\npoured flooring worker\nterrazzo worker\nepoxy terrazzo setter','\n',char(10)),'Terrazzo setters create terrazzo surfaces. They prepare the surface, installing strips to divide sections. They then pour the solution containing cement and marble chips. Terrazzo setters finish the floor by polishing the surface to ensure smoothness and shine.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a51ab2e2-aaa9-4d7d-a747-bff2e276636a','key_17156','','','7223','7223.9','heat treatment furnace operator',replace('heat treat furnace operative\nbillet heating operator\nheat regulator operator\nheat treatment furnace operator\nbillet heating technician\nheat treating bluer apprentice\nbillet heater\ncasting furnace technician\nheat treating machine operator\nheat treating furnace tender\nbatch heat operative\nheat regulator operative\nbillet heater operator\nheat treating technician\nheat treat operative\nheat treating bluer\nheat treating furnace operator\nmining oven technician\nheat treating setter\nbatch heat treat operator\nheat treat furnace operator\nheat treat apprentice','\n',char(10)),'Heat treatment furnace operators monitor the process of heat treatment of castings. They control the treatment furnaces and direct all activities of furnace operation, including the interpretation of computer data, temperature measurement and adjustment, and loading vessels. They control the chemicothermal treatment of castings in order to reach the standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a5245c01-4ca8-406e-a152-620c28530ea8','key_17157','','','7223','7223.12','metal planer operator',replace('lathe and turning machine tool tender\nplaner set up operator\nlathe and turning machine tool operator\nmetal planer operator\nlathe and turning machine tool setter\nmetal milling machine operator','\n',char(10)),'Metal planer operators set up and operate a planer, which is a metalworking machine designed to cut excess material from a metal workpiece using linear relative motion between the planer''s cutting tool and the workpiece in order to create a linear toolpath and cut.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a527f263-0feb-4d55-b416-0577e9dc7847','key_17158','','','5249','5249.2.2.1','rental service representative in recreational and sports goods',replace('rental service worker in recreational and sports goods\nrental service assistant in recreational and sports goods\nrental service manager in recreational and sports goods\nrental sales manager in recreational and sports goods\nrental service team leader in recreational and sports goods\nrental service desk agent in recreational and sports goods\nrental service representative in recreational and sports goods\nrental sales assistant in recreational and sports goods\nrental sales desk agent in recreational and sports goods\nrental sales team leader in recreational and sports goods\nrental service desk clerk in recreational and sports goods\nrental service supervisor in recreational and sports goods\nrental specialist in recreational and sports goods\nrental service desk supervisor in recreational and sports goods\nrental sales worker in recreational and sports goods\nrental advisor in recreational and sports goods\nrental sales desk supervisor in recreational and sports goods\nrental sales supervisor in recreational and sports goods\nrental sales desk clerk in recreational and sports goods','\n',char(10)),'Rental service representatives in recreational and sports goods are in charge of renting out equipment and determining specific periods of usage. They document transactions, insurances and payments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a580e79a-b752-49c1-b033-b5ab2b34bfba','key_17159','','','2261','2261.2','specialist dentist',replace('prosthodontic specialist\noral health care specialist\ndental public health specialist\ndental health specialist\npostgraduate dentist\nspecialist dentist\nconsulting dentist\nhospital dentist\nconsultant dentist\ndental specialist\noral and maxillofacial specialist\noral and maxillofacial pathology specialist\nspecial needs dentist\northodontician\npaedodontrist\nspecial care dentist\ndentofacial orthopaedic specialist\nperiodontologist\ngerodontic specialist\nendodontic specialist\nrestorative dentist\nprosthetic dentist\nperiodontician','\n',char(10)),'Specialist dentists prevent, diagnose and treat anomalies and diseases affecting the teeth, mouth, jaws and adjoining tissues specialised in oral surgery or orthodontics.','','In dentistry, the general <a resource="http://data.europa.eu/esco/occupation/e52b5707-7097-4dbc-ac61-054e917da241" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/e52b5707-7097-4dbc-ac61-054e917da241"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">dental practitioner</span></a> is habilitated to carry out all essential and optional skills performed by <a resource="http://data.europa.eu/esco/occupation/a580e79a-b752-49c1-b033-b5ab2b34bfba" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/a580e79a-b752-49c1-b033-b5ab2b34bfba"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">  specialist dentists</span></a> and specialist dentists also have the possibility to carry out all the essential and optional skills performed by the general dental practitioner. The difference between the general dental practitioner and the specialist dentists is that the latter, due to the specific training received is more prone to perform the skills related to the specialty in question (orthodontics or oral surgery) on a daily basis.','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a5826284-14fb-4866-b9be-69ce69db1b71','key_17160','','','1312','1312.2','aquaculture husbandry manager',replace('aquaculture on-growing manager\naquaculture husbandry manager\nfish farm on-growing manager\naquatic animals husbandry manager\nfish farm on-growing supervisor\naquaculture on-growing supervisor','\n',char(10)),'Aquaculture husbandry managers specialise in the husbandry of ongrowing aquatic species, especially in the feeding, growth, and stock management processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a59feab2-9bc9-4b25-97cf-995998164502','key_17161','','','3521','3521.1.11','video technician',replace('audio-video specialist\nimage technician\nvideo technician\naudio visual specialist\naudio visual technician\nAV technician\nvideo/graphics technician\naudio video specialist\ngraphics/video technician\nvisuals technician\naudio-visual specialist\nvideo conference technician\naudio-video technician\naudio/visual technician','\n',char(10)),'Video technicians set up, prepare, check and maintain equipment in order to provide optimal projected image quality for a live performance. They cooperate with road crew to unload, set up and operate video equipment and instruments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a5a4e4d6-e705-4b38-96b5-225f8ab01f2c','key_17162','','','3324','3324.4.16','wholesale merchant in hardware, plumbing and heating equipment and supplies',replace('B2B sales consultant in hardware, plumbing and heating equipment and supplies\nwholesale merchandiser in hardware, plumbing and heating equipment and supplies\nwholesale merchant in hardware, plumbing and heating equipment and supplies\nassistant wholesale merchant in hardware, plumbing and heating equipment and supplies\nwholesale buyer in hardware, plumbing and heating equipment and supplies\nbusiness to business seller in hardware, plumbing and heating equipment and supplies\ngraduate wholesale merchant in hardware, plumbing and heating equipment and supplies\nbusiness to business sales consultant in hardware, plumbing and heating equipment and supplies\ntrainee wholesale merchant in hardware, plumbing and heating equipment and supplies\nwholesaler in hardware, plumbing and heating equipment and supplies\nsenior wholesale merchant in hardware, plumbing and heating equipment and supplies\nwholesale trader in hardware, plumbing and heating equipment and supplies','\n',char(10)),'Wholesale merchants in hardware, plumbing and heating equipment and supplies investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a5dc92b4-9712-4bc5-a08a-f6efa07b855f','key_17163','','','2144','2144.1.2.1','agricultural equipment design engineer',replace('agricultural equipment engineering consultant\nagricultural equipment technology engineering expert\nagricultural irrigation engineer\nagricultural equipment engineering specialist\nagricultural equipment engineer\nagricultural equipment engineering adviser\nagricultural equipment designer\nagricultural landscape enginer\nagricultural research enginer\nagricultural equipment technology engineer\nagricultural systems specialist\nagricultural systems engineer\nagricultural automation enginer\nagricultural equipment engineering expert\nagricultural equipment technology engineering consultant\nagricultural equipment design engineer\nagricultural equipment technology engineering specialist\nagricultural equipment technology engineering adviser','\n',char(10)),'Agricultural equipment design engineers apply knowledge of engineering and biological science to solve various agricultural problems such as soil and water conservation and the processing of agricultural products. They design agricultural structures, machinery, equipment and processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a6007269-912a-4445-b792-b4d5fe016bb2','key_17164','','','4221','4221.2','host/hostess',replace('host\nhostess','\n',char(10)),'Hosts/hostesses welcome and inform visitors at airports, train stations, hotels, exhibitions fairs, and function events and/or attend to passengers in the mean of transport. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a603fdd9-31e7-424a-883a-e9f6bf88085a','key_17165','','','3123','3123.1.23','structural ironwork supervisor',replace('structural steel fitting supervisor\nsteel fitting supervisor\nstructural ironworks supervisor\nstructural steelwork supervisor\nsteel erection supervisor\nrebar supervisor\nstructural ironwork foreman\nstructural ironwork supervisor\nconstruction ironwork supervisor','\n',char(10)),'Structural ironwork supervisors monitor ironworking activities. They assign tasks and take quick decisions to resolve problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a632fb74-ef49-4f4a-9777-b7df54a0f897','key_17166','','','7133','7133.2','building exterior cleaner',replace('pressure washer operator\nfaçade cleaner\nbuilding restoration worker\nbuilding exterior cleaner\nexterior cleaner\ngraffiti remover\nbuilding restoration operative\nfloor polisher\nbuilding cleaning worker\nbuilding cleaning operative worker\nfloor sweeper','\n',char(10)),'Building exterior cleaners remove dirt and litter from a building''s exterior, as well as perform restoration tasks. They ensure the cleaning methods are compliant with safety regulations, and monitor the exteriors to ensure they are in proper condition.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a63733fa-f7ff-4733-8961-1ec4eeaab00b','key_17167','','','2621','2621.8','museum scientist',replace('museum administrator\nconservationist scientist\nmuseum researcher\nmuseum scientist','\n',char(10)),'Museum scientists perform and/or manage the curatorial, preparatory and clerical work in general museums, botanical gardens, art galleries, fine arts related collections, aquariums or similar areas. They manage the collections of natural, historical and anthropological material that is educational, scientific or aesthetic in purpose.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a645b7e5-dfa8-4cce-938f-2d6395a6b51d','key_17168','','','3119','3119.9','leather goods manufacturing technician',replace('leather goods industry forewoman\nleather goods industry foreman\nleather goods manufacturing technician\nleather goods worker\nleather goods cutting and finishing technician\nleather goods manufacture technician\nleather goods cutting & finishing technician\nleather goods technician','\n',char(10)),'Leather goods manufacturing technicians perform a wide range of activities and tasks related to the leather goods manufacturing process including cutting, closing and finishing, according to pre-defined quality criteria, resulting from the direct relationship with the costumer. They use manual techniques supported by simple traditional equipment to produce exclusive models or very small orders.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a6517365-211c-456d-aca4-1a0115c7b1e1','key_17169','','','2359','2359.6','e-learning architect',replace('e-learning architect\ndistance learning architect \ndistance learning expert \ndistance learning designer\ne-learning specialist \neLearning developer\ne-learning designer\neLearning architect\ne-learning expert\neLearning designer\ne-learning architects\ndistance learning specialist ','\n',char(10)),'E-learning architects establish goals and procedures for the application of learning technologies within an organisation and the creation of an infrastructure that supports these goals and procedures. They review the existing curriculum of courses and verify the online delivery capability, advising changes to the curriculum to adapt to online delivery.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a655aff1-e5e9-4c64-bc85-b902568a3dfb','key_17170','','','2659','2659.2','performance artist',replace('impersonator\nperformer\nperformance and installation artist\nmimic\nactress and performance artist\nactor & performance artist\nperformance artist\nperformance maker\nperformance & installation artist\nperformance poet\nmime\nactor and performance artist\nmultimedia performance artist\nlive performance artist\nimitator\nactress & performance artist\ndrag queen','\n',char(10)),'Performance artists create a performance which can be any situation that involves four basic elements: time, space, the performer''s body, or presence in a medium, and a relationship between the performer and audience or onlookers. They are flexible with the medium of the art work, the setting and the length of time of the performance.','','Excludes street artist, puppeteer and variety artist.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a659968d-0804-4357-bc60-735e4d41e65c','key_17171','','','3122','3122.4.6','footwear production supervisor',replace('footwear product inspection supervisor\nfootwear finishing room supervisor\nfootwear production inspector\nfootwear production forewoman\nfootwear cutting room supervisor\nfootwear production supervisor\nfootwear production foreman\nrubber footwear moulding supervisor\nfootwear manufacturing supervisor\nfootwear closing room supervisor','\n',char(10)),'Footwear production supervisors monitor and coordinate day-to-day production activities of a footwear manufacturing plant. They oversee quality control to ensure that the end product is in alignment with production specifications, and manage the footwear staff. The work also involves negotiations with suppliers as well as taking care of the production plan and production costs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a67721a6-a95d-4c3b-a48c-f9920941ecd1','key_17172','','','7223','7223.22','straightening machine operator',replace('straightening machine operator\nstraightening press operative\nstraightening machine tender\nstraightening machinist\nstraightening machine operative\nstraightener\nadjusting machine operator\nstraightening machine apprentice\nstraightening machine technician\nstraightening press operator','\n',char(10)),'Straightening machine operators set up and tend straightening machines designed to form metal workpieces into their desired shape using pressing practices. They adjust the angle and the height of the straightening rolls and select the settings for the pressing force required to straighten the workpiece, taking into account the end product''s yield strenght and size, without excess work hardening.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a6a0b60f-08da-4faa-bf54-942987efb471','key_17173','','','2511','2511.13','ICT system analyst',replace('ICT system analyst\nsystem analyst\nnetwork analyst\nICT systems analyst\nsystems analyst\nIT systems analyst\nICT system analysts\nICT systems analysts\nIT system analyst','\n',char(10)),'ICT system analysts specify the system needs to meet the end user requirements. They analyse system functions in order to define their goals or purposes and to discover operations and procedures for accomplishing them most efficiently. They also design new IT solutions to improve business efficiency and productivity, produce outline designs and estimate costs of new systems, specify the operations the system will perform, and the way data will be viewed by the end user. They present the design to the users and work closely with the users to implement the solution.','','Excludes people performing ICT programming.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a6af318e-8a3a-4266-a1ef-cf7afe588201','key_17174','','','6112','6112.3','hop farmer',replace('organic hop farmer\nhops grower\nhops producer\nhops cultivator\nhops tender\nhop grower\nhop producer\nhops farmer\nhop cultivator\nhop farmer\nhop tender\norganic hops cultivator\norganic hops grower','\n',char(10)),'Hop farmers plant, cultivate and harvest hops for the production of commodities such as beer.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a70315e3-ae41-44a9-9fe5-7fe668469ee0','key_17175','','','1324','1324.3.2.15','import export manager in furniture, carpets and lighting equipment',replace('graduate export manager in furniture, carpets and lighting equipment\ncustoms compliance manager in furniture, carpets and lighting equipment\ntrainee import manager in furniture, carpets and lighting equipment\nimport export manager in furniture, carpets and lighting equipment\nsenior import manager in furniture, carpets and lighting equipment\nassistant import manager in furniture, carpets and lighting equipment\ninternational trade compliance manager in furniture, carpets and lighting equipment\nsenior export manager in furniture, carpets and lighting equipment\nassistant import export manager in furniture, carpets and lighting equipment\ngraduate import export manager in furniture, carpets and lighting equipment\ninternational trade manager in furniture, carpets and lighting equipment\nsenior import export manager in furniture, carpets and lighting equipment\ngraduate import manager in furniture, carpets and lighting equipment\ntrainee import export manager in furniture, carpets and lighting equipment\nassistant export manager in furniture, carpets and lighting equipment\ntrainee export manager in furniture, carpets and lighting equipment','\n',char(10)),'Import export managers in furniture, carpets and lighting equipment install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a7198837-139d-49c7-8fd0-eb8d8c1ee844','key_17176','','','2269','2269.11','osteopath','osteopath','Osteopaths provide therapeutic treatment of disorders in the musculoskeletal system to patients with physical issues such as back pain, joint pain and digestive disorders. They mainly use manipulation of the body tissues, touch, stretching and massage techniques to relieve the patients` pain and promote a healthy lifestyle.',replace('An osteopath is an individual who has completed an appropriate education in osteopathy and continues to demonstrate the required standards. \n \nCEN Standard: http://www.osteopathy.org.uk/news-and-resources/document-library/about-the-gosc/council-may-2015-item-15a-annex-a-final-draft-cen-standard/','\n',char(10)),'','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a7358961-46d4-4715-ac1e-6148fd042510','key_17177','','','2143','2143.2','environmental expert',replace('environmental scientist\nsoil scientist\nenvironmental consultant\nmineral technologist\ngeologist\ntechnological environmentalist\nenvironmental compliance expert\ngeological technologist\nenvironmental technologist\nenvironmental advisor\necosystem restoration project manager\nenvironmental health manager\nclimate change specialist\nenvironmental expert\ngreen infrastructure analyst\nenvironmental sustainability expert\nenvironmental specialist','\n',char(10)),'Environmental experts search for technological solutions to tackle environmental problems. They detect and analyse environmental issues and develop new technological production processes to counter these problematic issues. They research the effect of their technological innovations and present their findings in scientific reports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a74f6c51-37e5-423e-aa5b-044e98663027','key_17178','','','2513','2513.4','web content manager',replace('web content manager\ndigital content\ncontent specialist\nweb content curatorspecialist\nweb content specialist\nweb content administrators\ncontent curator','\n',char(10)),'Web content managers curate or create content for a web platform according to the long-term strategic goals, policies and procedures for an organisation''s online content or their customers. They control and monitor compliance with standards, legal and privacy regulations and ensure web optimisation. They are also responsible for integrating the work of writers and designers to produce a final layout which is compatible with corporate standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a7594892-ff23-4e2a-aedf-2f967ebca15c','key_17179','','','1221','1221.3.2.1','sales manager',replace('sales executive\nsales coordinator\nsales director\ngroup sales manager\nsales commercial manager\nsales manager\ninternational sales manage\narea sales manager\ninside sales manager','\n',char(10)),'Sales managers develop sales and targeting strategies for a company. They manage sales teams, allocate sales resources based on the plans, prioritise and follow up on critical leads, develop sales pitches and adjust them over time, and maintain a sales platform to track all leads and sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a75c740c-d8d9-41a7-ab1a-e69a6eb6eac1','key_17180','','','2149','2149.9.1','alternative fuels engineer',replace('hydrogen engineer\nfuel cell engineer','\n',char(10)),'Alternative fuels engineers design and develop systems, components, motors, and equipment which replace the use of conventional fossil fuels as main power source for propulsion and power generation with the feature of using renewable energies and non-fossil fuels. They strive to optimise energy production from renewable sources and reduce production expenses and environmental strain. The alternative fuels employed mainly include Liquefied Natural Gas (LNG), Liquefied Petroleum Gas (LPG), biodiesel, bio-alcohol as well as electricity (i.e., batteries and fuel cells), hydrogen and fuels produced from biomass.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a76a05bb-e04d-4be8-953e-1ffd691dd58d','key_17181','','','2635','2635.3.17','mental health social worker',replace('care coordinator (mental health)\nsocial worker (mental health)\ncare coordinator\napproved mental health professional\nmental health social worker\nmental health community social worker\nmental health gateway worker\nmental health advocate\nmental health social support worker','\n',char(10)),'Mental health social workers assist and provide counselling to people with mental, emotional, or substance abuse problems. They focus on providing personalised support to cases and monitor the recovery process of their clients by providing therapy, crisis intervention, client advocacy and education. Mental health social workers may contribute to mental health services improvement and mental health outcomes for citizens.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a77bc925-c441-42f2-92d4-666b103fc025','key_17182','','','7523','7523.4','wood pallet maker',replace('wood pallet maker\npallet machine worker\npallet nailing machine technician\npallet machine specialist\npallet nailing machine specialist\npallet machine operator\npallet nailing machine operator\npallet nailing machine worker\npallet machine technician\nwooden pallet maker','\n',char(10)),'Wood pallet makers create wood pallets for use in storage, shipping and manipulation of goods. Pallet makers operate a machine that takes in usually low-grade softwood planks treated with heat or chemicals and nails them together. The material and shape of the pallets, the treatment methods, and the number and pattern of nails used are all highly standardised to make exchange of used pallets possible.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a784b063-89cf-441b-9624-deed9dbdeae6','key_17183','','','7132','7132.3','transport equipment painter',replace('vehicle paint sprayer\nvehicle painter\ntruck painter\nmotor vehicle refinisher\nvan painter\nrailway carriage painter\nvehicle paint technician\nvessel painter\nvan refinisher\nautomotive paint sprayer\nmotor vehicle painter\ntransport equipment painter\ncar painter\nvehicle body painter\nautomotive paint technician\ncar refinisher\nautomotive refinisher\ntruck refinisher\naircraft painter\nautomotive painter','\n',char(10)),replace('Transport equipment painters use painting machines and hand tools to coat individual parts and to paint the surface of all types of transport equipment such as cars, buses, boats, aircraft, motorcycles and railway cars. They prepare the surface of the pieces for the paint and apply the coat. Transport equipment painters can perform industrial painting or individual customisation. They may also remove or repair painting errors such as scratches. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a78d8e2b-fe31-447f-8694-38ed7198d143','key_17184','','','3433','3433.3','taxidermist',replace('taxidermy technician\nbird taxidermist\ntaxidermist\nanimal taxidermist','\n',char(10)),'Taxidermists mount and reproduce deceased animals or parts of animals such as trofy heads for the purpose of public display and education, such as in a museum or monument, or for other sources of scientific study, or for a private collection.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a79170ad-0b7b-4697-864f-1748359aa10a','key_17185','','','1330','1330.6','ICT product manager',replace('ICT product manager\nICT products manager\nICT products managers\nIT product manager\nICT product managers\nIT products manager','\n',char(10)),'ICT product managers analyse and define current and target status for ICT products, services or solutions. They estimate the cost effectiveness, points of risk, opportunities, strengths and weaknesses of products or services provided. ICT product managers create structured plans and establish time scales and milestones, ensuring optimisation of activities and resources.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a79d9e58-36be-4e67-a4db-ea6c8ff445fc','key_17186','','','1420','1420.2','retail department manager',replace('general store department  manager\ndepartmental  manager\noutlet store department manager\nconcession shop departmental manager\nretail department head\ndepartment sales manager\ngeneral merchandise department manager\nsection manager\ngeneral retail section manager\nretail department manager\nhead of department (retail)','\n',char(10)),'Retail department managers are responsible for activities and staff in a section in a store.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a79e7622-2307-44e2-94cc-d1ff1caf655a','key_17187','','','1344','1344.1.3','youth programme director',replace('youth development programme coordinator\nyouth development director\ninclusion director\nyouth mentoring director\nyouth development program coordinator\nyouth program director\nyouth programme coordinator\nyouth program coordinator','\n',char(10)),'Youth programme directors develop and implement programmes and policies to improve and ensure youths'' well-being. They facilitate communication with and between educational, recreational, counselling or other youth related institutions, organise events for youths and families, and promote social mobility and awareness.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a7a74a05-3dd0-46c6-99af-92df8042520c','key_17188','','','2131','2131.4.10','microbiologist',replace('helminthologist\nmicrobiology science researcher\nmicrobiology studies research scientist\nvirologist\nmicrobiology analyst\nmicrobiologist\nmicrobiology studies analyst\nmicrobiology studies scholar\nmicrobiology scholar\nmicrobiology research scientist\nhistologist\nbacteriologist\nmicrobiology studies research analyst\nmicrobiology scientist\nmicrobiology studies scientist\nmicrobiology research analyst\nparasitologist\nmicrobiology researcher\nmicrobiology biotechnologist\nmicrobiology studies researcher','\n',char(10)),'Microbiologists study and research the life forms, characteristics, and processes of microscopic organisms. They study microorganisms such as bacteria, protozoa, fungi, etc. in order to diagnose and counteract the effects that these microorganisms might have in animals, in the environment, in the food industry, or in the health care industry.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a7c1431b-a27c-4ab5-ace2-3f8753b481da','key_17189','','','8212','8212.3.4','photographic equipment assembler',replace('projector assembler\nphotography equipment assembler\nassembler of photographic equipment\nassembler of photography equipment\nassembler of cameras\nphotographic equipment assembler\ncamera assembler\nassembler of projectors\nvideo camera assembler\nassembler of photo equipment','\n',char(10)),'Photographic equipment assemblers build photographic products such as cameras, film cameras, and projectors. They collect the different optical, electronic, and mechanical camera components, such as the battery, mirrors, image processor, and lenses, and piece together cameras according to specifications using hand tools or machinery.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a7c1d23d-aeca-4bee-9a08-5993ed98b135','key_17190','','','2511','2511.15','ICT system developer',replace('ICT systems developers\nIT system developer\nsystem developer\nsystems programmer\nICT systems developer\nsystem software developer\nICT system developers\nsystems developer\nICT system developer\ncomponent developer\nchief ICT system developer','\n',char(10)),'ICT system developers maintain, audit and improve organisational support systems. They use existing or new technologies to meet particular needs. They test both hardware and software system components, diagnose and resolve system faults.','','Includes ICT system chief developer.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a7d6a377-3d3b-41cf-9301-f4a0a7ad3d96','key_17191','','','1321','1321.2.1.3','food production manager',replace('food production manager\nfood production team leader\nfood production supervisor\nproduction manager food and beverages','\n',char(10)),'Food production managers oversee and monitor production and have overall responsibility for staffing and related issues. Hence, they have a detailed knowledge of the manufacturing products and their production processes. On the one hand, they control process parameters and their influence on the product and on the other hand, they ensure that staffing and recruitment levels are adequate.','','Excludes people working in small companies.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a7f63810-a21a-4e7e-8aa2-6637d14ea3c1','key_17192','','','3411','3411.3','court administrative officer',replace('court clerk\ncourt administrator\ntribunal administration officer\ncourt administrative officer\ncourt administration officer\ntribunal administrative officer\ncourt registrar\ncourt usher','\n',char(10)),'Court administrative officers perform administrative and assisting duties for the court and judges. They are designated to accept or reject applications for informal probate and informal appointment of a personal representative. They manage case accounts and handle official documents. Court administrative officers perform assisting duties during a court trial, such as calling out the cases and identification of parties, keeping notes, and recording orders from the judge..','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a8c7004a-e59c-4a4d-9092-2cb4533ffe58','key_17193','','','6221','6221.5','aquaculture hatchery worker',replace('fish farm hatchery worker\naquatic animals hatchery worker\naquaculture hatchery worker','\n',char(10)),'Aquaculture hatchery workers are active in the production of aquatic organisms in land-based hatchery processes. They assist in the process of raising organisms throughout the early stages of their life cycle and the release of organisms when necessary.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a8ccc522-f57d-4800-a821-2e5c910e8301','key_17194','','','2113','2113.1.3','chemical tester','chemical tester','Chemical testers are responsible for the rapid on-the-spot analysis of steel test pieces incoming from the metal production shop for the purpose of timely corrections of the chemical composition of the liquid metal.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a8d05b64-f60b-4dd9-91c2-e2e63d82f2f5','key_17195','','','1112','1112.2','consul',replace('consul-general\nconsul\nhonorary consul\nattaché\nchargé d''affaires','\n',char(10)),'Consuls represent governments in foreign institutions such as embassies in order to facilitate economic and political cooperation between the two nations. They protect the interests of their home nation and provide bureaucratic assistance to citizens living as expatriates or travelling in the host country.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a9068f84-cecd-4cbb-9acb-e20c714435ec','key_17196','','','7114','7114.1','concrete finisher',replace('floor finisher\nconcrete renovator\npower floater\npowerfloat finisher\nconcrete layer\nconcrete floor finisher\nconcrete finisher and layer\nlayer and finisher\nwet concrete finisher\nconcrete finisher\ncement finisher\nconcrete flooring operative\nconcrete finisher ganger\ncement mason\nflooring operative\nconcrete floor finisher and layer','\n',char(10)),'Concrete finishers work with binding agents like cement and concrete. They put up any removable forms and pour concrete into the forms. They then execute one or several actions to finish the concrete: cutting, screeding or levelling, compacting, smoothing, and chamfering to prevent chipping.','',replace('Includes people perfomring renovation of concrete and cement. \n Excludes people performing insulated concrete forms.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a92a3e24-93f0-42b3-9ebb-d2d7d8a72cc5','key_17197','','','2114','2114.2','geophysicist',replace('consultant geophysicist\npetroleum geophysicist\nfield seismologist\nexploration geophysicist\nproject geophysicist\ngeophysicist\ngeophysical engineer','\n',char(10)),'Geophysicists study the physical characteristics of the earth and apply physical measurements to geological situations. Geophysicists use the principles of gravity, seismicity and electromagnetics to identify the structure and composition of the earth.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a9411821-4f54-406e-ba37-4c6e5a39f9fe','key_17198','','','3411','3411.2','conveyance clerk',replace('paralegal clerk\nconveyancer\nconveyance clerk\nconveyance law clerk\nconveyancing clerk\nproperty law clerk','\n',char(10)),'Conveyance clerks provide services for the legal transference of legal titles and properties from one party to another. They exchange the necessary contracts and ensure all properties, titles and rights are transfered.','','Excludes <a resource="http://data.europa.eu/esco/occupation/d21890a3-cbe9-49df-9a19-a4120d866548" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/d21890a3-cbe9-49df-9a19-a4120d866548"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">notary</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a945757e-e171-46a9-847a-13cf04f7cd27','key_17199','','','8172','8172.5.1','band saw operator',replace('contour band saw operator\nband saw user\ncontour  bandsaw operator\nband saw sawyer\nband saw operative\nhead saw specialist\nband saw technician\nresaw operator\ndouble-cut saw operator\nvertical band saw operator\ntimber bandsaw operator\nautomated band saw operator\nband saw operator\nmetal band saw operator\nvertical  bandsaw operator\nCNC band saw operator\ntimber band saw operator\nband scroll saw operator\nband scroll saw specialist\nhorizontal band saw operator\nbandsaw operator\nresaw specialist\nband saw specialist\nband scroll saw technician\nautomated bandsaw operator\nmetal bandsaw operator\nhead saw technician\nband saw worker\nproduction saw operator\nhorizontal bandsaw operator\nCNC bandsaw operator\nresaw technician\nhead saw operator','\n',char(10)),'Band saw operators work with industrial saws that feature a continuous flexible blade revolving around two or more wheels. Band saws are most effective at producing irregular shapes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a950e88b-cb5e-4a7d-8a94-3f9460b487d7','key_17200','','','2149','2149.2.7.1','compliance engineer',replace('compliance engineering expert\ncompliance engineer\ncompliance engineering adviser\ncompliance engineering specialist\ncompliance engineering consultant\ncompliance engineers','\n',char(10)),'Compliance engineers strive to keep the highest compliance of systems with engineering specifications. They can exert compliance in a varied array of engineering fields including mechanical, electrical, electronical systems. They ensure the engineering complies with regulations, safety measures, and internal directives.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a98f93a6-b1df-4759-88a9-3234f334afad','key_17201','','','7322','7322.9','screen printer',replace('silk-screen printing machine oeprator\nsilk-screen printing machine worker\nscreen printing machine specialist\nscreen printing machine technician\nsilk-screen printing operator\nsilk-screen printing machine technician\nscreen printing operator\nscreen-printer\nscreen print technician\nscreen printing machine operator\nsilk-screen printer\nsilk-screen printing machine operator\nsilk-screen printing machine specialist\nscreen printer','\n',char(10)),'Screen printers tend a press that presses ink through a screen. They are responsible for the setup, operation and maintenance of the screen printing machine.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a99b38bc-6e71-4bef-b127-0f2b5e357815','key_17202','','','2529','2529.5','ICT resilience manager',replace('vulnerability manager\nmanager for vulnerability\nmanager for IT resilience\nIT resilience manager\nICT resilience manager\ndisaster recovery manager\nmanager for disaster recovery','\n',char(10)),'ICT resilience managers research, plan and develop models, policies, methods, techniques and tools that enhance an organisation''s cyber security, resilience and disaster recovery.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a9a41b21-8dd4-476f-96c7-85733fa63d3b','key_17203','','','2635','2635.3.18','migrant social worker',replace('migrant advice and support worker\nrefugee social worker\nsocial worker\ncultural mediator social worker\nimmigrant advice worker\nasylum support worker\nimmigration support worker\nmigrant social worker\nimmigration social worker\nmigrant re-settlement worker\nasylum advice worker\nmigrant advice worker','\n',char(10)),'Migrant social workers provide advice to migrants in order to guide them through the necessary steps of integration, namely living and working in a foreign country. They explain eligibility criteria, rights, and duties. They help migrants in the development and maintenance of their information as clients for further referral to day care, social services and employment programs. Migrant social workers collaborate with employers and inform them of the available migrant services, advocating for migrant clients.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a9b20949-897b-481f-8642-d1f69263baff','key_17204','','','3115','3115.1.6','industrial maintenance supervisor',replace('maintenance machining and assembly supervisor\nmaintenance machine shop supervisor\nleading maintenance machinist\nmaintenance machine adjuster supervisor\nmaintenance machine charge hand\nmaintenance machine team leader\nlead maintenance  operative\nmaintenance machining and assembly manager\nmaintenance machine shop manager\nmaintenance machining supervisor\nmachine setter supervisor\nmaintenance machining department supervisor\nindustrial maintenance supervisor\nmaintenance work supervisor\nmaintenance machine and assembly maintenance team leader\nmaintenance and assembly team leader','\n',char(10)),'Industrial maintenance supervisors organise and supervise the activities and maintenance operations of machines, systems and equipment. They ensure inspections are done according to health, safety and environmental standards, and productivity and quality requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a9bcc9e0-5be7-47a0-818b-acad10fe90d9','key_17205','','','3111','3111.11','physics technician',replace('physics lab technician\nhealth physics technician\nphysics technician\nradiation health technician\nphysicist assistant\nmedical physics technician\nphysics assistant\nnuclear physics technician\nreprographics technician\nphysics laboratory technician','\n',char(10)),'Physics technicians monitor physical processes and perform tests for manufacturing, educational or scientific purposes. They work in laboratories, schools or production facilities where they assist physicists in their work. Physics technicians perform technical or practical work and report about their results.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a9c30651-ccbc-4a80-900c-7880615cdf6e','key_17206','','','2359','2359.10','instructional designer',replace('instructional designer\ninstructional developer\ninstructional engineer\ndesigner of corporate training\ninstructional designers\ninstructional design professional\ncorporate training designer\npractitioner of instructional course design\ninstructional design specialist\ninstructional course designer\ntraining course designer\ndesigner of training courses\ndesigner of instructional courses\ntraining designer\ninstructional course design practitioner\nlearning process designer\ninstructional systems designer','\n',char(10)),'Instructional designers develop instructional material for training courses using multimedia technology and authoring tools. They aim to create instructional experiences which make the acquisition of knowledge and skills more efficient, effective, and appealing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/a9c7a04d-807a-41b5-b441-87c73c73c9c5','key_17207','','','2643','2643.4','linguist',replace('koreanist\nscandinavist\nphilologist\nafricanist\ncomputational linguist\nromanist\nindologist\ngermanist\nslavist\nlinguistic scholar\nhellenist\nlinguistic scientist\nsemitist\narabist\nlatinist\nterminologist\nbaltist\nlinguistic researcher\nlinguists\nsinologist\njapanologist\ntibetologist\nanglist\nlinguistic research scientist\nlinguist\namericanist\nlinguistic science researcher\nphonetics specialist','\n',char(10)),'Linguists study languages scientifically. They master languages and can interpret them in terms of their grammatical, semantic, and phonetic characteristics. They research the evolution of language and the way it is used by societies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aa0fd823-2f45-41c8-a3c6-97d97885e455','key_17208','','','9216','9216.4','water-based aquaculture worker',replace('water-based aquaculture worker\nwater-based fish culture worker\nworker in water-based aquaculture\nwater-based fisheries worker\nwater-based fish farm worker','\n',char(10)),'Water-based aquaculture workers carry out manual activities in the ongrowing processes of cultured aquatic organisms in water-based suspended systems (floating or submerged structures). They participate in extraction operations and the handling of organisms for commercialisation. Water-based aquaculture workers maintain and clean facilities (nets, mooring ropes, cages).','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aa3780b7-3b06-40ae-8764-293fec6819d2','key_17209','','','7223','7223.6','drill press operator',replace('tapper operator\ndrill operative\nplastic CNC press operative\nautomatic driller and reamer\nreamer operator\ndrill-press operative\nmetal drill press operator\nautomatic drilling machine operator\ndrill press operator for metal\nplastic drill press operative\nautomatic reaming machine operator\nmetal cnc-press operator\nmetal drill-press operative\ncnc-press operative\nplastic drill press operator\nmetal cnc-press operative\nautomatic drilling machine operative\nplastic drill-press operator\nautomatic drill punch operator\ndrill press tender\nplastic drill-press operative\nbarrel reamer\nautomatic drilling technician\nmetal drill-press operator\ndrill operating technician\nCNC press operative\nmetal CNC press operative\nplastic CNC press operator\nmetal drill press operative\nplastic cnc-press operative\nmetal CNC press operator\ndrill press operator\ndrill press operative\ndrill presser\ndrill operator\nplastic cnc-press operator','\n',char(10)),'Drill press operators set up and operate drill presses designed to cut excess material from or enlarge a hole in a fabricated workpiece using a hardened, rotary, multipointed cutting tool that inserts the drill into the workpiece axially.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aa50ae49-1e40-4ba9-959f-22a11ff172b4','key_17210','','','3521','3521.1.6','recording studio technician',replace('studio sound engineer\nrecording technician\nsounds technician\nstudio technician\naudio restorer\nrecording studio audio engineer\nrecording studio sound technician\nre-recording mixer\nrecording studio technician\nrecording engineer\naudio post-production technician\naudio editor\nrecording sound engineer\nrecording audio technician\nspecial effects sound technician\nsound mixer\nrecording sound technician','\n',char(10)),'Recording studio technicians operate and maintain microphones and headsets in recording booths in recording studios. They operate mixing panels. Recording studio technicians manage all sound production requirements. They advise singers on the use of their voice. Recording studio technicians edit recordings into a finished product.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aa8fdeff-ab03-4b99-bdba-2f7a1a5c8946','key_17211','','','3331','3331.2.1.6','import export specialist in clothing and footwear',replace('trainee import export specialist in clothing and footwear\nassistant import specialist in clothing and footwear\ngraduate import specialist in clothing and footwear\ninternational trade compliance specialist in clothing and footwear\nsenior import specialist in clothing and footwear\ntrainee import specialist in clothing and footwear\ntrainee export specialist in clothing and footwear\nimport export specialist in clothing and footwear\nassistant export specialist in clothing and footwear\ngraduate import export specialist in clothing and footwear\ncustoms compliance specialist in clothing and footwear\nsenior export specialist in clothing and footwear\ngraduate export specialist in clothing and footwear\nsenior import export specialist in clothing and footwear\nassistant import export specialist in clothing and footwear','\n',char(10)),'Import export specialists in clothing and footwear have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aa9c6416-6a17-4e2f-a7c4-6d47c205d480','key_17212','','','5223','5223.7.24','medical goods specialised seller',replace('retail chemist shop specialised seller\ndrugstore salesperson\npharmacy salesperson\nmedical supply store salesperson\nmedical goods salesperson\nmedical goods specialized seller\nchemist shop specialised seller\npharmacy specialised seller\nretail chemist shop salesperson\nchemist shop salesperson\nmedical supply store specialised seller\ndrugstore specialised seller','\n',char(10)),'Medical goods specialised sellers dispense medicinal drugs and provide advice.','','Excludes  <a resource="http://data.europa.eu/esco/occupation/8db28ebf-8818-4639-aa49-30324901836e" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/8db28ebf-8818-4639-aa49-30324901836e"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">specialist pharmacist</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aaa485d0-6787-45c0-8596-b266c0b687fd','key_17213','','','7222','7222.3','locksmith',replace('key maker\nkey and lock specialist\nlocksmith\nkey and lock expert\nkey and lock installer','\n',char(10)),'Locksmiths install and repair mechanical and electronic lock systems using specialized tools. They cut and make duplicated keys for their clients and open locked doors in emergency situations. Locksmiths may also provide advice on security measures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aab8009a-5759-4f52-8865-6cc657b1f099','key_17214','','','1349','1349.19','publications coordinator',replace('head of publications\nglobal publications manager\npublications director\npublications manager\ndigital communication coordinator\npublications planning coordinator\nsenior publication coordinator\ntechnical publications coordinator\ninternational publications coordinator\ntechnical publications manager\nprint publications coordinator\noperational publications coordinator\ncorporate publications manager\npublications coordinator','\n',char(10)),'Publications coordinators are responsible for the production of print and online materials such as news letters, company procedures, technical documents and other publications for institutions and businesses. They supervise the publishing teams and make sure the publications reach their target audience.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aac0c510-f238-4dbe-beeb-1d7b73f4552c','key_17215','','','5163','5163.1','cemetery attendant',replace('churchyard attendant\ngraveyard worker\ncemetery worker\nburial site worker\ncemetery attendant\nundertaker''s helper\nburial ground worker\ngraves digger\nburial site attendant\ncemetery labourer\nburial ground attendant\nburial grounds custodian\ngraveyard attendant','\n',char(10)),'Cemetery attendants maintain the cemetery ground in good condition. They ensure the graves are ready for burial prior to funerals and ensure accurate burial records. Cemetery attendants offer advice to funeral services directors and the general public.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aac62c63-b6bf-4387-8ba3-75a487f25824','key_17216','','','3131','3131.3.9','steam turbine operator',replace('steam boiler operator\nsteam turbine safety operator\nsteam generator operator\nsteam turbine generator operator\nsteam turbine machinery operator\nturbine hall worker\nsteam turbine operator\nsteam turbine monitoring operator\nsteam-electric power operator','\n',char(10)),'Steam turbine operators operate and maintain machinery which generates power. They ensure the safety of the operations and monitor operations to detect problems, and react to emergency situations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aacc3918-b5d3-484b-9480-5d29aa550d74','key_17217','','','1324','1324.8','supply chain manager',replace('supply chain procurement manager\nsupply chain project manager\nsupply chain manager\nsupply chain specialist\nsupply and distribution manager\nglobal sourcing manager\nsupply chain coordinator','\n',char(10)),'Supply chain managers plan, manage and coordinate all activities related to the sourcing and procurement of supplies needed to run manufacturing operations from the acquisition of raw materials to the distribution of finished products. The supplies can be raw materials or finished products, and it can be for internal or external use. Moreover, they plan and commission all the activities needed to be performed in manufacturing plants and adjust operations to changing levels of demand for a company''s products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aad7819e-d62e-41de-a594-9f2be186a6c2','key_17218','','','2142','2142.1.9','transport engineer',replace('transport project engineer\ntransportation engineering consultant\ntraffic engineer\ntransportation engineer\ntransport infrastructure engineer\ntransport engineer\ntransportation engineering adviser\nhighways engineer\ntransportation engineering expert\ntransportation project engineer\ntransport projects engineer\nchartered transport engineer\ntransportation infrastructure engineer\nroads engineer\ntransportation engineering specialist','\n',char(10)),'Transport engineers design and set the engineering specifications for the construction and development of roadways and transport infrastructure. They apply engineering concepts and knowledge for developing sustainable and efficient modes of transportation ranging from roads to canals, railways, and airports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aaeb2402-2ab3-411f-9a44-8ff4dd092de6','key_17219','','','3324','3324.4.24','wholesale merchant in mining, construction and civil engineering machinery',replace('senior wholesale merchant in  mining, construction and civil engineering machinery\nB2B sales consultant in  mining, construction and civil engineering machinery\nbusiness to business seller in  mining, construction and civil engineering machinery\nwholesale merchant in mining, construction and civil engineering machinery\nwholesaler in  mining, construction and civil engineering machinery\nwholesale trader in  mining, construction and civil engineering machinery\nwholesale merchandiser in  mining, construction and civil engineering machinery\ngraduate wholesale merchant in  mining, construction and civil engineering machinery\nwholesale buyer in  mining, construction and civil engineering machinery\ntrainee wholesale merchant in  mining, construction and civil engineering machinery\nassistant wholesale merchant in  mining, construction and civil engineering machinery\nbusiness to business sales consultant in  mining, construction and civil engineering machinery','\n',char(10)),'Wholesale merchants in mining, construction and civil engineering machinery investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aaeec9a7-dc57-4485-890c-86b3eef735f9','key_17220','','','3512','3512.1','ICT help desk agent',replace('ICT help desk agent\nIT help desk advisor\nICT helpdesk agent\nIT help desk agent\nICT help desk advisor\nICT helpdesk agents\nICT help desk agents\nICT service desk agent','\n',char(10)),'ICT help desk agents provide technical assistance to computer users, answer questions or solve computer problems for clients via telephone or electronically. They provide assistance concerning the use of computer hardware and software.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aaf732b5-ee19-4c82-a08f-700fb98f5ca7','key_17221','','','3354','3354.3','passport officer',replace('passport examining officer\nborder control officer\npassport officer\npassport issuing administrator\nher majesty''s passport officer\npassport office worker','\n',char(10)),'Passport officers provide passports and other travel documents such as certificates of identity and refugee travel documents. They also keep record of all provided passports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ab104661-db96-4752-8c84-97d98f6e8969','key_17222','','','8152','8152.1.1','knitting machine supervisor',replace('knitting foreman\nknitter supervisor\nfabric knitting supervisor\nknitting inspector\nknitting forewoman\nknitter of fabrics\nknitting machine supervisor\nfabric knitter','\n',char(10)),'Knitting machine supervisors supervise the knitting process of a group of machines, monitoring fabric quality and knitting conditions. They inspect knitting machines after set up, start up and during production to ensure that the product being knit meets specifications and quality standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ab360abd-32e2-4e03-967d-a10758efffa7','key_17223','','','2144','2144.1.11','mechatronics engineer',replace('robotics engineer\nengineer of mechatronics\nspecialist mechatronic engineer\nelectromechanical engineer\nmechatronic engineering specialist\nengineer in mechatronics\nmechatronics systems designer\nmechanical systems engineer\nadvanced mechatronics engineer\ncybernetics engineer\nmechatronics engineer','\n',char(10)),'Mechatronics engineers design and develop intelligent systems, such as robotic devices, smart home appliances, and aeroplanes, through combining technologies from mechanical, electronic, computer, and control engineering. They create blueprints or design documents for parts, assemblies or finished products using software programs, and also oversee and manage projects.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ab3c4d3e-3189-47ad-82af-57cdae3cb589','key_17224','','','2149','2149.12','optical engineer',replace('optical engineering expert\noptical technology engineering expert\noptical engineering adviser\noptical technology engineering adviser\noptical engineering consultant\noptics engineer\noptical technology engineer\noptical engineering specialist\noptical technology engineering consultant\noptic engineer\noptical technology engineering specialist\noptical engineer','\n',char(10)),'Optical engineers design and develop different industrial applications with optics. They have knowledge of light, light transmission principles, and optics in order to design engineering specs of equipment such as microscopes, lenses, telescopes, and other optical devices.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ab4086a0-0e2f-436e-80f8-a3291e594bd1','key_17225','','','7321','7321.1.2','lithographer',replace('certified master lithographer\nCT technician\ncomputer-to-plate technician\nlithographic printer\nlithographic operator\nCTP worker\noffset lithographer\nplatesetter specialist\nlithographer and digital printer\nplatesetter operator\nplatesetter technician\nlithographer\nmaster printer/lithography\nCTP specialist\ncomputer-to-plate operator\nplate worker\nmetal lithographer\ncomputer-to-plate specialist\nCTP operator','\n',char(10)),'Lithographers make and prepare metal plates to be used as the original in various printing processes and media. Plates are usually laser-etched from digital sources with computer-to-plate technology, but can also be made by applying types of emulsions to the printing plate.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ab4dd4f9-ee6c-41cc-8b72-42ad0b13d1e2','key_17226','','','2143','2143.1.4','waste treatment engineer',replace('waste treatment engineering expert\nindustrial waste treatment engineer\nwaste treatment engineering specialist\nwaste treatment technology engineering adviser\nwaste treatment engineering consultant\nwaste treatment technology engineer\nwaste treatment engineering adviser\nwaste treatment technology engineering consultant\nwaste treatment engineers\nwaste treatment engineer\nwaste treatment technology engineering expert\nwaste treatment technology engineering specialist','\n',char(10)),'Waste treatment engineers design processes, facilities and equipment used in the collection, treatment, and distribution of waste. They research environmental standards and policies in order to optimise waste treatment procedures and ensure minimal strain on the environment by analysing and classifying the processed waste.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ab5a5073-453c-4a93-aff7-345a19f8b954','key_17227','','','3312','3312.6','mortgage loan underwriter',replace('loan underwriter\nloan approval officer\nmortgage compliance underwriter\nmortgage guideline underwriter\nmortgage loan underwriter','\n',char(10)),'Mortgage loan underwriters ensure compliance with underwriting guidelines. They participate in the implementation of new underwriting guidelines. They also review closed and denied loans.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ab762f91-d737-485f-a85c-67cab92dee01','key_17228','','','2433','2433.2','medical sales representative',replace('medical device sales representative\nmedical representative\nclinical device sales representative\nclinical representative\nmedical sales person\nmedical sales businessperson\nmedical salesman\nmedical saleswoman\nclinical sales representative\nmedical sales representative\nmedical device seller','\n',char(10)),'Medical sales representatives promote and sell medical devices, equipment and pharmaceutical products to healthcare professionals. They provide product information and demonstrate features to healthcare professionals. Medical representatives negotiate and close sales contracts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ab7bccb2-6f81-4a3d-a0c0-fca5d47d2775','key_17229','','','2163','2163.1','industrial designer',replace('product design specialist\nproduct designer\nCAD design expert\nindustrial design expert\n3D CAD design specialist\nindustrial design specialist\n3D CAD design expert\nergonomic designer\npackaging designer\nCAD design specialist\nceramic products designer\nproduct design expert\nglass products designer\nCAD designer\nindustrial designer\n3D CAD designer\ntoy designer','\n',char(10)),'Industrial designers work out ideas and develop them into designs and concepts for a wide variety of manufactured products. They integrate creativity, aesthetics, production feasibility, and market relevance in the design of new products.','','Includes people performing 3D CAD design for the manufacture of jewellery.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ab9e06c8-e0fc-4c1b-afa2-61421592f1a5','key_17230','','','8160','8160.37','germination operator',replace('brewer\nmalster\ngermination operator\nsteep vessel operator\nmaltster\nbrewery technician','\n',char(10)),'Germination operators tend steeping and germination vessels where barley is germinated to produce malt.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/abab3d6a-82b9-49bd-8a4b-7bc678130558','key_17231','','','2359','2359.1','academic advisor',replace('degree program guidance practitioner\nacademic student advisor\ndegree program advisor\nhigher education advisor\ndegree program coach\npost-secondary level student advisor\neducation advisor\nstudent career guide\nacademic advisor','\n',char(10)),'Academic advisors assist students, at post-secondary level, in recognising and achieving their educational goals. They advise students on their schedule selection of school programmes, communicate to them degree requirements, and assist them in career planning. Academic advisors also discuss the student''s academic performance and its implications and make suggestions for improvement including study advice. They also work closely with other university administrators and professors and ensure they are up to date on university or college regulation, program, or requirement changes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/abae06d8-525f-4a2d-8bbd-67b559aad8f2','key_17232','','','3259','3259.8','temperature screener ',replace('entrance screener\ncovid temperature screener\ntemperature checker\nhealth screener','\n',char(10)),'Temperature screeners are responsible for checking the temperature of guests, staff or visitors entering the premises of an organisation or public spaces, as a measure to prevent the spread of infectious diseases in private and public spaces.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/abc849b6-9e75-41ce-b1aa-4d488280a8a3','key_17233','','','7515','7515.2','farm milk controller',replace('dairy milk analyst\nfarm milk monitor\nfarm milk controller\ndairy milk controller\nfarm milk analyst','\n',char(10)),'Farm milk controllers are responsible for measuring and analysing the production and quality of the milk and providing advise accordingly. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ac036ea7-883a-4265-96d5-c02bbe6667e1','key_17234','','','3142','3142.1.2','aquaculture site supervisor',replace('fish culture site supervisor\nfish farm site supervisor\nfish farm site manager\nsupervisor of aquaculture sites\nfish culture site manager\nfishery site supervisor\naquaculture site supervisor','\n',char(10)),'Aquaculture site supervisors supervise production processes in large-scale aquaculture operations and inspect aquaculture sites to maintain and improve performance. They maintain the health, safety and security of the workplace, develop management plans to reduce the risks from pests, predators and diseases and supervise both the disposal of bio and chemical waste and the maintenance of equipment and machinery.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ac160654-0d3e-49aa-b798-1861ce9d78b6','key_17235','','','7523','7523.6','woodturner',replace('wood turning lathe setter\nwood turning lathe tender\nwood turning machinist\nwood lathe specialist\nwood turning lathe operator\nwood machinist\nwood lathe operator\nwood lather\nCNC wood machinist\nwood lathe technician\nwoodturner\nwood turning lathe operative\nmulti-spindle wood machinist\nwood turner\nlather of wood\nbodger\nwood lathe turner\nwood lather turner\nwoodworking machinist','\n',char(10)),'Woodturners use a lathe to remove superfluous material from wood. The lathe turns the workpiece around its axis, as shape tools are used to obtain the desired shape.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ac1fc6a9-70d2-475c-8fa0-82ef83830968','key_17236','','','2143','2143.1','environmental engineer',replace('industrial environmental engineer\nair protection environmental engineer\nenvironmental engineering adviser\nwater pollution engineer\nenvironmental analyst\nsanitary engineer\nenvironmental engineering expert\nenvironment engineer\nenvironmental engineer\nenvironmental engineering specialist\nchemical environmental engineer\nenvironmental engineering consultant\nagricultural conservation engineer\npollution engineer\nenvironmental specialist for water management','\n',char(10)),'Environmental engineers integrate environmental and sustainable measures in the development of projects of various natures. They seek to preserve natural resources and natural sites. They work together with engineers from other fields to envision all the implications that projects might have in order to design ways to conserve natural reserves, prevent pollution, and deploy sanitary measures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ac37627c-a999-4779-997e-9795cc4f9a3d','key_17237','','','2151','2151.1.5','power distribution engineer',replace('power distribution technology engineering specialist\npower distribution engineering expert\npower distribution engineering adviser\npower distribution engineer\npower distribution engineering specialist\npower distribution technology engineering consultant\npower distribution systems engineer\npower distribution system engineer\npower distribution technology engineering adviser\npower distribution technology engineering expert\npower distribution technology engineer\npower distributions engineer\npower distribution engineering consultant','\n',char(10)),'Power distribution engineers design and operate facilities which distribute power from the distribution facility to the consumers. They research methods for the optimisation of power distribution, and ensure the consumers'' needs are met. They also ensure compliance to safety regulations by monitoring the automated processes in plants and directing workflow.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ac401ec0-7ce0-423f-b8b5-167da9e046a7','key_17238','','','8181','8181.6','fibre machine tender',replace('extruding and forming machine setter\nfiber machine operative\nstaple fiber machine tender\nforming machine setter\nfiber machine tender\nstaple fibre machine tender\nfibre machine operative\nglass fibre machine operator\nfiber extruding machine operator\nfibre machine operator\nextruding machine setter','\n',char(10)),'Fibre machine tenders operate and maintain extrusion machines that form sliver from filaments. They work with synthetic materials such as fiberglass or liquid polymer or non-synthetic materials such as rayon.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ac60ed89-da6d-4f1a-93ee-1031789c7796','key_17239','','','3343','3343.1.1','court reporter',replace('courtroom reporter\ncourt stenographer\ndeposition reporter\ncourt recorder\nstenographer\nofficial court reporter\nshorthand reporter\ncertified shorthand reporter\ncourt reporter\ncourt transcriber\nlaw reporter','\n',char(10)),'Court reporters type in word processors or any other software each one of the words mentioned in the courtroom. They transcript the hearings that take place in court in order to issue the official hearings of the legal case. They allow that the case can be further studied by the parties in an accurate fashion.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ac734172-ae9a-41ab-80a0-a7b33694e027','key_17240','','','2654','2654.5','video and motion picture editor',replace('motion picture editor\ntelevision and motion picture editor\nmotion picture and video editor\ncinema editor\nfilm editor\nfilm and video editor\nvideo editor\nvideo and motion picture editor\neditor','\n',char(10)),'Video and motion picture editors are responsible for the assembly and cutting of raw footage into a logically and aesthetically-pleasant material for movies, television series, or domestic purposes. They reorganize scenes that have been shot and decide which special effects are needed. Video and motion picture editors work closely together with sound editors and music directors.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ac7c07d1-bc11-4f19-bca5-e468e9b1a53c','key_17241','','','7119','7119.4','steeplejack',replace('rope access steeplejack\nsteeplejack\nrope access technician\nheightworker\nMWEP operator\nsuspended platform driver','\n',char(10)),'Steeplejacks are specialised heightworkers who safely scale the outside of buildings and structures to perform critical work.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ac80b511-fd22-4edf-8570-e531704778bd','key_17242','','','2642','2642.1.1','blogger',replace('on-line content writer\nvegan food blogger\nlifestyle blogger\nhealth blogger\nblog content writer\nblog writer\nfashion blogger\nfood blogger\ndrinks blogger\nblogger\nsports blogger\ntravel blogger\nhead blogger','\n',char(10)),'Bloggers write online articles on a wide range of subjects such as politics, fashion, economics and sports. They can relate objective facts, but often they also give their opinion on the related topic. Bloggers also interact with their readers via comments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ac8b3cd1-a127-4e6a-8208-5cfcf7111955','key_17243','','','2120','2120.6','statistician',replace('statisticians\nstatistical researcher\nstatistic research scientist\nstatistics research analyst\nstatistician\nstatistics analyst\nstatistics specialist\nstatistics researcher\nstatistical scientist\nstatistical research scientist\nstatistical data analyst\nstatistical scholar\nstatistics science researcher\nsenior statistician\nstatistical research analyst\nstatistics research scientist\nstatistics scholar\nstatistics scientist\nstatistical science researcher\nstatistic research associate','\n',char(10)),'Statisticians collect, tabulate, and, most importantly, analyse quantitative information coming from a varied array of fields. They interpret and analyse statistical studies on fields such as health, demographics, finance, business, etc. and advise based on patterns and drawn analysis.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/acb6b99a-6c13-482a-81b1-8b5614e0153e','key_17244','','','3435','3435.25.5','stage technician',replace('theater technician\nstage technician coordinator\nall round event technician\nstage technician manager\naudio video and lighting technician\nallround stage technician\ntheater technics operator\nall round engineer\nmulti-skilled stage technician\nall round theatre technician\nstage technician assistant\naudio visuals & lighting technician\ntheater technical operator\ntheatre technician\nmulti-skilled theatre operator\nevent technician\ntheatre technical operator\ntheatre technics operator\nstage technician\ntheatre technical officer\nsound, lighting and video technician','\n',char(10)),'Stage technicians control different aspects of a performance based on the artistic or creative concept, in interaction with the performers. They prepare and perform the setup, program the equipment and operate various  systems. Stage technicians take care of lighting, sound, video, sets and/or fly systems. Their work is based on plans, instructions and calculations. They can work in small venues, theatres and other small artistic productions.','','Includes activities in event and rental companies.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/acbb7a20-c947-480d-a07e-868a0ab3eab8','key_17245','','','2131','2131.4.12','specialist biomedical scientist',replace('specialist biomedical doctor\nspecialist in biomedical science\nbiomedical science specialist\nspecialist medical scientist\nbiomedical scientist specialist\nspecialist biomedical scientist\nexpert in biomedical science\nspecialist clinical laboratory scientist\nspecialist biomedical researcher\nspecialist in biomedical research','\n',char(10)),'Specialist biomedical scientists lead a department or specialist area, working as a diagnostic partner with a clinical team (investigating and diagnosing patient illnesses like diabetes, haematological disorders, coagulation, molecular biology or genomics) or undertaking clinical research projects.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/acf28133-525f-4838-9248-d852e28b8f98','key_17246','','','1420','1420.4.16','delicatessen shop manager',replace('delicatessen counter manager\ngourmet shop manager\nfine foods shop manager\ndeli shop manager\ngourmet store manager\ncharcuterie shop manager\nfine foods store manager\ndelicatessen shop manager\ndelicatessen store manager\ncraft foods shop manager\nfarm foods shop manager\ndelicatessen department manager\ndeli store manager','\n',char(10)),'Delicatessen shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/acf462a3-9c34-46cb-952a-c612c38299f4','key_17247','','','2611','2611.1.2','prosecutor',replace('crown prosecutor\npublic prosecutor\nprosecutor\nstate attorney\nprosecuting attorney','\n',char(10)),'Prosecutors represent governmental bodies and the general public in court cases against parties accused of illegal activity. They investigate the court cases by examining evidence, interviewing involved parties, and interpreting the law. They use the results of their investigation in order to present the case during court hearings, and to construct persuasive arguments in order to ensure the outcome is the most favourable for the parties they represent.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/acf69cab-8629-45c8-ae10-c8fb15f474b6','key_17248','','','2632','2632.1','anthropologist',replace('ethnologist\ngender scientist\nmusic scientist\nanthropologist\nsocial anthropologist\nforensic anthropologist\nanthropologue\nsociocultural anthropologist\nmovie scientist\ntheatre scientist\ncultural scientist\nmusicology scientist\nbiological anthropologist\narts scientist\nanthropology research scientist\nlinguistic anthropologist\ntelevision scientist\narchaeological anthropologist\nart sciences scientist\ncultural anthropologist','\n',char(10)),'Anthropologists research all aspects of life pertaining to humans. They study the various civilisations that have existed along the time and their ways of organisation. They attempt to analyse the physical, societal, linguistic, political, economical, philosophical, and cultural aspects of different people. The aim of their studies is to understand and describe the past of humanity and solve topical societal problems. They explore different perspectives such as philosophical anthropology.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ad14ec64-128c-4a72-9c1a-257a5f3b180c','key_17249','','','2114','2114.1.2','exploration geologist',replace('exploration geology researcher\nmineralogist\nexploration geologist\nwellsite geologist\nminerals engineer\nsenior mine geologist\nexploratory geologist\nexploration geology research analyst\neconomic geologist\nminerals exploration geologist\nfield exploration geologist\nexploration geology scientist\nexploration geology analyst\nexploration geology research scientist\nexploration field geologist\nexploration geology science researcher\nexploration geological consultant','\n',char(10)),replace('Exploration geologists examine and prospect for mineral deposits. They identify, define and obtain legal title to an economically viable mineral deposit.  They are responsible for design, management and execution of the exploration programme. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ad404c6b-291f-439e-9ad4-c93ddabd3c13','key_17250','','','1324','1324.3.1.6.23','mining, construction and civil engineering machinery distribution manager',replace('mining, construction and civil engineering machinery logistics manager\nmining, construction and civil engineering machinery distribution planner\ngraduate mining, construction and civil engineering machinery distribution manager\nsenior mining, construction and civil engineering machinery distribution manager\nmining, construction and civil engineering machinery logistics planner\nmining, construction and civil engineering machinery supply chain planner\nmining, construction and civil engineering machinery supply chain manager\nassistant mining, construction and civil engineering machinery distribution manager\nmining, construction and civil engineering machinery distribution manager\ntrainee mining, construction and civil engineering machinery distribution manager','\n',char(10)),'Mining, construction and civil engineering machinery distribution managers plan the distribution of mining, construction and civil engineering machinery to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ad6589cb-ef1e-482d-863d-d80b2eb6b070','key_17251','','','7322','7322.4','hot foil operator',replace('hot press technician\nhot foil stamping machine technician\nfoil stamp machine operator\nhot press specialist\nfoil stamping operator\nhot foil stamping machine specialist\nhot stamp specialist\nhot stamp operator\nfoil press specialist\nhot press operator\nfoil press technician\nfoil printing machine operator\nhot foil operator\nhot foil technician\nhot foil machine specialist\nfoil blocking technician\nfoil stamping technician\nhot foil blocker\nfoil stamping specialist\nfoil press operator\nhot foil specialist\nhot foil machine operator\nfoil blocking specialist\nhot foil stamping die cutter\nfoil printing machine specialist\nhot stamp technician\nfoil printing machine technician\nhot foil machine technician\nfoil blocking operator\nhot foil stamping machine operator\nhot foil stamper','\n',char(10)),'Hot foil operators tend machines which apply a metallic foil on other materials using pressure cylinders and heating. They also mix colors, set up the appropriate machinery equipment and monitor printing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ad7bbc92-d5cb-4b64-a1a3-efc593afd437','key_17252','','','7543','7543.8.2','lumber grader',replace('head lumber grader\nlumber proofer\nlumber quality tester\nhead lumber quality grader\nhead lumber proofer\nhead lumber tester\nlumber quality grader\nhead lumber quality tester\nlumber tester\nlumber grader','\n',char(10)),'Lumber graders inspect lumber, or wood cut into planks. They test the lumber, look for irregularities and grade the wood based on quality and desirability of the pattern.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ad7c76e8-af96-42af-99a5-d579f707758c','key_17253','','','8211','8211.1','aircraft assembler',replace('aircraft sub-assembly installer\naircraft assembler\naeroplane assembler\naircraft rigging assembler\naircraft mechanical systems installer\naircraft structure assembler\naircraft fitter\nhelicopter assembler\nplane assembler\nflight control installer\nmilitary aircraft assembler\naircraft wing assembler\nfixed wing aircraft\njet aircraft  assembler\nflight control assembler\naircraft fuselage assembler','\n',char(10)),'Aircraft assemblers use hand tools, power tools and other equipment such as CNC machines or robots to construct, fit and install prefabricated parts to manufacture fixed or rotary wing aircrafts and aircraft subassemblies such as flight controls, aircraft skins, rigging and other mechanical systems. They read and interpret blueprints. They operate control systems to determine functional performance of the assemblies and adjust accordingly.','','Includes people working in benches or on aircraft structures.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ad8ab454-b506-46a5-afd3-66bbffd54115','key_17254','','','2165','2165.2','cartographer',replace('cartographic draftsperson\ncartographic science researcher\ncartographic analyst\ncartographic drafter\ncartographic research analyst\nmap maker\ncartographic research scientist\ncartographic researcher\ncartographer\ncartographic scientist\ncartographers','\n',char(10)),'Cartographers create maps by combining various scientific informations depending of the purpose of the map (e.g. topographic, urban, or political maps). They combine the interpretation of mathematical notes and measurements with the aesthetics and visual depiction of the site for developing the maps. They may also work on developing and improving geographic information systems and may perform scientific research within cartography.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ada3fc30-c205-4279-90b8-ed5c7c467626','key_17255','','','1223','1223.2.1.4','leather goods product development manager',replace('product development director leather goods\nleatherware product development supervisor\nleather goods product development director\nleather goods product development supervisor\nleather goods pre-production manager\nleatherware product development manager\nleather goods product manager\nleather goods product development manager\nleatherware product development director\nleather goods product development coordinator\nproduct development coordinator leather goods\nproduct development manager leather goods','\n',char(10)),'Leather goods product development managers coordinate the leather goods design and product development process in order to comply with marketing specifications, deadlines, strategic requirements and policies of the company. They communicate and collaborate with other cross functional teams or professionals involved in leather goods production, such as logistics and marketing, costing, planning, production and quality assurance. They are responsible for the leather goods product collections development which involve activities, such as tracking style development and reviewing design specification in order to meet the design vision. They are also responsible for the manufacturing environment and the rent-ability of the companies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ade351da-7bda-424e-8682-bbdcab2a25be','key_17256','','','4313','4313.1','payroll clerk',replace('payroll bookeeper\npayroll administrator\npersonnel and payroll technician\npayroll and benefits clerk\npayroll accounting clerk\npayroll technician\npayroll clerk\npayroll officer\npayroll accounts clerk\nwages clerk','\n',char(10)),'Payroll clerks manage the time sheets and pay checks of the employees and ensure the accuracy and correctness of the information. They check overtime, sick days and vacations and distribute the pay checks.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/adf9257e-78cb-467d-b259-59ed65bd7aed','key_17257','','','3112','3112.1.8','fire safety tester',replace('fire safety testing technologist\nfire inspection testing technology expert\nfire tester\nfire inspection technician\nfire safety testing technology expert\nfire inspection testing technology specialist\nfire safety testing technology specialist\nfire safety testing technician\nfire inspection testing technologist\nfire safety tester','\n',char(10)),'Fire safety testers conduct a variety of tests on materials such as building, transportation and textiles materials, as well as on fire prevention and fire fighting systems. They measures, among other things, the flame resistance and behaviour of materials under extreme circumstances.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ae2c1bf4-669e-4fec-a7d7-698753699083','key_17258','','','2310','2310.1.32','philosophy lecturer',replace('university philosophy teacher\nprofessor of philosophy\nsenior lecturer in philosophy\nlecturer of philosophy\nhigher education philosophy teacher\nphilosophy lecturer\nphilosophy instructor\nphilosophy docent\nphilosophy professor\nlector of philosophy\nphilosophy lector\nuniversity philosophy lecturer\nlecturer in philosophy\nuniversity lecturer in philosophy\nphilosophy teacher','\n',char(10)),'Philosophy lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, philosophy, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of philosophy, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ae3f1edd-d191-4a8d-8dfc-099dcdc6f1e8','key_17259','','','8142','8142.13','record press operator',replace('record press machinist\nrecord pressing machine technician\nvinyl record machine technician\nrecord press operative\nrecord press tender\nvinyl record machine tender\nrecord press operator\nvinyl record machine specialist\npress tender\nvinyl record press tender\nrecord pressing machine specialist\nvinyl record press specialist\nvinyl record press operator\nrecord press technician\nrecord press worker\nvinyl record machine operator\nvinyl record press technician\nrecord pressing machine operator\nrecord press specialist','\n',char(10)),'Record press operators tend a machine that presses vinyl with a negative impression of a master disc. As pressure is applied, the vinyl is forced into the grooves of the master disc and a playable record is obtained.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ae66ca51-e762-48b4-bd46-071dc87cb30d','key_17260','','','2651','2651.10','storyboard artist',replace('storyboard and animatic artist\nstoryboarder\nstory-boarder\nstoryboard artist and revisionist\nstoryboard artist\nstoryboard revisionist\nstoryboard & animatic artist\nstoryboard illustrator\nstoryboard artist & revisionist\nstory boarder','\n',char(10)),'Storyboard artists draw out the scenes of a motion picture or television series according to the script in order to see what will be possible during production. They work together with the producer and video and motion picture director.','','Includes people performing activities by hand or computer.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ae73f0dc-05a7-4e23-a234-9bb7e6d86975','key_17261','','','5243','5243.1','door to door seller',replace('travelling saleswoman\ndoor to door salesman\nhome seller\ndoor to door salesperson\ndoor to door seller\ndoor-to-door seller\ndoor to door saleswoman\ndoor to door entrepreneur\ntravelling salesman\ndoorstep seller\ntravelling salesperson','\n',char(10)),'Door to door sellers sell goods and services door to door.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ae8b19cc-8c6e-4a16-ada2-c15f97ded133','key_17262','','','2144','2144.1.15','optomechanical engineer',replace('optomechanical technology engineering specialist\noptomechanical technology engineering consultant\noptomechanical technology engineering expert\noptomechanical technology engineering adviser\noptomechanical engineering adviser\nopto-mechanics engineer\noptomechanical technology engineer\noptomechanical manufacturing engineer\noptomechanical engineering specialist\nengineer of optomechanical systems\nengineer in optical systems\noptomechanical engineer\nopto-mechanical engineer\noptomechanical design engineer\noptomechanical engineering consultant\nengineer in optomechanical systems\nengineer in optomechanical design\noptomechanical engineering expert','\n',char(10)),'Optomechanical engineers design and develop optomechanical systems, devices, and components, such as optical mirrors and optical mounts. Optomechanical engineering combines optical engineering with mechanical engineering in the design of these systems and devices. They conduct research, perform analysis, test the devices, and supervise the research.','','Excludes people performing engineering activites related to optoelectronics.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aeae4491-91b2-4029-9648-364e0bc4ecff','key_17263','','','3122','3122.4.4','electrical equipment production supervisor',replace('production supervisor in electrical equipment manufacturing\nsupervisor of electrical equipment manufacturing\nelectrical equipment production manager\nelectrical equipment production line supervisor\nelectrical equipment production supervisor\nelectrical equipment manufacturing manager\nmanager of electrical equipment manufacturing\nelectrical equipment production line manager\nelectrical production supervisor\nelectrical equipment manufacturing supervisor','\n',char(10)),'Electrical equipment production supervisors coordinate, plan and direct the production process of electrical equipment. They manage labourers working on the production line, oversee the quality of the assembled goods, and perform cost and resource management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aeb7c6bd-22c9-43c2-8551-a1cc2560584f','key_17264','','','1324','1324.8.1','forecast manager',replace('analytic and forecasting manager\ndemand planning manager\nforecast manager\nforecasting and analysis director\nforecast and planning analyst\nforecast analyst\nforecast planning manager','\n',char(10)),'Forecast managers have a deep understanding of the operations of the company, the inventory levels, production batches, requirements and costs of production per product, and trends in the demand levels. They use all that information in combination with forecasting software in order to define production orders aiming for the most efficient production combinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aecb033a-2be8-459c-bbdc-87bbae922894','key_17265','','','3123','3123.1.16','plastering supervisor',replace('stucco supervisor\nsupervisor, plastering\nplastering supervisor\nplastering-supervisor\nplasterboarding supervisor\ndry lining supervisor','\n',char(10)),'Plastering supervisors monitor plastering activities. They assign tasks and take quick decisions to resolve problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/af2f3615-63ab-44dc-957d-c9660410d336','key_17266','','','8219','8219.8','metal products assembler',replace('assembly technician\nproduction line worker\nmetal products assembler\nproduct assembler\nlight assembly operative\nfabricator assembler\nassembler\nmetal product assembler and fabricator\nfabrication assembler\nfabrication operator\nfabricator assembler metal products\nfabrication assembler metal products\nmetal fabricating operative\nfabricator assembly operator\nlight assembly worker\nmetal product fabricator\nproduction worker\nstructural fabrication operator\nfabricator assembly operative\nmetal product assembler\nstructural fabricator\nfabrication apprentice\nfabrication assistant\nmetal fabrication apprentice','\n',char(10)),'Metal products assemblers fit and fasten metal materials according to strictly laid down procedures in order to produce various parts or final products using power tools, hand tools, and other machinery.','','Excludes people performing assembly of electronic, electrical or mechanical elements.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/af355ac4-dd89-44df-99a6-bba70202d6fe','key_17267','','','3341','3341.5','data entry supervisor',replace('data processing coordinator\ndata records supervisor\ndata entry coordinator\ndata entry supervisor\ndata processing supervisor\ndata control clerk supervisor\ndata entry clerks supervisor','\n',char(10)),'Data entry supervisors manage the day-to-day operations of data entry staff. They organise the workflow and tasks. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/af513bd8-4340-4452-aa30-05a073ecc49a','key_17268','','','8322','8322.3','hearse driver',replace('hearse driver\nchauffeur\ndriver\nfuneral car driver\nfuneral driver','\n',char(10)),'Hearse drivers operate and maintain specialised vehicles to transport deceased persons from their homes, hospital or funeral home to their final resting place. They also assist the funeral attendants with their duties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/af69484e-b43f-4685-b22d-f3418df45c4d','key_17269','','','2634','2634.2.4','psychotherapist',replace('geriatric psychotherapist\nhypno-psychotherapist\npsychotherapist\nspecialist psychotherapist\nperson-centered psychotherapist\nperson-centred psychotherapist\nbody psychotherapist\npositive psychotherapist\nneuro-linguistic psychotherapist\ntransactional analytic psychotherapist\ngestalt psychotherapist\nexpert psychotherapist\nexpert in psychotherapy\npsycoterapy expert\nchild psychotherapist\nintegrative psychotherapist\npsychoanalytical psychotherapist\ncognitive behavioural therapist\nsystemic therapist\npractitioner of psychotherapy\nhumanistic psychotherapist\npsychotherapy practitioner\nmulti-modal psychotherapist\ngroup psychotherapist\npsychodrama psychotherapist\ntranspersonal psychotherapist\npsychodynamic psychotherapist\nyouth psychotherapist\nreality therapy psychotherapist\nexistential psychotherapist','\n',char(10)),'Psychotherapists assist and treat healthcare users with varying degrees of psychological, psychosocial, or psychosomatic behavioural disorders and pathogenic conditions by means of psychotherapeutic methods. They promote personal development and well-being and provide advice on improving relationships, capabilities, and problem-solving techniques. They use science-based psychotherapeutic methods such as behavioural therapy, existential analysis and logotherapy, psychoanalysis or systemic family therapy in order to guide the patients in their development and help them search for appropriate solutions to their problems. Psychotherapists are not required to have academic degrees in psychology or a medical qualification in psychiatry. It is an independent occupation from psychology, psychiatry, and counselling.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/afbe50b0-6634-4677-a3e7-0aada10cb9a7','key_17270','','','2149','2149.9.9','thermal engineer',replace('thermal engineering expert\nthermal technology engineering expert\nthermal plant operator\nthermal technology engineering adviser\nthermal engineers\nthermal engineering adviser\nthermal engineering consultant\nthermal engineering specialist\nthermal technology engineer\nthermal technology engineering specialist\nthermal engineer\nthermal power engineer\nthermal technology engineering consultant','\n',char(10)),'Thermal engineers design systems which provide heating or cooling, using thermodynamics, that transfer heat or energy through liquids and gases. They also construct these systems, and perform test to ensure proper functionality.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/afc5a0f4-42e5-4bfb-910c-2b54b44f99c5','key_17271','','','3315','3315.7','property appraiser',replace('property valuator\nreal estate appraisal technician\nappraiser of property\nproperty appraiser\nreal estate appraisal technicians\nproperties appraiser\nreal estate appraiser\nreal estate appraisers\nproperty assessor\nproperty evaluator\nappraiser of properties\nvaluation surveyor\nbuilding appraiser s\nproperty valuators\nvaluation surveyors\nproperty assessors\nbuilding appraiser\nresidential appraiser\nresidential appraisers','\n',char(10)),'Property appraisers undertake detailed analysis and investigation of properties in order to determine their value for sales, mortgage and insurance purposes. They compare the value of properties taking into account the age, actual state of property, its quality, repairs needed and overall sustainability. Property appraisers make an inventory of fixtures, compose a schedule of condition of property and prepare appraisal reports for both commercial and residential properties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/afcf9d03-6e51-4548-8cec-cfa50972929d','key_17272','','','1324','1324.3.2.29','import export manager in sugar, chocolate and sugar confectionery',replace('graduate import manager in sugar, chocolate and sugar confectionery\nassistant import export manager in sugar, chocolate and sugar confectionery\ngraduate import export manager in sugar, chocolate and sugar confectionery\ngraduate export manager in sugar, chocolate and sugar confectionery\nimport export manager in sugar, chocolate and sugar confectionery\nsenior export manager in sugar, chocolate and sugar confectionery\ninternational trade manager in sugar, chocolate and sugar confectionery\nassistant export manager in sugar, chocolate and sugar confectionery\nassistant import manager in sugar, chocolate and sugar confectionery\ncustoms compliance manager in sugar, chocolate and sugar confectionery\nsenior import export manager in sugar, chocolate and sugar confectionery\ntrainee import manager in sugar, chocolate and sugar confectionery\ninternational trade compliance manager in sugar, chocolate and sugar confectionery\nsenior import manager in sugar, chocolate and sugar confectionery\ntrainee export manager in sugar, chocolate and sugar confectionery\ntrainee import export manager in sugar, chocolate and sugar confectionery','\n',char(10)),'Import export managers in sugar, chocolate and sugar confectionery install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/afe9eaa4-433f-4ae6-90ff-afa098ccf1bb','key_17273','','','7223','7223.3','chain making machine operator',replace('hand chain maker\nnovelty chainmaker\nlay out worker\ncentrifugal casting machine operator\nnovelty chain maker\njewelry chainmaker\nlay-out technician\nlay out operative\nlay out technician\nfancy wire drawer\nhand chain making operator\ngold chain maker\nengine turning operator\nchain making machine tender\nchain making machine operative\ngold chainmaker\nchain making machine setter\njewelery chain maker\nchain making machine operator\nlay-out worker\nengine turner\nchainmaker\njewellery chain maker\nlay-out operator\njewellery chainmaker\nchain maker\ncentrifugal casting machine technician','\n',char(10)),'Chain making machine operators tend and operate the proper equipment and machinery for the creation of metal chains, including precious metal chains such as for jewellery, and produce these in all steps of the production process. They feed the wire into the chainmaking machine, use pliers to hook the ends of the chain formed by the machine together and finish and trim the edges by soldering them to a smooth surface.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/afee6a28-f654-4ac7-a665-f758616bc689','key_17274','','','3119','3119.5','food technician',replace('food tech expert\nfood technology expert\nfood technology specialist\nfood tech specialist\nfood technician','\n',char(10)),'Food technicians assist food technologists in the deveolpment of processes for manufacturing foodstuffs and related products based on chemical, physical, and biological principles. They perform research and experiments on ingredients, additives and packaging. Food technicians also check product quality to ensure compliance with legislation and regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/aff66ae0-d6e1-4de3-b6af-78647defd08c','key_17275','','','2635','2635.3.12','enterprise development worker',replace('enterprise social worker\nenterprise development officer\nbusiness social worker\nenterprise development adviser\ncommunity enterprise development worker\nindustry social worker\nenterprise development community worker\nenterprise development worker\ncorporate social worker\nenterprise development manager\nenterprise development facilitator\nsocial enterprise development worker\nsocial entrepreneur\ncooperative development worker','\n',char(10)),'Enterprise development workers support enterprises in solving big social problems by liaising with communities and customers. They strive to improve the productivity of employees and the health of their families by focusing on the work-life balance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b00e9e89-fd8d-4655-b6a2-f58b17629473','key_17276','','','5414','5414.1.1','airport security officer',replace('airport security officer\nairport security agent\n air marshall\nairport security guard\nTSA agent\nairport security professional','\n',char(10)),'Airport security officers interact with passengers to ensure smooth transition between areas of the airport, compliant with safety regulations. They perform searches of passengers, their ID and baggage in order to ensure safety and to prevent illegal activity.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b01affdd-c8a9-4121-bc50-a5be17f014eb','key_17277','','','3255','3255.3','shiatsu practitioner',replace('practitioner of shiatsu\nshiatsu therapist\nshiatsu practitioner\nshiatsu acupressurist\ntherapist of shiatsu massage','\n',char(10)),'Shiatsu practitioners provide health maintenance, health education, whole health evaluation and recommendations for wellbeing, and treatment of certain illness through energetic evaluation of the body''s life energy system (Ki) and regulation of the life energy system through various energetic and manual techniques.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b0473a10-e3de-480c-a841-171f2356a074','key_17278','','','3154','3154.2','aviation inspector',replace('aircraft inspection officer\naviation maintenance inspector\naircraft accident investigator\naircraft maintenance inspector\naircraft quality inspector\nflight inspector\naviation inspector\nlead inspection officer\ntransportation inspector\naviation inspection officer\ninspection officer','\n',char(10)),'Aviation inspectors perform inspections of the procedures followed in the matters of maintenance, air navigational aids, air traffic controls, and communications equipment. They check compliance with ICAO, EU, national and environmental regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b0480558-269d-4903-9bc7-f390cd6a6964','key_17279','','','7233','7233.1','construction equipment technician',replace('building construction equipment maintenance inspector\nbuilding equipment inspector\nbuilding equipment mechanic\nbuilding equipment servicer\nbuilding construction equipment operator\nbuilding construction equipment mechanic\nheavy equipment inspector\nbuilding equipment operator\nheavy equipment mechanic\nconstruction equipment technician\nbuilding construction equipment servicer','\n',char(10)),'Construction equipment technicians inspect, maintain and repair heavy-duty vehicles used in construction, forestry and earthworks such as bulldozers, excavators and harvesters. They perform evaluations of the equipment, and ensure the safety and optimal efficiency of the machinery.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b059d331-2dd9-4d7d-923e-1dd029d2e58c','key_17280','','','9520','9520.1','hawker',replace('street trader\nmarket stall trader\ncar-boot sale trader\npeddler\nmarket trader\nstreet seller\nindoor market trader\nstallholder\nlicensed hawker\ncostermonger\noutdoor market trader\nhawker\nstall holder\nstreet hawker\nstreet vendor','\n',char(10)),'Hawkers sell goods and services on established routes, street and market locations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b05f53db-b6b0-49bc-aa0c-1790c8c7012b','key_17281','','','7215','7215.2','rigger',replace('rigger/banksman\nrigger\ntent rigger\nrigger/slinger\nmarquee rigger\nlead rigger\ncrane rigger','\n',char(10)),'Riggers specialise in the lifting of heavy objects, often helped by a crane or derrick. They work with crane operators to attach and detach crane loads. They may also install the heavy object in place.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b065b637-5cb5-44d9-8c39-08004b51734e','key_17282','','','2149','2149.1','acoustical engineer',replace('acoustical technology engineering adviser\nacoustical engineering expert\nacoustics engineer\nacoustical technology engineer\nacoustical engineering specialist\nacoustic engineer\nacoustical engineering consultant\nacoustical engineer\nacoustical technology engineering consultant\nacoustical technology engineering expert\nacoustical technology engineering specialist\nacoustical engineering adviser','\n',char(10)),'Acoustical engineers study and apply the science of sound to various applications. They work in a wide range of areas including the consultation of the acoustics and elements affecting the transmission of sound in spaces for performances or recording activities. They can also consult on the levels of noise contamination for those activities that require compliance with standards on that matter.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b06a1dde-e5a1-4219-9491-2bedd0282e4b','key_17283','','','2641','2641.4','writer',replace('noir writer\ntechnical writer\nscience-fiction writer\nnovelist\ncreative writer\ntravel guide writer\nauthor\nmedical writer\ninvestigative writer\ntextbook writer\npoet\nessayist\nwriter\nfictional writer\nessay writer\nthriller writer','\n',char(10)),'Writers develop content for books. They write novels, poetry, short stories, comics and other forms of literature. These forms of writing can be fictional or non-fictional.','','Excludes <a resource="http://data.europa.eu/esco/occupation/ebe936e2-64e7-4777-94d6-42caa5a12409" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/ebe936e2-64e7-4777-94d6-42caa5a12409"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">script writer</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b07e291d-1ea0-44d6-861c-e2c36861f9e3','key_17284','','','7317','7317.2','basketmaker',replace('basketmaker\nreed weaver\nbasket weaving handicraft worker\nreed weaving handicraft worker\nartisan basket weaver\nreed basket weaver\nbasket weaver\nbasket reed weaver','\n',char(10)),'Basketmakers use stiff fibres to manually weave objects such as containers, baskets, mats and furniture. They use various traditional techniques and materials according to the region and the intended use of the object.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b09acdda-6680-49d1-a411-129610ee3bc0','key_17285','','','2412','2412.3','corporate risk manager',replace('risk manager\nrisk management specialist\nchief risk officer\noperational risk manager\nrisk and insurance consultant\ncorporate risk department manager\nchief risk executive\nrisk management director','\n',char(10)),'Corporate risk managers identify and assess potential threats and opportunities to a company, and give advice on how to deal with them. They create preventive plans to avoid and reduce risks, and put plans in place for when the company is threatened. They coordinate risk management aspects across the different functions of an organisation and are responsible for technical activities such as risk assessment, risk mapping and insurance purchase. They report on risk issues to the senior management and the company''s board.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b09ffdec-b04c-41a8-bacc-2c71d7a3e5ac','key_17286','','','7321','7321.1.6','typesetter',replace('typesetting technician\ntypesetting specialist\ngraphic designer & typesetter\ndigital typesetter\ntypesetting machine operative\ntypesetting machine worker\ntypesetting worker\ntypesetting operative\nproduction typesetter\ntypesetting machine technician\ndesigner & typesetter\ntypesetting machine specialist\ntypesetting machine operator\ntypesetter\ntypesetting operator\ngraphic designer and typesetter\ndesigner and typesetter','\n',char(10)),'Typesetters ensure that printed text is correctly set and visually pleasing. While typesetting was originally done manually and later employed techniques like linotype and phototypesetting, almost all typesetting is now done digitally using design programs or specialised typesetting programs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b0ad8659-0410-4b5b-9466-a90f8b3cdac9','key_17287','','','7543','7543.9','product quality controller',replace('product inspector\nproduct quality controller\nassembly inspector\nmanufacturing product quality controller\nquality control inspector\nproduct compliance inspector\nproduction inspector\nmanufacturing process quality controller\nproduct quality control inspector\nassembly line inspector\nproduct quality inspector\nproduction quality controller\nproduction controller','\n',char(10)),'Product quality controllers check the quality of manufactured products. They work in manufacturing facilities where they perform basic inspection and evaluation of products before, during or after the production process. They track production problems and send inferior or malfunctioning items back for repair.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b0c6f284-f3ed-4820-a62e-4e87869f5a3e','key_17288','','','8141','8141.1','rubber products machine operator',replace('rubber products machine operative\nrubber processing machine operator\nrubber products machine operator\nrubber production operative\nrubber process machine operator\ncalender machine operator\ncure operator\nrubber-products machine operator\nrubber processing machine operative\nrubber products machine minder\nrubber products machine worker\nbanbury operator\nrubber process machine operative\nrubber production operator','\n',char(10)),'Rubber products machine operators operate machines which knead, blend, calender, mould, extrude and cure rubber products from natural and synthetic rubber. They measure the ingredients for mixing and load them into the machine. Rubber products machine operators monitor and control the tempature, pressure and speed during production.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b0cb4f78-c361-48cf-97aa-25104ac2647c','key_17289','','','7521','7521.2','wood treater',replace('wood treating machine specialist\nwood treatment specialist\nwood treating machine worker\nwood treating machine operator\nwood treatment operator\nwood treating machine technician\nwood treater\nwood treatment worker\nwood treatment technician\nwood treatment operative','\n',char(10)),'Wood treaters apply treatments to wood to make it resistant to environmental factors like mould, cold, moisture, or staining. Treatments may also contribute to the colour of the wood. Wood treaters may use chemicals, heat, gasses, UV light, or a combination of these to treat wood.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b0f3f595-4965-4f13-ba5c-f6049b62469f','key_17290','','','2120','2120.6.1','biometrician',replace('biometric studies analyst\nbiometrics researcher\nbiometric studies research scientist\nbiometrician\nbiometric studies researcher\nbiometric studies scientist\nbiometricians\nbiometrics research scientist\nbiometrics scientist\nbiometrics analyst\nbiometric studies scholar\nbiometrics science researcher\nbiometric studies research analyst\nbiometrics research analyst\nbiometrics scholar','\n',char(10)),'Biometricians perform research in the field of biometrics. They carry statistical or biological research projects by measuring fingerprints, retinas, and human shapes for medical or industrial applications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b0fa69b4-998c-4e66-acd3-5f5d73cadb5c','key_17291','','','8183','8183.7','packaging and filling machine operator',replace('packaging and filling operator\npackaging and filling operative\npackaging and filling worker\npackaging and filling machine worker\npackaging and filling machine operator','\n',char(10)),'Packaging and filling machine operators tend machines for preparing and packing food products in various packaging containers such as jars, cartons, cans, and others.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b10d45f4-482d-4301-bc39-da206b17815c','key_17292','','','7543','7543.7.1','aircraft assembly inspector',replace('aviation assembly diagnostic inspector\naircraft assembly diagnostic inspector\naircraft assembly test inspector\naircraft assembly inspector\naviation assembly repair inspector\naviation assembly maintenance inspector\naviation assembly test inspector\naircraft assembly maintenance inspector\naircraft assembly repair inspector\naviation assembly installation inspector\naircraft assembly installation inspector\naviation assembly inspector\naviation assembly safety inspector\naircraft assembly safety inspector','\n',char(10)),'Aircraft assembly inspectors use measuring and testing equipment to inspect and monitor aircraft assemblies to ensure conformity to engineering specifications and to safety standards and regulations. They examine the assemblies to detect malfunction or damage and check repair work. They also provide detailed inspection documentation and recommend action where problems are discovered.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b10dc548-10e1-4447-8406-5101ff170bbf','key_17293','','','3434','3434.1.2.1','head pastry chef',replace('executive pastry chef\nmaster dessert chef\nmaster patissier\nmaster pastry chef\nhead pastry chef\nexecutive dessert chef','\n',char(10)),'Head pastry chefs manage pastry staff and ensure the preparation, cooking and presentation of desserts, sweet products and pastry products. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b11e1742-5e28-4270-b081-b0193d85ee7d','key_17294','','','2521','2521.3','database developer',replace('database programmer\ndatabase development engineer\ndatabase design specialist\ndatabase coder\ndatabase developer\ndata base developer\ndatabase developers\ndata base developers','\n',char(10)),'Database developers program, implement and coordinate changes to computer databases based on their expertise of database management systems.','','Excludes people performing managerial activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b12b14fd-5b74-40c6-afd9-dd35b34e27ed','key_17295','','','3122','3122.3','industrial assembly supervisor',replace('quality control supervisor\nassembly team leader\nassembly foreman\ngoods compliance supervisor\nassembly overseer\nassembly chargehand\nassembly co-ordinator\nassembly forewoman\nassembly controller\nproduction assembly supervisor\nquality supervisor\nassembly line supervisor\nindustrial assembly supervisor\nassembly planner\ngoods production supervisor','\n',char(10)),'Industrial assembly supervisors are in charge of organizing, planning and coordinating assembly operations. They keep track of all the work activities and manage the process for efficient functioning in order to tackle problems such as production loss. They answer to the industrial production and the manufacturing manager.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b148e7a3-e5b0-4edc-b770-85ab81f36ac9','key_17296','','','1324','1324.3.2.18','import export manager in household goods',replace('graduate export manager  in household goods\nassistant import manager  in household goods\ngraduate import manager  in household goods\ngraduate import export manager  in household goods\nimport export manager in household goods\ntrainee import manager  in household goods\nassistant import export manager  in household goods\nsenior import manager  in household goods\ntrainee export manager  in household goods\nassistant export manager  in household goods\ninternational trade compliance manager  in household goods\nsenior export manager  in household goods\ncustoms compliance manager  in household goods\ntrainee import export manager  in household goods\ninternational trade manager  in household goods\nsenior import export manager  in household goods','\n',char(10)),'Import export managers in household goods install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b156960c-8508-4067-908d-f167847c55b1','key_17297','','','1322','1322.2','quarry manager',replace('quarrying engineer\nquarry operations manager\nquarry manager\nquarry engineer\nquarrying industry manager\nquarry operations engineer\nquarry managers\nquarry management expert','\n',char(10)),'Quarry managers plan, oversee and coordinate quarry operations. They coordinate extraction, processing and transportation and ensure these processes run smoothly and according to health and safety standards. Quarry managers ensure the successful running of the quarry and implement company strategies and guidelines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b176e5d7-1f94-495c-907f-80a7f61aed59','key_17298','','','3111','3111.13','remote sensing technician',replace('remote sensing technology studies scholar\nremote sensing technology research scientist\nremote sensing technology studies research scientist\nremote sensing technology studies analyst\nremote sensing technology scientist\nremote sensing technologist\nremote sensing technology scholar\nremote sensing technology science researcher\nremote sensing technology studies researcher\nremote sensing technology research analyst\nremote sensing technician\nremote sensing technology researcher\nremote sensing technology analyst\nremote sensing technology studies scientist\nremote sensing technology studies research analyst','\n',char(10)),'Remote sensing technicians collect airborne data. They utilise equipment aimed for the collection of data and determination of geographical points in order to help in a variety of operations such as land conservation, urban planning, and military operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b1ab3572-fa2f-4624-8eac-3c3d986d237b','key_17299','','','2431','2431.1','advertising copywriter',replace('creative copy\ntext writer\ncontent writer\ncopywriter\ncopy strategist\ncopy-based creative\nslogan writer\nadvertising strategist\nadvertisement copywriter\ncopy writer\nadvertising writer\ncreative copy artist\ncreative copy consultant\nadvertising copywriter\nweb copywriter\ncopywriting consultant\ncommercial writer\ncontent marketer\ncontinuity writer','\n',char(10)),'Advertising copywriters are responsible for the written or verbal design of advertisements and commercials. They write slogans, catchphrases and work closely together with advertising artists.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b1c0714d-77f1-4732-8fd8-9f0739fb9974','key_17300','','','2166','2166.13','special effects artist',replace('special effect image designer\nspecial effects artist\nspecial effects animator\ndigital effects specialist\nvisual effects artist\nspecial effects technician\nsenior special effects artist\ndigital effects artist\nspecial effects specialist','\n',char(10)),'Special effects artists create illusions for movies, videos and computer games. They use computer software.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b1c80369-067e-4041-84c8-dbe65640a5d8','key_17301','','','2511','2511.10','ICT consultant',replace('ICT ERP consultant\nIT consultant\nICT consultant\nICT advisor\nIT advisor','\n',char(10)),'ICT consultants provide advice on how to optimise the use of existing tools and systems, make recommendations for the development and implementation of a business project or technological solution and contribute to project definitions. They raise awareness on information technology innovations and their potential value to a business. They also participate in the assessment and choice of ICT solutions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b206a740-c7eb-407d-9cdb-93502368c4d8','key_17302','','','3123','3123.1.7','crane crew supervisor',replace('crane operations supervisor\ncrane safety supervisor\nsupervisor of crane crew\ncrane operator supervisor\ncrane crew supervisor\ncrane supervisor\nlifting operations supervisor','\n',char(10)),'Crane crew supervisors are present to supervise crane operations. They keep an eye on safety and make sure regulations are followed. The supervisor takes quick decisions to resolve problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b21aac17-8237-4264-bc2c-0e315346fd8f','key_17303','','','3133','3133.1.3','chemical plant control room operator',replace('closed-circuit television control room operator\ncontrol room attendant\nmanufacturing control room operator\nproduction monitor\npanel board operator\nproduction control room operator\nchemical plant control room operator\nCCTV control room operator','\n',char(10)),'Chemical plant control room operators monitor and inspect remotely the production systems during their shift, reporting all anomalies and incidents using the required systems. They operate the control room panels and ensure the safety of production workers and production equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b21b5952-1658-44f8-b719-e5be7fb39866','key_17304','','','1420','1420.4.31','motor vehicle shop manager',replace('sales manager motor trade\nmotor vehicle shop manager\nautomobile store manager\nautomotive store manager\nlight and heavy motor vehicles shop manager\nvehicle store manager\nnew car sales manager\nused car sales manager\ncar store manager\ncar showroom senior manager\nprincipal sales manager\ncar shop manager\nsales manager motor dealership\nvehicle shop manager\ncar supermarket manager\ndealership showroom manager\nsenior sales manager\ncar showroom principal\ncar show room manager\nlight and heavy motor vehicles store manager\nautomobile shop manager\nshop manager in vehicle showroom\nvehicle sales showroom manager\nvehicle sales principal\ndealership manager\nfranchise sales manager\ndealership sales manager\nstore manager in vehicle showroom','\n',char(10)),'Motor vehicle shop managers assume responsibility for activities and staff in a vehicle showroom. ​They manage employees, monitor the sales of the store, manage budgets and order supplies when a product is out of supply and perform administrative duties if required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b223f402-0318-4e27-8691-df6e7f7295d2','key_17305','','','0110','0110.10','lieutenant',replace('police lieutenant\nlieutenant\narmed forces lieutenant','\n',char(10)),'Lieutenants command troops of platoons of soldiers and lead them in training and discipline exercises. They also perform administrative duties, and function as advisers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b2678105-9d5c-403f-b822-274aff706309','key_17306','','','6121','6121.5','sheep breeder',replace('sheep breeder\nshearer\ngoat breeder\nsheep breeders\nsheep specialist\nsheep rearer','\n',char(10)),'Sheep breeders oversee the production and day-to-day care of sheep. They maintain the health and welfare of sheep.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b27854dd-3fbe-406e-a750-54f677d55936','key_17307','','','7232','7232.4','aircraft maintenance coordinator',replace('helicopter maintenance coordinator\nmilitary aircraft maintenance manager\naviation maintenance coordinator\nplane maintenance manager\naeroplane maintenance coordinator\ncoordinator of aircraft maintenance\nplane maintenance coordinator\naircraft maintenance planning officer\njet aircraft maintenance coordinator\nairplane maintenance coordinator\nlight aircraft maintenance manager\nmilitary aircraft maintenance coordinator\nrotary aircraft maintenance manager\naircraft maintenance scheduler\nrotary aircraft maintenance coordinator\nlight aircraft maintenance coordinator\nhelicopter maintenance manager\naviation maintenance planning officer\ncivilian aircraft maintenance coordinator\npiston aircraft maintenance manager\naircraft maintenance coordinator\naviation maintenance scheduler\nairplane maintenance manager\ncivilian aircraft maintenance manager\npiston aircraft maintenance coordinator\njet aircraft maintenance manager\naeroplane maintenance manager','\n',char(10)),'Aircraft maintenance coordinators plan, schedule, and manage the preparation and maintenance works in the hangars and workshops. They communicate with higher level managers in order to prepare the necessary resources for smooth and efficient operations in airports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b2826761-6fc4-4344-9d36-d8455cb6b185','key_17308','','','1324','1324.3.2.12','import export manager in fish, crustaceans and molluscs',replace('senior export manager in fish, crustaceans and molluscs\ngraduate import manager in fish, crustaceans and molluscs\ncustoms compliance manager in fish, crustaceans and molluscs\ngraduate export manager in fish, crustaceans and molluscs\ninternational trade manager in fish, crustaceans and molluscs\nassistant export manager in fish, crustaceans and molluscs\ninternational trade compliance manager in fish, crustaceans and molluscs\nsenior import export manager in fish, crustaceans and molluscs\ngraduate import export manager in fish, crustaceans and molluscs\nassistant import export manager in fish, crustaceans and molluscs\ntrainee import export manager in fish, crustaceans and molluscs\nimport export manager in fish, crustaceans and mollusks\ntrainee export manager in fish, crustaceans and molluscs\nsenior import manager in fish, crustaceans and molluscs\nassistant import manager in fish, crustaceans and molluscs\ntrainee import manager in fish, crustaceans and molluscs','\n',char(10)),'Import export managers in fish, crustaceans and molluscs install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b2cac4bc-abb7-41b3-a515-2ad006b8ebf6','key_17309','','','7543','7543.4','control panel tester',replace('electrical test technician\ncontrol panel inspection technician\nelectrical inspection and test  technician\ncontrol panel tester\nelectric control panel repairer\ncontrol tester\npanels tester\ninspector of control panels\ntechnician expert in electrical control panels\nelectric control panel tester and repairer\ntester of control panels\ncontrols tester\npanel tester\ncontrol panel testing technician\ncontrol panel testing technologist\npanel wireman\nelectro-mechanical test technician\ncontrol panel test technician\nelectric control panel fault-finder\ntester of electrical control panels\nelectrical control panel test technician\npanel builder\ncontrol panel testing technology expert\ncontrol panel inspector\nelectric control panel tester\nelectrical control panel tester\nelectric control panel inspector and tester\ncontrol panel testing technology specialist','\n',char(10)),'Control panel testers test the electrical control panels. They read blueprints to check if the wiring is connected correctly. Control panel testers use electrical measuring and testing equipment to detect malfunctions and may correct faulty wiring and components.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b2cede50-82bb-4684-9f11-1930e12ad672','key_17310','','','2149','2149.9','energy engineer',replace('energy technology engineering consultant\nenergy engineering specialist\nenergy technology engineering specialist\nenergy engineering expert\nenergy engineering researcher\nenergy efficiency engineer\npower engineer\nenergy engineering consultant\nenergy technology engineering expert\nenergy engineer\nenergies engineer\nenergy engineering adviser\nenergy technology engineering adviser\nenergy technology engineer','\n',char(10)),'Energy engineers design new, efficient and clean ways to produce, transform, and distribute energy to improve environmental sustainability and energy efficiency. They deal with the extraction of energy through natural resources, such as oil or gas, or renewable and sustainable sources, such as wind or solar power. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b2dab4fb-b7f7-4f57-94b2-9c0f0848564a','key_17311','','','7124','7124.1','insulation worker',replace('insulation installation worker\ninsulator\ncavity insulation installer\ninsulation worker\nlagger\nloft insulation installer\nenergy saving materials installer','\n',char(10)),'Insulation workers install a variety of insulation materials to shield a structure or materials from heat, cold, and noise from the environment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b2e81332-0972-4e0f-9a4d-1d15c9c9513b','key_17312','','','4312','4312.2','back office specialist',replace('back office clerk\nback office specialist\nfinancial office specialist\nback office administrator\nback office officer','\n',char(10)),'Back office specialists perform operations of administrative and organisational nature in a financial company, in support of the front office. They process administration, take care of financial transactions, manage data and company documents and perform supportive tasks and other diverse back office operations in coordination with other parts of the company. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b2eacfdf-5de5-4e8c-a8cb-3d32e201c210','key_17313','','','3131','3131.3.6','nuclear reactor operator',replace('nuclear reactor operator\nnuclear reactor technician\nnuclear plant monitoring technician\nnuclear reactor operative\nnuclear reactor safety supervisor\nnuclear reactor worker\nnuclear plant safety technician\nnuclear reactor operation technician\nnuclear reactor controller\nnuclear reactor control panel monitor\nnuclear reactor control panel worker','\n',char(10)),'Nuclear reactor operators directly control nuclear reactors in power plants from control panels, and are solely responsible for the alterations in reactor reactivity. They start up operations and react to changes in status such as casualties and critical events. They monitor parameters and ensure compliance with safety regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b2f5ed56-c83f-40d0-b861-f5253cd3d1d9','key_17314','','','2651','2651.7','glass artist',replace('stained glass restorer\nstained glass glazier\nstained glass creator\nstained glass designer & maker\nglass artist\nstained glass artist\nstained glass window maker\nstained glass designer and maker\nstained glass artist and conservator\nstained glass artist & conservator\nstained glass conservator\nstained glass artist & restorer\nstained glass artist and restorer','\n',char(10)),'Glass artists create original artworks by assembling pieces of glass. They can be involved in restoration processes (such as those going on in cathedrals, churches, etc.) and can create accessoires, windows or decorations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b3086ad2-1f3c-4a60-9bdb-8b4746989d4b','key_17315','','','3422','3422.4.7','tennis coach',replace('mini tennis coach\ntennis coach\ntennis trainer\nperformance tennis coach\ncardio tennis coach\ntennis teacher\ntennis instructor\nhead tennis coach','\n',char(10)),' Tennis coaches advise and guide individuals and groups on playing tennis. They conduct lessons and teach the rules and techniques of the sport such as grips, strokes and serves. They motivate their clients and help improve their performance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b31e404e-9af6-457d-a58a-208f612eeba3','key_17316','','','3115','3115.1','mechanical engineering technician',replace('engineering technical apprentice\nmechanical engineering inspector\nmechanical engineering technical operator\nmechanical engineering system inspector\nengineering technical manager\nmechanical engineering technician\nengineering test supervisor','\n',char(10)),'Mechanical engineering technicians provide technical support to mechanical engineers in producing and manufacturing mechanical machinery. They help make designs and adjustments, and perform tests. They also develop layouts and drawings, collate and interpret data and write reports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b322bb4c-bbef-45c2-bd7e-00a97c3ab268','key_17317','','','8122','8122.10','rustproofer',replace('rustproof machine operator\nrustproofer\npaint prepper\nrust proof machine operative\nrust proof machine operator\nrustproofing worker\nrustproof machine operative\nrust removing operative\npaint prepping operative\nsandblast operator\nrust stripping operative\nrust-proofing worker\nrust stripper','\n',char(10)),'Rustproofers use the proper equipment and machinery to provide metal workpieces with a hard, durable finishing coat, composed of specific chemical formulas, that prevents or delays iron and steel pieces from rusting and protects against corrosion.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b343dd0c-8a83-4e13-a79c-0ac52f526c64','key_17318','','','3115','3115.1.3','aircraft engine tester',replace('helicopter engine tester\nlight aircraft engine tester\naeroplane  engine inspection engineer\nairplane engine tester\nrotary aircraft engine tester\nmilitary aircraft engine tester\nplane engine tester\naircraft engine tester\ncivilian aircraft engine tester\nengine tester\nengine inspection engineer\naeroplane engine inspection technician\npiston aircraft engine tester\nengine inspection technician\njet aircraft engine tester\naeroplane engine tester','\n',char(10)),'Aircraft engine testers test the performance of all engines used for aircraft in specialised facilities such as laboratories.They position or give directions to workers positioning engines on the test stand. They use hand tools and machinery to position and connect the engine to the test stand. They use computerised equipment to enter, read and record test data such as temperature, speed, fuel consumption, oil and exhaust pressure.','','Excludes <a resource="http://data.europa.eu/esco/occupation/ef9c2914-2f3d-464c-ae0a-d02b403dc7e9" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/ef9c2914-2f3d-464c-ae0a-d02b403dc7e9"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">aircraft engine inspector</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b37d15b3-402e-4792-8292-f18c0de7aca6','key_17319','','','7422','7422.3','mobile devices technician',replace('mobile hardware technician\nmobile technician\nmobile hardware maintainer\nmobile devices technicians\ncell phone devices technician\nmobile equipment engineer\nmobile equipment technician','\n',char(10)),'Mobile devices technicians carry out proper fault diagnosis to improve the quality of mobile devices and repair them. They provide information related to a number of services, including warranties and after-sale services.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b3855886-bfee-4fd9-827e-2a0b6b4b4441','key_17320','','','2144','2144.1.13','mine ventilation engineer',replace('mine ventilation engineer\nventilation officer\nventilation system coordinator','\n',char(10)),'Mine ventilation engineers design and manage systems and equipment to ensure fresh air supply and air circulation in underground mines and the timely removal of noxious gases. They co-ordinate ventilation system design with mine management, mine safety engineer and mine planning engineer.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b3867d0a-e902-4d88-b0c2-3e0d2cf35c83','key_17321','','','3257','3257.4.1','hazardous materials inspector',replace('hazardous waste control specialist\nwaste disposal adviser\nhazmat compliance specialist\ncompliance manager\nhazardous waste disposal specialist\nhazardous waste management specialist\nhazardous materials inspector\nenvironmental regulations compliance specialist\nindustrial waste analyst\nsolid waste compliance specialist\nenvironmental compliance specialist\nhazardous waste compliance specialist\nhazardous waste inspector','\n',char(10)),'Hazardous materials inspectors inspect facilities which handle hazardous materials to ensure compliance to health and safety regulations, and hazardous materials handling legislation. They investigate violations, oversee tests of emergency and risk response plans, and consult on the improvement of the facilities'' operations and procedures, as well as on hazardous materials regulations. They also advise plants on potential sources of danger to a community, and on better safety regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b39a74a2-37fe-4fdc-ba8e-5bbbbdce2d89','key_17322','','','1324','1324.7','railway station manager',replace('head of station\nstation master\nmanager of railway station\nrailway station manager\nstation director','\n',char(10)),'Railway station managers are in charge of organising tasks linked to the operation of train stations. Their tasks cover the organisation of the maintenance and repairment works for buildings and interior equipment. They care for the comfort, service, safety and security of passengers, other clients and the commercial use of buildings. Depending on the size of the station, the number of staff that railway station managers are directly responsible for can be high.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b3b32930-05f0-4b2e-93bf-34d1e3f89a3b','key_17323','','','3153','3153.2.3','flight instructor',replace('pilot instructor\noccupational flight instructor\nflight instructor\nflying trainer\nflying instructor\nflight simulation instructor\npilot trainer\naviation instructor\npractitioner of flight instruction\nflight instruction practitioner\ninstructor in flight','\n',char(10)),'Flight instructors train both new and experienced pilots seeking to gain licences or experience in flying new aircraft, how to properly operate an aircraft according to regulations. They teach their students both the theory and practice of how to optimally fly and maintain an airplane, and they observe and evaluate student technique. They also focus on the regulations relating to operational and safety procedures specific to different (commercial) airline aircraft.','','Includes people performing air force training and recreational flight instruction.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b3b79192-f6e0-4aa0-92d1-f876508e05b3','key_17324','','','7531','7531.1','costume maker',replace('costumer\ncostume & prop maker\ncostume builder\nprop costume maker\nprop & costume maker\nset costumer\ncostume maker\ncostume prop maker\nprop and costume maker\ncostume and prop maker','\n',char(10)),replace('Costume makers construct, sew, stitch, dye, adapt and maintain costumes to be used in events, live performances and in movies or television programs. Their work is based on artistic vision, sketches or finished patterns combined with knowledge of the human body to ensure the wearer maximum range of movement. They work in close cooperation with the designers. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b3cd0522-14a9-4e21-a7ee-c9a1f9c0de51','key_17325','','','7318','7318.2','carpet weaver',replace('carpet maker\nrug maker\ncarpet-weaving machine operator\nrug weaver\ncarpet weavers\ncarpet-tufting machine operator\ncarpet weaver','\n',char(10)),'Carpet weavers operate machinery to create textile floor coverings. They create carpets and rugs from wool or synthetic textiles using specialised equipment. Carpet weavers can use diverse methods such as weaving, knotting or tufting to create carpets of different styles.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b3ce981c-5b32-4de2-8e53-502a6357b2bc','key_17326','','','3312','3312.4','credit risk analyst',replace('credit risk controller\nfinancial risk technician\ncredit risk surveyor\nfinancial risk specialist\ncredit risk technician\nfinancial risk surveyor\ngraduate credit risk specialist\nassistant credit risk specialist\nfinancial risk manager\ncredit risk consultant\nsenior credit risk specialist\ncredit risk analyst\ntrainee credit risk specialist\ncredit risk manager\nfinancial risk analyst','\n',char(10)),'Credit risk analysts manage individual credit risk and care for fraud prevention, business deal analysis, legal documents analysis and recommendations on the level of the risk.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b3e459a2-0103-4182-8882-c255d474a7ff','key_17327','','','2631','2631.2.4','tax policy analyst',replace('pecunary consultant\nfiscal and policy analyst\nfiscal analyst\ntax research analyst\ntax policy advisor\nfiscal policy analyst\ntax analyst\ncontributions advisor\ntax policy analyst\ntax policy adviser','\n',char(10)),'Tax policy analysts research and develop taxation policies and legislation in order to improve and develop tax policies. They advise official bodies on policy implementation and financial operations, as well as forecast financial influence of changes in tax policies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b3ebf55f-5e50-4262-946a-f08d7fbe29b2','key_17328','','','2310','2310.1.19','healthcare specialist lecturer',replace('dietetics docent\nhealthcare specialist teacher\northopaedics lecturer\nembryology teacher\nuniversity healthcare science lecturer\ngeriatrics professor\nembryology professor\nhealthcare specialist docent\nlecturer in specialised healthcare\nembryology docent\npaediatrics instructor\nhealthcare specialist lector\northopaedics instructor\nembryology lecturer\nnursing specialist docent\northopaedics professor\npathology professor\northopaedics docent\nhealthcare specialist instructor\nlecturer of specialised healthcare\npaediatrics professor\npathology instructor\nhealthcare lector\nnursing specialist professor\nnursing specialist instructor\noncology lecturer\npathology lecturer\npaediatrics teacher\ngeriatrics teacher\nlecturer in specialist healthcare science\npaediatrics docent\northopaedics teacher\ndietetics lecturer\nnursing specialist teacher\nembryology instructor\nspecialist lecturer in healthcare science\ngeriatrics lecturer\ndietetics teacher\nhealthcare specialist lecturer\nnursing specialist lecturer\ndietetics professor\nspecialist nursing lecturer\nuniversity lecturer in healthcare science\npedagogy lecturer\npathology teacher\ndietetics instructor\ngeriatrics instructor\ngeriatrics docent\npathology docent\nlecturer in specialized healthcare\nhealthcare specialist professor\nlecturer of specialized healthcare\npaediatrics lecturer','\n',char(10)),'Healthcare specialist lecturers are subject professors, teachers, or lecturers, and often doctors who instruct students who have obtained an upper secondary education diploma in their own specialised field of healthcare, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for grading papers and exams, for leading laboratory practices, and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of specialist healthcare, publish their findings and liaise with other university colleagues.','','Includes people performing specialised fields of healthcare education. Includes professors of <a resource="http://data.europa.eu/esco/skill/dbd3e193-6a61-43d1-ab00-b34bae73e82a" typeOf="http://data.europa.eu/esco/model#Skill" href="http://data.europa.eu/esco/skill/dbd3e193-6a61-43d1-ab00-b34bae73e82a"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">embryology</span></a>, <a resource="http://data.europa.eu/esco/skill/2ed78f04-8282-40ab-89a6-9392fde00826" typeOf="http://data.europa.eu/esco/model#Skill" href="http://data.europa.eu/esco/skill/2ed78f04-8282-40ab-89a6-9392fde00826"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">orthopaedics</span></a>, <a resource="http://data.europa.eu/esco/skill/52c19dc8-651f-4095-98fe-dcb2799d702e" typeOf="http://data.europa.eu/esco/model#Skill" href="http://data.europa.eu/esco/skill/52c19dc8-651f-4095-98fe-dcb2799d702e"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">geriatrics</span></a>, <a resource="http://data.europa.eu/esco/skill/db8a9ba7-96ad-4c53-b552-d4f1570dfefb" typeOf="http://data.europa.eu/esco/model#Skill" href="http://data.europa.eu/esco/skill/db8a9ba7-96ad-4c53-b552-d4f1570dfefb"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">pathology</span></a>, <a resource="http://data.europa.eu/esco/skill/a117f32c-662b-4dd9-b739-1230dd04cb60" typeOf="http://data.europa.eu/esco/model#Skill" href="http://data.europa.eu/esco/skill/a117f32c-662b-4dd9-b739-1230dd04cb60"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">pedagogy</span></a>, <a resource="http://data.europa.eu/esco/skill/8671290a-ab17-42c2-9114-adcb741e7257" typeOf="http://data.europa.eu/esco/model#Skill" href="http://data.europa.eu/esco/skill/8671290a-ab17-42c2-9114-adcb741e7257"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">paediatrics</span></a> and <a resource="http://data.europa.eu/esco/skill/f190355a-cea5-4cd0-b6cd-a5c3d9a14985" typeOf="http://data.europa.eu/esco/model#Skill" href="http://data.europa.eu/esco/skill/f190355a-cea5-4cd0-b6cd-a5c3d9a14985"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">dietetics</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b42c5ed4-c6e4-4694-934f-96127719cc43','key_17329','','','1219','1219.1.1','facilities manager',replace('facility maintenance technician\nbuilding cleaning and maintenance manager\nfacilities superintendent\nbuilding maintenance manager\nfacilities manager\nfacilities administrator\nfacilities coordinator\nfacilities maintenance manager','\n',char(10)),'Facilities managers perform strategic planning as well as routine operational planning related to buildings'' administration and maintenance. They control and manage health and safety procedures, supervise the work of contractors, plan and handle buildings maintenance operations and fire safety and security issues, oversee buildings'' cleaning activities and utilities infrastructure and are in charge of space management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b42d8731-386b-4d34-a824-4b736feee1e5','key_17330','','','3334','3334.2.1','title closer',replace('closer\nclosing agent\nescrow closer\ncloser of titles\nclosing coordinator\nreal estate closer\nescrow officer\ntitle processor\ntitle closer','\n',char(10)),'Title closers handle and investigate all documentation needed for a property sale including the contracts, settlement statements, mortgages, title insurance policies, etc. They ensure compliance with legal requirements and review all the fees related to the real estate sales process.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b4306d87-7992-47fc-914a-4485a4e7003c','key_17331','','','7515','7515.3','food grader',replace('food grader\nfood product grader','\n',char(10)),'Food graders inspect, sort and grade food products. They grade food products according to sensory criteria or with the help of machinery. They determine the product''s use by grading them into the appropriate classes and discarding damaged or expired foods. Food graders measure and weigh the products and report their findings so the food can be further processed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b471350e-23ce-4789-a821-5500b28cffe1','key_17332','','','3411','3411.6','court enforcement officer',replace('court bailiff\nher majesty''s court bailiff\ntribunals officer\nwarrant server\ncourt administrator\ndebt collector\njury bailiff\ncourt adminstrative officer\ncourt usher\nbailiff\nenforcement agent\ncourt enforcement officer','\n',char(10)),'Court enforcement officers enforce orders of court judgements such as managing the recovery of money owed, seizing of goods, and selling goods in public auctions to obtain the money owed. They also send summons and arrest warrants to ensure attendance in court or other judicial procedures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b476f45f-2c79-44d5-a4d7-fa357aa83074','key_17333','','','5113','5113.1','tourist guide',replace('museum guide\ntour guide\npalace guide\ncity guide\nguide\ncastle guide','\n',char(10)),'Tourist guides assist individuals or groups during travel or sightseeing tours or at places of touristic interest, such as museums, art facilities, monuments and public places. They help people to interpret the cultural and natural heritage of an object, place or area and provide information and guidance in the language of their choice.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b493b10d-7131-4814-93bb-a2c1be4a095f','key_17334','','','7421','7421.5','office equipment repair technician',replace('office service technician\nIT repair technician\noffice facility repair inspector\noffice facilities manager\ncopier repair technician\noffice equipment repairer\noffice equipment service technician\noffice equipment repair technician\nIT repair inspector\noffice machines technician\nelectronic service technician\noffice equipment service inspector\noffice facilities inspector\noffice equipment inspector\noffice facilities tecnhician\noffice service inspector','\n',char(10)),'Office equipment repair technicians provide services to businesses related to installing, maintaining and repairing new or existing equipment such as printers, scanners and modems, on the clients'' premises. They keep records of performed services and return equipment to a repair centre if needed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b49f95f8-c1c8-428f-9f1f-5f5abae55e51','key_17335','','','1324','1324.3.2.19','import export manager in live animals',replace('assistant import manager in live animals\ngraduate import manager in live animals\ninternational trade compliance manager in live animals\ncustoms compliance manager in live animals\nsenior export manager in live animals\nimport export manager in live animals\nassistant export manager in live animals\nsenior import manager in live animals\nsenior import export manager in live animals\ntrainee import manager in live animals\ntrainee export manager in live animals\ngraduate export manager in live animals\nassistant import export manager in live animals\ngraduate import export manager in live animals\ninternational trade manager in live animals\ntrainee import export manager in live animals','\n',char(10)),'Import export managers in live animals install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b4bff870-5c8f-4c33-b07f-f04d79830633','key_17336','','','5246','5246.1',' food service worker',replace('food services worker\nairline catering assistant\nairline food preparer\nfood production assistant\nairline catering worker\nkitchen assistant\nairline food kitchen worker\nairline food service worker\ncatering worker','\n',char(10)),'Food service workers prepare food and serve customers. They prepare simple dishes to be used in places that serve food, such as restaurants, cafeterias, hotels, hospitals and ensure that kitchen operations run smoothly and efficiently.','','Includes food service workers preparing food for flight and aviation companies.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b4c6d1b0-929e-48be-9f67-47bd8c30658b','key_17337','','','7121','7121.1','roofer',replace('tinsmith\nhouse roofer\nroof slater\nroofer\nroofing carpenter\ncladding installer\nroof tiler\nasphalt roofer\nfelt roofer','\n',char(10)),'Roofers cover structures with roofs. They install the weight-bearing elements of a roof, either flat or pitched, then cover it with a weatherproof layer.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b4e26d7f-475e-4ada-b287-d669de03fb21','key_17338','','','2145','2145.2','rubber technologist',replace('rubber technician\nrubber technology scientist\nrubber technology expert\nrubber technology analyst','\n',char(10)),'Rubber technologists develop compound formulations to respond to specific needs and attain the required in-rubber properties, starting from technical specifications and application demands. They have knowledge of physical and chemical characteristics of raw rubber material and the process to convert it into market products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b4f08f2e-3c97-4484-9094-446a17dad802','key_17339','','','8132','8132.1','motion picture film developer',replace('motion picture film expert\nmotion picture film technologist\nfilm laboratory expert\nmotion picture developer\nmotion picture film technician\nmotion picture films developer\nfilm lab specialist\nmotion pictures film developer\nfilm developer\nfilm laboratory specialist\nfilm lab technologist\nmotion picture film specialist\nfilm laboratory technician\nfilm lab technician\nfilm laboratory developer\nfilm lab expert\nmotion picture film developer\nfilm lab developer\nfilm laboratory technologist','\n',char(10)),'Motion picture film developers develop film material into visible videos and material. They develop the footage into different formats and presentations, such as black and white and colour. They work small cine-films per request of clients.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b4f4b21e-7664-45ca-9697-9123cfd8c9ef','key_17340','','','8342','8342.5','pile driving hammer operator',replace('piledriver\npile hammer operator\npile driving hammer operative\npile drivin'' hammer operator\npiling equipment operative\npile driving hammer operator\npile-driver operator\npile-driver operative\npiling operative\npile driving hamer operator\npiling equipment operator','\n',char(10)),'Pile driving hammer operators work with a piece of heavy equipment that positions piles and hammers them into the ground using a rigging mechanism.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b4ff30f8-073b-4dd8-9128-ecf9d0ff87ba','key_17341','','','6121','6121.1','cattle breeder',replace('cattle rearer\ncattle breeder\ncattle specialist','\n',char(10)),'Cattle breeders oversee the production and day-to-day care of cattle. They maintain the health and welfare of cattle.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b50ae5f6-3f67-4cc7-92bb-6cec7214bfc4','key_17342','','','3115','3115.1.13','motor vehicle engine tester',replace('diesel engine tester\nvehicle engine inspection technician\ncar engine tester\ndiesel engine inspection technician\nelectric engine tester\nengine tester\npetrol engine tester\nlorry engine tester\ncoach engine tester\nengine inspection engineer\ncar engine inspection technician\nhybrid engine inspection technician\nmotor vehicle engine tester\nhybrid engine tester\ngas engine inspection technician\npetrol engine inspection technician\nbus  engine inspection technician\nbus engine tester\nelectric engine inspection technician\ntruck engine inspection technician\ntruck engine tester\ngas  engine tester\nengine inspection technician','\n',char(10)),'Motor vehicle engine testers test the performance of diesel, petrol, gas and electric engines in specialised facilities such as laboratories. They position or give directions to workers positioning engines on the test stand. They use hand tools and machinery to position and connect the engine to the test stand. They use computerised equipment to enter, read and record test data such as temperature, speed, fuel consumption, oil and exhaust pressure.','','Excludes   <a resource="http://data.europa.eu/esco/occupation/8b490c1a-2f8c-4693-bcfa-ecfc88838a8e" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/8b490c1a-2f8c-4693-bcfa-ecfc88838a8e"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">motor vehicle engine inspector</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b53209fa-ba54-4a7a-b71c-bcc8014053ec','key_17343','','','3331','3331.2.1.20','import export specialist in machinery, industrial equipment, ships and aircraft',replace('trainee import export specialist in machinery, industrial equipment, ships and aircraft\ntrainee export specialist in machinery, industrial equipment, ships and aircraft\ncustoms compliance specialist in machinery, industrial equipment, ships and aircraft\ngraduate export specialist in machinery, industrial equipment, ships and aircraft\nimport export specialist in machinery, industrial equipment, ships and aircraft\ninternational trade compliance specialist in machinery, industrial equipment, ships and aircraft\ntrainee import specialist in machinery, industrial equipment, ships and aircraft\nsenior export specialist in machinery, industrial equipment, ships and aircraft\nassistant import specialist in machinery, industrial equipment, ships and aircraft\nassistant export specialist in machinery, industrial equipment, ships and aircraft\nassistant import export specialist in machinery, industrial equipment, ships and aircraft\nsenior import export specialist in machinery, industrial equipment, ships and aircraft\ngraduate import specialist in machinery, industrial equipment, ships and aircraft\nsenior import specialist in machinery, industrial equipment, ships and aircraft\ngraduate import export specialist in machinery, industrial equipment, ships and aircraft','\n',char(10)),'Import export specialists in machinery, industrial equiment, ships and aircraft have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b55718eb-e8ff-4485-8ad0-bbac32cbd77b','key_17344','','','2431','2431.10.1','advertising media planner',replace('media coordinator\nmedia manager\nmedia planning strategist\nmedia planner\njunior media planner\nmedia planner/buyer\nmedia planning manager\ndigital media planner\nsenior media planner\nadvertising media planner\nmedia planning expert\nmedia planning executive','\n',char(10)),'Advertising media planners advise on the best communication media platforms to convey ideas. They analyse advertising plans in order to assess the aim and objective of the marketing strategy. They assess the potential and response rate that different communication channels might have on the transmission of a message related to a product, company, or brand.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b558a934-9308-48fc-b03b-bea8e23ab085','key_17345','','','7312','7312.4','membranophone musical instruments maker',replace('membranophone musical instruments maker\nkazoo maker\nmembranophone musical instruments builder\nmembranophone musical instrument craftsman\nmembranophone musical instrument production worker\nmembranophone musical instrument constructor\ntubular drum maker\ndrum maker\nkettle drum maker\nmembranophone musical instrument craftswoman\nfriction drum maker\nmirliton maker\nswazzle maker\nmembranophone musical instrument assembler\nframe drum maker','\n',char(10)),'Membranophone musical instruments makers create and assemble parts to make membranophone instruments to specified instructions or diagrams. They stretch and attach the membrane to the frame of the instrument, test the quality and inspect the finished instrument.','','Includes people producing smaller musical instruments or accesories such as metronomes, tuning forks or musical boxes.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b560456a-bc4d-41a9-94c8-bf8636147246','key_17346','','','3253','3253.1.1','first aid instructor',replace('educator in first aid\ninstructor in first aid principles\ntrainer in first aid\nfirst aid educator\nfirst aid instructor\ninstructor in first aid\ninstructor in first aid techniques\nfirst aid trainer','\n',char(10)),'First aid instructors teach students immediate lifesaving emergency measures, such as cardiopulmonary resuscitation, the recovery position, and injury care. They provided practice materials such as a specialised manikin.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b567a979-c556-49fa-8b36-8af6cfe97624','key_17347','','','9333','9333.6','stevedore',replace('ship freight handler\nlongshoreman\nstevedore\ndock worker\ndock labourer','\n',char(10)),'Stevedores sort, handle, load and unload freight into over-the-road equipment in accordance with oral and written instructions, as well as state regulations. They move freight such as boxes, bulky items, or even large pallets of goods to and from storage areas and onto transport vehicles.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b5b8c259-7557-4f5f-9f56-155b038946c1','key_17348','','','8342','8342.10','surface mine plant operator',replace('quarry earth moving equipment operative\naggregate driver\nsurface miner equipment operator\nsurface mining heavy equipment driver\nsurface miner (heavy equipment)\nsurface mine plant operator\nquarry plant operator\ntipper driver','\n',char(10)),'Surface mine plant operators control heavy-duty equipment such as excavators and dump trucks, often involving a high level of spatial awareness, to excavate, load and transport ore, raw mineral including sand, stone and clay and overburden at quarries and surface mines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b5d1912e-89c7-4a66-ab96-0dc5f4f6c426','key_17349','','','2131','2131.6','immunologist',replace('immunology research analyst\nimmunology studies analyst\nimmunology studies research scientist\nimmunology studies scientist\nimmunology scientist\nimmunologist\nimmunology researcher\nimmunology studies scholar\nimmunologists\nimmunology analyst\nimmunology research scientist\nimmunology studies researcher\nimmunology studies research analyst\nimmunology scholar\nimmunology science researcher','\n',char(10)),'Immunologists research the immune system of living organisms (e.g. human body) and the way it reacts to external infections or invasive harmful agents (e.g. virus, bacteria, parasites). They focus their study on those diseases that affect the immunology of living organisms in order to classify them for treatment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b5d2db88-9e2b-4efa-8f2f-0e62a494791e','key_17350','','','2422','2422.12.5','environmental policy officer',replace('environmental officer\nenvironmental policy advisor\nprincipal environmental policy officer\nsenior environmental policy officer\nenvironmental policy officer\npolicy officer\nenvironmental policy analyst\nenvironmental policy researcher\nenvironmental projects and policy officer\nenvironmental policy consultant','\n',char(10)),'Environmental policy officers research, analyse, develop and implement policies related to the environment. They give expert advice to entities such as commercial organisations, government agencies and land developers. Environmental policy officers work on reducing the impact of industrial, commercial and agricultural activities on the environment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b5d76ddd-f59a-491a-9543-f6c0e2d8a950','key_17351','','','4221','4221.5','tour operator representative',replace('tour operator representative\nholiday rep\ntour representative\ntour rep\nholiday representative','\n',char(10)),'Tour operator representatives act on behalf of the tour operator to give practical information, provide assistance, handle services and sell excursions to tourists while at their destinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b5e21e2b-ab0c-4c8d-8277-d98be4ad79c4','key_17352','','','3114','3114.1.1','avionics inspector',replace('aviation safety inspector\naviation diagnostic inspector\navionics systems inspector\naviation test inspector\navionics installation inspector\navionics test inspector\naviation maintenance inspector\navionics inspector\navionics diagnostic inspector\naviation installation inspector\nflight safety inspector\navionics safety inspector\navionics repair inspector\navionics maintenance inspector\naviation systems inspector\naviation repair inspector','\n',char(10)),'Avionics inspectors inspect instruments, electrical, mechanical and electronic systems of aircrafts to ensure their compliance with performance and safety standards. They also examine maintenance, repair and overhaul work and review any modification to check its conformity to standards and procedures. They provide detailed inspection, certification and repair records.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b5eaf231-77ad-4a86-8e54-15cc4398aad2','key_17353','','','2141','2141.10','process engineer',replace('processing engineer\nprocess engineer\nmanufacturing systems engineer\nprocess technology engineering adviser\nprocesses engineer\nprocess engineering adviser\nprocess technology engineer\nprocess engineering specialist\nprocess technology engineering specialist\nprocess technology engineering expert\nprocess engineering consultant\nprocess engineering expert\nprocess technology engineering consultant\nindustrial quality engineer','\n',char(10)),'Process engineers apply engineering concepts in order to improve all kinds of production and manufacturing processes in terms of efficiency and productivity. They evaluate the variables and constraints present in given processes and present engineering solutions to optimise them.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b5f6bb2c-0602-484d-a87f-9cac33f83010','key_17354','','','8321','8321.1','motorcycle delivery person',replace('timed delivery motorcycle driver\nsameday delivery motorcycle driver\nexpress delivery motorcycle driver\nmotorcyle courier\nmotorcycle delivery person\ntelephone directory motorcycle deliverer\nhome delivery motorcycle driver\nmotorcycle driver of deliveries','\n',char(10)),'Motorcycle delivery persons perform the transportation of all kinds of packets containing objects, loose pieces, prepared meals, medications and documents that require special treatment in terms of urgency, value or fragility. They transport and deliver their packets by motorcycle.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b633af32-c8c0-4481-9971-7932ea4b16b5','key_17355','','','1219','1219.5.5','wood factory manager',replace('natural wood factory controller\nwood factory supervisor\nwood factory manager\nsolid wood factory supervisor\ntimber factory manager\nwood factory administrator\nsolid wood factory controller\nsolid wood factory manager\nwood factory executive\nfurniture factory manager\nnatural wood factory manager\nwood factory controller\nwood factory overseer\nnatural wood factory supervisor\nwood manager\nwood factory coordinator','\n',char(10)),'Wood factory managers realise planning, commercial and advisory tasks of wood factory and timber trade. They also manage purchasing, sales, customer service and marketing of wood and wood products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b65133db-b3de-44d1-88fa-1d3deda08245','key_17356','','','2310','2310.1.22','law lecturer',replace('university law lecturer\nlaw lecturer\nuniversity law teacher\nlaw docent\nlaw teacher\nlaw lector\nlecturer in law\nprofessor of law\nspecialist law lecturer\nlecturer of law\nlaw professor\nuniversity lecturer in law\nhigher education law teacher\nsenior lecturer in law\nlaw instructor\nuniversity law instructor','\n',char(10)),'Law lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, law, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of law, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b65530b8-0b50-49da-8e9e-331b161374a5','key_17357','','','2642','2642.1.2','broadcast news editor',replace('TV news editor\nnews director\nnight news editor\nradio editor\ntv news director\nbroadcast news editor\nradio reporter\ntelevision news editor\ninternational news director\ntelevision news director\nradio journalist\nnews editor\nradio news editor\ndigital news editor','\n',char(10)),'Broadcast news editors decide which news stories will be covered during the news. They assign journalists to each item. Broadcast news editors also determine the length of coverage for each news item and where it will be featured during the broadcast.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b66eb697-e65d-4bbb-b51c-d847f6875f1a','key_17358','','','1420','1420.4.15','craft shop manager',replace('weaving shop manager\npottery shop manager\ncraft superstore manager\ncraft supermarket manager\npaint shop manager\ncraft shop manager\ncraft counter manager\ncraft department manager\nhobby shop manager\ncraft concession manager\nsewing shop manager\nartists materials shop manager','\n',char(10)),'Craft shop managers are responsible for activities and staff in specialised shops that sell equipment and supplies for craft activities such as sewing, painting or pottery making. ​They manage employees, monitor the sales of the store, manage budgets and order supplies when a product is out of supply and perform administrative duties if required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b674a89a-4439-4ad5-822d-518036415503','key_17359','','','5223','5223.7.36','tobacco specialised seller',replace('cigarette specialised seller\ncigar salesperson\ntobacconists salesperson\ncigarette salesperson\ntobacco salesperson\ntobacconist salesperson\ntobacconists specialised seller\ntobacco specialized seller\ntobacconist specialised seller\npurveyor of tobacco and tobacco products\ncigar and cigarette specialised seller\ncigar specialised seller\ncigar and cigarette salesperson','\n',char(10)),'Tobacco specialised sellers sell tobacco products in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b680251e-b3c7-4029-9849-66a95081801c','key_17360','','','1211','1211.1.2','bank treasurer',replace('CFO\ntreasury clerk\nchief financial officer\nfinancial institution treasurer\nsenior treasury analyst\nbank treasurer\ndeputy bank treasurer','\n',char(10)),'Bank treasurers oversee all aspects of the financial management of a bank. They manage the liquidity and solvency of the bank. They manage and present current budgets, revise financial forecasts, prepare accounts for audit, manage the bank''s accounts and maintain accurate record-keeping of financial documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b69d9b94-a40c-434d-af8f-e584257314f6','key_17361','','','8160','8160.44','milk heat treatment process operator',replace('milk treatment operator\nmilk processing operator\nmilk treatment operative\nmilk heat treatment process operator\nmilk processing operative','\n',char(10)),'Milk heat treatment process operators use equipments to perform different methods of pasteurisation and/or sterilisation to dairy liquid products. They operate equipment such as raw product booster pumps, homogenisers, flavour control equipment, clarifiers, separators, auxiliary pumps, and filters.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b6a62e85-5ffe-41aa-8f2a-9959365dfe85','key_17362','','','8181','8181.2','brick and tile caster',replace('brick and tile caster\nbrick and tile making machine operative\nbrick and tile-making machine operator\ncutter operator, tile\ntile caster\ncaster (brick and tile)\ncutter operator, brick\nbrick and tile making machine operator\nauger-press operator\ncastables operative\npug mill operator\ncastables worker\nbrick caster\nbrick and tile-making machine operative','\n',char(10)),'Brick and tile casters operate and maintain mixing machines used in brick and tile products development.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b6beb46d-9ed2-4a66-9117-7e3f21f234dd','key_17363','','','2352','2352.1.2','early years special educational needs teacher',replace('preschool special educational needs teacher\nearly years teacher in special educational needs\nearly years SEN teacher\nearly childhood special educational needs teacher\nnursery special educational needs teacher\nearly years special needs teacher\nteacher in early years special educational needs\nteacher in early years SEN','\n',char(10)),'Early years special educational needs teachers provide specially-designed instruction to students with a variety of disabilities on a kindergarten level and ensure they reach their learning potential. Some early years special educational needs teachers work with children who have mild to moderate disabilities, implementing a modified curriculum to fit each student''s specific needs. Other early years special educational needs teachers assist and instruct students with intellectual disabilities and autism, focusing on teaching them basic literacy and life skills. All teachers assess the students'' progress, taking into account their strengths and weaknesses, and communicate their findings to parents, counselors, administrators and other parties involved.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b6c851d1-bf09-40aa-aa50-47dde84e7d50','key_17364','','','2355','2355.2','circus arts teacher',replace('circus arts trainer\ncircus teacher\ntrainer in circus arts\nteacher of circus arts\ninstructor of circus arts\ncircus arts instructor\ncircus arts teaching practitioner\ncircus arts educator\ncircus arts teacher','\n',char(10)),'Circus arts teachers instruct students in a recreational context in the various circus techniques and acts such as trapeze acts, juggling, mime, acrobatics, hooping, tightrope walking, object manipulation, unicycling tricks, etc. They provide students with a notion of circus history and repertoire, but mainly focus on a practice-based approach in their courses, in which they assist students in experimenting with and mastering different circus techniques, styles and acts and encourage them to develop their own style. They cast, direct and produce circus performances, and coordinate the technical production and possible set, props and costume usage on stage.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b6f4573d-659f-49ca-a885-850a48d01913','key_17365','','','2433','2433.6.4','technical sales representative in machinery and industrial equipment',replace('technical salespeople in industrial equipment\ntechnical salesman in machinery\ntechnical saleswoman in machinery\ntrainee technical sales engineer in machinery and industrial equipment\ntechnical salesman in industrial equipment\ntechnical salesperson in machinery\nplant machinery sales representative\nmachinery and industrial equipment salesperson\ntrainee technical sales representative in machinery and industrial equipment\ntechnical salespeople in machinery\ntechnical salespeople in machinery and industrial equipment\ngraduate technical sales engineer in machinery and industrial equipment\nmachinery sales representative\nsenior technical sales representative in machinery and industrial equipment\ntechnical salesperson in industrial equipment\ntechnical salesperson in machinery and industrial equipment\ntechnical saleswoman in machinery and industrial equipment\ngraduate technical sales representative in machinery and industrial equipment\ntechnical saleswoman in industrial equipment\ntechnical sales representative in machinery and industrial equipment\ntechnical salesman in machinery and industrial equipment\nassistant technical sales engineer in machinery and industrial equipment\nsenior technical sales engineer in machinery and industrial equipment\nassistant technical sales representative in machinery and industrial equipment\nmechanical equipment sales representative','\n',char(10)),'Technical sales representatives in machinery and industrial equipment act for a business to sell its merchandise while providing technical insight for customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b73c752b-9858-4f53-82de-f3a340cbcae9','key_17366','','','2131','2131.4.7','epidemiologist',replace('epidaemiologist\nepidemiologist\nepidemiology science researcher\nepidemiology analyst\nepidemiology research scientist\nepidemiology scientist\nepidemiology researcher\nepidemiology scholar\nepidemiology research analyst','\n',char(10)),'Epidemiologists centre their research on origin and causes for illness eruption in humans. They determine the way in which diseases are spread out and propose risk preventive measures to health policy organisms.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b73f4ce5-13fa-43a0-bbf6-01d7315849b8','key_17367','','','2635','2635.3.23','social work supervisor',replace('social work supervisor\nsocial work team leader\nsocial work team manager\nsenior social worker\nsocial work programme supervisor\nsocial services team manager\nsocial work team supervisor\nsocial work manager\nsocial work programme manager\nsocial services coordinator','\n',char(10)),'Social work supervisors manage social work cases by investigating alleged neglect or abuse cases. They make family dynamics assessment and provide assistance to sick people or with emotional or mental disorders. They train, assist, advise, evaluate and assign work to subordinate social workers making sure that all work is done according to the established policies, laws, procedures and priorities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b7474960-a03e-46c3-88ce-81575cb8f657','key_17368','','','1324','1324.3.1.6.24','perfume and cosmetics distribution manager',replace('graduate perfume and cosmetics distribution manager\nsenior perfume and cosmetics distribution manager\nperfume and cosmetics distribution manager\nperfume and cosmetics supply chain planner\nperfume and cosmetics supply chain manager\nperfume and cosmetics distribution planner\nperfume and cosmetics logistics manager\nperfume and cosmetics logistics planner\nassistant perfume and cosmetics distribution manager\ntrainee perfume and cosmetics distribution manager','\n',char(10)),'Perfume and cosmetics distribution managers plan the distribution of perfume and cosmetics to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b7507136-e71e-47e8-b873-e77d5cdf2922','key_17369','','','2635','2635.3.27','youth worker',replace('youth project team leader\nwelfare advisor\nyouth employment advisor\nyouth centre worker\nyouth advisor\nyouth worker\nsocial worker youth justice\nyouth mentor\nsenior youth worker\nyouth project team worker\nyouth advocate\nyouth support worker\nyouth and community worker\nyouth social worker\nyouth programmes social worker\njuvenile care worker\nyouth guidance worker','\n',char(10)),'Youth workers support, accompany and counsel young people, focusing on their personal and social development. They are involved in managing community projects and services through one-to-one or group-based activities. Youth workers can be volunteers or paid professionals who facilitate non-formal and informal learning processes. They are engaged in a large spectrum of activities by, with and for young people.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b76178c0-a03a-4793-b079-1e61e636c521','key_17370','','','2431','2431.5','business developer',replace('business continuity analyst\nbusiness development executive\nbusiness developer\nbusiness development strategist','\n',char(10)),'Business developers strive to improve the market share of companies in the market. They perform strategic analyses of the core advantages that a company''s products or services have to offer, they cooperate in the development of marketing campaigns for lead generation and support on sales efforts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b7744a45-a4ce-4037-b679-3d2377e17f31','key_17371','','','1420','1420.5','supermarket manager',replace('discount supermarket manager\noutlet centre manager\nhypermarket manager\nfood supermarket manager\ngeneral merchandise manager\ngraduate supermarket manager\nsenior supermarket manager\nconcession store manager\nsupermarket general store manager\ngrocery shop manager\nassistant supermarket manager\ntrainee supermarket manager\ngraduate trainee supermarket manager\nsupermarket manager','\n',char(10)),'Supermarket managers are responsible for activities and staff in a supermarket.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b789dae6-6c77-4f46-9253-098c424c92f2','key_17372','','','3331','3331.2.1.12','import export specialist in  fish, crustaceans and molluscs',replace('assistant import export specialist in  fish, crustaceans and molluscs\ngraduate import export specialist in  fish, crustaceans and molluscs\nassistant import specialist in  fish, crustaceans and molluscs\ncustoms compliance specialist in  fish, crustaceans and molluscs\ntrainee import export specialist in  fish, crustaceans and molluscs\nsenior import export specialist in  fish, crustaceans and molluscs\ntrainee export specialist in  fish, crustaceans and molluscs\nsenior import specialist in  fish, crustaceans and molluscs\ninternational trade compliance specialist in  fish, crustaceans and molluscs\ngraduate export specialist in  fish, crustaceans and molluscs\ngraduate import specialist in  fish, crustaceans and molluscs\nsenior export specialist in  fish, crustaceans and molluscs\ntrainee import specialist in  fish, crustaceans and molluscs\nimport export specialist in  fish, crustaceans and mollusks\nassistant export specialist in  fish, crustaceans and molluscs','\n',char(10)),'Import export specialists in fish, crustaceans and molluscs have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b79cfd33-6e46-4b71-b180-c9a84422f047','key_17373','','','2114','2114.1.10','seismologist',replace('seismology researcher\nseismology science researcher\nseismology scholar\nseismologists\nseismology scientist\nseismologist\nseismology research analyst\nseismology research scientist\nseismology analyst','\n',char(10)),'Seismologists study the movement of tectonic plaques in the Earth which causes the propagation of seismic waves and earthquakes. They study and observe the various sources causing earthquakes such as volcanic activity, atmospheric phenomena, or the behaviour of oceans. They provide their scientific observations to prevent dangers in construction and infrastructure.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b7aa4b4e-dbb6-4f58-b36c-35b9a7267972','key_17374','','','7422','7422.2','computer hardware repair technician',replace('computer service agent\ncomputer repairman\ncomputer hardware repairman\ncomputer hardware repairer\ncomputer hardware repair technician\ncomputer technical support agent\ncomputer expert\ncomputer service and repair technician\ncomputer technician\ncomputer repairer','\n',char(10)),'Computer hardware repair technicians install, examine, test and repair computer hardware and peripheral components. They test computers'' functionality, identify the problems and replace damaged components and parts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b7b17a85-ccd0-4d2f-92f2-7e99839731b0','key_17375','','','2131','2131.4.3','biomedical scientist advanced',replace('advanced medical scientist\nbiomedical scientist advanced\nadvanced analyst in biomedical science\nexpert scientist in biomedicine\nadvanced researcher in biomedical science\nadvanced clinical laboratory scientist\nadvanced medical laboratory scientist\nadvanced practitioner in biomedical science\nadvanced biomedical researcher\nadvanced biomedical doctor','\n',char(10)),'Biomedical scientists advanced undertake advanced translational research in the biomedical science field and perform as educators of their professions or as other professionals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b7b75eb6-34bc-462d-b412-eed393cc1397','key_17376','','','4222','4222.1','customer contact centre information clerk',replace('customer relations representative\ncustomer contact center information clerk\ncustomer representative\ncustomer helpdesk clerk\ncustomer service clerk\nclient services clerk\ncustomer support officer\ncustomer contact clerk\ncustomer information center assistant','\n',char(10)),'Customer contact centre information clerks provide information to customers via the telephone and other media such as email. They answer inquiries about a company''s or oganisation''s services, products and policies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b7f47c25-bdf3-42fa-a68e-ad32754b2dba','key_17377','','','2413','2413.1.2','investment analyst',replace('investment operations analyst\ninvestment specialist\nportfolio analyst\ninvestment banking business analyst\ninvestment analyst\ninvestment accounting analyst\ninvestment officer\noil and gas investment banking analyst\ninvestment banking analyst\ninvestment compliance analyst','\n',char(10)),'Investment analysts undertake research to make informed recommendations to fund managers. They research investments globally but depending on the nature and field of their employer they can specialise in fields like retail, infrastructure, energy, banking and financial services. They focus on financial and economical information such as the political and economic developments that can impact financial markets, the financial performance of the target companies and use the interpretation of data from different sources to understand how it affects investment decision making.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b7fc1cd1-0d6d-4e9e-8a9f-c3270201be81','key_17378','','','4312','4312.3','foreign exchange cashier',replace('foreign exchange teller\nforeign exchange office clerk\nbank foreign exchange cashier\nforeign exchange clerk\nbank foreign exchange office clerk\nforex cashier\nmoney exchanger\nforeign exchange cashier','\n',char(10)),'Foreign exchange cashiers process cash transactions from clients in national and foreign currencies. They provide information on the conditions and exchange rates for buying and selling foreign currencies, make deposits of money, record all foreign exchange transactions and check for money validity.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b8139469-92cc-4f57-9e69-a0d9f7fc3817','key_17379','','','2320','2320.1.12','firefighter instructor',replace('trainer in fire academy\ntrainer of firefighters\nfirefighter teaching practitioner\nfirefighter trainer\nfire academy trainer\ninstructor of firefighters\nfire academy instructor\nfirefighter instructor\neducator of firefighters\nfire instructor\ninstructor in fire academy','\n',char(10)),'Firefighter instructors train probationary, new academy recruits, or cadets, on the theory and practice necessary to become a firefighter. They conduct theoretical lectures on academic subjects such as law, basic chemistry, safety regulations, risk management, fire prevention, reading blueprints etc. Fire academy instructors also provide more hands-on, practical instruction regarding the usage of assistive equipment and rescue tools such as a fire hose, fire axe, smoke mask etc., but also heavy physical training, breathing techniques, first aid, self defense tactics and vehicle operations. They also prepare and develop lesson plans and new training programmes as new public service-related regulations and issues arise. The instructors monitor the students'' progress, evaluate them individually and prepare performance evaluation reports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b864c9a3-ef6e-46a0-a40c-66188f74dfa7','key_17380','','','0110','0110.8','colonel',replace('wing commander\ncolonel\nlieutenant colonel\nbattalion commander','\n',char(10)),'Colonels serve in the staff of a military commander, and function as primary advisers in operational and strategic decision-making to senior officers.','',replace('Includes Battalion commander. \n Includes Wing commander.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b865693a-eb11-44cc-bf6c-38e5b4d7e169','key_17381','','','6112','6112.1','arboriculturist',replace('tree climber\nclimber\narborist\narboriculturists\narboretum worker\ntree doctor\narboriculture specialist\narboriculturist','\n',char(10)),'Arboriculturists carry out specialised tasks related to observation, health and maintenance of trees.','',replace('Excludes <a resource="http://data.europa.eu/esco/occupation/773cf1de-50a4-42e7-bab1-e68dcbae3f54" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/773cf1de-50a4-42e7-bab1-e68dcbae3f54"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">forest worker</span></a>. \n Excludes <a resource="http://data.europa.eu/esco/occupation/699057b7-7aeb-4b5c-bb90-01d935ff32c1" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/699057b7-7aeb-4b5c-bb90-01d935ff32c1"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">forestry equipment operator</span></a>.  \nExcludes people performing managerial activities in forests or harvesting timbers. \nExcludes people performing tree growth activities. \nIncludes people performing activities focusing on the health and safety of individual plants and trees.\nIncludes people working in rural and urban settings.  \nIncludes people performing felling, preservation, plantation and protection of trees. \nIncludes people working with heavy equipment. \nIncludes people managing the relationship between trees, their surroundings and people.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b866b3bb-3dbd-4b3d-9a53-9ec15f43ca2f','key_17382','','','2642','2642.1.4','columnist',replace('food columnist\nadvice columnist\ncommentator\ncolumnist\neditorial opinion columnist\nhumor columnist\nsenior columnist\nmagazine journalist\ngossip columnist\npolitical columnist','\n',char(10)),'Columnists research and write opinion pieces about new events for newspapers, journals, magazines and other media. They have an area of interest and can be recognised by their writing style.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b87a7a27-ee74-4e5e-b274-84a3cab27bf4','key_17383','','','1324','1324.3.2','import export manager',replace('senior export manager\ngraduate international trade compliance manager\nassistant international trade compliance manager\nsenior international trade manager\ntrainee import export manager\nsenior import manager\nimport export manager\ngraduate customs compliance manager\ngraduate import export manager\nsenior customs compliance manager\ntrainee international trade manager\nassistant import manager\nassistant export manager\ngraduate import manager\nassistant customs compliance manager\ntrainee international trade compliance manager\ntrainee import manager\nassistant international trade manager\ngraduate export manager\ntrainee customs compliance manager\ntrainee export manager\nsenior international trade compliance manager\ngraduate international trade manager\nassistant import export manager\nsenior import export manager','\n',char(10)),'Import export managers install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b89ba3a8-f03a-46b8-ac54-c87d7204aad0','key_17384','','','2432','2432.9.1','spokesperson',replace('spokesman\npublic relations officer\nmedia relations specialist\nspokeswoman\nmedia coordinator\npublic relations coordinator\npress secretary\ncommunications officer\ncompany spokesperson\nspokesperson','\n',char(10)),'Spokespeople speak on behalf of companies or organisations. They use communication strategies to represent clients through public announcements and conferences. They promote their clients in a positive light and work to increase an understanding of their activities and interests.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b89f5984-3fb7-4339-80d5-00d1b0750b53','key_17385','','','4212','4212.4.1','casino pit boss',replace('gaming supervisor\ncasino dealer & pit boss\ngaming floor boss\npit boss\npit manager\nelectronic gaming supervisor\ncasino pit boss\ncasino dealer and pit boss\ngaming floor manager\ncasino pit boss and dealer\ngaming floor supervisor\ncasino pit boss & dealer','\n',char(10)),'Casino pit bosses support the management team and are able to manage, inspect and deal with all gaming activities. They supervise the gaming floor operation, and are accountable for influencing the spend and income per head to achieve the required margin whilst ensuring that the highest standards of efficiency, security and signature service standards are achieved in accordance with all company procedures and current legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b8b98b65-7db2-43f8-8ac4-2f1903984e7b','key_17386','','','5419','5419.2','civil enforcement officer',replace('traffic warden\ncivil enforcement officer\ncivil enforcement supervisor\nparking manager\ncivil enforcement agent\nparking attendant','\n',char(10)),'Civil enforcement officers patrol the streets to enforce parking restrictions, ensure a free flow of traffic is maintained, ensure the safety of pedestrians, and ensure traffic and parking laws are complied with. They aid with the combating of crime and crime reduction by providing assistance during incidents and providing patrol services.','','Includes <a resource="http://data.europa.eu/esco/occupation/12da0bb1-26c8-46eb-9476-797f4f1c441e" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/12da0bb1-26c8-46eb-9476-797f4f1c441e"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">crossing guards</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b8db7ef4-4057-4e88-8e2a-105b4b59fb67','key_17387','','','7231','7231.1','automotive brake technician',replace('truck brake technician\ncar brake technician\nlorry brake technician\nautomotive braking systems repair technician\nbrake technician\nsuspension technician\nautomotive brake technician\nautomotive braking systems diagnostic technician\nautomotive braking systems technician','\n',char(10)),'Automotive brake technicians inspect, maintain, diagnose and repair braking, steering and suspension systems as well as wheels and tyres.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b91034a0-1d3e-40e4-aced-07052ee0f9e9','key_17388','','','8160','8160.1','animal feed operator',replace('feed mill operator\nanimal feed worker\nfeed mill worker\nanimal feeder\nfeeder\nfeed worker\nanimal feed operator','\n',char(10)),'Animal feed operators tend various animal feed processing machines in industrial plants such as mixing machines, filling machines, and loading machines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b915ab4a-f7b1-44bc-a5a4-585e91d31dfd','key_17389','','','3334','3334.2','property acquisitions manager',replace('porperty acquisitions manager\nproperty acquisitions area manager\nproperty transactions supervisor\nproperty acquisitions manager\nland acquisition coordinator\nland acquisition specialist\nproperty acquisition manager\ndirector of property acquisition services\nland acquisition manager\nreal estate acquisitions specialist\nreal estate acquisitions manager','\n',char(10)),replace('Property acquisitions managers ensure land or property acquisitions transactions. They liaise with relevant stakeholders concerning financial aspects and risks arising from the acquisition of property. Property acquisitions managers ensure compliance with legal requirements for purchasing property and take care of all documentation and closure techniques needed.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b9296f1a-a68b-4ac7-9904-f2cf1374fbaf','key_17390','','','3115','3115.1.10','material stress analyst',replace('aviation stress analyst\nmaterials analyst\nstress analyst\nmaterial stress-strain analyst\nmaterials stress analyst material analyst\nstructure engineer\nmaterials stress analyst\nmaterials stress technician\nstress tester\naircraft body stress analyst\nmaterial stress analyst\naeroplane stress analyst\nairframe stress analyst\nstructural engineer\nstress technician\nstress-strain analyst','\n',char(10)),'Material stress analysts plan and use software to perform structural analyses including static, stability and fatigue analyses on a variety of machines. They develop analysis of primary and secondary structures. They prepare technical reports to document their analysis results, participate in design reviews and recommend process improvements. They also assist in the development of structural test plans.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b94e2f4c-6604-46a9-bc72-160fc9d2cf36','key_17391','','','3324','3324.4.23','wholesale merchant in metals and metal ores',replace('business to business sales consultant in metals and metal ores\nwholesale merchandiser in metals and metal ores\ntrainee wholesale merchant in metals and metal ores\ngraduate wholesale merchant in metals and metal ores\nwholesaler in metals and metal ores\nwholesale trader in metals and metal ores\nwholesale buyer in metals and metal ores\nassistant wholesale merchant in metals and metal ores\nwholesale merchant in metals and metal ores\nsenior wholesale merchant in metals and metal ores\nbusiness to business seller in metals and metal ores\nB2B sales consultant in metals and metal ores','\n',char(10)),'Wholesale merchants in metals and metal ores investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b95bdbed-1e7e-482a-aa73-7bdbb3e36358','key_17392','','','2412','2412.7','investment fund manager',replace('investments manager\nportfolio administrator\nportfolio managers\nfund manager\nportfolio administrators\nfund directors\ninvestment fund coordinator\nfund director\nfund managers\ninvestment fund adviser\nfixed income portfolio manager\ninvestment managers\ninvestment fund portfolio manager\ninvestment fund manager\ninvestment fund advisor\ninvestments fund manager','\n',char(10)),'Investment fund managers implement and monitor the investment strategy of a fund. They manage the fund''s portfolio trading activities and supervise the financial, securities, and investment analysts in charge to perform research on the investments and then make buying and selling recommendations. They make decisions on when to buy or sell the assets included in a portfolio. Investment fund managers works in a variety of settings such as banks, investment vehicles and stockbroking companies, working closely with the investment analyst. This occupation manages strategy and does not always work with relations between shareholders or investors.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b9711647-463f-4991-a858-d5de66ff2f83','key_17393','','','1323','1323.1.2','property developer',replace('speculative property developer\nhouse developer\nhousing developer\ndomestic property developer\nproperty developers\ncommercial developer\noffice property developer\nspeculative developer\ndeveloper of real estate\nspeculative builder\noffice developer\nproperty developer\nland developer\ncommercial property developer\nreal estate developer','\n',char(10)),'Property developers buy land, finance deals, order construction projects and orchestrate the process of development. They purchase a tract of land, decide on a marketing strategy, and develop the building program. Developers must also obtain legal approval and financing. When the project is finished, they may lease, manage, or sell the property.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b972feaa-e2a6-4b22-9de4-18040f3f04eb','key_17394','','','5249','5249.2.1.1','rental service representative in agricultural machinery and equipment',replace('rental advisor in agricultural machinery and equipment\nrental service supervisor in agricultural machinery and equipment\nrental sales desk supervisor in agricultural machinery and equipment\nrental sales assistant in agricultural machinery and equipment\nrental service team leader in agricultural machinery and equipment\nrental sales desk clerk in agricultural machinery and equipment\nrental sales manager in agricultural machinery and equipment\nrental sales desk agent in agricultural machinery and equipment\nrental service desk clerk in agricultural machinery and equipment\nrental sales worker in agricultural machinery and equipment\nrental service manager in agricultural machinery and equipment\nrental sales team leader in agricultural machinery and equipment\nrental service representative in agricultural machinery and equipment\nrental sales supervisor in agricultural machinery and equipment\nrental service worker in agricultural machinery and equipment\nrental service assistant in agricultural machinery and equipment\nrental specialist in agricultural machinery and equipment\nrental service desk supervisor in agricultural machinery and equipment\nrental service desk agent in agricultural machinery and equipment','\n',char(10)),'Rental service representatives in agricultural machinery and equipment are in charge of renting out equipment and determining specific periods of usage. They document transactions, insurances and payments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b9836ed2-1ca7-49b4-9f0e-bb5d2ca14443','key_17395','','','4323','4323.14','road transport maintenance scheduler',replace('road transport maintenance coordinator\nroad transport maintenance scheduler\nvehicle maintenance scheduler\nvehicle maintenance coordinator\nroute vehicle maintenance planning officer\nroute vehicle maintenance coordinator\nvehicle maintenance planning officer\nroad transport maintenance officer\nscheduler of road transport maintenance\nroute vehicle maintenance scheduler','\n',char(10)),'Road transport maintenance schedulers are responsible for the effective execution of all maintenance work control processes of vehicles for urban transport, and for the efficient and effective use of planning and scheduling of all resources to conduct maintenance activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b997a091-23b3-4f87-a6bc-bdbea2f609ae','key_17396','','','8160','8160.54','wine fermenter',replace('fermenter\nwine products fermenter\nwine fermenter\nfermentation process operator','\n',char(10)),'Wine fermenters tend tanks to ferment crushed fruit or must into wines. They dump specified amounts of crushed fruits into wine tanks and mix them with syrup, chemicals or yeast. They prevent the growing of bacteria during fermentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b9b47087-6045-405d-b769-6a0c94e2eb84','key_17397','','','2621','2621.5','cultural visitor services manager',replace('mediation and education manager\neducation and visitor services manager\nvisitor service and education manager\nvisitor service supervisor\nart curator\nvisitor service manager\nvisitor services and education manager\nart mediation developer\neducation and visitor service manager\nmuseum education specialist\ncultural visitor services supervisor\nmuseum interpretation developer\nvisitor engagement manager','\n',char(10)),'Cultural visitor services managers are in charge of all programmes, activities, studies and research concerning the presentation of the cultural venue''s artefacts or programme to current and prospective visitors.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b9c80d74-97b6-4a4b-849e-a3908ba57ae1','key_17398','','','7543','7543.11','veneer grader',replace('plywood quality rater\nveneer grader\nplywood quailty grader\nveneer quality tester\nplywood rater\nplywood stock grader\nveneer quailty grader\nplywood-stock grader\nveneer rater\nplywood tester\nveneer quality rater\nplywood grader\nplywood quality tester\nveneer tester','\n',char(10)),'Veneer graders inspect slices of veneer for quality. They look for irregularities, blemishes and production errors, and grade the slices for the desirability of the patterns.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b9d03330-c71c-4827-8797-3e96249d54db','key_17399','','','7317','7317.6','woodcarver',replace('artisan woodworker\nheritage wood worker\nmaster woodcarver\ncraft wood carver\nheritage woodcarver\ntraditional woodcarver\nheritage woodworker\nartisan woodcarver\ntraditional wood carver\nartisan\nheritage wood carver\ntraditional handcarver\nartisan wood worker\ncraft woodcarver\nartisanal woodcarver\nhandcarver\nwoodcarver','\n',char(10)),'Woodcarvers manually shape wood into the desired shape using equipment like knives, gouges and chisels. Woodcarvers produce wooden products to serve as decoration, to be integrated into a composite product, as utensils or as toys.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b9dd2ee7-5fea-46f8-b226-ddae9501f728','key_17400','','','2635','2635.1.5','sexual violence counsellor',replace('sexual violence advisor\nsexual assault social counselor\nvictim care unit counsellor\ncounsellor, sexual violence\nsexual violence counselor\nsexual violence victim advocate\nvictim care unit worker\nsexual assault counsellor\nsexual violence support worker\nvictim support worker\nvictim care worker\ndomestic violence counsellor','\n',char(10)),'Sexual violence counsellors provide support services, crisis care services and counselling to women and adolescents who have been directly or indirectly exposed to sexual assault and/or rape. They inform victims of the relevant legal procedures and protective services maintaining client confidentiality. They also address problematic sexualized behaviours of children.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b9de6d8b-1cf6-4722-bf8c-d73aad03bf87','key_17401','','','5120','5120.1.2','fish cook',replace('sea food restaurant cook\nsea food cook\nseafood cook\nfish station chef\nfish cook\nseafood restaurant cook\nfish fryer\nfish chef\nfish restaurant chef\nfish section cook\nsaucier\nfish frier\nfish restaurant cook\nfish section chef\nfish station cook\npoissonnier','\n',char(10)),'Fish cooks are responsible for preparing and presenting fish dishes using a variety of techniques. They may also prepare the accompanying sauces and purchase fresh fish for these dishes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/b9e51ff4-d7fb-4653-bc72-65086ffc6e65','key_17402','','','3142','3142.1.3','viticulture adviser',replace('viticulturist\nviticulture adviser\nviticulture specialist\nadviser on viticulture\nviniculturist\nviniculture adviser\nwine production adviser','\n',char(10)),'Viticulture advisers advise on improving vineyard production and wine-making.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ba1e5427-46e0-4b6c-b196-5312d136dd3b','key_17403','','','3331','3331.2.1.24','import export specialist in mining, construction, civil engineering machinery',replace('trainee export specialist in mining, construction, civil engineering machinery\nassistant export specialist in mining, construction, civil engineering machinery\nassistant import specialist in mining, construction, civil engineering machinery\nimport export specialist in mining, construction, civil engineering machinery\ngraduate import export specialist in mining, construction, civil engineering machinery\nsenior export specialist in mining, construction, civil engineering machinery\nsenior import export specialist in mining, construction, civil engineering machinery\ntrainee import specialist in mining, construction, civil engineering machinery\ngraduate export specialist in mining, construction, civil engineering machinery\ngraduate import specialist in mining, construction, civil engineering machinery\ncustoms compliance specialist in mining, construction, civil engineering machinery\nsenior import specialist in mining, construction, civil engineering machinery\ninternational trade compliance specialist in mining, construction, civil engineering machinery\nassistant import export specialist in mining, construction, civil engineering machinery\ntrainee import export specialist in mining, construction, civil engineering machinery','\n',char(10)),'Import export specialists in mining, construction, civil engineering machinery have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ba3bacd4-73e9-4861-a9c0-f89f1f28055a','key_17404','','','1323','1323.1.1','construction general contractor',replace('construction general contractor\nconstruction contactor\nconstruction site contractor\ngeneral contractor in construction\nconstruction project manager','\n',char(10)),'Construction general contractors take on the responsibility to deliver a construction project. They participate on bid processes for construction projects and hire subcontractors to deliver the different stages of the construction process from beginning to completion. They make sure subcontractors hold up their part of the agreement and work in the construction site to make sure the project is delivered in time and according to agreed standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ba490796-46f7-45b7-827b-db6882fca3e1','key_17405','','','2149','2149.9.6','onshore wind energy engineer',replace('onshore wind energy engineering expert\nonshore wind power engineer\nonshore wind energy technology engineering specialist\nonshore wind energy engineering specialist\nonshore wind energy systems engineer\nonshore wind energy specialist','\n',char(10)),'Onshore wind energy engineers design, install and maintain wind energy farms and equipment. They research and test locations to find the most productive location, test equipment and components such as wind-turbine blades, and develop strategies for more efficient energy production, and environmental sustainability.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/baae6dd1-4a59-480d-83b1-a0708a5a1736','key_17406','','','1324','1324.3.1.2','maritime water transport general manager',replace('general manager of maritime water transport\nmaritime water transportation managing director\nmaritime water transportation general manager\nsea transport general manager\nmaritime water transport general manager\nmarine transport general manager\nsea transportation general manager\nmaritime transport managing director\ngeneral manager of maritime water transportation\nmarine transportation general manager\nmaritime transport manager\nsea transportation managing director\nmaritime transportation managing director\nmaritime water transport managing director\nsea transport managing director','\n',char(10)),'Maritime water transport general managers coordinate the administrative activities that go with the management of vessels in the context of maritime water transport. They prepare vessels, check availability, liaise with clients, manage budgets, and oversee operations from an economic standpoint, following regulation for maritime operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bab5fa79-7f96-4e21-87b6-1eba560b8d9a','key_17407','','','2166','2166.1','3D modeller',replace('3D modellers\n3D texturing artist\n3D modeler\nCGI modeller\ncomputer-generated imagery modeller\n3D artist\n3D developer\n3D specialist','\n',char(10)),'3D modellers design 3D models of objects, virtual environments, layouts, characters, and 3D virtual animated agents.','','Excludes people performing managerial activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bac62d45-bb74-45fa-9b38-a3ebf2095212','key_17408','','','3411','3411.1','case administrator',replace('legal executive\nlegal case administrator\ncase administrator\nprobation case administrator\nlegal administation officer\nprison case administrator\ndomestic abuse case administrator','\n',char(10)),'Case administrators supervise the progress of criminal and civil cases from the point of opening to closing. They review the case files and case progression to ensure proceedings occur compliant with legislation. They also ensure the proceedings occur in a timely manner and that everything has been concluded before closing cases.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bac64fa2-dcd6-47bb-8e5c-04e04a86a199','key_17409','','','2632','2632.5','geographer',replace('economic geographer\ngeography research scientist\nphysical geographer\nnatural geographer\nbiogeographer\ngeographer\ncultural geographer\ngeographers\nhuman geographer','\n',char(10)),'Geographers are scholars who study human and physical geography. Depending on their specialisation, they study political, economical and cultural aspects of humanity contained within human geography. Moreover, they study land formations, soils, natural frontiers, and water flows contained in physical geography.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bafbc672-0ad9-41af-9de8-db0c91a24f9b','key_17410','','','4311','4311.1','billing clerk',replace('financial clerk\naccount receivable clerk\naccounts receivables clerk\ninvoice clerk\naccounting officer\nfinancial administration clerk\nposting clerk\nbilling clerk\naccounts payable officer','\n',char(10)),'Billing clerks create credit memos, invoices and monthly customer statements and issue them to customers by all necessary means. They update customer files accordingly. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bb131b09-5fdf-4f4f-a76c-402c69cd090a','key_17411','','','8171','8171.8','pulp technician',replace('pulp and paper technologist\npulp production engineer\npulp and paper production specialist\npulp production specialist\npulp production technologist\npulp and paper technician\npulp production technician\npulp technician','\n',char(10)),'Pulp technicians perform technical tasks in the production of pulp. They work in pulp production teams where they maintain machines, resolve technical malfunctions and ensure the production process runs according to specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bb2933e9-f6ec-4ab0-b2ea-3cc34cf56d18','key_17412','','','8122','8122.7','electroplating machine operator',replace('lock plater\nelectric plater\nsilver plater\ngalvanising machine operator\ngold plater\ngold plating machine operator\ncopper plater\nmetal plating technician\nelectro plating technician\nelectroplating machine operator\nnon nickel plater\nhot-dip plater\nzinc plater\nmetal plater\nelectro-plater\nhot dip plater\noxidised finish plater\nchrome plater\nbronze plater\nnickel plater\nelectro plater\nelectroplater\nelectrodeposition operator','\n',char(10)),'Electroplating machine operators set up and tend electroplating machines designed to finish and coat the metal workpieces'' (such as future pennies and jewelry) surface by using electric current to dissolve metal cations and to bond a thin layer of another metal, such as zinc, copper or silver, to produce a coherent metal coating to the workpiece''s surface.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bb4ca5c1-879d-44e8-840d-24f320206bff','key_17413','','','3313','3313.3','grants administrator',replace('grants and contracts administrator\nproposals administrator\ngrants and trusts administrator\ngrants and contracts officer\ngrants administrator\nresearch grants administrator\ngrants & contracts officer\ngrants administration funding officer','\n',char(10)),'Grants administrators handle the pass-through track of grants, often given out by the government to the grant recipient. They prepare the paperwork such as the grant applications and give out the grants. They are also responsible to make sure that the grant recipient spends the money correctly according to the terms laid out.','','Excludes <a resource="http://data.europa.eu/esco/occupation/89330b57-6a30-40e1-b623-50f47a4b0c34" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/89330b57-6a30-40e1-b623-50f47a4b0c34"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">grants mangement officers</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bb609566-3ab6-44dd-8f48-cf0b15b96827','key_17414','','','2141','2141.4.2.1','automation engineer',replace('engineer in automated systems\nengineer in automation systems\nengineer in automation\nunmanned vessel engineer\nagricultural automation engineer\nautomated systems engineer\nautomation engineer\nautomated systems specialist\nspecialist in automated systems\nspecialist in automation systems\nmanufacturing  engineer in automation\nautomation systems engineer\nvessel autonomy engineer\nfleet autonomy system engineer','\n',char(10)),'Automation engineers research, design, and develop applications and systems for the automation of the production process. They implement technology and reduce, whenever applicable, human input to reach the full potential of industrial robotics. Automation engineers oversee the process and ensure all systems run safely and smoothly.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bb6198c4-1d3e-40b9-9cc4-ad2dd9d7c74b','key_17415','','','2514','2514.3','industrial mobile devices software developer',replace('industrial mobile devices software developer\nindustrial mobile devices software coder\nhandheld software developer\nhandheld programmer\nindustrial mobile devices software programmer','\n',char(10)),'Industrial mobile devices software developers implement applications software for specific, professional industrial mobile (handheld) devices, based on the industry needs, using general or specific development tools for device operating systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bb95ef58-9139-4967-bfc1-da8386a4e672','key_17416','','','3323','3323.2.2','ICT buyer',replace('ICT procurer\nICT buyers\nIT buyer\nICT purchaser\nICT buyer\nICT procurement officer\nIT procurement officer','\n',char(10)),'ICT buyers create and place purchase orders for ICT products and services, handle receiving and invoice issues, assess current procurement practices and effectively apply strategic sourcing methodologies. They build relationships with strategic vendors and negotiate price, quality, service levels and delivery terms.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bbaa1444-2c0a-4f76-8375-d72e3ea968fd','key_17417','','','5120','5120.1.3','grill cook',replace('barbecue cook\nline cook\nrotisserie chef\ngrill chef\nbarbecue chef\ngrill cook\nsteakhouse cook\nline chef\ngrill section chef\ngrill station chef\nrotisserie cook\ngrill station cook\nsteakhouse chef\ngrill section cook','\n',char(10)),'Grill cooks prepare and present meat, vegetables and fish using grill equipment such as grills and rotisseries.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bbba890e-ecae-43ba-9c5b-a65baa318f15','key_17418','','','1324','1324.3.2.21','import export manager in machine tools',replace('import export manager in machine tools\nsenior import export manager in machine tools\nassistant import export manager in machine tools\ntrainee export manager in machine tools\ncustoms compliance manager in machine tools\nsenior import manager in machine tools\ntrainee import manager in machine tools\nsenior export manager in machine tools\nassistant import manager in machine tools\ntrainee import export manager in machine tools\ninternational trade compliance manager in machine tools\ninternational trade manager in machine tools\ngraduate export manager in machine tools\nassistant export manager in machine tools\ngraduate import manager in machine tools\ngraduate import export manager in machine tools','\n',char(10)),'Import export managers in machine tools install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bbd1fe06-1b55-4937-9317-fc162311ab38','key_17419','','','0110','0110.7','brigadier',replace('director of staff\nbrigadier\nmilitary brigadier\narmy brigadier\nfield officer\nbrigadier general','\n',char(10)),'Brigadiers command large units of troops called brigades, oversee strategic and tactical planning, and monitor operations of their brigade. They manage the headquarters of the brigade''s division and ensure correct operations of the division on base and in the field.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bbefa1b2-521e-4d87-a7ad-9bbd0b32e271','key_17420','','','3315','3315.8','real estate surveyor',replace('real estate surveyor\nreal estate surveyors\nreal estate analyst\nmunicipal assessor','\n',char(10)),'Real estate surveyors undertake research in order to assess the value of property for tax purposes. They investigate multiple properties at once, using accurate appraisal techniques. They provide their services usually to local and governmental bodies for taxation reasons.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bc1748c7-29d6-43d5-a304-6b74f317ac26','key_17421','','','5164','5164.8','kennel supervisor',replace('dog/cat boarding supervisor\nanimal care manager\npet boarding operator\nkennel operator\ndog/cat kennel supervisor\npet boarding supervisor\ndog/cat boarding manager\nkennel manager\nanimal care supervisor\nkennel supervisor\npet boarding manager\ndog/cat kennel manager','\n',char(10)),'Kennel supervisors monitor the daily operations of the kennel under their supervision. They ensure that the pets kept in kennels are being properly handled and cared for. Kennel supervisors supervise the working staff and maintain contact with the owners of the pets while they drop off or pick up the pets.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bc1c82a5-0fc3-453f-8537-9d1a6e9e85aa','key_17422','','','2142','2142.1.1','airport planning engineer',replace('aviation development planner\nairport development director\nairport planning manager\nairport development engineer\nairport planning engineer','\n',char(10)),'Airport planning engineers manage and coordinate the planning, design, and development programs in airports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bc4d1659-6860-488e-bc13-e87eb0e36f67','key_17423','','','2163','2163.1.4','furniture designer',replace('furniture planner & designer\nfurniture designer\nfurniture & interior designer\ninterior FF&E designer\nfurniture maker\nfurniture planner\nfurniture craftsman\nfurniture designer and maker\nfurniture design consultant\nfurniture planner and designer\nfurniture designers\nfurniture designer and planner\nfurniture design advisor\nFF&E designer\nfurniture designer & planner\ninterior and furniture designer\nfurniture, fixments, and equipment designer\nfurniture builder\nhand-made furniture maker\nfurniture artist\nfurniture craftswoman\nfurniture and interior designer\nhand-made furniture builder\nfurniture design specialist\nproduct designer, furniture\nfurniture technologist\nhand-made furniture designer\nfurniture design expert\nfurniture technician\ninterior & furniture designer\nfurniture designer & maker','\n',char(10)),'Furniture designers work on items of furniture and related products. They design the product and are involved in its production as craftsmen and designers or makers. The conception of furniture combines innovative design, functional requirements and aesthetic appeal.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bc61af54-0c3b-411c-a434-4b0d23c6fcce','key_17424','','','7115','7115.4','kitchen unit installer',replace('kitchen unit installer\nkitchen joiner and fitter\nkitchen fitting  specialist\nkitchen installation worker\nkitchen carpenter and joiner\nreplacement kitchen installer\nkitchen fitter\nkitchen refurbishment team member\nkitchen furniture fitter\nkitchen installation specialist\nkitchen refurbishment team worker\nkitchen installer','\n',char(10)),'Kitchen unit installers install kitchen elements in homes. They take the necessary measurements, prepare the room, removing old elements if necessary, and install the new kitchen equipment, including the connection of water, gas and sewage pipes and electricity lines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bc61e0de-2d72-471c-90c1-a9fe371020f9','key_17425','','','7221','7221.4','mechanical forging press worker',replace('automatic casting machine technician\nautomatic casting- forging machine operative\nforgesmith\ndie forger\nmechanical forging press forge hand\nautomatic forging machine operative\nautomatic casting forging machine technician\nmechanical forging press forge operator\nforge press operator\nforge press operative\nautomatic casting- forging machine operator\nautomatic casting forging machine operator\nautomatic casting machine operator\nmechanical forging press worker\nspring forge operator\nspring forger\ndrop forge operator\nautomatic forging machine operator','\n',char(10)),'Mechanical forging press workers set up and tend mechanical forging presses, designed to shape ferrous and non-ferrous metal workpieces including pipes, tubes and hollow profiles and other products of the first processing of steel in their desired form by use of preset, compressive forces provided by cranks, cams and toggles at reproducible strokes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bc659adc-f42c-4ef6-89fe-fd030033f446','key_17426','','','2111','2111.2','cosmologist',replace('evolutionary cosmologist\ncosmology research scientist\ncosmology data scientist\ncosmology scientist\ntheoretical cosmologist\nobservational cosmologist\ncosmologist','\n',char(10)),'Cosmologists focus on the study of the universe as a whole, which is made up by its origin, evolution and ultimate fate. They use tools and scientific instruments to observe and study other galaxies and astronomical objects such as stars, black holes, planets and other celestial bodies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bc6649ff-05f8-4c98-9db6-9be0d13883a9','key_17427','','','7223','7223.4.3','laser cutting machine operator',replace('laser cutting machine operator\nlaser set up operative\nlaser mill operative\nCNC metal cutting machine worker\nlaser cutting technician\ncnc laser cutter\nlaser drill operator\nmetal cutting machine operative\nlaser mill inspector\nlaser mill technician\nlaser cutting machine worker\nCNC metal cutting machine setter\nCNC laser cutting machine operative\nmetal cutting machine tender\nlaser cutting machine setter\nlaser cutting machine tender\ncomputer numerical controlled laser cutting machine worker\nlaser cutting machine set-up operator\nlaser mill operator\ncomputer numerical controlled metal cutting machine worker\ncomputer numerical controlled laser cutting machine setter\nlaser drilling operative\nCNC laser cutting machine operator\nlaser cutter production operator\nCNC laser cutting machine set-up technician\ncomputer numerical controlled laser cutter operator\ncomputer numerical controlled laser cutting machine tender\ncomputer numerical controlled metal cutting machine tender\nCNC laser cutting machine worker\nlaser cutter\nlaser drill set-up technician\nlaser drill operative\nlaser cutter inspector\nlaser drill inspector\nlaser cutter technician\nCNC laser cutter\nCNC laser cutting machine tender\nCNC laser cutting machine setter\nlaser cutting machine operative\nlaser cutter production operative\ncomputer numerical controlled laser cutting machine operative\nmetal cutting machine worker\nlaser drill set-up inspector\nCNC metal cutting machine tender\ncomputer numerical controlled laser cutting machine operator\nCNC laser set-up inspector\nlaser drill technician\nmetal cutting machine setter\nlaser cutting machine set-up operative\ncomputer numerical controlled metal cutting machine setter\nlaser cutter production worker\nlaser set up operator\nCNC metal cutting machine operative','\n',char(10)),'Laser cutting machine operators set up, program and tend laser cutting machines, designed to cut, or rather burn off and melt, excess material from a metal workpiece by directing a computer-motion-controlled powerful laser beam through laser optics. They read laser cutting machine blueprints and tooling instructions, perform regular machine maintenance, and make adjustments to the milling controls, such as the intensity of the laser beam and its positioning.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bca063d2-a904-45db-b3f4-0fd7e681753e','key_17428','','','7311','7311.5','surgical instrument maker',replace('orthopaedic appliance maker\nsurgical instrument maker\nsurgical instrument designer\nmedical instrument design technician\nsurgical device repairer\nsurgical instrument assembler\nmedical instrument designer\nmedical instrument repairer\nsurgical device design technician\nmedical instrument assembler\nsurgical instrument technician\nsurgical instrument design technician\nmedical instrument technician\nsurgical device assembler\nsurgical instrument repairer\nsurgical device designer\nsurgical device technician','\n',char(10)),'Surgical instrument makers create, repair and design surgical instruments, such as clamps, graspers, mechanical cutters, scopes, probes and other surgical instruments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bca2528a-5a87-4576-a3b5-1edbbce40a9f','key_17429','','','5165','5165.1','driving instructor',replace('driving educator\nteacher of driving\ninstructor in driving\nlearner driver instructor\ndriving instructor\ndriving trainer\nteacher of learner drivers','\n',char(10)),'Driving instructors teach people the theory and practice on how to operate a vehicle safely and according to regulations. They assist students in developing the skills needed to drive and prepare them for the driving theory and the driving examination. They may also supervise driving examinations.','','Excludes people performing the instruction of occupational drivers.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bcb7efae-2374-4ea7-a8e7-a77e4c6ecfc8','key_17430','','','8112','8112.1','mineral crushing operator',replace('stone crushing operator\nmineral crushing plant operative\nstone crushing plant operative\nmineral crushing machine operative\nstone crushing process operator\nstone crushing process operative\nmineral crushing plant operator\nmineral crushing machine operator\nstone crushing machine operative\nmineral crushing operator\nmineral crushing process operative\nstone crushing plant operator\nmineral crushing process operator\nstone crushing machine operator','\n',char(10)),'Mineral crushing operators operate and monitor crushers and other machines to crush materials and minerals. They move stones to the crushers, fill the machines with minerals, monitor the crushing process and ensure that the end products meet requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bcba793e-44fe-45eb-b0fb-56772529cbab','key_17431','','','3343','3343.2','editorial assistant',replace('editorial production assistant\ncommercial editorial assistant\neditorial assistant\nmagazine editorial assistant\ndigital editorial assistant\ntravel and property editorial assistant\nonline editorial assistant\neditorial arts assistant\nfashion editorial assistant\nbeauty editorial assistant\neditorial aide\neditorial intern\nchildren''s editorial assistant','\n',char(10)),'Editorial assistants support the editorial staff at all stages of the publication process of newspapers, websites, online newsletters, books and journals. They collect, verify and process information, acquire permits and deal with rights. Editorial assistants act as point of contact for the editorial staff, schedule appointments and interviews. They proofread and give recommendations on the content. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bcc199fe-f7df-42a5-bd45-e33ae6160f26','key_17432','','','7541','7541.3','rescue diver',replace('para-rescue diver\nlifeguard\ndive rescue technician\ndive emergency worker\nunderwater rescue worker\nsearch and rescue diver\nemergency rescue diver\nrescue diver\ndiver life support technician','\n',char(10)),'Rescue divers are divers that manage underwater emergencies. They prevent and manage problems underwater, handle dive emergencies and operate rescue equipment. Rescue divers manage diving operations in seas or waterways. They work under demanding physical conditions to rescue and retrieve people or objects.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bcc21c63-7eee-4520-8fa7-43eefd389668','key_17433','','','3119','3119.8','industrial engineering technician',replace('industrial tool design inspector\nindustrial tool development  technologist\nindustrial tool inspector\nindustrial tool design quality control inspector\nindustrial engineering technician\nindustrial tool design quality control  technologist\nindustrial tool  technologist\nindustrial tool development inspector\nindustrial tool design  technologist','\n',char(10)),'Industrial engineering technicians assist industrial engineers in improving efficiency, safety and productivity in manufacturing plants. They conduct production studies, prepare layouts for machinery and equipment and suggest solutions to remedy quality problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bcc2c1db-f969-4355-bebf-4878ff6c2a94','key_17434','','','1349','1349.21','service manager',replace('service coordinator\nfield service manager\nsupport services manager\nregional service manager\nservice operations manager\ncorporate service coordinator\nservice director\nclient services coordinator\nclient services representative\nclient services manager\nservice manager\ncustomer service manager','\n',char(10)),'Service managers are responsible for the supervision and coordination of the provision of different professional and technical services to customers. They ensure a smooth interaction with clients and high levels of satisfaction post-service. This occupation includes the provision of policing, correctional, library, legal and fire services.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bcead4e9-3697-4831-b62e-ddb83d7d469d','key_17435','','','6223','6223.2','fisheries master',replace('fisheries master\nmaster of fishing vessel\nfishing vessel captain','\n',char(10)),'Fisheries masters plan, manage and execute the activities of fishing vessels inshore, coastal and offshore waters. They direct and control the navigation. Fisheries masters can operate on ships of 500 gross tonnage or more. They control the loading, unloading and stevedoring, as well as the collection, handling, processing and preservation of fishing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bced9b86-d4e7-42f8-bb47-acb2001b9bd0','key_17436','','','5142','5142.6','manicurist',replace('nail technician\nmanicurist\nfinger nail technician','\n',char(10)),'Manicurists provide fingernail care. They clean, cut and shape the nails, remove cuticles and apply polish. Manicurists apply artificial fingernails and other decorative items on nails. They advice on nail and hand care and sell specialised products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bcf8f332-c7e1-4a08-8651-28d668290eaf','key_17437','','','2146','2146.5','metallurgist',replace('consultant metallurgist\nchemical tester\nchemical technician\nextraction metallurgist\nforging engineer\nmetallurgist\nsteel industry experts\nphysical metallurgist','\n',char(10)),'Metallurgists specialise in the extraction and processing of metals such as iron, steel, zinc, copper and aluminium. They work to mold or combine both pure and mixed metals (alloys) into new shapes and properties. Metallurgists handle the extraction of metal ores and develop their use in metal processing techniques. They may work in both manufacturing or do scientific research about the performance of metals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bcffa133-c499-4db4-8e4f-b2d95da3b0fc','key_17438','','','7221','7221.1.1','farrier',replace('shoesmith\nfarriers\nhorse hoof trimmer\nhorse shoe fitter\nhorse pedicure specialist\nfarrier','\n',char(10)),replace('Farriers inspect, trim and shape the hooves of horses and make and fit horseshoes, in compliance with any regulatory requirements.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bd1cd902-1fb1-40b1-abce-bb9ef60805b4','key_17439','','','5419','5419.3','coastguard watch officer',replace('coastguard rescue officer\ncoastguard officer\ncoastguard watch officer\ncoastguard\nmaritime and coastguard officer','\n',char(10)),'Coastguard watch officers patrol and survey coast and sea regions to prevent accidents, as well as perform search and rescue missions in the event of an emergency. They react to emergency calls, advise on safety procedures, and prevent accidents and illegal activity at sea. Coastguard watch officers investigate shipping activities and aid during pollution incidents and in flood relief.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bd206ee9-61d6-45de-a369-0c71b8429efa','key_17440','','','7531','7531.6','wig and hairpiece maker',replace('wig and hairpiece maker\nwig & hairpiece maker\njunior wig maker\nwig and hairpiece builder\nwig technician\nsenior wig maker\nwig builder\nwig consultant\nwig and hairpiece technician\nwig-hairpiece maker\nhairpiece maker\nwig maker\nwig specialist\nmake up artist & wig maker','\n',char(10)),replace('Wig and hairpiece makers create adapt and maintain hair prostheses for use in live performance. They work from sketches, pictures and artistic visions combined with knowledge of the human body to ensure the wearer maximum range of movement. They work in close cooperation with the designers. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bd22a34c-1dc2-48df-a254-66e5d03dec12','key_17441','','','8350','8350.5','matrose',replace('AB\nable seaman\nwatchstander\nmatrose\ndayworker\nable seawoman','\n',char(10)),'Matroses are members of the deck department of an inland water transportship. They may work on the helm, on the deck, in the cargo hold and in the engine room. They may be called on to use emergency, lifesaving, damage control, and safety equipment. They perform all operations connected with the launching of lifesaving equipment and are expected to be able to operate deck machinery, mooring and anchoring equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bd272aee-adc9-4a06-a15c-a73b4b4a46a7','key_17442','','','2514','2514.2','ICT application developer',replace('application software developers\nICT application developer\napplication designer\napplication developer\napp software developer\nsoftware programmer\napplication software engineer','\n',char(10)),'ICT application developers implement the ICT (software) applications based on the designs provided using application domain specific languages, tools, platforms and experience.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bd2c868c-371b-4958-83b4-012c52bc7faf','key_17443','','','2659','2659.2.4','stand-up comedian',replace('stand-up comedian\nhumorist\ncomic\nstand up comedian\nstand up\nstand-up\ncomedian\nstand-up comic\nstand up comic','\n',char(10)),'Stand-up comedians tell humorist stories, jokes and one-liners typically described as a monologue, act or routine. They often perform in comedy clubs, bars, nightclubs and theatres. They may also use music, magic tricks or props to enhance their performance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bd378c4e-f656-46af-a728-fcfb40b113ef','key_17444','','','8172','8172.2','crosscut saw operator',replace('cross-cut saw operator\ncrosscut saw worker\ncrosscut sawyer\ncrosscut saw operator\ncross-cut saw specialist\ncrosscut saw specialist\ncross-cut sawyer\ncrosscut saw technician\ncross-cut saw technician','\n',char(10)),'Crosscut saw operators use a manual crosscut saw. Crosscut sawing is used for felling and bucking trees, or taking off the limbs to obtain logs. Crosscut sawyers may also work with smaller crosscut saws in a workshop to make manual cuts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bd55e8d7-8bdf-4179-a84b-ed43073f05a1','key_17445','','','7312','7312.6.2','harp maker',replace('celtic harp builder\nlever harp maker\nharp maker\nwire harp builder\nmulti-course harp maker\nlever harp builder\npedal harp builder\nceltic harp maker\npedal harp maker\nwire harp maker\nmulti-course harp builder','\n',char(10)),'Harp makers create and assemble parts to create harps according to specified instructions or diagrams. They sand wood, measure and attach strings, test quality of strings and inspect the finished instrument.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bd5a2f44-fc06-45fd-909b-3a4001c8b8a3','key_17446','','','0310','0310.3','infantry soldier',replace('foot soldier\nmilitary ranger\ninfantryman\narmy soldier\nsniper\nrifleman\ninfantry soldier','\n',char(10)),'Infantry soldiers fight in combat missions or provide aid in peacekeeping missions and other humanitarian services. They handle weapons and provide defence services where they are required, and attempt to complete missions while causing minimal harm.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bd7c070b-333f-4b9d-a70a-25f135ff931e','key_17447','','','1324','1324.3.1.6.15','furniture, carpets and lighting equipment distribution manager',replace('furniture, carpets and lighting equipment distribution planner\nassistant furniture, carpets and lighting equipment distribution manager\nsenior furniture, carpets and lighting equipment distribution manager\nfurniture, carpets and lighting equipment logistics manager\ngraduate furniture, carpets and lighting equipment distribution manager\nfurniture, carpets and lighting equipment logistics planner\nfurniture, carpets and lighting equipment supply chain planner\nfurniture, carpets and lighting equipment distribution manager\nfurniture, carpets and lighting equipment supply chain manager\ntrainee furniture, carpets and lighting equipment distribution manager','\n',char(10)),'Furniture, carpets and lighting equiment distribution managers plan the distribution of furniture, carpets and lighting equiment to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bd94312f-05c8-455d-a2f6-5490489ac4a4','key_17448','','','3435','3435.18','prop master/prop mistress',replace('prop mistress\nprop supervisor\nprop master/prop mistress\nprop coordinator\nproperty  mistress\nprop technician\nproperty coordinator\nprop master/mistress assistant\nprop assistant\nproperty master\nproperty technician\nprop master','\n',char(10)),'Prop masters/mistresses setup, prepare, check and maintain objects used on stage by actors or other small movable objects called props. They cooperate with road crew to unload, set up and prepare the props. During a performance they position props, hand them over or take them back from the actors.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bd9d395a-d587-45c6-8d72-ceef226df9e1','key_17449','','','2511','2511.16','ICT system integration consultant',replace('network integration consultant\napplication integration consultant\nICT system integration consultant\nsystem integration consultants\nsystems integration consultant\nsystems integration consultants','\n',char(10)),replace('ICT system integration consultants advise on bringing together different systems to interoperate within an organisation for enabling data sharing and reducing redundancy.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bda98f02-4f92-4015-a0c8-b86b8abc711a','key_17450','','','8153','8153.1.2','clothing sample machinist',replace('sample clothing sewer\nsample sewing machinist\nsample garment machinist\nsample sewer\ngarment sample machinist\nclothing sample machinist','\n',char(10)),'Clothing sample machinists create the first made-up sample of a garment design. They make decisions regarding the makeup of garments taking into consideration bulk production to ensure sealing samples are ready on time. They press finished garments and make quality control checks. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bdc74965-1d1a-4f8e-a490-9cf71f494e02','key_17451','','','3251','3251.1','dental chairside assistant',replace('dental chairside assistant\ndental support worker\ntrainee dental nurse\northodontic nurse\ndental assistant\nqualified dental nurse','\n',char(10)),'Dental chairside assistants support dental practitioners in clinical treatments, as preparation and helping in practical execution and follow-up, and administrative tasks under supervision and following the orders of the dental practitioner.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bdf2dd9d-6c92-46f4-a8e7-76c12302d3e7','key_17452','','','4221','4221.3','ticket sales agent',replace('ticket sales agent\nticket reservation agent\nticket agent\nticket booking agent','\n',char(10)),'Ticket sales agents provide initial service to customers, sell travel tickets and fit the reservation offer to customers'' queries and needs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bdfb48e0-79dc-4b63-8470-db2e03b37326','key_17453','','','5222','5222.1','shop supervisor',replace('boutique supervisor\nshop general supervisor\nshop staff supervisor\nstore supervisor\nretail shop supervisor\nretail store supervisor\nshop supervisor','\n',char(10)),'Shop supervisors are responsible for the smooth operation of stores according to regulations and company policy. They oversee business activities such budgets, inventory and customer service. Shop supervisors also monitor employees'' performance and ensure that goals are being met.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bdfd018a-aae1-407e-a88c-471e50b0695d','key_17454','','','3122','3122.4.16','wood production supervisor',replace('sawmill overseer\nsawmill supervisor\nsaw mill overseer\nwood production supervisor\nsupervisor in wood production\noverseer of wood production\nmanager of wood production\nwood production overseer\nsaw mill senior manager\nsaw mill supervisor\nsawmill senior manager\nsawmill controller\ncontroller of wood production\nwood production controller\nsupervisor of wood production\nwood production manager\nsaw mill controller','\n',char(10)),'Wood production supervisors monitor processes involved in the conversion of felled trees into usable lumber. They follow the production process and make quick decisions to resolve problems. ​They ensure that production targets, such as quantity and quality of products, timeliness and cost-effectiveness, can be achieved.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/be0e6189-46a5-4b90-8622-2d1988efba1a','key_17455','','','8211','8211.2','industrial machinery assembler',replace('assembly line machine tender\nindustrial machinery assembly line inspector\nbench precision assembler\nassembly line operative\nmachine assembly worker\nindustrial machinery assembler\nmachine assembler\nmanufacturing assembler\nassembly worker\nsub assembler\nfinal assembler\nassembly line operator','\n',char(10)),'Industrial machinery assemblers manufacture industrial equipment such as industrial robots, assembly line machines, and labeling machines. They use hand tools and computer-controlled machines.','','Excludes <a resource="http://data.europa.eu/esco/occupation/269c47e7-9017-4aa6-bce8-49e89a696a64" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/269c47e7-9017-4aa6-bce8-49e89a696a64"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">industrial machinery mechanics</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/be235deb-6181-43ca-8127-7010a689643a','key_17456','','','2269','2269.10','specialist chiropractor',replace('licensed specialised chiropractor\nspecialised osteopath\nspecialist chiropractor\nspecialised etiopath\nspecialised musculoskeletal therapist\nspecialised chiropractor\nspecialised neuromusculoskeletal therapist','\n',char(10)),replace('Specialist chiropractors have an advanced knowledge and understanding in a specific chiropractic area, acquiring complex decision making skills and clinical competency within the scope of the chiropractic profession. Specialist chiropractors may have undertaken an advanced course of studies to acquire the necessary skills and competency and qualification. Specialist chiropractors may be found in different specific fields such as education, functional neurology, orthopaedics, paediatrics, research, radiology, sport. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/be2896db-9ab7-4719-a558-96b18c93027d','key_17457','','','2250','2250.1','animal chiropractor',replace('animal chiropractitioner\nveterinary chiropractor\nanimal chiropractor\nanimal chiropractors','\n',char(10)),'Animal chiropractors provide a therapeutic treatment following veterinary diagnosis or referral. They concentrate on spinal manipulation or manual therapy for animals in accordance with national legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/be375e11-9a70-47cb-8091-711c94ed0c13','key_17458','','','5142','5142.11','tanning consultant',replace('sun parlor worker\ntan specialist\nsun parlor attendant\ntanning salon operator\nsolarium attendant\ntanning specialist\nsolarium worker\ntanning consultant\ntanning salon manager\nsun parlour attendant\nsolarium consultant\ntan consultant\ntanning salon worker\ntanning salon attendant','\n',char(10)),'Tanning consultants assist clients with their tanning needs. They offer advice on purchases and treatments in solariums and tanning salons.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/be3c021d-bd93-45a0-ad32-e8fa8e0d17ef','key_17459','','','2310','2310.1.26','modern languages lecturer',replace('modern languages teacher\ninstructor in modern languages\nprofessor of modern languages\nuniversity modern languages teacher\nmodern languages lector\nuniversity modern languages lecturer\nmodern languages professor\nhigher education modern languages teacher\nuniversity lecturer in modern languages\nmodern languages instructor\nmodern languages docent\nlecturer in modern languages\nmodern languages lecturer\nsenior lecturer in modern languages\nspecialist lecturer in modern languages\nlecturer of modern languages','\n',char(10)),'Modern languages lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, modern languages, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants in the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of modern languages, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/be54b8da-bc71-40f0-b150-45c5e0769375','key_17460','','','3332','3332.2.1','venue director',replace('banquet manager\nconference manager\nvenue director\nevent manager\nvenue manager\nbanqueting manager\nparty manager\nconferencing manager\nconferencing and banqueting manager\ncatering manager','\n',char(10)),'Venue directors plan and manage conference, banqueting and venue operations in a hospitality establishment to reflect clients'' needs. They are responsible for promotional events, conferences, seminars, exhibitions, business events, social events and venues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/be6d7fd3-2e51-499f-a31d-8aa0bbd32f2a','key_17461','','','2635','2635.3.20','palliative care social worker',replace('hospice carer\nhospice worker\npalliative care social worker\nhospice care assistant\npalliative care assistant\npalliative care supervisor\nhospice nursing assistant\nsocial worker, palliative care\npalliative and hospice social worker','\n',char(10)),'Palliative care social workers provide assistance and counselling to patients with a chronic or a terminal disease and their families with the practical arrangements. They arrange the required medical care for the patient and help the family to adjust to the diagnosis by providing support and attention to their emotional needs, helping them to understand their options.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/be845b10-21d0-49a7-b5b9-095828c32515','key_17462','','','2149','2149.9.3','geothermal engineer',replace('geothermal technology engineering adviser\ngeothermal O&M engineer\ngeothermal power plant manager\ngeothermal engineering consultant','\n',char(10)),'Geothermal engineers research, design, plan and operate processes and equipment that convert thermal energy into electricity or heating and cooling. They use natural heat sources from underground to generate power, to cool in the summer and heat in the winter industrial, commercial and residential buildings. Geothermal engineers develop strategies for more efficient energy production and analyse environmental consequences.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bea705fe-06ac-4147-b8e0-6e8ac1208d8f','key_17463','','','9333','9333.8','warehouse worker',replace('warehouse operator\nwarehouse worker\nwarehouse picker\nwarehouse packager\npackager\npacker and shipper\npacker\norder clerk\nwarehouse packer\norder picker\nwarehouse operative','\n',char(10)),'Warehouse workers execute the accurate handling, packing and storage of materials in a warehouse. They receive goods, label them, check quality, store the goods and document any damage. Warehouse workers also monitor stock levels of items, keep inventory and ship goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bea99fea-0383-4c63-b944-70d4799de2c5','key_17464','','','1219','1219.6','project manager',replace('project leader\nprojects manager\nproject manager\nproject coordinator\nproject expediter\nproject director\nspecial project manager\nsenior project manager','\n',char(10)),'Project managers oversee the project on a daily basis and are responsible for delivering high-quality results within the identified objectives and constraints, ensuring the effective use of the allocated resources. They are responsible for risk and issue management, project communication and stakeholder management. Project managers perform the activities of planning, organising, securing, monitoring and managing the resources and work necessary to deliver specific project goals and objectives in an effective and efficient way.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/beacd3d6-e414-42ea-baf9-3ddc34fc143a','key_17465','','','8160','8160.55','yeast distiller',replace('yeast handler\nbrewing yeast distiller\nyeast distilling operative\nbrewing yeast handling operative\nyeast distiller','\n',char(10)),replace('Yeast distillers extract alcohol from yeast to use it in the manufacture of distilled liquors. They weigh yeast quantities and gauge temperature to determine adequate conditions for distillation. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/becbe3ca-e000-40c0-9f67-5c4035b930ea','key_17466','','','3114','3114.1.3','computer hardware test technician',replace('computer hardware testing technician\nhardware tester\ncomputer hardware inspector\ninspection technician in computer hardware\ncomputer hardware inspection technician\ncomputer hardware tester\ncomputer hardware test technician\ninspector of computer hardware\ntester of computer hardware','\n',char(10)),'Computer hardware test technicians conduct testing of computer hardware such as circuit boards, computer chips, computer systems, and other electronic and electrical components. They analyse the hardware configuration and test the hardware reliability and conformance to specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/beedd3c0-d3e2-4efe-8f39-381df669ee35','key_17467','','','9212','9212.3','groom',replace('stud hand\ntrek assistant\njockey\ngroom\nriding coach\nhorse groom','\n',char(10)),'Grooms provide practical daily horse care to ensure the horses health, welfare and safety. They take part in exercising horses, cleaning and maintaining the stables, buildings and territory.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bf2ca3bb-5fa5-41d9-a039-949cad61616d','key_17468','','','2433','2433.6.2','technical sales representative in chemical products',replace('chemical products inside technical sales representative\ntrainee technical sales representative in chemical products\ngraduate technical sales representative in chemical products\nassistant technical sales representative in chemical products\nchemical products outside technical sales representative\nsenior technical sales engineer in chemical products\nassistant technical sales engineer in chemical products\ntrainee technical sales engineer in chemical products\nchemical products technical field sales representative\nchemical products field salesman\nsenior technical sales representative in chemical products\ntechnical sales representative in chemical products\ngraduate technical sales engineer in chemical products\nchemical products field salesperson\nchemical products  field saleswoman','\n',char(10)),'Technical sales representatives in chemical products act for a business to sell its merchandise while providing technical insight for customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bf367464-950a-416c-98b1-52b9d571550e','key_17469','','','8183','8183.6','leather goods packing operator',replace('packing attendant\npacking worker\nleather goods packing attendant\nleather goods packing operator\nleather goods packing worker','\n',char(10)),'Leather goods packing operators perform the final revision of the leather goods products. They apply accessories such as the handles, padlocks, or other features of the product, e.g. labels. They introduce products in textile sacs if applicable, fill them with paper to maintain the product''s shape and then place products in boxes using adequate tools for products'' protection. They are in charge of general packaging, and check the completion of each order by getting the boxes into the parcels and preparing the documentation for expedition by the transport agency.','',replace('Includes people performing a final generic quality control according to the previous requirements regarding visual aspects of the product. \n Excludes people performing managerial and manufacturing activities.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bf7d8b16-4e2c-48ef-b44e-dc25b2d0ab61','key_17470','','','5132','5132.1.1','barista',replace('coffee maker\nspecialised bartender\nspecialised coffee bartender\nbarista\ncoffee bartender','\n',char(10)),'Barista''s prepare specialised types of coffee using professional equipment in a hospitality/coffee shop/bar unit. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bf802371-4758-4268-a780-58e3d269831b','key_17471','','','2263','2263.5','radiation protection officer',replace('radiation protection manager\nradiation protection officer\nradiation protection advisor\nradiation protection team leader\nradiation protection supervisor','\n',char(10)),'Radiation protection officers are responsible for the protection from harmful effects caused by exposure to ionising radiation. They ensure compliance with legislation and regulations by enforcing safety measures. Furthermore they develop radiation protection plans in particular for nuclear plants and facilities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bf89925d-00a1-4f94-ab3b-2148c887af33','key_17472','','','5221','5221.1','retail entrepreneur',replace('retail business developer\nretail business entrepreneur\nshop keeper\nretail trade entrepreneur\nretail product developer\nretailer\nretail business transformer\nshopping entrepreneur\nretail innovator\nretail experience entrepreneur\nretail entrepreneur\nentrepreneur\nretail sales entrepreneur','\n',char(10)),'Retail entrepreneurs organise business processes and concepts in his/her personally owned business.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bfc1b4e9-624c-4fba-89f2-d218bba9e257','key_17473','','','3435','3435.14','performance flying director',replace('flying FX director\nflying FX choreographer\nflying effects choreographer\nfly director\nflying effects director\nflying director','\n',char(10)),'Performance flying directors design people flying effects for a performance and supervise or perform the execution of it. Their work is based on research and artistic vision. Their design is influenced by and influences other designs and must be conform with these designs and the overall artistic vision. They train the actors for the flying choreography and manipulate them during the performance. Performance flying directors prepare and supervise the setup, perform security checks and operate the person fly systems. The manipulation of persons on height, close to or above performers and audience makes this a high risk occupation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bfc92738-128b-44f5-92d4-51b10df17961','key_17474','','','2633','2633.2','philosopher',replace('moralist\nphilosopher\nphilosophy student\nphilosophy scholar\nphilosophy expert','\n',char(10)),'Philosophers study and argument over general and structural problems pertaining to society, humans and individuals. They have well-developed rational and argumentative abilities to engage in discussion related to existence, value systems, knowledge, or reality. They recur to logic in discussion which lead to levels of deepness and abstraction.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bfdc096e-e450-424d-9340-46968047ed68','key_17475','','','8131','8131.13','nitroglycerin neutraliser',replace('nitroglycerin production operator\nnitroglycerin neutralizer\nneutralizer\nnitroglycerin production operative\nnitroglycerin production worker\nnitroglycerine neutralizer\nneutraliser','\n',char(10)),'Nitroglycerin neutralisers maintain explosives mixing tanks by neutralizing the process remaining acids.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bfe142b9-a049-4d41-89c4-fd2f34ac56b6','key_17476','','','3313','3313.1','accounting assistant',replace('junior accountant\nassistant accountant\naccounting assistant\naccountant assistant\nfinance assistant\nbilling and accounting assistant','\n',char(10)),'Accounting assistants record and report ticketing accounting situations to the accountant they work with, verify deposits and prepare daily reports and income. They arrange authorised refund vouchers, maintain the returned check accounts and communicate with ticketing managers concerning about any  issues with ticketing systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bff716c9-42c7-471e-8af0-582f799fb29a','key_17477','','','2133','2133.2','aquaculture environmental analyst',replace('aquaculture environmental scientist\naquaculture environment specialist\nmarine scientist\nfish farming environmental specialist\naquaculture environmental specialist\nspecialist in aquaculture environmental analysis\naquaculture environmental analyst','\n',char(10)),'Aquaculture environmental analysts assess, plan and implement programmes to recognise, monitor and control environmental factors that can potentially affect the health of aquatic animals and plants.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bff7e8a3-6206-4d38-b25f-fcb6e634c7af','key_17478','','','2310','2310.1.2','archaeology lecturer',replace('instructor in archaeology\narchaeology instructor\nlecturer in archaeology\nuniversity lecturer in archaeology\narchaeologist lector\narcheology lecturer\nlecturer in archaeological studies\narchaeology lector\narchaeology professor\narchaeology teacher\nprofessor of archaeology\narchaeology docent\nsenior lecturer in archaeology\nuniversity archaeology lecturer\nlecturer of archaeology','\n',char(10)),'Archaeology lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, archaeology, which is predominantly academic in nature. They work with university research assistants and university teaching assistants for the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of archaeology, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/bffbe18a-cb8b-4f90-bd87-7505cd3b16f5','key_17479','','','2267','2267.1','optometrist',replace('optometry practitioner\nexpert optometrist\nspecialist optometrist\noptometrist\npractitioner of optometry\nspecialist in optometry\nophthalmic optician\noptometric expert','\n',char(10)),'Optometrists examine and test eyes to identify abnormalities, visual problems, or disease. They prescribe and fit lenses such as spectacles and contacts, and offer advice on visual problems. They may also refer patients to a medical practitioner. Their scope of practice and title varies according to national regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c011a88b-95ab-46ad-ad30-539e3fc56092','key_17480','','','2330','2330.1.10','literature teacher at secondary school',replace('literature teacher high school\nsecondary school literature tutor\nhigh school literature teacher\nhigh school literature tutor\nliterature teacher in high school\nteacher of literature in high school\nenglish literature teacher\nenglish literature teacher in secondary schools\nliterature teacher','\n',char(10)),'Literature teachers at secondary schools provide education to students, commonly children and young adults, in a secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, literature. They prepare lesson plans and materials, monitor the students'' progress, assist individually when necessary, and evaluate the students'' knowledge and performance on the subject of literature through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c01cdf85-3ce0-4d12-ae86-e4e63493fafa','key_17481','','','3311','3311.2','financial broker',replace('broker\nfinancial services manager\nfinancial services broker\nfinancial broker\nfinance broker\nfinance adviser\nfinancial services adviser','\n',char(10)),'Financial brokers undertake financial market activities on behalf of their clients. They monitor securities, financial documentation of their clients, market trends and conditions and other legal requirements. They plan buying and selling activities and calculate transactions costs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c02ee787-c30b-4f0d-9d7f-6eb36fc44a5d','key_17482','','','1223','1223.2.2','research manager',replace('head of laboratory\nresearch manager\nresearch scientist\nhead laboratory analyst\nscientific research director\nprincipal research scientist\nscientific director\nchief science officer','\n',char(10)),'Research managers oversee the research and development functions of a research facility or program or university. They support the executive staff, coordinate work activities, and monitor staff and research projects. They may work in a wide array of sectors, such as the chemical, technical and life sciences sector. Research managers can also advise on research and execute research themselves.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c0386702-0699-4550-920b-1f7027c9a3d9','key_17483','','','7511','7511.2','fish preparation operator',replace('seafood preparation operator\nfish preparation operator\nfish counter worker\nfish gutter\nfish boner\nfish stall worker\nshellfish preparation operator\nseafood seller\nfresh fish processing operator\nfishmonger\nfish shop worker\nfish processing operator\nfresh fish preparation operator\nfish seller\nfish counter salesperson','\n',char(10)),'Fish preparation operators realize the preparation of fish and shellfish according to hygiene, food safety and trade regulations. They carry out fish processing operations, and also handle retail activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c0463fbe-7914-48ac-84ec-abaee1a7f292','key_17484','','','3311','3311.2.4','securities broker',replace('financial services sales agent\nsecurities broker\njunior stock broker\ninvestment broker\nstock salesperson','\n',char(10)),'Securities brokers create the connection between the investors and the available investment opportunities. They purchase and sell securities on their clients'' behalf, based on their expertise in the financial markets. They monitor the performance of their clients'' securities, assessing their stability or speculative tendencies. Securities brokers calculate the securities'' price and place orders.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c0489e22-6a1b-4e52-974a-eb71bbb42134','key_17485','','','2432','2432.9','public relations officer',replace('public relations coordinator\npress relations officer\npress consultant\npublic relations officer\ncommunications officer\npress secretary\nmedia relations officer\npublic affairs officer\npublic information officer\nmedia consultant\nmedia coordinator','\n',char(10)),'Public relations officers represent a company or organisation to stakeholders and the public. They use communications strategies to promote an understanding of the activities and image of their clients in a favourable way.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c05090e2-d599-46c8-a47b-3d52a893ca55','key_17486','','','3435','3435.12','mask maker',replace('mask designer & maker\nmask maker\nmask designer and builder\nmask designer & builder\nmask designer/maker\nmask maker and designer\nmask builder & designer\nmask designer and maker\nmask builder and designer\nmask maker & designer\nmask builder\nmask-maker','\n',char(10)),'Mask makers construct, adapt and maintain masks for live performances. They work from sketches, pictures and artistic visions combined with knowledge of the human body to ensure the wearer maximum range of movement. They work in close cooperation with the designers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c0749528-ee2a-41bd-a1f6-c13825b5e569','key_17487','','','7231','7231.6','roadside vehicle technician',replace('van recovery technician\nheavy vehicle roadside technician\ncoach roadside technician\nPCV roadside technician\nbus and coach roadside technician\nLGV roadside technician\nautomotive recovery technician\ntruck recovery technician\nroadside vehicle technician\nbus roadside technician\nvehicle recovery technician\nmobile auto mechanic\ncar recovery technician\nlight vehicle roadside technician\nmobile vehicle mechanic','\n',char(10)),'Roadside vehicle technicians perform onsite repairs, tests and maintenance to roadside vehicles. They locate and travel to customers'' vehicles to provide services such as tyre replacements and engine repairs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c0768628-46ad-4077-aee0-66ab6d7711df','key_17488','','','5223','5223.7.18','flower and garden specialised seller',replace('plant salesperson\nplant and garden shop specialised seller\nplant nursery specialised seller\nfloristry store salesperson\nflower and garden specialized seller\nlawn and garden store specialised seller\nlawn and garden products salesperson\nflower salesperson\nnursery shop salesperson\ngarden superstore specialised seller\nflower shop specialised seller\ngarden supplies specialised seller\nlawn and garden shop specialised seller\nplant and garden shop salesperson\nfloristry shop salesperson\nfloristry shop specialised seller\ngarden superstore salesperson\nflower specialised seller\nlawn and garden products specialised seller\ngarden supplies salesperson\nplant nursery salesperson\nplant shop specialised seller\nfloristry store specialised seller\nlawn and garden shop salesperson\ngarden store specialised seller\nplants specialised seller\nflower shop salesperson\ngarden store salesperson\ngarden centre salesperson\nplant centre salesperson\ngarden centre specialised seller\nnursery shop specialised seller\nlawn and garden store salesperson','\n',char(10)),'Flower and garden specialised sellers sell flowers, plants, seeds and/or fertilisers in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c07ab008-79c1-4516-8dcc-8289f797dc34','key_17489','','','7412','7412.9','mining electrician',replace('underground mining electrician\nelectrical specialists\nmine machinery specialist\nplant electrician\nelectrical engineering technical\nmaintenance electricians\nauto electricians\nauto electrician\nelectrical fitters\nelectrical technicians\nelectrical specialist\nmaintenance electrician\nmining electricians\nmining electrician\nelectrical engineering technicals','\n',char(10)),'Mining electricians install, maintain and repair specialised electrical mining equipment using their knowledge of electrical principles.  They also monitor mine electricity supply.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c0a15ec7-10e6-45e7-b69c-b85ff6f4f87f','key_17490','','','6221','6221.7','aquaculture husbandry worker',replace('fish farming husbandry worker\naquaculture husbandry worker\naquaculture on-growing worker\naquatic animals husbandry worker','\n',char(10)),'Aquaculture husbandry workers are active in the production of aquatic organisms in land-based on-growing processes. They assist in the process of raising organisms through all stages of their life cycle.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c0a35355-be44-44c0-82d2-acf8363ed45e','key_17491','','','7543','7543.9.3','metal product quality control inspector',replace('metal fabrication supervisor\nmechanical supervisor\nmetal product quality control inspector\nmetal production manager\nmetal production overseer\nmelter supervisor\nmetal grading technician\nmetal nondestructive testing technician\nmetal quality superintendent\nmetal fabricating supervisor\nmetal testing technician\nmetal quality control superintendent\ntesting specialist for weapons and ammunition','\n',char(10)),'Metal product quality control inspectors perform preventive and operational quality control on the metal products. They examine the materials at various stages to make sure it conforms to the desired standard, test the products, and send them back for repair if necessary.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c0c1364d-f784-4e76-8d3f-1e998dc6545d','key_17492','','','7422','7422.5','security alarm technician',replace('security alarm installer\nintruder alarm installer\nelectronic security systems installer\nfire alarm technician\nsecurity alarm engineer\nburglar alarm technician\nintruder alarm technician\nfire alarm installer\nsecurity alarm fitter\nsecurity alarm technician\nburglar alarm installer\nsecurity systems technician','\n',char(10)),'Security alarm technicians install and maintain security alarm systems to protect against hazards such as fire and burglary. They install sensors and control systems and connect them to power and telecommunication lines if required. Security alarm technicians explain usage of the installed systems to the prospective users.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c0eb9240-4f70-4393-bdf0-723ce7fdbd36','key_17493','','','8172','8172.4','engineered wood board machine operator',replace('particleboard machine operator\nengineered wood press operator\nengineered wood press technician\nparticle board machine technician\nfibreboard machine technician\nengineered wood board machine technician\nengineered wood press specialist\nparticle board machine specialist\nengineered wood machine technician\nfibreboard machine specialist\nengineered wood board machine operator\nparticle board machine operator\nengineered wood board gluing operator\nengineered wood machine operator\nengineered wood board machinist\nfibreboard machine operator\nengineered wood board gluing specialist\nengineered wood machine specialist\nparticleboard machine technician\nengineered wood board gluing technician\nfibre board machine specialist\nengineered wood board machine specialist\nfibre board machine operator\nengineered wood board machine worker\nparticleboard machine specialist\nfibre board machine technician','\n',char(10)),'Engineered wood board machine operators work with machines to bond particles or fibres made from wood or cork. Various industrial glues or resins are applied to obtain fibre board, particle board or cork board.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c0ec3a06-1dac-4c21-9f08-4c9dfbc9d6cf','key_17494','','','7133','7133.5','decontamination worker',replace('decontamination worker\nsoil decontamination worker\nenvironmental remediation worker\nradio-active decontamination worker\nenvironmental decontamination technician\nhazardous material decontamination worker\nenvironmental decontamination worker','\n',char(10)),'Decontamination workers remove and dispose of hazardous materials, such as radioactive materials or contaminated soil. They handle hazardous materials in compliance with safety regulations, investigate causes of contamination, and remove the contamination from the structure or site.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c11e2b53-06f7-465f-9b5c-708016bc96f4','key_17495','','','7323','7323.4','print folding operator',replace('printing folding machine operator\nfolder guillotine operator\nfolder machine operator\nfolder/cutter operator\nminiture folding specialist\nprint folding operator\nfolder/guillotine operator\nminiture folding technician\nfolding machine specialist\nfolder gluer specialist\nfolding machine technician\npaper folding machinist\nfolder/gluer specialist\npaper folder machine worker\nminiture folding operator\nfolder cutter operator\nfolder machine tender\nprint folding technician\nprint folding specialist\ncomplex folder/gluer operator','\n',char(10)),'Print folding operators tend a machine that folds paper and bundles of paper.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c143d51a-5267-41a8-a73b-9022cf74c495','key_17496','','','3331','3331.2.1.26','import export specialist in office machinery and equipment',replace('trainee import specialist in office machinery and equipment\nsenior export specialist in office machinery and equipment\ngraduate export specialist in office machinery and equipment\ninternational trade compliance specialist in office machinery and equipment\nassistant import export specialist in office machinery and equipment\ngraduate import export specialist in office machinery and equipment\ngraduate import specialist in office machinery and equipment\ntrainee import export specialist in office machinery and equipment\ntrainee export specialist in office machinery and equipment\nassistant import specialist in office machinery and equipment\nsenior import specialist in office machinery and equipment\nimport export specialist in office machinery and equipment\nassistant export specialist in office machinery and equipment\nsenior import export specialist in office machinery and equipment\ncustoms compliance specialist in office machinery and equipment','\n',char(10)),'Import export specialists in office machinery and equipment have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c18fddf5-cfc4-4f9c-94d6-86608faaa1dd','key_17497','','','1344','1344.1','social services manager',replace('health and social care service manager\nsocial care manager\nsocial services team manager\nelderly homes manager\nsocial services manager\nsocial and care services manager\nadult social services manager\nolder adults social services manager\nmanager of social service\nfrontline manager\nresidential home manager\npolicy manager\nsocial care team manager\ncare service manager\nsocial services team leader\nsocial services co-ordinator\nsocial and community services manager\nchildren''s social services manager\nsocial services delivery manager','\n',char(10)),'Social services managers have the responsibility for strategic and operational leadership and management of staff teams and resources within and or across social services. They are responsible for the implementation of legislation and policies relating to, for example, decisions about vulnerable people.  They promote social work and social care values and ethics, equality and diversity, and relevant codes guiding practice. They are responsible for liaising with other professionals in criminal justice, education and health. They can be responsible for contributing to local and national policy development.  ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c1a86d6d-7785-43e1-994b-958eba58fff7','key_17498','','','3213','3213.1','pharmacy assistant',replace('dispensing assistant\npharmacy assistant\npharmacy service assistant\nassistant in pharmacy\npharmacy dispensing assistant','\n',char(10)),'Pharmacy assistants perform general duties, such as stock management, serving at the cash desk, or performing administrative duties. They deal with the inventory within the pharmacy under the supervision of a pharmacist.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c1ee52e0-f30a-4f8d-a7f5-b07f5f06f88f','key_17499','','','1324','1324.3.1.6.9','dairy products and edible oils distribution manager',replace('dairy products and edible oils supply chain planner\ndairy products and edible oils distribution planner\nassistant dairy products and edible oils distribution manager\ngraduate dairy products and edible oils distribution manager\ntrainee dairy products and edible oils distribution manager\ndairy products and edible oils supply chain manager\nsenior dairy products and edible oils distribution manager\ndairy products and edible oils distribution manager\ndairy products and edible oils logistics manager\ndairy products and edible oils logistics planner','\n',char(10)),'Dairy products and edible oils distribution managers plan the distribution of dairy products and edible oils to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c22724a3-079c-4897-bd92-d99e068b6ffc','key_17500','','','8142','8142.12','pultrusion machine operator',replace('pultrusion machine operative\npultruded fibreglass product maker\npultruded fibreglass maker\npultrusion process worker\npultruded fibreglass product worker\npultrusion process operator\npultrusion process operative\nthermoplastic process worker\npultrusion machine operator\npultruded fibreglass worker\npultrusion machine worker\nreinforced fibreglass maker\npultrusion operator','\n',char(10)),'Pultrusion Machine Operators tend, control, and maintain machines which enable the production of composite materials with consistent cross-sections by adding reinforcement fibres such as fibreglass to the existing material and coating the resulting material with resin; this then is pulled through a heated dye where it becomes cured.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c227dcff-dd84-487e-a171-da3d919129bb','key_17501','','','3312','3312.7','student financial support coordinator',replace('financial aid coordinator\nstudent financial support services coordinator\nstudent finance officer\ncoordinator of student financial support services\nfinancial support coordinator\nstudent financial support coordinator\nfinancial support officer\ncoordinator of student financial support','\n',char(10)),'Student financial support coordinators assist students and education administrators in the management of tuition fees and student loans. They advise on and determine the amounts and the eligibility of student loans, advise students on available, suitable loans and liaise with outside loans sources, such as banks, to facilitate the student loan process. They make professional judgement decisions concerning students'' eligibility for financial aid and may set up counsel meetings including the student''s parents to discuss financial support issues and solutions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c25b3700-6095-42b7-99fc-b87dc326c044','key_17502','','','2132','2132.1','agricultural scientist',replace('agricultural biotechnology research scientist\nagricultural development consultant\nresearch scientist, agriculture\nresearcher in agriculture science\nagricultural research scientist\nagricultural scientist\nagriculture researcher\nagricultural quality scientist\nbio-technical research scientist in agriculture\nagricultural development specialist','\n',char(10)),'Agricultural scientists research and study soil, animals and plants with the objective of improving agricultural processes, the quality of agricultural products or the impact of agricultural processes on the environment. They plan and implement projects such as development projects on behalf of clients or institutions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c262bb96-03bb-4c97-a45b-0cf36db5b048','key_17503','','','2310','2310.1.5','assistant lecturer',replace('lector’s assistant\nadjunct assistant professor\nassistant lecturer at a college\nlecturer’s assistant\nuniversity assistant instructor\nuniversity assistant lecturer\njunior lecturer\nassistant professor\nhigher education assistant lecturer\nresearch assistant professor\nassistant teacher\nassistant lecturer\ncollege assistant lecturer\nresearch assistant lecturer\ncollege assistant instructor\nassistant lecturer in university\nassistant docent\nassistant lector\nassistant lecturer in higher education','\n',char(10)),'Assistant lecturers share university or college lecturers'' academic workload, specifically the provision of lectures to students. They prepare and teach classes, and meet with students privately regarding evaluation. They also combine their lecturing and other academic duties with conducting their own research in their field of study. Assistant lecturers occupy an autonomous, full-time position, despite what the subservience element in the occupation title may suggest.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c269387f-a652-4880-860a-e91ef105bfb7','key_17504','','','1341','1341.3','nursery school head teacher',replace('kindergarten director\nnursery school principal\nheadteacher of nursery school\npreschool headteacher\npreschool head teacher\nhead teacher of nursery school\nnursery headteacher\nnursery school headmaster\nnursery school director\nhead teacher of nursery\nkindergarten headmaster\nnursery school headteacher\nkindergarten principal','\n',char(10)),'Nursery school head teachers manage the day-to-day activities of a kindergarten or nursery school. They manage staff, make decisions concerning admissions and are responsible for meeting curriculum standards, which are age-appropriate for kindergarten students and facilitate social and behavioural development education. They also ensure the school meets the national education requirements set by law.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c28cb56b-003e-499a-8dbf-b654864e7867','key_17505','','','9629','9629.1','advertising installer',replace('sign poster\nbillboard poster helper\nadvertising installer\nbill board poster\nbill board installer\nbill poster\nadvertisment installer\npublicity campaign installer','\n',char(10)),'Advertising installers attach posters and other advertisement materials on buildings, buses and underground transport and in other public places such as shopping malls, in order to attract the attention of passersby. They use equipment to climb buildings and reach higher places, following health and safety regulations and procedures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c29f1a42-25cb-4192-9bc1-cc7560c00590','key_17506','','','5165','5165.1.4','truck driving instructor',replace('lorry driver training instructor\ndriving instructor\nHGV instructor\ntruck driving instructor\nLGV instructor\ncommercial vehicle driving instructor\nlorry driving training instructor\nCat C/C+E instructor\nlorry driver instructor\ntruck driving training instructor\ndriver training instructor\ntruck driver instructor\ndriver education instructor\nlorry driving instructor\ntruck driver training instructor\nCat C instructor','\n',char(10)),'Truck driving instructors teach people the theory and practice of how to operate a truck safely and according to regulations. They assist students in developing the skills needed to drive and prepare them for the driving theory tests and the practical driving test. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c2a29b8d-1042-4766-a162-e174c7a77421','key_17507','','','2230','2230.2','complementary therapist',replace('traditional therapist\ntraditional practitioner of natural health\nalternative therapist\nayurveda practitioner\ntherapist in complementary medicine\npractitioner in complementary therapy\nholistic therapist\npractitioner in complementary medicine\nnaturopathic practitioner\nsustainable health practitioner\nnatural medicine therapist\ncomplementary therapist\ncomplementary therapy practitioner\nnatural health practitioner\nalternative medicine therapist\nnaturopath\ncomplementary practitioner\nalternative medicine practitioner\ncomplementary medicine practitioner','\n',char(10)),'Complementary therapists use a range of complementary and alternative therapies to work simultaneously with the clients` body, mind, and spirit in a holistic approach. They offer practices and services for holistic wellbeing, health maintenance, health education, health promotion and prevention of illness, along with the sustainable treatment of certain illnesses, especially chronic illnesses. Their practices include but are not limited to acupuncture, aromatherapy, banotherapy, homeopathy, and herbal medicine.','',replace('Includes people performing Acupuncture, Alexander Technique, Aromatherapy, Art therapy, Autogenic training, Ayurveda, Bach Flower Remedies, Balneotherapy, Bioresonance, Bowen therapy Buyteko, Chi Gung, Craniosacral therapy, Cupping, Feldenkreis, Herbalism, Homeopathy, Hydrotherapy, Hypnotherapy, Iridology, Kinesiology, Macrobiotics, Massage, Music Therapy, Naturopathy, Nerro linguistic programming, Osteopathy, Phythotherapy, Pliates, Plarity therapy, Reflexology, Reiki, Rolfing, Sports massage, Shiatsu, T''ai Chi, Tibetan Medicine, TCM, Yoga. \n Includes doctors of medicine or other healthcare professionals (e.g. <a resource="http://data.europa.eu/esco/occupation/006cc1f9-2841-41c3-991a-dc3f2f3bd533" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/006cc1f9-2841-41c3-991a-dc3f2f3bd533"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">physiotherapist</span></a>) who perform complementary therapy.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c2d5daae-8f8a-45b1-8e76-3284b84ce195','key_17508','','','8121','8121.6','spring maker',replace('box spring maker\nspring making machine operative\nspring maker\nextension spring maker\nclock spring maker\ntorsion spring maker\nwire coiler spring maker setter\nwire coiler spring maker operator\ncompression spring maker\nspring making machine tender\nspring making machine operator\nwire coiler spring maker\nspringer\nspring assembling operator\nwire coiler spring maker operative\nwire coiler spring making operative\nwire coiler spring maker tender setter\ncoil spring maker\nspring assembler\nspring setting operator\nspring making machine setter\ntension spring maker\nleaf spring maker\nspring setter\ncoiler','\n',char(10)),'Spring makers operate a variety of equipment and machinery designed to manufacture different types of spring, including leaf, coil, torsion, clock, tension, and extension spring.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c2e6609d-0abe-4a38-b295-e06edbd52e82','key_17509','','','2432','2432.3','fundraising manager',replace('fundraiser\nfundraising coordinator\ncharity fundraiser\nnon-profit fundraiser\nfundraising manager\nphilanthropic manager\ngiving manager\nplanned giving manager\nfundraising consultant\nindividual giving manager\nfundraising specialist','\n',char(10)),replace('\nFundraising managers are responsible for raising money on behalf of organisations, often non-profit such as charities. Moreover, they manage the fundraised resources developing programs for its use. They perform a variety of tasks to raise money such as developing corporate partnerships, coordinating direct mail campaigns, organizing fundraisers, contacting donators or sponsors, and sourcing grant income from trusts, foundations and other statutory bodies.','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c2efadc4-6f47-4f34-997b-dd80aba609dd','key_17510','','','2355','2355.1','arts education officer',replace('art mediator\neducation officer\narts education officer\neducation coordinator\neducation and visitor services coordinator\nmuseum education officer\nart educator\nmuseum education coordinator\nmuseum visitor services officer\nmuseum visitor service coordinator\nvisitor services officer\nmuseum visitor service officer\nvisitor service coordinator\neducation and visitor service coordinator\nvisitor service officer\nmuseum visitor services coordinator\neducation and visitor services officer\nvisitor services coordinator','\n',char(10)),'Arts education officers deal with all the activities concerning the cultural venue and art facilities visitors, both current and prospective. They aim to deliver high quality and dynamic learning and participation programmes. Arts education officers develop, deliver and evaluate programmes and events for classes, groups or individuals, ensuring these events are a valuable learning resource for all ages.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c302791c-6864-4ec5-9a6c-2fca38764f0e','key_17511','','','3112','3112.1.6','fire inspector',replace('fire inspector\nfire prevention officer\nfire investigator','\n',char(10)),'Fire inspectors conduct inspections of buildings and properties to ensure they are compliant with fire prevention and safety regulations, and enforce the regulations in facilities which are not compliant. They also perform educational activities, educating the public on fire safety and prevention methods, policies, and disaster response.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c32155d3-9ac5-4e44-8e8b-25c47ad31c38','key_17512','','','8171','8171.2','digester operator',replace('digester worker\npulp mill machine technician\ndigester cook\npulp mill specialist\npulp mill machine specialist\npulp mill operator\ndigester operative\npulp mill machine operator\npulp mill technician\ndigester technician\ndigester specialist\ndigester operator','\n',char(10)),'Digester operators, or cooks, cook wood chips with soda ash or acid to separate the wood pulp from unwanted constituents. They test the resulting solution.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c332af81-4481-4494-9786-aea1f5c5ffcf','key_17513','','','3113','3113.1.1','calibration technician',replace('instrument calibration technician\ntechnician in instrument calibration\ntechnician in calibration of electrical and electronic equipment\npyrometry instrument calibration technician\ninstrumentation calibration technician\ninstrument and calibration technician\ncalibrator\nelectrical instrument calibration technologist\nelectronic  instrument calibration technician\nelectrical and electronic equipment calibrator\ncalibration technician\nelectrical and electronic equipment calibration technician\nelectrical instrument calibration technician\ncalibration technologist\ninstrument calibration technologist\ninstrument calibrator\ncalibrator of electrical and electronic equipment\ncalibrations technician','\n',char(10)),'Calibration technicians test and calibrate electrical and electronic equipment. They read blueprints and other technical drawings to develop testing procedures for every product.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c36fc197-1f5b-4563-acd8-6f1d06aa1c03','key_17514','','','2144','2144.1.10','marine engineer',replace('marine design engineer\nvessel engineer\nmarine power systems engineer\nmarine engineering adviser\nmarine engineering consultant\nmarine engineers\nmarine auxiliary systems engineer\nship''s engineer\nmarine systems engineer\nnaval engineer\nship engineer\nmarine engineer\nmarine repair engineer\nmarine engineering specialist\nvessel systems engineer\nmarine engineering expert','\n',char(10)),'Marine engineers design, build, maintain and repair the hull, mechanical, electronic equipment and auxiliary systems such as engines, pumps, heating, ventilation, generator sets. They work on all types of boats from pleasure crafts to naval vessels, including submarines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c3760388-d070-491a-9c1f-be305a576815','key_17515','','','4323','4323.15','ship pilot dispatcher',replace('dispatcher of ship pilots\nship pilot controller\nshipping pilot dispatcher\ndispatcher, ship pilot\nship pilot coordinator\nship pilot dispatcher','\n',char(10)),'Ship pilot dispatchers coordinate ships entering or leaving port. They write orders showing name of ship, berth, tugboat company, and time of arrival or departure, and notify the maritime pilot of assignment. They obtain receipts of pilotage from the pilot upon return from ship. Ship pilot dispatchers also record charges on receipt, using tariff book as guide, compile reports of activities, such as number of ships piloted and charges made, and keep records of ships entering port, showing owner, name of ship, displacement tonnage, agent, and country of registration.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c37d0026-989b-4705-9bcb-30cccd8308fa','key_17516','','','2265','2265.1.1','prepared meals nutritionist',replace('account manager adult nutrition\nprepared meals nutritionist\nnutrition scientist','\n',char(10)),'Prepared meals nutritionists value ingredients, manufacturing processes, and foodstuffs in order to assure nutritional quality and suitability of prepared meals and dishes. They study nutritional value of foodstuffs and dishes and advise on the composition of different meals or dishes for human feed, allergenic items and macro and micronutrients.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c3959398-a5d7-4b26-abcf-ef7af87e5822','key_17517','','','3131','3131.3.4','geothermal power plant operator',replace('geothermal plant operative\ngeo-thermal power plant operator\ngeothermal power plant operative\ngeo-thermal plant operative\ngeothermal power plant worker\ngeothermal power plant operator\ngeo-thermal plant worker\ngeothermal plant operator\ngeo-thermal plant operator\ngeothermal plant worker\ngeo-thermal power plant worker\ngeo-thermal power plant operative','\n',char(10)),'Geothermal power plant operators operate and maintain equipment, often steam-driven turbines, which produce electrical energy. They monitor measuring equipment to ensure the safety of operations, and that the production needs are met. They also react to system problems, and repair faults. They may regulate the generators to control the flow of electricity to the power lines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c3aae59e-1441-4b3e-bdd1-6da50dc7cf42','key_17518','','','3313','3313.2','bookkeeper',replace('bookkeeping clerk\naccounting bookkeeper\naccounts clerk\ngeneral bookkeeper\nbookkeeper\ngeneral-ledger bookkeeper\naccounting clerk\nbook-keeper\nbook-keeping clerk\naccount information clerk\naccounting technician','\n',char(10)),'Bookkeepers record and assemble the day-to-day financial transactions of an organisation or company, consisting usually of sales, purchases, payments and receipts. They ensure all financial transactions are documented in the appropriate (day) book and general ledger, and that they are balanced out. Bookkeepers prepare the recorded books and ledgers with financial transactions for an accountant to then analyse balance sheets and income statements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c3abb0cb-c78c-4136-b9f9-f9feca9179fa','key_17519','','','4323','4323.7','gas scheduling representative',replace('short range planner\ngas production planner\ngas scheduler\nshort range scheduler\ngas demand forecaster\nnatural gas scheduling representative\ngas production scheduling representative','\n',char(10)),'Gas scheduling representatives track and control the flow of natural gas between pipelines and the distribution system, compliant with schedules and demands. They report on the natural gas flow, ensure the schedule is followed or make scheduling adaptations in case of problems to attempt to meet demands.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c3b82711-273d-4fa8-8de7-0ac3d99c16fc','key_17520','','','2310','2310.1.11','computer science lecturer',replace('lecturer in computer sciences\nspecialist lecturer in computer science\ncomputer science docent\nsenior lecturer in computer science\nlecturer of computer science\ncomputer science lector\nuniversity lecturer in computer science\ncomputer science teacher\nhigher education computer science teacher\ncomputer sciences lector\nlecturer in computer science\nuniversity computer science teacher\ncomputer science professor\nuniversity computer science lecturer\ncomputer science lecturer\ncomputer science instructor','\n',char(10)),'Computer science lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, computer science, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, grading papers and exams and leading review and feedback sessions for the students. They also conduct academic research in their field of computer science, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c3c8c56c-b3ab-4425-a38a-abcace020d22','key_17521','','','2634','2634.2.1','clinical psychologist',replace('practitioner of clinical psychology\nclinical psychologist\nexpert in clinical psychology\nspecialist clinical psychologist\nhospital psychologist\npsychologist in hospital\nclinical psychology practitioner\nexpert clinical psychologist\nhospital psychology practitioner','\n',char(10)),'Clinical psychologists diagnose, rehabilitate, and support individuals affected by mental, emotional, and behavioural disorders and problems as well as mental changes and pathogenic conditions through use of cognitive tools and appropriate intervention. They use clinical psychological resources on the basis of psychological science, its findings, theories, methods, and techniques for the investigation, interpretation, and prediction of human experience and behaviour.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c3cae318-8b15-4339-a617-1a66b9ff6609','key_17522','','','8211','8211.5.2','motor vehicle engine assembler',replace('truck engine assembler\ngas  engine builder\nbus engine assembler\ncar engine builder\nbus engine builder\nelectric engine builder\npetrol engine builder\nhybrid engine assembler\nlorry engine builder\nmotor vehicle engine assembler\nhybrid engine builder\npetrol engine assembler\ndiesel engine assembler\ndiesel engine builder\ngas  engine assembler\nelectric engine assembler\ncar engine assembler\ncoach engine builder\ntruck engine builder\nlorry engine assembler\ncoach engine assembler','\n',char(10)),'Motor vehicle engine assemblers build and install prefabricated parts to form motor vehicle engines such as diesel, gas, petrol and electric engines. They review specifications and technical drawings to determine materials and assembly instructions. They inspect and test the engines and reject malfunctioning components.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c3dc576e-2a58-4481-9b6f-9853067af8ea','key_17523','','','3323','3323.5','timber trader',replace('timber merchant\nlumber trader\ntimber seller\nwood merchant\nlumber merchant\ntrader of timber','\n',char(10)),'Timber traders assess the quality, quantity and market value of timber and timber products for trade. They organise the selling process of new timber and purchase stocks of timber.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c3fc5265-6333-483f-9339-b4b860efa08d','key_17524','','','3359','3359.3','fisheries observer',replace('fishing area observer\nfisheries observer\nfishing activity observer\nfisheries control reporter\nfishery observer','\n',char(10)),'Fisheries observers record and report on fishing activity and the level of compliance and enforcement of conservation measures in force in the work area by controlling the position of the vessel, the use of the fishing gear and the catch. They provide the reports required for the scientific monitoring of the fishing area, provide policy advice and plan and coordinate activities. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c4046ba8-cfe0-4748-beac-22e865962d75','key_17525','','','3122','3122.4.9','malt house supervisor',replace('malt house coordinator\nmalting supervisor\nmalt house supervisor\nmalt supervisor\nmalt house manager','\n',char(10)),'Malt house supervisors oversee the malting processes in its integrity. They supervise the processes of steeping, germination, and kilning. They monitor each one of the processing parameters aiming to meet customer specifications. They provide assistance and leadership to malt house production employees and ensure that they operate in a safe and professional manner.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c4071145-a774-4cf4-aefa-ae18a385cca4','key_17526','','','2149','2149.2.6','logistics engineer',replace('logistics engineering consultant\nlogistics engineer\nlogistics technology engineer\nlogistics technology engineering expert\nlogistics engineering adviser\nlogistics technology engineering consultant\nlogistics technology engineering specialist\nlogistics engineering specialist\nlogistics technology engineering adviser\nlogistic engineer\nlogistics engineering expert','\n',char(10)),'Logistics engineers design and implement systems aimed to optimise the time and efforts invested in transportation activities. They ensure that systems allow for micro-tracking and smooth communication across transport methods and stages.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c40a2919-48a9-40ea-b506-1f34f693496d','key_17527','','','2513','2513.5','web developer',replace('web developers\nweb engineer\nweb application developer\nweb programmer\nweb developer\nweb specialist','\n',char(10)),'Web developers develop, implement and document web-accessible software based on the designs provided. They align the client''s web presence with its business strategy, troubleshoot software problems and issues and look for ways to improve the application.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c40ef517-d4ab-4932-b184-9c2ea5b24fe2','key_17528','','','2132','2132.2','agronomist',replace('crop scientist\ncrop research scientist\nagronomy analyst\nagronomy researcher\ncrop researcher\ncrop adviser\nadviser for agricultural production\ncrop research analyst\ncrop production adviser\nplant growing advisor\ncrop science researcher\nagronomy adviser\nagronomy research scientist\nagronomy scientist\nagronomy consultant\nagronomy science researcher\nplant growing adviser\ncrop analyst\norganic farming advisor\ncrop consultant\nagronomy research analyst\nhorticultural adviser\nadviser for vegetable production\nagronomist','\n',char(10)),'Agronomists provide consulting services to companies, agricultural cooperatives, agronomical crop growers and horticultural crop growers on the cultivation of food crops. They study the science, technology and business related to growing plants. They examine crops and perform experiments in order to improve crop yields and farms'' production. Agronomists also examine the most effective ways to harvest and cultivate plants.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c418ed9a-132f-48cd-ae5c-dd375070ee0e','key_17529','','','2422','2422.18','trade development officer',replace('trade development officer\ninternational trade development officer\nsenior trade development officer\ntrade development director\nimport & export director\nimport and export officer\nimport and export director','\n',char(10)),'Trade development officers develop and implement trade policies both internally and in international import and export relations. They analyse the domestic and foreign markets in order to promote and establish business operations, and ensure trade proceedings are compliant with legislation and businesses are protected from distortions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c430500d-0b8b-4ef5-8f91-3e98cff32356','key_17530','','','1324','1324.3.2.32','import export manager in tobacco products',replace('graduate import manager in tobacco products\nsenior import manager in tobacco products\nimport export manager in tobacco products\ncustoms compliance manager in tobacco products\nassistant import export manager in tobacco products\ninternational trade compliance manager in tobacco products\nsenior export manager in tobacco products\ninternational trade manager in tobacco products\ntrainee import export manager in tobacco products\ntrainee export manager in tobacco products\nassistant import manager in tobacco products\ntrainee import manager in tobacco products\ngraduate export manager in tobacco products\nassistant export manager in tobacco products\nsenior import export manager in tobacco products\ngraduate import export manager in tobacco products','\n',char(10)),'Import export managers in tobacco products install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c44a118d-18c1-46ac-985f-3e802a064b73','key_17531','','','8212','8212.2.1','control panel assembler',replace('electrical control panel assembler\nmonitoring panel assembler\nelectrical control panel technician\ncontrol panel assembly technician\nfabricator of electrical control panels\ncontrol panel fabricator\nassembler of electrical control panels\ncontrol panel technician\ncontrol panel production assembler\npanel wirer\npanel board assembler\nprocess control panel assembler\ncontrol panel assembler\nfabricator of control panels\nassembler of control panels','\n',char(10)),'Control panel assemblers read schematic drawings to assemble control panel units for electrical equipment. They put together wiring, switches, control and measuring apparatus and cables with hand operated tools.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c44c078d-85e4-40a3-9598-2c333d7a24ca','key_17532','','','2145','2145.1.8','paper engineer',replace('paper engineer\npaper mill process engineer\nprocess engineer\npulp paper engineer\npulp and paper engineer\nmechanical engineer\nmaterials engineer\npaper machine superintendant','\n',char(10)),'Paper engineers ensure an optimal production process in the manufacture of paper and related products. They select primary and secondary raw materials and check their quality. In addition, they optimize machinery and equipment usage as well as the chemical additives for paper making.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c44c37a2-88c3-4ac2-9cf9-959f42deb02b','key_17533','','','2149','2149.9.5','offshore renewable energy engineer',replace('marine renewable energy engineer\nocean renewable energy engineer\nhydrodynamics engineer\nORE engineer','\n',char(10)),'Offshore renewable energy engineers design and supervise the installation of offshore energy farms and equipment. They research and test locations to find the most productive location, ensure the successful execution of the design plan and make any necessary modifications or provide targeted advice. Offshore renewable energy engineers test equipment such as wind-turbine blades, tidal stream and wave generators. They develop strategies for more efficient energy production, and environmental sustainability.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c44f9f8c-46a6-40da-9630-cc568a18cc51','key_17534','','','7223','7223.14','ornamental metal worker',replace('welder fabricator installer\nstructural and ornamental metalwork steel fabricator\nStructural and ornamental metalwork copper fabricator\nornamental metal worker\nornamental metalwork metal fabricator\nprecision structural steel fitter\nstructural and ornamental metalwork metal fabricator\nwelder fabricator\nprecision structural metal fitter','\n',char(10)),'Ornamental metal workers use finishing equipment and machinery to shape and finish fabricated ornamental metal workpieces, often used for the installation process in construction, such as railings, staircases, open steel flooring, fences and gates, and others.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c453886f-a8f0-434c-be0e-e6a12c49954f','key_17535','','','8114','8114.7','stone polisher',replace('hand grinder\nsurface polisher\nworktop polisher\nmarble polisher\nhand stone polisher\nwet stone polisher\ngranite polisher\nstone polishing worker\nstone polishing machine operative\nstone polisher','\n',char(10)),'Stone polishers operate grinding and polishing tools and equipment in order to smooth stones.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c467efc2-6b87-429d-b0be-44512cf9535d','key_17536','','','7115','7115.3','fireplace installer',replace('fireplace installation technician\nfireplace technician\ngas fireplace servicer\nfireplace builder\nchimney technician\nfireplace installer helper','\n',char(10)),'Fireplace installers install wood, gas and electric fireplaces in homes, according to the instructions from the manufacturer and in compliance with health and safety requirements. They take the necessary measurements, prepare the equipment and materials for the installation and install fireplaces safely. Fireplace installers perform maintenance and repairs on systems when needed. They are the primary contact point for their customers, provide information on how to operate the product and liaise with the manufacturer in case of issues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c4a81f47-6ed8-4a17-ab4a-21a2e67fa1d9','key_17537','','','5311','5311.1.2','babysitter',replace('baby-sitter\nbabysitter\nchildminder','\n',char(10)),'Babysitters provide short-term care services to children on the premises of the employer, depending on the employer''s needs. They organise play activities and entertain children with games and other cultural and educative activities according to their respective age, prepare meals, give them bathes, transport them from and to school and assist them with homework on a punctual basis.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c4d6d07a-ae90-40ce-bc5a-c952805a9bf3','key_17538','','','2266','2266.1','audiologist',replace('paediatric audiologist\naudiology scientist\naudiology expert\naudiological scientist\nspecialist in audiology\ntechnical audiologist\nexpert in audiology\naudiologist\naudiology specialist\nhearing aid audiologist\nsenior audiologist','\n',char(10)),replace('Audiologists assess, diagnose and treat patients ( children or adults) with audiological and vestibular disorders caused by infectious, genetic, traumatic or degenerative conditions, such as hearing loss, tinnitus, dizziness, imbalance, hyperacusis and auditory processing difficulties. They can prescribe a hearing aid and have a role in assessing and managing patients who may benefit from cochlear implants.  \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c52129d9-4656-4ce8-ba13-8a72a51d5400','key_17539','','','3118','3118.3.12','product development engineering drafter',replace('product development engineering draughter\nproduct development engineering draftsperson\nproduct development engineering draughtsperson\nproduct development engineering drafting specialist\nproduct development engineering drafting adviser\nproduct development engineering drafter\nproduct development engineering drafting consultant\nproduct development engineering drafting expert','\n',char(10)),'Product development engineering drafters design and draw blueprints to bring new concepts and products to life. They draft and draw detailed plans on how to manufacture a product.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c530b0ee-46f8-40fb-818c-d9b44b891cb9','key_17540','','','1221','1221.2','banking products manager',replace('electronic banking products manager\nsavings products manager\ncommercial banking products director\nmortgage products manager\nfinancial institution products manager\nbanking product manager\ninternet banking manager\ncredit cards product manager\ncredit products manager\nbank products supervisor\ninternet banking executive\ne-banking products manager\ne-banking product executive\nbanking products director\nsenior banking products manager\nbanking products manager\ncommercial banking products manager\nbanking products analyst','\n',char(10)),'Banking products managers study the market of banking products and adapt the existing ones to the characteristics of this evolution or create new products to suit clients needs. They monitor and evaluate the performance indicators of these products and suggest improvements. Banking products managers assist with the sales and marketing strategy of the bank.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c5328083-eda0-4ca8-b9cb-99a46e73a9bc','key_17541','','','2131','2131.4.5','botanicals specialist',replace('botanical milling operative\nbotanicals specialist\nbotanical specialist\nbotanical expert\nbotanical science expert','\n',char(10)),'Botanicals specialists bring a deep knowledge in the science of plants and botanicals to the production of alcoholic beverages based on herbs. These professionals combine knowledge of flavour chemistry, biological sciences and technology process. They may operate botanical milling machines ensuring that the crushing process retains as much flavour and aroma as possible.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c549f0de-c415-428e-865c-973bcd224b39','key_17542','','','2310','2310.1.23','linguistics lecturer',replace('applied linguistics lecturer\nlinguistics professor\nuniversity linguistics lecturer\nlinguistics docent\nuniversity lecturer in linguistics\nlecturer in linguistics\nhigher education linguistics teacher\nlinguistics lecturer\nprofessor of linguistics\nlecturer of linguistics\nuniversity linguistics teacher\nprofessor of applied linguistics\nlinguistics lector\nlinguistics teacher\nlecturer in applied linguistics\nlinguistics instructor','\n',char(10)),replace('Linguistics lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, linguistics, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants \nfor the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of linguistics, publish their findings and liaise with other university colleagues.','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c56295bb-ef33-4ccd-a929-59e3dd73f876','key_17543','','','6112','6112.4','vineyard cellar master',replace('cellar master of a vineyard\nwinemaker\nvintner\nwinery supervisor\nvineyard cellar master','\n',char(10)),replace('Vineyard cellar masters are responsible for vineyard cellars from the entry of grapes to on-site bottling and distribution. They ensure quality at all stages, in compliance with regulations and laws. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c56bb750-db0b-487b-89f4-82dee6dcab09','key_17544','','','3259','3259.4','covid tester ',replace('mobile screening nurse\ncoronavirus tester\ncovid testing clinician\ncovid community testing facilitator\ntesting support assistant \ncovid testing specialist\ntesting supervisor','\n',char(10)),'Covid testers perform Covid tests through nasal or throat swabs. They complement the sample information with health-related questions they ask the individuals, and input the data collected into digital devices. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c5902c47-0c2e-45aa-94dd-951ad641449c','key_17545','','','7211','7211.1.2','foundry moulder',replace('sand moulder\ncore maker\nfoundry worker\ncoremaker\nfoundry sand moulder\nfoundry coremaker\nfoundry core maker\nfoundry molder','\n',char(10)),'Foundry moulders manufacture cores for metal moulds, which are used to fill a space in the mould that must remain unfilled during casting. They use wood, plastic or other materials to create the core, selected to withstand the extreme environment of a metal mould.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c593ded7-2e97-44a5-a5f3-f6115ff98233','key_17546','','','2310','2310.1.34','politics lecturer',replace('political science lecturer\npolitical science professor\nlecturer in politics\nhigher education politics teacher\npolitical studies lecturer\npolitical studies docent\npolitics and international studies lecturer\npolitics professor\nuniversity political studies lecturer\npolitical sciences lector\npolitical science lector\npolitical studies instructor\nprofessor of politics\npolitics and international relations lecturer\npolitical science teacher\npolitical science docent\nprofessor of political science\nteacher\nuniversity lecturer in political studies\npolitical science instructor\nlecturer in politics and international studies\npolitical studies professor\nlecturer in politics and international relations\npolitics lecturer\nprofessor of political studies','\n',char(10)),replace('Politics lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, politics, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants \nfor the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of political studies, publish their findings and liaise with other university colleagues.','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c5b45ebe-0e9f-4bc4-9563-7fdf31115e53','key_17547','','','7543','7543.7.6','rolling stock assembly inspector',replace('carriage assembly quality inspector\nrail vehicle quality compliance supervisor\ntraction assembly quality inspector\nwagon quality compliance supervisor\nmultiple unit assembly quality inspector\nlocomotive assembly quality inspector\nmultiple unit quality compliance supervisor\nrolling stock assembly quality inspector\ntraction quality compliance supervisor\nrailcar  assembly quality inspector\nlocomotive quality compliance supervisor\nwagon assembly quality inspector\nrailway vehicle assembly quality inspector\nrolling stock quality compliance inspector\ncarriage quality compliance supervisor\nrailway vehicle quality compliance supervisor\nrail vehicle assembly quality inspector\nrolling stock assembly inspector\nrailcar  quality compliance supervisor','\n',char(10)),'Rolling stock assembly inspectors use measuring and testing equipment to inspect and monitor rolling stock assemblies to ensure conformity to engineering specifications and to safety standards and regulations. They examine the assemblies to detect malfunction and damage and check repair work. They also provide detailed inspection documentation and recommend action where problems were discovered.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c5c1c40f-d2b7-437f-b3e8-b9da48f6d728','key_17548','','','4312','4312.6','property assistant',replace('property assistant\nreal estate clerk\nreal estate agent assistant\nviewing and sales assistant\nproperty clerk\nreal estate assistant','\n',char(10)),'Property assistants perform several duties including administrative tasks in the real estate sector. They provide clients with financial information about properties and advise them, they schedule appointments and organise property viewings, they prepare contracts and assist in property valuation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c5d36eef-df20-47a8-a332-5b4358fa83e3','key_17549','','','2153','2153.1.1','telecommunications analyst',replace('telecommunications consultant\ntelecommunications analyst\ntelecom analyst\ntelecoms analysts\ncommunication analyst\ncommunications analyst\ntelecommunications analysts\nnetwork planner','\n',char(10)),'Telecommunications analysts review, analyse and evaluate an organisation''s telecommunications needs and systems. They provide training on the telecommunications system features and functionalities.','','Excludes people performing telecommunication design activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c5d779f4-345b-4918-872b-a1cbaeb1d9be','key_17550','','','8332','8332.4','dangerous goods driver',replace('dangerous goods transport specialist\ndangerous goods driving specialist\ndangerous goods driver\nexplosives truck driver\nADR driver\nhazardous materials driver\ndangerous goods trucker\ndangerous goods deliverer\ndriver of dangerous goods','\n',char(10)),'Dangerous goods drivers transport fuel and bulk liquid, hazardous products and chemicals by road.','','Excludes people working in an intermediate bulk container.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c60ede09-b67b-4173-8991-b9b694149488','key_17551','','','2635','2635.3.11','employment support worker',replace('employability support worker\nsupported employment worker\nemployment support worker\nunemployment social worker\nsocial worker (community development)\njob placement social worker\nemployment support social worker\nemployment rights worker\nemployment social worker','\n',char(10)),'Employment support workers provide assistance to people with difficulties to find a job and long-term unemployed people. They provide guidance in the creation CVs, searching for job openings, contacting employers, and the preparation for job interviews.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c6196b21-20f9-4608-83fb-6f143afc740d','key_17552','','','2114','2114.1','geologist',replace('paedologist\ngeology studies research analyst\ngeology analyst\ngeology research analyst\ngeologist prospector\ngeology scientist\npetroleum geologist\ngeology studies scientist\nsite geologist\ngeology science researcher\nglaciologist\nstructural geologist\ngeology researcher\ngeology studies analyst\ngeology studies research scientist\ngeology research scientist\nchief geologist\nbedrock geologist\ngeoscience consultant\ngeology studies researcher\ngeology scholar\ngeothermal scientist\nvolcanologist\ngeologist engineer\nconsulting geologist\nfield geologist\ncoal geologist\ngeologist\nsedimentologist\ngeology studies scholar\nquaternary geologist','\n',char(10)),'Geologists research the materials that form the earth. Their observations depend on the purpose of the research. Depending on their specialisation, geologists study how the Earth has been shaped over time, its geological layers, the quality of minerals for mining purposes, earthquakes and volcanic activity for private services, and similar phenomena.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c6239b9e-5973-4b0a-a2ed-4c4b41331e04','key_17553','','','3412','3412.4.1','adult community care worker',replace('independent living advisor\ncommunity carer worker\ncommunity carer\nadult community care worker','\n',char(10)),'Adult community care workers perform assessment and care management of communities of adults who live with physical impairments or convalescing states. They aim to improve their life in the community and enable them to live safely and independently at their own home.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c644e6f6-dffc-4d7a-bf36-a384e465937b','key_17554','','','7312','7312.3','keyboard musical instrument maker',replace('keyboard musical instrument assembler\nkeyboard musical instrument maker\nkeyboard musical instrument constructor\nkeyboard musical instrument craftswoman\nkeyboard musical instrument production worker\nkeyboard musical instrument craftsman\nkeyboard musical instrument builder','\n',char(10)),'Keyboard musical instrument makers create and assemble parts to create keyboard instruments according to specified instructions or diagrams. They sand wood, tune, test and inspect the finished instrument.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c64a6e4e-5b38-4f93-b26d-aded817aeaf3','key_17555','','','1120','1120.4','chief operating officer',replace('company deputy chief executive officer\nchief operations officer\nchief operating officer\nchief administrative officer\ndeputy chief executive officer\nexecutive vice-president\nacting general manager\nmanaging director','\n',char(10)),'Chief operating officers are the right hand and second in command of a company''s chief executive officer. They ensure that the daily operations of the company run smoothly. Chief operating officers also develop company policies, rules and goals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c66b4569-09aa-4d8d-93d9-ac9b9176655d','key_17556','','','5414','5414.1.8','security consultant',replace('security chief\nsecurity specialist\nsecurity advisor\nsecurity expert\nsecurity supervisor\nsecurity consultant\nsecurity manager','\n',char(10)),'Security consultants provide security services to clients and individuals that help them preventing, planning and mitigating threats such as terrorism, espionage, theft, and violence to their organisation, including the buildings, operations, and employees. The services they provide depend on their clients'' security needs and requirements and may include services such as threat analysis, security risk management, security programme development.','','Excludes  <a resource="http://data.europa.eu/esco/occupation/0ce5a9f4-e00a-4bbe-b255-3c63407167a4" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/0ce5a9f4-e00a-4bbe-b255-3c63407167a4"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">ICT security consultant</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c6a26e11-bdf1-47a1-849d-fdcf819d458d','key_17557','','','0110','0110.2','armed forces officer',replace('major\nlieutenant general\nRoal Air Force officer\nsecond lieutenant\ngeneral\nlieutenant\nmilitary officer\nair force officer\nRoyal Navy officer\nmajor general\nRAF officer\narmed forces officer\ncaptain\nofficer cadet\ncolonel\nnaval officer\narmy officer\nRoyal Marines officer\nnavy officer\nlieutenant colonel\nbrigadier','\n',char(10)),'Armed forces officers supervise operations and manoeuvres, assign duties, and command subordinate staff. They ensure efficient communication within and between units and perform training duties. They also operate equipment and supervise equipment maintenance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c6bd511a-d966-4df9-a48e-4f800354f268','key_17558','','','1321','1321.2.3','operations manager',replace('operations general manager\noperations supervisor\noperations administrator\nbusiness operations manager\noperations manager\ndivision operations manager\noperations director','\n',char(10)),'Operations managers plan, oversee and coordinate the daily operations of production of goods and provision of services. They also formulate and implement company policies and plan the use of human resources and materials.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c6c48286-2037-47e3-a132-b745dce858e2','key_17559','','','2619','2619.2','coroner',replace('chief coroner\nforensic medical examiner\npathologist\nmedical examiner\nchief medical examiner\ncoroner','\n',char(10)),'Coroners oversee the examination of deceased individuals in order to determine the cause of death in uncommon circumstances. They ensure records are maintained of the deaths within their jurisdiction, and facilitate communication with other officials to ensure the investigation is complete.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c6e5e0e7-fa99-4f71-9fc1-9889d85282a5','key_17560','','','0110','0110.4','army general',replace('lieutenant general\nmilitary general\nmajor general\ngeneral\ngeneral military\narmy general\narmed forces general','\n',char(10)),'Army generals command large divisions of the army. They perform management duties, administrative duties, and planning and strategic duties. They develop policies for the improvement of the military and general defence, and ensure the nation''s safety.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c706886d-5bc7-4430-9a72-051fc483235f','key_17561','','','8143','8143.1','absorbent pad machine operator',replace('diaper machine operator\nabsorbent pad machine operative\nabsorbent pad machine technician\nabsorbent pad machine operator\ntampon machine specialist\ntampon machine technician\nabsorbent pad machine specialist\ntampon machine operator\ndiaper machine technician\nabsorbent pad machine worker\ndiaper machine specialist','\n',char(10)),'Absorbent pad machine operators tend a machine that takes in cellulose fibres and compresses them to a highly absorbent pad material for use in hygienic products such as diapers and tampons.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c71ee6a5-ce48-4afd-aa1e-eaabf17ccbc0','key_17562','','','1324','1324.3.2.33','import export manager in waste and scrap',replace('trainee import export manager in waste and scrap\ngraduate export manager in waste and scrap\ngraduate import manager in waste and scrap\nassistant import manager in waste and scrap\ntrainee import manager in waste and scrap\ngraduate import export manager in waste and scrap\nassistant import export manager in waste and scrap\nsenior export manager in waste and scrap\nimport export manager in waste and scrap\ninternational trade manager in waste and scrap\nsenior import export manager in waste and scrap\ncustoms compliance manager in waste and scrap\ninternational trade compliance manager in waste and scrap\nsenior import manager in waste and scrap\ntrainee export manager in waste and scrap\nassistant export manager in waste and scrap','\n',char(10)),'Import export managers in waste and scrap install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c737f094-7921-4d57-b883-6442343a5fd8','key_17563','','','2653','2653.1.1','choreologist',replace('benesh notator\nchoreologist\nbenesh movement notator\nbenesh choreologist\nbenesh dance notator\ndance notator','\n',char(10)),'Choreologists are specialised creators of dance in specific styles or traditions, such as ethnic dance, early dance or baroque dance. Their work is contextualised historically and sociologically as an expression of the human group that developed it. Choreologists analyse dance from intrinsic aspects: theory, practice and epistemology of movements in itself. They also study dance from the extrinsic perspective: the social, ethnological, etnographical and sociological context in which dance is developed.','','Excludes choreographer.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c76566f1-97a1-43e9-a7d8-155db0e56987','key_17564','','','8131','8131.2.6','varnish maker',replace('varnish production process operative\nvarnish blender\nmaker of varnish\nvarnish maker\nvarnish & paint maker\nvarnish and paint maker\nvarnish production worker\nkettle charger\nvarnish production process worker\nkettle operator\nvarnish melter\nvarnish cooker','\n',char(10)),'Varnish makers operate equipment and mixers for varnish production, by melting, mixing and cooking the required chemical ingredients, according to specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c766198e-5b9c-4c85-9fef-be27c783ab19','key_17565','','','3352','3352.2','tax inspector',replace('pecunary consultant\ntax specialist\ncontributions advisor\npublic funds inspector\nexcise officer\ninspector of taxes\ntax inspector\ngovernment tax inspector\nfiscal advisor\ntaxation lawyer','\n',char(10)),'Tax inspectors are responsible for the calculation of taxation and the ensurance of its timely payment by individuals and organisations. They provide information and guidance concerning taxation legislation and examine financial documents and accounts to ensure compliance with legislation. They also examine records to investigate fraud.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c76dda38-938c-404e-8a6d-78c8705eebc0','key_17566','','','5222','5222.1.1','checkout supervisor',replace('checkout manager\ncheckout services manager\ncheckout supervisor\ncheck out team leader m,f,n)\ntill team manager\ntill supervisor\ncheckout clerk\ncheck out clerk\ntill team captain\ntill team leader\ncustomer service manager\ncheck out manager\ncheckout team leader\ncheck out supervisor\ncounter manager\ncheck out services manager','\n',char(10)),'Checkout supervisors oversee cashiers in department stores and other big stores.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c784c2db-e636-429f-8492-3191790a58d7','key_17567','','','3423','3423.2','fitness instructor',replace('strength and conditioning coach\nindoor cycling trainer\ngroup fitness instructor\nwater aerobics instructor\naerobics instructor\nindoor cycling instructor\nspinning instructor\nfitness instructor\nswimming instructor\naqua fitness instructor\nfitness specialist\naqua aerobics instructor\nfitness class instructor\ngymnastics instructor\ndance fitness instructor\nfitness group instructor','\n',char(10)),'Fitness instructors build fitness participation of new and existing members through fitness experiences that meet their needs. They deliver fitness instruction to individuals, with the use of equipment, or to a group, through fitness classes. Both individual and group instructors have the purpose of promoting and delivering safe and effective exercise.  Depending on the specific situation, some additional knowledge, skills and competences may be required.','','Includes group fitness instructor.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c7ad889f-10fc-4346-b740-bd479ad82b47','key_17568','','','3253','3253.1','community health worker',replace('community healthcare worker\ncommunity carer\ncommunity support worker\ncommunity healthcare support worker\ncommunity first responder\ncommunity healthcare assistant\ncommunity health worker\ncommunity health assistant','\n',char(10)),'Community health workers provide advice and information about various health subjects to the community. They can assist with pre- and post-natal care, give nutritional advice and help individuals stop smoking. Community health workers develop health and prevention programmes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c7b31477-88e3-400f-988f-3baa090de173','key_17569','','','7312','7312.3.2','organ builder',replace('organ maker\npipe organ designer\npipe organ maker\norgan designer\norgan builder\npipe organ builder\norgan constructor\npipe organ constructor','\n',char(10)),'Organ builders create and assemble parts to build organs according to specified instructions or diagrams. They sand wood, tune, test and inspect the finished instrument.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c7c3f05b-319b-4aa7-891e-78106f875de2','key_17570','','','8131','8131.20','soap tower operator',replace('tower operator\ntower operator (soap)\nsoap plant worker\nsoap plant operative\nsoap tower operator\nsoap powder production operative\nsoap powder production worker\ntower operative (soap)','\n',char(10)),'Soap tower operators control, monitor and maintain tower operations, using the control panel, in order to produce soap powders. They inspect operating units to ensure the parameters of flow of oil, air, perfume or steam are according to specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c7ee21d5-9a13-4cf8-a3b9-292eefd37f7a','key_17571','','','3122','3122.4.1','animal feed supervisor',replace('animal feed production manager\nanimal feed production processor\nfeed mill supervisor\nanimal feed supervisor\nlivestock feed supervisor\nfeed manufacturer\nanimal feed quality controller\nfodder production supervisor','\n',char(10)),'Animal feed supervisors supervise the manufacturing process of animal feeds. They control quality, take samples for labs, follow up on lab results and take measures according to the results.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c7f5cab7-7cfb-47d7-890c-6b9c00a9eaaf','key_17572','','','3112','3112.1.5','energy assessor',replace('energy performance certificate assessor\nenergy savings advisor\nenergy consumption advisor\nhousehold energy use assessor\nenergy assessor\nenergy performance assessor\nEPC certification assessor','\n',char(10)),'Energy assessors determine the energy performance of buildings. They create an Energy Performance Certificate (EPC) which indicates what the estimated energy consumption of a property is. Furthermore they give advice on how to improve energy conservation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c7ffc270-7bd2-4bb1-a51a-4766170d7935','key_17573','','','3324','3324.4.4','wholesale merchant in chemical products',replace('wholesale merchandiser in chemical products\ngraduate wholesale merchant in chemical products\nsenior wholesale merchant in chemical products\nwholesaler in chemical products\nwholesale trader in chemical products\nwholesale buyer in chemical products\nwholesale merchant in chemical products\nB2B sales consultant in chemical products\nbusiness to business sales consultant in chemical products\nassistant wholesale merchant in chemical products\ntrainee wholesale merchant in chemical products\nbusiness to business seller in chemical products','\n',char(10)),'Wholesale merchants in chemical products investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','Includes people performing the wholesale of solid, liquid and gaseous fuels and related products.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c81af273-1112-4b3f-a2a5-b85b1290ae22','key_17574','','','7212','7212.3.2','laser beam welder',replace('laser operations\nlaser welder\nlaser tube welder apprentice\nlaser tube welder\nmachine-welder\nlaser beam welding machine operator\nwelder\nlaser beam machine operator\nrepair fitter\nlaser welding operator\nlaser repair welder\nlaser repair technician\nLBW machine operator\nmachine welder, lasers\nmaintenance technician\nlaser beam welder\nlaser beam machine technician','\n',char(10)),'Laser beam welders set up and tend laser beam welding machines designed to join separate metal workpieces together through the use of a laser beam radiating a concentrated heat source that allows for precise welding.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c822ca35-6c36-4eab-97fa-90f2775091c3','key_17575','','','5211','5211.1','market vendor',replace('barrow boy\ncostermonger\nmarket trader\noutdoor market vendor\nstreet market vendor\ncovered market vendor\nstreet market seller\nfish market vendor\nflower market vendor\nmarket vendor\nfruit market vendor\nstreet market trader\nmarket seller\nvegetable market vendor\nindoor market vendor','\n',char(10)),'Market vendors sell products such as fruit, vegetables and household products on organised outdoor or indoor market places. They use sales techniques to recommend their goods to passersby.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c84a055d-00fe-4789-a054-f4a62914ff6e','key_17576','','','9333','9333.4','mover',replace('removal man\nremoval woman\nindustrial mover\nmover\ncompany mover\ncorporate mover\nremoval porter\nfurniture mover\nremoval driver and porter\nrelocation mover','\n',char(10)),'Movers are responsible for the physical handling of goods and belongings to be relocated or transported from one place to the other. They disassemble goods, machinery or belongings for transporting and assemble or install them in the new location. They ensure that objects are well protected and packed, secured and placed correctly in trucks and transports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c8505fa5-0a33-4143-bd87-f24f569e8813','key_17577','','','4419','4419.2','foreign language correspondence clerk',replace('foreign language correspondence officer\nforeign language correspondence assistant\nforeign language correspondence clerk\nforeign correspondent assistant\nforeign language correspondence secretary\nforeign language correspondent clerk\nforeign correspondent secretary\nforeign correspondence clerk','\n',char(10)),'Foreign language correspondence clerks read and reply to a company''s correspondence in foreign languages. They also perform clerical duties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c85e39a4-2c40-413f-a171-45907ecdd599','key_17578','','','3339','3339.7','talent agent',replace('commercial agent\nmodelling agent\ntalent booking agent\ntalent agent\ntheatrical agent\ntalent agency\nvoice talent agent\nmusic agent\nsport agent\nliterary agent','\n',char(10)),'Talent agents represent actors, authors, broadcast journalists, film directors, musicians, models, professional athletes, screenwriters, writers, and other professionals in various entertainment or broadcast businesses. They promote their clients in order to attract prospective employers. Talent agents set up public appearances, auditions and performances. They take care of contract negotiations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c871b431-2bef-4824-a504-50b60cf633f7','key_17579','','','3324','3324.4.32','wholesale merchant in tobacco products',replace('wholesale buyer in tobacco products\nwholesale merchant in tobacco products\nbusiness to business seller in tobacco products\nwholesale trader in tobacco products\nassistant wholesale merchant in tobacco products\nB2B sales consultant in tobacco products\ngraduate wholesale merchant in tobacco products\nsenior wholesale merchant in tobacco products\nwholesale merchandiser in tobacco products\nwholesaler in tobacco products\ntrainee wholesale merchant in tobacco products\nbusiness to business sales consultant in tobacco products','\n',char(10)),'Wholesale merchants in tobacco products investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c872187a-079b-413b-8503-7b5dd92abb88','key_17580','','','3257','3257.2','food regulatory advisor',replace('legislation and specification  advisor\nfood regulatory compliance advisor\nfood labelling specialist\nfood safety regulatory advisor\nlegislation and controls advisor\nfood law advisor\nfood consultant\nfood regulatory affairs advisor\nfood expert\nfood safety expert\nfood safety auditor\nfood regulatory advisor\nregulatory advisor, food','\n',char(10)),'Food regulatory advisors are extra-judicial or judiciary technical experts. They ensure food industry practices comply with the regulatory norms. They perform audits, make diagnosis and monitor activities of inspection. These experts have expertise in food processing, food analysis, quality, safety, certification, traceability. They update, review, and approve labelling designs, develop nutrition facts panels, and ensure that products and labels meet appropriate standards and regulations.','','Includes food labelling specialist.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c89afd9a-7dad-4bae-9315-5fcadd9ad8b6','key_17581','','','7413','7413.1','electricity distribution technician',replace('linesman\nlineworker\nelectricity distribution worker\nelectricity power line worker\nelectricity transmission worker\npowerline worker\nelectric powerline worker\nlinewoman\nlineman\nlineswoman\npower lines construction worker\nelectric powerline technician','\n',char(10)),'Electricity distribution technicians construct and maintain electric power transmission and distribution systems. They maintain and repair powerlines, compliant with safety regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c8b296be-54ae-48cf-be21-031fa8457937','key_17582','','','3343','3343.1.7','supply chain assistant',replace('supply chain assistant\nsupply chain logistics assistant\nglobal supply chain assistant\nsupply chain supervisor\nproduction assistant\nsupply chain officer','\n',char(10)),'Supply chain assistants work closely together with managers in the operations processes, namely purchasing, manufacturing, and distribution processes. They help with administrative and pragmatic follow up of actions such as invoicing, drafting and preparation of contracts and purchasing orders, reconciliation of inventory against documents, and communication with distribution channels.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c8e6cdcc-824e-4887-8ee2-197e7d794258','key_17583','','','2114','2114.1.3','geochemist',replace('metals analyst\npetroleum technician\nexploration geochemist\nmining geochemist\ngeo-technician\noil geochemist\nresearch geochemist\nenvironmental geochemist\ngeochemist\nconsultant geochemist\ngeoscience technician','\n',char(10)),'Geochemists study the characteristics and chemical elements in minerals, rocks and soils, and how they interact with hydrological systems. They coordinate the collection of samples and indicate the suite of metals to be analysed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c8ee41fc-7a5f-4255-adbd-dcc47cf2a28e','key_17584','','','6224','6224.1','hunter',replace('pelt trapper\ngame trapper\nwildlife manager\ndeer hunter\nanimal hunter\nfur trapper\ntrapper\nhunter\nanimal trapper\ndeer stalker\nwildlife culler','\n',char(10)),'Hunters track and pursue animals with the intention of trapping or killing them. They hunt animals for the purpose of gaining food and other animal products, recreation, trade or wildlife management. Hunters specialise in the skill of tracking down and shooting animals with weapons such as rifles and bows. They also use devices to trap animals for similar purposes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c8ee6499-7613-4c02-9edf-2a0edcf47705','key_17585','','','3119','3119.3','aviation safety officer',replace('aviation security officer\ntransportation security officer\nairfield safety officer\naviation safety officer\nairport safety supervisor\nairfield safety supervisor\nflight safety supervisor\nflight safety officer\nairport security supervisor\nairport safety officer\naviation security supervisor','\n',char(10)),'Aviation safety officers plan and develop safety procedures for aviation companies. They study safety regulations and restrictions relative to aviation company operations. Hence, they direct activities of personnel in order to safeguard the application of safety measures in compliance with regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c8fa93eb-7c2c-42c3-b135-c2e825a6615e','key_17586','','','2149','2149.16','test engineer',replace('agricultural equipment test engineer\ntest engineer','\n',char(10)),'Test engineers plan and perform detailed quality tests during various phases of the design process to make sure that the systems are properly installed and function correctly. They analyse the data collected during tests and produce reports. They are also responsible for the safety of the test operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c90a6ebe-adcc-4395-9f5b-ff3e96e93cef','key_17587','','','2310','2310.1.36','religious studies lecturer',replace('professor of religious education\nlecturer of religion\nreligious studies docent\ntheology professor\nreligious studies instructor\nuniversity lecturer in religious studies\nreligious studies professor\ntheology lecturer\nuniversity religious studies lecturer\ntheology teacher\ntheology instructor\nlecturer in religion\nreligious studies teacher\nreligious studies lector\nlecturer of religious studies\nsenior lecturer in religious studies\nhigher education theology teacher\nreligious education lecturer\nprofessor of religious studies\ntheology docent\nlecturer in religious studies\nreligion lector\nreligious studies lecturer','\n',char(10)),'Religious studies lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, theology, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of theology, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c90e5009-37f2-4dcd-aea2-362fc817bc98','key_17588','','','1330','1330.10','ICT environmental manager',replace('information and communication technologies environmental manager\ninformation technologies environmental manager\nICT environment managers\nICT environment manager\nIT environmental manager\nICT environmental manager','\n',char(10)),replace('ICT environmental managers know the green ICT legal framework, understand the role of the ICT network configurations in the economy and energy resources deployment and evaluate the impact of CO2 footprint of each ICT resource in the organisation''s network. They plan and manage the implementation of environmental strategies for ICT networks and systems by conducting applied research, developing organisational policy, and devising strategies to meet sustainability targets. They ensure that the whole organisation uses ICT resources in a way that is as friendly as possible for the environment.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c9191f7f-28b5-4df8-991d-804c53009b83','key_17589','','','9213','9213.1','crop production worker',replace('farm worker\ncrop production worker\nfarm hand\ngrowing worker\ncrop worker\ncrops production worker\ncrop growing worker','\n',char(10)),replace('Crop production workers carry out practical activities and assist in the production of agronomical crops.  \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c92a4851-9418-4fb5-8201-c7e03cc2b6e2','key_17590','','','8312','8312.1','level crossing signalperson',replace('level crossing attendant\nlevel crossing signalwoman\nlevel crossing signaller\nlevel crossing signalperson\nlevel crossing signalman','\n',char(10)),'Level crossing signalpersons operate equipment in the safeguarding of level crossings, according to safety regulations. They supervise the traffic situation arround the level crossing, and communicate with traffic controllers, drivers, and other signalpeople when necessary. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c932109b-3e4f-43aa-b346-1ea421da0adb','key_17591','','','7534','7534.1','aircraft interior technician',replace('aircraft furniture maker\naircraft interior electtrical coordinator\naircraft interior maintenance cordinator\naircraft interior technician\naircraft interior maintenance technician\naircraft interior assembler\naircraft interior lighting technician\naircraft entertainment systems technician\naircraft interior instrument operative\naircraft interior electrical operator\naircraft interior electrical mecanic\naircraft interior instrument operator\naircraft electrical inspector\naircraft interior furniture maker\naircraft upholstrer\naircraft interior panelling technician\naircraft seating technician\naircraft interior finishes technician\naircraft instrument technicin\naircraft interior electrical tecnician\naircraft interior design technician\naircraft carpeting technician\naircraft furnishing technician\naircraft interior repairer\naircraft instrument mecanic\naircraft interior maintenance supervisor\naircraft interior electrical operative\naircraft furnishing maker\naviation interior instrument inspector','\n',char(10)),'Aircraft interior technicians manufacture, assemble and repair interior components for aircrafts such as seats, carpeting, door panels, ceiling, lighting, etc. They also replace entertainment equipment such as video systems. They inspect incoming materials and prepare the vehicle interior for new components.','',replace('Excludes    <a resource="http://data.europa.eu/esco/occupation/d9ab0f3d-078d-42a7-a502-3e739e0aeff6" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/d9ab0f3d-078d-42a7-a502-3e739e0aeff6"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">avionics technician</span></a>.\n Excludes   <a resource="http://data.europa.eu/esco/occupation/a784b063-89cf-441b-9624-deed9dbdeae6" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/a784b063-89cf-441b-9624-deed9dbdeae6"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">transport equipment painter</span></a>.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c93d58bd-3aa6-4de4-812d-010542f1dec8','key_17592','','','8131','8131.17','pill maker operator',replace('tablet production worker\npiling machine operative\npill production operator\ntablet making machine tender\npiling machine tender\npill maker\npill production worker\npill production operative\npill production process worker\ntablet production operator\ntablet production operative\ntablet production process worker\npiling machine operator\npill maker operator\ntablet making machine operator\ntablet making machine operative\npillmaker operator','\n',char(10)),'Pill maker operators tend the pilling machine that create pills in various sizes and shapes. They also fill the machine with necessary materials, open valves to control the flow of the materials, and regulate the temperature of the machine.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c94dc5b2-6b23-4915-8de5-a4e6ae86d802','key_17593','','','0310','0310.1','air force pilot',replace('military pilot\nnavy pilot\narmed forces pilot\nnaval pilot\nair force pilot\naircrew pilot\nmilitary aircraft driver','\n',char(10)),'Air force pilots operate aircrafts in combat missions, patrol missions, or search and rescue missions. They ensure aircraft maintenance, and communicate with air force bases and other vessels to ensure safety and efficiency in operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c94f3565-d987-431f-a46b-6f4e018e0bd7','key_17594','','','1213','1213.4','programme manager',replace('programme director\nprojects portfolio manager\nprogram director\nprogram manager\nprogram management coordinator\nprogramme management coordinator','\n',char(10)),'Programme managers coordinate and oversee several projects working simultaneously. They ensure workability and compatibility among projects ensuring that overall, each one of the projects under the management of project managers, turn out profitable and leveraging one to the other.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c95121e9-e9f7-40a9-adcb-6fda1e82bbd2','key_17595','','','3116','3116.1.4','hazardous waste technician',replace('waste disposal site compliance technician\ntoxic waste removal technician\nhazardous waste technician\nhazardous waste removal technician\nwaste treatment technician\nlandfill site technician\nhazardous waste disposal technician\nhazardous waste treatment technician\ntoxic waste disposal technician\nwaste sortation technician\nwaste disposal compliance technician\ntoxic waste treatment technician\ntoxic waste technician','\n',char(10)),'Hazardous waste technicians dispose of materials that have been in contact with or are ignitable, corrosive, reactive, toxic or a combination of the aforementioned hazardous traits. They remove the waste from industrial facilities or households and transport them to a treatment facility to ensure they are treated and disposed of according to regulations. They may also advise on the proper treatment of hazardous waste, and aid in the clean-up of hazardous waste spills.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c956be07-53a3-4b01-b8f8-f0c5a428eaa8','key_17596','','','2433','2433.6','technical sales representative',replace('field salesperson\ninside technical sales representative\ntrainee technical sales representative\nassistant technical sales engineer\ntechnical field sales representative\nfield saleswoman\nsenior technical sales engineer\ngraduate technical sales engineer\nfield salesman\noutside technical sales representative\ntechnical sales representative\ngraduate technical sales representative\nassistant technical sales representative\ntrainee technical sales engineer\nsenior technical sales representative','\n',char(10)),'Technical sales representatives act for a business to sell its merchandise while providing technical insight for customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c9839843-6cac-4564-8aa7-de4a5b9b62e1','key_17597','','','3311','3311.2.3','mutual fund broker',replace('mutual fund sales agent\nmutual fund sales representative\nmutual fund salesman\nmutual fund broker\nmutual fund dealer\nmutual fund manager','\n',char(10)),'Mutual fund brokers handle and raise cash from shareholders in order to invest them in stocks, bonds and money-market securities. They engage with investors by making inquiries about the client''s mutual funds account status and transaction procedures. Mutual fund brokers make use of their expertise in investment theory, market experience, and research to pick the most approapriate investments for their fund portfolio. They ensure that the mutual fund''s operations are in compliance with legal requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c9862fdd-53b7-4da9-b742-4302d704836c','key_17598','','','2421','2421.4','lean manager',replace('lean engineer\nlean facilitator\ncontinuous improvement manager\nlean coach\nprocess excellence manager\nkaizen manager\nlean expert\nlean director\noperational excellence manager\nmanufacturing excellence manager\nlean project manager','\n',char(10)),'Lean managers plan and manage lean programs in different business units of an organisation. They drive and coordinate continuous improvements projects aimed at achieving manufacturing efficiency, optimise workforce productivity, generate business innovation and realise transformational changes impacting on operations and business processes, and report on results and progresses to the company management. They contribute to the creation of a continuous improvement culture within the company, and they are responsible for developing and training a team of lean experts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c9874f14-5474-43f6-b4a2-77a94f9abfb3','key_17599','','','7534','7534.3.3','motor vehicle upholsterer',replace('bus upholsterer\nmotor vehicle upholsterer\ncar trimmer\nbus trimmer\nlorry upholsterer\ntruck trimmer\ncar upholsterer\ntruck upholsterer\nmotorcycle upholstery worker\ntruck  upholstery worker\nbus upholstery worker\ncoach upholstery worker\nlorry trimmer\nmotorcycle trimmer\ncoach trimmer\nlorry upholstery worker\ncar upholstery worker\nmotorcycle upholsterer','\n',char(10)),'Motor vehicle upholsterers create manufacturing templates, manufacture and assemble interior components for cars, buses, trucks etc. They use power tools, hand tools and shop equipment to prepare and fasten materials. They also inspect incoming materials and prepare the vehicle interior for trim items.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c995fe34-0909-4ce3-b7b7-97945efede56','key_17600','','','2166','2166.12','performance video designer',replace('projection & video designer\nvideo designer\ngraphics and video designer\nvideo & graphics designer\nvideo and projection designer\nvisual designer\nprojection designer\nprojection and video designer\ngraphics & video designer\nvideo & projection designer\nvideo and graphics designer\nvideo jockey','\n',char(10)),'Performance video designers develop a projected image design concept for a performance and supervise the execution of it. Their work is based on research and artistic vision. Their design is influenced by and influences other designs and must be conform with these designs and the overall artistic vision. Therefore, the designers work closely with artistic directors, operators and the artistic team. Performance video designers prepare media fragments to be used in a performance, which can involve recording, composing, manipulating and editing. They develop plans, mapping, cue lists and other documentation to support the operators and production crew. They sometimes also work as autonomous artists, creating video art outside a performance context.','','This occupation is focused on live performance and not on media or broadcast activities. Includes activities in event and rental companies.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c9a5f7c5-0f8b-4252-9729-5f19c105ef81','key_17601','','','3112','3112.1.11','road maintenance technician',replace('road maintenance worker\nhighways maintenance inspector\nroad maintenance inspector\nhighways technician\nhighways inspector\nroad inspector\nroad maintenance technician\nroad maintenance supervisor\nhighways maintenance technician','\n',char(10)),'Road maintenance technicians inspect and manage roads in enclosed areas for maintenance and repair. They help to relieve traffic safely and smoothly, and check if traffic signs, roads and pavements are in good condition.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c9b2b0a7-0adc-4ca7-bf7c-dc95e18aeac7','key_17602','','','3343','3343.1.5','procurement support officer',replace('procurement clerk\nprocurement assistant\njunior buyer','\n',char(10)),'Procurement support officers assist the staff of the procurement team throughout the procurement processes, making sure all procurement documents are compliant with procedural, technical and legal requirements, and that meetings and other contacts are well organised.  ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c9d89e09-d57f-4980-9164-880e60711e97','key_17603','','','1345','1345.1.6','primary school head teacher',replace('primary education head teacher\nprimary school headteacher\nprimary school headmaster\nhead teacher of primary school\nelementary school principal\nprimary school director\nheadteacher of primary school\nheadmaster of primary school\nprimary education headteacher\nprimary school principal\ndirector of primary school','\n',char(10)),'Primary school head teachers manage the day-to-day activities of a primary school or elementary school. They manage staff, make decisions concerning admissions and are responsible for meeting curriculum standards, which are age-appropriate for primary school students and facilitate social and academic development education. They also ensure the school meets the national education requirements set by law.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/c9f6c245-8c66-4b13-9253-9835194abb23','key_17604','','','7315','7315.2','glass beveller',replace('glass beveler\nglass beveller machine operator\nglass hand-beveller\nglass beveling machine operator\nglass edger\nhand-beveller\nglass cutter\nglass finisher','\n',char(10)),'Glass bevellers measure, cut, assemble and install flat glass and mirrors. They also load and unload glass, mirrors and equipment, drive to installation sites, install metal or wood frameworks that need to be fitted with glass, and work according to client specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ca2da084-3bfa-47d8-84ba-ef890191708d','key_17605','','','8160','8160.53','vermouth manufacturer',replace('vermouth processor\nvermouth manufacturer\nvermouth production operative\nvermouth production operator\nvermouth operative\nvermouth producer','\n',char(10)),'Vermouth manufacturers perform all the production processes required to produce vermouth. They mix ingredients and botanicals with wine and other spirits. They perform maceration, mixing and filtering of beverages together with the botanicals. Moreover, they manage the maturation of the beverages and forecast when the vermouth is ready for bottling.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/caa75a5b-b32f-4d57-bffa-03dbb91058cf','key_17606','','','1321','1321.1','green coffee coordinator',replace('coffee coordinator\ngreen coffee coordinator\ngreen coffee manager\ngreen coffee co-ordinator\ngreen coffee supervisor','\n',char(10)),replace('Green coffee coordinators organise and manage the operations performed by workers in coffee plants and plan the functioning of machines that blend various types of green coffee beans. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/caafca69-9faa-4850-8930-a3fa07fea4e6','key_17607','','','2411','2411.1.1','accounting analyst',replace('accounting analyst\naccounting methods analyst\naccounts analyst\naccounting specialist\naccounting systems analyst','\n',char(10)),'Accounting analysts evaluate the financial statements of clients, usually companies, which include the income sheet, the balance sheet, the statement of cash flows and additional notes to other financial statements. They interpret and implement new accounting systems and accounting procedures and will analyse and determine if the proposed systems conform to accounting regulations and meet user information requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cadbf9af-1da0-406f-8282-b6d27a7e1a74','key_17608','','','7215','7215.2.2','ground rigger',replace('ground rigger\nrigging crew member\ndown rigger','\n',char(10)),replace('Ground riggers assist level riggers assembling temporary suspension structures to support performance equipment. Their work is based on instruction and plans. They work indoor as well as outdoor. They cooperate closely with high riggers. \n ','\n',char(10)),'',replace('Excludes people working at heights. \nIncludes people working in events and rental companies. \nExcludes people performing industrial rigging.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cae71f3f-ad4d-480e-b501-bff22827e60c','key_17609','','','8342','8342.1','bulldozer operator',replace('bulldozer operator\nbulldozer driver\nbull dozer driver\ndozer driver\ndozer operative\nbulldozer operative\nearthmover driver\nbull dozer operator\ndozer/crawler driver\nconstruction plant operator\nearthmover operator','\n',char(10)),'Bulldozer operators operate a heavy vehicle to move earth, rubble or other material over the ground.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/caf7b8fb-b2d6-46b7-bffa-bea7f8e4aed8','key_17610','','','2422','2422.12','policy officer',replace('policy analyst\nhealth policy officer\npolicy researcher\npolicy coordinator\npolicy manager\nstrategy advisor\npolicy officer\nstrategy manager\npolicy advisor','\n',char(10)),'Policy officers research, analyse and develop policies in various public sectors, and shape and implement these policies to improve the existing regulation around the sector. They evaluate effects of existing policies and report findings to the government and members of the public. Policy officers work closely with partners, external organisations or other stakeholders and provide them with regular updates.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cb3432f8-bad7-4703-ba66-dc980a4ce4f7','key_17611','','','7223','7223.18','scrap metal operative',replace('metal shredder operator\nscrap metal processor\nrecycled metal operative\nmetal processing operative\nrecycler\nrecyling operative\nscrap metal operative\nsmelter operative\nmetal recycling operative\nscrap sorter\nshredder operator','\n',char(10)),'Scrap metal operative cut big sheets of metal scrap in order to prepare them for being used in a smelter.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cb3b2662-774a-4e6f-841e-f120244d7031','key_17612','','','0210','0210.2','sergeant',replace('station sergeant\ndrill sergeant\npolice sargeant\nlance sergeant\nwarrant officer\nordnance sergeants\ncommissary sergeant\nadjutant\narmy sargeant\nquartermaster sergeant\ncolour sergeant\npetty officer\nsergeant\nstaff sergeant\nchief petty officer\nchief non-commissioned officer','\n',char(10)),'Sergeants command squads as a second in command. They allocate tasks and duties, supervise equipment, and ensure proper training of staff. They also advise commanding officers and perform support duties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cb3c0f82-a79b-4518-ad1f-5463b3e74831','key_17613','','','8342','8342.8','scraper operator',replace('towed-scraper driver\nscraper driver\nconstruction plant operator, scraper\nscraper operative\nmotor-scraper driver\ntowed-scraper operator\nscraper operator\nplant operator, scraper\nheavy plant operator, scraper\nscraper worker','\n',char(10)),'Scraper operators work with a mobile piece of heavy equipment that scrapes the top layer of the ground and deposits it in a hopper to be hauled off. They drive the scraper over the surface to be scraped, adapting the speed of the machine to the hardness of the surface.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cb446db8-e8b3-418c-a21c-2d10faea42a5','key_17614','','','1219','1219.5.3','power plant manager',replace('power plant director\npower plant supervisor\nelectric power station engineer\npower supply manager\nelectric power plant manager\npower station manager\npower plant superintendent\npower plant project manager\ndemand generation manager\nelectric power supply manager\nelectricity generation manager\nelectric power generation manager\npower  plant generation manager\npower generating manager\nenergy generation manager\npower plant coordinator\npower plant manager\ngas generation manager\npower generation systems manager\nelectric power plant project manager\npower generation networks manager','\n',char(10)),'Power plant managers supervise operations in power plants which produce and transport energy. They coordinate the production of energy in the plant, and supervise the construction, operation and maintenance of energy transmission and distribution networks and systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cb4f98c8-6b8d-41d8-87eb-a2a2033a3a25','key_17615','','','2422','2422.15','public procurement specialist','','Public procurement specialists are full-time public procurement practitioners working as part of a procurement team in a large organisation or central purchasing body in all phases of the procurement cycle. They translate needs into contracts and deliver value for money for the organisation and the public. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cb60beab-7afb-4e99-8162-221abea68e6d','key_17616','','','3359','3359.2','consumer rights advisor',replace('trading standards officer\nlegal services adviser\nconsumer rights adviser\nconsumer rights legal advisor\nlegal services advisor\nfood hygiene inspector\nconsumer rights advisor\nconsumer rights legal adviser','\n',char(10)),'Consumer rights advisors aid consumers with complaints and provide advice and information on rights as a consumer after purchase of products or services. They monitor organisations to ensure compliance to consumer rights standards, and aid consumers in handling disputes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cb8be514-9388-4ef0-a58d-cff496747610','key_17617','','','8212','8212.2.3','wire harness assembler',replace('wire harness technician\ncable harness fabricator\nwire harness fabricator\ntechnician in wire harnesses\nfabricator of wire harnesses\nfabricator of cable harnesses\ncable harness assembler\nassembler of cable harnesses\ncable harness technician\nwire harness assembler','\n',char(10)),'Wire harness assemblers bind wires or cables to form wire harnesses used in electronic and electrical systems and equipment. They read electrical wiring plans and tie the wire together according to specifications, using cable ties, cable lacing, electrical conduit, and sleeves.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cb932c74-c13b-47e9-9a85-318bd8eb2f96','key_17618','','','9112','9112.6','train cleaner',replace('train cleaning staff member\ntrain night cleaner\ntrain depot cleaner\ntrain cleaning crew member\ntrain cleaning team member\ntrain cleaning operative\ntrain day cleaner\ntrain cleaner\ntrain and station cleaner','\n',char(10)),'Train cleaners keep the interiors of trains tidy and clean. They clean out the bins in the different compartments, and perform other cleaning activities such as hoovering, mopping and deep cleaning.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cb93fd23-1f51-4495-867a-936c1967066b','key_17619','','','2654','2654.1','art director',replace('advertising art director\ncreative director\nvisual art director\ndesigner\nartistic projects manager\nart director\nmaestro\nstage director','\n',char(10)),'Art directors shape the visual layout of a concept. They create innovative designs, develop artistic projects  and manage the cooperation between all aspects involved. Art directors may perform creative work in theatre, marketing, advertising, video and motion picture, fashion or online companies. They ensure that the work performed is visually appealing to audiences.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cbad55a4-c7cf-4ab6-9540-eac72b26729b','key_17620','','','9111','9111.1','domestic cleaner',replace('domestic helper\nservant\nhousehold worker\nhousekeeping cleaner\ncharworker\npersonal maid\ndomestic worker\nmaid\ndomestic maid\ncleaning lady\nhouse cleaner\ndomestic cleaner\nhousekeeper\ncleaning maid\nchambermaid','\n',char(10)),'Domestic cleaners perform all necessary cleaning activities in order to clean their clients'' houses. They vacuum and sweep floors, wash dishes, launder clothes, dust, scrub and polish surfaces and disinfect equipment and materials.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cbdb80db-c760-4b68-b074-88c397650ef5','key_17621','','','2642','2642.1.8','editor-in-chief',replace('executive director of editorial\nmanaging editor\nexecutive editor\neditor-in-chief\nhead editor\neditor in chief\nchief editor\neditorial director\nexecutive editorial director','\n',char(10)),'Editors-in-chief oversee the production of news stories for newspapers, magazines, journals and other media. They manage the day-to-day operations of a publication and make sure it is ready on time.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cbdbe324-81fe-48da-a8f7-0106dd94a05b','key_17622','','','3133','3133.1.4','gas processing plant supervisor',replace('gas production controller\ngas processing safety controller\ndownstream gas processing controller\ngas processing quality controller\ngas processing plant supervisor\ngas processing maintenance controller\ngas processing performance controller\ngas processing controller\ngas compressor controller','\n',char(10)),'Gas processing plant supervisors supervise the processing of gas for utility and energy services by controlling compressors and other processing equipment to ensure standard operation. They supervise the maintenance of the equipment, and perform tests to detect problems or deviations, and to ensure quality.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cbde1a3a-406d-4d17-b30b-03981925035d','key_17623','','','3112','3112.5','energy analyst',replace('energy  procurement analyst\nenergy performance analyst\nenergy efficiency analyst\nenergy regulation specialist\nsenior energy analyst\nenergy and sustainability analyst\nenergy analyst\nenergy advisor','\n',char(10)),'Energy analysts evaluate the consumption of energy in buidings owned by consumers and businesses. By analysing existing energy systems, they recommend cost-effective alternatives. Energy analysts suggest efficiency improvements, make business analyses and participate in the development of policies concerning the use of traditional fuels, transportation, and other factors relating to energy consumption.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cbde2432-cb7f-451f-a686-b22cad3957a6','key_17624','','','2355','2355.4','drama teacher',replace('performing and dramatic arts teacher\ndrama teacher\ndrama instructor\nperforming and dramatic arts instructor\ninstructor in performing and dramatic arts\nteacher of dramatic arts\ndrama educator\neducator in drama\nteacher of drama','\n',char(10)),'Drama teachers instruct students in a recreational context in the various theatrical genres and dramatic expression forms, such as comedy, tragedy, prose, poetry, improvisation, monologues, dialogues etc. They provide students with a notion of theatre history and repertoire, but mainly focus on a practice-based approach in their courses, in which they assist students in experimenting with and mastering different dramatic expression styles and techniques and encourage them to develop their own style. They cast, direct and produce plays and other performances, and coordinate the technical production and the set, props and costume usage on stage.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cc844eb8-c81d-40ff-af3d-119700e60f23','key_17625','','','3323','3323.1','costume buyer',replace('buyer of costumes\ncostume and composite effects buyer\ncostume buyer\ncostume purchaser\ncostumes buyer','\n',char(10)),'Costume buyers work with the costume designer in order to identify the materials for the costumes. They buy and rent fabric, thread, accessories and other items needed to finish the wardrobe. Costume buyers may also purchase  ready-made clothing items  They base their purchases on the sketches of the costume designer.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cc8f929a-75fa-404d-aa17-3301fb494b4f','key_17626','','','7543','7543.8','product grader',replace('quality grader\ngrader\nquality inspector\nproduct quality inspector\nproduct inspector\nproduct quality observer\nproduct grader\nproduct evaluator\nproduct quality monitor\nproduct quality grader','\n',char(10)),'Product graders perform preventive and operational quality control on products and rescources. They inspect, grade and evaluate the materials at various stages to make sure it conforms to the desired standard, and send the product back for repair or improvement if necessary. They work in companies and industrial facilities to ensure the produced commodities achieve quality standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cc937d5f-4072-4fe7-bc23-2c9a465b8754','key_17627','','','1349','1349.18','museum director',replace('head of museum\nmuseum manager\ndirector of collections\nmuseum director\nmuseum and heritage director\nart gallery director\nart collection director','\n',char(10)),'Museum directors oversee the management of the art collections, artefacts, and exposition facilities. They secure and sell works of art on the one hand, and strive to preserve and maintain the art collection of a museum on the other hand. Moreover, they also manage finances, employees, and marketing efforts of the museum.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cc9fcab5-a5c6-4cc9-9606-cbcc2852537f','key_17628','','','2131','2131.4.6.1','curator of horticulture',replace('curator and horticulturist\ncurator of botany\ncurator of horticulture\nconservation horticulturist\nhorticulture curator\ngarden curator\ncurator & horticulturist','\n',char(10)),'Curators of horticulture develop and maintain the botanical collections, exhibits and landscapes of a botanical garden.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ccbb7213-79f7-489e-9dad-f90091e550d0','key_17629','','','1324','1324.3.2.6','import export manager in clothing and footwear',replace('senior import export manager in clothing and footwear\ngraduate export manager in clothing and footwear\nimport export manager in clothing and footwear\ntrainee export manager in clothing and footwear\ninternational trade compliance manager in clothing and footwear\ngraduate import export manager in clothing and footwear\nassistant import manager in clothing and footwear\ngraduate import manager in clothing and footwear\ncustoms compliance manager in clothing and footwear\ntrainee import export manager in clothing and footwear\ntrainee import manager in clothing and footwear\nsenior export manager in clothing and footwear\nassistant export manager in clothing and footwear\nsenior import manager in clothing and footwear\ninternational trade manager in clothing and footwear\nassistant import export manager in clothing and footwear','\n',char(10)),'Import export managers in clothing and footwear install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ccf0becd-247d-4497-9ee7-25a46c1ffb7a','key_17630','','','5223','5223.7.29','press and stationery specialised seller',replace('magazines and stationery shop specialised seller\nnewspapers and magazines salesperson\nmagazines and stationery shop salesperson\nnewspaper seller\nnewsagent retail salesperson\nnewspaper kiosk salesperson\nkiosk salesperson\nstationery shop salesperson\nnewsagents salesperson\nnewsagent retail specialised seller\nnewspapers and magazines specialised seller\npaper shop specialised seller\nnewspapers and magazines shop salesperson\nnewspaper kiosk specialised seller\nstationery salesperson\nnewspapers and magazines shop specialised seller\npress and stationery specialized seller\nnewsagents shop specialised seller\npaper shop salesperson\nnewsagents shop salesperson\nstationery specialised seller\nkiosk specialised seller\nnewsagents specialised seller\nstationery shop specialised seller','\n',char(10)),'Press and stationery specialised sellers sell newspapers and office supplies such as pens, pencils, paper, etc. in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cd19e8d1-6abd-4c99-a9bb-85e7c30aca31','key_17631','','','3151','3151.2','marine chief engineer',replace('marine chief engineer\nchief engineer\nchief engineer of a ship\nfisheries chief engineer\nmaritime chief engineer\nchief engineering technician\nchief ship engineer','\n',char(10)),'Marine chief engineers are responsible for the entire technical operations of the vessel including engineering, electrical, and mechanical divisions. They are the head of the entire engine department aboard the ship and have overall responsibility for all technical operations and equipment on-board the ship. Marine chief engineers collaborate on security, survival and health care on board and observe the national and international standards of application.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cd3f5356-a16f-4aca-9d4b-ceba2b2afec5','key_17632','','','7534','7534.3.1','furniture upholsterer',replace('furniture upholsterer\ncommercial upholster\nmattress maker\ncraftsman upholster\nsoft furnishing maker\nchair builder\nheritage upholsterer\nseat  builder\nchair maker\ncraftswoman upholster\nseat maker\ncraft upholster\nchairmaker\ncraftsperson upholster\nmattress builder','\n',char(10)),'Furniture upholsterers provide furniture with padding, springs, webbing and covers. Sometimes they have to remove old padding, filling and broken strings before to replace them using tools such as a tack puller, chisel or mallet. The aim is to provide comfort and beauty to seats as backs of the furniture.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cd4f41a5-87a6-4b3c-8ee3-14e6f457c365','key_17633','','','2411','2411.1.2','audit supervisor',replace('audit checker\naudit team leader\nauditor-in-charge\naudit examiner\naudit overseer\naudit assessor\naudits supervisor\nauditing supervisor\naudit associate\naudit supervisor\naudit leader\naudit inspector','\n',char(10)),'Audit supervisors oversee audit staff, planning and reporting, and review the audit staff''s automated audit work papers to ensure compliance with the company''s methodology. They prepare reports, evaluate general auditing and operating practices, and communicate findings to the superior management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cd7b838c-51f6-41ec-9067-bb974c0f991b','key_17634','','','1222','1222.1','communication manager',replace('internal communications manager\nsocial media manager\nforeign language correspondent communications manager\ncommunication practitioner\npress spokesperson\ncommunications associate\npublic relations manager\ncommunication manager\ncommunications manager\nexternal communications manager\ncorporate communication manager\npublic relations and communication manager\nonline communication manager','\n',char(10)),'Communication managers are responsible for developing communication strategies in order to promote the organization''s mission, services or product. They coordinate communication projects and manage the communications issued by the company for both the internal and the external clients. They supervise internal communications, ensuring that communications reach each one of the employees and further questions can be answered. For external communications, they coordinate coherence among the messages transmitted in mails, printed materials, press articles, and corporate promotional materials. They strive to maintain truthful communications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cd92596c-bd96-4126-8b67-7bf6ed2ca8fb','key_17635','','','3115','3115.1.8','marine mechatronics technician',replace('maritime mechatronics technologist\nnautical mechatronics technologist\nmarine mechatronics technologist\nship mechatronics technician\nnautical mechatronics technician\nnaval mechatronics technician\nmaritime mechatronics technician\nnaval mechatronics technologist','\n',char(10)),'Marine mechatronics technicians work on shipyards and vessels to configure and optimize industrial mechatronic systems and plans, supervise and execute their assembly and maintenance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cd94def5-3442-4c2e-ae3d-0761a3008bcb','key_17636','','','9333','9333.8.1','warehouse order picker',replace('picker & packager\npacker\npacker and shipper\nwarehouse picker\nwarehouse order picker\norder clerk\nwarehouse packager\nwarehouse worker\npackager & picker\nwarehouse operator\npackager and picker\norder picker\nwarehouse packer\nwarehouse operative\norder lifter\npicker and packager\npackager','\n',char(10)),'Warehouse order pickers prepare orders manually. They pick up orders and bring them to the delivery platform for processing, or in the trade sector to allow customers to pick them up. They are expected to complete orders for shipment, keeping in mind the quantity and type of goods specified and meeting quality criteria established by the company. They also assemble various types of merchandise for shipment and transport orders to shipping locations as specified by a supervisor. They typically stack bundled articles on pallet by hand, are responsible for wrapping articles on the pallet to secure them while moving, and for ensuring the integrity of the pallet.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cdd3d773-75a4-4f35-bd1d-1ac720afcf9d','key_17637','','','2511','2511.18','IT auditor',replace('IT auditor\nIT Quality Appraiser\nICT auditor\ninformation technology auditor','\n',char(10)),'IT auditors perform audits of information systems, platforms, and operating procedures in accordance with established corporate standards for efficiency, accuracy and security. They evaluate ICT infrastructure in terms of risk to the organisation and establish controls to mitigate loss. They determine and recommend improvements in the current risk management controls and in the implementation of system changes or upgrades.','','Excludes people performing managerial activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cdef4961-fcc2-4e00-bfe8-4480b33aa6f3','key_17638','','','7212','7212.4','welding coordinator',replace('welding fabricator supervisor\nwelding coordinator\nwelding technician\nbutt welding supervisor\nproduction line welding supervisor\nconstruction welder supervisor\nsheet-metal welding supervisor\nresistance welding supervisor\nelectric arc welding co-ordinator\nstitch welding supervisor\nultrasonic metal welder supervisor\nresponsible welding co-ordinator\nwelding supervisor\ntack welding supervisor\nsheet metal welding supervisor\nelectric spot welding supervisor\nrobotic welding supervisor\nwelder-fabricator technician','\n',char(10)),'Welding coordinators supervise the workflow of welding applications. They monitor welding processes performed by other welders, supervise the staff, being sometimes responsible for vocational training. They also weld particularly demanding parts. Welding coordinators ensure that the necessary welding equipment is ready for usage. They mostly coordinate welding applications and related professional activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ce2b8609-34c7-40d3-a4f7-581c76bd258a','key_17639','','','3355','3355.3','police inspector',replace('chief inspector\npolice superintendent\npolice inspector\nsuperintendent\ninspector of police','\n',char(10)),'Police inspectors coordinate and supervise a division in a police department. They ensure the division''s compliance with rules and regulations, and monitor the performance of personnel as well as assigning tasks to them. They perform administrative duties to ensure the maintenance of records and reports, and may also develop regulatory guidelines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ce53a1b2-2fec-4d04-9390-47fb3dd525f0','key_17640','','','2635','2635.3.6','community social worker',replace('community social worker\nsenior community worker\noutreach worker\nneighbourhood outreach worker\nestate outreach worker\nneighbourhood worker\nhousing social worker\ncommunity outreach worker\nsocial development worker\ncommunity support worker\ncommunity welfare social worker','\n',char(10)),'Community social workers help people in disadvantage or excluded from society to change their situation and handle their integration problems. They work with communities focusing on specific groups. Community social workers liaise closely with social workers, schools, local authorities and probation officers representing people before policy makers at local and national level.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ce594060-b05a-45d8-ac41-1cf3bb980085','key_17641','','','7536','7536.2.4','footwear hand sewer',replace('footwear hand sewer\nmocassin sewer\nshoes hand stitcher\nmocassin hand stitcher\nshoes hand sewer\nhand sewer, shoes','\n',char(10)),'Footwear hand sewers join the cut pieces of leather and other materials using simple tools, such as needles, pliers and scissors to produce the uppers. Also, they perform hand stitches for decorative purposes or for assembling the uppers to soles in case of complete footwear.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ce5b1eb1-5862-4f00-a813-0f90aecd5ef5','key_17642','','','8122','8122.6','dip tank operator',replace('dipping tank operator\nimpregnating tank operator\nhot dip plater\nplating tank operator\nmetal dipping machine operator\ncoating machine worker\ncoating machine operative\ncoating machine operator\ndip tank operator\nplating tank technician\ndip tank worker\nplating tank operative\ndipping tank specialist\ndipping tank operative\ndip tank operative\nimpregnating tank operative\nhot dipping plater\nhot dip galvanizing operative\ndip tank technician\ndipping tank worker\nhot dip galvanizer\nplating tanker operator\nmetal dipping machine operative\ndip tank specialist\ndipping tank technician\ntanker','\n',char(10)),'Dip tank operators set up and tend dip tanks, which are coating machines, designed to provide otherwise finished work pieces with durable coating by dipping them in a tank of a specific sort of paint, preservative or molten zinc.','','Includes <a resource="http://data.europa.eu/esco/occupation/ce5b1eb1-5862-4f00-a813-0f90aecd5ef5" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/ce5b1eb1-5862-4f00-a813-0f90aecd5ef5"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">dip tank operators</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ce6230b8-154f-4e0b-ac03-dca3a269a19c','key_17643','','','1431','1431.1.1','cultural centre director',replace('director of arts center\ndirector of cultural center\ncultural center manager\ndirector of cultural centre\ncenter director\narts centre director\narts centre manager\ncommunity center manager\ncultural centeredirector\ndirector of arts centre\ncultural centre manager\narts center director\ncentre director','\n',char(10)),'Cultural centre directors manage operations of a cultural community centre, they organise and promote cultural activities and events, manage staff, and aim to promote the overall inclusion of cultural programmes in the community.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ce7d3362-5383-47d1-b66f-57b021c3d862','key_17644','','','2654','2654.4','radio producer',replace('radio broadcast producer\nradio producer\nradio programme producer\nradio content and production manager\nproducer\nradio show production manager\ncontent and production manager\nproducer in radio\nradio show producer\nradio production manager','\n',char(10)),' Radio producers are responsible for organising the making of radio shows. They oversee aspects of radio shows such as content, audio production, resource planning and personnel supervision.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ce8145f5-d7ea-40ea-b12e-ef168f11184a','key_17645','','','2652','2652.5','sound artist',replace('sound artist\nsound installation creator\nsound designer & artist\nlive sound artist\nsonic manipulator\nsound designer and artist\nsound artist & designer\nsound artist and designer','\n',char(10)),'Sound artists use sound as a main creative medium. They express, through the creation of sounds, their intention and identity. Sound art is interdisciplinary in nature and takes on hybrid forms.','','Excludes composer.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cea4d5c9-7805-434a-8e6b-af5e9fa54c9e','key_17646','','','7321','7321.1.4','scanning operator',replace('scanning machine operator\nscanning operator\nscanning specialist\ndocument scanning specialist\ndocument scanner\nscanning clerk\nscanner operator\nscanner tender\nscanning machine tender\nscanner worker\ndocument scanning operator\nscanner technician\nscanning technician\nscanner specialist','\n',char(10)),'Scanning operators tend scanners. They feed print materials into the machine and set controls on the machine or on a controlling computer to obtain the highest resolution scan.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ceb1a21b-a6d5-49eb-a3f3-d53fec25d5f8','key_17647','','','3324','3324.4.6','wholesale merchant in clothing and footwear',replace('trainee wholesale merchant in clothing and footwear\nwholesale merchant in clothing and footwear\nbusiness to business seller in clothing and footwear\nwholesaler in clothing and footwear\nsenior wholesale merchant in clothing and footwear\nwholesale trader in clothing and footwear\nwholesale buyer in clothing and footwear\nbusiness to business sales consultant in clothing and footwear\ngraduate wholesale merchant in clothing and footwear\nassistant wholesale merchant in clothing and footwear\nwholesale merchandiser in clothing and footwear\nB2B sales consultant in clothing and footwear','\n',char(10)),'Wholesale merchants in clothing and footwear investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ceb5277b-2c4d-4cb9-9b7e-b386e66e11ba','key_17648','','','7523','7523.1','nailing machine operator',replace('nailing-machine worker\nnailing machine operator\nnailing machine technician\nnailing-machine technician\nnailing-machine specialist\nnailing-machine operator\nnailing-machine operative\nnailing machine operative\nnailing-machinist\nnailing machine worker\nnailing machine specialist\nnailing machinist','\n',char(10)),'Nailing machine operators work with machines that nail wooden elements together, usually hydraulically. They put the elements to be nailed in the right position, and monitor the process to prevent downtime.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ceb9910a-1174-4926-82c9-5afee2bd3702','key_17649','','','3123','3123.1.12','glass installation supervisor',replace('window installation supervisor\nglass installation foreman\nfenestration installation supervisor\nglass installation supervisor\nplate glass installation supervisor\nforeman glazier\nglazing foreman\nwindow glass installation supervisor\nglazing supervisor','\n',char(10)),'Glass installation supervisor monitor the process of installing plate glass. They assign tasks and take quick decisions to resolve problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ceda53cd-3f1f-425b-bd1b-c4fa17254680','key_17650','','','1346','1346.6','pension scheme manager',replace('pension services manager\npension scheme manager\nindividual pension adviser\npension plans administrator\npensions retirement plan specialist\npensions manager\npension planner\npensions adviser\npension adviser\npension advisor\nretirement planner\npension fund manager','\n',char(10)),'Pension scheme managers coordinate pension schemes in order to provide retirement benefits to individuals or organisations. They ensure the daily deployment of the pension fund and define the strategic policy for developing new pension packages.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ceda6443-e1dd-4890-b018-42318e3abac2','key_17651','','','3255','3255.4','sophrologist',replace('sophrology practitioner\npractitioner of dynamic relaxation methods\nsophrology expert\ndynamic relaxation practitioner\nspecialist sophrology practitioner\ndynamic relaxation methods practitioner\npractitioner of sophrology\nsophrology specialist\nsophrologist','\n',char(10)),'Sophrologists aim to reduce their clients` stress and produce optimal health and well-being by applying a dynamic relaxation method which consists of a specific set of physical and mental exercises on a doctor''s order.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cee31c49-abe9-4f29-ad10-5eefe63f2a57','key_17652','','','3334','3334.4','real estate leasing manager',replace('commercial leasing manager\ncommercial real estate manager\nleasing administration manager\nlease operator\nreal estate leasing manager\nproperty leasing coordinator\nleasing administrator\nreal estate rental and management manager\nmanager of leasing activities\ncommercial leasing administrator\nleasing manager\nproperty leasing manager','\n',char(10)),'Real estate leasing managers set up the lease or rental efforts of an apartment community and properties not in co-ownership and also manage the leasing staff. They produce, track and manage file leasing deposits and documents. They oversee the lease administration and prepare tenancy budgets on an annual and monthly basis. They also actively promote the vacancies available in order to get new residents, show properties to potential tenants and are present to conclude contracts between landlords and tenants when dealing with private property.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cf2b03cd-feb7-4f47-90f6-ff1ed6016d3d','key_17653','','','2523','2523.3','ICT network engineer',replace('networks designer\nnetwork engineers\nnetwork configurator\nICT network engineer\nnetworks engineer\nnetwork developer\nnetworks developer','\n',char(10)),'ICT network engineers implement, maintain and support computer networks. They also perform network modelling, analysis, and planning. They may also design network and computer security measures. They may research and recommend network and data communications hardware and software.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cf578f1d-1832-4a4d-a3b2-e0a00517b51d','key_17654','','','7321','7321.1.3','prepress operator',replace('prelim/prepress Operator\nelectronic pre-press operator\npre-press artworker\nproof-press specialist\nproofer\ndigital pre-press operator\npre-press proofer\nelectronic prepress operator\nprepress operator\nproof-press technician\nproof-press operator','\n',char(10)),'Prepress operators create a prepress proof or sample of what the finished product is expected to look like. They monitor printing quality, ensuring that graphics, colors and content meet the required quality and technical standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cf74a14f-f1fe-4008-a935-5c173f246655','key_17655','','','8159','8159.5','mattress making machine operator',replace('mattress making machine operative\nmattress making machine worker\nmattress factory machine operator\nmattress forming machine operative\nmattress factory machine worker\nmattress factory operative\nmattress factory worker\nmattress forming machine setter\nmattress making machine setter\nmattress making machine operator','\n',char(10)),'Mattress making machine operators utilise machines to form mattresses. They create pads and coverings and cut, spread and attach the padding and cover material over the innerspring assemblies.','','Excludes people performing the manufacturing of inflatable rubber mattresses and rubber waterbed mattresses.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cf7b89d0-1cf9-4567-a8bf-9d6905a2b31b','key_17656','','','2652','2652.3.1.1','choirmaster/choirmistress',replace('choirmistress\nchoir director\nchoral director\nchoir leader\nchoirmaster/choirmistress\nchoirmaster','\n',char(10)),'Choirmasters/choirmistresses manage various aspects of the vocal, and sometimes instrumental, performances of musical groups, such as choirs, ensembles, or glee clubs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cf9b6da9-3a59-4780-bec9-500b148b8878','key_17657','','','2269','2269.6','podiatrist',replace('podiatrist\nspecialist podiatrist\nspecialist in chiropody\nexpert podiatrist\nchiropodist\nspecialist in podiatry\nspecialist in podology\nexpert in podiatry\nspecialist chiropodist\nspecialist podologist\npodiatric physician','\n',char(10)),'Podiatrists are foot specialists who study foot physiology and pathology from a structural and functional standpoint. They assess, diagnose, and treat physical, sporting injuries, rehabilitation, medical and surgical problems of the foot and lower limb in their clinical practice. Podiatrists are active outside the clinical environment in research, medico-legal, and forensic activities. Podiatrists act within the scope of their podiatry training, experience, and regulating authority of their member country.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/cfda19c9-c7e1-497d-a18b-66e29c773745','key_17658','','','1349','1349.2','airspace manager',replace('air traffic and airspace manager\nairspace manager\nairspace and air traffic manager\nairspace management officer\nmanager of airspace','\n',char(10)),'Airspace managers control activities aimed at developing the European airspace into a continuum that is flexible and reactive to changes of users’ needs in airspace. They aim to optimise the network capacity and improve the performance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d00b4607-c4fe-4f3e-8974-29401e23223c','key_17659','','','1213','1213.2.1','social security administrator',replace('administrative officer\ncommunity services administrator\nsocial security administrator\nsocial security advisor\nbenefits advisor\nbenefits officer\ncommunity services director','\n',char(10)),'Social security administrators direct and develop government-provided social security programmes in order to aid public welfare, as well as promote social security programmes. They supervise staff working in governmental social security, and investigate existing policies in order to assess issues and develop improvement proposals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d0341f2c-f797-4306-b847-411dee1058a9','key_17660','','','5241','5241.1','art model',replace('life nude model\nlife model\nart model\nnude art model\nartist''s model\nartists'' model','\n',char(10)),'Art models pose for visual artists as reference or inspiration for their creative work. They stand model for artists that perform figure drawing, paint, make sculptures or create photographic art. Art models are professional models that use their body to become the object of the artist''s creation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d051a141-92cd-4800-b0ee-6c8c31ea5838','key_17661','','','7233','7233.2','crane technician','crane technician','Crane technicians assemble industrial and harbor cranes components. They install conveyors and controls. Crane technicians perform the final assembly on site and maintain and repair cranes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d07a4984-e775-46a7-b967-a3d0b3a54268','key_17662','','','7224','7224.1','metal polisher',replace('circle finisher\nair bag finisher\nmetal buffer\nbed finisher\neco pickled surface operator\nhand buffer\ntube buffer\ncircle buffer\nsmooth clean surface operator\nmachine buffer\ntube finisher\nmechanical buffer\nmachine finisher\nbed buffer\nmetal polisher\nautomatic metal finisher\nbuffer operator\nmechanical finisher\npickling operator\nair bag buffer\nbuffer\nhand finisher\nautomatic metal buffer','\n',char(10)),'Metal polishers use metal working equipment and machinery to polish and buff almost finished metal workpieces in order to enhance their smoothness and appearance and to remove oxidisation, tarnishing the metal after the other fabrication processes. They may operate equipment using diamond solutions, silicon-made polishing pads, or working wheels with a leather polishing strop, and tend to these materials ensuring their effectiveness.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d07e6a57-a339-4cfd-bf22-a951713148a3','key_17663','','','7422','7422.6','telecommunications equipment maintainer',replace('telecommunications equipment maintainers\ntelecommunications equipment maintainer\ntelecoms equipment maintenance technician\ntelecoms equipment maintenance engineer\ntelecoms equipment maintainer\ntelecom equipment maintainer\ntelecommunications maintenance technician','\n',char(10)),'Telecommunications equipment maintainers repair, install or maintain mobile or stationary radio transmitting, broadcasting, and receiving equipment, and two-way radio communications systems (cellular telecommunications, mobile broadband, ship-to-shore, aircraft-to-ground communications, radio equipment in service and emergency vehicles). They also focus on communication towers, antennas, amplifiers and connectors. They may test and analyse network coverage.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d08b4700-758c-467d-ab77-d5a8029c4070','key_17664','','','3323','3323.4','standalone public buyer',replace('public buyer\nprocurement expert\npublic procurer','\n',char(10)),'Standalone public buyers manage the procurement process and cover all of the procurement needs for a small contracting authority. They are involved in each stage of the procurement process and collaborate with professionals from other departments of the organisation to find the kinds of specialised knowledge that may not be available. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d0aa0792-4345-474b-9365-686cf4869d2e','key_17665','','','2512','2512.3','software architect',replace('software architects\nsoftware architect\nsoftware designer\napplication architect','\n',char(10)),'Software architects create the technical design and the functional model of a software system, based on functional specifications. They also design the architecture of the system or different modules and components related to the business'' or customer requirements, technical platform, computer language or development environment.','','Excludes people performing programming and coding activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d0c923f1-e32b-4838-a109-eac58945dab1','key_17666','','','7312','7312.6.3','violin maker',replace('violin maker\nviolin craftsman\nviolin assembler\nviolin builder\nviolin craftswoman','\n',char(10)),'Violin makers create and assemble parts to create violins according to specified instructions or diagrams. They sand wood, measure and attach strings, test quality of strings and inspect the finished instrument.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d0d7d777-073f-4c33-8f27-1d4a0e8e626f','key_17667','','','3139','3139.3','pulp control operator',replace('pulp control operator\npulp production assistant\npulp and paper producer\npulp production operator\npulping control operator\npulp control worker\npulping production operator\npulp producer\npulp paper operator\npulping production assistant\npulping control worker','\n',char(10)),'Pulp control operators operate and monitor multi-function process control machinery and equipment to control the processing of wood, scrap pulp, recycable paper and other cellulose materials in the production of pulp. They set up, operate and maintain the machinery, analyse the production results and adjust the process when necessary.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d0e85fe6-78e3-4218-b21a-99b75438b775','key_17668','','','1346','1346.2','brokerage firm director',replace('brokerage firm chief executive officer\nbrokerage firm director\nbrokerage managing director\nbrokerage firm chief executive\nbrokerage senior manager\nbrokerage firm executive manager\nbrokerage firm managing director\nbrokerage firm supervisor\nbrokerage supervisor\nbrokerage chief executive','\n',char(10)),replace('\nBrokerage firm directors organise the activities and the people involved in securities trading. They envision strategies aimed at increasing the efficiency of asset trading with a focus on profitability. They may also advise clients on appropriate trades.','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d117ef65-b253-4f04-bc85-353ef853443a','key_17669','','','2131','2131.8.2','cytology screener',replace('cervical cytology screener\nscreener in cytology\ncytoscreener\nspecialist cytology screener\ncytotechnologist\nexpert cytology screener\ncytology screener\ncytology practitioner\nsenior cytology screener','\n',char(10)),replace('Cytology screeners examine under a miscrocope samples of human cells obtained from various body parts such as the female reproductive tract, the lung or gastrointestinal tract, assist in identifying cell abnormality and disease such as cancer or infectious agents under supervision, following the orders of the doctor of medicine. The abnormal cells are being transferred to the pathologist for medical diagnosis. They may also work under the supervision of a biomedical scientist. They do not treat patients or assist in medical treatments. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d185c066-cddf-4a8c-a931-5eefc21fc2a1','key_17670','','','2320','2320.1.8','cabin crew instructor',replace('inflight service instructor\nflight service instructor\ntrainer of cabin crew\ninstructor of cabin crew\ncabin service instructor\nflight attendant trainer\ncabin crew instructor\ncabin crew trainer\nflight attendant instructor','\n',char(10)),'Cabin crew instructors teach trainees all the matters regarding the operations in aircraft cabins. They teach, depending on the type of airplane, the operation carried out in the aircraft, the pre and post flight checks, the safety procedures, the service equipment, and client service procedures and formalities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d18a8976-a283-42ad-9541-75c49396dbd7','key_17671','','','4212','4212.6','lottery operator','lottery operator','Lottery operators run the day-to-day functions of lotteries. They verify and enter data into the system, prepare reports and assist the forwarding of company equipment. They operate the communication tools used. Operators install, tear down and maintain equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d18deca3-3063-4970-9ddc-e3c70404a28c','key_17672','','','3251','3251.2','dental hygienist',replace('dental therapist\noral health practitioner\noral health specialist\ntrainee dental hygienist\nregistered dental hygienist\northodontic therapist\ndental hygienist\nqualified dental hygienist\noral health therapist','\n',char(10)),'Dental hygienists work in cleaning and polishing teeth, supra- and sub-gingival scaling of the teeth, application of prophylactic materials to the teeth, collecting data, administering comprehensive advice on oral hygiene and the care of the mouth, according to patients` needs under the supervision of dental practitioners following her/his directions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d1974e0c-8f92-473b-a32d-f0616c08b1ff','key_17673','','','7222','7222.4','precision mechanic',replace('precision operations mechanic\nprecision engineering supervisor\nprecision process engineer\nprecision processing engineer\nprecision mechanic\nprecision engineering operations supervisor\nprecision mechanical engineer\nmechanical precision engineer','\n',char(10)),'Precision mechanics manufacture precision metal components for machines and assemble them into functional units. They also build electronic measuring and control components. Precision mechanics use milling, drilling, grinding and honing machines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d19932b3-169a-4cdf-a9cd-bdf8857b0184','key_17674','','','2433','2433.6.7','technical sales representative in the textile machinery industry',replace('textile industry machinery  field saleswoman\ntextile industry machinery inside technical sales representative\ntextile industry machinery field salesperson\nsenior technical sales engineer in the textile industry machinery\nassistant technical sales engineer in the textile industry machinery\ntechnical sales representative in the textile machinery industry\ntrainee technical sales representative in the textile industry machinery\nassistant technical sales representative in the textile industry machinery\nsenior technical sales representative in the textile industry machinery\ntextile industry machinery  field salesman\ngraduate technical sales representative in the textile industry machinery\ntextile industry machinery outside technical sales representative\ntrainee technical sales engineer in the textile industry machinery\ntextile industry machinery technical field sales representative\ngraduate technical sales engineer in the textile industry machinery','\n',char(10)),'Technical sales representatives in the textile machinery industry act for a business to sell its merchandise while providing technical insight for customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d1a554d0-7ad6-4d42-897e-ed31d022fb0b','key_17675','','','2163','2163.1.3.1','clothing fashion designer',replace('couturier\nclothing designer\nclothing product designer\nclothes designer\nclothing fashion designer','\n',char(10)),'Clothing fashion designers create concepts and make sketches of their creative ideas by hand or by using software. They analyse and interpret fashion trends in order to propose new ideas with high aesthetical value. They perform forecasting and market research to put together collections. They build collection lines by operating mood or concept boards, colour palettes, materials, drawings and sketches considering among others ergonomical criteria, etc.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d1b1dfdf-7d60-430a-b569-7dd71b2ebeaa','key_17676','','','2163','2163.1.2','costume designer',replace('costume designer\ncostume designer & stylist\nset & costume designer\nwardrobe specialist\ncostume designer and stylist\ncostume & set designer\ncostume maker & designer\ncostume and set designer\ncostume designer and fashion stylist\nfashion stylist and costume designer\nfashion stylist & costume designer\nset and costume designer\ncostume consultant\ncostume designer & maker\ncostume maker and designer\ncostume designer & fashion stylist\ncostume specialist\ncostume designer and maker','\n',char(10)),replace('Costume designers develop a costume design concept for events, a performance, a movie or a television program. They supervise the execution of it. Their work is based on research and artistic vision. Their design is influenced by and influences other designs and must be conform with these designs and the overall artistic vision. Therefore, the designers work closely with artistic directors, operators and the artistic team. Costume designers develop sketches, design drawings, patterns or other documentation to support the workshop and performance crew. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d1b8c046-3867-423a-84fd-41f7be3a6d29','key_17677','','','3259','3259.2','assistant clinical psychologist',replace('assistant psychologist\npsychology assistant\npsychological assistant\npersonality assessment coordinator\nassistant clinical psychologist','\n',char(10)),'Assistant clinical psychologists aid psychologists in their work. They work in healthcare facilities or private practices where they assist psychologists with their treatment of patients. Assistant clinical psychologists are healthcare professionals that can assess patients with psychological tests and aid with therapy, as well as performing administrative functions.','','Excludes <a resource="http://data.europa.eu/esco/occupation/52ded7d7-11df-42e3-b90a-d7f4b70fb4b9" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/52ded7d7-11df-42e3-b90a-d7f4b70fb4b9"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">psychologist</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d1fc31d2-38e1-4000-85b7-3f82d2fbed9e','key_17678','','','5169','5169.1','club host/club hostess',replace('promo boy\nparty host\nparty hostess\npromo girl\npromo worker\nnight club worker\ndrinks host\nvip host\ndrinks hostess\nvip hostess\nbar worker\nclub host/club hostess','\n',char(10)),'Club host/hostess welcome customers to the club. They ensure that clients are well looked after and satisfied.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d2078576-9c43-40f7-bf52-356f53d650fd','key_17679','','','7531','7531.3','hide grader',replace('crust grader\nwet-blue grader\nskin grader\ncrust sorter\nhide inspector\nhide assorter\nhide grader\nhide graders\nraw hide grader','\n',char(10)),'Hide graders sort hides, skins, wet blue, and crust depending on the natural characteristics, category, weight and also magnitude, location, number and type of defects. They compare the batch to specifications, provide an attribution of grade and are in charge of trimming.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d20fd82a-9d99-4335-ad83-87484e297a46','key_17680','','','6122','6122.1','poultry breeder',replace('poultry breeder\npoultry specialist\nchicken breeder\npoultry rearer','\n',char(10)),'Poultry breeders oversee the production and day-to-day care of poultry. They maintain the health and welfare of poultry.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d21890a3-cbe9-49df-9a19-a4120d866548','key_17681','','','2619','2619.10','notary',replace('notary signing agent\ncivil law notary\npublic notary\nnotarial officer\nlaw signing agent\nnotary\ncommon law notary ','\n',char(10)),'Notaries ensure the authenticity and legitimacy of official documents such as declarations, certificates, contracts, deeds and purchases. They examine the documents, witness the signing and authenticate them. They administer oaths and affirmations and perform other acts of notarisation.','',replace('Excludes people performing the transfer of legal properties and titles.\nExcludes <a resource="http://data.europa.eu/esco/occupation/a9411821-4f54-406e-ba37-4c6e5a39f9fe" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/a9411821-4f54-406e-ba37-4c6e5a39f9fe"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">conveyance clerk</span></a>.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d23f4d59-2bea-4bd8-bcdb-5e23b1c50552','key_17682','','','1345','1345.1.3','education programme coordinator',replace('education programme director\neducation program coordinator\ninternational education programme coordinator\neducation program director\neducation & training consultant\ninternational education program coordinator\neducation and training consultant\neducation programmes manager\neducation programs manager','\n',char(10)),'Education programme coordinators supervise the development and implementation of educational programmes. They develop policies for the promotion of education and manage budgets. They communicate with education facilities to analyse problems and investigate solutions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d23fbcdc-d5e9-4724-a83f-99a0cfa8adb0','key_17683','','','2652','2652.4','répétiteur',replace('music répétiteur\nmusic repetiteur\nopera répétiteur\nrépétiteur\nopera repetiteur\npiano accompanist\naccompanist\nrepetiteur\nrehearsal accompanist','\n',char(10)),'Répétiteurs accompany performers, usually singers, following the instructions of musical conductors in directing rehearsals and guiding the artists in the rehearsal process.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d261a6a4-7ea7-4295-a84e-f4b1a56990d7','key_17684','','','3334','3334.3.1','letting agent',replace('leasing broker\nestate agent\nrental agent\nreal estate agents\nestate agents\nleasing consultants\nrental agents\nleasing agents\nreal estate leasing specialist\nletting consultant\nleasing agent\nreal estate leasing specialists\nreal estate leasing consultant\nreal estate leasing consultants\nreal estate agent\nletting agent\nleasing consultant\nletting consultants','\n',char(10)),'Letting agents schedule appointments with clients in order to show and lease real estate to prospective residents. They assist in marketing the property for rent through advertising and community out-reach. They are also involved in daily communication and administrative tasks.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d26a941d-443a-4301-a648-3cb3a4427775','key_17685','','','3134','3134.2','gas processing plant operator',replace('gas distribution plant operator\ngas distribution system operative\ngas distribution plant maintenance operator\nnatural gas transmission system operative\ngas distribution system operator\ngas distribution pipeline operator\ngas transmission system operative\ngas distribution operator\ngas distribution network operator\ngas processing plant operator\ngas distribution system worker','\n',char(10)),'Gas processing plant operators operate and maintain distribution equipment in a gas distribution plant. They distribute gas to utility facilities or consumers, and ensure the correct pressure is maintained on gas pipelines. They also oversee compliance with scheduling and demand.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d2af4f6b-c990-4d8e-bb17-befca23a307a','key_17686','','','3115','3115.1.14','optomechanical engineering technician',replace('technologist in optomechanical systems\ntechnician in optomechanical systems\ntechnologist in optical systems\noptomechanical technologist\nassistant in optomechanical engineering\noptomechanical technician\noptomechanical engineering assistant\noptomechanical engineering technician','\n',char(10)),'Optomechanical engineering technicians collaborate with engineers in the development of optomechanical devices, such as optical tables, deformable mirrors, and optical mounts. Optomechanical engineering technicians build, install, test, and maintain optomechanical equipment prototypes. They determine materials and assembly requirements.','','Excludes people performing engineering activites related to optoelectronics.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d2d451cc-e839-4192-be54-d7724814aa03','key_17687','','','2412','2412.2','corporate banking manager',replace('cash management officer\ncorporate banking officer\ncorporate banking centre manager\ncash management adviser\ncorporate banker\ncash management manager\ncommercial banking accounts officer\ncorporate banking manager\nbusiness banking officer','\n',char(10)),'Corporate banking managers offer advice on a broad range of financial goods and services such as securities services, credit services, cash management, insurance products, leasing, information on merges and acquisitions and capital markets activities, to institutions and organisations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d2db926b-8b14-4686-9c98-fb0dc3a38cbb','key_17688','','','2431','2431.7','creative director',replace('creative director\nadvertising director\ncreative manager\nexecutive creative director\ncreative coordinator\ncreative services manager\ndesign director','\n',char(10)),'Creative directors manage the team that is responsible for the creation of advertisements and commercials. They oversee the whole creation process. Creative directors pitch the designs of their team to the client.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d2df16f3-87d7-4c6e-a640-95cee39a8f4e','key_17689','','','2145','2145.1.3','cider master',replace('cider master\ncider maker\ncider production manager\ncider manufacture manager\nmaster cider maker','\n',char(10)),'Cider masters envision the manufacturing process of cider. They ensure brewing quality and follow one of several brewing processes. They modify existing brewing formulas and processing techniques in order to develop new cider products and cider-based beverages.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d2e63d7e-2605-4af1-bb7d-b1ed9ad08825','key_17690','','','3314','3314.2','statistical assistant',replace('information technology expert\nstatistician''s assistant\nstatistical assistant\nstatistician assistant\nstatistical report compiler\nstatistician\nadmin assistant\nstatistics assistant\nadministrative assistant\nIT expert\ncomputer expert\nreport compiler\nstatistics compiler','\n',char(10)),'Statistical assistants collect data and use statistical formulas to execute statistical studies and create reports. They create charts, graphs and surveys.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d2ff134e-b41a-4fa3-aceb-1b9f5f5cf015','key_17691','','','1349','1349.21.1','legal service manager',replace('legal office head\nhead of the legal services office\nlegal department director\nlegal service manager\nlegal practice manager\nlegal firm manager\nlegal department chief','\n',char(10)),'Legal service managers oversee the general management of a legal service office. They not only strive for the highest efficiency and effectiveness when delivering legal services and advice, but they also coordinate a team of legally trained professionals. They manage different profiles of clients and adjust the legall services to their needs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d3382c5a-589f-453d-a6d8-d28695057079','key_17692','','','8131','8131.7','fermenter operator',replace('chemicals fermenter operative\nchemicals fermenter operator\nfermenter operative\nfermenter operator\nfermentation worker\nfermentation operative\nfermentation process worker\nchemicals fermentation operative\nchemicals fermentation process worker\nchemicals fermentation worker\nfermenter worker\nchemicals fermenter worker\nchemical fermentation process worker\npharmaceutical fermenter operator','\n',char(10)),'Fermenter operators control and maintain the equipment and tanks for the production of active and functional ingredients for pharmaceuticals such as antibiotics or vitamins. They also work in the production of cosmetics or personal care products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d3570716-cc0d-4a61-b9a0-9b3081050e9d','key_17693','','','2642','2642.1.9','entertainment journalist',replace('live event journalist\nlive reporter\nmusic journalist\nentertainment journalist\nfilm & entertainment journalist\narts journalist\nfilm and entertainment journalist\nentertainment reporter\nred carpet reporter\nevent reporter\ntheatre reporter\narts reporter\nfilm journalist\nentertainment correspondent','\n',char(10)),'Entertainment journalists research and write articles about cultural and social events for newspapers, magazines, television and other media. They conduct interviews with artists and celebrities and attend events.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d35f9e79-5778-43b4-8667-0e3ba1fcb19a','key_17694','','','9334','9334.1','shelf filler',replace('senior shelf filler\nshelf stacker\nsupermarket shelf stacker\nshelf stocker\ngarden centre shelf stacker\nsupermarket shelf filler\nshop shelf filler\nrack jobber\ndepartment store shelf filler\ndepartment store shelf stacker\ngarden centre shelf filler\ntrainee shelf filler\noutlet centre shelf stacker\nhypermarket shelf stacker\nfilling station shelf stacker\nfilling station shelf filler\nhypermarket shelf filler\noutlet centre shelf filler\ntrainee shelf stacker\nshop shelf stacker\nsenior shelf stacker','\n',char(10)),replace('Shelf fillers stock and rotate merchandise on shelves, identifying and removing expired products. They clean the shop after its operational hours, ensuring that the shelves are fully stocked for the next day. Shelf fillers may use trolleys, small forklifts to move stock and ladders to reach high shelves. They also give directions to customers in order to locate specific products. \n \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d36b66f8-c08a-4416-953b-62c271ca7509','key_17695','','','6113','6113.2','horticulture production manager',replace('horticultural crop manager\nhorticulture production manager\nhorticultural manager\nhorticulture manager\nhorticultural producer\nhorticultural production manager','\n',char(10)),'Horticulture production managers plan the production, manage the enterprise and participate in the horticultural production.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d372b694-041a-4978-bd81-efbb763ee70d','key_17696','','','2642','2642.1.6','crime journalist',replace('crime journalist\nforensic journalist\ncrime and defence correspondent\ncrime reporter\ncrime & critical events journalist\ncrime writer\ncrime correspondent\nsenior crime journalist\ncrime & defence correspondent\ninvestigative journalist\ncrime and critical events journalist','\n',char(10)),'Crime journalists research and write articles about criminal events for newspapers, magazines, television and other media. They conduct interviews and attend court hearings.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d372e2e4-714a-4127-bb82-6b53eb817410','key_17697','','','3324','3324.4.35','wholesale merchant in wood and construction materials',replace('business to business sales consultant in wood and construction materials\nwholesale buyer in wood and construction materials\nassistant wholesale merchant in wood and construction materials\ntrainee wholesale merchant in wood and construction materials\nsenior wholesale merchant in wood and construction materials\nB2B sales consultant in wood and construction materials\nwholesaler in wood and construction materials\nwholesale merchandiser in wood and construction materials\nwholesale trader in wood and construction materials\nbusiness to business seller in wood and construction materials\ngraduate wholesale merchant in wood and construction materials\nwholesale merchant in wood and construction materials','\n',char(10)),'Wholesale merchants in wood and construction materials investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d37cc820-15f5-4a81-88db-af48b0ee6a32','key_17698','','','1345','1345.1.7','secondary school department head',replace('high school department head\nhead of department in high school\nhigh school head of department\nhead of department in secondary school\nsecondary school head of department\nschool department head','\n',char(10)),'Secondary school department heads manage and supervise their assigned departments to ensure that students are instructed and supported in a safe learning environment. They work closely with the secondary school principal to lead and assist school staff and to optimise communication between school management and teachers, parents, and other districts and schools. They facilitating meetings, develop and review curriculum programmes, observe staff when the principal delegates this work, and assume shared responsibility with the principal for financial resource management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d38c2107-98f2-47b7-8bb4-f69750d51082','key_17699','','','5321','5321.1','healthcare assistant',replace('resident care assistant\nassistant healthcarer\nresident healthcare assistant\nelderly care assistant\nprimary healthcare assistant\nmaternity care assistant\nhealthcare assistant\nhealth care assistant\npatient care assistant','\n',char(10)),'Healthcare assistants work in teams of nurses within the occupational fields of nursing, social care, clinical care and care of people of all age-groups. Healthcare assistants assist in the promotion and restoration of patients'' health by providing physical and psychological support to patients, friends and families.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d3ac4c05-79e2-42da-b8c6-e55765aee381','key_17700','','','3331','3331.2.1.33','import export specialist in waste and scrap',replace('senior export specialist in waste and scrap\ngraduate export specialist in waste and scrap\ntrainee export specialist in waste and scrap\nassistant export specialist in waste and scrap\nsenior import export specialist in waste and scrap\ninternational trade compliance specialist in waste and scrap\nassistant import specialist in waste and scrap\ngraduate import export specialist in waste and scrap\nimport export specialist in waste and scrap\ntrainee import specialist in waste and scrap\ngraduate import specialist in waste and scrap\ncustoms compliance specialist in waste and scrap\ntrainee import export specialist in waste and scrap\nsenior import specialist in waste and scrap\nassistant import export specialist in waste and scrap','\n',char(10)),'Import export specialists in waste and scrap have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d3c1c718-a093-4e2e-80a0-2bd331266a8b','key_17701','','','8113','8113.3','oil rig motorhand',replace('rig driller\nrig maintenance technicians\ndrilling equipment operator\noil rig motorhand\noil rig technician\noil field driller\ndrilling equipment technician\noil rig operator','\n',char(10)),'Oil rig motorhands assume responsibility for the engines powering the drilling equipment. They ensure that all other rig equipment operates correctly.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d3de3205-dd5c-4112-86bc-161e41cd77a5','key_17702','','','7543','7543.10.1','cigar inspector',replace('cigar product examiner\ncigar packing inspector\ncigar packing examiner\ntobacco products inspector\ncigar quality tester\ncigar product tester\nquality checker (cigars)\ncigar products quality inspector\ncigar tester\ncigar quality checker\nquality tester (cigars)\ncigar sampler\ncigar checker\ncigar quality inspector\ncigar product checker\ncigar products inspector\ncigar inspector\ncigar examiner\nquality inspector (cigars)','\n',char(10)),replace('Cigar inspectors test, sort, sample and weigh cigars in order to find defects and deviations from the product''s specifications.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d3e32e5e-7f24-48e3-b939-e4f800eb62fb','key_17703','','','2423','2423.3','human resources officer',replace('HR director\nHR rep\nlifelong learning coach\npersonnel director\npersonnel recruiter\nHR supervisor\npersonnel officer\npersonnel administrator\npersonnel assistant\nhuman resources representative\nHR representative\nhuman resources director\npersonnel rep\npersonnel representative\npersonnel supervisor\nHR worker\npersonnel worker\npersonnel manager\nskills trainer\npersonnel development specialist\ncompetencies trainer\nhuman resources officer\npersonnel expert\nhuman resources supervisor\nhuman resources rep\nHR officer\nhuman resources worker\nhuman resource officer','\n',char(10)),'Human resources officers develop and implement strategies that help their employers select and retain appropriately qualified staff within that business sector. They recruit staff, prepare job advertisements, interview and short-list people, negotiate with employment agencies, and set up working conditions. Human resources officers also administer the payroll, review salaries and advise on remuneration benefits and employment law. They arrange for training opportunities to enhance employees'' performance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d3edb8f8-3a06-47a0-8fb9-9b212c006aa2','key_17704','','','2511','2511.3','data analyst',replace('data warehousing analyst\ndata storage analyst\ndata warehouse analyst','\n',char(10)),'Data analysts import, inspect, clean, transform, validate, model, or interpret collections of data with regard to the business goals of the company. They ensure that the data sources and repositories provide consistent and reliable data. Data analysts use different algorithms and IT tools as demanded by the situation and the current data. They might prepare reports in the form of visualisations such as graphs, charts, and dashboards.','','Excludes people performing managerial, engineering, and programming activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d3fd0898-51f7-42a3-8f44-09408fc796f6','key_17705','','','5142','5142.2','beauty salon attendant',replace('sun parlour attendant\nsuntan salon worker\nsolarium attendant\nsolarium worker\ntanning salon attendant\nbeauty salon attendant\nsun parlor attendant\nbeauty salon worker\nsuntan salon attendant\nsun parlor worker','\n',char(10)),'Beauty salon attendants schedule clients'' appointments, greet clients on the premises, give detailed information on the salon''s services and treatments and gather clients'' complaints. They clean the salon regularly and ensure all products are in stock and well deposited. Beauty salon attendants take payments from clients and may sell various beauty products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d41ff08f-1ba9-4e39-b9b5-ad6bfe8f9611','key_17706','','','8160','8160.16','chocolate moulding operator',replace('chocolate mould worker\nchocolate mould operator\nchocolate molding operator\nchocolate moulder\nchocolate mould operative','\n',char(10)),replace('Chocolate moulding operators tend machines and equipment that pour tempered chocolate into moulds to form bars, blocks, and other shapes of chocolate. They monitor machines to ensure that moulds do not jam. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d43e9693-7087-412c-b54c-e9068012630a','key_17707','','','3132','3132.4','wastewater treatment operator',replace('wastewater treatment plant worker\nwater treatment system operative\nwastewater safety operative\nwastewater treatment operator\nwastewater treatment system worker\nwastewater safety tester\ndrinking water treatment system operative\nwastewater quality operative\ndrinking water treatment system worker\ndrinking water treatment system operator\nwastewater quality tester\nwastewater treatment plant operative','\n',char(10)),'Wastewater treatment operators operate equipment used in a water or wastewater plant. They treat and clean drinking water before it is distributed to the consumer and process wastewater to remove harmful substances before returning it to rivers and seas. They take samples and perform tests to analyse the water quality.','','Excludes <a resource="http://data.europa.eu/esco/occupation/d99e2022-cbe1-4aaa-8f9d-7db129d61f0c" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/d99e2022-cbe1-4aaa-8f9d-7db129d61f0c"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">wastewater treatment technician</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d44c5eb0-fc6c-4f4a-9850-f2f29aaac505','key_17708','','','8212','8212.3.2','electromechanical equipment assembler',replace('electromechanical equipment products production operative\nelectromechanical equipment products wirer/assembler\nelectromechanical equipment products assembly operative\nelectromechanical equipment fabricator\nassembler electromechanical equipment products\nelectromechanical equipment products assembly worker\nelectromechanical equipment products wirer\nassembler of electromechanical instruments\nelectromechanical equipment products production worker\nelectromechanical equipment products assembler\nelectromechanical equipment assembler\nelectromechanical equipment production worker\nassembler specialised in electromechanical equipment\nassembler of electro-mechanical equipment\nelectromechanical assembler\nassembler of electromechanical equipment\nfabricator of electromechanical equipment\nelectro-mechanical equipment assembler\nelectromechanical equipment production operative\nelectromechanical instrument assembler\nelectromechanical instrument fabricator','\n',char(10)),'Electromechanical equipment assemblers read and interpret blueprints, drawings and instructions to assemble or modify electromechanical equipment or devices. They inspect and test the completed units to guarantee good working order and compliance with specifications and standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d46a0f9d-4474-46f8-b59d-7970da915b94','key_17709','','','1324','1324.3.1.6.16','hardware, plumbing and heating equipment and supplies distribution manager',replace('trainee hardware, plumbing and heating equipment and supplies distribution manager\nhardware, plumbing and heating equipment and supplies logistics manager\ngraduate hardware, plumbing and heating equipment and supplies distribution manager\nassistant hardware, plumbing and heating equipment and supplies distribution manager\nhardware, plumbing and heating equipment and supplies supply chain manager\nhardware, plumbing and heating equipment and supplies supply chain planner\nhardware, plumbing and heating equipment and supplies distribution planner\nhardware, plumbing and heating equipment and supplies distribution manager\nsenior hardware, plumbing and heating equipment and supplies distribution manager\nhardware, plumbing and heating equipment and supplies logistics planner','\n',char(10)),'Hardware, plumbing and heating equipment and supplies distribution managers plan the distribution of hardware, plumbing and heating equipment and supplies to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d4709722-6f17-4bec-a5f4-1b660e6b28b1','key_17710','','','6221','6221.8','aquaculture mooring manager',replace('aquaculture mooring manager\nfish farm mooring operator\nfishery mooring manager\naquaculture mooring supervisor\nfish farm mooring manager\nfish culture mooring manager','\n',char(10)),'Aquaculture mooring managers carry out and supervise the mooring of cages in stable stations, drifting cages or even self-propelled and semi-submerged cages. They safely operate and moor a variety of different types of large-scale cages, manage conditions such as currents, wave climate and seabed profile, in open or semi-open water areas.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d47d495f-159b-40c9-b3d2-6b651c903e28','key_17711','','','7511','7511.6.2','kosher slaughterer',replace('kosher slaughterperson\nkosher method slaughterer\nkosher slaughterman\nkosher slaughterer\nkosher slaughterwoman\nslaughterer following kosher method','\n',char(10)),'Kosher slaughterers slaughter animals and process carcasses of kosher meat for further processing and distribution. They slaughter animals as stated in Jewish law and according to rituals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d48d4949-73a2-40d7-8350-329cda6b5a9a','key_17712','','','3153','3153.2.6','second officer',replace('inflight engineer\nsecond officer\nflight engineer','\n',char(10)),'Second officers are responsible for monitoring and controlling various aircraft systems including fixed-wing and rotary wing. They work in close coordination with the two pilots during all phases of flight. They make pre-flight, inflight, and post flight inspections, adjustments, and minor repairs. They verify parameters such as passenger and cargo distribution, the amount of fuel, aircraft performance, and appropriate engine speed according to instructions of pilots.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d4cbc865-ad88-4b07-9525-2af3dd31cf0d','key_17713','','','7511','7511.1.2','kosher butcher',replace('kosher meat butcher\nkosher butcher\nkosher meat slicer\nkosher meat boner','\n',char(10)),'Kosher butchers order, inspect and buy meat to prepare it and sell it as consumable meat products in accordance with Jewish practices. They perform activities such as cutting, trimming, boning, tying, and grinding  meats from kosher animals such as cows, sheep and goats. They prepare kosher meat for consumption.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d4ccb6d4-ea97-47e5-a319-b72cffbf0631','key_17714','','','5411','5411.1.3','mine rescue officer',replace('mining rescue officer\nmine emergency officer\nmine response and rescue officer\nmining safety officer\nmine rescue officer\nmine emergency response co-ordinator\nmines rescue officer\nauxiliary mine rescue worker','\n',char(10)),'Mine rescue officers coordinate mine rescue operations and need to be trained to work underground. They are the first line of response in emergency situations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d4d6842b-27f0-4222-85db-c542acf6d434','key_17715','','','2422','2422.12.12','public health policy officer',replace('healthcare admistrator\nhealthcare planner\nhealthcare services planning officer\ngeneral manager healthcare\nhealthcare planning officer\nhealth service manager\ngeneral manager\npublic health policy officer','\n',char(10)),'Public health policy officers develop and implement strategies for the improvement of a community''s health care policy. They advise governments on policy changes and identify problems in current health care policies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d4dd502d-9407-456c-a54a-b733fc9cf68b','key_17716','','','1420','1420.4.14','cosmetics and perfume shop manager',replace('perfume store manager\ncosmetics and toilet articles store manager\ncosmetics and toiletries store manager\nbeauty and aroma shop manager\nmake-up and cosmetics shop manager\nbeauty shop manager\ncosmetics and perfume shop manager\nperfume shop manager\ncosmetics shop manager\nbeauty store manager\ncosmetics and perfume store manager\nbeauty and aroma store manager\ncosmetics store manager\nmake-up and cosmetics store manager\ncosmetics and toiletries shop manager\ncosmetics and toilet articles shop manager','\n',char(10)),'Cosmetics and perfume shop managers assume responsibility for staff and activities in a drugstore. They manage employees, monitor the sales of the store, manage budgets and order supplies when a product is out of supply and perform administrative duties if required.  They sell cosmetics, perfume, medicines and miscellaneous items.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d4edb527-2e5d-440c-9f8c-cd5caa9cc4d6','key_17717','','','3153','3153.3','astronaut',replace('cosmonaut\ncommander astronaut\nastronaut\nshuttle commander\nscience officer\npilot  cosmonaut\nspacecraft pilot\nspacecraft crew member\npilot astronaut\nflight engineer\nspace shuttle crew member\nscientific cosmonaut\nshuttle pilot','\n',char(10)),'Astronauts are crew members commanding spacecrafts for operations beyond low Earth orbit or higher than the regular altitude reached by commercial flights. They orbit the Earth in order to perform operations such as scientific research and experiments, launching or release of satellites, and building of space stations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d4fdb8cb-8928-4047-8166-5feedc3c1a98','key_17718','','','8181','8181.5','dry press operator',replace('presser\ndry press operator\ndry press worker\ndry press tender\ndry-press operator\nbrick and tile dry press operator\nbrick presser\nbrick & tile dry press operator','\n',char(10)),'Dry press operators press dry tempered clay or silica into bricks and other shapes. They select and fix the pressing dies, using rule and wenches. Dry press operators remove the bricks from the press machine and stack them in a specified pattern on the kiln car.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d515e82a-ca9f-4be4-9823-33694dff7304','key_17719','','','2163','2163.2','leather goods product developer',replace('leather product designer and developer\nleatherware engineer\nleather technologist\nleather product development engineer\nleather product technologist\nleather product engineer\nleather goods product developer\nleatherware product developer\nproduct leather goods developer\nleather product designer & developer','\n',char(10)),'Leather goods product developers perform and interface between design and actual production. They analyse and study designer’s specifications and transform them into technical requirements, updating concepts to manufacturing lines, selecting or even designing components and selecting materials. Leather goods product developers also perform the pattern engineering, namely they make patterns manually and produce technical drawings for various range of tools, especially cutting. They evaluate prototypes, performing required tests for samples and confirming the customer’s quality requirements and pricing constrains.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d52db93d-9ae5-47d9-b9dc-eab8aa9f2ba9','key_17720','','','7215','7215.1.1','stagehand',replace('loader & stage hand\nrunner\nstage assistant\nloader and stage hand\nassistant stage technician\nstagehand & loader\nstagehand\nloader & stagehand\nstagehand and loader\nstage hand & loader\nstage hand and loader\nstage hand\ntheatre assistant\nassistant theater technician\ntheater assistant\nstage-hand\nassistant  technician\nloader and stagehand\nassistant theatre technician','\n',char(10)),replace('Stagehands assist stage technicians setting up and preparing equipment for live performance. Their work includes setting up the scenery, lights, sound, props, rigging, and special effects for a production. \n ','\n',char(10)),'','Includes people working in event and rental companies.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d5954a2b-a525-45b7-b6d9-b62efafc6c78','key_17721','','','5162','5162.1','companion',replace('male servant\ncaregiver\ncompanion\nlive-in companion\nservant\nlive in companion','\n',char(10)),'Companions perform housekeeping duties and meal preparation for the people they assist on their own premises such as elderly people or people with special needs or who suffer from an illness. They also provide entertainment activities such as playing cards or reading stories. They may do shopping activities as well as punctual transportation to doctor''s appointments, etc.','','Excludes people performing medical care of the assisted person.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d5a43cd3-230e-46d0-ba05-d67d61e5cfbb','key_17722','','','2166','2166.7','digital media designer',replace('interactive media designer\nmultimedia developer\nnew media designer\ndigital media engineer\ndigital media designers\ndigital multimedia designer\ndigital media designer\ndigital media developer','\n',char(10)),'Digital media designers create and edit graphics, animations, sound, text and video to assist in the creation of integrated multimedia products. They may perform activities related to the web, social networks, augmented reality and virtual reality but exclude the production of music using physical instruments and complex software sound synthesis tools. Digital media designers may program and build websites, mobile applications and other multimedia products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d5ad1da5-cf69-4508-9148-a5338f4ffe0e','key_17723','','','8131','8131.16','perfume production machine operator',replace('perfume production machine tender\nfragrance production worker\nperfume production machine operative\nfragrance factory worker\nperfume production machine operator\nfragrance production machine operative\nperfume factory worker\nperfume plant worker\nperfume production machine worker\nfragrance production machine tender\nfragrance production machine worker\nfragrance plant worker\nperfume production worker','\n',char(10)),'Perfume production machine operators tend machinery for the manufacturing of perfumes by setting up the machines and tools, cleaning and maintaining the equipment and abiding by the production schedule.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d5c4ab26-c293-4f4d-ad89-fe776f49a67f','key_17724','','','2166','2166.5','digital artist',replace('digital compositor\ndigital games artist\ndigital sculptor\ncomputer-generated imagery artist\ndigital designer and animator\ncomputer artist\ndigital artist\ndigital matte painter\nspecial effects digital artist\ndigital environment artist\ndigital scupltress\ndigital matte artist\n3D digital artist\n3D digital matte painter','\n',char(10)),'Digital artists create art which applies digital technology as an essential part of the creative process. Digital art is usually created using computers or more specialised digital equipment. It may be enjoyed using the same instruments, shared over the internet, or presented using more traditional media.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d5ce0c23-6f8a-459f-9bf9-a7cb9768740a','key_17725','','','5142','5142.9','personal stylist',replace('fashion colour analyst\nwardrobe consultant\nfashion consultant\npersonal style advisor\npersonal style coach\ncorporate image consultant\nimage consultant\npersonal stylist\nfashion advisor\npersonal wardrobe consultant\npersonal stylist professional','\n',char(10)),'Personal stylists assist their clients in making fashion choices. They advise on the latest fashion trends in clothing, jewellery and accessories and help their clients choose the right outfit, depending on the type of social event, their tastes and body types. Personal stylists teach their clients how to make decisions regarding their overall appearance and image.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d5db9d5c-2ebf-4a54-a79a-1b7e7ff70471','key_17726','','','5131','5131.2','waiter/waitress',replace('waiting staff\nevent server\nwaiter/waitress\nwaitress\ntable waiter\nwait staff\nwaiter\nchef de rang','\n',char(10)),'Waiters/waitresses supply guests with food and drinks as requested. Waiters/waitresses usually work in restaurants, bars and hotels. This involves the preparation of tables, serving food or beverages and taking payments.','',replace('Excludes  <a resource="http://data.europa.eu/esco/occupation/53ed8787-3423-4843-8c63-7be1877d29e2" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/53ed8787-3423-4843-8c63-7be1877d29e2"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">head waiter/head waitress</span></a>.\n Excludes <a resource="http://data.europa.eu/esco/occupation/2f5e38a0-6578-4665-bdbd-e2ab505be140" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/2f5e38a0-6578-4665-bdbd-e2ab505be140"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">bartender</span></a>.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d5eb6150-bbff-4a9c-9d0c-21eab4dbe2b6','key_17727','','','1412','1412.1','restaurant manager',replace('bar manager\nbeverage supervisor\nrestaurant food and beverage manager\nrestaurant manager\ncatering manager\nfood and drinks manager\ncafe manager\nfood and beverages manager\nF & B manager\nfood & beverage supervisor','\n',char(10)),'Restaurant managers are in charge of managing food and beverage operations in the kitchen and other food and beverage outlets or units in a hospitality establishment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d63789ac-74b5-40e3-b890-685513c38e12','key_17728','','','1111','1111.3','governor',replace('state governor\nprovincial governor\nprefect\nregional governor\nviceroy','\n',char(10)),'Governors are the main legislators of a nation''s unit such as a state or province. They supervise staff, perform administrative and ceremonial duties, and function as the main representative for their governed region. They regulate local governments in their region.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d63b95e6-cf44-479e-bea2-e93498d171d0','key_17729','','','8172','8172.5.5','wood router operator',replace('wood routing machine tender\nCNC wood router setter\nwood router technician\nwood carving machine tender\nCNC wood router operator\nwood router\nCNC wood routing machine operative\nwood carving machine technician\nwood carving machine operator\nCNC wood routing machine tender\nwood carving machine operative\nwood router specialist\nwood router operator\nCNC wood router operative\nwood routing machine setter\nwood carving machine setter\nwood carving machine specialist\nwood routing machine operative\nCNC wood routing machine operator\nCNC wood routing machine setter\nwood routing machine operator','\n',char(10)),'Wood router operators work with industrial routers to cut wood into the desired shape. Routers have a routing head that moves over the wood, going up and down to regulate the depth of the incision. Contemporary industrial wood routers usually are computer controlled for extremely fine and consistent results.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d68ff35f-e34d-4e32-88ab-d6c06a6c25b9','key_17730','','','2166','2166.6','digital games designer',replace('digital games designers\ndigital game designer\nvideo games designer\nvirtual games designer\ndigital games designer\ncomputer games designer','\n',char(10)),'Digital games designers develop the layout, logic, concept and gameplay of a digital game. They focus on playfield design, specification writing, and entry of numeric properties that balance and tune the gameplay.','','Excludes people performing programming and coding activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d694b54f-208a-4154-9fe1-d3d335992467','key_17731','','','3331','3331.2.1.11','import export specialist in electronic and telecommunications equipment',replace('assistant import export specialist in electronic and telecommunications equipment\nsenior export specialist in electronic and telecommunications equipment\nassistant export specialist in electronic and telecommunications equipment\nassistant import specialist in electronic and telecommunications equipment\ncustoms compliance specialist in electronic and telecommunications equipment\ntrainee import specialist in electronic and telecommunications equipment\nimport export specialist in electronic and telecommunications equipment\ntrainee import export specialist in electronic and telecommunications equipment\ninternational trade compliance specialist in electronic and telecommunications equipment\nsenior import specialist in electronic and telecommunications equipment\ngraduate export specialist in electronic and telecommunications equipment\ntrainee export specialist in electronic and telecommunications equipment\nsenior import export specialist in electronic and telecommunications equipment\ngraduate import export specialist in electronic and telecommunications equipment\ngraduate import specialist in electronic and telecommunications equipment','\n',char(10)),'Import export specialists in electronic and telecommunications equipment have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d6a395eb-6664-47f1-9be5-91aa2e8ba87d','key_17732','','','3435','3435.9','fight director',replace('sword fight director\nfight arranger\nhistorical combat instructor\nstage combat instructor\nstage combat coordinator\nfight choreographer\nfight coordinator\nmartial arts fight director\nstage fight coordinator\nfight coach\nfight director\nstage fight instructor','\n',char(10)),'Fight directors coach performers to safely execute fight sequences. They direct fights for performances such as dance, movies and television, circus, variety, and others. Fight directors may have a background in sports such as fencing, shooting or boxing, martial arts such as judo, wushu or karate, or military training.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d6b339ed-8dc9-4281-8c91-c8287eceee40','key_17733','','','5249','5249.2.2.2','rental service representative in video tapes and disks',replace('rental service desk agent in video tapes and disks\nrental sales desk supervisor in video tapes and disks\nrental service worker in video tapes and disks\nrental service assistant in video tapes and disks\nrental service desk supervisor in video tapes and disks\nrental service manager in video tapes and disks\nrental advisor in video tapes and disks\nrental sales worker in video tapes and disks\nrental sales supervisor in video tapes and disks\nrental sales desk agent in video tapes and disks\nrental sales manager in video tapes and disks\nrental sales assistant in video tapes and disks\nrental specialist in video tapes and disks\nrental service team leader in video tapes and disks\nrental sales desk clerk in video tapes and disks\nrental sales team leader in video tapes and disks\nrental service representative in video tapes and disks\nrental service desk clerk in video tapes and disks\nrental service supervisor in video tapes and disks','\n',char(10)),'Rental service representatives in video tapes and disks are in charge of renting out equipment and determining specific periods of usage. They document transactions, insurances and payments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d6bffef4-f09a-4711-9ecb-92ec04353b21','key_17734','','','2635','2635.3.24','substance misuse worker',replace('drug and alcohol social worker\nmentor (drugs and alcohol)\nalcohol outreach worker\nsubstance abuse worker\nsubstance mis-use mentor\naddiction social worker\nsubstance abuse social worker\nsubstance misuse outreach worker\nmentor (drugs and alcahol)\ndrug and alcohol worker\nsubstance misuse adviser\ndrug abuse outreach worker\nsubstance mis-use counsellor\nsubstanse abuse social worker\nsocial worker (substance misuse)','\n',char(10)),'Substance misuse workers provide assistance and counselling to people with substance abuse problems. They monitor their progress, advocate for them, and performe crisis interventions and group therapy. They also assist patients dealing with the consequences of tobacco, alcohol or recreational drugs abuse, which can be problematics as unemployment, physical or mental disorders and poverty.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d6d1af75-7811-4914-b16f-50441bcb0e74','key_17735','','','2146','2146.7','mine health and safety engineer',replace('mining health and safety engineer\nmine health and safety engineers\nindustrial health and safety engineer\nmine safety engineer\nmine health and safety engineer','\n',char(10)),'Mine health and safety engineers develop and implement systems and procedures to prevent employee injury and sickness, improve mine working conditions, reduce health and safety risks and prevent damage to equipment and property.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d6ef013f-57e9-4100-bbe5-3e4930664dee','key_17736','','','2422','2422.12.15','social services policy officer',replace('social services policy researcher\npolicy officer at social services\nsocial policy advisor\nsocial policy officer\nsocial policy researcher\nsocial policy development officer\npolicy officer, social services\nsocial policy analyst\nsocial services policy officer\nsocial policy specialist\nsocial services policy advisor','\n',char(10)),'Social services policy officers research, analyse and develop social services policies and implement these policies and services to improve circumstances of disadvantaged and vulnerable members of society such as children and elderly people. They work in the administration of social services and stay in contact with organisations and other stakeholders and provide them with regular updates.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d71eb4f9-2de3-4955-ba79-7b82ea3fe32c','key_17737','','','3435','3435.25.1','followspot operator',replace('followspot operator\nfollowspot lighting operator\nspotlight operator','\n',char(10)),'Followspot operators control follow spots based on the artistic or creative concept, in interaction with the performers. Follow spots are specialized lighting instruments, designed to follow performers or movements on stage. Movement, size, beam width and color are controlled manually. Therefore, the operators work closely together with the light board operators and performers. Their work is based on instructions and other documentation. Their work can include working on heights, in bridges or above an audience.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d7380260-1386-4e36-98b5-8115ca4a3536','key_17738','','','2141','2141.4.2','production engineer',replace('production engineering expert\nproduction engineering consultant\nagricultural production engineer\nproduction engineering specialist\nproduction engineer\nproductions engineer\nplanning and production support manager\nproduction engineering adviser','\n',char(10)),'Production engineers review and evaluate production performance, perform data analysis and identify under-performing production systems. They search for long or short term solutions, plan production enhancements and process optimizations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d7607255-746f-4ad4-bb4e-812e73d24983','key_17739','','','5163','5163.3','funeral attendant',replace('attendant for funeral organisation\nfuneral home assistant\npallbearer\nfuneral attendant\nmortician''s helper','\n',char(10)),'Funeral attendants lift and carry coffins prior and during the funeral service, placing it in the chapel and into the cemetery. They handle floral offerings around the coffin, direct mourners and assist with storing the equipment after the funeral.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d792974a-077b-44a7-9421-b3ab7fdc7c40','key_17740','','','7223','7223.23','swaging machine operator',replace('swaging machine operative\nforging machine operative\nswaging machine tender\nswaging machine setter\nforging machine setter\nswaging machine operator\nforging machine tender','\n',char(10)),'Swaging machine operators set up and tend rotary swaging machines, designed to alter round ferrous and non-ferrous metal workpieces into their desired shape by first hammering them into a smaller diameter through the compressive force of two or more dies and then tagging them using a rotary swager, a process through which no excess material is lost.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d7a12ff0-7e25-4dab-98c5-1f5a781da8ad','key_17741','','','1324','1324.3.2.17','import export manager in hides, skins and leather products',replace('graduate import export manager in hides, skins and leather products\ntrainee export manager in hides, skins and leather products\nimport export manager in hides, skins and leather products\ninternational trade compliance manager in hides, skins and leather products\nassistant import export manager in hides, skins and leather products\nsenior import manager in hides, skins and leather products\ngraduate import manager in hides, skins and leather products\ntrainee import manager in hides, skins and leather products\nsenior export manager in hides, skins and leather products\nsenior import export manager in hides, skins and leather products\nassistant import manager in hides, skins and leather products\nassistant export manager in hides, skins and leather products\ninternational trade manager in hides, skins and leather products\ncustoms compliance manager in hides, skins and leather products\ngraduate export manager in hides, skins and leather products\ntrainee import export manager in hides, skins and leather products','\n',char(10)),'Import export managers in hides, skins and leather products install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d7a9f9e0-e2f5-4804-a0e0-b21ebb1403ee','key_17742','','','4321','4321.1','inventory coordinator',replace('store coordinator\ninventory coordinator\ninventory supervisor\ninventory associate\ninventory control supervisor\nstock coordinator\nwarehouse coordinator\ninventory taker\ninventory specialist\nstockroom coordinator\ninventory administrator\nwarehouse specialist\ninventory controller','\n',char(10)),'Inventory coordinators keep track of products stored in warehouses for transport to stores, wholesalers and individual customers. They inspect the inventory and maintain paperwork and documents.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d7d986e1-7333-431b-9719-0c5c6939e360','key_17743','','','2142','2142.1','civil engineer',replace('harbour civil engineer\ncivil engineering adviser\ncivil engineering researcher\ninfrastructure project manager\ncivil engineering consultant\ndirector of infrastructure projects\ncivil engineering expert\ncivil engineer\ninvestments civil enginee\nquality assurance civil engineer\ncivil engineering specialist','\n',char(10)),'Civil engineers design, plan, and develop technical and engineering specifications for infrastructure and construction projects. They apply engineering knowledge in a vast array of projects, from the construction of infrastructure for transportation, housing projects, and luxury buildings, to the construction of natural sites. They design plans that seek to optimise materials and integrate specifications and resource allocation within the time constraints.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d7e663b2-f042-47d8-9fda-b5b8ba30ca04','key_17744','','','2144','2144.1.1','aerospace engineer',replace('aeronautical engineer\naerospace engineering expert\naerospace project engineer\nmissile design specialist\naerospace development engineer\nmissile designer\naerostructure engineer\naerospace test engineer\naerospace engineering consultant\naerostructures engineer\nastronautical engineer\nspacecraft engineer\naerospace design engineer\naerospace weapons engineer\naerospace engineering specialist\navionics engineer\nmissile design expert\naerospace engineering adviser\naerospace engineer\naerospace engineers','\n',char(10)),'Aerospace engineers develop, test and oversee the manufacture of flight vehicles such as aircrafts, missiles, and spacecrafts. The field of engineering they are active in, can be divided into two branches: aeronautical engineering and astronautical engineering.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d7f3d76b-23e8-447e-93d5-da13ff9bc102','key_17745','','','8219','8219.4','furniture assembler',replace('furniture assembler\nfurniture assembly technician\nfurniture assembly operative\nwooden furniture assembler\nfurniture assembly worker\nfurniture hand assembler','\n',char(10)),'Furniture assemblers place together all parts of furniture and auxiliary items such as furniture legs and cushions. They may also fit springs or special mechanisms. Furniture assemblers follow instructions or blueprints to assemble the furniture, and use hand tools and power tools.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d811881c-5241-47b1-be29-64ee488d3642','key_17746','','','4323','4323.3','baggage flow supervisor',replace('baggage operations manager\nluggage flow supervisor\nbaggage flow coordinator\nluggage operations supervisor\nbaggage operations coordinator\nluggage operations coordinator\nbaggage operations supervisor\nluggage flow manager\nluggage flow coordinator\nluggage operations manager\nbaggage flow manager\nbaggage flow supervisor','\n',char(10)),'Baggage flow supervisors monitor the flow of baggage in airports to ensure baggage makes connections and arrives at the destinations in a timely manner. They communicate with baggage managers to ensure compliance with regulations and apply solutions. Baggage flow supervisors collect, analyse and maintain records on airline data, passenger, and baggage flow, as well as create and distribute daily reports regarding staff needs, safety hazards, maintenance needs and incident reports. They ensure cooperative behaviour and resolve conflicts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d849fd2a-5210-434b-a50b-2d92de3a4bac','key_17747','','','3118','3118.3.8','electronics drafter',replace('electronics drafting specialist\nelectronics engineering draughter\nelectronics draughtsperson\nelectronics drafting adviser\nelectronic drafter\nelectronics draughter\nelectronic draughtsperson\nelectronics design drafter\ndraughter in electronics\nelectronics draughtswoman\nelectronics design draughter\nelectronics engineering drafter\nelectronics drafting expert\nelectronic draughter\nelectronics drafting consultant\nelectronics drafter\nelectronics draftsperson\ndraughter in electronic equipment\nelectronic draftsperson\ndraughter of electronics\nelectronics draughtsman','\n',char(10)),'Electronics drafters support electronic engineers in the design and conceptualisation of electronic equipment. They draft blueprints and assembly diagrams of electronic systems and components using technical drawing software.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d84bf1d7-ef99-40c4-a407-3791d6531b44','key_17748','','','3522','3522.1','telecommunications engineering technician',replace('communications administrator\ntelecommunications engineering technician\ncommunication administrator\ntelecom administrator\ntelecommunications administrators\ntelecommunications administrator\ntelecoms administrator\ntelecommunication administrator','\n',char(10)),'Telecommunications engineering technician deploy, maintain and monitor a telecommunication system that provides interactions between data and voice communications, such as telephone, video conferencing, computer, and voicemail systems. They are also involved in the design, manufacturing, construction, maintenance and repair of telecommunication systems. Telecommunications engineering technicians provide technical assistance in the research and development of telecommunication equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d85e041f-8306-476e-a2cd-59baa1e61992','key_17749','','','6221','6221.2','aquaculture harvesting technician',replace('aquaculture harvesting inspector\nmarine harvesting inspector\nfish farm harvesting technician\nfisheries harvesting technician\ntechnician in aquaculture harvesting\naquaculture harvesting technician\nwater harvesting inspector\nharvest technician\naquatic harvester\naquaculture harvest technician\nmarine harvesting manager\naquatic harvester operator\nwater harvesting operative\nharvesting technician','\n',char(10)),'Aquaculture harvesting technicians operate the harvesting process of aquatic organisms'' production, managing the complex equipment and machinery used in the harvesting of specific cultured species.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d871af8b-f6d7-4377-b609-d422afe3b5de','key_17750','','','1420','1420.4.18','drugstore manager',replace('drugstore manager\ndrugstore counter manager\nhealthcare shop manager\nchemist''s shop manager\ndrugstore department manager','\n',char(10)),'Drugstore managers are responsible for activities and staff in drugstores that sell items such as magazines, household products, medicines, cosmetics, candy or refreshments. ​They manage employees, monitor the sales of the store, manage budgets and order supplies when a product is out of supply and perform administrative duties if required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d87b1c64-eed1-404c-87de-f9f81c85fbde','key_17751','','','8151','8151.1','cotton gin operator',replace('cotton gin machinist\ncotton gin machine tender\ncotton gin operator\ncotton gin machine operator','\n',char(10)),'Cotton gin operators perform ginning operations for separating cotton fibres from the seeds. They tend bale presses and remove the processed bales from the gin. They perform machine maintenance and ensure smooth running of processing operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d89b23c2-d042-4ca6-a917-109fe5a0b30c','key_17752','','','2351','2351.1','curriculum administrator',replace('education consultant\ncurriculum support administrator\neducation administrator\neducation officer\ncurriculum management administrator\ncurriculum director\ncurriculum developer\ncurriculum enhancement administrator\ncurriculum coordinator\neducation manager\ncurriculum specialist\ncurriculum administrator\nteaching aids specialist\neducation coordinator\ncurriculum manager\ncurriculum development consultant','\n',char(10)),'Curriculum administrators develop and improve the curricula of education institutions. They analyse the quality of existing curricula and work towards improvement. They communicate with education professionals to ensure an accurate analysis. They report on curriculum developments and perform administrative duties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d8e502b4-1be6-4d10-a224-151688f8f0c8','key_17753','','','2165','2165.4','land surveyor',replace('geomatics surveyor\nland surveyor\nsurveying engineer\nsurveyor\nsurveyors','\n',char(10)),'Land surveyors determine, by means of specialised equipment, the distances and positions of points at the surface of sites for construction purposes. They use measurements of the specific aspects of construction sites, such as electricity, distance measurements, and metal structure volumes to create architectural drawings and develop construction projects.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d8e5410b-5199-403f-8cbd-d32efb17a296','key_17754','','','1420','1420.4.11','computer shop manager',replace('consumer electronics shop manager\ncomputer and peripheral units store manager\nIT store manager\nIT shop manager\nICT shop manager\ncomputer superstore manager\ncomputer and accessories shop manager\nICT superstore manager\ncomputer hardware shop manager\nIT superstore manager\ncomputer store manager\nICT store manager\nconsumer electronics store manager\ncomputer shop manager\ncomputer and peripheral units shop manager\ncomputer hardware store manager\ncomputer and accessories store manager','\n',char(10)),'Computer shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d8ed1703-9c03-4ea0-83cc-b6169bc54ffd','key_17755','','','4416','4416.1','human resources assistant',replace('human resources secretary\nhuman resources executive assistant\nhuman resources associate\nhuman resources administrative assistant\nhuman resources assistant','\n',char(10)),'Human resources assistants provide support in all the processes and efforts carried by human resources managers. They help in the preparation of recruitment processes by scanning CVs and narrowing the selection to the most suitable candidates. They perform administrative tasks, prepare communications and letters, and perform the tabulation of the surveys and assessments carried out by the department.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d8ff83c0-be8b-47f9-93e3-b13e261e7da5','key_17756','','','2114','2114.1.7','mud logger',replace('logging geologist\nmud logging technican\nmudlogger\nsurface logging technician\nmud logger\nsurface logger\nsurface logging specialist','\n',char(10)),'Mud loggers analyse the drilling fluids after they have been drilled up. They analyse the fluids in a laboratory. Mud loggers determine the position of hydrocarbons with respect to depth. They also monitor natural gas and identify lithology.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d9004a14-bda2-4c63-ab64-17ca163ac1bd','key_17757','','','2422','2422.11','parliamentary assistant',replace('MP''s PA\npolitical assistant\nMLA''s assistant\nMSP''s parliamentary assistant\nMSP''s assistant\nparliamentary intern\nMP''s parliamentary assistant\nMP''s personal assistant\nMEP''s assistant\nparliamentary assistant\npolitician''s assistant\nMP''s assistant\nMLA''s parliamentary assistant\nAM''s assistant\nexecutive officer\nconstituency assistant\nAM''s parliamentary assistant','\n',char(10)),'Parliamentary assistants provide support to officials and politicians of regional, national and international parliaments and undertake logistical tasks. They revise official documents and follow procedures set by the respective parliaments. They support on the communication with stakeholders and provide the logistical support required in handling official processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d907b046-682b-45fa-bdd0-d2ed7a382670','key_17758','','','3331','3331.2.1.32','import export specialist in tobacco products',replace('graduate export specialist in tobacco products\nassistant import specialist in tobacco products\ntrainee import export specialist in tobacco products\nimport export specialist in tobacco products\nassistant import export specialist in tobacco products\nsenior import export specialist in tobacco products\nsenior export specialist in tobacco products\ngraduate import export specialist in tobacco products\ntrainee export specialist in tobacco products\ngraduate import specialist in tobacco products\ntrainee import specialist in tobacco products\nassistant export specialist in tobacco products\ncustoms compliance specialist in tobacco products\ninternational trade compliance specialist in tobacco products\nsenior import specialist in tobacco products','\n',char(10)),'Import export specialists in tobacco products have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d954fd71-0e2f-4cf9-85f0-c040fa6cbda9','key_17759','','','1221','1221.3.3','promotion manager',replace('sales promotion manager\npromotion director\npromotions coordinator\npromotions director\npromotion coordinator\npromotion manager\npromotions manager\nadvertising and promotion manager\nmarketing and promotion manager','\n',char(10)),'Promotion managers plan and implement promotional programs in the point-of-sale of products. They coordinate all efforts from personnel, below-the-line (BTL) advertising material, and conventional advertising efforts in order to raise awareness of a specific promotion.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d9612966-e295-4d81-ba2b-9f36bad0b392','key_17760','','','2424','2424.1','business coach',replace('professional development coach\nleadership coach\nbusiness development coach\npractitioner of business coaching\ncoach in business development\nbusiness leadership coach\npersonal development coach\nbusiness coach\ncorporate coach\nexecutive coach','\n',char(10)),'Business coaches guide employees of a company or other institution in order to improve their personal effectiveness, increase their job satisfaction, and positively impact their career development in the business setting. They do this by leading the coachee (the person who is being coached) to resolution of their challenges by their own means. Business coaches aim to address specific tasks or reach specific goals, as opposed to overall development.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d9632b0a-3c95-4a8a-a15c-c5413beda52e','key_17761','','','1324','1324.3.2.28','import export manager in pharmaceutical goods',replace('import export manager in pharmaceutical goods\ncustoms compliance manager in pharmaceutical goods\nsenior import export manager in pharmaceutical goods\ntrainee import export manager in pharmaceutical goods\nassistant export manager in pharmaceutical goods\ngraduate export manager in pharmaceutical goods\ninternational trade manager in pharmaceutical goods\ngraduate import manager in pharmaceutical goods\ntrainee export manager in pharmaceutical goods\nsenior import manager in pharmaceutical goods\nassistant import manager in pharmaceutical goods\ntrainee import manager in pharmaceutical goods\nassistant import export manager in pharmaceutical goods\ninternational trade compliance manager in pharmaceutical goods\nsenior export manager in pharmaceutical goods\ngraduate import export manager in pharmaceutical goods','\n',char(10)),'Import export managers in pharmaceutical goods install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d96de969-d43d-4082-8559-7fc35484a217','key_17762','','','2412','2412.4.1','relationship banking manager',replace('private banker\npersonal financial advisor\ncommercial banking relationship manager\nrelationship manager\nfinancial products relationship manager\nprivate banking relationship manager\nrelationship banking manager\nclient manager\nrelations banker\nrelationship banker','\n',char(10)),'Relationship banking managers retain and expand existing and prospective customer relationships. They use cross-selling techniques to advise and sell various banking and financial products and services to customers. They also manage the total relationship with customers and are responsible for optimising business results and customer satisfaction.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d9723f33-7faf-4f8a-8a98-6b02d1e10f2b','key_17763','','','2411','2411.1.3','bankruptcy trustee',replace('trustees in bankruptcy\ninsolvency practitioners\ninsolvency trustee\nbankruptcy analyst\nbankruptcy trustee\ntrustee in bankruptcy\ninsolvency practitioner','\n',char(10)),'Bankruptcy trustees administer a client''s bankruptcy case, investigate legal documentation for fraud possibilities and manage the money received from the sale of non-exempt property so as to distribute it to the owed creditors.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d97fd1e8-4c61-459b-82cd-1bd0e792c568','key_17764','','','9312','9312.1.2','rail layer',replace('tramway layer\nrail track layer\nlight rail layer\nrailway line layer\nlayer in railway sites\ntrack machine operator\nrail machine operator\ntrack layer\nrail layer\nrailtrack layer\nlight railtrack layer\nplate layer','\n',char(10)),'Rail layers construct railway tracks on prepared sites. They monitor equipment that sets railroad sleepers or ties, usually on a layer of crushed stone or ballast. Rail layers then lay the rail tracks on top of the sleepers and attach them to make sure the rails have a constant gauge, or distance to each other. These operations are usually done with a single moving machine, but may be performed manually.','',replace('Excludes people performing preparation activites such as bulldozing or scraping. \n Excludes people performing electric power lines installation. \n Includes people performing flash welding. \n Includes people performing laying activities of tram, metro and light rail tracks.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d99e2022-cbe1-4aaa-8f9d-7db129d61f0c','key_17765','','','3132','3132.5','wastewater treatment technician',replace('wastewater treatment monitoring technician\nwastewater safety technician\nwastewater treatment quality technician\nwastewater treatment plant operator\nwastewater safety tester\nwater plant repair technician\nwastewater treatment plant worker\nwater plant monitoring technician\nwater plant measurement technician\nwater plant maintenance technician\nwastewater treatment technician\nwastewater treatment sampling monitoring technician\nsewage plant technician\nwastewater quality tester\nwaste water technician\nwater treatment system technician\nwastewater treatment plant technician\nwater plant quality technician\nsewage treatment plant technician\nwastewater quality technician\nwater plant equipment maintenance technician','\n',char(10)),'Wastewater treatment technicians aid wastewater treatment operators in the operation and maintenance of wastewater treatment equipment, and the purification procedure of wastewater, in sewage plants. They perform repair duties.','','Excludes <a resource="http://data.europa.eu/esco/occupation/d43e9693-7087-412c-b54c-e9068012630a" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/d43e9693-7087-412c-b54c-e9068012630a"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">wastewater treatment operator</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d9ab0f3d-078d-42a7-a502-3e739e0aeff6','key_17766','','','7421','7421.2','avionics technician',replace('avionics systems technician\nline technician\navionics diagnostic technician\navionics maintenance technician\navionics test technician\naircraft instrument technician\navionics repair technician\navionics mechanic\naircraft electronics mechanic\naircraft electrical mechanic\naircraft instrument mechanic\naircraft electromechanic technician\navionics inspection technician\navionics technician\navionics installation technician\naircraft electro-mechanic technician','\n',char(10)),'Avionics technicians install, test, inspect and adjust electrical and electronic equipment, such as navigation, communication and flight control systems in aircrafts and spacecrafts. They carry out maintenance and repair work. They perform functional tests, diagnose problems and take corrective action.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d9e4581a-91b3-47f9-a2d5-70eb542caac5','key_17767','','','8183','8183.4','footwear finishing and packing operator','footwear finishing and packing operator','Footwear finishing and packing operators apply several techniques for assuring an appropriate final appearance of the packed pairs of footwear that are going to be sold. They follow information received from their supervisor about the shoes that are going to be finished, the means and materials to be used and the sequence of operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d9f67d41-5e35-49c1-82a4-05ed94fbc3ab','key_17768','','','3359','3359.8','weights and measures inspector',replace('inspector and tester of weights and measures\ntrading standards officer\ntester of weights and measures\nweights and measures inspector and tester\nregulatory services officer\nweights and measures tester\ntrading standards team leader\ntrading standards supervisor\ninspector of weights and measures\ntrading standards manager\nweights and measures inspector','\n',char(10)),'Weights and measures inspectors ensure adherance to regulations and agreements pertaining to the weights and measures of packaged goods. They investigate and document compliance issues and violations to promote best practice. Weights and measures inspectors confirm that packaged goods are in accordance to agreements such as quantity, content and packaging.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/d9fb82d0-9b07-4c38-b8e2-d146ff8850b9','key_17769','','','5223','5223.7.17','floor and wall coverings specialised seller',replace('wallpaper and accessories salesperson\nwallpaper salesperson\ncarpet superstore salesperson\nfloor and wall coverings specialized seller\ninterior decoration salesperson\ninterior fitting salesperson\ncarpet and rug salesperson\ncarpet salesperson\ncarpet and rug specialised seller\ncarpet specialised seller\ncarpet superstore specialised seller\nwallpaper specialised seller\nfloor and wall coverings salesperson\nwallpaper and accessories specialised seller\ninterior decoration specialised seller\ninterior fitting specialised seller','\n',char(10)),'Floor and wall coverings specialised sellers sell wall and floor coverings in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/da00cd76-22fe-450d-aefc-f4e4a3864171','key_17770','','','8212','8212.3.5','printed circuit board assembler',replace('through-hole technology assembly operative\nassembler of printed circuit boards\nthrough-hole technology assembly operator\nPCB assembler\nprinted circuit board fabricator\nprinted circuit board assembler\nPCB fabricator\nTHT operator\nthrough-hole technology assembler\nPCB assembly operative','\n',char(10)),'Printed circuit board assemblers read blueprints and drawings to assemble printed circuit boards. They use hand- and automated soldering tools and machines to connect electronic components to the board.','','Includes people working with surface-mount technology (SMT) or through-hole technology (THT).','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/da4589ce-7534-4cc1-9ce4-cabf9364b87c','key_17771','','','2643','2643.6.1','localiser',replace('localisation games tester\nlocaliser QA tester\nlocalisation project manager\nlocaliser quality assurance tester\nlocalisation translator\nlocalisation tester\nlocalizer','\n',char(10)),'Localisers translate and adapt texts to the language and culture of a specific target audience. They convert standard translation into locally understandable texts with flairs of the culture, sayings, and other nuances that make the translation richer and more meaningful for a cultural target group than it was before.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/da6220cb-21ee-4076-8796-1e922b44c394','key_17772','','','3115','3115.1.22','vessel engine inspector',replace('electric motor inspector\nnuclear reactor inspector\nmarine steam engine inspector\nmarine engine tester\nfour-stroke diesel engine inspector\nboat engine assembler\nvessel engine inspector\nmarine engine inspector\ngas turbine engine inspector\nliquid natural gas engine inspector\ntwo-stroke diesel engineer inspector\noutboard motor inspector\nLNG engine inspector\ndual fuel engine inspector\nboat engine builder\nmarine engine builder\nboat engine inspector','\n',char(10)),'Vessel engine inspectors inspect ship and boat engines such as electric motors, nuclear reactors, gas turbine engines, outboard motors, two-stroke or four-stroke diesel engines, LNG, fuel dual engines and, in some cases, marine steam engines in assembly facilities to ensure compliance with safety standards and regulations. They conduct routine, post-overhaul, pre-availability and post-casualty inspections. They provide documentation for repair activities and technical support to maintenance and repair centres. They review administrative records, analyse the operating performance of engines and report their findings.','','Excludes <a resource="http://data.europa.eu/esco/occupation/e58eadca-e512-4167-96f1-fff980f117e3" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/e58eadca-e512-4167-96f1-fff980f117e3"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">vessel engine tester</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/daa6197d-dba6-49b5-8223-b31b7a126e4e','key_17773','','','8131','8131.21','wax bleacher',replace('wax pumper\ncandle production process worker\nwax bleacher\ncandle production operative\nbees-wax bleacher\ncandle production operator\ncandle wax bleacher\ncandlewax bleacher\nbeeswax bleacher\ncandle production process operative\nwax bleacher (chemical)\ncandle production worker','\n',char(10)),'Wax bleachers operate and maintain filter presses and tanks, used in wax bleaching, for candle production processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dab60fc6-c16e-4de7-b969-b323784f5a49','key_17774','','','1221','1221.3.2.1.1','category manager',replace('product category manager\nFMCG category manager\nindirect category manager\nmerchandising category specialist\npricing and marketing manager\ncategory development manager\nproduct category specialist\nassistant category manager\non-line category manager\ngraduate category manager\nvendor management manager\ng.m. category manager\ngeneral merchandise category manager\ncategory manager\nplanning and inventory control manager\nsenior category manager','\n',char(10)),replace('Category managers define the sales programme for specific product groups. They research market demands and newly supplied products. \n \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dab9c6dd-8efb-4fe2-8b10-477ef2bcfd8b','key_17775','','','2310','2310.1.24','mathematics lecturer',replace('university maths teacher\nmaths professor\nlector in maths\nmathematics docent\nuniversity mathematics lecturer\nmathematics lecturer\nlecturer in math\nlecturer in maths\nmathematics lector\nprofessor of mathematics\nmath lector\nlecturer in mathematics\nprofessor of maths\nhigher education mathematics teacher\nmaths lector\ninstructor in mathematics\nuniversity lecturer in mathematics\nlector in mathematics\nmathematics teacher\nmathematics professor\nlector in math\nmathematics instructor','\n',char(10)),'Mathematics lecturers instruct students who have obtained an upper secondary education diploma in their own specialised field of study, mathematics, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of mathematics, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dacaa18b-93f6-48b4-b4f2-cb33e21804e7','key_17776','','','3116','3116.1.2','chemical manufacturing quality technician',replace('qc technician chemical manufacturing\nQC technician chemical manufacturing\nmanufacturing quality technician\nchemical manufacturing quality technician\nchemical manufacturing quality assurance officer\nchemical manufacturing quality inspector\nchemical production quality technician\nmanufacturing quality technician chemicals\nchemistry quality control technician\nchemical manufacturing quality tester\nchemicals quality inspector','\n',char(10)),'Chemical manufacturing quality technicians perform inspections and precision measurements in order to test and ensure the quality of products, by using computer controlled machinery and systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/db20c08e-819e-4410-91e2-6ac06add4e27','key_17777','','','5169','5169.3','escort',replace('male prostitute\nescort messenger\nsex worker\ngigolo\nlady of the night\nincall girl\nprostitute\nout call girl\nescort\nsocial escort\npatient escort\ncall girl','\n',char(10)),replace('Escorts accompany persons to social events but also on more private occasions, such as dinners or visits of night clubs. They may be in charge of arranging transportation and buying tickets to places of interest. This might include the provision of erotic or sexual services to the client in case it is a legal activity in the respective country. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/db25e659-d77c-4f4a-a8a2-ee199d060345','key_17778','','','8160','8160.45','miller',replace('flour miller\nmiller\nflour mill worker\ngrain mill worker\ngrain miller','\n',char(10)),replace(' Millers tend mills to grind cereal crops to obtain flour. They regulate the flow of materials that go into mills and adjust the grind to a specified fineness. They ensure basic maintenance and cleaning of equipments. They evaluate sample of product to verify fineness of grind. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/db3b1cb9-8460-4de0-8fc7-aff7a9a74b7e','key_17779','','','7316','7316.1.3','porcelain painter',replace('china designer & painter\nchina painter\nporcelain designer & painter\nporcelain designer and painter\nchina decorator\nporcelain decorator\nporcelain painter\nchina designer and painter','\n',char(10)),'Porcelain painters design and create visual art on porcelain surfaces and objects such as tiles and pottery. They use a variety of techniques to produce decorative illustrations ranging from stenciling to free-hand drawing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/db3da11f-67a0-415f-b785-81f33d3be810','key_17780','','','2642','2642.1.3','business journalist',replace('business & finance journalist\nbusiness reporter\nbusiness and economics reporter\nbusiness and economics journalist\nbusiness & economics reporter\neconomic reporter\nforeign business correspondent\nbusiness journalist\nbusiness correspondent\nbusiness & economics journalist\neconomic journalist\nbusiness and finance journalist\nsenior business journalist\nfinancial journalist\nbusiness writer\non-line business journalist','\n',char(10)),'Business journalists research and write articles about economy and economic events for newspapers, magazines, television and other media. They conduct interviews and attend events.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/db4057de-0c5a-4f68-8f81-2c0765978afb','key_17781','','','1349','1349.14','front of house manager',replace('theatre front of house manager\nhead of front of house\ntheater front of house supervisor\ntheatrical house manager\ntheatre front of house supervisor\nvenue house manager\nfront of house manager\nfront of house supervisor\narts venue front of house manager','\n',char(10)),'Front of house managers are responsible for those areas of a live event venue that are accessible to the public. They make sure interaction with the customers or visitors goes smoothly and professionally. Front of house managers are responsible for ticket sales, any refreshments, and make sure spaces accessible to the public are set out properly. They interact with the venue manager and the stage manager.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/db4f28ff-c208-4830-ab8b-4b07776db134','key_17782','','','1349','1349.3','artistic director',replace('orchestra artistic director\nballet director\nart director\nartistic director for dance company\nartistic director\ndirector of programming\nvenue programming director\nartistic consultant\nopera director\nvenue programming manager\ntheatre director\nfestival director','\n',char(10)),'Artistic directors are in charge of the programme of an artistic project or a cultural organisation. They are responsible for the strategic vision, the visibility and the quality of all kind of artistic activities and services such as theatre and dance companies. Artistic directors also manage staff, finances and policies.','','Excludes venue programmer.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/db55bf08-3ad3-4bf6-bf59-7f9f426a6de5','key_17783','','','8153','8153.1.1','clothing alteration machinist',replace('clothing alteration machine operator\nclothing alteration operator\nalterations seamstress\nalterations hand\nclothing alteration machinist\nclothing alteration hand\nalterations tailor\nalterations sewer','\n',char(10)),'Clothing alteration machinists ensure the alteration of finished garments in line with business demands. They are responsible for the quality of any alterations or customisations and brand generic stock in line with customer branding guidelines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dbc88790-a8c1-4dc4-9507-8f9dcff9c990','key_17784','','','2144','2144.1.5','engine designer',replace('engine design technologist\nengine design developer\nengine design technician\nengine design producer\nelectrical engine designer\nengine design quality control supervisor\nengine test engineer\nengine tool production designer\nengine production supervisor\nengines designer\nengine design engineer\nengine production specialist engineer\nengine design specialist\nengine design expert\nengine designer\nengine development engineer\nindustrial engine developer','\n',char(10)),'Engine designers carry out engineering duties in designing mechanical equipment such as machines and all types of engines. They also supervise their installation and maintenance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dbdc9f7b-31c6-4f21-a9c5-48253bb69c08','key_17785','','','3323','3323.2.3','purchase planner',replace('purchasing team worker\ngraduate supply chain planner\ndemand and inventory planner\nsenior buyer planner\ngraduate buyer planner\nassistant purchase planner\npurchase planner\nassistant supply chain planner\nprocurement planner\npurchase planning office worker\ndemand planner\ncompany buyer\npurchasing team leader\ntrainee purchase planner\npurchase planning office manager\ngraduate purchase planner\nsenior purchase planner\nmaterial planner\nsenior supply chain planner\npurchase administrator\npurchasing coordinator\ntrainee supply chain planner\nassistant buyer planner\ntrainee buyer planner','\n',char(10)),'Purchase planners organise the continuous supply with goods out of existing contracts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc14bed4-87ca-4e6c-9f69-458e2689d2d6','key_17786','','','7211','7211.1','foundry operative',replace('metal molder\nfoundry process operator\nfoundry worker\nmetal caster\nmetal moulder\nmould casting worker\nfoundryman\ncasting worker\nladleman\npourer and caster\nfoundry operator\nmolding and casting worker\nfoundry operative\ncasting operator\nmold casting worker\ncaster\nCentral control caster\nladleperson\nmoulding and casting worker m,f,n)','\n',char(10)),'Foundry operative manufacture castings, including pipes, tubes, hollow profiles and other products of the first processing of steel, by operating hand-controlled equipment in a foundry. They conduct the flow of molten ferrous and non-ferrous metals into moulds, taking care to create the exact right circumstances to obtain the highest quality metal. They observe the flow of metal to identify faults. In case of a fault, they notify the authorised personnel and participate in the removal of the fault.','','Includes people performing the manufacture of jewerelly and bijouterie.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc29cd23-d6ba-46b0-bc2f-424e20270099','key_17787','','','1324','1324.3.2.14','import export manager in fruit and vegetables',replace('trainee import manager in  fruit and vegetables\nsenior import manager in  fruit and vegetables\ntrainee export manager in  fruit and vegetables\nsenior export manager in  fruit and vegetables\nassistant import manager in  fruit and vegetables\nsenior import export manager in  fruit and vegetables\ntrainee import export manager in  fruit and vegetables\ncustoms compliance manager in  fruit and vegetables\ngraduate import manager in  fruit and vegetables\ngraduate export manager in  fruit and vegetables\ngraduate import export manager in  fruit and vegetables\ninternational trade compliance manager in  fruit and vegetables\ninternational trade manager in  fruit and vegetables\nimport export manager in fruit and vegetables\nassistant export manager in  fruit and vegetables\nassistant import export manager in  fruit and vegetables','\n',char(10)),'Import export managers in fruit and vegetables install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc2a924c-653a-4922-9226-f6909dd64580','key_17788','','','1324','1324.3.2.2','import export manager in agricultural raw materials, seeds and animal feeds',replace('trainee export manager in agricultural raw materials, seeds and animal feeds\nsenior import export manager in agricultural raw materials, seeds and animal feeds\ninternational trade compliance manager in agricultural raw materials, seeds and animal feeds\nassistant import export manager in agricultural raw materials, seeds and animal feeds\ncustoms compliance manager in agricultural raw materials, seeds and animal feeds\nsenior import manager in agricultural raw materials, seeds and animal feeds\ntrainee import export manager in agricultural raw materials, seeds and animal feeds\ninternational trade manager in agricultural raw materials, seeds and animal feeds\nassistant import manager in agricultural raw materials, seeds and animal feeds\ngraduate import export manager in agricultural raw materials, seeds and animal feeds\ntrainee import manager in agricultural raw materials, seeds and animal feeds\ngraduate export manager in agricultural raw materials, seeds and animal feeds\nassistant export manager in agricultural raw materials, seeds and animal feeds\nsenior export manager in agricultural raw materials, seeds and animal feeds\ngraduate import manager in agricultural raw materials, seeds and animal feeds\nimport export manager in agricultural raw materials, seeds and animal feeds','\n',char(10)),'Import export managers in agricultural raw materials, seeds and animal feeds install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc34c345-caf9-4ab7-b496-e18a572194ba','key_17789','','','5163','5163.2','embalmer','embalmer','Embalmers arrange for the removal of bodies of deceased persons from the place of death and they prepare the bodies for burials and cremations. They clean and disinfect the bodies, use make-up to create the impression of a more natural appearance and hide any visible damage. They are in close contact with funeral services directors in order to comply with the wishes of the deceased family members.','','Includes <a resource="http://data.europa.eu/esco/occupation/857ed740-f4ae-43d0-8295-d6181f1d7eed" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/857ed740-f4ae-43d0-8295-d6181f1d7eed"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">funeral services director</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc3cc282-488d-4d8f-be80-191c6b521823','key_17790','','','7515','7515.4','malt master',replace('malting expert\nmalting master\nmalt master\nmalter\nmalt expert','\n',char(10)),'Malt masters evaluate and grade different malts on a sensory basis for brewing purposes. They assess the look, the smell, and the taste of raw materials and unfinished products in order to maintain the consistency of products. They use their knowledge to prepare mixtures as part of product development.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc52966e-72db-4ad0-b3a9-5e17f1311ab8','key_17791','','','7313','7313.1.6','jewellery repairer',replace('jewellery hammersmith\njewellery repair technician\nadornment repairer\njewelry repairer','\n',char(10)),'Jewellery repairers use specialised hand tools to carry out adjustments and repairs to all types of jewellery pieces. They resize rings or necklaces, reset gems, and repair broken jewellery parts. Jewellery repairers identify the suitable precious metals to be used as replacements, solder and smooth joints. They clean and polish the repaired pieces to be returned to the customer.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc54d531-70b5-4301-bc75-da76c2dfac1a','key_17792','','','3122','3122.3.6','rolling stock assembly supervisor',replace('locomotive production supervisor\nrolling stock production supervisor\ncarriage assembly supervisor\nwagon production supervisor\nrolling stock assembly quality supervisor\nrailway vehicle assembly supervisor\nmultiple unit assembly supervisor\nmultiple unit production supervisor\nrailcar  assembly supervisor\ntraction assembly supervisor\nrail vehicle assembly supervisor\nwagon assembly supervisor\nrolling stock assembly supervisor\nrail vehicle production supervisor\nrailway vehicle production supervisor\ncarriage production supervisor\nrolling stock production supervisor supervisor\nrailcar  production supervisor\nlocomotive assembly supervisor\nrail traction  production supervisor','\n',char(10)),'Rolling stock assembly supervisors coordinate the employees involved in rolling stock manufacturing and schedule their activities. They prepare production reports and recommend measures to reduce the cost and improve productivity such as hiring, ordering new equipment and implementing new production methods. They train employees in company policies, job duties and safety measures. They oversee the supplies and communicate with other departments to avoid unnecessary interruptions of the production process.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc69f2ed-ebe9-43da-bf9d-31aac874d4c2','key_17793','','','2635','2635.3.22','rehabilitation support worker',replace('reablement officer\nrehabilitation worker\nrehabilitation outreach worker\ndisability social worker\noccupational therapy support worker\nrehabilitation support worker\nrehabilitation support specialist\nrehabilitation social worker','\n',char(10)),'Rehabilitation support workers provide counselling to individuals dealing with birth defects or with major consequences caused by diseases, accidents, and burnouts. They help them to cope with personal, social and vocational issues. They assess the personal needs of clients, develop rehabilitation plans, get involved in the training, and help people undergoing a rehabilitation plan with job placement.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc6c0b28-adf7-4558-a987-2ef1506ef2d1','key_17794','','','3331','3331.2.1.5','import export specialist in china and other glassware',replace('assistant export specialist in china and glassware\nsenior export specialist in china and glassware\ntrainee import export specialist in china and glassware\ntrainee import specialist in china and glassware\ngraduate import specialist in china and glassware\ninternational trade compliance specialist in china and glassware\nassistant import specialist in china and glassware\nsenior import specialist in china and glassware\nsenior import export specialist in china and glassware\ngraduate import export specialist in china and glassware\ntrainee export specialist in china and glassware\ncustoms compliance specialist in china and glassware\nimport export specialist in china and other glassware\ngraduate export specialist in china and glassware\nassistant import export specialist in china and glassware','\n',char(10)),'Import export specialists in china and other glassware have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc8ef7da-d2c0-4474-96a0-6d0385eeec1e','key_17795','','','3514','3514.1','webmaster',replace('webmasters\nwebmaster\nwebsite administrator\nweb administrator\nweb technician\ne-commerce technician','\n',char(10)),'Webmasters deploy, maintain, monitor and support a web server to meet service requirements. They ensure optimum system integrity, security, backup and performance. They coordinate the content, quality and style of websites, execute the website strategy and update and add new features to websites.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc90cee4-bf4a-4d62-b729-faf49e62643e','key_17796','','','8343','8343.2','container crane operator',replace('dock crane operator\nstraddle crane operator\ncontainer crane operator\ncrane driver\noperator of container crane\nship crane operator\ndockside crane operator\nshipping container crane operator','\n',char(10)),'Container crane operators operate electrically powered cranes equipped with cantilevers on which hoisting gear is supported to load or unload container cargo. They move towers in position alongside vessel and lower cantilevers over the deck or hold of a vessel. They lift and move containers along the cantilever and position the container on the dock, on the vessel deck or in the hold.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc97adbe-f807-4ad8-8f3c-c24b3416cdef','key_17797','','','1221','1221.5','digital marketing manager',replace('digital marketing technician\ndigital marketing specialist\ndigital marketing and communication professional\nsocial media specialist\nweb marketing specialist','\n',char(10)),'Digital marketing managers are responsible for the elaboration of the company''s digital marketing strategy with the goal of improving brand recognition and brand awareness, in line with the company''s mission and vision. They oversee the execution of digital marketing and communication strategies involving the use of social media, email marketing, marketing automation, search engine optimisation, online events and online advertisement through data driven methodologies and by measuring and monitoring digital marketing KPIs in order to promptly implement corrective action plan. They manage and interpret competitors and consumers'' data and conduct research on market conditions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dc9b98da-c389-4caf-a21a-c6f8c530c0bf','key_17798','','','7536','7536.2.8','shoe repairer',replace('shoe polisher\nboot maker\nshoe maker\ncobbler\nshoe repairer\nboot repairer','\n',char(10)),'Shoe repairers repair and renew deteriorated footwear and other items like belts or bags. They use hand tools and specialised machinery to add soles and heels, replace worn-out buckles and clean and polish shoes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dca305c8-d665-462c-bdc9-c3a19734ba18','key_17799','','','2141','2141.4.3','surface engineer',replace('surface engineers\nsurface engineering equipment technician\nsurface technology engineering expert\nsurface engineering specialist\nsurface engineering expert\nsurface engineering adviser\nsurface technology engineering consultant\nengineering operative\ncorrosion engineer\nsurface technology engineering specialist\nheavy equipment operative\nsurface engineering consultant\nengineering operator\nsurface engineering technician\nsurface technology engineering adviser\nsurface engineering operator\nsurface engineer\nsurface equipment operator\nsurface engineering worker\nsurface engineering operative\nsurface technology engineer','\n',char(10)),'Surface engineers research and develop technologies for manufacturing processes that assist in altering the properties of the surface of bulk material, such as metal, in order to reduce degradation by corrosion or wear. They explore and design how to protect surfaces of (metal) workpieces and products utilising sustainable materials and testing with a minimum of waste.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dce6a68c-8a05-4c26-85d4-6fbc43e7d822','key_17800','','','3521','3521.1.5','projectionist',replace('projectionist\nprojector booth operator\ndigital cinema projectionist\ncinema projectionist\nprojection operator\nbooth operator\nmovie projector operator\nfilm projectionist\nprojector operator\nprojection technician','\n',char(10)),'Projectionists operate and maintain projection equipment in cinema theatres. They inspect the movie films before loading them into the projector. Projectionist make sure that everything runs smoothly during the projection of the movie. They also are responsible for the proper storage of movie films.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dcfeb6e4-f39d-49fb-96cd-23292fb3e037','key_17801','','','3114','3114.1.9','optoelectronic engineering technician',replace('opto-electronic technician\noptoelectronic engineering technician\nopto-electronic engineering technician\ntechnologist in optoelectronic engineering\noptoelectronic systems technologist\noptoelectronic technician\ntechnician in optoelectronic systems\noptoelectronic engineering technologist\ntechnician in optoelectronics\noptoelectronic systems technician\ntechnologist in optoelectronic systems','\n',char(10)),'Optoelectronic engineering technicians collaborate with engineers in the development of optoelectronic systems and components, such as photodiodes, optical sensors, lasers and LED''s. Optoelectronic engineering technicians build, test, install and calibrate optoelectronic equipment. They read blueprint and other technical drawings to develop testing and calibrating procedures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dd25a2b0-add0-4ae2-bd85-bb644e0d0c39','key_17802','','','1439','1439.1','call centre manager',replace('contact centre manager\ncontact centre coordinator\ncall centre coordinator\ncall center manager\nclient centre manager\ncustomer centre manager\ncall centre director','\n',char(10)),'Call centre managers set the objectives of the service per month, week, and day. They perform micromanagement of the results obtained in the centre in order to proactively react with plans, trainings, or motivational plans depending on the problems faced by the service. They strive for achievement of KPIs such as minimum operating time, sales per day, and compliance with quality parameters.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dd89e26f-bb5c-45cb-91c6-8b4d5d82a31f','key_17803','','','7119','7119.2','demolition worker',replace('mattockwoman\ntopwoman\ndemolition and dismantling worker\ndemolition site worker\ndemolition labourer\ndemolition project worker\ndemolition operations worker\ndemolition worker\nmattockman\ndemolition experienced worker\ntopman\ndemolition reclamation operative','\n',char(10)),'Demolition workers operate equipment to demolish structures. They safely destroy buildings and remove the debris to permit the site to be used for a different purpose.','','Excludes blasters.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dd935705-d703-40ba-9ba7-2a8a2b7651b4','key_17804','','','3334','3334.1','housing manager',replace('housing agent\nhousing officer\nhousing manager\nneighbourhood manager\nhousing administrator\nhousing coordinator','\n',char(10)),'Housing managers oversee housing services for tenants or residents. They work for housing associations or private organisations for which they collect rental fees, inspect properties, suggest and implement improvements concerning repairs or neighbour nuissance issues, maintain communication with tenants, handle housing applications and liaise with local authorities and property managers. They hire, train and supervise personnel.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ddac39cf-5d8b-416a-b24b-9ce3a8a4a668','key_17805','','','3331','3331.2.1.27','import export specialist in perfume and cosmetics',replace('import export specialist in perfume and cosmetics\ngraduate import export specialist in perfume and cosmetics\nsenior export specialist in perfume and cosmetics\ncustoms compliance specialist in perfume and cosmetics\nsenior import export specialist in perfume and cosmetics\ntrainee export specialist in perfume and cosmetics\nassistant export specialist in perfume and cosmetics\ntrainee import specialist in perfume and cosmetics\ngraduate export specialist in perfume and cosmetics\nassistant import export specialist in perfume and cosmetics\nassistant import specialist in perfume and cosmetics\ntrainee import export specialist in perfume and cosmetics\nsenior import specialist in perfume and cosmetics\ngraduate import specialist in perfume and cosmetics\ninternational trade compliance specialist in perfume and cosmetics','\n',char(10)),'Import export specialists in perfume and cosmetics have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ddb3e6a4-5bae-480a-a49a-748f93022892','key_17806','','','8350','8350.2','decksman',replace('general purpose crew member\ngeneral purpose hand\nboat crew member\nyacht deckshand\ncrew member\ndecksman','\n',char(10)),'Decksmen are unlicensed members of the deck department of an inland vessel. This position is usually the first step on the way to become an able seaman and beyond. They perform a variety of duties related to the operation and upkeep of deck department areas, the engine, and other equipment, mooring and unmooring, as well as (to a certain extent) the steering of the ship.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dddf9385-2939-4395-951a-70ef8a36cabe','key_17807','','','9333','9333.7','stevedore superintendent',replace('stevedore general superintendent\nstevedoring supervisor\nstevedoring manager\nstevedore superintendent\nstevedore supervisor\nstevedore manager\nstevedoring controller\nstevedoring general superintendent\nstevedore superintendant\nstevedoring superintendent','\n',char(10)),'Stevedore superintendents supervise and monitor the freight handling and longshore labor in a dockyard to maximise productivity. They manage the loading and unloading of cargo and monitor the safety of the work area. Stevedore superintendents may also investigate incidents and prepare accident reports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/de0084a5-7e00-43cc-bc51-3cb70a031295','key_17808','','','1324','1324.3.1.6.10','electrical household appliances distribution manager',replace('senior electrical household appliances  distribution manager\nelectrical household appliances  supply chain planner\nelectrical household appliances distribution manager\nelectrical household appliances  logistics planner\nassistant electrical household appliances  distribution manager\nelectrical household appliances  logistics manager\nelectrical household appliances  supply chain manager\nelectrical household appliances  distribution planner\ntrainee electrical household appliances  distribution manager\ngraduate electrical household appliances  distribution manager','\n',char(10)),'Electrical household appliances distribution managers plan the distribution of electrical household appliances to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/de0856fe-21fa-46d6-88ee-f99d3b4946bc','key_17809','','','3257','3257.4.2','hazardous waste inspector',replace('hazardous waste management inspector\nhazardous waste inspector\nhazardous waste compliance inspector\nhazardous waste disposal inspector\nhazardous waste tester/inspector\nhazardous waste control inspector','\n',char(10)),'Hazardous waste inspectors inspect industrial sites to ensure they adhere to waste disposal legislation, and inspect their equipment to see if it is operational and functions according to regulations. They also aim to educate the public on hazardous substances and on hazardous waste handling regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/de31a27f-c6ba-4d4f-87cd-b405e1852121','key_17810','','','5249','5249.1','rental manager',replace('graduate rental manager\nrental team leader\nrental manager\ntrainee rental coordinator\ngraduate rental coordinator\nrental station co-ordinator\nrental station manager\nsenior rental manager\nsenior rental coordinator\nrental office coordinator\nrental office manager\nassistant rental manager\nrental team coordinator\ntrainee rental manager\nassistant rental coordinator','\n',char(10)),'Rental managers supervise the activities of a rental office or station and its staff.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/de50b422-8700-4153-955c-131c3d277738','key_17811','','','3324','3324.4.19','wholesale merchant in live animals',replace('graduate wholesale merchant in live animals\nwholesaler in live animals\nbusiness to business seller in live animals\nsenior wholesale merchant in live animals\nbusiness to business sales consultant in live animals\nB2B sales consultant in live animals\nwholesale trader in live animals\nwholesale buyer in live animals\nwholesale merchant in live animals\nassistant wholesale merchant in live animals\nwholesale merchandiser in live animals\ntrainee wholesale merchant in live animals','\n',char(10)),'Wholesale merchants in live animals investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/de74eb4e-c95e-425a-9b3a-7be805ec0321','key_17812','','','3112','3112.7','energy consultant',replace('energy saving consultant\nenergy saving equipment advisor\nenergy advisor\nenergy consultant\nenergy and environmental consultant\nenergy advice consultant\nenergy and sustainability consultant\nenergy procurement advisor\nenergy saving advisor\nenergy procurement consultant\nsustainability consultant','\n',char(10)),'Energy consultants advise clients on the advantages and disadvantages of different energy sources. They help clients to understand energy tariffs and try to reduce their energy consumption and carbon footprint by using energy efficient products and methods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/de80bbf0-447d-4a6a-845a-fdba4e0dc30c','key_17813','','','2431','2431.4','brand manager',replace('assistant brand manager\nsenior brand manager\ntrainee brand manager\nbrand manager\nformula manager\nmarketing manager (brand development)\nmarket development executive\ngraduate brand manager\nbrand executive\nmarketing development manager\nbrand development manager\nbrand marketing manager','\n',char(10)),'Brand managers analyse and plan the way a brand is positioned on the market.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/de99886e-59e3-432d-ba53-9652dbe378d6','key_17814','','','2412','2412.9','programme funding manager',replace('programme funding coordinator\nprogram funding manager\nprogramme funding administator\ngrants manager\nprogramme funding officer','\n',char(10)),'Programme funding managers take the lead in developing and realizing the funding strategy of the programmes of an organisation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dec7ed73-8688-4328-bc50-c72d31b25956','key_17815','','','7311','7311.4','precision instrument assembler',replace('gauge assembler\nthermostat calibrator\nprecision instrument tester\ngauge tester\nmicrometer assembler\ngauge calibrator\nprecision instrument assembler\nprecision instrument craftsman\nthermostat tester\nprecision instrument maker\nmeter tester\nprecision instrument calibrator\nmicrometer tester\nmicrometer calibrator\nprecision instrument craftswoman\nthermostat assembler\nmeter assembler\nmeter calibrator\nprecision instrument craftsperson','\n',char(10)),'Precision instrument assemblers read blueprints and assembly drawings to assemble precision instruments such as micrometers, gauges, thermostats and utility meters. They collect the different components and piece them together using hand tools or machinery. Furthermore they calibrate the instruments and test their precision.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/defbf906-e823-44b4-872f-99406c51022b','key_17816','','','3118','3118.3.9','heating, ventilation, air conditioning (and refrigeration) drafter',replace('heating, ventilation, air conditioning (and refrigeration) draughter\nHVAC(R) draughter\nHVACR design engineer\nheating, ventilation, air conditioning engineering draughter\nHVAC design engineer\nHVAC draughter\nHVACR drafting adviser\nheating, ventilation, air conditioning, and refrigeration drafter\nHVACR drafting expert\nheating, ventilation, air conditioning and refrigeration design engineer\nheating, ventilation, air conditioning (and refrigeration) design engineer\nHVAC(R) design engineer\nHVACR drafting consultant\nheating, ventilation, air conditioning engineering design engineer\nheating, ventilation, air conditioning (and refrigeration) drafter\nHVACR draftsperson\nHVACR draughter\n​heating, ventilation, air conditioning, and refrigeration draughter\nHVACR draughtsperson\nHVACR drafting specialist','\n',char(10)),'Heating, ventilation, air conditioning (and refrigeration) drafters create prototypes and sketches, technical details, and aesthetic briefings provided by engineers for the creation of drawings, usually computer aided, of heating, ventilation, air conditioning and possibly refrigeration systems. They can draft for all kinds of projects where these systems can be used.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/defc8417-eeb6-4f13-8fcd-6518b99a40f4','key_17817','','','8160','8160.36','fruit and vegetable canner',replace('fruit and veg canner\nfruit ad veg canning operative\nfruit and vegetable canner','\n',char(10)),'Fruit and vegetable canners tend machines to prepare industrial products based on fruits and vegetables for storage or shipping. They perform a wide range of tasks such as sorting, grading, washing, peeling, trimming, and slicing. Moreover, they follow procedures for canning, freezing, preserving, packing food products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/df433613-4856-4e50-9be5-7a0ec0f4260a','key_17818','','','8141','8141.1.5','rubber dipping machine operator',replace('rubber dipping machine operator\nrubber process machine operator\nrubber dipping machine operative\ndrop-machine operator\nrubber processing machine operative\ndrop-machine operative\nrubber processing machine operator','\n',char(10)),'Rubber dipping machine operators dip forms into liquid latex to manufacture rubber products such as balloons, finger cots or prophylactics. They mix the latex and pour it into the machine. Rubber dipping machine operators take a sample of latex goods after final dip and weigh it. They add ammonia or more latex to machine if the product does not meet requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/df66a6e6-2130-4932-8aaf-263dafa569fa','key_17819','','','1322','1322.1.1','mine production manager',replace('quarry production manager\nmine production manager\ncoordinator of mine production\nmining engineer\nquarry production engineer\nmine supervisor\nmining operations manager\nresident manager\nmine operations manager\nmine production engineer','\n',char(10)),'Mine production managers coordinate and implement short and medium term mine production schedules and plans, such as drilling, blasting, ore and mineral extraction, and waste management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dfa51b4d-0929-4885-b682-29e375c6b52c','key_17820','','','2411','2411.1.6','dividend analyst',replace('dividend analyst\ndividend calculation clerk\ndividends analyst\ndividends clerk\ndividend clerk\ndividend and coupon analyst','\n',char(10)),'Dividend analysts calculate and allocate dividends and interest incomes of a company''s earnings to a category of its shareholders. They assess business systems and processes in order to identify user needs and to deliver appropriate solutions. They also undertake dividend forecasts on amounts and payment schedules and identify potential risks, based on their financial and market price expertise.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/dfb93ddb-ce34-4d2e-a178-541d45a7d98b','key_17821','','','8157','8157.2','laundry workers supervisor',replace('laundry and dry cleaning operator\nlaundry and dry-cleaning chief\nlaundry and dry cleaning assistant\nlaundry and dry-cleaning supervisor\nlaundry and dry cleaning attendant\ndry cleaning workers supervisor\nlaundry and dry cleaning workers chief\nlaundry and dry cleaning workers supervisor\nlaundry and dry cleaning workers manager\nlaundry and dry-cleaning manager\nlaundry workers supervisor','\n',char(10)),'Laundry workers supervisors monitor and coordinate the activities of the laundry and dry-cleaning staff of laundry shops and industrial laundry companies. They plan and implement production schedules, hire and train workers and monitor the production quality levels.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e00c46c4-bd3c-4d88-8d30-fc6f951554bd','key_17822','','','7512','7512.3','confectioner',replace('confectioner\nconfectionary products worker\ncake maker\ncakes and sweets maker\nconfectionary worker','\n',char(10)),'Confectioners make a varied range of cakes, candies and other confectionery items for industrial purposes or for direct selling.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e011f10f-e172-43ce-bfd9-fb56e426c0c9','key_17823','','','2133','2133.4','countryside officer',replace('natural areas maintenance team leader\ncountryside officer\ncountryside officers\npark ranger\nrural areas officer','\n',char(10)),'Countryside officers are responsible for a range of activities that manage and maintain the natural environment and associated public access and recreation. They encourage visitors to open spaces/the countryside, promote awareness of the natural environment and protect and preserve the open space/countryside for future enjoyment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e01719f4-d7dd-42fe-b504-93128520f84a','key_17824','','','2431','2431.3','advertising specialist',replace('advertising consultant\nadvertising strategist\nstrategic marketing specialist\nadvertising adviser\nbrand specialist\nadvertising advisor\nadvertising and promotional specialist\nadvertising account manager\nadvertising specialist','\n',char(10)),'Advertising specialists provide advice to companies and organisations about the development of their advertising strategies and on advertising-related topics covering a more general strategic approach. They combine knowledge of marketing, budgets, and psychology with a creative mind to develop advertising campaigns. They propose alternatives to clients that promote their organisations, products, or projects.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e027434f-8aa4-4d87-a070-b1d5c57749e6','key_17825','','','2113','2113.1.5','fragrance chemist',replace('perfume and cosmetics formulator\nflavorist\nfragrance developer\nfragrance development chemist\nformulator of perfume products\nperfume chemist\nperfume formulator\nfragrance formulator\ndevelopment engineer formulation chemist\nflavour chemist\nfragrancer\nflavourist\nfragrance chemist\nfragrance research chemist\nfragrance product developer\napplication engineer','\n',char(10)),'Fragrance chemists develop and improve fragrance chemicals by formulating, testing and analysing fragrances and their ingredients so that the end product meets the expectations and the needs of customers.','','Includes flavour chemists.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e02d7c8a-1e38-4a67-a6e1-f580e550238f','key_17826','','','5223','5223.7.26','music and video shop specialised seller',replace('video shop salesperson\nmusic and video shop specialized seller\nmusical instrument shop salesperson\nsheet music shop salesperson\nmusic shop salesperson\ncd specialised seller\nmusic and video specialised seller\nmusical instrument salesperson\nvideo specialised seller\naudio-visual equipment salesperson\nmusical instrument specialised seller\naudio-visual equipment specialised seller\nvideo salesperson\nmusic and video salesperson\ncd salesperson\nrecord salesperson\nhome entertainment equipment salesperson\nsheet music specialised seller\nhome entertainment superstore salesperson\nsheet music shop specialised seller\nmusic superstore specialised seller\nmusic shop specialised seller\nhome entertainment equipment specialised seller\ncd shop specialised seller\nsheet music salesperson\nmusic superstore salesperson\nrecord shop specialised seller\nrecord specialised seller\nvideo shop specialised seller\ncd shop salesperson\nmusical instrument shop specialised seller','\n',char(10)),'Music and video shop specialised sellers sell musical records, audio tapes, compact discs, video tapes and DVDs in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e0433bbb-8a93-4ab4-ac7a-84ccc1a04637','key_17827','','','1324','1324.3.1.6.3','beverages distribution manager',replace('beverages logistics planner\ntrainee beverages distribution manager\nbeverages supply chain planner\nsenior beverages distribution manager\nbeverages supply chain manager\nbeverages distribution manager\nassistant beverages distribution manager\nbeverages distribution planner\nbeverages logistics manager\ngraduate beverages distribution manager','\n',char(10)),'Beverages distribution managers plan the distribution of beverages to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e047b8f5-9491-4da2-8816-7aa37ad2370d','key_17828','','','2149','2149.13','patent engineer',replace('patent agent\npatent and trademark engineer\npatent scientist\ntechnical patent specialist\nintellectual property engineer\npatent engineer','\n',char(10)),'Patent engineers advise companies on different aspects of intellectual property law. They analyse inventions, and research their economic potential. They check if  patent rights have already been given out for an invention and ensure that these rights have not been affected or violated.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e04bca7b-c0c7-454d-9d15-dfb64a30dc0a','key_17829','','','1324','1324.3.1.6.1','agricultural machinery and equipment distribution manager',replace('trainee agricultural machinery and equipment distribution manager\nagricultural machinery and equipment distribution planner\nagricultural machinery and equipment logistics planner\nsenior agricultural machinery and equipment distribution manager\nagricultural machinery and equipment logistics manager\nagricultural machinery and equipment supply chain planner\nassistant agricultural machinery and equipment distribution manager\nagricultural machinery and equipment supply chain manager\nagricultural machinery and equipment distribution manager\ngraduate agricultural machinery and equipment distribution manager','\n',char(10)),'Agricultural machinery and equipment distribution managers plan the distribution of agricultural machinery and equipment to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e0628770-57f0-4cf2-952e-5b16547db8c1','key_17830','','','6221','6221.6','aquaculture husbandry technician',replace('fish farm husbandry technician\ntechnician in aquaculture husbandry\nfisheries husbandry technician\naquaculture husbandry technician\naquaculture on-growing technician','\n',char(10)),'Aquaculture husbandry technicians operate in aquatic organisms'' production, specialising in the husbandry of ongrowing culture processes, especially in feeding and stock management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e0904e3f-d3e7-4313-8ff9-3facf9fce70d','key_17831','','','3324','3324.4.3','wholesale merchant in beverages',replace('wholesale merchant in beverages\nwholesaler in beverages\nwholesale trader in beverages\nwholesale merchandiser in beverages\ntrainee wholesale merchant in beverages\nB2B sales consultant in beverages\nbusiness to business seller in beverages\nsenior wholesale merchant in beverages\nwholesale buyer in beverages\nassistant wholesale merchant in beverages\ngraduate wholesale merchant in beverages\nbusiness to business sales consultant in beverages','\n',char(10)),'Wholesale merchants in beverages investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e0b347de-f88b-4f69-b7d0-0a965e41f0d5','key_17832','','','8160','8160.20','cocoa mill operator',replace('cocoa mill worker\ncocoa milling operator\ncocoa mill operator\ncocoa miller','\n',char(10)),'Cocoa mill operators tend machines to pulverise cacao beans into powder of specified fineness. They use air classification systems that separate powder based on its density. Moreover, they weigh, bag, and stack the product.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e0b544dd-b621-4126-a55e-054af25c6ea0','key_17833','','','2523','2523.2','ICT network architect',replace('network architects\nnetwork designer\nICT network architect','\n',char(10)),'ICT network architects design the topology and connectivity of ICT network such as hardware, infrastructure, communication and hardware components.','','Excludes people performing managerial activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e0c5e005-6303-4d5a-9125-a7f86ed1c4df','key_17834','','','8160','8160.42','liquor grinding mill operator','liquor grinding mill operator','Liquor grinding mill operators run mills that grind cracked cocoa beans or nibs of cocoa bean paste as to obtain liquid chocolate of specified consistency. They operate hoppers by sliding their gates to release cocoa nibs which then go through grinding stones.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e1106f6d-81fc-44f8-af94-8b7d2763065c','key_17835','','','3435','3435.1','assistant stage director',replace('first assistant director\norchestra assistant\nassistant stage director\nsecond assistant director\norchestra secretary','\n',char(10)),'Assistant stage directors support the needs of the stage director and the production for each assigned stage production, and serve as a liaison between performers, theatre staff and stage directors. They take notes, provide feedback, coordinate the rehearsal schedule, take blocking, rehearse or review scenes, prepare or distribute actor notes, and facilitate communication between designers, production staff, and stage director.','','Excludes répétiteur and dance répétiteur.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e11e5be4-49e7-48ac-8e0d-6d3ffd3dbcd0','key_17836','','','5223','5223.7.13','delicatessen specialised seller',replace('deli specialised seller\ndelicatessen department salesperson\ndeli salesperson\ngourmet specialised seller\nfine foods salesperson\nfine foods specialised seller\ndelicatessen department specialised seller\ndelicatessen salesperson\ndelicatessen counter salesperson\ndelicatessen specialized seller\ndelicatessen counter specialised seller\ngourmet salesperson','\n',char(10)),'Delicatessen specialised sellers sell delicatessen in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e12f08fb-4748-4388-9489-b647df60332a','key_17837','','','2142','2142.1.5','hydropower engineer','hydropower engineer','Hydropower engineers research, design and plan the building of facilities that generate electricity from the movement of water. They search optimal locations, conduct trials and try different materials to achieve the best result. Hydropower engineers develop strategies for more efficient energy production and analyse environmental consequences.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e139b0a3-3bc5-4c33-bfbf-51ac20ac12fa','key_17838','','','3211','3211.2','radiation therapist ',replace('practitioner of therapeutic radiography\nradiotherapy technologist\nRTT\ntherapy radiographer\ntherapeutic radiographer\ntherapeutic radiography practitioner\ntechnologist in radiotherapy','\n',char(10)),'Radiation therapists are responsible for the accurate delivery of radiotherapy to cancer patients and, as part of the multidisciplinary team, for elements of treatment preparation and patient care. This encompasses the safe and accurate delivery of the radiation dose prescribed and the clinical care and support of the patient throughout the treatment preparation, treatment delivery and immediate post treatment phases. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e1416610-ad08-4f37-8b46-9f99632a5c0f','key_17839','','','7522','7522.2','cabinet maker',replace('maker of kitchen cabinets\nfurniture constructor\nhand crafted furniture maker\ncraftsman cabinetmaker\ncraftswoman cabinetmaker\nhand crafted furniture fitter\nhand crafted furniture builder\nmaker of chairs\nmaker of settees\ncraftswoman cabinet maker\nchair maker\nmaker of beds\nmanufacture of storage units\ncabinetmaker\nfurniture fitter\ncabinet maker\ncraftsman cabinet maker\nhand crafted furniture constructor\nfurniture design and maker','\n',char(10)),'Cabinet makers build cabinets or other pieces of furniture by cutting, shaping and fitting pieces of wood. They use different kind of power and hand tools, such as lathes, planers and saws.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e14548c8-c2b9-4ef8-8670-d5967d7ad804','key_17840','','','2164','2164.4','urban planner',replace('regional planner\nsmart city planner\ncity planner\nurban planning engineer\ntown planner\nurban planner\ncountry planner','\n',char(10)),'Urban planners create development plans for towns, urban areas, cities, and regions. They research the needs of the community or the region (economic, social, transport) and evaluate other parameters such as sustainability in order to present solid programs aimed at the improvement of the site.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e148e1ed-4f46-40a7-95c9-d6914f664f17','key_17841','','','7319','7319.1','candle maker',replace('wax candle maker\ncandle former\nartisanal candle maker\ncandle making machine tender\ncandle maker\ncandlemaker\ncandle factory worker\ncandle production inspector\ncandle making machine operative\ncandle making machine operator\ncandle molder\ncandle making machine worker\nartisan candle maker\nchandler\ncandle moulder\ncandle inspector','\n',char(10)),'Candle makers mold candles, place the wick in the middle of the mold and fill the mold with wax, by hand or machine. They remove the candle from the mold, scrape off excess wax and inspect the candle for any deformities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e1587bbb-2d4e-4726-839a-7f4895c440e7','key_17842','','','1345','1345.1','headteacher',replace('headmaster\nacademy school manager\ngrammar school manager\nprimary school manager\nexecutive headteacher\nprincipal\nsecondary school manager\neducational institution manager\nheadmistress','\n',char(10)),' Headteachers manage the day-to-day activities of an educational institution. They make decisions concerning admissions and are responsible for meeting curriculum standards, which facilitate academic development for the students. They manage staff, working closely with the different department heads, and evaluate the subject teachers in a timely manner in order to secure optimal class performance. They also ensure the school meets the national education requirements set by law and cooperate with local communities and governments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e16c2422-1323-4d8a-bd0f-c7584de70be1','key_17843','','','2431','2431.14','pricing specialist',replace('graduate supermarket pricing specialist\nprice clerk\nprice point analyst\ngraduate trainee pricing specialist\npricing specialist\nprice analyst\nfood supermarket pricing specialist\nassistant pricing specialist\ndiscount supermarket pricing specialist\ntrainee pricing specialist\nprice marker\npricing development analyst\ncommercial product price analyst\nretail pricing analyst\nsenior pricing specialist\ncommercial pricing analyst\nprice checker','\n',char(10)),replace('Pricing specialists analyse production prices, market trends and competitors in order to establish the right price, taking brand and marketing concepts into consideration.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e16e4cb0-6f7d-4bf9-8f90-d1d866ad229b','key_17844','','','9629','9629.5','laundromat attendant',replace('launderette worker\nlaundromat attendant\nlaundromat worker\nlaundromat operator\nlaundromat assistant\nlaunderette attendant\nlaundromat supervisor\nlaunderette assistant\nlaundry assistant\nlaunderette operator\nlaundry aide\nlaunderette supervisor','\n',char(10)),'Laundromat attendants assist the customers of self-servicing laundries with issues related to coin-machines, dryers or vending machines. They maintain the general cleanliness of the laundry.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e19a0f13-da49-42cb-8b3a-27da8ce44c8c','key_17845','','','8181','8181.8','glass forming machine operator',replace('production machine operator\nglass forming machine worker\nglass production operator\nglass forming machine operative\nglass-blowing machine operator\nglass moulder\nglass forming and finishing machine operator\nglass molder\nglass product production worker\nglass forming machine operator','\n',char(10)),'Glass forming machine operators operate and maintain machines that press or blow molten glass in moulds to form or shape products, such as neons, bottles, jars and drinking glasses. They set up and adjust machines, and weigh, measure and check production samples to check conformity to the set specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e19fe26f-0759-4bb1-af71-5bbecf3df69f','key_17846','','','3354','3354.1','immigration adviser',replace('immigration adviser\nsolicitor\nimmigration advisor\nimmigration consultant\nsenior immigration consultant\nglobal immigration consultant\nimmigration lawyer','\n',char(10)),'Immigration advisers aid people seeking to move from one nation to another by advising them on immigration legislation, and assisting them in obtaining the necessary documentation to ensure the immigration process occurs in accordance with immigration laws.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e1c72b5f-4c5c-487c-a6df-e84b64a51dae','key_17847','','','2511','2511.14','ICT system architect',replace('ICT systems architects\nIT system architect\nsystems architect\nICT system architect\nICT systems architect\nsolution architect\nIT systems architect\ninformation systems architect\nICT system architects','\n',char(10)),'ICT system architects design architecture, components, modules, interfaces, and data for a multi-components system to meet specified requirements.','','Excludes people performing development activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e1c9b304-2784-48e7-b7e8-089867743bbf','key_17848','','','1420','1420.4.13','confectionery shop manager',replace('confectionery shop manager\ncandy store manager\nchocolate confections store manager\npastry and confectionery shop manager\nconfectionery store manager\nchocolate shop manager\nchocolate confections shop manager\nsugar confectionery store manager\npastry and confectionery store manager\nchocolate store manager\nsugar confectionery shop manager\nsweet store manager\nsweet shop manager\ncandy shop manager','\n',char(10)),'Confectionery shop managers assume responsibility for activities and staff in specialised shops for confectionery e.g. pastries, candy, and chocolate.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e1cf6897-158d-4b48-a39e-2a3a8cac216c','key_17849','','','9412','9412.1','kitchen assistant',replace('kitchen worker\nkitchen trainee\nkitchen boy\ncatering assistant\nspare hand\ncatering trainee\nkitchen assistant\nroundsman','\n',char(10)),'Kitchen assistants assist in the preparation of food and cleaning of the kitchen area.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e1fdca51-a0e1-4bea-822f-82a090ece780','key_17850','','','2132','2132.6','livestock advisor',replace('livestock advisor\nlivestock business advisor\nagriculture advisor\nagricultural advisor\nagricultural zootechnologist\nlivestock consultant\nagricultural business advisor','\n',char(10)),'Livestock advisors provide complex specialist advice to farmers and livestock breeders to ensure that their business and production is optimised.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e1fdd92c-46bd-45a0-8672-7f500c950cb0','key_17851','','','3331','3331.2.1.31','import export specialist in textiles and textile semi-finished and raw materials',replace('graduate export specialist in textiles and textile semi-finished and raw materials\nassistant export specialist in textiles and textile semi-finished and raw materials\ngraduate import specialist in textiles and textile semi-finished and raw materials\nsenior import specialist in textiles and textile semi-finished and raw materials\nsenior import export specialist in textiles and textile semi-finished and raw materials\ntrainee import specialist in textiles and textile semi-finished and raw materials\ngraduate import export specialist in textiles and textile semi-finished and raw materials\ncustoms compliance specialist in textiles and textile semi-finished and raw materials\ntrainee export specialist in textiles and textile semi-finished and raw materials\nimport export specialist in textiles and textile semi-finished and raw materials\ntrainee import export specialist in textiles and textile semi-finished and raw materials\ninternational trade compliance specialist in textiles and textile semi-finished and raw materials\nsenior export specialist in textiles and textile semi-finished and raw materials\nassistant import specialist in textiles and textile semi-finished and raw materials\nassistant import export specialist in textiles and textile semi-finished and raw materials','\n',char(10)),'Import export specialists in textiles and textile semi-finished and raw materials have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e20816a9-51d0-410b-b2c6-cbc22c1c4256','key_17852','','','5132','5132.1.2','cocktail bartender',replace('mixology waiter\ncocktail bartender\nmixologist\nmixology bartender\nflair bartender','\n',char(10)),'Cocktail bartenders perform expert mixing of alcoholic and non-alcoholic cocktails.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e20eee17-4876-4f21-a0cb-5d7f40c63cd8','key_17853','','','3311','3311.1','asset manager',replace('wealth manager\nassets manager\nwealth management advisor\nfinancial asset manager\nfinancial advisor\nwealth management adviser\nindependent financial advisor\nindependent financial adviser\nasset management advisor\nasset management adviser\nasset portfolio manager\nasset manager','\n',char(10)),'Asset managers invest the money of a client into financial assets, through vehicles such as investment funds or management of individual clients’ portfolios. This includes the management of the financial assets, within a given investment policy and risk framework, the provision of information, and the assessment and monitoring of risks.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e246eca2-2cec-45b4-ae76-aa1f37d07bc5','key_17854','','','1213','1213.2.3','tourism policy director',replace('visitor attraction director\neconomic development director\ntourism policy director\nmanager of tourism policy\ntourism policy manager\ndirector tourism strategy\ndirector of visitor attractions\ntourism strategy director\ndirector of economic development\npolicy director tourism','\n',char(10)),'Tourism policy directors develop and implement policies to improve tourism to their region. They develop marketing plans to promote the region in foreign regions, and monitor the operation of the tourism industry. They conduct research to investigate how tourism policies could be improved and implemented and investigate the benefits of the tourism industry to the government.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e297ec12-4712-40a4-ad98-ba004cacb205','key_17855','','','1330','1330.1','chief data officer',replace('chief analytics officer\nCDO\nchief data officer\nchief data officers','\n',char(10)),'Chief data officers manage companies'' enterprise-wide data administration and data mining functions. They ensure data are used as a strategic business asset at the executive level and implement and support a more collaborative and aligned information management infrastructure for the benefit of the organisation at large.','',replace('Excludes data scientists <a resource="http://data.europa.eu/esco/occupation/258e46f9-0075-4a2e-adae-1ff0477e0f30" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/258e46f9-0075-4a2e-adae-1ff0477e0f30"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">data scientist</span></a>. \n Excludes data analysts <a resource="http://data.europa.eu/esco/occupation/d3edb8f8-3a06-47a0-8fb9-9b212c006aa2" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/d3edb8f8-3a06-47a0-8fb9-9b212c006aa2"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">data analyst</span></a>. \n Includes people performing high executive data roles.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e2a495fb-68a3-46f9-8753-6ce566a7a085','key_17856','','','2145','2145.1.9','pharmaceutical engineer',replace('pharmaceutical research engineer\npharmaceutical engineering expert\npharmaceutical engineering consultant\npharmaceutical project engineer\npharmaceutical engineer\npharmaceutical engineering adviser\npharmaceutical engineering specialist\npharmaceutical manufacturing engineer\npharmaceutical technology engineering consultant\npharmaceutical technology engineering expert\npharmaceutical development engineer\npharmaceutical process engineer\nbiotechnology engineer\npharmaceutical technology engineering specialist\npharmaceuticals engineer\npharmaceutical engineering designer and developer\npharmaceutical engineering developer\npharmaceutical production engineer\npharmaceutical technology engineering adviser\npharmaceutical technology engineer','\n',char(10)),'Pharmaceutical engineers design and develop technologies used in pharmaceutical research and drugs manufacture, advising the pharmaceutical manufacturing plants to maintain and operate those technologies and ensuring the customers` and workers` safety requirements are met. They may also be involved in the conception and design of pharmaceutical manufacturing plants and research centers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e2ad6d3e-1736-466b-a2bf-9823f683c8ab','key_17857','','','1349','1349.6','book publisher',replace('book publisher\npublishing house manager\npublisher','\n',char(10)),'Book publishers are responsible for the selection of new materials. They decide which manuscripts, that the book editor has provided, are being published. Book publishers oversee the production, marketing and distribution of these texts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e2c38c87-2bcf-492a-a78e-3be9480481cc','key_17858','','','3122','3122.3.5','motor vehicle assembly supervisor',replace('motor vehicle production line forewoman\nmotor vehicle production chargehand\nmotor vehicle production line foreman\nmotor vehicle production line chargehand\nmotor vehicle assembly chargehand\nmotor vehicle production forewoman\nmotor vehicle assembly foreman\nmotor vehicle assembly forewoman\nmotor vehicle assembly supervisor\nmotor vehicle assembly team leader\nmotor vehicle production foreman\nmotor vehicle production line team leader\nmotor vehicle production team leader','\n',char(10)),'Motor vehicle assembly supervisors coordinate the employees involved in motor vehicle manufacturing and schedule their activities. They prepare production reports and recommend measures to reduce the cost and improve productivity such as hiring, ordering new equipment and implementing new production methods. They train employees in company policies, job duties and safety measures. They oversee the supplies and communicate with other departments to avoid unnecessary interruptions of the production process.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e2e3ecfa-a77b-49df-857e-09dee26e4198','key_17859','','','8219','8219.2','bicycle assembler',replace('road bike maker\nchildren''s bicycle maker\nracing bike assembler\ncycle maker\nracing bike maker\ntag-along maker\nmountain bike maker\nbicycle trailer assembler\nbike maker\nbike assembler\nbicycle trailer maker\nroad bike assembler\ncycle assembler\npedal cycle maker\nbicycle assembler\npedal cycle assembler\nbike trailer maker\nchildren''s bicycle assembler\nmountain bike assembler','\n',char(10)),'Bicycle assemblers build, tune and ensure good working order of all types of bicycles such as mountain bikes, road bikes, children’s bikes etc. They also assemble accessory products like tag-alongs and trailers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e31c83ac-cb74-4936-88f1-90ed0997b29b','key_17860','','','9629','9629.2','amusement and recreation attendant',replace('activities attendant\nattraction attendant\namusement and recreation attendant\nfair ground ride attendant\nrecreation attendant\nfairground attendant\nride host\nrides host\nride attendant\nrides and attractions host','\n',char(10)),'Amusement and recreation attendants perform a variety of attending duties at amusement or recreation facility. They may schedule the use of recreation facilities, maintain and provide equipment to participants of sporting events or recreational pursuits or operate amusement concessions and rides.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e3229e40-f571-4b26-baca-29edc8fe313e','key_17861','','','2511','2511.1','computer scientist',replace('IT scientist\nICT science researcher\ncomputers scientist\nICT scientist\nICT research scientist\ncomputer scientist\nIT research scientist\nIT researcher\nIT science researcher\nICT researcher','\n',char(10)),'Computer scientists conduct research in computer and information science, directed toward greater knowledge and understanding of fundamental aspects of ICT phenomena. They write research reports and proposals. Computer scientists also invent and design new approaches to computing technology, find innovative uses for existing technology and studies and solve complex problems in computing.','',replace('Includes people performing in-house research activities. \n Excludes people performing outsource research activities.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e34b93ec-5e11-4858-ba1e-e245c4c39cec','key_17862','','','3122','3122.3.8','wood assembly supervisor',replace('wooden assembly overseer\nwood assembly controller\nwooden assembly manager\nwooden assembly supervisor\nwood assembly manager\nwooden assembly controller\nwood assembly overseer\nwood assembly supervisor','\n',char(10)),'Wood assembly supervisors monitor the various processes in the assembly of wood products. They have a thorough understanding of the production processes under their supervision and take quick decisions when required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e3713dd7-572d-4c6c-87ec-1944b3572175','key_17863','','','8160','8160.17','cider fermentation operator',replace('cider products fermenter\ncider fermentation process operator\ncider fermentation operator\ncider fermenter','\n',char(10)),'Cider fermentation operators control the fermentation process of mash or wort inoculated with yeast.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e3b08984-19fe-4d51-a1b0-990676fc7234','key_17864','','','2621','2621.1','archivist',replace('archives record clerk\ndigital archivist\narchives and manuscripts librarian\nland register archivist\narchivist\ncadastral archivist\narchives specialist\nprocessing archivist\nreference archivist\narchives researcher','\n',char(10)),'Archivists assess, collect, organise, preserve and provide access to records and archives. Records maintained are in any format, analogue or digital and include several kinds of media (documents, photographs, video and sound recordings, etc.).','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e3cc09ba-7ad5-4411-940c-20613000863a','key_17865','','','2133','2133.3','conservation scientist',replace('park naturalist\npark ranger\nconservation scientist\nconservationist\nenvironmental scientist\nconservation programs specialist\nwildlife biologist','\n',char(10)),'Conservation scientists manage the quality of specific forests, parks and other natural resources. They protect the wildlife habitat, biodiversity, scenic value, and other unique attributes of preserves and conservation lands. Conservation scientists perform field work.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e3dc66de-99c7-4607-a82b-7244036d316d','key_17866','','','8160','8160.34','food production operator',replace('food production operator\nfood production operative\nfood production worker\nfood manufacturing worker\nfood worker','\n',char(10)),'Food production operators supply and perform one or more tasks in different stages of the food production process. They perform manufacturing operations and processes to foods and beverages, perform packaging, operate machines manually or automatically, follow predetermined procedures, and take food safety regulations on board.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e3df1f82-3df9-41e5-8487-635e184653d1','key_17867','','','3152','3152.1','deck officer',replace('ship first officer\nchief mate\nship deck officer\ndeck officer\nfirst mate\nship chief officer','\n',char(10)),'Deck officers or mates perform the watch duties on board of vessels like determining the course and speed, manoeuvring to avoid hazards, and continuously monitoring the vessels position using charts and navigational aids. They maintain logs and other records tracking the ship''s movements. They ensure that the proper procedures and safety practices are followed, check that equipment is in good working order, and oversee the loading and discharging of cargo or passengers. They supervise crew members engaged in maintenance and the primary upkeep of the vessel.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e40e4a07-4246-4c6f-a51c-89d35b6fe75e','key_17868','','','3324','3324.1.2','waste broker',replace('mine waste broker\nwaste broker\nmining waste broker\nlower tier waste broker\nwaste management broker\nwaste management intermediary\nanimal waste broker\nwaste recycling broker\nrecycling waste broker\nupper tier waste broker\nabattoir waste broker\nquarry waste broker\nagricultural waste broker','\n',char(10)),'Waste brokers act as mediating party between clients and waste management industries. They ensure the waste is collected from the client by a specialised professional, and transported to a waste mangement facility where it is processed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e4112a88-973f-4c7a-97f5-2241e6b6833f','key_17869','','','2423','2423.2','employment and vocational integration consultant',replace('outplacement consultant\njob placement adviser\nvocational counsellor\ncareer mentor\njob placement consultant\ncorporate  coach\njob placement expert\nemployment and vocational integration consultant\ncareer coach\nvocational adviser\npublic employment service consultant\nvocational expert\njob coach\nbusiness coach\noutplacement expert','\n',char(10)),'Employment and vocational integration consultants offer assistance to unemployed individuals in finding jobs or vocational training opportunities, according to their educational or professional background and experience.They advise them on how to market their skills in the job-hunting process. Employment and vocational integration consultants help job-seekers write CVs and cover letters, prepare for a job interview and indicate where to search for new job or training opportunities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e43d0f86-7abf-4c6e-b2b3-10970ddf6518','key_17870','','','7122','7122.1','carpet fitter',replace('carpet fitter\ncarpet layer\ncommercial carpet fitter\ndomestic carpet layer\ncarpetlayer\ncommercial carpet layer\nindustrial carpet fitter\ndomestic carpet fitter\nindustrial carpet layer\nindustrial and commercial carpet layer\ncommercial and industrial carpet fitter','\n',char(10)),'Carpet fitters lay rolls of carpet as a floor covering. They cut the carpet to size, prepare the surface, and put the carpet in place.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e44b2459-af5e-492d-9c57-d9c2e62944e7','key_17871','','','0110','0110.12','squadron leader',replace('air force commandant\nsquadron commander\nair force squadron leader\nsquadron leader\nair force squadron team leader','\n',char(10)),'Squadron leaders command a squadron specialising in different operations, such as aircraft squadrons, ground operations squadrons, or armoured fighting vehicles squadrons.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e44d2570-10cc-4984-9692-63ece1b6dce8','key_17872','','','7313','7313.1.5','jewellery polisher',replace('jewelry polisher\njewellery polishing machine setter\njewelry polishing machine worker\njewelery repairer\njewellery finisher\njewelry polishing machine operative\njewellery polishing machine worker\nbarrel polishing machine worker\nbarrel polishing machine tender\nbarrel polishing machine operative\njewelry polishing machine tender\njewelry polishing machine setter\njewelry polishing machine operator\nbarrel polishing machine setter\njewellery polishing machine operator\njewellery polishing machine operative\nbarrel polishing machine operator\njewellery polishing machine tender','\n',char(10)),'Jewellery polishers ensure that finished jewellery pieces are cleaned by customer demand or prepared for sale. They may also perform minor repairs. They use either hand tools such as files and emery paper buff sticks and/or hand held polishing machines. They also use mechanised polishing machines such as barrel polishers. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e4ad2547-9188-4fbd-897a-c91b3c097779','key_17873','','','1345','1345.1.1','dean of faculty',replace('faculty director\nfaculty dean\nuniversity head of faculty\ndean of faculty\nuniversity head of school\nhead of faculty\ndirector of faculty','\n',char(10)),'Deans of faculty lead and manage a collection of related academic departments and work with the post-secondary school principal and the different heads of department to deliver the agreed faculty and university strategic objectives. They promote the faculty in associated communities and market the faculty nationally as well as internationally. Deans of faculty also focus on achieving the faculty''s financial mangement target.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e4c915ff-6c9c-4fa9-a8fb-09d0c67e30d4','key_17874','','','0110','0110.5','army major',replace('battalion commandant\narmy commandant\narmed forces major\nlieutenant commander\nmilitary major\nmajor\narmy major','\n',char(10)),'Army majors command large units of officers and soldiers, supervise their training, and oversee their wellfare. They also supervise their administration, and equipment management.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e4cdfa86-c3b1-41f1-a4e2-384c73d03bd3','key_17875','','','3324','3324.2','commodity trader',replace('commodity handler\ncommodities trader\ncommodity exchange floor trader\ncommodities trading manager\ncommodity trader','\n',char(10)),'Commodity traders use negotiation techniques to sell and buy physical goods and raw materials such as gold, cattle, oil, cotton and wheat on the trading floor. They receive and implement purchasing and selling instructions and negotiate the terms of sale and delivery of commodities. Commodity traders do research about market conditions of specific commodities, their price trends and demand in order to inform their employers, they make bid offers and calculate the cost of transactions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e4f33b45-ee95-48b5-b2f8-e397aa14ed41','key_17876','','','9333','9333.2','distribution centre dispatcher',replace('bulk station operator\ndistribution centre operator\ndistribution center dispatcher\ndistribution center operator\nproduct dispatcher\nbulk station dispatcher\nmanufactured goods dispatcher\ndistribution centeredispatcher\ndispatcher at distribution centre','\n',char(10)),'Distribution centre dispatchers ensure efficient shipping of manufactured goods. They stipulate routes and complete shipping documents.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e4fca489-4c79-4392-874f-9ea1051c05c8','key_17877','','','3435','3435.8','extra',replace('extra\nbackground actress\nwalker on\ntelevision extra\nextra performer\nmovie extra\nbackground artist\nmotion picture extra\nbackground actor\nfilm extra\nTV extra','\n',char(10)),'Extras perform actions in the background or in crowds during filming. They do not contribute to the plot directly but they are important to create a certain atmosphere.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e5257110-e7f1-453a-a2dc-bd374945bc4a','key_17878','','','1321','1321.2.1.6.1','leather goods production manager',replace('head of leather goods production\nleather research and development manager\nleather goods production manager\nfashion production manager\nleather goods production supervisor\nproduction planning manager','\n',char(10)),'Leather goods production managers perform a wide range of activities and tasks in the field of management, namely, they plan, distribute and coordinate all necessary activities of the different leather goods manufacturing phases envisaging the accomplishment of the quality standards and production and productivity pre-defined goals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e52b5707-7097-4dbc-ac61-054e917da241','key_17879','','','2261','2261.1','dental practitioner',replace('dental practitioner\nprincipal dentist\nassociate dental practitioner\npaediatric dentist\ncommunity dentist\ngeriatric dentist\ncommunity dental practitioner\nassociate dentist\nprincipal dental practitioner\nconservative dentist\ndentist','\n',char(10)),'Dental practitioners prevent, diagnose and treat anomalies and diseases affecting the teeth, mouth, jaws and adjoining tissues.','','','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e53e1bd8-7316-4fab-9338-c6a66f50e5b7','key_17880','','','3154','3154.3','flight operations officer',replace('flight operations officer\nflight operations supervisor\nair operations supervisor\ndirector of flight operations\naviation dispatch officer\ndirector of airport operations\nair operations officer\nmanager of flight operations','\n',char(10)),'Flight operations officers compile flight information to expedite movement of aircraft between and through airports. They compile aircraft dispatch data such as scheduled arrival and departure times at checkpoints and scheduled stops, amount of fuel needed for flight, and maximum allowable gross take-off and landing weight.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e54683ca-1fc9-468d-b880-6544ff6b7d0b','key_17881','','','9611','9611.1','refuse collector',replace('household refuse collector\nhousehold waste collector\ndustman\ndustbin man\nrefuse sorter\nrecycling operator\nbinman\nrefuse collection operative\nrefuse collection worker\ndomestic refuse collector\noffice waste collector\ngarbage and recycling collector\ndemolition site refuse collection worker\nindustrial waste collector\ncommercial waste collector\nkerbside collector\nhazardous waste collector\nrefuse collector\nwaste collector\ndomestic waste collector','\n',char(10)),'Refuse collectors remove waste from homes and other facilities and put it in the bin lorry so it can be transported to a treatment and disposal facility. They assist the driver of the bin lorry, help unload the waste, and record the amount of refuse collected. They may also collect waste from construction and demolition sites, and hazardous waste.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e55e1a62-eab4-4829-a71e-e0d294cacf6b','key_17882','','','2641','2641.4.1','lyricist',replace('lyricist\nsinger-lyricist\ncopywriter & lyricist\ncomposer-lyricist\nlibretist\nlyric writer\nlyric songwriter\nsongwriter','\n',char(10)),'Lyricists interpret the style of a music piece and write words to accompany the melody. They work together with the music composer.','','Excludes  <a resource="http://data.europa.eu/esco/occupation/7dc05fcc-b079-45ed-b5d4-74c75f99976c" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/7dc05fcc-b079-45ed-b5d4-74c75f99976c"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">composer</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e575efca-c3e4-43fe-8c46-327d310f795e','key_17883','','','3123','3123.2','underwater construction supervisor',replace('canal lock construction supervisor\nsub-sea construction supervisor\nconstruction diving supervisor\nmarine construction supervisor\nmarine construction project supervisor\nunderwater construction project supervisor\nbridge pillar construction supervisor\nsub-sea construction project supervisor\nharbour construction supervisor\ntunnel construction supervisor\nunderwater construction supervisor','\n',char(10)),'Underwater construction supervisors monitor underwater construction projects such as tunnels, canal locks and bridge pillars. They guide and instruct construction commercial divers and make sure they adhere to safety regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e58eadca-e512-4167-96f1-fff980f117e3','key_17884','','','3115','3115.1.23','vessel engine tester',replace('liquid natural gas engine tester\nboat engine tester\nelectric motor tester\ndual fuel engine tester\nboat engine test engineer\ngas turbine engine tester\nvessel engine tester\nmarine engine tester\nboat engine test technician\nLNG engine tester\nnuclear reactor tester\noutboard motor tester\nmarine engine test engineer\ntwo-stroke diesel engineer tester\nmarine engine test technician\nfour-stroke diesel engine tester\nmarine steam engine tester','\n',char(10)),'Vessel engine testers test the performance of vessel engines such as electric motors, nuclear reactors, gas turbine engines, outboard motors, two-stroke or four-stroke diesel engines, LNG, dual fuel engines and, in some cases, marine steam engines in specialised facilities such as laboratories. They position or give directions to workers positioning engines on the test stand. They use hand tools and machinery to position and connect the engine to the test stand. They use computerised equipment to enter, read and record test data such as temperature, speed, fuel consumption, oil and exhaust pressure.','','Excludes <a resource="http://data.europa.eu/esco/occupation/da6220cb-21ee-4076-8796-1e922b44c394" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/da6220cb-21ee-4076-8796-1e922b44c394"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">vessel engine inspector</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e5a63940-57d3-4065-8713-74b4e2c93063','key_17885','','','2431','2431.15','product and services manager',replace('graduate product and services manager \nsenior e-catalogue manager\ncustomer e-catalogue manager\ngraduate e-catalogue manager\nsenior product and services manager \nproduct and services manager\nmarketing e-catalogue manager\nonline catalogue manager\ne-catalogue manager\nassistant e-catalogue manager\nonline e-catalogue manager\ntrainee product and services manager\nproduct catalogue manager\nassistant product and services manager manager\nmarketing catalogue manager\nsales e-catalogue manager\ncustomer product and services manager manager\ntrainee e-catalogue manager\ncatalogue manager\nsales catalogue manager','\n',char(10)),'Product and services managers are in charge of defining the content and structure of a catalogue or portfolio within a company.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e5e3ea36-4ed9-4c57-987c-9974385551a8','key_17886','','','9211','9211.2','vineyard worker',replace('grape harvester\ngrape picker\nvineyard worker\nworker in a vineyard\nwinery worker\nvineyard labourer','\n',char(10)),'Vineyard workers carry out manual activities related to cultivation, propagation of grape varieties and the production and/or packaging of wines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e6023abf-ced3-4ac9-9437-cc79e4396e3e','key_17887','','','2269','2269.9.1','animal assisted therapist',replace('animal assisted intervention professional\nanimal assisted therapist','\n',char(10)),'Animal assisted therapists provide support to individuals with cognitive, motoric, or social-emotional disabilities through animal assisted intervention. They involve pets and domesticated animals in a specific intervention plan such as therapy, education, and human service, and aim to restore and maintain the patients` well-being and recovery.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e614861b-4685-4699-a12d-965cdd65ec64','key_17888','','','8156','8156.2.2','footwear maintenance technician','footwear maintenance technician','Footwear maintenance technicians are the professionals who install, program and tune different types of cutting, stitching, assembling and finishing equipment used in footwear production. They perform preventive and corrective maintenance, and verify periodically working conditions and performance. They analyse faults, correct problems, repair and substitute components or pieces, and do routine lubrications, providing information on their use and energetic consumption mainly to the decision makers inside the company.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e65a9b0e-7347-4f6e-945e-aaeee976ef6e','key_17889','','','5151','5151.1','hotel butler',replace('house steward\nbutler administrator\nhotel butler\nhousehold manager','\n',char(10)),'Hotel butlers provide personalised services to guests in high level hospitality establishment. They manage the housekeeping staff to ensure clean interiors and excellent customer service. Hotel butlers are responsible for the general welfare and satisfaction of the guests.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e666c384-2a14-4cd9-981f-7067dc0bb9a9','key_17890','','','7536','7536.1.3','leather goods hand stitcher',replace('leather goods hand stitcher\nleather goods hand sewer\nhand sewer, leather goods','\n',char(10)),'Leather goods hand stitchers join the cut pieces of leather and other materials using simple tools such as needles, pliers and scissors to close the product. They also perform hand stitches for decorative purposes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e6800524-eec2-4c33-af0e-6f36d1d679bc','key_17891','','','2152','2152.1.3','instrumentation engineer',replace('autonomy engineer\nmine instrumentation engineer\ninstrumentation engineer technology engineering specialist\nC&I engineer\ninstrumentation engineer\ninstrumentation engineering adviser\ninstrumentation engineer technology engineering consultant\nsupervisory control and data acquisition engineer\nmeasurement engineer\ninstrument engineer\ninstrumentation engineer technology engineering expert\ninstrumentation engineering consultant\ninstrumentation engineering specialist\ninstrumentation engineer technology engineering adviser\ninstrumentation engineer technology engineer\ncontrol and instrumentation engineer\ninstrumentation engineering expert','\n',char(10)),'Instrumentation engineers envision and design equipment used in manufacturing processes for controlling and monitoring various engineering processes remotely. They design equipment for the monitoring of production sites such as manufacturing systems, machinery uses and production processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e69697d0-2639-4aad-b73a-05e0b98357b1','key_17892','','','2151','2151.1.4','mine electrical engineer',replace('electrical engineering expert\nmines electrical engineer\nenergy specialist for mining\nmine electrical technician\nmining electrical equipment officer\nmining electrical engineer\nmine electrical engineer\nelectrical technologist\nmine electrical engineers\nenergy specialist for refinement of fossil fuels\nelectrical engineer','\n',char(10)),'Mine electrical engineers supervise the procurement, installation and maintenance of mining electrical equipment, using their knowledge of electrical and electronic principles. They organise the replacement and repair of electrical equipment and components.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e6a3ce2e-c0f2-47a7-937a-61ae9abfe1fa','key_17893','','','3111','3111.9','metrology technician',replace('metrology technologist\ninstrument specialist\nmetrology technician\nmetrologist\njunior metrology technician\ncomposite technician\ninstrument technician\nmeter service technician\nsenior metrology technician\nmetrology calibration technician\nmetrology engineering technician\ndimensional engineer\nmeter technician','\n',char(10)),'Metrology technicians apply their practical knowledge of metrology to calibrate measuring instruments, test equipment and analyse their performance. They ensure that the evaluated equipment meets requirements for precision, performance and accuracy. They report about their work and advise on technical matters concerning measuring instruments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e6c298a4-62de-4b62-b48e-c5fdca8f6071','key_17894','','','2433','2433.6.6','technical sales representative in office machinery and equipment',replace('office machinery and equipment outside technical sales representative\ngraduate technical sales representative in office machinery and equipment\ngraduate technical sales engineer in office machinery and equipment\ntrainee technical sales representative in office machinery and equipment\noffice machinery and equipment field salesman\nassistant technical sales engineer in office machinery and equipment\nassistant technical sales representative in office machinery and equipment\ntrainee technical sales engineer in office machinery and equipment\nsenior technical sales engineer in office machinery and equipment\noffice machinery and equipment  inside technical sales representative\ntechnical sales representative in office machinery and equipment\noffice machinery and equipment  technical field sales representative\nsenior technical sales representative in office machinery and equipment\noffice machinery and equipment  field salesperson\noffice machinery and equipment  field saleswoman','\n',char(10)),'Technical sales representatives in office machinery and equipment  act for a business to sell its merchandise while providing technical insight for customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e6dfc67e-331e-4468-a249-347a08decbdb','key_17895','','','2152','2152.1.8','microelectronics materials engineer',replace('microelectronics materials engineering consultant\nmicroelectronics materials developer\nmicroelectronics materials technology engineering consultant\nengineer specialised in microelectronics materials\nengineer of microelectronics materials\nmicroelectronics materials technology engineering specialist\nmicroelectronics materials designer\nspecialist microelectronics materials engineer\nmicroelectronics materials engineering adviser\nmicroelectronics materials technology engineering expert\nmicroelectronics materials design engineer\nmicroelectronics materials technology engineer\nmicroelectronics materials engineering expert\nmicroelectronics materials engineering specialist\nmicroelectronics materials development engineer\nengineer in microelectronics materials','\n',char(10)),'Microelectronics materials engineers design, develop and supervise the production of materials that are required for microelectronics and microelectromechanical systems (MEMS), and are able to apply them in these devices, appliances, products. They aid the design of microelectronics with physical and chemical knowledge about metals, semiconductors, ceramics, polymers, and composite materials. They conduct research on material structures, perform analysis, investigate failure mechanisms, and supervise research works.','',replace('Includes the materials which are required for microelectronics and microelectromechanical systems, like metals, semiconductors, ceramics, polymers, and composite materials.\nIncludes knowledge about materials that are necessary to design and develop any kind of microelectronics; however, it does not require an in-depth understanding about microelectronics.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e6eea1ed-e982-4583-bafe-a0ac796f6cf1','key_17896','','','7535','7535.1','leather sorter',replace('leather sorting supervisor\ngrader\nleather seconds inspector-grader\nleather grader\nleather sorter\nleather parts matcher\nleather sorters\nleather inspector','\n',char(10)),'Leather sorters inspect and classify leather during and at the end of the production process according to the qualitative features, destinations of use and customer requirements. They work in the tannery and in the warehouses checking the quality, colour, size, thickness, softness and natural defects. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e7117cdc-ee3d-45b0-9680-6b3a60c7c9b3','key_17897','','','3331','3331.2.1.23','import export specialist in metals and metal ores',replace('assistant export specialist in metals and metal ores\ncustoms compliance specialist in metals and metal ores\ngraduate export specialist in metals and metal ores\ninternational trade compliance specialist in metals and metal ores\ntrainee import export specialist in metals and metal ores\nsenior export specialist in metals and metal ores\nsenior import specialist in metals and metal ores\nsenior import export specialist in metals and metal ores\ntrainee export specialist in metals and metal ores\nimport export specialist in metals and metal ores\ntrainee import specialist in metals and metal ores\nassistant import export specialist in metals and metal ores\ngraduate import specialist in metals and metal ores\ngraduate import export specialist in metals and metal ores\nassistant import specialist in metals and metal ores','\n',char(10)),'Import export specialists in metals and metal ores have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e7417f4f-7072-4b07-a71f-71f6fc7f5e5b','key_17898','','','2621','2621.9','zoo registrar',replace('animal record keeper\nanimal records coordinator\nzoo registrar and records coordinator\naquarium registrar\nrecords coordinator\nzoo registrar\nanimal records keeper\nanimals registrar\ncollections registrar\nzoo animal registrar\naquarium record keeper\nrecords keeper','\n',char(10)),'Zoo registrars are responsible for the maintenance of a wide variety of records relating to animals and their care in zoological collections. This includes both historic and current records. They have the responsibiltity to collate records into an organised and recognised records keeping system. In most cases this also involves submitting regular reports to regional or international species information systems and/or as part of managed breeding programmes i.e. zoo registrars are usually responsible for both internal and external management if institutional records . Zoo registrars also often coordinate animal transportation for the zoological collection.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e74513c3-8ba8-462f-978c-0450522f4427','key_17899','','','2310','2310.1.8','chemistry lecturer',replace('chemistry lecturer\nchemical science teacher\nchemical science docent\nchemical science instructor\nchemistry instructor\nlecturer in chemistry\nuniversity lecturer in chemistry\nchemical science lector\nsenior lecturer in chemistry\nchemical science lecturer\nchemistry docent\nprofessor of chemistry\nspecialist lecturer in chemistry\nhigher education chemistry teacher\nuniversity chemistry lecturer\nchemistry professor\nchemical sciences lector\nchemical science professor\nuniversity chemistry teacher\nchemical sciences lecturer\nchemistry lector\ninstructor in chemistry','\n',char(10)),replace('Chemistry lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, chemistry, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants \nfor the preparation of lectures and of exams, for leading laboratory practices, grading papers and for leading review and feedback sessions for the students. They also conduct academic research in their field of chemistry, publish their findings and liaise with other university colleagues.','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e75305db-9011-4ee0-ab62-8d41a98f807e','key_17900','','','8322','8322.6','private chauffeur',replace('personal valet\npersonal chauffeur\npersonal driver\nprivate valet\nprivate driver\nprivate chauffeur','\n',char(10)),'Private chauffeurs transport their employers to a particular destination safely and on time. They use navigation devices to reach the destination in the shortest time possible, advise on weather and traffic conditions and comply with legal driving regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e763dfa6-924d-493b-892a-6435e0cffe70','key_17901','','','3331','3331.2.1.10','import export specialist in electrical household appliances',replace('customs compliance specialist in electrical household appliances\nsenior export specialist in electrical household appliances\nimport export specialist in electrical household appliances\ngraduate import export specialist in electrical household appliances\nassistant import specialist in electrical household appliances\nassistant export specialist in electrical household appliances\ngraduate export specialist in electrical household appliances\ninternational trade compliance specialist in electrical household appliances\ntrainee import export specialist in electrical household appliances\ngraduate import specialist in electrical household appliances\nsenior import export specialist in electrical household appliances\nassistant import export specialist in electrical household appliances\nsenior import specialist in electrical household appliances\ntrainee import specialist in electrical household appliances\ntrainee export specialist in electrical household appliances','\n',char(10)),'Import export specialists in electrical household appliances have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e777fb61-16f5-476b-8262-578edfdb4d9d','key_17902','','','2320','2320.1.21','physical education vocational teacher',replace('teacher of vocational physical education\nphysical education vocational teacher\nvocational teacher of physical education\nvocational teacher in physical education\ninstructor in physical education\nvocational PE teacher\nvocational teacher of PE\nvocational instructor in physical education\nvocational PE instructor','\n',char(10)),'Physical education vocational teachers instruct students in their specialised field of study, physical education, which is predominantly practical in nature. They provide theoretical instruction in service of the practical skills and techniques the students must subsequently master for a physical education-related profession, such as health specialist or outdoor activities organiser. They induce students in the convenient social frameworks of their field of study and teach the appropriate attitudes and values. Physical education vocational teachers monitor the students'' progress, assist individually when necessary, and evaluate their knowledge and performance on the subject of physical education through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e7970c7a-7769-4002-87d3-0a8ea94fdd6f','key_17903','','','6113','6113.5','landscape gardener',replace('landscaper\nlandscaping gardener\nlandscape gardener\nlandscape gardeners','\n',char(10)),'Landscape gardeners plan, construct, renovate and maintain parks, gardens and public green spaces.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e7ef8a86-6acd-458d-bf48-634b8fd28950','key_17904','','','8141','8141.1.3','foam rubber mixer',replace('foam rubber mixing machine operator\nmattress moulder\nmixer of foam rubber\nfoam rubber molder\nfoam rubber machine worker\nfoam rubber mixing machine worker\nfoam rubber and liquid latex mixer\nfoam cushion maker\nfoam rubber molding machine worker\nfoam rubber moulder\nfoam cushion molder\nfoam rubber machine operator\nfoam rubber mixing machine tender\nfoam rubber machine setter\nfoam rubber maker\nmoulder, foam rubber\nfoam rubber mixer\nfoam mattress moulder\nfoam rubber mixing machine setter\nfoam rubber mixing machine operative\ncushion maker\nfoam rubber molding machine setter\nmattress molder\nfoam mattress maker\nfoam rubber molding machine operator\nfoam rubber moulding machine operator\ncushion moulder\ncushion molder\nfoam rubber moulding machine tender\nmolder, foam rubber\nfoam rubber machine operative\nrubber mixer\nfoam rubber molding machine tender\nfoam rubber machine tender\nfoam rubber moulding machine operative\nfoam rubber moulding machine worker\nfoam mattress molder\nfoam rubber moulding machine setter\nfoam cushion moulder\nfoam rubber molding machine operative','\n',char(10)),'Foam rubber mixers tend the machine that mixes foam rubber particles with liquid latex. They weigh the right amount of ingredients and pour mixtures into moulds to make cushions and matresses.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e7f6e98a-cef5-41be-b65c-70cf1feed11d','key_17905','','','1420','1420.4.22','flower and garden shop manager',replace('floristry store manager\ngarden supplies store manager\ngarden store manager\nplant shop manager\nplant centre manager\nflower and garden shop manager\ngarden superstore manager\ngarden centre manager\nflower shop manager\nflower store manager\nplant and garden shop manager\nlawn and garden shop manager\nlawn and garden store manager\nfloristry shop manager\ngarden supplies shop manager\nplant nursery manager\nnursery shop manager','\n',char(10)),'Flower and garden shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e80cee57-3672-4aaf-8ca7-53b90771c364','key_17906','','','8141','8141.1.9.1','V-belt coverer',replace('V-belt coverer\nV-belt covering machine minder\nV-belt covering machine operative\nrubber belt coverer\nV-belt covering machine worker\nV-belt covering machine operator\nrubber V-belt coverer','\n',char(10)),'V-belt coverers operate machines that cover belts with rubberised fabric. They cut the fabric after one revolution of the belt.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e81269d8-f301-4c82-9235-ba33ec9b0434','key_17907','','','2250','2250.2','animal osteopath',replace('veterinary osteopathic therapist\nveterinary osteopath\nanimal osteopath\nanimal ostepathic therapist\nanimal osteopaths','\n',char(10)),replace('Animal osteopaths provide a therapeutic treatment following veterinary diagnosis or referral. They concentrate on the application of treatment techniques using manipulation of the body tissues to resolve strain and injury in animals in accordance with national legislation. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e83bf81f-c1d0-48e1-8533-7d65be8007c5','key_17908','','','1324','1324.3.1.6.26','sugar, chocolate and sugar confectionery distribution manager',replace('senior sugar, chocolate and sugar confectionery distribution manager\ngraduate sugar, chocolate and sugar confectionery distribution manager\nsugar, chocolate and sugar confectionery logistics manager\nassistant sugar, chocolate and sugar confectionery distribution manager\nsugar, chocolate and sugar confectionery supply chain planner\nsugar, chocolate and sugar confectionery distribution manager\nsugar, chocolate and sugar confectionery supply chain manager\ntrainee sugar, chocolate and sugar confectionery distribution manager\nsugar, chocolate and sugar confectionery distribution planner\nsugar, chocolate and sugar confectionery logistics planner','\n',char(10)),'Sugar, chocolate and sugar confectionery distribution managers plan the distribution of sugar, chocolate and sugar confectionery to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e8461d2e-3d75-477c-93a2-ea8d342bb55b','key_17909','','','2133','2133.15','environmental technician',replace('environmental conservation technician\npollution control technician\npollution prevention technician\nenvironmental protection technician\nenvironmental technician\ngroundwater protection technician','\n',char(10)),'Environmental technicians investigate sources of pollution and aid in the development of pollution prevention and environment protection plans. They take samples of soil, water or other materials and perform tests to analyse the pollution level and identify its source.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e859428d-6dbd-406b-bb2e-d5a0ae21df29','key_17910','','','7411','7411.1.3','event electrician',replace('mobile power team member\nevent/generator electrician\nmobile power crew member\nevent and generator electrician\nlive event electrician\ngenerator technician\nmobile power expert\nevent electrician\ngenerator technician and event electrician','\n',char(10)),'Event electricians set up and dismantle temporary, reliable electrical systems to support events. They work in locations without access to the power grid as well as locations with temporary power access. Their work is based on instruction, plans and calculations. They work indoors as well as outdoors. They cooperate closely with technical crew and operators.','',replace('Includes people working in event and rental companies. \nExcludes people performing industrial or building electricity activities.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e89e330c-daa3-48f0-b34e-04e3344ea8dc','key_17911','','','2655','2655.1.1','voice-over artist',replace('male voice-over artist\nvoice actress\nstoryteller\nvoice artist\nfemale voice-over artist\nvoice-over actor\nvoice over actress\nvoice over actor\nvoice-over artist\nvoice-over actress\nvoice over artist\nvoice actor\nnarrator','\n',char(10)),'Voice-over artists perform the dialogues of animated television or movie characters. They empathise with their characters and make them come alive with their voice.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e8b93ce7-e39b-4261-a25a-9b84764eb724','key_17912','','','2330','2330.1.14','philosophy teacher secondary school',replace('philosophy tutor secondary school\nteacher of philosophy in secondary schools\nphilosophy teacher high school\nhigh school philosophy teacher\nphilosophical studies teacher in secondary school\nphilosophy teacher\nsecondary school teacher of philosophy\nphilosophical studies high school teacher\nsecondary school philosophy tutor','\n',char(10)),'Philosophy teachers at secondary schools provide education to students, commonly children and young adults, in a secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, philosophy. They prepare lesson plans and materials, monitor the students progress, assist individually when necessary, and evaluate the students knowledge and performance on the subject of phylosophy through practical, usually physical, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e8bbc7fd-cfa2-406f-a664-57e5024cfbc5','key_17913','','','1330','1330.8','ICT vendor relationship manager',replace('ICT vendor relationship manager\nICT vendor relationship managers\nIT vendor relations manager\nICT vendor relationships manager\nICT vendor relations manager\nIT vendor relationship manager','\n',char(10)),'ICT vendor relationship managers establish and maintain positive business relationships between stakeholders (external or internal), by deploying activities which are compliant with organisational processes. They also manage the outsourcing process for the organisation''s ICT department and the supply chain communications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e8fca903-6590-48a8-8902-094bb41ee90c','key_17914','','','2149','2149.3','autonomous driving specialist',replace('highly automated drive engineer\nautonomous driving engineer\nADF testing and validation engineer\nAV engineer\nautonomous vehicle engineer\nC-V2X specialist\nconnected car specialist\nautonomous vehicle specialist\nADAS testing and validation engineer\nself-driving car engineer\nautonomous car specialist\nconnected vehicle specialist','\n',char(10)),'Autonomous driving specialists design and oversee the operation of an autonomous vehicle. They collect data on systems performance, conduct vehicle testing and analyse test data. Autonomous driving specialists are aware of different technologies employed in self-driving cars, automotive systems and automotive technologies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e91bc34c-0c4f-4ac9-8e2f-8f337d75f9ca','key_17915','','','3315','3315.5','loss adjuster',replace('loss adjuster\nautomobile claims technical adjuster\nmaterial damage appraiser\nvehicle damage appraiser\ncommercial adjuster\nfield claims adjuster\nmotor claims adjuster\ninsurance loss adjuster\nfire claims adjuster\nfield appraiser\nphysical damage appraiser\nchartered loss adjuster\nclaims adjusters supervisor\nclaims adjuster\ndamage appraiser\ncivil claims adjuster\nautomobile appraiser\ninsurance appraiser\npersonal claims adjuster\ninsurance claims adjudicator\ndomestic adjuster','\n',char(10)),'Loss adjusters treat and evaluate insurance claims by investigating the cases and determining liability and damage, in accordance with the policies of the insurance company. They interview the claimant and witnesses and write reports for the insurer where appropriate recommendations for the settlement are made. Loss adjusters'' tasks include making payments to the insured following his claim, consulting damage experts and providing information via telephone to the clients.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e937449a-d9e0-4ab7-9ea5-4d3ac673be6d','key_17916','','','3321','3321.3','insurance underwriter',replace('group insurance underwriter\ncommercial insurance underwriter\npersonal lines underwriter\npublic liability insurance underwriter\ncommercial underwriter\ncasualty insurance underwriter\nlife underwriter\ncommercial property underwriter\ninsurance underwriter\nrisk assessor\ncommercial lines underwriter\naccident and sickness insurance underwriter\nfire insurance underwriter\nautomobile insurance underwriter\nliability risk assessor\npackage insurance underwriter','\n',char(10)),'Insurance underwriters assess business risks and liability policies and make decisions about commercial property. They inspect the conditions of businesses’ properties, analyse inspection policies, assist with real estate and rent issues, prepare loan contracts and handle commercial risks in order to align them with business practices. Insurance underwriters analyse various information from prospective customers in order to assess the likelihood that they will report a claim. They work to minimise risk for the insurance company and make sure that the insurance premium aligns with the associated risks. They can be specialists in life insurance, health insurance, reinsurance, commercial insurance, mortgage insurance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e93b3b49-12e1-4b47-991c-dd4b0aec17d8','key_17917','','','3315','3315.6','personal property appraiser',replace('personal items valuer\npersonal items claims adjuster\npersonal property valuer\npersonal property claims adjuster\nprofessional valuer of personal items\npersonal property claims assessor\npersonal property appraiser\npersonal items assessor\npersonal property assessor\nprofessional valuer of personal property\npersonal items valuation professional','\n',char(10)),'Personal property appraisers undertake detailed analysis and investigation of personal items such as books, wine, arts and antiques in order to determine their value for sales and insurance purposes. They assess the value of the items, taking into account the age, current condition, quality and if any repairs are required. Personal property appraisers prepare appraisal reports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e93d5c8d-1219-4c11-91d7-9d0ab8d9c08e','key_17918','','','6221','6221.4','aquaculture hatchery technician',replace('fish hatchery technician\ntechnician in aquaculture hatchery\nfish farm hatchery technician\naquaculture hatchery technician','\n',char(10)),'Aquaculture hatchery technicians operate and control all aspects of the hatchery production processes, from broodstock management to pregrowing juveniles.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e9507d34-c70f-474f-a4a3-d2cbfe50f6e9','key_17919','','','1219','1219.1.2','security manager',replace('security manager\nsecurity management specialist\nprivate security manager\nsecurity specialist\nsecurity operations manager\ncorporate security manager\nsafety and security manager\ncybersecurity specialist\nchief security guard\nsurveillance manager\nsecurity officer\nsecurity systems manager','\n',char(10)),replace('Security managers ensure security for people, such as customers and employees, and company’s assets either fixed, movable, machines, vehicles, and real state. They ensure safety and security by enforcing security policies, keeping track of different events, implementing security protocols, creating emergency response procedures, conducting security evaluations, and supervising security staff members. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e95ea9ec-3ac6-4a2d-a3cc-f9e8829cf15e','key_17920','','','3331','3331.2.1.4','import export specialist in chemical products',replace('assistant import specialist in chemical products\ninternational trade compliance specialist in chemical products\ngraduate import specialist in chemical products\nsenior import export specialist in chemical products\nsenior import specialist in chemical products\nimport export specialist in chemical products\ntrainee export specialist in chemical products\nsenior export specialist in chemical products\ngraduate export specialist in chemical products\nassistant export specialist in chemical products\ntrainee import specialist in chemical products\nassistant import export specialist in chemical products\ntrainee import export specialist in chemical products\ncustoms compliance specialist in chemical products\ngraduate import export specialist in chemical products','\n',char(10)),'Import export specialists in chemical products have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e9640041-ca34-4955-aa8c-629717d0dcda','key_17921','','','4323','4323.18','tram controller',replace('tram dispatcher\ntram supervisor\ntram system supervisor\ntram supervisor and controller\ntram system controller\ntraffic supervisor\ntram controller and supervisor\ntram controller','\n',char(10)),'Tram controllers assign and manage tram vehicles and drivers for the transport of passengers, including records of distances covered and of repairs made.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e9665bdf-2989-4dc5-9aa9-7b791e489779','key_17922','','','3117','3117.2','geotechnician',replace('mine geotechnician\ngeo-technician\nsurface logging technician\nlogging geologist\nsurface logging specialist\nsurface logger\ngeological operator\ngeological expert\nmine geological technician\ngeologist\ngeotechnician\nformation evaluation engineer\nlogging engineer','\n',char(10)),'Geotechnicians collect and process rock and soil samples for geomechanical testing. They also describe the quality of rock mass, including structure, discontinuities, colour and weathering. Mine geotechnicians may measure the size of underground openings. They report the collected information to geologists and engineers as required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e96cec32-9960-4496-a790-a9d2254906fc','key_17923','','','4212','4212.8','race track operator',replace('tote board operator\ntote technician\ntote operator\nadvanced tote operator\ntote manager','\n',char(10)),'Race track operators run the day-to-day functions of the tote operation at a horse race track, such as tote system data entry and verification, prepare the reports for the racetrack office, assist the forwarding of company equipment and spare parts. They maintain, operate and troubleshoot tote boards and auxiliary odds boards. They operate the communication tools used at the racetrack. They install, tear down and maintain equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e97ce81a-cf24-4012-b39e-f87bedc5b450','key_17924','','','2131','2131.2','animal feed nutritionist',replace('feed nutritionist\nanimal feed nutritionist\nanimal feed expert\nanimal nutrition expert','\n',char(10)),'Animal feed nutritionists analyse the nutritional value of animal feeds in order to provide dietary advice to agricultural, manufacturing, zoological and public sector staff. They undertake research on nutritionally balanced food stuffs and maintain awareness of technical and scientific developments on the subject.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e982a28b-9de2-460c-b85d-0f65ea0aa3ca','key_17925','','','5212','5212.1','street food vendor',replace('street food supplier\nstall holder\nstreet food business operator\nstreet food caterer\npurveyor of street food\nstreet market food vendor\nstreet food trader\nstreet food vendor\nstreet food seller\nfood stall holder','\n',char(10)),'Street food vendors sell food preparations, dishes and products on organised outdoor or indoor market places, or on the streets. They prepare the food in their stalls. Street food vendors use sales techniques to recommend their products to passers-by.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e997358a-f6ed-4b5d-bebe-2a33b01ea3d2','key_17926','','','1324','1324.3.2.10','import export manager in electrical household appliances',replace('international trade compliance manager in electrical household appliances\ninternational trade manager in electrical household appliances\nimport export manager in electrical household appliances\ntrainee export manager in electrical household appliances\ngraduate import export manager in electrical household appliances\ngraduate import manager in electrical household appliances\nsenior import manager in electrical household appliances\ngraduate export manager in electrical household appliances\nsenior import export manager in electrical household appliances\ntrainee import manager in electrical household appliances\nsenior export manager in electrical household appliances\nassistant import manager in electrical household appliances\nassistant export manager in electrical household appliances\ncustoms compliance manager in electrical household appliances\nassistant import export manager in electrical household appliances\ntrainee import export manager in electrical household appliances','\n',char(10)),'Import export managers in electrical household appliances install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e9a65aca-64f6-450d-a637-1ecf86ba2a53','key_17927','','','7212','7212.3.1','electron beam welder',replace('atomic welder\nsheet-metal welder\nelectron beam welding machine operator\nelectric arc welder\nelectron beam welder\nconstruction welder\nultrasonic metal welder\nsheet metal welder\nflash welder\narc welder apprentice\nweld technician\nexperimental welder\narc welder\ncombination welder\nwelder-fabricator\nbutt welder\nelectric spot welder\nwelder fabricator\nwelder-fitter\nfitter-welder\nrobotic welder','\n',char(10)),'Electron beam welders set up and tend machines designed to join separate metal workpieces together through the use of a high-velocity electron beam. They monitor the machining processes providing an alteration in the kinetic energy of the electrons that allows for them to transform into heat for the metal to melt and join together in a process of precise welding.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e9cf8b24-6b8b-47cc-9dd3-b601aa135960','key_17928','','','1114','1114.1','special-interest groups'' official',replace('trades union research officer\nspecial-interest groups'' staff member\nlobbying body official\nspecial-interest groups'' worker\nprofessional body official\nspecial-interest group official\nsport governing body official\nspecial-interest groups'' official\nhumanitarian organisation official\nspecial-interest group worker\nindustry association official\ntrades union official\nemployers organisation official\nspecial-interest group staff member\nsports association official\ncampaign group official\ntrade association official','\n',char(10)),'Special-interest groups'' officials represent and act on behalf of special-interest groups such as trade unions, employer organisations, trade and industry associations, sports associations and humanitarian organisations. They develop policies and ensure their implementation. Special-interest groups'' officials speak for their members in negotiations about topics such as working conditions and safety.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e9f0b7ae-c848-464e-af9d-55eb87a592e0','key_17929','','','8122','8122.2','anodising machine operator',replace('anodiser\nanode treater operator\nanodise machine operator\nanode line operator\nanode machine operative\nanodising line technician\nanodising machine operator\nanode machine technician\nanodising machine operative\nanode treater\nanode machine operator','\n',char(10)),'Anodising machine operators set up and tend anodising machines designed to provide otherwise finished metal workpieces, usually aluminum-based, with a durable, anodic oxide, corrosion-resistant finishing coat, by a electrolyctic passiviation process that increases the thickness of the natural oxide layer of the metal workpieces'' surface.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/e9f50268-4dab-4579-90c2-2ff219eb56f1','key_17930','','','2310','2310.1.16','engineering lecturer',replace('professor of mechanical engineering\nprofessor of engineering\nengineering science instructor\nengineering teacher\nengineering science docent\nengineering docent\nlecturer in electrical engineering\nuniversity lecturer in engineering\nhigher education engineering teacher\nengineering sciences professor\nengineering lector\nlecturer in mechanical engineering\nengineering sciences teacher\nlecturer in engineering\nengineering science professor\nengineering sciences docent\nengineering sciences instructor\nuniversity engineering lecturer\nprofessor of electrical engineering\nuniversity engineering teacher\nengineering science teacher\nengineering instructor\nengineering lecturer\nengineering professor','\n',char(10)),'Engineering lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, engineering, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants for the preparation of lectures and of exams, for leading laboratory practices, grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their field of engineering, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ea034d66-0d35-4891-b82c-6434cad2c76f','key_17931','','','5112','5112.3','train conductor',replace('train conductor\ntrain attendant\ntrain guard\nrailway conductor\nrail conductor\nconductor on trains','\n',char(10)),'Train conductors assist passengers in the boarding and leaving of the train. They answer questions from passengers concerning train rules, stations, and provide timetable information. They collect tickets, fares, and passes from passengers and support the chief conductor in performing his operational tasks e.g in regard to door closing or certain operational communication. They ensure the safety of passengers respond to technical incidents and emergency situations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ea3db1b3-7c03-4306-9e3d-7e20eb7f1826','key_17932','','','6121','6121.4','pig breeder',replace('pig breeder\npig specialist\npig rearer','\n',char(10)),'Pig breeders oversee the production and day-to-day care of pigs. They maintain the health and welfare of pigs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ea4aff10-4d20-4a2b-886b-299c2bd349f1','key_17933','','','7322','7322.5','offset printer',replace('offset printer\nnewspaper printer\nnewspaper press specialist\noffset pressman\noffset printer operator\noffset press specialist\nlithographic printing technician\nnewspaper press operator\noffset lithography printer\nlithographic printing specialist\noffset printer specialist\noffset press operator\nlithographic printing operator\nnewspaper press technician\noffset press assistant\npad printing machine operator\noffset printer technician\noffset press technician','\n',char(10)),'Offset printers handle an offset press in order to print an image. The offset press transfers an inked image from the plate to a rubber blanket before to print it onto the printing surface.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ea4c9aa1-cb9d-4037-a748-ac1305af7129','key_17934','','','5419','5419.6','emergency response worker',replace('first responder\ndisaster relief worker\nincident response worker\nfirst response worker\nenvironmental emergency worker\nnatural emergency response worker\nemergency response worker\nclean up worker\nemergency response mission worker\nemergency worker\nemergency response team worker\naid worker','\n',char(10)),'Emergency response workers work in missions to aid in emergency and disaster situations, such as natural disasters or oil spills. They clean up the debris or waste caused by the event, ensure the people involved are brought to safety, prevent further damage, and transport goods such as food and medical supplies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ea6cd713-94ca-4292-9ba9-ca8a4ec3ed5f','key_17935','','','7214','7214.1','dismantling worker',replace('disassembly worker\ndismantling worker','\n',char(10)),'Dismantling workers perform the dismantling of industrial equipment, machinery and buildings as instructed by the team leader. They use heavy machinery and different power tools depending on the task. At all times safety regulations are taken into account.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eaba592d-f3f0-4c7d-b7c0-302b7d0212ce','key_17936','','','6221','6221.10','aquaculture recirculation technician',replace('fish farm recirculation technician\naquaculture recirculation technician\ntechnician in aquaculture recirculation\nfishery recirculation technician','\n',char(10)),'Aquaculture recirculation technicians operate and control the production processes of aquatic organisms in land-based recirculation systems, which utilise water re-use processes and the operation of pumping, aerating, heating, lighting and biofilter equipment as well as backup power systems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eb00745b-9abb-4bd8-8291-aade48af029d','key_17937','','','3111','3111.7','material testing technician',replace('material testing technology scientist\nmaterial testing technology scholar\nmaterial testing technician\nmaterial testing technology studies research analyst\nmaterials testing technologist\nmaterial testing technology studies analyst\nmaterial testing technology analyst\nmaterial testing technologist\nmaterial testing technology researcher\nmaterial testing technology studies scientist\nmaterial testing technology research scientist\nmaterial testing technology studies researcher\nmaterial testing technology science researcher\nmaterial testing technology studies scholar\nmaterials tester\nmaterial tester\nmaterial testing technology research analyst\nmaterials testing technician\nmaterial testing technology studies research scientist','\n',char(10)),'Material testing technicians perform a variety of tests on materials such as soils, concrete, masonry and asphalt, in order to verify conformance to intended usage cases and specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eb198788-0b99-4583-a7c5-0139ff9f91ad','key_17938','','','4212','4212.4','casino gaming manager',replace('casino gaming manager\nsenior gaming manager\ngaming manager\ngaming operations manager\ngroup head of gaming\nhead of electronic gaming\ngaming team manager\ngaming service manager\ngroup gaming manager\nhead of gaming\nelectronic gaming manager','\n',char(10)),'Casino gaming managers oversee the day-to-day operations of gaming facilities. They supervise personnel, monitor the gaming areas, oversee the security services, ensure that all gaming rules are followed, and monitor compliance with regulatory requirements, responsible for implementing the operational objectives of the business.','','Includes gaming floor pitt boss.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eb48ccbd-75f2-4799-81be-443034858473','key_17939','','','2269','2269.9.2','art therapist',replace('art therapist\ndramatherapist','\n',char(10)),'Art therapists help patients overcome psychological and emotional difficulties through an artistic process which reflects emotions and feelings, focusing on patients who suffer a variety of problems such as mental, psychological, and behavioural disorders in order to facilitate self-understanding and awareness.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eb5df714-15ce-4e31-853c-678d2aba568b','key_17940','','','8143','8143.2','corrugator operator',replace('corrugator specialist\ncorrugator operative\ncorrugator technician\ncarton machine operator\ncorrugator operator\ncorrugated paperboard maker\ncorrugator worker\ncarton machine specialist\ncardboard machine operator\ncorrugated paper maker\ncarton machine technician\ncorrugating machine technician\ncorrugating machine specialist','\n',char(10)),'Corrugator operators tend a machine which folds a sheet of heavy paper in a wave-like pattern and covers it on both sides to create a lightweight, sturdy material suitable for packaging.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eb64b583-20fe-467f-8e44-8aae263a0139','key_17941','','','7313','7313.1.8','silversmith',replace('silver earrings fabricator\nboutique silversmith\nsilversmith\nsilver jewellery maker\nsilversmiths\ntrophy maker\nsilver jewellery fabricator\ntrophy silversmith\nsilver ring fabricator\nsilver earrings maker\nsilver jewel smith\nsilver ring maker','\n',char(10)),'Silversmiths design, manufacture and sell jewelry. They also adjust, repair and appraise gems and jewelry. Silversmiths are specialized in working with silver and other precious metals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eb6e7100-446f-407c-a764-2d65f3ffc7eb','key_17942','','','3324','3324.4.1','wholesale merchant in agricultural machinery and equipment',replace('senior wholesale merchant in agricultural machinery and equipment\nbusiness to business sales consultant in agricultural machinery and equipment\ntrainee wholesale merchant in agricultural machinery and equipment\ngraduate wholesale merchant in agricultural machinery and equipment\nB2B sales consultant in agricultural machinery and equipment\nwholesale merchandiser in agricultural machinery and equipment\nwholesale trader in agricultural machinery and equipment\nassistant wholesale merchant in agricultural machinery and equipment\nwholesale buyer in agricultural machinery and equipment\nbusiness to business seller in agricultural machinery and equipment\nwholesaler in agricultural machinery and equipment\nwholesale merchant in agricultural machinery and equipment','\n',char(10)),'Wholesale merchants in agricultural machinery and equipment investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eb6efbb9-9216-4e86-bc3b-de2223cd4667','key_17943','','','2250','2250.6','general veterinarian',replace('comprehensive vet\ngeneral vet\ngeneral veterinarians\ngeneral veterinarian\nveterinary surgeon\nall-purpose veterinary\nfamily vet\nequine veterinarian\ncompanion animal veterinarian\nfood production animal veterinarian','\n',char(10)),'General veterinarians are professionals with a comprehensive scientific education. They have the authority to carry out, in an independent, ethical and personally responsible capacity, all aspects of veterinary medicine, in the interest of the health and welfare of animals and public health in accordance with national and international legislation. The generalist veterinarian can work with any species, however they may choose to work with a single species or types such as equine, companion or production animals.','','','http://data.europa.eu/esco/regulated-professions/regulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eb7e42d0-9b3f-49ad-81f1-b9ee8491bf39','key_17944','','','2642','2642.1.15','political journalist',replace('journalist specialising in EU politics\npolitics reporter\njournalist specialising in international politics\npolitical analyst\npolitical correspondent\njournalist specialising in US politics\npolitical journalist\npolitical reporter\njournalist specialising in global politics\npolitical commentator','\n',char(10)),'Political journalists research and write articles about politics and politicians for newspapers, magazines, television and other media. They conduct interviews and attend events.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eb9479c6-5fdf-4f45-ace4-28b547b8c7e5','key_17945','','','1321','1321.2.1','industrial production manager',replace('area production manager\nproduction manufacturing supervisor\nproduction manufacturing manager\nproduction control manager\nproduction line manager\nproduction control supervisor\nindustrial production supervisor\nindustrial production manager\nindustrial production and manufacturing manager','\n',char(10)),'Industrial production managers oversee the operations and the resources needed in industrial plants and manufacturing sites for a smooth running of the operations. They prepare the production schedule by combining the requirements of clients with the resources of the production plant. They organise the journey of incoming raw materials or semi finished products in the plant until a final product is delivered by coordinating inventories, warehouses, distribution, and support activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ebb4061a-906f-45fe-b4c0-a871c11f6fab','key_17946','','','5414','5414.1.6','hospitality establishment security officer',replace('hotel security officer\nhospitality establishment security officer\nhospitality establishment security guard\nhospitality establishment security manager\nhospitality security manager\nhospitality security officer\nhotel security manager','\n',char(10)),'Hospitality establishment security officers are in charge of managing the overall process and implementation of hospitality facility security in terms of both safety of belongings, personal safety and building security.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ebe42b55-4801-493f-af18-04a35ebfbb9c','key_17947','','','7223','7223.5','drilling machine operator',replace('drilling machine worker\ndrilling and broaching technician\nvertical drilling mill operator\ndrill punch operator\ngang drill operator\ncomputer numerical controlled drilling machine operator\ndrill setup operative\nfoot drill operative\ndrilling machine inspector\nfoot drill operator\ndriller and reamer\ndrilling machine tender\ndrill set-up operator\ndrill punch operative\nhorizontal drilling milling machine set-up operator\ngang drill operative\nCNC drilling machine operator\nadzing and drilling  machine operator\ndrilling machine operative\nmultiple-spindle vertical drilling mill set-up operator\ndrill setup operator\ndrill set-up operative\ndrilling mill operator\ncomputer controlled drilling machine operator\ndrill and reamer technician\ndrilling machine operator\ndriller and broacher','\n',char(10)),'Drilling machine operators set up, program and control drilling machines, designed to drill holes in workpieces using a computer-controlled, rotary-cutting, multipointed cutting tool, inserted into the workpiece axially. They read drilling machine blueprints and tooling instructions, perform regular machine maintenance, and make adjustments to the drilling controls, such as the depth of drills or the rotation speed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ebe936e2-64e7-4777-94d6-42caa5a12409','key_17948','','','2641','2641.4.2','script writer',replace('TV script writer\nmovie author\ntelevision writer\nTV series script writer\nmovie script writer\nfilm author\ntelevision script writer\nscreen writer\nscreenplay writer\nscript writer','\n',char(10)),'Script writers create scripts for motion pictures or television series. They write a detailed story that consists of plot, characters, dialogue and physical environment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ebff4fcf-6b69-4290-a55c-9bfe4cc7a1ef','key_17949','','','7318','7318.3','fishing net maker',replace('drift net maker\nfishing net maker\nfisheries net maker\nmaker of fishing nets','\n',char(10)),'Fishing net makers make and assemble fishing net gear and carry out reparation and maintenance, as directed by the drawings and/or traditional methods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ec0e64c9-6d9b-4a9f-a022-a418f3b381fe','key_17950','','','2651','2651.4','ceramicist',replace('artistic ceramicist\nceramics designer and maker\nceramics maker and designer\nceramics artist\nceramist\nceramics designer & maker\nceramicist\nceramics maker & designer','\n',char(10)),'Ceramicist have an in-depth knowledge of materials and the relevant know-how to develop their own methods of expression and personal projects through ceramic. Their creations can include ceramic sculptures, jewellery, domestic and commercial tablewares and kitchenwares, giftware, garden ceramics, wall and floor tiles.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ec21ce88-ce14-49b6-be0a-cc53cf5a3a67','key_17951','','','2145','2145.1','chemical engineer',replace('pyrotechnics chemical engineer\nchemical technology engineering specialist\nwater purification chemical engineer\nchemical engineering adviser\nchemicals engineer\nchemical engineer\nchemical engineering specialist\nchemical technology engineering consultant\nchemical technology engineering adviser\nrefinery process engineer\nchemical engineering expert\nglass chemical engineer\nchemical technology engineer\nchemical engineering consultant\nchemical technology engineering expert\nceramics chemical engineer','\n',char(10)),'Chemical engineers design and develop large-scale chemical and physical production processes and are involved in the entire industrial process required for transforming raw materials into products.','','Includes people working in pharmaceuticals, rubber, plastics and toiletries, cement and paper, food and drink, energy or oil and gas.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ec238461-93b6-4516-a251-52d1153b53ac','key_17952','','','8142','8142.3','compression moulding machine operator',replace('compression molding machine operative\ncompression moulding machine operative\nmoulding machine setter\nmolding machine setter\ncompression molding machine operator\nproduction operative (plastic moulding)\nproduction operator (plastic moulding)\ncompression molding machine setter\nmoulding machine operative\nmolding machine operative\ncompression moulding machine setter','\n',char(10)),'Compression moulding machine operators set up and operate machines to mould plastic products, according to requirements. They select and install dies on press. Compression moulding machine operators weigh the amount of premixed compound needed and pour it into the die well. They regulate the temperature of dies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ec2c0655-5ba0-4cc8-860a-977f73b37f34','key_17953','','','2643','2643.6.2','subtitler',replace('closed caption editor\nsubtitler and surtitler\ncaptioner\nsubtitler\nCART provider\nsubtitlers\nlive subtitler\nsubtitles adaptor\ncaption editor\nsubtitler/adapter\nvideo subtitler\ncaption writer\nfilm subtitler\nsubtitles adapter\nsubtitler/adaptor','\n',char(10)),'Subtitlers can work intralingually, within the same language, or interlingually, across languages. Intralingual subtitlers create the subtitles for hearing-impaired viewers, whereas interlingual subtitlers create the subtitles for movies or television programmes in a different language to the one heard in the audiovisual production. They both ensure that the captions and subtitles are synchronised with the sound, images and dialogue of the audiovisual work.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ec5a067f-6278-4e35-990f-c9b7205a9ff4','key_17954','','','2141','2141.3','homologation engineer',replace('automotive homologation engineer\nhomologation manager','\n',char(10)),'Homologation engineers are responsible for the homologation process of new types of vehicles, components and systems and for ensuring the compliance with regulatory requirements for the sales country. They develop and implement homologation programmes and facilitate type approval testing in accordance with the European legislation, ensuring the respect of homologation timings. They research on and interpret regulatory requirements and are the main contact point for homologation and certification purposes within the organisation and with external agencies. Homologation engineers draft technical documentation and support design and test engineers in the vehicle development process.','','Does not include the motor vehicle assembly inspector. Does not include the motor vehicle engine inspector.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ec5b72d1-0206-4179-9abd-570c66bc44b9','key_17955','','','2359','2359.7','e-learning developer',replace('eLearning developers\ne-learning developer\neLearning producer\ne-learning developers\ne-learning producer','\n',char(10)),'E-learning developers design and develop simpler forms of digital learning content including reference materials, slides, assessments, screen-casts, interview videos and podcasts. They write and provide content for a computer based learning application.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ecac98ea-9fc3-4475-9b23-8af94f41603d','key_17956','','','7222','7222.5','tool and die maker',replace('tool machine operative\ndie setter\ntool machine set up operator\ntool setting operator\ntool-setting operative\ntool setter\ntool setting technician\ntool setting operative\ndie maker\ndie dresser\ntool maker\njig maker\ntool dresser\ntool machine set up operative\ntool and die maker\ntool machine operator','\n',char(10)),'Tool and die makers operate a variety of equipment and machinery designed to create metal tools and dies, which are both needed in several areas of manufacturing, and produce these tools in all steps of the production process. They design the tools and dies, then cut and shape them to size and finish them by manually operated machine tools, power tools, hand tools, or programming and tending CNC tool and die making machines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ecd0fad8-b099-4c47-93f6-dde9f6f6c5fc','key_17957','','','2651','2651.3','cartoonist',replace('political cartoonist\nstrip cartoonist\ncomic artist\ncaricaturist\npocket cartoonist\ncartoonist\nsatirical cartoonist\ntopical cartoonist','\n',char(10)),'Cartoonists draw people, objects, events, etc. in a comical or derogatory way. They exaggerate physical features and personality traits. Cartoonists also portray political, economic, cultural ans social events in a humoristic way.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ecdc43f5-3b32-4f94-abf8-cd46480c8cb8','key_17958','','','7421','7421.8','vehicle electronics installer',replace('vehicle electronics installer\nvehicle electronics repairer\nmotor vehicle technician, auto electronics\nvehicle electronic installation technician\nvehicle electronics technician\nmotor vehicle electronics technician\nmotor vehicle technician (auto electronics)\nauto electronic technician\nelectronic installation technician\nauto technician\nautoelectronic technician','\n',char(10)),replace('Vehicle electronics installers set up equipment and accessories in motor vehicles such as CD players and GPS. They use electric drills and routers to install and examine malfunctioning electronic systems. \n ','\n',char(10)),'','Excludes <a resource="http://data.europa.eu/esco/occupation/6656337f-67eb-4f48-b1e4-ecccdfa44dd2" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/6656337f-67eb-4f48-b1e4-ecccdfa44dd2"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">automotive electrician</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ecfcf554-254f-4dd7-a20c-b60cc6b16054','key_17959','','','8160','8160.18','cigarette making machine operator',replace('machine operator tobacco processing\nfilter cigarette-making machine operator\ncigarette machine tender\nfilter cigarette making machine operator\ncigarette machine maker attendant\nmachine operative (tobacco processing)\ncigarette making machine operative\ncigarette manufacturing worker\ntobacco processing machine tender\ncigarette machine minder\nattendant in cigarette manufacturing\ncigarette maker\ncigarette machine operator\ncigarette making machine operator\ncigarette production worker\ntobacco processing machine minder\ncigarette making machine assistant\nhigh-speed machine operator (tobacco products)\nmanufacture of cigarette operative\ncigarette production operative\ncigarette manufacturing attendant\ncigarette machine driver\ncigarette- making machine operator\ncigarette maker operator\ntobacco processing machine operators\ncigarette manufacturing machinist\ncigarette machine operative\ncigarette manufacturing operative\ncigarette rolling machine operator','\n',char(10)),replace(' Cigarette making machine operators tend cigarette-making machines to encase tobacco in continuous paper rolls followed by cutting cigarettes from roll. They place roll of cigarette paper on spindles and set monogram-printing devices to print brand name on cigarette paper at specified position. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ed096599-56a9-443d-9683-8a92f58aa894','key_17960','','','8153','8153.1.3','embroidery machine operator',replace('computerised embroidery machine operator\nembroidery machine operator\nembroidery machinist\nmonogram machine operator\ncomputerized machine operator','\n',char(10)),'Embroidery machine operators decorate wearing apparel by tending embroidery machines varying in their technology in order to embroider and ornamental wearing apparel.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ed256ed0-0432-4969-8544-3e5e01a517a9','key_17961','','','2636','2636.2','minister of religion',replace('nun\nspiritual leader\npastor\nminister of the church\npriest\nmember of the clergy\nreligious minister\nchurch minister\nminister of religion\ndeacon\nmonk\nordained clergyman\nbishop','\n',char(10)),'Ministers of religion lead religious organisations or communities, perform spiritual and religious ceremonies and provide spiritual guidance to members of a particular religious group. They may undertake missionary work, pastoral or preaching work, or work within a religious order or community, such as a monastery or convent. Ministers of religion perform duties such as leading worship services, giving religious education, officiating at funerals and marriages, counselling congregation members and offer a range of other community services, both in conjunction with the organisation they work for, and through their own personal day to day activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ed3cf43d-c2c1-4c46-82fc-1375e27e0290','key_17962','','','7126','7126.8','plumber',replace('plummer\npipe worker\ngas fitter\nplumbber\nplumber\ncommercial plumber\nplumbeer\ngasfitter\ndomestic plumber\npipeworker','\n',char(10)),'Plumbers maintain and install water, gas and sewage systems. They inspect pipes and fixtures on a regular basis or make repairs as needed. They bend, cut, and install pipes. They test systems and make adjustments safely and following regulations. They place sanitary equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ed41ca50-bb2a-41e2-a332-9b2df83040c1','key_17963','','','8160','8160.48','refining machine operator',replace('refining operator\nrefining machine operator\nrefining worker\nrefining machine operative\nrefining machine worker','\n',char(10)),'Refining machine operators tend machines to refine crude oils, such as soybean oil, cottonseed oil, and peanut oil. They tend wash tanks to remove by-products and remove impurities with heat.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ed976196-3d5d-4121-9367-e1022af603fa','key_17964','','','3119','3119.16.1','footwear quality control laboratory technician',replace('footwear quality control laboratory technician\nfootwear quality control laboratory analyst\nfootwear quality laboratory technician\nfootwear quality control engineer\nfootwear quality control laboratory technologist','\n',char(10)),'Footwear quality control laboratory technicians perform all laboratory tests of footwear and materials or components according to the national and international standards. They analyse and interpret their results, and prepare the report for the quality manager, advising on rejections or acceptances. They apply previously defined quality management tools aiming at achievement of the objectives stated in the quality policy. They participate in monitoring and control of the quality system, namely internal and external auditing. They collaborate in the preparation of documents related to quality and in the linkage with outsourced laboratories for the tests which cannot be performed inside the company.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eda0d957-3c3c-4139-b89a-a18bc9e18897','key_17965','','','2411','2411.1','accountant',replace('cost accountant\nbookkeeper\naccounting supervisor\naccounting officer\nauditor\naccountant\nproduction accountant\nfinancial reporting accountant\naccount auditor\ncertified public accountant\nregister accountant\nfinancial accountant\naccounting technician\nhead financial accountant','\n',char(10)),'Accountants review and analyse financial statements, budgets, financial reports, and business plans in order to check for irregularities resulting from error or fraud, and provide their clients with financial advice in matters such as financial forecasting and risk analysis. They may audit financial data, resolve insolvency cases, prepare tax returns and provide other tax-related advice in reference to current legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eda6cb5e-c27a-4661-b536-eadeb387dee3','key_17966','','','1420','1420.4.7','bicycle shop manager',replace('bicycle shop manager\nbike superstore manager\nbike shop manager\ncycling superstore manager\ncycles and  cycle accessories store manager\ncycle store manager\ncycle superstore manager\nbicycle and bicycle accessories shop manager\nbike store manager\ncycles and  cycle accessories shop manager\nbicycle and bicycle accessories superstore manager\ncycle shop manager\ncycling shop manager\nbicycle and bicycle accessories store manager\ncycles and  cycle accessories superstore manager\ncycling store manager','\n',char(10)),'Bicycle shop managers are responsible for activities and staff in specialised shops that sell and repair bicycles. ​They manage employees, monitor the sales of the store, manage budgets and order supplies when a product is out of supply and perform administrative duties if required.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/edf2e989-d7c5-496e-b365-81fc5cb9eb39','key_17967','','','3115','3115.1.11','mechatronics engineering technician',replace('mechatronic device technician\nmechatronics technician\ntechnologist in mechatronics\ntechnician in mechatronic devices\nmechatronic device technologist\ntechnologist in mechatronic devices\ntechnician in mechatronics\nmechatronics engineering technician\nmechatronics technologist','\n',char(10)),'Mechatronics engineering technicians collaborate with engineers in the development of mechatronic devices and applications through a combination of mechanical engineering, electronic engineering, and computer engineering. They build, test, install, and calibrate mechatronics and solve technical problems.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ee37a4a5-5360-4e94-a749-a2b8ead08500','key_17968','','','8131','8131.2.5','tracer powder blender',replace('flammable chemical mixer\ntracer-powder blender\nigniter powder mixer\ntracer powder maker\ntracer powder blender\nflammable chemical blender\nigniter powder blender\ntracer powder mixer','\n',char(10)),'Tracer powder blenders operate machines and equipment used to create igniters and tracer powders, by mixing liquid and dry chemicals, making sure the required parameters are set and that the flammable mixtures are according to specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ee5f8b2f-4b40-4426-8290-a6926ae4898b','key_17969','','','1324','1324.3.1.6.21','meat and meat products distribution manager',replace('meat and meat products supply chain manager\ngraduate meat and meat products distribution manager\ntrainee meat and meat products distribution manager\nsenior meat and meat products distribution manager\nmeat and meat products logistics planner\nmeat and meat products logistics manager\nmeat and meat products distribution planner\nmeat and meat products supply chain planner\nmeat and meat products distribution manager\nassistant meat and meat products distribution manager','\n',char(10)),'Meat and meat products distribution managers plan the distribution of meat and meat products to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ee646fa1-3f09-4e43-9275-3aa958ca00ae','key_17970','','','3152','3152.5','skipper',replace('boatmaster\nboat captain\ncommander\nship captain\nskipper\nshipmaster','\n',char(10)),'Skippers are the highest authority on-board or on inland waterways, they are in charge of the vessel and are held responsible for the safety and well being of the clients and crew. They are licensed by the responsible authority and will determine the operations of the vessel at any time. They are the ultimate instance responsible for the crew, the ship, the cargo and/or passengers, and the voyage.','','Excludes people working with <a resource="http://data.europa.eu/esco/skill/cea76131-1049-467d-bd00-bd155f5d65da" typeOf="http://data.europa.eu/esco/model#Skill" href="http://data.europa.eu/esco/skill/cea76131-1049-467d-bd00-bd155f5d65da"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">fishing vessels</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eea2b5c6-48d1-4612-957e-585783b3c7bb','key_17971','','','1343','1343.1','elderly home manager',replace('nursing home manager\neldery home administrator\nelderly care home supervisor\ngeneral manager for nursing home\nelderly home supervisor\ncare home for the elderly supervisor\ncare home for the eldery manager\nelderly home manager\nelderly care home manager\nhome for the elderly manager\nnursing home general manager','\n',char(10)),'Elderly home managers oversee, plan, organise and evaluate the provision of elderly care services for people who are in need of these services due to the effects of ageing. They manage the elderly care home and supervise the activities of the staff.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eeac6251-1974-42f5-91ed-513c6c883755','key_17972','','','8344','8344.1','forklift operator',replace('forklift operator\nwarehouse forklift operator\nwarehouse forklift driver\nwarehouse material handler\norder picker driver\nforklift truck operator\noperator of forklift trucks\nforklift driver','\n',char(10)),'Forklift operators are responsible for operating forklifts to move, locate, relocate, stack, and count merchandise. They are accountable for the safe and efﬁcient operation of forklifts. Moreover, they perform the filling of orders and check the accuracy of other orders.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eedbf8b1-9432-4f42-ab7f-db5cd3eb2a05','key_17973','','','8342','8342.3','excavator operator',replace('plant operator, excavator\ntracked excavator operator\nequipment operator, excavator\nheavy plant operator, excavator\nexcavator operator\n360 degree excavator operator\nconstruction plant operator, excavator\nbackhoe excavator operator\nexcavator\nexcavation worker','\n',char(10)),'Excavator operators use excavators to dig into earth or other materials to remove it. They are involved in a variety of projects, such as demolition, dredging, and the of digging holes, foundations and trenches.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/eee68e13-8248-43bb-a9fb-959832a6c217','key_17974','','','2131','2131.4','biologist',replace('biology science researcher\nentomologist\nzoologist\nmammalogist\nichthyologist\nwildlife biologist\nmycologist\nornithologist\nlepidopterist\nbiology biotechnologist\nnatural science taxonomist\nherpetologist\nplant pathologist\nbiologist\nbiology research scientist','\n',char(10)),'Biologists study living organisms and life in its broader extent in combination with its environment. Through research, they strive to explain the functional mechanisms, interactions, and evolution of organisms.','','Includes people performing medical research, <a resource="http://data.europa.eu/esco/skill/61bef658-d7f9-4759-b1b0-0f9cb3bc581a" typeOf="http://data.europa.eu/esco/model#Skill" href="http://data.europa.eu/esco/skill/61bef658-d7f9-4759-b1b0-0f9cb3bc581a"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">biotechnology</span></a>, biochemistry, pharmaceutical research.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ef53663e-c295-43a4-a48e-84254270b138','key_17975','','','7126','7126.5','irrigation system installer',replace('irrigation sprinkler system installer\nirrigation installer\nirrigation specialist\nirrigation system installer\nirrigation project worker\nagricultural irrigation system installer\nirrigator','\n',char(10)),'Irrigation system installers build the necessary infrastructure for irrigation of soil, usually for agricultural purposes. They may be specialised in one or more of the various types of stationary irrigation systems.','','Excludes people using mobile irrigation devices.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ef5b17a5-3ca8-4965-bd3d-61c4aca490d1','key_17976','','','1349','1349.11','document management officer',replace('records manager\ndocument manager \narchive manager \ndocument management specialist \nlead document controller \ndocument controller\ndocumentation manager\ndocument control manager','\n',char(10)),'Document management officers ensure that the documents required for their organisation''s functioning and daily operations are registered, classified and archived correctly and are made available to the different services or to the public upon request. They oversee the implementation of internal procedures and promote proper document management practices within the organisation, providing training to other employees on document management procedures. They may operate electronic records management systems (ERMS), electronic document management systems (EDMS) and archives management systems (AMS) and provide support in defining related technical requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ef6020f9-11b4-4d1a-96e0-903331594121','key_17977','','','1223','1223.2.1.2','footwear product development manager',replace('footwear product manager\nfootwear production and development manager\nfootwear product development manager\nproduct development team leader\nfootwear production & development manager \npre-production manager\nfootwear development team leader\nsenior product developer','\n',char(10)),'Footwear product development managers coordinate the footwear design and the product and collection development process in order to comply with design specifications, deadlines, strategic requirements and policies of the company. They track style development and review design specifications in order to meet the design vision, the manufacturing environment, and the company’s financial goals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ef909843-97c6-4c48-8038-2e7aefdfcad5','key_17978','','','1342','1342.1','clinical informatics manager',replace('clinical informatics manager\nhealth informatics director\nclinical informatics director\nclinical information systems manager\nhealth informatics manager\nmanager of clinical informatics\nsupervisor of clinical informatics\nclinical information systems supervisor\nclinical informatics supervisor\ndirector of clinical informatics\npathology ICT systems manager','\n',char(10)),'Clinical informatics managers oversee the daily operations of information systems used in a medical institutions. They also carry out research, using their understanding of clinical practices to find ways to improve healthcare services.','',replace('Excludes people performing patients treatment. \n Excludes people performing assistance in medical treatment.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ef937393-3382-438d-b570-399d44b5a41c','key_17979','','','3331','3331.2.1.21','import export specialist in machine tools',replace('assistant import specialist in machine tools\ntrainee import export specialist in machine tools\nsenior import export specialist in machine tools\ngraduate export specialist in machine tools\ntrainee import specialist in machine tools\nsenior export specialist in machine tools\nassistant import export specialist in machine tools\ngraduate import specialist in machine tools\nassistant export specialist in machine tools\nimport export specialist in machine tools\ntrainee export specialist in machine tools\ngraduate import export specialist in machine tools\ncustoms compliance specialist in machine tools\nsenior import specialist in machine tools\ninternational trade compliance specialist in machine tools','\n',char(10)),'Import export specialists in machine tools have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ef9c2914-2f3d-464c-ae0a-d02b403dc7e9','key_17980','','','3115','3115.1.2','aircraft engine inspector',replace('engine inspector\nplane engine inspector\naeroplane engine inspection technician\npiston aircraft engine inspector\njet aircraft engine inspector\nrotary aircraft engine inspector\naircraft engine inspector\nmilitary aircraft engine inspector\ncivilian aircraft engine inspector\nairplane engine inspector\nlight aircraft engine inspector\nhelicopter engine inspector\naeroplane engine inspector\naeroplane  engine inspection engineer\nengine inspection technician\nengine inspection engineer','\n',char(10)),'Aircraft engine inspectors inspect all types of engines used for aircrafts in factories to ensure compliance with safety standards and regulations. They conduct routine, post-overhaul, pre-availability and post-casualty inspections. They provide documentation for repair activities and technical support to maintenance and repair centres. They review administrative records, analyse the operating performance of engines and report their findings.','','Excludes <a resource="http://data.europa.eu/esco/occupation/b343dd0c-8a83-4e13-a79c-0ac52f526c64" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/b343dd0c-8a83-4e13-a79c-0ac52f526c64"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">aircraft engine tester</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/efa81635-9125-4276-8902-9deb12170ea7','key_17981','','','2161','2161.1.1','interior architect',replace('project architect\njunior interior designer\ninterior architect\ninterior remodeler\nsenior interior designer\nbuilding designer\nspace planning interior designer\ninterior designer\ninterior design project manager\nconstruction manager\ndesign architect\narchitect','\n',char(10)),'Interior architects create plans of the interior of a home, building or other structure. They determine the specifications and distribution of the space. Interior architects combine an understanding of space with a sense for aesthetics in order to create a harmonious interior design. They draw architectural drawings using computer-aided equipment and software, or using conventional methods like paper and pen.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/efc75d4e-dfbf-4178-929c-0ae198801c36','key_17982','','','2142','2142.1.6','mining geotechnical engineer',replace('mining hydrologist\nmining geotechnical engineer\nmine hydrologist','\n',char(10)),'Mining geotechnical engineers in mining perform engineering, hydrological and geological tests and analyses to improve the safety and efficiency of mineral operations. They oversee the collection of samples and the taking of measurements using geotechnical investigation methods and techniques. They model the mechanical behaviour of the rock mass and contribute to the design of the mine geometry.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/efd5d3cb-3716-43b7-b21b-98e17f00e9eb','key_17983','','','9311','9311.1','mining assistant','mining assistant','Mining assistants perform routine duties in mining and quarrying operations. They assist the miners with maintaining equipment, with laying pipes, cables and tunnels, and with removing wast.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f00154a5-d27f-429e-b9a4-1d65c672cee7','key_17984','','','4226','4226.1.2','veterinary receptionist',replace('veterinary receptionists\nveterinary practice administration officer\nveterinary practice secretary\nvet secretary\nanimal care receptionist\nveterinary receptionist\nanimal practice secretary','\n',char(10)),'Veterinary receptionists provide reception and office/administrative support in a veterinary practice, scheduling appointments and receiving clients, sale and advice on animal related products, in accordance with national legislation. ','','Excludes people working in veterinary hospitals or large practices.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f0296b9c-764d-42ab-8de9-2a3729a9b67a','key_17985','','','2131','2131.4.9','marine biologist',replace('sea biologist \nmarine biology researcher \nmarine botanist\nmarine zoologist \nmarine ecologist\nresearcher in marine biology ','\n',char(10)),'Marine biologists study marine living organisms and ecosystems and their interaction underwater. They research on the physiology, the interactions between organisms, their interactions with their habitats, the evolution of marine species, and the role of the environment in their adaptations. Marine biologists also perform scientific experiments in controlled conditions to understand these processes. They also focus on the effects of human activities on the life in the oceans and seas.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f04eaeff-52f5-4d52-93ad-6f846884827f','key_17986','','','1311','1311.1','forester',replace('woodland manager\nforestry manager\nforester\nforest manager','\n',char(10)),'Foresters are responsible for monitoring the natural and economic viability of a woodland or forest and for activities related to its management and conservation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f0691bb2-c717-4215-a814-88a15b10ee4d','key_17987','','','7131','7131.2','marine painter',replace('marine blaster\nboat painter\nnaval painter\nship painter\nmaritime painter','\n',char(10)),'Marine painters work in the shipbuilding industry and are responsible for completing the blasting, painting, hull washing and cleaning, scraping and protection jobs of all varieties as assigned by supervisors. They follow set guidelines, processes and procedures to ensure successful completion of work.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f07aab15-c7e8-49a4-a8dd-221d9cf3a925','key_17988','','','1420','1420.4.24','fuel station manager',replace('supermarket filling station manager\ngas station manager\nfuel site manager\nfuel station manager\npetrol station manager\nretail fuel forecourt manager\nretail fuel manager\npetrol site manager\nfuel retail manager\nretail forecourt manager\nfilling station manager','\n',char(10)),'Fuel station managers assume responsibility for activities and staff in a fuel station.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f0978ec0-ae7d-4c4a-8f80-3fd200edddac','key_17989','','','3112','3112.1.3','construction quality inspector',replace('construction quality inspector\nconstruction QA QS inspector\nconstruction quality control and safety inspector\nconstruction QA QS supervisor\nconstruction quality control and safety supervisor','\n',char(10)),'Construction quality inspectors monitor the activities at larger construction sites to make sure everything happens according to standards and specifications. They pay close attention to potential safety problems and take samples of products to test for conformity with standards and specifications.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f09b6100-28a6-4be5-9726-ecf5b24f74f6','key_17990','','','3435','3435.25','theatre technician',replace('theater technician\nstage technician\nstagehand\nperformance technician\ntheatrical technician\ntheatre tech\naudio technician\nlighting technician','\n',char(10)),'Theatre technicians execute all technical tasks to support live performances. They build and break down stages and decors, install and operate sound, light, recording and video equipment and organise the transport of decors and technical equipment for performances on displacement.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f0ca39a8-1e30-487c-95f9-ef2d5e734adc','key_17991','','','1330','1330.5','ICT operations manager',replace('IT process manager\nsystems and networks manager\nIT operations manager\ndata processing manager\nICT acquisitions manager\nIT data processing manager\nICT operations managers\nICT operations manager\nICT data processing manager','\n',char(10)),'ICT operations managers coordinate ICT services and infrastructure ensuring that the organisation has the required infrastructural resources. They also plan and monitor stages of either a business process or a computer process, negotiate contracts and take mitigation action in case of non-fulfilment of agreements. They oversee the day-to-day tasks involving infrastructure components, ICT systems and software.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f0e1cc13-48e0-4995-8699-ee43467bce0d','key_17992','','','2330','2330.1.11','mathematics teacher at secondary school',replace('high school mathematics teacher\nmathematics teacher\nsecondary school mathematics teacher\nhigh school maths teacher\nsecondary school maths teacher\nmaths teacher\nteacher of mathematics in high school\nteacher of maths in secondary school\nteacher of maths in high school\nmathematics teacher high school\nteacher of mathematics in secondary schools','\n',char(10)),'Mathematics teachers at secondary schools provide education to students, commonly children and young adults, in a secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, mathematics. They prepare lesson plans and materials, monitor the students'' progress, assist individually when necessary, and evaluate the students'' knowledge and performance on the subject of mathematics through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f0e479d9-7afd-40b1-b60f-b68c400c15fb','key_17993','','','3112','3112.1.2','building inspector',replace('asbestos inspector\nbuilding control officer\nbuilding services inspector\nbuilding inspector\nconstruction control surveyor\nbuildings inspector\nbuilding control surveyor\nbuilding standards inspector\nconstruction standards inspector\nconstruction services inspector\nconstruction inspector\nconstruction control officer','\n',char(10)),'Building inspectors perform inspections of buildings to determine compliance with specifications for various focuses of assessment. They observe and determine the suitability of construction, quality and resistance, and general compliance with regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f0f6d531-eb96-4bad-84ff-8e2999ea68e6','key_17994','','','7422','7422.3.1','mobile phone repair technician',replace('hand held phone technician\ncell phone repair technician\ncell phone technician\ncellullar phone technician\nmobile phone repairer\nLCD technician\ncommunication equipment repair technician\nmobile phone repairman','\n',char(10)),'Mobile phone repair technicians run tests to assess the mobile phones'' functionality, install and update phone software, troubleshoot wiring problems, and replace damaged parts and components such as batteries, LCD screens, keypads, buttons. They also advise their clients on warranty issues and recommend products based on their expertise.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f14194e2-8e7f-4fc9-9c8c-78d9ae2e7284','key_17995','','','2149','2149.9.4','nuclear engineer',replace('nuclear engineer\nnuclear engineers\nnuclear engineering consultant\nnuclear engineering adviser\nnuclear technology engineering specialist\nnuclear technology engineering consultant\natomic power engineer\nnuclear technology engineering adviser\nnuclear power engineer\nnuclear facility engineer\natomic decommissioning engineer\natomic facility engineer\nnuclear safety engineer\nnuclear decommissioning engineer\nnuclear technology engineer\natomic safety engineer\nnuclear engineering specialist\nnuclear technology engineering expert\nnuclear engineering expert','\n',char(10)),'Nuclear engineers plan and design engineering equipment and processes in nuclear plants and sites. They engage in engineering activities pertaining to nuclear power plants, and develop risk solutions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f1783e61-38b7-468e-b495-9c3c92f5ab0d','key_17996','','','7511','7511.6.1','halal slaughterer',replace('halal slaughterperson\nhalal method slaughterer\nhalal slaughterman\nhalal slaughterer\nhalal slaughterwoman\nslaughterer following halal method','\n',char(10)),'Halal slaughterers slaughter animals and process carcasses of halal meat from cows and chickens for further processing and distribution. They slaughter animals as stated in Islamic law and ensure that the animals are fed, slaughtered and hung up accordingly.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f1b89616-5dfe-40d4-8b02-58a6982b1a01','key_17997','','','2151','2151.1.6','substation engineer',replace('sub stations engineer\nsubstation technology engineer\nsubstation technology engineering expert\nsubstation technology engineering consultant\nsubstation engineering expert\nsub station engineer\nsubstation engineer\nsub-stations engineer\nsubstation engineering specialist\nsubstation engineering adviser\nsubstation engineering consultant\nsub-station engineer\nsubstations engineer\nsubstation technology engineering adviser\nsubstation technology engineering specialist','\n',char(10)),'Substation engineers design medium and high voltage substations used for the transmission, distribution, and generation of electrical energy. They develop methods for the efficient operation of the energy process, and ensure compliance to safety and environmental standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f1c8c186-bef9-4d72-9257-90fc4f992ab7','key_17998','','','3133','3133.1.5','gas station operator',replace('butadiene compressor operator\ngas-compressor operative\ngas compressor and gas pumping station operator\ngas compressor and gas pumping station operative\ngas pumping station operative\nbutane compressor operator\nethylene compressor operator\ngas compressor station operator\ngas-compressor operator\ngas station operator\ngas pumping station operator','\n',char(10)),'Gas station operators process gases for compression, transmission or recovery by using gas, steam or electric engine compressors. They perform chemical tests on gases and are responsible for pumps and pipelines operations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f1ecc3c4-8212-4386-871a-6fda1b5b4d91','key_17999','','','3339','3339.2','auctioneer',replace('sale auctioneer\nmarket auctioneer\ntrainee auctioneer\nauctioneer for personal goods\nsenior auctioneer\nmerchant\nauctioneer for real estate\nauctioneer\nauctioneer for personal property\nfactor\nagent','\n',char(10)),'Auctioneers conduct auctions by accepting bids and declaring goods sold.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f1fcad3b-fdf0-444a-81b0-e50e96f8966a','key_18000','','','3432','3432.2','merchandiser',replace('senior retail merchandiser\nmerchandise planner\nmerchandising controller\nmerchandising manager\nretail merchandising manager\nmerchandising associate\nmerchandiser\nmerchandising supervisor\nassistant merchandiser\nsenior merchandiser\ngraduate merchandiser\nretail merchandising supervisor\nretail merchandising associate\nassistant retail merchandiser\nretail merchandising controller\ngraduate retail merchandiser','\n',char(10)),'Merchandisers are responsible for positioning goods following standards and procedures.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f20d3ae6-baeb-482c-b68d-c2fb115e5dde','key_18001','','','5111','5111.2.1','flight attendant',replace('flight attendant\ninflight service attendant\ncabin  attendant\nflight crew member\nsteward\nstewardess\ncabin crew member\naircraft attendant\nair crew member','\n',char(10)),'Flight attendants perform a variety of personal services conducive to the safety and comfort of airline passengers during flight. They greet passengers, verify tickets, and direct passengers to assigned seats. They prepare reports after landing describing how the flight went in terms of operations, procedures, and anomalies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f219f643-4df1-4a9a-b6c5-dc2f48c22806','key_18002','','','5311','5311.1.1','au pair',replace('baby sitter\nau pair\nchild caretaker\nchildminder','\n',char(10)),'Au pairs live and work for a host family in another country and are usually in charge of taking care of the family''s children. They are young individuals, seeking to explore another culture while providing child care services as well as other light housekeeping activities such as cleaning, gardening and shopping.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f235bc5a-dbf6-4f9e-bbdb-cf313d32b75b','key_18003','','','2354','2354.1','music teacher',replace('singing teacher\nrythmics teacher\nteacher of music\ninstructor of music\ninstrument teacher\nprivate music instructor\nprivate music teacher\neurythmist\nprivate teacher of music\nindividual music teacher\nmusic teacher\nperipatetic music instructor\nperipatetic music teacher','\n',char(10)),'Music teachers instruct students in various musical genres and expression forms, such as classical, jazz, folk, pop, blues, rock, electronic etc. in a recreational context. They provide students with an overview of music history and repertoire, but primarily utilise a practice-based approach in their courses. In these courses, they assist students to experiment with different styles and techniques, in the musical instrument of their choice while encouraging them to develop their own style. They cast, direct, and produce musical performances, and coordinate the technical production.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f2413988-f76a-4636-a4b1-9952517aa21f','key_18004','','','1112','1112.4','fire commissioner',replace('brigade manager\nfire brigade commissioner\nfire service commissioner\nchief fire officer\nfire commissioner\narea manager','\n',char(10)),'Fire commissioners oversee the activity of the fire department making sure the services supplied are effective and the necessary equipment is provided. They develop and manage the business policies ensuring the legislation in the field is followed. Fire commissioners perform safety inspections and promotes fire prevention education.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f272a521-1a38-4d34-a78e-3adeca95b977','key_18005','','','9312','9312.1.3','road maintenance worker',replace('road maintenance team member\nroad maintenance worker\nhighway maintenance worker\nroad maintenance operative\nhighways operative','\n',char(10)),'Road maintenance workers perform routine inspections of roads, and are sent out to perform repairs when called for. They patch potholes, cracks and other damage in roads.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f2a416b8-c7e4-49ee-964d-a49f6af79b84','key_18006','','','3152','3152.4','ship captain',replace('sea captain\nship captain\nshipmaster\ncaptain of a ship\nship''s captain','\n',char(10)),'Ship captains are in charge of a vessel for the transportation of goods and passengers, operating in offshore and coastal waters. The size of the vessel can range from a small vessel to a cruise liner depending on the tonnage that they are certified to sail. Ship captains have vast experience with ships and their operation, and are likely to have worked their way through the ranks of other ship-related positions.','','Excludes people managing fishing vessels.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f2b15a0e-e65a-438a-affb-29b9d50b77d1','key_18007','','','2512','2512.4','software developer',replace('software specialist\nsoftware developers\nprogrammer\napplication software developer\nsoftware engineer\nsolutions developer\napplications engineer\ndeveloper of software\napplication programmer\nsoft developer\napplication developer\nsoftware developer','\n',char(10)),'Software developers implement or program all kinds of software systems based on specifications and designs by using programming languages, tools, and platforms.','','Includes people performing software design.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f2cc5978-e45c-4f28-b859-7f89221b0505','key_18008','','','0110','0110.1','air force officer',replace('royal airforce officer\nflight lieutenant\nflight officer\nair commodore\nair force officer\nmilitary group captain\ncommand and control officer\npilot officer\nsquadron leader','\n',char(10)),'Air force officers specialise in flying or ground duties, and supervise a team of air force personnel. They coordinate their team''s training and welfare, and perform duties specific to their area of specialisation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f2ee2cda-9d02-49c3-8280-da5892d0055b','key_18009','','','7413','7413.1.3','street lighting electrician',replace('highway lights operative\nstreet lighting repair electrician\nstreet lighting installation electrician\nstreet light electrician\nstreet light repair electrician\nhighway lighting repair electrician\nhighway lighting operative\nstreet lighting operative\nstreet light operative\nhighway lighting installation electrician\nstreet lighting electrician','\n',char(10)),'Street lighting electricians construct and maintain electric power transmission and distribution in street lights. They maintain, test and repair street lights in compliance with safety regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f2f4ed8f-c9d3-47e4-8f72-a30739bcea2b','key_18010','','','1420','1420.4.6','beverages shop manager',replace('soft and alcoholic drinks shop manager\nbeverages and alcoholic drinks store manager\noff-licence store manager\nalcoholic beverages shop manager\nbeverages store manager\nalcoholic drinks shop manager\nalcoholic drinks store manager\noff-licence manager\nsoft and alcoholic beverages store manager\nbeverages shop manager\nsoft and alcoholic beverages shop manager\nalcoholic beverages store manager\noff-licence shop manager\noff-sales store manager\noff-sales shop manager','\n',char(10)),'Beverages shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f31e25d0-8794-4f88-ac07-a229199c6f0f','key_18011','','','8156','8156.3.1','leather goods maintenance technician','leather goods maintenance technician','Leather goods maintenance technicians programme and tune different types of cutting, stitching, finishing and specific equipment related to leather goods manufacturing. They take care of the preventive and corrective maintenance of the various equipment by periodically verifying their working conditions and performance, analysing faults, correcting problems, repairing and substituting components and performing routine lubrications. They provide information on the usage of equipment and its energetic consumption to decision makers inside the company.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f31f487b-4ce2-40f3-90e8-7d0011b4ef1d','key_18012','','','1330','1330.5.1','software manager',replace('software applications manager\napplications manager\nICT applications manager\nsoftware managers\nsoft manager\nsoftware manager','\n',char(10)),'Software managers oversee the acquisition and development of software systems in order to provide support to all organisational units. They also monitor the results and quality of the different software solutions and projects implemented in the organisation.','',replace('Includes people performing managerial activities. \n Excludes people performing programming and coding activities.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f33a70bd-c5b6-495d-8e0c-5e2c6e56d93e','key_18013','','','3131','3131.3.7','power plant control room operator',replace('electric power station operative\nelectricity generation operative\nelectric power station worker\npower station control room worker\npower station operative worker\nelectricity generating plant worker\nelectric power station operator\nelectricity generation operator\npower station operator\nelectricity generating plant operative\nelectricity generation plant worker\npower plant control room operator\nelectricity generation plant operator','\n',char(10)),'Power plant control room operators are responsible for the safe and proper operation of power plants, switchyards and associated control structures. They repair and maintain the involved machinery and equipment to ensure the plant''s efficient operation and to tackle emergency situations such as blackouts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f34cad67-3cd9-4d31-8dde-e439ac720136','key_18014','','','3331','3331.2.1.30','import export specialist in textile industry machinery',replace('senior export specialist in textile industry machinery\nsenior import specialist in textile industry machinery\ngraduate import specialist in textile industry machinery\nassistant import export specialist in textile industry machinery\nassistant import specialist in textile industry machinery\ntrainee import specialist in textile industry machinery\ninternational trade compliance specialist in textile industry machinery\ngraduate export specialist in textile industry machinery\nsenior import export specialist in textile industry machinery\ncustoms compliance specialist in textile industry machinery\ntrainee import export specialist in textile industry machinery\ntrainee export specialist in textile industry machinery\nassistant export specialist in textile industry machinery\nimport export specialist in textile industry machinery\ngraduate import export specialist in textile industry machinery','\n',char(10)),'Import export specialists in textile industry machinery have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f3b99acc-b212-45cf-bb4f-7b83cc6f0d71','key_18015','','','3141','3141.2.1','bacteriology technician',replace('medical laboratory technician\nchemistry technologist\nbiochemistry technologist\nbacteriology technician\nchemist\nbacteriology technologist\nmedical lab technician\nlaboratory technician\nbiologist\nbiology technician\nbacteriology lab technician\nchemistry technician\nlab technician\nbiochemistry lab technician\nbiochemistry laboratory technician\nbacteriology culture technician\nbiology technologist','\n',char(10)),'Bacteriology technicians provide technical assistance in researching and testing bacteria using laboratory equipment. They collect and analyse data for experiments, compile reports and maintain laboratory stock.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f3d2074e-3f44-45b0-b896-5ee5629a23c1','key_18016','','','3331','3331.2.1.7','import export specialist in coffee, tea, cocoa and spices',replace('assistant import specialist in coffee, tea, cocoa and spices\nsenior import export specialist in coffee, tea, cocoa and spices\ngraduate export specialist in coffee, tea, cocoa and spices\nsenior import specialist in coffee, tea, cocoa and spices\ntrainee export specialist in coffee, tea, cocoa and spices\nassistant export specialist in coffee, tea, cocoa and spices\ntrainee import export specialist in coffee, tea, cocoa and spices\nsenior export specialist in coffee, tea, cocoa and spices\nassistant import export specialist in coffee, tea, cocoa and spices\ngraduate import export specialist in coffee, tea, cocoa and spices\ntrainee import specialist in coffee, tea, cocoa and spices\nimport export specialist in coffee, tea, cocoa and spices\ngraduate import specialist in coffee, tea, cocoa and spices','\n',char(10)),'Import export specialists in coffee, tea, cocoa and spices have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f3d8bc61-f812-4106-afd2-a356e4c11fce','key_18017','','','2641','2641.3','literary scholar',replace('literary studies scholar\nliterary researcher\nliterature scholar\nliterary science researcher\nliterary research analyst\nliterary scholar\nliterary research scientist\nliterary studies researcher\nliterary analyst\nliterary studies research scientist\nliterary studies scientist\nliterary scientist','\n',char(10)),'Literary scholars research works of literature, history of literature, genres, and literary criticism in order to appraise the works and the surrounding aspects in an appropriate context and to produce research results on specific topics in the field of literature.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f3da16bd-157d-4a48-8a49-33809a8b5d96','key_18018','','','3324','3324.4.13','wholesale merchant in flowers and plants',replace('wholesale merchant in flowers and plants\nwholesaler in flowers and plants\nbusiness to business sales consultant in flowers and plants\nbusiness to business seller in flowers and plants\nB2B sales consultant in flowers and plants\nwholesale trader in flowers and plants\nwholesale merchandiser in flowers and plants\ngraduate wholesale merchant in flowers and plants\nassistant wholesale merchant in flowers and plants\ntrainee wholesale merchant in flowers and plants\nwholesale buyer in flowers and plants\nsenior wholesale merchant in flowers and plants','\n',char(10)),'Wholesale merchants in flowers and plants investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f3e23761-0663-4ae0-97a2-0b2dd3fa38a1','key_18019','','','2145','2145.1.1','biochemical engineer',replace('biochemical engineering specialist\nbiochemical engineering researcher\nbiochemical technology engineering expert\nbiochemical technology engineering specialist\ntissue engineer\nbiochemical process engineer\nmetabolic engineer\nbiochemical engineering consultant\nbiochemical researcher\nprocess development engineer\nenzyme engineer\nbiochemical technology engineering consultant\nbiochemical engineering expert\nbiochemical technology engineer\nbiochemical engineer\nbio-fuel engineer\nbiochemical engineering adviser\nbiochemical research engineer\nbiochemical technology engineering adviser\nbio-chemical engineer','\n',char(10)),'Biochemical engineers research on the field of life science striving for new discoveries. They convert those findings into chemical solutions that can improve the wellbeing of society such as vaccines, tissue repair, crops improvement and green technologies advances such as cleaner fuels from natural resources.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f3eb330a-03b5-4537-ac0e-bcee5c67bcff','key_18020','','','4323','4323.1','aircraft cargo operations coordinator',replace('air freight coordinator\ncoordinator of aircraft cargo operations\nair freight operations officer\nair cargo traffic officer\nco-ordinator of aircraft cargo operations\nair cargo operations supervisor\naircraft cargo operations and ramp services coordinator\nsupervisor of aircraft cargo operations\naircraft cargo operations and ramp services co-ordinator\naircraft cargo operations coordinator','\n',char(10)),'Aircraft cargo operations coordinators direct and coordinate air transport terminal cargo and ramp activities. They review data on incoming flights as to plan the working activities. They direct preparation of loading plans for each departing flight and confer with supervisory personnel to ensure workers and equipment are available for air cargo and baggage loading, unloading, and handling activities.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f3fc11ca-9a72-40da-bf0f-b2aab7e33079','key_18021','','','7113','7113.1','stonemason',replace('craft stonemason\nmemorial mason\nstone setter\nstone cutter\nartisanal stonemason\nstone carver\narchitectural stone mason\nbuilding mason\ntombstone carver\nartisanal stone mason\nstone finisher\ncraft mason\nstonemason\nheritage stone mason\nmemorial stonemason\narchitectural stonemason\nstone mason\nmason\nfixer mason\nstructural stoneworker\nstoneworker\nbanker mason\nheritage stonemason','\n',char(10)),'Stonemasons manually carve and assemble stone for construction purposes. While CNC operated carving equipment is the industry standard, artisanal carving for ornamental stone is still done manually.','','Excludes people using automated equipment.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f444e8ec-5eed-4777-866e-7127aa06ce4f','key_18022','','','3139','3139.1.1','clothing process control technician',replace('garment manufacturing technician\nclothing manufacturing technician\nclothing process control technician\nsample room supervisor\nsample room line supervisor','\n',char(10)),'Clothing process control technicians operate multiple process control equipment in manufacturing assembly lines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f4525ed8-54eb-4a3b-90db-55cc01b0d9fd','key_18023','','','3412','3412.4.2','care at home worker',replace('community home care worker\nhome care assistant\nhomecare assistant\nindependent living support worker\ndomiciliary home care worker\ndomiciliary care worker\ncare worker (homecare)\ndomiciliary homecare worker\nliving at home support worker\ncare at home worker\nhome care worker','\n',char(10)),'Care at home workers provide domiciliary services to vulnerable adults including frail elderly or disabled people who are living with physical impairment or convalescing. They aim to improve their lives in the community and assure patients can live safely and independently in their own home.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f470b785-643c-46f9-8b31-6085427ab7b8','key_18024','','','3154','3154.1.2','aeronautical information specialist',replace('aeronautical information manager\naeronautical data specialist\nspecialist in aeronautical information\naeronautical information specialist','\n',char(10)),'Aeronautical information specialists provide high quality aeronautical information management services through technological means. They support senior aeronautical information specialists and assess changes in aeronautical information affecting charts and other products. They answer requests related to aeronautical data needs for airway companies, operational groups and systems.','','Excludes <a resource="http://data.europa.eu/esco/occupation/4be03a63-0423-4e50-acbf-aec405bb0741" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/4be03a63-0423-4e50-acbf-aec405bb0741"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">aeronautical information officers</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f489b1ec-3d6c-44b1-b1d7-c6c97db7df30','key_18025','','','6210','6210.1','forest ranger',replace('forest ranger\nwoodland ranger\nforestry officer\nwoodland officer','\n',char(10)),'Forest rangers are responsible for the protection and conservation of natural resources, particularly in regards to forests and woodlands.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f49d893a-ac89-4f3b-b547-1cbdf4d69fde','key_18026','','','7233','7233.8.2','forestry machinery technician',replace('woodland machinery technician\nforest machine mechanic\nforestry machine mechanic\nforest machinery operative\ntransportation equipment operator\nforestry machinery technician\nforestry machine repairer\nforestry machinery operative\nforestry machine operator\nforest machine repairer\nforestry machinery operator\nforest machine transporter operator\nforestry machinery repairman\nforestry equipment machinist\nforest machine transporter operative','\n',char(10)),'Forestry machinery technicians maintain and transport forestry machinery. As part of the maintenance of forestry machinery, they make use of specialised software and data recording systems and instruments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f4a22809-c00c-4dd0-8b09-c7251f8dcd1c','key_18027','','','7123','7123.2','plasterer',replace('fibrous plasterer\nplaster labourer\nsolid plasterer\nwall plasterer\nstucco mason\nplaster worker\nwall finisher\nheritage plasterer\nplasterer','\n',char(10)),'Plasterers apply plaster made from gypsum, cement or other solutions to walls as a smooth finish. They mix dry plaster powder with water, then smear the resulting paste onto a wall. The plaster is then smoothed before it hardens and forms a solid coating on the wall.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f4a25243-b06a-42a9-9c69-246853df63ad','key_18028','','','9622','9622.1','handyman',replace('handywoman\nhandyman\nbuilding handyperson\nbuilding supervisor\nbuilding maintenance technician\nbuilding maintainer\nbuilding handywoman\nbuilding superintendent\nbuilding handyman','\n',char(10)),'Handymen perform various maintenance and repair activities for buildings, grounds and other facilities. They repair and renovate structures and components, fences, gates and roofs, assemble furniture and perform plumbing and electrical activities. They check heating and ventilation systems, air quality and humidity in the building.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f4b4fb1d-0224-4e6b-b7c9-84bcf133be00','key_18029','','','5223','5223.7.34','telecommunications equipment specialised seller',replace('phone salesperson\nmobile phone and devices salesperson\nmobile devices and accessories specialised seller\nphone specialised seller\nmobile phone and accessories specialised seller\nmobile phone and accessories salesperson\nmobile devices and accessories salesperson\nmobile phone specialised seller\nmobile phone and devices specialised seller\nmobile phone salesperson\ntelecommunications equipment specialized seller','\n',char(10)),'Telecommunications equipment specialised sellers sell telecommunication equipment and services in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f4c997b5-f9e0-4419-b8e7-8e5e2cffc73e','key_18030','','','8159','8159.7','nonwoven  textile technician',replace('non woven technician\nnonwoven textile worker\nnonwoven technician\nnonwoven department manager\nnon-woven textile worker\nnon-woven technician\nnonwoven textile operator\nnonwoven  textile technician\nnon woven textile worker\nnonwoven machine operator','\n',char(10)),'Nonwoven textile technicians perform operations related to setting up non-woven processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f4de7e28-ca67-46cd-b760-30e02b3aa83b','key_18031','','','1349','1349.17','medical laboratory manager',replace('medical laboratory director of operations\nclinical laboratory administrator\nmedical laboratory administrator\nclinical laboratory supervisor\nclinical laboratory director of operations\nmedical laboratory supervisor\nmedical laboratory manager','\n',char(10)),'Medical laboratory managers oversee the daily operations of a medical laboratory. They manage employees and communicate the schedule of activities. They monitor and ensure all laboratory operations are performed according to specifications, arrange the necessary laboratory equipment and assure that the appropriate health and safety standards are followed. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f4e0016b-29fa-48ac-b98d-364c658f8aed','key_18032','','','8160','8160.41','liquor blender',replace('liquor blender\nspirits blender','\n',char(10)),'Liquor blenders regauge, filter, rectify, blend and verify proof of alcoholic drinks before these are prepared for bottling. For the purpose, they tend equipment and machinery to perform each one of these processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f4e3dd1c-f86b-4a69-8887-547255eadc79','key_18033','','','1439','1439.6','tourist information centre manager',replace('tourist information center manager\ntourist information centre (TIC) manager\ntourist information supervisor\ntourist information centre supervisor\ntourist information point manager','\n',char(10)),'Tourist information centre managers are in charge of managing employees and activities of a centre which provides information and advice to travellers and visitors about local attractions, events, travelling and accommodation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f4eaff3d-f8d0-4a6a-aa90-76827c0fbada','key_18034','','','7412','7412.6','household appliances repair technician',replace('household appliances repair technician\nwashing machine servicer\ndryer repairer\ndishwasher repairer\nhome appliance repairman\nwindow air conditioner repairer\nAC repairer\nrefrigerator repairer\nappliance service technician','\n',char(10)),'Household appliances repair technicians use electrical equipment to test resistance or voltage and to identify appliances'' malfunctions. They repair small and large electrical or gas home appliances such as vacuum cleaners, washing machines, dishwashers, air conditioners and refrigerators.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f4f671d5-208f-4423-a250-c049de8d9adc','key_18035','','','2250','2250.4.1','animal hydrotherapist',replace('animal hydrotherapists\nanimal hydrotherapist\nveterinary hydrotherapist','\n',char(10)),'Animal hydrotherapists provide a therapeutic treatment following veterinary diagnosis or referral. They concentrate on use of water-resistance to treat conditions, assist post-operative recovery, give pre-operative exercises and enhance general fitness in accordance with national legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f5166c6b-3467-4fc4-9343-d12e870d9def','key_18036','','','1439','1439.3','contact centre manager',replace('customer service manager\ncontact center manager','\n',char(10)),'Contact centre managers coordinate and plan the daily operations of contact centres. They ensure that customer inquiries are satisfied efficiently and according to policies. They manage employees, resources and procedures to improve best practices and achieve high levels of customer satisfaction.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f5332f2d-3119-4194-8b5c-d0a5d97f55ef','key_18037','','','3339','3339.1','advertising sales agent',replace('advertising sales agent\nmedia saleswoman\nmedia salesperson\nadvertising sales rep\nad sales representative\nmedia salesman\nad sales agent','\n',char(10)),'Advertising sales agents sell advertising space and media time to businesses and individuals. They make sales pitches to potential clients and follow up on after-sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f53dbdb1-2f64-42f8-a6a4-0552c40e4f57','key_18038','','','8160','8160.33','flour purifier operator',replace('grain purifier\nflour purification operator\nflour purification operative\nflour purifier operator\nflour purifier','\n',char(10)),replace('Flour purifier operators tend machines to blend and sift flour. They tend screw conveyors to transport flour for blending and purification processes. They operate separators to sieve blended flour and to remove lumps before it is ready for packaging. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f5576c5b-8d03-4a3d-9cb7-b8c4ac07a7ef','key_18039','','','1344','1344.1.2','youth centre manager',replace('youth center manager\nyouth and community centre manager\nyouth centre coordinator\nyouth community centre manager\ncommunity centre youth worker\nyouth program manager\nyouth recreation centre manager\nyouth & community worker\nyouth club manager','\n',char(10)),'Youth centre managers plan and supervise the operations of children and youth homes which provide care and counselling services. They assess the needs of youth in the community, develop and implement pedagogical methods, and develop programs for the improvement of youth care in the centre.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f5596182-70b2-4c18-97ab-f9405bae0385','key_18040','','','7321','7321.1.5','screen making technician',replace('silk screen engraver\nrotary engraver\ntextile engraver\nsilk screen etcher\ntextile engravers\nscreen making technician\ntextile screen maker\netcher and engraver','\n',char(10)),'Screen making technicians engrave or etch screens for textile printing. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f569145d-8f87-4f80-b861-45583430d9f4','key_18041','','','7312','7312.1','idiophone musical instruments maker',replace('bow maker\nrattle idiophone maker\nidiophone musical instrument maker\nidiophone musical instruments maker\nidiophone musical instrument builder\nscraper idiophone maker\npercussion idiophone maker\nconcussion idiophone maker\nbell maker\nplucked idiophone maker\nidiophone musical instruments builder\nidiophone maker\nbow rehairer\nfriction idiophone maker\nbell neck hammerer','\n',char(10)),'Idiophone musical instruments makers create and assemble parts to make idiophone instruments to specified instructions or diagrams. They shape, drill, sand and string the parts that are mostly made out of glass, metal, ceramics or wood, clean, test the quality and inspect the finished instrument.','','Includes people producing smaller musical instruments or accessories, such as metronomes, tuning forks or musical boxes.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f56e33a3-01bb-46ba-98f4-d47aa242b983','key_18042','','','1324','1324.3.1.5','road transport division manager',replace('road transportation division manager\nmanager of road transport division\ndirector of road transport\nroad transportation operations manager\nroad operations director\nroad transportation operations director\nroad fleet manager\nhead of road transport\nfleet director\nroad transport operations manager\nroad transport operations director\nmotor transport manager\nroad transport division manager','\n',char(10)),'Road transport division managers maintain control of processes related to vehicles, staff, customers, routes, and contracts.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f57eb0cb-e67a-419c-9b7b-4abd811b7e2f','key_18043','','','4323','4323.9','move coordinator',replace('company move planner\ndomestic moving coordinator\ncorporate move coordinator\ncompany move coordinator\ncorporate move planner\nmoving planner\nmove coordinator\nrelocation coordinator\nrelocation planner\nindustrial move coordinator\nremoval planner\nindustrial move planner\nmoving coordinator\ninternational and domestic moving coordinator','\n',char(10)),'Move coordinators envision all the activities required for a successful moving. They receive briefings from the client and translate them in actions and activities that assure a smooth, competitive, and satisfactory moving.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f5aa06b0-745e-4eb1-b050-3e15d11e5eb5','key_18044','','','3122','3122.4.2','chemical processing supervisor',replace('chemical production quality assurance supervisor\nchemical production process supervisor\nchemical plant quality supervisor\nchemical production quality tester\nchemical plant production supervisor\nchemical production supervisor\nchemical processing supervisor\nchemical processing quality supervisor\nchemical processing quality assurance supervisor\nchemical plant process supervisor','\n',char(10)),'Chemical processing supervisors coordinate the activities and the staff involved in the chemical production process, ensuring the production goals and deadlines are met. They control quality and optimize chemicals processing by ensuring defined tests, analysis and quality control procedures are performed.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f5ba8fb4-1387-459a-9b22-59feb891e229','key_18045','','','8212','8212.2.2','electrical cable assembler',replace('wire assembler\nwire and cable assembler\ncable assembler\nassembler of electrical cable\nassembler of electrical cables\nelectrical cable fabricator\ncable wiring operative\ncable and wire assembler\nelectrical cable assembler\ncabling technician','\n',char(10)),'Electrical cable assembler manipulate cables and wires made of steel, copper, or aluminium so they can be used to conduct electricity in a variety of appliances.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f5cb9901-86fc-48d0-9cde-667fbe8cef3a','key_18046','','','9321','9321.2','hand packer',replace('order filler and packer\ne-commerce packer\ne-commerce packer and shipper\nmanual packaging operative\nhand packer\nnight packer\npacker and warehouse operative\npicker and packer\ne-commerce picker and packer\ne-commerce order filler and packer','\n',char(10)),'Hand packers collect, pack and label goods and materials by hand. They ensure that all goods and materials are packed according to instructions and requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f5e80af5-eebe-4133-878a-5dc5ef34fe93','key_18047','','','6111','6111.1','agronomic crop production team leader',replace('crop team manager\nagronomic crop production team leader\ncrops production team leader\ncrop growing team leader\ncrop team leader\nproduction team leader','\n',char(10)),'Agronomic crop production team leaders are responsible for leading and working with a team of crop production workers. They organise the daily work schedules for crop production and participate in the production.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f605bcd2-90b6-45a0-a558-d05016d68a77','key_18048','','','1212','1212.2','human resources manager',replace('personnel director\nHRM manager\noutplacement office manager\nstaffing manager\nhuman resources director\nhuman resources manager\nbenefits manager\npersonnel manager\nrelocation manager\nemployee benefits manager\ncareer advice office manager\nindustrial relations manager\nrecruitment manager\nHR manager\nhuman resources specialist','\n',char(10)),'Human resources managers plan, design and implement processes related to the human capital of companies. They develop programs for recruiting, interviewing, and selecting employees based on a previous assessment of the profile and skills required in the company. Moreover, they manage compensation and development programs for the company''s employees comprising trainings, skill assessment and yearly evaluations, promotion, expat programs, and general assurance of the well-being of the employees in the workplace.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f60dc59b-7681-45d4-8114-b3a646e02ab2','key_18049','','','5141','5141.1.4','performance hairdresser',replace('hair and wig stylist\nhair & wig artist\nhair artist\nhair and wig artist\nperformance hairdresser\nhair & wig stylist\nevent hair stylist','\n',char(10)),replace('Performance hairdressers assist and support artists before, during and after the performance to ensure the hairdressing is in line with the artistic vision of the stage director and the artistic team. They maintain, check and repair wigs and assist with quick changes. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f634ce9b-68a5-4a8a-af4a-ed13fc0deba9','key_18050','','','3411','3411.9','justice of the peace',replace('advocate\npeace commissioner\nmagistrate\njustice\njustice of the peace\npeace justice\ncourt judge','\n',char(10)),'Justices of the peace deal with small claims and disputes, and minor offences. They ensure the keeping of the peace within their jurisdiction, and provide mediation between disputing parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f643bff3-74ef-48d1-b033-820e2d8659e9','key_18051','','','4323','4323.13','rail traffic controller',replace('rail traffic signaller\ncontroller of rail traffic\nrailway signal operator\nsignal box operator\nrail traffic controller\nsignal box controller\ntrain controller','\n',char(10)),'Rail traffic controllers operate signals and points that help make sure trains run safely and on time. They operate from a signal box in order to control the order and movement of trains and ensure safety at all times. They are responsible for maintaining safety standards when trains are running normally and also in degraded or emergency operational situations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f6803a58-91d9-4251-9a07-09fc387cec16','key_18052','','','2652','2652.3','musician',replace('musician\noboe player\nmusic player\nmandolin player\nsaxophonist\norganist\nflutist\nflute player\nbig band musician\nviolinist\nup-right bass player\nfirst violinist\ntrumpet player\nbass player\nclarinet player\nconcert soloist\nsaxophone player\nconcert master\ncharango player\nclarinetist\ncellist\npercussionist\nfrench hornist\norchestra player\nbanjo player\ntrombonist\njazz musician\noboist\nguitarist\ndidgeridoo player\nharmonica player\nfrench horn player\nmarimba player\norchestra musician\nhorn player\ntheremin player\ncembalist\ndrummer\nwashboard player\nharpist\nviolist\npianist\nhammond player\nvibraphonist\nclassical musician\npiccolo player\norchestra section leader\norchestra solo player','\n',char(10)),'Musicians perform a vocal or musical part that can be recorded or played for an audience. They have know-how and practice of one or many instruments or using their voice. The musician can also write and transcribe music.','',replace('Includes music performers. \nIncludes music creators. \nIncludes music leisure instructors. \nExcludes people performing music composition not for recordings or audiences.\nExcludes sound designer and composer.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f6811475-94d4-4d13-8d5f-f173978ae7ec','key_18053','','','2432','2432.7','political party agent',replace('political organisation manager\nchief whip\nparty agent\npolitical party agent\npolitical organization manager\npolitical party whip\nwhip\npublic relations agent','\n',char(10)),'Political party agents manage the administrative tasks of a political party, such as budget management, record keeping, writing of agendas, etc. They also ensure productive communication with governmental bodies, and with press and media.','','Excludes <a resource="http://data.europa.eu/esco/occupation/0a3c2d5b-e1f9-4200-9288-62bf3fed3310" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/0a3c2d5b-e1f9-4200-9288-62bf3fed3310"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">membership administrators</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f6ca9392-708d-46cb-a2e9-51519595baa2','key_18054','','','2330','2330.1.6','drama teacher secondary school',replace('dramatic arts teacher secondary school\nteacher of drama in secondary schools\nsecondary school drama teacher\ndrama teacher\nsecondary school drama tutor\nhigh school performing arts teacher\nhigh school dramatic arts teacher\ndrama teacher high school\nhigh school drama teacher\nhigh school performing and dramatic arts teacher\ndrama tutor secondary school','\n',char(10)),'Drama teachers at secondary schools provide education to students, commonly children and young adults, in a secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, drama. They prepare lesson plans and materials, monitor the studentsâ progress, assist individually when necessary, and evaluate the studentsâ knowledge and performance on the subject of drama through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f6f1a9c5-a956-4a10-98ad-b83525e843fb','key_18055','','','8160','8160.8','bulk filler',replace('bulk operator\nbulk worker\nbulk filling worker\nbulk filler\nbulk filling operative','\n',char(10)),replace(' Bulk fillers perform dumping of food products into barrels, tubs, or containers together with prescribed amounts of preservatives, such as salt, sugar, brine, syrup, or vinegar for the manufacturing of food products. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f7066fc0-5f2f-43a9-bd35-a99e6119490d','key_18056','','','5164','5164.1.3','zookeeper',replace('zoo animal keeper\nzookeeper and tour guide\nzoo animal care professional\nzoo caretaker\nzoo presenter & animal keeper\nzookeeper & tour guide\nanimal care worker\naquarist\nzookeeper\nzoo presenter and animal keeper\nzoo keeper\nzoo attendant\nzoo animal caretaker','\n',char(10)),'Zookeepers manage animals that are kept in captivity for conservation, education, research and/or to be displayed to the public. They are usually responsible for the feeding and the daily care and welfare of the animals. As a part of their routine, zookeepers clean the exhibits and report possible health problems. They may also be involved in particular scientific research or public education, such as conducting guided tours and answering questions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f70e5875-df7e-40be-a315-3ac1c9d36d64','key_18057','','','1420','1420.4.37','second-hand shop manager',replace('charity store manager\narmy surplus store manager\nthrift shop manager\nvintage clothes store manager\nsecond-hand shop manager\nthrift store manager\nvintage clothes shop manager\narmy surplus shop manager\nsecond-hand store manager\nbric a brac store manager\njunk store manager\ncharity shop manager','\n',char(10)),'Second-hand shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f7145f87-5940-43f5-bc98-aa1a2b872b47','key_18058','','','7233','7233.8.1','agricultural machinery technician',replace('agricultural production technician\nagricultural technician\nagricultural conservation technician\nagricultural systems technician\nagricultural equipment test technician\nagricultural research technician\nfarm equipment technician\nagricultural machinery technician\nagricultural landscape technician\nagricultural automation technician\nagricultural machinery mechanic','\n',char(10)),'Agricultural machinery technicians repair, overhaul and maintain agricultural equipment including tractors, tillage equipment, seeding equipment and harvesting equipment. They perform evaluations of the equipment, perform preventive maintenance activities and repair faults.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f7177f79-63e7-4944-8896-044056b62523','key_18059','','','3423','3423.2.2','personal trainer',replace('well-being personal trainer\npersonal trainer and sport therapist\npersonal trainer & nutritionist\npersonal trainer and nutritionist\npersonal coach\npersonal trainer & sport therapist\npersonal progress manager\npersonal trainer\npersonal fitness trainer','\n',char(10)),'Personal trainers design, implement and evaluate exercise or physical activity programmes for one or more individual clients by collecting and analysing client information. They strive to ensure the effectiveness of personal exercise programmes. A personal trainer should also actively encourage potential clients to participate in and adhere to regular programmes, employing appropriate motivational strategies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f738f568-b4ff-4f23-acb7-5f99d9a3b393','key_18060','','','3331','3331.2.1.9','import export specialist in dairy products and edible oils',replace('import export specialist in dairy products and edible oils\ncustoms compliance specialist in dairy products and edible oils\ngraduate export specialist in dairy products and edible oils\nsenior import export specialist in dairy products and edible oils\nassistant export specialist in dairy products and edible oils\ntrainee export specialist in dairy products and edible oils\nsenior import specialist in dairy products and edible oils\ngraduate import export specialist in dairy products and edible oils\ntrainee import specialist in dairy products and edible oils\ntrainee import export specialist in dairy products and edible oils\ninternational trade compliance specialist in dairy products and edible oils\nassistant import export specialist in dairy products and edible oils\nsenior export specialist in dairy products and edible oils\ngraduate import specialist in dairy products and edible oils\nassistant import specialist in dairy products and edible oils','\n',char(10)),'Import export specialists in dairy products and edible oils have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f73e11e7-0eb8-4a46-b2e1-9e544c2f3652','key_18061','','','8154','8154.4','textile finishing machine operator',replace('drying machine operator\ntextile finishing supervisor\ntextile machine operator for segment\ntextile machine settler and operator\ntextile finishing machine operator\ntextile finishing machinist\ntextile dyeing and finishing machine operator\nlaminating and coating machine operator\ndyeing, finishing, coating, laminating textile machine operator\ntextile finishing range operator\ncleaning machine operator','\n',char(10)),'Textile finishing machine operators operate, supervise, monitor and maintain the production of textiles finishing machines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f7450c1f-20b7-45dd-b3bb-25095830bf3f','key_18062','','','2422','2422.12.7','foreign affairs officer',replace('foreign affairs analyst\ninternational relations analyst\ninternational affairs analyst\nforeign affairs specialist\nforeign affairs manager\ninternational relations manager\nconsulate officer\nforeign affairs officer\nforeign affairs advisor','\n',char(10)),'Foreign affairs officers analyse foreign affairs policies and operations, and write reports outlining their analyses in a clear and comprehensible manner. They communicate with parties who benefit from their findings, and act as advisers in the development or implementation of or reporting on foreign policy. Foreign affairs officers may also perform administrative duties in the department, such as assisting with problems concerning passports and visas. They promote friendly and open communication between different nations'' governments and institutions.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f756fdab-7726-4c48-bfcc-94ff8810fc08','key_18063','','','9412','9412.2','kitchen porter',replace('kitchen porter\npot and pan washer\nkitchen hand\nkitchen steward\ndish washer\nKP','\n',char(10)),'Kitchen porters wash and clean kitchen areas, including pots, pans, utensils, cutlery and dishes. They prepare the kitchen area before service, and receive and store supplies.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f7b04542-d8c7-42db-8475-e63b507cce82','key_18064','','','4226','4226.1','receptionist',replace('receiving clerk\nsecretary\nreceptionist\ncompany secretary\noffice receptionist\nmedical receptionist\nreceiving secretary\nfront desk receptionist\nhead receptionist\nfront desk agent\ncorporate receptionist','\n',char(10)),'Receptionists are responsible for the reception area of a business. They answer the phone, greet guests, pass information, respond to inquiries and instruct visitors. They are the first point of contact for clients and customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f7ddb68a-beba-4aa9-b5cb-d174d70ea724','key_18065','','','3115','3115.1.24','welding inspector',replace('electric arc welding engineer\nelectric spot welding engineer\nwelding checker\nwelding overseer\nwelding assessor\nengineering welding fabricator  inspector\nsheet-metal welding engineer\nwelder-fabricator  inspector\nresistance welding inspector\nultrasonic metal welder engineer\nwelding inspector\nwelding inspectors\nwelding assurance inspector\nproduction line welding  inspector\nrobotic welding engineer\nwelding examiner\ntack welding  inspector\nconstruction welder  inspector\nwelding  inspector\nwelding supervisor\nwelding control inspector\nflash welding engineer\nwelding engineer inspector\nstitch welding engineer\nbutt welding engineer\nsheet metal welding inspector\nengineering welding  inspector','\n',char(10)),'Welding inspectors examine the connections and bonds between metals. They utilise visual tools and electrical instruments to inspect and ensure the quality and safety of connections. Welding inspectors ensure that all related welding activities, plans and materials follow the appropriate guidelines, in accordance with safety regulations. In addition to working in the field completing their examinations of welding projects, inspectors spend time in an office setting compiling their reports.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f7f8c077-26ec-455f-8311-aeafbc24d9a5','key_18066','','','2635','2635.1.1','bereavement counsellor',replace('family and bereavement support officer\nbereavement support worker\nbereavement counselor\nadult bereavement counsellor\ncommunity bereavement support officer\nbereavement support  counsellor\nbereavement advice counsellor\nbereavement officer\nbereavement social counsellor\nfamily bereavement counsellor\nbereavement service coordinator','\n',char(10)),'Bereavement counsellors support and guide patients and their families to better cope with the death of the loved ones by assisting them in emergent situations, at the hospices and at the memorial services. They train other professionals and communities anticipating the supportive needs of bereavement and responding to the education requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f817aab2-ec8a-45ec-88ef-c11e0f19fd85','key_18067','','','2422','2422.12.4','education policy officer',replace('education policy development officer\neducation policy researcher\neducation policy specialist\neducation policy analyst\npolicy officer, education\npolicy officer\neducation policy officer\npolicy officer for education\neducation policy advisor','\n',char(10)),'Education policy officers research, analyse and develop education policies, and implement these policies to improve the existing education system. They try to improve all aspects of education which will impact institutions such as schools, universities and vocational schools. They work closely with partners, external organisations or other stakeholders and provide them with regular updates.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f8304b4c-1220-4979-b2dd-47d2a2f0734a','key_18068','','','5223','5223.7.32','specialised antique dealer',replace('antique furniture shop salesperson\nantique retail store\nantiquities specialised seller\nspecialised merchant, antique retail store\nantiques showroom salesperson\nantique shop specialised seller\nantiquities shop salesperson\nantiques and collectibles specialised seller\nantiques and collectibles shop specialised seller\nantiques store salesperson\nantiques gallery specialised seller\nspecialised seller, antique retail store\nantiques dealer\nantique shop salesperson\nantiquities shop specialised seller\nantique furniture shop specialised seller\nantiques showroom specialised seller\nspecialised dealer\nspecialised antique seller\nantique dealer\nantiques shop specialised seller\nantique furniture store salesperson\nantiques gallery salesperson\nspecialised merchant of antiques\nantiques specialised seller\nantiques specialised dealer\nspecialised seller of antiques\ncollectables shop salesperson\nspecialized antique dealer\nspecialised dealer in antiques\nantique furniture specialised seller\ncollectables specialised seller\nantiques and collectibles shop salesperson\nantiques and collectibles store salesperson\nantiquities store salesperson\nspecialised antique merchant\nantiques specialised merchant','\n',char(10)),'Specialised antique dealers sell antique goods in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f83f19ca-b816-484a-b8be-92b7785121ea','key_18069','','','3117','3117.4','mine safety officer',replace('health and safety manager\nmine safety inspector\nmine safety expert\nsafety officer\nhealth and safety officer\nmines inspector\nsafety manager\nmine inspector\nmining inspector\nmine safety manager\nmine safety officer','\n',char(10)),'Mine safety officers oversee health and safety systems at mining operations. They report workplace accidents, compile accident statistics, estimate risks to employee safety and health, and suggest solutions or new measurements and techniques.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f8438d21-c726-4534-8d7c-1c5aa9ef05ef','key_18070','','','9129','9129.1','drapery and carpet cleaner',replace('carpet and drapery cleaner\ncarpet cleaning technician\ncarpet cleaner\ndrapery and carpet cleaner\nrug cleaner\ndrapery cleaner','\n',char(10)),'Drapery and carpet cleaners clean draperies and carpets for their clients by removing stains, dust or odors. They do this by applying chemical and repellent solutions and with the use of brushes or mechanical equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f84aae41-28cc-4aa0-a0df-e7c3030516fd','key_18071','','','5169','5169.2','dating service consultant',replace('matchmaking consultant\nmatchmaker\nmarriage broker\ndating agency consultant\ndating agency manager\ndating service consultant','\n',char(10)),'Dating service consultants provide support to clients in searching and finding a partner and setting up the date. They provide personalised advice to help clients meet their dating objectives. They also work in the virtual environment where they assist the online users in managing personal profiles, sending messages, and making connections.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f84dbfe2-fa6b-43ef-97f5-54c3ad9c631f','key_18072','','','4321','4321.1.3','raw material reception operator',replace('raw materials reception operative\nraw materials reception worker\nraw materials worker\nraw material reception operator\nraw material processing operator','\n',char(10)),'Raw material reception operators operate and maintain tools and equipment to ensure that qualitative and quantitative requirements in the reception of the raw material are met. They use equipment to evaluate products such as grains, potatoes, cassava root, etc. They store and distribute products to the different processing factory units.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f874980a-e08f-4a58-bc21-2ff3cf472ec5','key_18073','','','5249','5249.2.1.3','rental service representative in cars and light motor vehicles',replace('rental specialist in cars and light motor vehicles\nrental advisor in cars and light motor vehicles\nrental service desk clerk in cars and light motor vehicles\nrental service desk agent in cars and light motor vehicles\nrental sales team leader in cars and light motor vehicles\nrental service team leader in cars and light motor vehicles\nrental service worker in cars and light motor vehicles\nrental sales worker in cars and light motor vehicles\nrental service desk supervisor in cars and light motor vehicles\nrental service supervisor in cars and light motor vehicles\nrental sales manager in cars and light motor vehicles\nrental sales desk agent in cars and light motor vehicles\nrental sales supervisor in cars and light motor vehicles\nrental service assistant in cars and light motor vehicles\nrental sales desk clerk in cars and light motor vehicles\nrental sales assistant in cars and light motor vehicles\nrental service manager in cars and light motor vehicles\nrental sales desk supervisor in cars and light motor vehicles\nrental service representative in cars and light motor vehicles','\n',char(10)),'Rental service representatives in cars and light motor vehicles are in charge of renting out vehicles and determining specific periods of usage. They document transactions, insurances and payments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f87779ae-7389-44e7-b155-16e87904857e','key_18074','','','3315','3315.2','gemmologist',replace('gemmology research analyst\ngemmology scholar\ngemstone buyer\nmining gemologist\ngemstone assessor\nconsultant gemologist\nmineral gemmologist\njewellery  gemologist\ngemmologist\ngemmology analyst\njewellery  gemmologist\nmineral gemologist\ngemmology research scientist\ngemstone valuer\ngemmology researcher\ngemstone appraiser\ngemmology science researcher\ngem stone buyer\ngem stone assessor\nmining gemmologist\ngem stone valuer\nstone buyer\ngem stone appraiser\ngemologist\nconsultant gemmologist\ngemmology scientist','\n',char(10)),'Gemmologists value precious stones by analysing their characteristics, cut, and provenience either for trading or for further polishing efforts. They assess stones and gems to give them a market value.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f88124d7-ee7e-4d21-9036-0658b496c119','key_18075','','','3255','3255.2','physiotherapy assistant',replace('neuromusculoskeletal physiotherapy assistant\nphysiotherapy rehabilitation assistant\ngeriatric therapy assistant\nmanipulative physiotherapy assistant\nrespiratory physiotherapy assistant\nneurological physiotherapy assistant\nremedial physiotherapy assistant\nneuromuscular physiotherapy assistant\ntherapy assistant\ncardiovascular physiotherapy assistant\noccupational physiotherapy assistant\nphysiotherapy care assistant\nphysiotherapist assistant\nassistant physiotherapist\nphysiotherapy assistant','\n',char(10)),'Physiotherapy assistants work under supervision, within defined contexts using agreed treatment protocols and procedures such as collecting client data and maintaining the equipment required in physiotherapy interventions. The overall responsibility is retained by the delegating professional.','','Excludes <a resource="http://data.europa.eu/esco/occupation/006cc1f9-2841-41c3-991a-dc3f2f3bd533" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/006cc1f9-2841-41c3-991a-dc3f2f3bd533"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">physiotherapist</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f889dcbd-d058-4177-927f-5fe59d044fa6','key_18076','','','8212','8212.3.3','electronic musical instrument maker',replace('electric guitar maker\nelectric keyboard maker\nelectronic musical instrument craftsperson\nelectric guitar builder\nelectronic musical instrument craftsman\nelectronic musical instrument maker\nelectric keyboard builder\nelectronic musical instrument craftswoman\nelectronic musical instrument builder','\n',char(10)),'Electronic musical instrument makers create and assemble parts to make electronic instruments according to specified instructions or diagrams. They install and test electric pick-ups, clean and inspect the finished instrument.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f88fb9dd-5a46-4086-a284-b1992174e55c','key_18077','','','1420','1420.4.19','eyewear and optical equipment shop manager',replace('optometrists shop manager\nspectacle store manager\nopticians shop manager\neyewear shop manager\noptical equipment shop manager\noptometrists store manager\noptical equipment store manager\neyewear store manager\nopticians store manager\nspectacle shop manager\ncontacts and glasses store manager\neyewear and optical equipment shop manager','\n',char(10)),'Eyewear and optical equipment shop managers assume responsibility for activities and staff in specialised shops.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f89b12a1-689f-4a4f-8695-abb620810f1e','key_18078','','','2352','2352.1.5','special educational needs teacher primary school',replace('SEN teacher primary school\nprimary school special education teacher\nspecial educational needs teacher in primary school\nspecial educational needs teacher elementary school\ninclusive education teacher primary school\nprimary school special educational needs teacher\nprimary school special needs teacher\nprimary school special needs educator\nprimary school SEN teacher\nteacher in special educational needs in primary school','\n',char(10)),'Special educational needs teachers at primary schools provide specially-designed instruction to students with a variety of disabilities on a primary school level and ensure they reach their learning potential. Some special educational needs teachers at primary schools work with children who have mild to moderate disabilities, implementing a modified curriculum to fit each student''s specific needs. Other special educational needs teachers at primary schools assist and instruct students with intellectual disabilities and autism, focusing on teaching them basic and advanced literacy, life and social skills. All teachers assess the students'' progress, taking into account their strengths and weaknesses, and communicate their findings to parents, counselors, administrators and other parties involved.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f8ab2e08-d14e-4938-a2cd-c04821556db7','key_18079','','','7421','7421.3','consumer electronics repair technician',replace('repairer\nconsumer electronics repair technician\nconsumer electronics technician\nelectronics repairer\nrepairman\ntrouble-shooter\nconsumer electronics repairman\nconsumer\ntechnician','\n',char(10)),'Consumer electronics repair technicians use electrical equipment to diagnose malfunctions and test functionality of consumer electronics such as TVs, video and audio systems and digital cameras. They read manufacturers'' instructions and conduct the necessary repairs or replacements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f8d14b95-6a36-4290-b226-2342e236406e','key_18080','','','2642','2642.1.12','magazine editor',replace('magazine supplements editor\nmagazine editor\nfeatures editor\nmagazine features editor','\n',char(10)),'Magazine editors decide which stories are interesting enough and will be covered in the magazine. They assign journalists to each item. Magazine editors determine the length of each article and where it will be featured in the magazine. They also ensure that publications are finished on time for publishing.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f8d1bcf9-9ca1-408f-a713-7bdf28c400fd','key_18081','','','7412','7412.7','lift technician',replace('lift installation worker\nlift maintenance engineer\nelevator installer\nlift constructor\nelevator service inspector\nelevator erecting mechanic\nelevator installation team member\nelevator builder\nlift construction worker\nelevator service technican\nlift erecting mechanic\nescalator installer\nlift installation team worker\nelevator service mechanic\nlift service mechanic\nlift repair engineer\nelevator maintenance mechanic\nlift builder\nlift installer\nelevator service engineer\nlift repair technician\nlift engineer\nlift erector\nelevator constructor\nlift installation team member\nlift service technician\nlift maintenance technician\nlift callout engineer\nlift maintenance mechanic\nlift inspector\nlift service engineer\nelevator engineer\nelevator erector','\n',char(10)),'Lift technicians set lifts into a prepared framed hoist-way. They install support assembly, set up the lift pump or motor, piston or cable, and mechanism. Lift technicians connect the necessary electronic elements to complete the installation and connection of the lift cabin. They also perform the necessary steps to inspect and repair lifts, as well as the shaft and any associated electronics. Lift technicians make sure every inspection and report action is noted in a log book, and report to the client on the state of the serviced lift.','','Includes people working in installation and maintenance of escalators, stair lifts and other electronic transport equipment.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f8d7d59d-242d-4903-9e0d-d2d23a0ef4c3','key_18082','','','1324','1324.3.1.6.7','coffee, tea, cocoa and spices distribution manager',replace('graduate coffee, tea, cocoa and spices distribution manager\ncoffee, tea, cocoa and spices distribution manager\nassistant coffee, tea, cocoa and spices distribution manager\ncoffee, tea, cocoa and spices logistics planner\ncoffee, tea, cocoa and spices supply chain planner\ncoffee, tea, cocoa and spices distribution planner\ncoffee, tea, cocoa and spices supply chain manager\ntrainee coffee, tea, cocoa and spices distribution manager\nsenior coffee, tea, cocoa and spices distribution manager\ncoffee, tea, cocoa and spices logistics manager','\n',char(10)),'Coffee, tea, cocoa and spices distribution managers plan the distribution of coffee, tea, cocoa and spices to various points of sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f8da6546-0c8a-4bd0-a952-6f3c253c9aba','key_18083','','','5223','5223.3','personal shopper',replace('global personal shopper\nonline personal shopper\ninternational personal shopper\npersonal shopper','\n',char(10)),'Personal shoppers assist their individual clients in selecting and buying clothing items and other goods such as gifts, according to their personal tastes, desires and style.','',replace('Includes self-employed <a resource="http://data.europa.eu/esco/occupation/f8da6546-0c8a-4bd0-a952-6f3c253c9aba" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/f8da6546-0c8a-4bd0-a952-6f3c253c9aba"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">personal shoppers</span></a>. \nIncludes people performing activities for a fashion or specialty store.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f8e435dd-7aa9-4d7a-97c3-b3b5789b6b25','key_18084','','','5414','5414.1.3','bodyguard',replace('personal safety specialist\npersonal security worker\nprotection officer\nclose protection officer\nbodyguard\npersonal safety worker\npersonal protection officer','\n',char(10)),'Bodyguards offer protection to their clients against injury, attacks and harassment. They accompany individuals such as politicians and actors to various events and gatherings. Bodyguards observe the surrounding environment, anticipate threats and respond to security emergencies. They may carry and use guns.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f8e76759-811e-4b39-9adf-3e77429cff8f','key_18085','','','7536','7536.2','shoemaker',replace('custom shoemaker\nhand craft shoe maker\nshoe repairer\nshoemaker\ncobbler\ntraditional shoe manufacturer','\n',char(10)),'Shoemakers use hand or machine operations for traditional manufacturing of a various range of footwear. They also repair all types of footwear in a repair shop.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f8f34041-3640-41a9-852e-c5a7574de780','key_18086','','','4413','4413.1','proofreader',replace('proof reading clerk\nproof reader\nproofreader','\n',char(10)),'Proofreaders examine facsimiles of the finished products such as books, newspaper and magazines. They correct grammatical, typographical and spelling errors in order to ensure the quality of the printed product.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f9116001-8cf5-453c-af51-3adaef7841ef','key_18087','','','8156','8156.2.5','pre-lasting operator',replace('insole beveler\ntoe puf moulder\ninsole and heel stiffener\narch-cushion-press operator\nbevel cutting machine tender\narch cushion press operator\nbevel cutting machine operator\npre-lasting operator\nstiffener moulder','\n',char(10)),'Pre-lasting operators handle tools and equipment for placing stiffeners, moulding toe puff and carry out other actions necessary for lasting the uppers of the footwear over the last. They make preparations for lasting-cemented construction by attaching the insole, inserting the stiffener, back moulding and conditioning the uppers before lasting.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f9395e4d-d66f-49ce-a8f5-721991139e79','key_18088','','','2269','2269.9','recreational therapist',replace('recreation therapist\nrecreational activity provider\nrecreational therapist\npsychomotor therapist\nsocial therapist\nmilieu therapist\nactivity co-ordinator\nactivities co-ordinator','\n',char(10)),'Recreational therapists offer treatment to persons who have behavioural disorders or conditions. They use techniques and interventions such as art, music, animals and dance to promote, maintain and restore the development and health of the patient.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f9433fdb-dde1-46cf-9e91-b49d243946a7','key_18089','','','2149','2149.5','bioengineer',replace('bioengineering expert\nbio-engineer\nbioengineering technology adviser\nbioengineering consultant\nbiological engineer\nbioengineering technology expert\nbioengineering biotechnologist\nbioengineer\nbioengineering technology consultant\nbioengineering adviser\nbioengineering technology specialist\nbioengineering specialist','\n',char(10)),'Bioengineers combine state of the art findings in the field of biology with engineering logics in order to develop solutions aimed at improving the well-being of society. They can develop improvement systems for natural resource conservation, agriculture, food production, genetic modification, and economic use.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f96e3735-405c-4ca9-8a86-d37f64ea7164','key_18090','','','7312','7312.7','wind musical instrument maker',replace('brass instrument assembler\nwoodwind instrument builder\nwind musical instrument maker\nwoodwind instrument assembler\nwind musical instrument builder\nbrass instrument craftsman\nbrass instrument builder\nwoodwind instrument craftswoman\nbrass instrument maker\nbrass instrument craftswoman\nwind instrument maker\nwoodwind instrument maker\nwoodwind instrument craftsman','\n',char(10)),'Wind musical instrument makers create and assemble parts to make wind instruments according to specified instructions and diagrams. They measure and cut the tubing for the resonator, assemble parts such as braces, slides, valves, piston, bell heads and mouth pieces, test and inspect the finished instrument.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f97dedb0-da68-4fc3-8bcc-3f4025ccacd3','key_18091','','','3331','3331.2.1.17','import export specialist in hides, skins and leather products',replace('graduate export specialist in hides, skins and leather products\nassistant import export specialist in hides, skins and leather products\nsenior import specialist in hides, skins and leather products\nsenior import export specialist in hides, skins and leather products\nassistant export specialist in hides, skins and leather products\ntrainee import specialist in hides, skins and leather products\ntrainee export specialist in hides, skins and leather products\ngraduate import export specialist in hides, skins and leather products\nassistant import specialist in hides, skins and leather products\ntrainee import export specialist in hides, skins and leather products\nsenior export specialist in hides, skins and leather products\nimport export specialist in hides, skins and leather products\ninternational trade compliance specialist in hides, skins and leather products\ngraduate import specialist in hides, skins and leather products\ncustoms compliance specialist in hides, skins and leather products','\n',char(10)),'Import export specialists in hides, skins and leather products have and apply deep knowledge of import and export goods including customs clearance and documentation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/f9cbdeb4-1559-4d45-b2d7-edf8aebe400b','key_18092','','','3142','3142.1.1','aquaculture quality supervisor',replace('supervisor of aquaculture quality\nfish farming supervisor\naquaculture quality supervisor\naquaculture quality manager','\n',char(10)),'Aquaculture quality supervisors establish standards and policies for the quality control of aquatic organisms’ production. They test and inspect the stock according to hazard analysis and critical control points (HACCP) principles and safety regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fa1aef45-49a4-4446-94b4-e2b563d9d025','key_18093','','','5111','5111.2.3','train attendant',replace('train attendant\ntrain steward\ntrain on-board staff member\ntrain customer service representative\ntrain on-board steward\ntrain on-board customer service representative\ntrain stewardess\ntrain on-board stewardess','\n',char(10)),'Train attendants work on trains to provide services to passengers such as welcoming passengers, answering their questions and serving meals.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fa30c1cb-dbc5-409c-8124-7284592b123c','key_18094','','','6113','6113.6','tree surgeon',replace('tree surgeon/climber\ntree pruner\ntree surgeon & climber\npruner\ntree surgeon\narboriculturist\ntree surgeon and climber\narborist','\n',char(10)),'Tree surgeons maintain trees. They use heavy machinery to prune and cut trees. Tree surgeons are often required to climb the trees to perform maintenance.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fa32eb3f-5c9f-4668-8f85-5eefb3badab8','key_18095','','','2132','2132.5','forestry adviser',replace('woodland manager\nforestry consultant\nforestry adviser\nwoodland consultant\nsilviculturist\nforestry advisor\nforestry advisers','\n',char(10)),'Forestry advisers provide services and advice on economic and environmental issues related to timber and forestry management in compliance with laws and regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fa3fde92-f773-4381-9ae6-8e23d7334cd4','key_18096','','','8211','8211.5.1','motor vehicle body assembler',replace('car body assembler\nlorry body assembler\ntruck body assembler\nvehicle production body assembler\nmotorcycle body assembler\ncoach body assembler\nmotor vehicle body assembler\nbus body assembler','\n',char(10)),'Motor vehicle body assemblers fasten motor vehicle body parts and components together such as frames, doors, chassis and hoods. They use hand tools, power tools and other equipment such as CNC machines or robots. They read technical plans and use automated assembling equipment to build motor vehicle bodies. They inspect individual parts for malfunctions and check the quality of assemblies to make sure the standards are met and the specifications respected. Motor vehicle body assemblers report any assembly issues to their supervisor.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fa404a79-4091-47f9-a635-c2e3c576ece8','key_18097','','','4323','4323.11','port coordinator',replace('terminal supervisor\nport terminal manager\nport operations supervisor\nport operations coordinator\ncoordinator of port operations\nport coordinator\nport terminal supervisor\nport operations manager','\n',char(10)),'Port coordinators manage operations of traffic division for port authorities. They enforce rules and regulations, such as the berthing of ships, handling and storing of cargo, and the use of port facilities. They direct policing and cleaning activities of the harbour department land, streets, buildings, and water areas. Port coordinators also ensure that activities concerning revenue are documented and submitted to the accounting division. They advise port authorities on rates and revisions of port tariff, and solicit steamship companies to use port facilities. They direct activities concerned with compiling daily and annual ship and cargo statistics.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fa4a3b52-990c-44eb-a912-490f5d689400','key_18098','','','2635','2635.2','social pedagogue',replace('social pedagogy practitioner\ncommunity educator\nsocial pedagogue\nsocial outreach worker\ncommunity education practitioner\nsocio-cultural animator\npractitioner in social pedagogy\nsocial work pedagogue\ncommunity social pedagogue\nsocial pedagogue cultural animation\nsocial pedagogy project worker\nqualified social pedagogue\ncultural animator\nsocial pedagogy team worker\nsocial educator','\n',char(10)),'Social pedagogues provide care, support, and education to children and young persons with different backgrounds or capabilities. They develop educational processes for young persons to be in charge of their own experiences, using a multi-disciplinary approach set to the learning experience. Social pedagogues contribute to the individuals'' learning, welfare, and societal inclusion, and put an emphasis on building self-reliance.','','Social pedagogues include in their practice the contributions of family members and other relevant professionals to the task of educating children and young people.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fa5b9075-3796-4e86-be0a-b5e6d725c25a','key_18099','','','2112','2112.2','metrologist',replace('machine assembly calibration technician\ncalibration technician\noptical 3D metrology engineer\nelectronics calibration technician\nmetrologist\nmetrology research engineer\nlab calibration technician\nlaboratory calibration technician\nmetrology technician\nmetrology technologist','\n',char(10)),'Metrologists study and practice the science of measurement. They develop quantity systems, units of measurement and measuring methods to be used in science. Metrologists establish new methods and tools to quantify and better understand information.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fa6bd9e9-9631-4abe-b2f8-fad7bf8a819d','key_18100','','','1341','1341.2','child day care centre manager',replace('before school daycare coordinator\nchild day care center manager\nchild day care centre administrator\nafter school day care coordinator\nchildcare centre manager\nchild daycare centre coordinator\nchild daycare centre manager','\n',char(10)),'Child day care centre managers provide social services to children and their families. They supervise and support child care workers and manage the childcare facilities. Child day care centre managers have the responsibility for strategic and operational leadership and management of staff teams and resources within and or across child care services.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fab474ea-a4a9-4c11-9548-1d50ad594a34','key_18101','','','2133','2133.8','natural resources consultant',replace('natural resources consultant\nresources consultant\nnatural resources expert\nnatural resource consultant\nnatural resources adviser\nnatural resources specialist','\n',char(10)),'Natural resources consultant provide advice on the protection and management of natural resources, namely fauna, flora, soil and water to companies and governments which exploit these resources. They strive to guide companies on an appropriate policy for exploiting natural resources in industrial contexts, raise awareness on health issues, and ensure the conservation of ecosystems for sustainable interventions in natural habitats.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fad4abdf-fe1b-4751-8253-aef40b49afe0','key_18102','','','3117','3117.1','desalination technician',replace('operator of desalination plant\ndesalination plant technician\ndesalination technicians\nwater treatment plant technician\ndesalination equipment operator\ndesalination technician\nwater treatment plants technician','\n',char(10)),'Desalination technicians operate, monitor and maintain desalination plant equipment. They ensure compliance with legal regulations and safety and health requirements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fadb09c5-db84-4240-9fd6-68b4c0abeed5','key_18103','','','3311','3311.2.1','foreign exchange broker',replace('broker in foreign exchange\nforeign exchange broker\nforex broker\nforex trader\nfx broker','\n',char(10)),'Foreign exchange brokers buy and sell foreign currencies on behalf of their clients in order to secure a profit on fluctuations in foreign exchange rates. They undertake technical analysis of economic information such as market liquidity and volatility, to predict the future rates of currencies on the foreign exchange market.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fae51298-03a8-4df4-829a-685dddc1580e','key_18104','','','5151','5151.2','housekeeping supervisor',replace('executive housekeeper\nhousekeeping manager\nhead of housekeeping\nhead housekeeping manager\nhousekeeping supervisor\nhousekeeping coordinator\nhead housekeeper\ndeputy head housekeeping manager\ndeputy head housekeeper','\n',char(10)),'Housekeeping supervisors are in charge of supervising and coordinating the daily running of cleaning and housekeeping activities hospitality establishments.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/faed05c0-c1d1-4e34-b575-0dea96459e56','key_18105','','','1323','1323.1','construction manager',replace('construction cost consultant\nconstruction manager\nconstruction project manager\ndevelopment project cost consultant\nconstruction project cost consultant\ndevelopment project cost manager','\n',char(10)),'Construction managers are responsible for the planning and coordination of the construction projects. They provide expertise in the design phase of construction projects by facilitating a better estimate of the costs and the functional implications. They participate on bid processes for construction projects and handle subcontractors to deliver the different stages of the construction process from beginning to completion. They strive to enhance the value of the projects both improving efficiency and creating value for customers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/faed411a-f920-4100-86a8-b877928b429c','key_18106','','','2511','2511.19','user experience analyst',replace('user experience officer\nusability analyst\nuser experience analyst\nuser experience analysts','\n',char(10)),'User experience analysts assess client interaction and experience and analyse users'' behaviours, attitudes, and emotions about the usage of a particular product, system or service. They make proposals for the improvement of the interface and usability of products, systems or services. In doing so, they take into consideration the practical, experiential, affective, meaningful and valuable aspects of human–computer interaction and product ownership, as well as the person''s perceptions of system aspects such as utility, ease of use and efficiency, and user experience dynamics.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fb022de4-920a-4eb0-80f9-fbb8ecfc36b1','key_18107','','','2621','2621.4','cultural archive manager',replace('archivist\ncultural records manager\ncultural archive administrator\narchive manager\ncultural archive services manager\narchivist and records manager\ncultural archive and records manager','\n',char(10)),'Cultural archive managers ensure the care and preservation of a cultural institution and the archives within. They ensure the management and development of the institution’s assets and collections, including the digitisation of the archive collections. ','','Excludes collection manager <a resource="http://data.europa.eu/esco/occupation/96be6ac3-5932-4bfc-933d-84b267d0d486" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/96be6ac3-5932-4bfc-933d-84b267d0d486"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">collection manager</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fb0c7da2-6d0e-4a3f-be3e-344f58f0e82d','key_18108','','','2330','2330.1.12','modern languages teacher secondary school',replace('modern languages tutor secondary school\nteacher of modern languages in high school\nmodern languages teacher\nteacher of modern languages in secondary schools\nsecondary school teacher of modern languages\nmodern languages teacher in secondary school\nmodern languages high school teacher\nsecondary school modern languages tutor\nmodern languages teacher high school\nhigh school modern languages teacher','\n',char(10)),'Modern languages teachers at secondary schools provide education toÂ students, commonly children and young adults, in aÂ secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, modern languages. They prepare lesson plans and materials, monitor the studentsâ progress, assist individually when necessary, and evaluate the studentsâ knowledge and performance on the subject of modern languagesÂ through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fb18dfef-2b90-4219-8b39-a5fcddb8f238','key_18109','','','9312','9312.1.1','drainage worker',replace('drainage system installer\ngroundwater drainage worker\ngroundwater drainage operative\ndeep drainage worker\npipelayer\ndrainage pipe layer\ndrainage groundworker\ndrainage installer\ndrainage worker\ndeep drainage operative\ndrainage operative','\n',char(10)),'Drainage workers assemble and maintain drainage and dewatering systems. They lay tubes or drainpipes to dry out the ground of a certain structure in order to put a hold to imminent groundwater. This work is usually performed under pavements and in basements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fb1a7aab-1e33-4712-b8e0-d335243aba58','key_18110','','','4212','4212.5','gaming dealer',replace('game operator\ngaming operator\ntable gaming dealer\ncasino dealer\ncasinio gaming dealer\ngaming operations dealer\ngame dealer\ndealer\ncroupier\ngaming dealer','\n',char(10)),'Gaming dealers operate table games. They stand behind the game table and operate games of chance by dispensing the appropriate number of cards to players, or operating other gaming equipment. They also distribute winnings, or collect players'' money or chips.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fb3f5f35-e6f7-4168-aef2-e4dfb7f2a3ee','key_18111','','','3111','3111.4','geology technician',replace('geological engineering technician\ngeological technician\ngeoscience technician\nmaterial sampler\ngeophysical data technician\nmineral industry sampler\nmineral technician\nmine production techician\ngeology technician\ngeophysical technician\ngeo-mechanics technician\ngeological and petroleum technician\ngeo-technician\nmining technician\ngeological survey technician\nminerals survey technician\nmine surveying technician\nmineralogy technician','\n',char(10)),replace('Geology technicians assist in all the activities carried out by geologists. Under the supervision of geologists, they collect materials, carry out research and study the samples collected from the Earth. Geology technicians assist in determining the value of the land for oil or gas exploration. They perform various technical activities, including collecting samples during geochemical surveys, working on drill sites, and participating in geophysical surveys and geological studies.\n\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fb4819db-03fa-4dc1-a471-2cdc24dcbe6f','key_18112','','','7132','7132.2','surface treatment operator',replace('marine vessel painter\nanti- corrosion surface treatment worker\nanti-corrosion paint technician\nsurface treatment operative\nsurface treatment worker\nship painter\nvessel painter\nanti- corrosion surface treatment operative\nsurface treatment technician\nsurface coatings technician\nanti- corrosion surface treatment operator\nsurface treatment operator','\n',char(10)),'Surface treatment operators apply chemicals and paint to the material surface in order to protect against corrosion. They calculate the materials needed for surface protection.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fb6df00f-4810-442b-8f93-89b9580c7c9f','key_18113','','','7522','7522.5','furniture finisher',replace('furniture finishing worker\nfurniture finishing operative\nwooden furniture finisher\nfurniture stainer\nfurniture machine finisher\nfurniture painter\nfurniture varnisher\nfurniture polisher\nwooden furniture production worker\nfurniture finishing technician\nwood furniture varnisher\nfurniture sander\nwooden furniture varnisher\nfurniture hand finisher\nwood furniture painter\nwooden furniture painter\nwooden furniture stainer\nFrench polisher\nwood furniture stainer\nfurniture finisher\nwooden furniture production operative\nwooden furniture sander\nfurniture sprayer\nwood furniture finisher\nwooden furniture polisher\nwood furniture sprayer','\n',char(10)),'Furniture finishers treat the surface of wood furniture using hand and power tools to sand, clean and polish. They apply wood coatings onto wooden surfaces by using different techniques such as brushing or using a spray gun. They choose and apply the right coatings with protective and/or decorative purposes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fb7e2f4f-1545-42f1-972e-94082e49c6dc','key_18114','','','9313','9313.1','building construction worker',replace('construction site worker\ncivil construction worker\nbuilding site labourer\nconstruction site labourer\ninstallation and commissioning construction worker\nbuilding construction labourer\nconstruction labourer\nbuilding construction worker\nmechanical construction worker\nelectrical construction worker\nbuilding site worker\nconstruction worker','\n',char(10)),'Building construction workers prepare and maintain building construction activities on construction sites. They perform preparation and clean-up work in order to assist specialised construction workers.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fbc9ab3e-18f1-4b7b-8cd5-d6374ed5e9a9','key_18115','','','2310','2310.1.9','classical languages lecturer',replace('classical studies lecturer\nhigher education classical languages teacher\nGreek and Latin docent\nlecturer in greek and latin language\nclassics lector\nGreek and Latin professor\nclassical languages instructor\nuniversity classical languages lecturer\nlecturer in classical languages\nGreek and Latin teacher\nuniversity lecturer in classical languages\nlecturer in greek language\nclassical studies lector\nlecturer in latin language\nclassics lecturer\nclassical languages lector\nclassical languages docent\nclassical languages professor\nlecturer of classical languages\nGreek and Latin lecturer\nuniversity classical languages teacher\nclassical languages lecturer\nprofessor of classical languages\nGreek and Latin instructor\ninstructor in classical languages','\n',char(10)),'Classical languages lecturers are subject professors, teachers, or lecturers who instruct students who have obtained an upper secondary education diploma in their own specialised field of study, classical languages, which is predominantly academic in nature. They work with their university research assistants and university teaching assistants in the preparation of lectures and of exams, for grading papers and exams and for leading review and feedback sessions for the students. They also conduct academic research in their respective field of classical languages, publish their findings and liaise with other university colleagues.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fbceeac6-798b-4307-a825-626707a753ad','key_18116','','','2149','2149.2.1','calculation engineer',replace('stress calculation engineer\ncalculation engineer\ntechnical engineer\nsimulation calculation engineer\nstructural engineer\nstructural calculation engineer\npiping calculation engineer\nsupports and structural calculation engineer\nprocess modelling engineer\npipe stress calculation engineer\nprocess engineer\ncomputational engineer','\n',char(10)),'Calculation engineers draw conclusions about real systems, such as on strength, stability and durability, by performing experiments on virtual models. They test production processes as well.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fbf23c18-d9a3-4349-83dc-2bc39948bdf7','key_18117','','','9216','9216.2','aquaculture harvesting worker',replace('harvesting worker\naquaculture harvesting worker\nfish farming harvesting worker\naquatic animals harvesting worker','\n',char(10)),'Aquaculture harvesting workers work in the harvesting of those aquatic organisms cultured in land-based on-growing processes.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fbf3aefb-70ff-440b-8724-bac3ed5e2c93','key_18118','','','3143','3143.1','forestry technician',replace('forestry operations foreman\nforestry operations technician\nforestry technician\nforestry foreman\nforesty supervisor\nforestry technologist\nforestry operations technologist\nforestry operations supervisor\nforestry operations forewoman\nforestry forewoman','\n',char(10)),'Forestry technicians assist and support the forest manager and implement their decisions. They supervise a team of forestry equipment operators and support and supervise forestry and environmental protection through research and data collection. They also manage resource conservation and harvesting plans.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fc1da244-880f-407d-bfa7-789a2f468ea0','key_18119','','','5164','5164.2.1','animal trainer',replace('dog training specialist\nanimal behaviour trainer\nanimal behaviour training specialist\npet trainer\ncanine training specialist\ncat behaviourist\nzoo animal trainer\ncanine trainer\nanimal trainers\nanimal trainer','\n',char(10)),'Animal trainers train animals and/or animal handlers for general and specific purposes, including assistance, security, leisure, competition, transportation, obedience and routine handling, entertainment and education, in accordance with national legislation.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fc35b7ca-ecec-40d7-91b5-5065f662d955','key_18120','','','1324','1324.1','airport director',replace('airport duty manager\nbaggage programme director\nairport manager\nairport director\nterminal operations manager\nairport security compliance manager\nstation manager\nairport section director\nairport environmental operations manager\nfinance director\nairside and expansions operations director\ndirector of tax\ncustomer relations and service director\naerodrome section director\naerodrome section manager\nairport security manager','\n',char(10)),replace('Airport directors oversee a group of managers who lead or supervise a particular area of the airport, programme or a project.\n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fc81b573-e0ea-4466-8560-c2bb278c314a','key_18121','','','3311','3311.3.3','futures trader',replace('futures contracts manager\nspreader\nfutures trader\narbitrageur','\n',char(10)),'Futures traders undertake daily trading activities in the futures trading market by buying and selling futures contracts. They speculate on the futures contracts'' direction, trying to make a profit by buying futures contracts they foresee to rise in price and sell contracts they foresee to fall in price. ','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fc85156e-4f0b-46c4-ba66-a3b17318d932','key_18122','','','3412','3412.3','life coach',replace('life coaches\npersonal development counsellor\nlife mentor\nlife coach','\n',char(10)),'Life coaches help clients set clear objectives for their personal development and help them achieve their goals and personal vision. They provide counselling and guidance and establish progress reports in order to keep track of their clients'' achievements.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fc9a86ec-7ad5-4ccb-a08f-1e369197e14a','key_18123','','','8143','8143.5','paperboard products assembler',replace('cardboard products assembly operator\ncardboard products assembly worker\npaperboard products assembly operative\ncardboard box constructor\ncorrugated box cutter\ncardboard products assembly operative\npaperboard products assembly operator\npaper plate maker\npaperboard products maker\npaperboard products constructor\ncraft board maker\npaperboard products assembler\ncardboard box assembler\ncorrugated box assembler\ncardboard box maker\ncorrugated box maker\npaperboard products assembly worker\ncorrugated box constructor','\n',char(10)),'Paperboard products assemblers construct the components or parts made from paperboard according to strictly laid down procedures. They assemble products such as tubes, spools, cardboard boxes, paper plates and craft boards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fcaab37b-ada2-4832-a2fd-9d64362e0280','key_18124','','','3343','3343.1.4','international student exchange coordinator',replace('student exchange officer\ncoordinator of international student exchange\nstudent exchange coordinator\ninternational exchange officer\ninternational exchange coordinator\nstudent exchange director\ninternational student exchange coordinator\ninternational exchange director','\n',char(10)),'International student exchange coordinators lead the entire process of an international exchange study for the exchange students. They manage the students'' applications and admissions, arrange accommodation, be it on a college or university campus or with guest families, and handle all the administration regarding these activities, including the student''s grades and official departing. They function as guides for the (young) international students through their cultural adjustment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fcab4bb1-c05b-43e3-98d8-f60b13c152f2','key_18125','','','8141','8141.1.2','coagulation operator',replace('latex coagulation worker\nrubber production process machine worker\nrubber production process machine operative\ncoagulation operative\nlatex coagulation operator\nlatex coagulation operative\nrubber coagulation operative\nrubber production process machine operator\ncoagulation operator\nrubber coagulation operator\nrubber coagulation worker\ncoagulation worker\nrubber production process operator','\n',char(10)),'Coagulation operators control machines to coagulate synthetic rubber latex into rubber crumb slurry. They prepare these rubber crumbs for finishing processes. Coagulation operators examine the appearance of the crumbs and adjust the operation of filters, shaker screens and hammer mills to remove moisture from the rubber crumbs.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fcbb66f5-843b-4152-9ce4-e7b19c2a88d9','key_18126','','','2144','2144.1.12','mine mechanical engineer',replace('mine mechanical engineer\nmechanical engineer (mining)\nmine operations manager','\n',char(10)),'Mine mechanical engineers supervise the procurement, installation, removal and maintenance of mining mechanical equipment, using their knowledge of mechanical specifications. They organise the replacement and repair of mechanical equipment and components.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fccecd5d-ad90-41a8-bb89-96d7595abca3','key_18127','','','7221','7221.1','blacksmith',replace('heritage blacksmith\nfoundry moulding operator\nartisan blacksmith\nornamental blacksmith\nhorseshoe maker\nforge worker\npatternmaking technician\nblacksmith\ncraft blacksmith\npatternmaking operator\nsheet metal operative\nsheet metal apprentice\nfoundry patternmaker technician\nsheet metal technician\nsmith\nforgeworker','\n',char(10)),'Blacksmiths heat metal, usually steel, in a forge and shape it with a hammer, chisel, and an anvil. Contemporarily, they predominantly create artisanal metal products, including ornamental work, as well as horse shoes, one of the sole metal fabricating processes that has not been industrialised.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fcf9024e-277c-43c7-8ec8-8903e5c9c576','key_18128','','','2142','2142.1.11','water engineer',replace('water engineer\nwater systems engineer\nwater resource engineer\nhydraulic engineer\nwater management specialist','\n',char(10)),'Water engineers research and develop methods for the provision of clean water, water treatment and flood damage prevention and reaction. They research water needs in a location and develop methods for meeting those needs, such as designing and developing projects for managing water resources such as treatment plants, pipelines, pump systems, irrigation or draining systems and other water supply systems. Water engineers also ensure proper installation of these systems on construction sites. Water engineers also maintain, repair and build structures that control water resources, such as bridges, canals and dams.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fd00e4bb-dcee-41d7-81d3-2cdebad8fe70','key_18129','','','1324','1324.3.2.4','import export manager in chemical products',replace('senior export manager in chemical products\nassistant import manager in chemical products\ninternational trade compliance manager in chemical products\ngraduate import export manager in chemical products\nsenior import export manager in chemical products\ninternational trade manager in chemical products\ntrainee import manager in chemical products\nimport export manager in chemical products\ncustoms compliance manager in chemical products\ngraduate import manager in chemical products\ngraduate export manager in chemical products\nassistant export manager in chemical products\ntrainee import export manager in chemical products\ntrainee export manager in chemical products\nsenior import manager in chemical products\nassistant import export manager in chemical products','\n',char(10)),'Import export managers in chemical products install and maintain procedures for cross-border business, coordinating internal and external parties.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fd0801c9-809a-4cfd-98ed-6c5860c163f2','key_18130','','','4312','4312.5','investment clerk',replace('investment administrator\ninvestment clerk\ninvestment secretary\ninvestment assistant','\n',char(10)),'Investment clerks assist with the administration of investments such as stocks, bonds or other securities and perform general clerical duties in the investment sector of a financial company.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fd1c0f5a-f465-4067-af54-621e68c90f74','key_18131','','','7531','7531.4','milliner',replace('hat maker\ncustom headwear manufacturer\nhatter\nmilliner\nheadwear manufacturer\nheadgear manufacturer','\n',char(10)),'Milliners design and manufacture hats and other headwear.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fd1c4e94-25aa-4020-b0ef-0cf5aaf2e4d2','key_18132','','','3323','3323.2.4','textile sourcing merchandiser',replace('textile sourcing merchandiser\nfabric purchasing manager\ntextile purchasing manager\ntextile sourcing specialist\ntextile sourcer merchandiser\ntextile merchandiser\ntextile merchandise manager','\n',char(10)),'Textile sourcing merchandisers organise efforts for textile producers from fibre to final products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fd1c83b3-60d5-4a5f-86c5-94dc2842c246','key_18133','','','2152','2152.1.7.1','integrated circuit design engineer',replace('integrated circuit designer\ndesigner of integrated circuits\nanalog and mixed signal design engineer\nengineer of integrated circuits\nmixed signal IC design engineer\nanalog design engineer\nIC designer\nintegrated circuit engineer\nanalog IC design engineer\nintegrated circuit design engineer','\n',char(10)),'Integrated circuit design engineers design the layout for integrated circuits according to electronics engineering principles. They use software to create design schematics and diagrams.','','Excludes people performing the assembly of integrated circuits.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fd2dd3e9-498f-490f-8589-8c1eea05aa3f','key_18134','','','7223','7223.4.7','punch press operator',replace('CNC laser punch press operator\ninjection moulding machine operative\nCNC punchpress operative\npress brake operative\nCNC punch press operator\npress punch setter\nturret punch press operative\nbrake press setter\nplastic punch press operator\npunch press worker\npunch machine operator\nCNC turret machine operator\npunchpress worker\nCNC punch press brake operator\npunch machine setter\npunchpress operator\nCNC punch operator\nCNC press brake operator\npress setter\npress brake setter/operator\nCNC laser punchpress operative\npress punch operative\npunchpress operative\npress punch inspector\ncentre punch operator\npunch press brake operator\nplastic press operator\npress operative\npress inspector\npunch press operative\npress brake setter\npunch press operator\npress brake operator\nTurret punch press operator','\n',char(10)),'Punch press operators set up and tend punch presses designed to cut workpieces into their desired shape by propelling a hydraulic ram to punch holes into them with a single die set that pushes through the top dead centre, through the surface, and to the bottom dead centre of the workpiece.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fd4b90ed-6bb4-447e-b13a-afb8015700b3','key_18135','','','2433','2433.5','solar energy sales consultant',replace('solar power sales advisor\nsolar energy sales consultant\ndomestic solar energy sales consultant\nsolar energy sales advisor\nsolar power sales representative\nsolar energy sales assistant\nsolar sales assistant\nindustrial solar energy sales consultant\nsolar sales consultant\nsolar sales advisor\nsolar power sales assistant\ndomestic solar power sales consultant\nindustrial solar power sales consultant\nsolar sales representative\nsolar energy sales representative','\n',char(10)),'Solar energy sales consultants provide advice on solar energy for domestic or industrial purposes, and aim to promote the use of solar energy as an alternative and more sustainable source of energy. They communicate with prospective clients, and attend networking events, to ensure increased sales of solar energy products.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fd6a1c18-b0ff-419f-a36a-e05957452d55','key_18136','','','3132','3132.3','solid waste operator',replace('solid waste processing technician\nsolid waste operator\nwaste treatment equipment operator\nwaste disposal compliance technician\nsolid waste treatment technician\nwaste sorter\nsolid waste remediation technician\nsolid waste disposal technician\nsolid waste collection technician\nwaste treatment equipment worker\nlandfill site worker\nwaste treatment equipment operative\nwaste disposal site compliance technician\nlandfill technician\nlandfill site operative','\n',char(10)),'Solid waste operators operate and maintain solid waste treatment and distribution equipment, and test samples to monitor pollution. They assist in the collection and disposal of solid waste, such as construction and demolition debris, and ensure treatment is compliant to safety regulations. They ensure community waste containers are emptied, ensure proper differentiation between waste which needs to be recycled or disposed of, and monitor equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fd86ebe1-bad4-4486-9b76-01a48030e49a','key_18137','','','7412','7412.11','rolling stock electrician',replace('electrical train electrician\nmultiple unit builder\ncarriage builder\ndepot electrician\nrail depot electrician\nrail electrician\nrailcar electrician\nrail vehicle electrician\nlocomotive electrician\nrailway vehicle builder\nrail workshop engineering technician\nrolling stock builder\nrailcar builder\nrailway depot electrician\ncarriage electrician\nrail vehicle builder\nmultiple unit electrician\nrolling stock electrician\nwagon builder\nrailway vehicle electrician\nlocomotive builder\nrailway workshop engineering technician\nwagon electrician\nworkshop engineering technician\nrailway electrician','\n',char(10)),'Rolling stock electricians install, maintain and repair electrical and electronic systems in rail vehicles such as air conditioning systems, lamps, heating systems, electrical wiring etc. They use diagnostic testing equipment to inspect vehicles and find faults. To do repair work, they use hand tools and specialised electrical instruments and machines.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fd882949-aad7-42ff-825e-00d49a5ec469','key_18138','','','3324','3324.4.14','wholesale merchant in fruit and vegetables',replace('business to business seller in fruit and vegetables\nwholesale merchandiser in fruit and vegetables\ngraduate wholesale merchant in fruit and vegetables\nwholesaler in fruit and vegetables\nwholesale merchant in fruit and vegetables\nsenior wholesale merchant in fruit and vegetables\nwholesale buyer in fruit and vegetables\nbusiness to business sales consultant in fruit and vegetables\nwholesale trader in fruit and vegetables\nB2B sales consultant in fruit and vegetables\nassistant wholesale merchant in fruit and vegetables\ntrainee wholesale merchant in fruit and vegetables','\n',char(10)),'Wholesale merchants in fruit and vegetables investigate potential wholesale buyers and suppliers and match their needs. They conclude trades involving large quantities of goods.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fd8b68c0-7341-44bb-855d-9b1254eade33','key_18139','','','5161','5161.4','psychic',replace('palmist\nclairvoyant\npsychic\npalm-reader\nmental telepathist\nchanneller\nmind-reader\nsensitive\npsychic reader\ntelepath','\n',char(10)),replace('Psychics claim to have extra-sensory talents to gather information and insight into people''s lives, events or situations. They offer advice to clients about topics of importance to their clients such as health, money and love. Psychics often work with traditional practices such as tarot card reading, palm reading or using astrological charts. \n ','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fdb16700-109e-4d02-949e-3213f278df93','key_18140','','','1321','1321.2.2','industrial quality manager',replace('engineering and quality control manager\nindustrial quality assessment manager\nindustrial quality supervisor\nindustrial quality control technologist\nindustrial quality control manager\nquality control industrial engineer\nindustrial quality control testing supervisor\nindustrial quality manager','\n',char(10)),'Industrial quality managers monitor and control information assets by detailing processes and procedures to ensure compliance with industrial standards. They perform audits in industrial processes, advise on preventive and corrective actions, and ensure compliance with industrial standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fdc8adfb-1807-4146-98ae-3a26fbe19996','key_18141','','','0310','0310.2','bomb disposal technician',replace('bomb disposal technician\nbomb disposal expert\nbomb disposal officer\narmed forces soldier\nsapper\nmine disposal technician\nminesweeper\nordnance technician','\n',char(10)),'Bomb disposal technicians search areas using special equipment such as metal detectors, or using trained animals, for the presence of land mines and bombs, and remove the explosive materials according to safety regulations. After the search for and location of mines and bombs, they disarm them aiming to avoid any risk of detonation, and ensure the area is cleared before disarming and removal procedures.','',replace('Includes marine minesweepers. \nIncludes people working underwater.','\n',char(10)),'http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fdd8eef7-d89c-419f-9166-0747f466a769','key_18142','','','2642','2642.1.7','critic',replace('theater critic\ncinema critic\nreviewer\nfilm critic\nblogger\ntelevision critic\ncritic\nmusic critic\ntheatre critic\nliterary critic\nart critic\narchitecture critic\ngame critic\narchitecture & design critic\nfood critic\nTV critic','\n',char(10)),'Critics write reviews of literary, musical and artistic works, restaurants, movies, television programs and other themes for newspapers, journals, magazines, radio, television and other media. They evaluate theme, expression and technique. Critics make judgements based on their personal experience and knowledge.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fddb7bd8-f176-4dc8-9d12-32b6072452c9','key_18143','','','7411','7411.1.2','electric meter technician',replace('electricity meter technician\nsmart meter technician\nelectricity meter installer\nelectricity meter installation technician\nelectric meter maintenance operative\nelectric meter test technician\nelectricity meter maintenance worker\nelectric meter technician\nsmart meter installer\nelectricity meter test technician\nelectric meter installer\nelectric meter maintenance worker\nelectric meter installation technician\nelectricity meter maintenance operative','\n',char(10)),'Electric meter technicians install and maintain electric meter systems in facilities or buildings. They install the equipment in accordance with regulations and repair faults and other problems. They test the equipment and advise on the use and care.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fde4372d-8293-42de-afbb-8b5a9b274fe7','key_18144','','','2149','2149.2','application engineer',replace('application engineering consultant\napplication engineering specialist\napplied research engineer\napplication technology engineering consultant\napplication technology engineer\napplied engineer\napplication engineering adviser\napplication technology engineering expert\napplication engineering expert\napplication technology engineering adviser\napplication technology engineering specialist\napplication engineer','\n',char(10)),'Application engineers deal with the technical requirements, management, and design for the development of various engineering applications, such as systems, new product designs, or the improvements of processes. They are responsible for the implementation of a design or process improvement, they offer technical support for products, answer questions about the technical functionality and assist the sales team.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fde97584-d8e3-413e-a662-511c9055357e','key_18145','','','4222','4222.1.1','live chat operator',replace('live chat operator\nreal time chat operator\nonline chat operator\nvirtual chat agent\nlive chat agent\nonline chat agent\nvirtual chat operator','\n',char(10)),'Live chat operators respond to answers and requests posed by customers of all nature through online platforms in websites and online assistance services in real time. They are available to provide service through chat platforms and have the ability to solve inquiries of clients via written communication merely.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fdf7b000-d923-4086-9660-eb8ee5e77ef0','key_18146','','','2422','2422.12.13','recreation policy officer',replace('sports and recreation policy advisor\nsports and recreation policy officer\nsports policy officer\nsports and recreation policy specialist\nrecreation policy officer\nsports and recreation policy analyst\nsports policy advisor\nrecreation policy specialist\nsports and recreation policy researcher\nrecreation policy advisor\nsport and recreation policy officer','\n',char(10)),'Recreation policy officers research, analyse and develop policies in the sports and recreation sector and implement these policies in order to improve the sport and recreation system and improve the health of the population. They strive to increase the participation in sports, support athletes, enhance the performance of athletes in national and international competitions, improve social inclusion and community development. They work closely with partners, external organisations or other stakeholders and provide them with regular updates.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fdfce14e-992d-4ff4-9f9d-7a353c75654e','key_18147','','','2611','2611.1.1','corporate lawyer',replace('corporation lawyer\nbusiness lawyer\ncorporate solicitor\ncorporate advocate\ntrademark lawyer\ncorporate counsel\ncorporate attorney\ncorporate lawyer\ncompany solicitor\npatent lawyer\nbusiness solicitor\nintellectual property lawyer','\n',char(10)),'Corporate lawyers provide legal consulting services and representation to corporations and organisations. They give advice on matters relating to taxes, legal rights and patents, international trade, trademarks, and legal financial issues arising from operating a business.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fe0fa514-b48d-4a53-9757-a283c0baacf0','key_18148','','','2511','2511.9','ICT business analyst',replace('ICT business analyst\nICT business analysts\nbusiness process specialist\nIT business analyst','\n',char(10)),'ICT business analysts are in charge of analysing and designing an organisation''s processes and systems, assessing the business model and its integration with technology. They also identify change needs, assess the impact of the change, capture and document requirements and then ensure that these requirements are delivered whilst supporting the business through the implementation process.','','Excludes people performing development activities.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/fe4a72af-6206-4d86-90c1-b5fb4b5ba2b2','key_18149','','','3521','3521.2','broadcast technician',replace('broadcast transmitter operator\nbroadcast engineering technician\nbroadcast technician\nbroadcasting technician\nradio and TV technician\nbroadcasting engineer\nbroadcast technicians\nbroadcast transmitting equipment operator\nbroadcasting equipment operator\nbroadcast transmitting technician','\n',char(10)),'Broadcast technicians install, start up, maintain, monitor and repair equipment used for the transmission and reception of television and radio broadcast signals. They ensure that all materials are available in a suitable format of transmittable quality according to the transmission deadline. Broadcast technicians also maintain and repair this equipment.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/febd4100-9a9b-46a0-b964-8623d79d0f32','key_18150','','','2133','2133.14','air pollution analyst',replace('air quality consultant\nair environment technician\nair quality technician\nenvironmental analyst\nair quality field technican\nair quality monitoring technician\nair pollution analyst\nair quality technologist','\n',char(10)),'Air pollution analysts conduct field and lab tests to examine the pollution of air in different areas. They also identify sources of pollution.','','Includes people working with specialists such as engineers and <a resource="http://data.europa.eu/esco/occupation/11d72bd6-779a-4643-9962-313833740094" typeOf="http://data.europa.eu/esco/model#Occupation" href="http://data.europa.eu/esco/occupation/11d72bd6-779a-4643-9962-313833740094"><span property="http://www.w3.org/2000/01/rdf-schema#label" xml:lang="en">environmental scientists</span></a>.','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ff142063-f89d-446d-92ac-9a3a124210a0','key_18151','','','2330','2330.1.4','chemistry teacher secondary school',replace('chemistry studies high school teacher\nhigh school chemistry teacher\nsecondary school chemistry tutor\nteacher of chemistry in secondary schools\nchemistry tutor secondary school\nsecondary school teacher of chemistry\nchemistry studies teacher in secondary school\nchemistry teacher high school','\n',char(10)),'Chemistry teachers at secondary schools provide education to students, commonly children and young adults, in a secondary school setting. They are usually subject teachers, specialised and instructing in their own field of study, chemistry. They prepare lesson plans and materials, monitor the studentsâ progress, assist individually when necessary, and evaluate the studentsâ knowledge and performance on the subject of chemistry through assignments, tests and examinations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ff2c1183-3fd7-40c7-8ddc-b20b100363c3','key_18152','','','2635','2635.3.5','community development social worker',replace('social worker-community development\ncommunity advocate\ncommunity development team leader\ncommunity animateur\ncommunity development support worker\nsocial development worker\ncommunity development social worker','\n',char(10)),'Community development social workers support individuals, families, and groups in socially or financially deprived areas. They provide leadership and bring local people together to make changes and tackle social inequality, helping people to develop the skills needed to eventually run their own community groups.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ff3a164d-4045-4511-a472-49093dabf9fc','key_18153','','','3322','3322.1.2','renewable energy sales representative',replace('renewable energy sales consultant\nresidential alternative energy sales representative\nrenewable energy sales promoter\nresidential renewable energy sales representative\nalternative energy sales consultant\nalternative energy salesman\nalternative energy sales negotiator\nrenewable energy sales negotiator\nrenewable energy seller\nrenewable energy sales representative\nrenewable energy salesperson\nalternative energy saleswoman\nalternative energy sales rep\nrenewable energy saleswoman\nrenewable energy sales rep\nalternative energy salesperson\nalternative energy seller\nrenewable energy salesman\nalternative energy sales promoter','\n',char(10)),'Renewable energy sales representatives assess clients'' energy supply needs, and attempt to secure sales of renewable energy methods. They promote renewable energy suppliers and the use of renewable energy products, and liaise with consumers to increase sales.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ff42d44c-34f8-46be-ad3a-2aad9a5e43f0','key_18154','','','7512','7512.5','pastry maker',replace('pastry maker\npastry production operator\npastry cook\npastry production worker','\n',char(10)),replace(' Pastry makers prepare and bake cakes, cookies, croissants, pies and alike products according to recipes. \n','\n',char(10)),'','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ff4c28e2-66b1-4ae7-9053-fba8ec3428be','key_18155','','','3141','3141.2.5','zoology technician',replace('zoologist\nanimal researcher\nzoology lab technician\nzoological technician\nzoology field technician\nzoology technician\nwildlife biologist\nwildlife manager\nwildlife health technician\nzookeeper\nzoology research technician\nzoology laboratory technician\nanimal behaviorist\nanimal health technician\nzoology researcher','\n',char(10)),'Zoology technicians provide technical assistance in researching and testing animal species using laboratory equipment. They aid in research concerning animals as well as their environments and ecosystems. They collect and analyse data, compile reports and maintain laboratory stock.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ff5210a5-2ea9-43b4-bebc-8d565f928eaa','key_18156','','','8131','8131.4','cosmetics production machine operator',replace('cosmetics production machine operator\ncosmetics production machine tender\ncosmetics production machine worker\ncosmetics production operative\ncosmetics factory worker\ncosmetics production operator\ncosmetics plant worker\ncosmetics production worker\ncosmetics production machine operative','\n',char(10)),'Cosmetics production machine operators tend machinery for the manufacturing of cosmetic products by setting up the machines and tools, cleaning and maintaining the equipment and abiding by the production schedule.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ff63fa99-c634-4e6f-a693-6a49482952e9','key_18157','','','6223','6223.2.1','fisheries boatmaster',replace('local fishery boatmaster\nboatmaster\nfisheries boatmaster\ncoastal skipper\nboat captain\ncoastal fisheries boatmaster\nfishery boatmaster\nlocal boatmaster','\n',char(10)),'Fisheries boatmasters operate fishing vessels in coastal waters performing operations at the deck and engine. They control the navigation as well as capture and conservation of fish within the established boundaries in compliance with national and international regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ff656b3a-65a8-4b4f-b04e-9db12936519b','key_18158','','','2120','2120.2','demographer',replace('demography research analyst\ndemography studies researcher\ndemography scientist\ndemography studies research analyst\ndemography research scientist\ndemography scholar\ndemographers\ndemography studies research scientist\ndemography researcher\ndemography studies analyst\ndemographer\ndemography science researcher\nfield demographer\ndemography analyst\ndemography studies scholar\ndemography studies scientist','\n',char(10)),'Demographers study a variety of parameters related with population. They develop, based on their observations, statistical analyses on the evolutions and changes of births, elderly, marriage and divorce, employment, mortality, immigration and related matters.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ff8d4065-db44-47c7-a4bd-51b3c306ef70','key_18159','','','9612','9612.2','sorter labourer',replace('sorter laborer\ngrader\nyard labourer\nrecycler\nrecycling site sorter\nyard operative\nrecycling site labourer\nsort belt operative\ntextile sorter\nrecycling metal sorter\nsort line operative\nrecycling site sorter labourer\nsorter','\n',char(10)),'Sorter labourers sort recyclable materials and waste from a recycling stream, and ensure no unsuitable materials wind up among the recyclable materials. They inspect the materials and perform cleaning duties, and work in compliance with waste regulations.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ffa4dd5d-702b-4828-ad85-17ec50db6656','key_18160','','','5414','5414.1.2','armoured car guard',replace('armoured truck escort\ntruck escort\narmored car guard\narmoured truck guard\ncar escort\nvan escort\narmoured van escort\narmoured van guard\narmoured car escort\narmoured car security guard','\n',char(10)),'Armoured car guards ensure the safe transportation of valuable items such as money between locations like stores and banks. They load and unload the vehicle, handle the documentation of the transfer, deliver the items to the correct location and respond quickly to robberies, according to the security protocol in place. They may also handle guns and drive the armoured car.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ffade2f4-2528-4dfd-b36a-6e4092be08ed','key_18161','','','2422','2422.1','civil service administrative officer',replace('government administrative officer\ncivil service administrative officer\nlocal authority administrator\npublic administrative officer','\n',char(10)),'Civil service administrative officers perform administrative duties in civil service organisations and government departments. They ensure record maintenance, handle enquiries and provide information to the public, either in person, through emails or phone calls. They support senior staff, and ensure a fluent internal flow of information.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
INSERT INTO occupations(ESCOURI,ID,UUID,ORIGINUUID,ISCOGROUP,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,DEFINITION,SCOPENOTE,REGULATEDPROFESSIONNOTE) VALUES('http://data.europa.eu/esco/occupation/ffc292f1-89e2-4406-8eff-39096c94e731','key_18162','','','8211','8211.5','motor vehicle assembler',replace('automobile assembler\nmotor vehicle assembler\ntruck builder\nmotor vehicle builder\nmotor vehicle assembly tester\nmotor vehicle assemblies inspector\nlorry production line worker\nlorry builder\nauto assembler\ncar factory operative\nlorry assembler\ncar production line worker\ncar assembler\ncar builder\nmotor vehicle assemblies tester\ncar factory worker\nmotor vehicle assembly  inspector\ntruck production line worker\nmotor vehicle body builder','\n',char(10)),'Motor vehicle assemblers install and put prefabricated motor vehicle parts and components together. They inspect the motor vehicles for defects, and test the assembled equipment for proper performance and conformity to quality standards.','','','http://data.europa.eu/esco/regulated-professions/unregulated');
