INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0110','key_633','','','','education not further defined','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0111','key_634','','','','education science','',replace('Education science is the study of the learning process and the theories, methods and techniques of imparting knowledge to others.\nProgrammes and qualifications with the following main content are classified here:\nCurriculum studies\nDidactics\nEducational assessment, testing and measurement\nEducational evaluation and research\nPaedagogical sciences\n\n=== Exclusions\nTeacher training programmes which combine education science with the practice of teaching are excluded from this detailed field and included in one of the detailed fields for teacher training (0112-0114).','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0112','key_635','','','','training for pre-school teachers','',replace('Training for pre-school teachers is the study of the theories, methods and practice of teaching very young children up to 7 years of age within formal school settings at pre-primary or early childhood educational development levels.\nProgrammes and qualifications with the following main content are classified here:\nEarly childhood teaching (within formal school settings)\nPre-primary teacher training\n\n=== Exclusions\nTraining in caring for children outside formal school settings is excluded from this detailed field and included in detailed field 0922 ''Child care and youth services''.¬ù','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0113','key_636','','','','teacher training without subject specialisation','',replace('Training for teachers without subject specialisation is the study of the theories, methods and practice of providing children between approximately 5 and 15 years of age with the fundamental skills in reading, writing and mathematics along with an elementary understanding of other subjects such as history, geography, social science etc. and laying the foundation for lifelong learning. Subject specialisation is stressed less than in detailed field 0114 ''Teacher training with subject specialisation''. The study of teaching children with special needs is included in this detailed field, likewise the study of teaching adults fundamental reading and writing skills and teaching immigrants these types of skills in their home or first language.\nProgrammes and qualifications with the following main content are classified here:\nClass teacher training\nIndigenous teacher training\nPrimary teaching\nTeacher training for children with special needs\n\n=== Inclusions\nThe study of teaching adults basic literacy and numeracy skills is included here. Teacher''s aide (teaching assistant) is also included here.\n\n=== Exclusions\nTeacher training with subject specialisation even if intended for the teaching of children in primary or lower secondary education is excluded from this detailed field and included in detailed field 0114 ''Teacher training with subject specialisation''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0114','key_637','','','','teacher training with subject specialisation','',replace('Teacher training with subject specialisation is the study of the theories, methods and practice of teaching a specific subject, mostly at secondary or higher levels of education. Programmes and qualifications included in this detailed field often comprise both studies as well as the subject/subjects that are going to be taught.\nProgrammes and qualifications with the following main content are classified here:\nTeacher training - arts and crafts\nTeacher training - commercial subjects\nTeacher training - music\nTeacher training - nursing\nTeacher training - physical training\nTeacher training - second languages\nTeacher training - specific theoretical subjects, e.g. English, mathematics, history\nTeacher training - technical subjects\nTeacher training - vocational subjects\nTraining of driving instructors\nTraining of trainers\n\n=== Inclusions\nTeacher training courses for university teachers, instructors of work place and working life skills and in practical and artistic subjects are included here.\n\n=== Exclusions\nTraining of sports trainers is excluded from this detailed field and included in detailed field 1014 ''Sports''.','\n',char(10)),'');
