INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0188','key_640','','','','inter-disciplinary programmes and qualifications involving education','','Inter-disciplinary programmes and qualifications to which the greatest intended learning time is devoted to education are classified here.','');
