INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0311','key_673','','','','economics','',replace('Economics is the study of economic policy, economic theory and economic decisionmaking.\nProgrammes and qualifications with the following main content are classified here:\nEconometrics\nEconomic history\nEconomics\nPolitical economics\n\n=== Exclusions\nStudy of economics where emphasis is given to business studies is excluded from this detailed field and classified under 041 ''Business and administration''. Study of home economics is excluded from this detailed field and included in the detailed field 1011 ''Domestic services''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0312','key_674','','','','political sciences and civics','',replace('Political sciences and civics is the study of government and political principles or practice. The study of the rights and duties of the citizens are included here.\nProgrammes and qualifications with the following main content are classified here:\nCivics\nHuman rights\nInternational relations\nPeace and conflict studies\nPolitical history\nPolitical science\nPublic policy studies\n\n=== Exclusions\nStudy of political economics is excluded from this detailed field and included in the detailed field 0311 ''Economics''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0313','key_675','','','','psychology','',replace('Psychology is the study of the human mind and behaviour as a result of individual differences, experience and environment.\nProgrammes and qualifications with the following main content are classified here:\nCognitive science\nPsychoanalysis\nPsychology\nPsychotherapy','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0314','key_676','','','','sociology and cultural studies','',replace('Sociology and cultural studies is the study of human beings and the way they behave in groups and in relation to society. The study of ethnology and social anthropology are included here, likewise the study of human and social geography.\nProgrammes and qualifications with the following main content are classified here:\nCriminology\nCultural geography\nCultural studies\nDemography/population studies\nEthnology\nGender studies\nSocial anthropology\nSociology\n\n=== Exclusions\nSocial welfare where emphasis is given to practical social work is excluded from this detailed field and included in detailed field 0923 ''Social work and counselling''.\nPhysical geography is excluded from this detailed field and included in detailed field 0532 ''Earth science''.','\n',char(10)),'');
