INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0320','key_679','','','','journalism and information not further defined','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0321','key_680','','','','journalism and reporting','',replace('Journalism and reporting is the study of the theory and practices of journalism/reporting as part of the field of mass communication. Journalism and reporting is about the wording and content of messages. It comprises news reporting, writing commentaries and feature stories of public interest etc.\nProgrammes and qualifications with the following main content are classified here:\nBroadcast journalism\nEditing\nInformation (wording and content)\nJournalism\nMass communication (wording and content)\nNews reporting\n\n=== Exclusions\nStudy of techniques for mass communication (e.g. printing and radio/TV production) is excluded from this detailed field and included in detailed field 0211 ''Audio-visual techniques and media production''.\nStudy of lay-out and publishing design is excluded from this detailed field and included in detailed field 0211 ''Audio-visual techniques and media production''.\nStudy of public relations is excluded from this detailed field and included in detailed field 0414 ''Marketing and advertising''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0322','key_681','','','','library, information and archival studies','',replace('Library, information and archival studies is the study of the methods of selecting, acquiring, organizing and storing collections of information, and facilitating the use of information. Museum and library studies are included here.\nProgrammes and qualifications with the following main content are classified here:\nArchival sciences\nCuratorial studies\nDocumentation\nInformation science\nLibrary studies\nMuseum documentation\nMuseum studies\nMuseology\n\n=== Exclusions\nRestoration and preservation of artistic heritage are excluded from this field and included in field 0222 ''History and archaeology''.','\n',char(10)),'');
