INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0388','key_684','','','','inter-disciplinary programmes and qualifications involving social sciences, journalism and information','','Inter-disciplinary programmes and qualifications to which the greatest intended learning time is devoted to social sciences, journalism and information are classified here.','');
