INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0510','key_710','','','','biological and related sciences not further defined','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0511','key_711','','','','biology','',replace('Biology is the study of the structure, function, reproduction, growth, evolution and behaviour of all living organisms.\nProgrammes and qualifications with the following main content are classified here:\nBiology\nBotany\nCell biology\nEntomology\nGenetics\nMycology\nZoology\n\n=== Exclusions\nStudy of environmental sciences, which is the study of the relation between living organisms and the environment, is excluded from this detailed field and included in detailed field 0521 ''Environmental sciences''. Study of chemical processes in living organisms (biochemistry, toxicology, pharmacology etc.) is excluded from this detailed field and included in detailed field 0512 ''Biochemistry''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0512','key_712','','','','biochemistry','',replace('Biochemistry, sometimes called Biological chemistry, is the study of the chemistry of living organisms, including but not limited to, living matter.\nProgrammes and qualifications with the following main content are classified here:\nBiological chemistry\nCell technology\nForensic sciences\nGenetic code (DNA, RNA) studies\nGenetic engineering\nPharmacology\nTissue culture technology\nToxicology\nVirology\n\n=== Inclusions\nBiotechnology is included here.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0519','key_713','','','','biological and related sciences not elsewhere classified','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/052','key_714','','','','environment','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0520','key_715','','','','environment not further defined','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0521','key_716','','','','environmental sciences','',replace('Environmental sciences are the study of organisms in relation to one another and to the environment.\nProgrammes and qualifications with the following main content are classified here:\nEcology\nEnvironmental science\n\n=== Exclusions\nThe study of biology and toxicology are excluded from this detailed field and included under narrow field 051 ''Biological and related sciences''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0522','key_717','','','','natural environments and wildlife','',replace('Natural environments and wildlife is the study of the relationships between living organisms in natural environments in order to protect nature and wildlife. It includes the study of establishing and maintaining national parks in order to preserve their original natural state.\nProgrammes and qualifications with the following main content are classified here:\nNational parks and wildlife management\nNature conservation\nWildlife\n\n=== Exclusions\nStudy of agriculture, horticulture and forestry are excluded from this detailed field and included in some of the detailed fields under 08 ''Agriculture, forestry, fisheries and veterinary''.\nStudy of hunting and trapping is excluded from this detailed field and included in detailed field 0821 ''Forestry''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0529','key_718','','','','environment not elsewhere classified','','','');
