INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0530','key_720','','','','physical sciences not further defined','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0531','key_721','','','','chemistry','',replace('Chemistry is the study of substances and their elements and how they react when combined.\nProgrammes and qualifications with the following main content are classified here:\nInorganic chemistry\nOrganic chemistry\nPhysical chemistry\n\n=== Exclusions\nBiochemistry is excluded from this detailed field and included in detailed field 0512 ''Biochemistry''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0532','key_722','','','','earth sciences','',replace('Earth sciences is the study of the composition and structure of earth including the hydrosphere and the atmosphere.\nProgrammes and qualifications with the following main content are classified here:\nClimate research\nEarth science\nGeodesy\nGeography (physical)\nGeology\nGeomatics\nGeospatial technology\nMeteorology\nOceanography\nSeismology\n\n=== Exclusions\nSocial geography is excluded from this detailed field and included in detailed field 0314 ''Sociology and cultural studies''. Soil science is excluded from this detailed field and included in detailed field 0811 ''Crop and livestock production''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0533','key_723','','','','physics','',replace('Physics is the study of properties and interactions of matter and energy. It includes the study of astronomy and space science.\nProgrammes and qualifications with the following main content are classified here:\nAstronomy\nAstrophysics\nChemical physics\nMedical physics\nOptics\nPhysics\nSpace science\n\n=== Exclusions\nOpticians'' practice is excluded from this detailed field and included in detailed field 0914 ''Medical diagnostic and treatment technology''.','\n',char(10)),'');
