INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0541','key_727','','','','mathematics','',replace('Mathematics is the study of abstract deductive systems. It includes algebra, arithmetic, geometry, real and complex analysis and pure and applied mathematics.\nProgrammes and qualifications with the following main content are classified here:\nAlgebra\nGeometry\nMathematics\nNumerical analysis\nOperational research','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0542','key_728','','','','statistics','',replace('Statistics is the study of collecting, describing, arranging and analysing numerical data. The study of probability theory and actuarial science is included.\nProgrammes and qualifications with the following main content are classified here:\nActuarial science\nProbability theory\nStatistics, applied\nSurvey design\nSurvey sampling\n\n=== Inclusions\nStudy of mathematical (theoretical) statistics is included here.\n\n=== Exclusions\nDemography/population studies is excluded from this detailed field and included in detailed field 0314 ''Sociology and cultural studies''.','\n',char(10)),'');
