INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0611','key_736','','','','computer use','',replace('Computer use is the study of using computers and computer software and applications for different purposes. These programmes are generally of short duration.\nProgrammes and qualifications with the following main content are classified here:\nComputer use\nUse of software for calculating (spread sheets)\nUse of software for data processing\nUse of software for desk top publishing\nUse of software for word processing\nUse of Internet','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0612','key_737','','','','database and network design and administration','',replace('Database and network design and administration is the study of the design, maintenance and integration of software applications. Computer media applications are included.\nProgrammes and qualifications with the following main content are classified here:\nComputer administration and management\nComputer media applications\nComputer network installation and maintenance\nDatabase administrator studies\nInformation technology administration\nInformation technology security\nNetwork administration\nNetwork design\nWeb design','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0613','key_738','','','','software and applications development and analysis','',replace('Software and applications development and analysis is the study of the design and development of computer systems and computing environments.\nProgrammes and qualifications with the following main content are classified here:\nComputer programming\nComputer science\nComputer systems analysis\nComputer systems design\nInformatics\nOperating systems\nProgramming languages development\nSoftware development\nSoftware programming\n\n=== Exclusions\nComputer engineering (hardware) is excluded from this detailed field and included in detailed field 0714 ''Electronics and automation''.\nProgrammes and qualifications in using computer applications are excluded from this detailed field and included in detailed field 0611 ''Computer use''. Database and network design and administration is excluded from this detailed field and included in detailed field 0612 ''Database and network design and administration''.','\n',char(10)),'');
