INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0810','key_773','','','','agriculture not further defined','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0811','key_774','','','','crop and livestock production','',replace('Crop and livestock production is the study of growing, maintaining and harvesting crops and pastures, and grazing and managing animals. It includes the study of managing and maintaining farms and producing unprocessed plant and animal products.\nProgrammes and qualifications with the following main content are classified here:\nAgricultural sciences\nAgronomy and crop science\nAnimal husbandry\nCrop growing\nDog breeding\nFarm and ranch management\nFarming\nFruit growing\nGrain growing\nHorse breeding\nPig farming\nPoultry husbandry\nRice farming\nRye and wheat growing\nSheep farming\nSoil science\nSugar cane growing\nVegetable planting\nWine growing\n\n=== Inclusions\nStudy of soil fertility and irrigation techniques are included here.\nStudy of growing intensively managed crops such as fruit and vegetables is included here.\n\n=== Exclusions\nStudy of wine production is excluded from this detailed field and is included in detailed field 0721 ''Food processing''.\nJockeying is excluded from this detailed field and is included in detailed field 1014 ''Sports''','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0812','key_775','','','','horticulture','',replace('Horticulture is the study of horticultural technology and management, floriculture, greenhouse methods, nursery management, landscape gardening etc.\nProgrammes and qualifications with the following main content are classified here:\nFloriculture\nGardening\nGreen keeping\nHorticultural techniques\nNursery management\nTurf cultivation\n\n=== Inclusions\nThe laying out and construction of urban and domestic parks and gardens is included here.\nGardening programmes combining floriculture and growing vegetables are included here.\n\n=== Exclusions\nStudy of growing edible crops (such as wheat, rice, fruit, vegetables etc,) is excluded from this detailed field and included in detailed field 0811 ''Crop and livestock production''.\nStudy of soil science, soil fertility and irrigation techniques is excluded from this detailed field and included in detailed field 0811 ''Crop and livestock production''.\nStudy of National park management is excluded from this detailed field and included in detailed field 0522 ''Natural environments and wildlife''.\nStudy of Landscape architecture is excluded from this detailed field and included in detailed field 0731 ''Architecture and town planning''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0819','key_776','','','','agriculture not elsewhere classified','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/082','key_777','','','','forestry','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0821','key_778','','','','forestry','',replace('Forestry is the study of establishing, cultivating, harvesting and managing forests. It includes the study of hunting and trapping.\nProgrammes and qualifications with the following main content are classified here:\nCharcoal burning\nForest keeping\nForest product techniques\nForestry\nHunting and trapping\nLogging\nTree felling\n\n=== Exclusions\nStudy of National park management is excluded from this detailed field and included in detailed field 0522 ''Natural environments and wildlife''.\nTimber technology (sawmill production etc) is excluded from this detailed field and included in detailed field 0722 ''Materials (glass, paper, plastic and wood)''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/083','key_779','','','','fisheries','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0831','key_780','','','','fisheries','',replace('Fisheries is the study of breeding, rearing and harvesting fish and other seafood.\nProgrammes and qualifications with the following main content are classified here:\nAquaculture\nFish breeding\nFish farms\nFishery science and technology\nPearl cultivating\nSeafood farming\nShellfish breeding\n\n=== Inclusions\nProgrammes for operating fishing boats are included here.\n\n=== Exclusions\nStudy of industrial processing of fish is excluded from this detailed field and included in detailed field 0721 ''Food processing''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/084','key_781','','','','veterinary','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0841','key_782','','','','veterinary','',replace('Veterinary is the study of preventing, diagnosing and treating diseases and injuries in animals, and their general care. The study of caring for sick, injured and infirm animals undergoing treatment in veterinary clinics is included. It also includes the study of providing assistance to veterinary scientists.\nProgrammes and qualifications with the following main content are classified here:\nAnimal health care\nAnimal reproduction (science)\nArtificial insemination (of animals)\nVeterinary assisting\nVeterinary medicine\nVeterinary nursing\nVeterinary science\n\n=== Inclusions\nThe science of animal reproduction is included here.\n\n=== Exclusions\nStudy of animal breeding is excluded from this detailed field and included in detailed field 0811 ''Crop and livestock production''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/088','key_783','','','','inter-disciplinary programmes and qualifications involving  agriculture, forestry, fisheries and veterinary','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0888','key_784','','','','inter-disciplinary programmes and qualifications involving  agriculture, forestry, fisheries and veterinary','','Inter-disciplinary or broad programmes and qualifications to which the greatest intended learning time is devoted to agriculture, forestry, fisheries and veterinary are classified here.','');
