INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0920','key_801','','','','welfare not further defined','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0921','key_802','','','','care of the elderly and of disabled adults','',replace('Care of the elderly and of disabled adults is the study of non-medical care of the elderly and disabled.\nProgrammes and qualifications with the following main content are classified here:\nCare of the elderly\nNon-medical care of disabled adults\nPersonal care of adults\n\n=== Exclusions\nStudy of health care of the elderly and disabled is excluded from this detailed field and included in detailed field 0913 ''Nursing and midwifery''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0922','key_803','','','','child care and youth services','',replace('Child care and youth services are the study of the development and non-medical care of children and youths. It includes the study of recreation and leisure activities as a social service for school children and young adults.\nProgrammes and qualifications with the following main content are classified here:\nChild care\nChild recreation programmes\nDay care\nNon-medical care of disabled children\nYouth services\nYouth worker programmes\n\n=== Exclusions\nStudy of early childhood education in pre-schools is excluded from this detailed field and included in detailed field 0112 ''Training for pre-school teachers''.\nStudy of health care of children is excluded from this detailed field and included in detailed field 0913 ''Nursing and midwifery''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0923','key_804','','','','social work and counselling','',replace('Social work and counselling is the study of the welfare needs of communities, specific groups and individuals and the appropriate ways of meeting these needs. The focus is on social welfare with emphasis on social policy and practice.\nProgrammes and qualifications with the following main content are classified here:\nAlcohol and drug abuse counselling\nAlcohol, tobacco, drugs (knowledge about)\nCrisis support\nFamily and marriage counselling\nMobbing and maltreatment (knowledge about)\nParole officer training\nProbation officer training\nSocial policy\nSocial practice\nSocial theory (applied)\nSocial work (welfare)\nVocational counselling\nVocational guidance\n\n=== Exclusions\nStudy of sociology and social science is excluded from this detailed field and included in detailed field 0314 ''Sociology and cultural studies''.\nStudies combining health care and social care (e.g. for old people) are excluded from this detailed field and included in detailed field 0913 ''Nursing and midwifery''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/0929','key_805','','','','welfare not elsewhere classified','','','');
