INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/1011','key_815','','','','domestic services','',replace('Domestic services are the study of various domestic services, such as housekeeping, cleaning, laundering, sewing etc.\nProgrammes and qualifications with the following main content are classified here:\nCaretaking, housekeeping and home service workers\nChimney sweeping\nCleaning\nCooking (home)\nDomestic science\nDry-cleaning\nFuneral services and mortuary science\nHome economics\nLaundry\nNeedlework (home)\nSewing (home)\nWindow cleaning\n\n=== Inclusions\nCleaning is included here even if directed towards cleaning schools, hospitals, factories etc.\n\n=== Exclusions\nStudy of building maintenance is excluded from this detailed field and included in detailed field 0732 ''Building and civil engineering''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/1012','key_816','','','','hair and beauty services','',replace('Hair and beauty services are the study of caring for the hair and the body for beautification.\nProgrammes and qualifications with the following main content are classified here:\nBarbering\nBeauty therapy\nCosmetology (make up)\nFitness and weight control\nHairdressing\nManicure\nPedicure','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/1013','key_817','','','','hotel, restaurants and catering','',replace('Hotel, restaurants and catering is the study of providing food, beverages, accommodation and related services at hotels, restaurants etc.\nProgrammes and qualifications with the following main content are classified here:\nCatering\nCooking (restaurant and hotel-type)\nFast food preparation\nFood serving\nHospitality services\nHotel and restaurant studies\nHotel receptionist training\nHotel services\nWaiting and bar service\n\n=== Exclusions\nStudy of food processing (industrial) is excluded from this detailed field and included in detailed field 0721 ''Food processing''.\nReceptionist training (general) is excluded from this detailed field and included in detailed field 0415 ''Secretarial and office work''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/1014','key_818','','','','sports','',replace('Sports is the study of techniques and skills in a chosen sport.\nProgrammes and qualifications with the following main content are classified here:\nSport trainer studies\nTechniques and skills in a chosen sport\nTraining of umpires/referees and other sports officials\n\n=== Inclusions\nJockeying is included here. Chess playing is included here.\n\n=== Exclusions\nTraining of school teachers in physical education (sports teachers) is excluded from this detailed field and included in detailed field 0114 ''Teacher training with subject specialisation''.\nFitness services are excluded from this detailed field and included in detailed field 1012 ''Hair and beauty services''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/1015','key_819','','','','travel, tourism and leisure','',replace('Travel and tourism is the study of marketing and advertising tourist destinations and events. It includes the study of ticketing and reservation practices. Leisure is the study of recreational and leisure activities for individuals and groups.\nProgrammes and qualifications with the following main content are classified here:\nAbseiling (rope climbing)\nAdventure based activities\nGround crew training (airport)\nGuiding, tour leading\nRecreation and leisure\nTourist trades programmes\nTravel agency services\nTravel and tourism\nTravel services\n\n=== Inclusions\nBasic training in how to treat tourists is included here.\n\n=== Exclusions\nStudy of recreation and leisure activities for school children is excluded from this detailed field and included in detailed field 0922 ''Child care and youth services''.','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/1019','key_820','','','','personal services not elsewhere classified','','','');
