INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/1020','key_822','','','','hygiene and occupational health services not further defined','','','');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/1021','key_823','','','','community sanitation','',replace('Community sanitation is the study of community services dealing with items that affect public health such as hygienic standards in food, water supply, disposal of sewage and garbage and street cleaning.\nProgrammes and qualifications with the following main content are classified here:\nHygiene, community\nHygienic standards\nRefuse collection\nRefuse/sewage disposal\nStreet cleaning\nWater supply (service)\nWaste management','\n',char(10)),'');
INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/1022','key_824','','','','occupational health and safety','',replace('Occupational health and safety is the study of recognising, evaluating and controlling environmental factors associated with the workplace.\nProgrammes and qualifications with the following main content are classified here:\nErgonomics (occupational health and safety)\nHealth and safety in the workplace\nIndustrial welfare\nLabour protection\nLabour security\nLabour welfare (safety)\nOccupational health and industrial hygiene\nOccupational safety\nStress management\nWork environment\n\n=== Exclusions\nStudy of vocational rehabilitation and occupational therapy is excluded from this detailed field and included in detailed field 0915 ''Therapy and rehabilitation''.\nStudy of labour law is excluded from this detailed field and included in 0421 ''Law''.\nStudy of ergonomics is excluded from this detailed field if emphasis is given to technical aspects and is included in the appropriate detailed field under 071 ''Engineering and engineering trades''.','\n',char(10)),'');
