INSERT INTO skillGroups(ESCOURI,ID,UUID,ORIGINUUID,CODE,PREFERREDLABEL,ALTLABELS,DESCRIPTION,SCOPENOTE) VALUES('http://data.europa.eu/esco/isced-f/1032','key_829','','','','protection of persons and property','',replace('Protection of persons and property is the study of services to the community concerning protection of property and persons. It comprises training in police work, public security, fire protection and fire-fighting.\nProgrammes and qualifications with the following main content are classified here:\nCivil security\nCustoms programmes\nFire-protection (fire-fighting)\nFire technology\nLife guarding\nPolice work\nPolicing studies\nPrison work\nPublic security\nSecurity and loss prevention services\nSecurity guarding\n\n=== Exclusions\nStudy of law is excluded from this detailed field and included in 0421 ''Law''.\nStudy of criminology is excluded from this detailed field and included in detailed field 0314 ''Sociology and cultural studies''.','\n',char(10)),'');
